INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5, 24894, 17021, '6회에서 김은희(한예리)와 안효석(이종원)이 윤태형(김태호)의 은신처에서 서울로 돌아갈 때 이 카페에 들린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6, 24895, 17021, '10회에서 하라(배윤경)는 은희(한예리)에게 이 커피숍에서 만나자고 메시지를 보낸다. 그녀는 은희에게 곧 미국으로 돌아간다고 알리고 건주(신동욱)에 대한 이야기를 이어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7, 24896, 17021, '안효석(이종원)은 바리스타로, 김은주(추자현)는 단골손님이다. 1화에서 이진숙(원미경)이 자녀와 사위에게 폭탄을 떨어뜨리는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8, 24897, 17021, '시누이 도혜지(황우슬혜)는 남편이 가문의 회사를 상속받을 수 있도록 그녀가 길을 잃지 않기를 바라는 한편, 세리의 비서 홍씨(고규필)과 동시에 그녀가 가능한 한 빨리 발견되기를 기도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (9, 24898, 17021, '김은희(한예리)와 동료 서경옥(가득희)은 1회에서 작가 의뢰인을 만나기 위해 이 한옥을 방문한다. 추후 은희는 여기로 돌아와 작가의 명상 세션에 참여합니다. 은희는 눈을 감고 자신의 인생에서 가장 긴 날인 2016년 3월 10일을 회상한다. 세션이 끝난 후 동생 김지우(신재하)와 소원해진 절친 박찬을 보고 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (10, 24899, 17021, '10회 P&F에서 계약 체결 후 박찬혁(김석)은 김은희(한예리)에게 그가 가고 싶은 곳이 있다고 말한다. 은희는 찬혁에게 함께 가자고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (11, 24900, 17021, '15회에서 김은희(한예리)가 이 도서관에서 독립출판사 되기 세미나에 참석하는 모습과 건주 (신동욱)이 화자로 밝혀졌다. 세미나가 끝난 후 두 사람은 대화를 나누는데, 건주는 자신이 그녀의 계획을 따랐다고 말하며 그녀는 이를 반박한다. 그런 다음 그는 그녀가 직장을 그만두기 전에 미국에서 교육 프로그램을 신청해야 한다고 제안합니다. 그들은 밖으로 나갔고 건주는 은희가 해명하지 않으면 기분이 나쁠까 걱정하며 하라와 함께 그 주를 언급한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (12, 24901, 17021, '다구 회장(이덕화 분)이 가장 좋아하는 일일 드라마의 촬영 장소 중 하나로 등장, 회장 자신이 이곳에서 조유정(서혜원)의 어머니 진채림(김영아)과 만나 조유정(서혜원)의 손자 강태무(안효섭)와 딸이 서로를 만나기 시작했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (13, 24902, 17021, '김은희(한예리), 그녀의 동료 친구 서경옥(가득희), 그리고 그녀의 새로운 지인 전하라(배윤경)는 8회에서 쇼핑을 마치고 이 식당에서 저녁을 먹기로 결정했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (14, 24903, 17021, '13회에서 쉬는 날 박찬혁(김지석 분)을 자발적으로 초대해 이 식당에 함께 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (15, 24904, 17021, '9화에서 이진숙(원미경)과 딸 김은주(추자현)가 찾아온 카페이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (16, 24905, 17021, '12회에서 김지우(신재하 분)와 윤서영(신혜정/혜정 분)이 이 공원에서 회사 제품 화보 촬영을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (17, 24906, 17021, '12회에서 김은희(한예리)와 박찬혁(김지석)이 진솔한 이야기를 나누기 위해 이 공원을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (18, 24907, 17021, '김상식(정진영)은 12회에서 아내 이진숙(원미경)을 데리고 이 계단으로 나와 아이들에게 커피 테이크아웃을 시켜준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (19, 24908, 17021, '김은희(김지석)는 이곳을 지나가다가 학생들이 걸어가는 방향을 피해서 김은희(예한) 학생들을 붙잡는다. 그리고 나서 그는 그녀의 손을 잡을 기회를 잡는다. 잠시 말문이 막힌 은희가 어색하지 않게 손을 뗀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (20, 24909, 17021, '김상식(정진영)은 온라인에서 나이 많은 커플들의 좋은 데이트 장소로 검색해 아내 이진숙(원미경)을 데려왔다. 그는 온라인에서 읽은 데이트 조언에 따라 커피를 마시면서 휴대 전화에 사진을 보여줍니다. (진심어린 대화로 상황이 더 진지해질 때까지.)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (21, 24910, 17021, '12화에서 바리스터 안효석(이종원)이 김은주(추자현)에게 작별인사를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (22, 24911, 17021, '김은희(한예리)는 4년 만에 만난 박찬혁(김지욱)과 저녁을 먹기 위해 이 주차장에 차를 주차했다. 저녁식사 후, 그는 주차비를 지불하고 그녀를 배웅해 준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (23, 24912, 17021, '계약 체결 중 박찬혁(김지석)은 그날 밤 김은주(추자현)로부터 만나자고 연락을 받는다. 그는 이 술집에서 은주를 만난다. 은주의 결혼 사진과 가족 문제에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (24, 24913, 17021, '10회 방송에서는 술 취한 찬혁(김지석)과 술 취한 은희(한예리)가 20대 가을의 추억을 되새기기 위해 이 산책로를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (25, 24914, 17021, '카페의 옥상(실은 이곳이 옥상)에서 그녀에게 고백을 하고 두 사람은 이별의 키스로 저녁을 마무리한다. 은희는 언니의 차가 도착하는 것을 보고 갑자기 자신의 심장이 방금 키스한 사람이 아니라 다른 사람을 위해 뛰고 있다는 걸 깨닫는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (26, 24915, 17021, '1회에서 김은희(한예리)사 P&F 북의 신임 부사장 임건주(신동욱)를 위한 환영 만찬은 펼쳐진다. 안으로 들어서자 결혼 1주년을 맞아 아내가 도착하기를 기다리는 전 남자친구 이종민(최웅 분)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (27, 24916, 17021, '은신처로 가는 길에 윤태형(김태훈)은 5화에서 전망을 보기 위해 이 전망대 전망대에 잠시 들른다. 6회에서는 김은희(한예리)와 박찬혁(김지석)도 태형(은희의 처남)을 찾기 위해 이곳에 들르는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (28, 24917, 17021, '김은주(추자현)가 윤태형(김태훈)을 깜짝 찾아오자 두 사람은 이 해변을 찾아오며 최종 16회를 마무리 짓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (29, 24918, 15934, '그녀에게 끌리는 오싹한 선배 지수와 함께 밥을 먹는다. 실수로 언니의 셔츠에 소스를 바르고, 동생이 닦자고 손을 뻗기도 전에 같은 반 친구 이연남(김민규)이 그녀를 데리고 화장실로 가자고 한다. 연남은 충남에게 아침 일찍 장난을 쳤던 것에 대해 사과하고, 둘은 서로에게 별 감정 없음을 확인하고 친구가 되기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (30, 24919, 15934, '충남이 카페의 용암초콜릿 케이크가 먹고 싶었다. SNS에 올라온 부정적인 댓글 때문에 자신이 부족하다고 느끼지만, 연남은 그녀에게 자신을 격려한다. 시크릿가든 스타일로 충남과의 키스를 꿈꾸는 연남이도 여기에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (31, 24920, 15934, '그녀는 어린 시절의 달달한 만남 때문에 카페 주인 홍대영(김성규)을 짝사랑하고 있는 것으로 밝혀졌다. 1화에서는 학교 선배인 김태이(HELLOVENUS 유영), 이연남(김민구)과 함께 온다. (대영이가 연남과 인연이 있는 줄도 모르고.)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (32, 24921, 15934, '1화에서 소름 끼치는 선배 지수가 저녁 식사 후 충남에게 집까지 태워주려 하고, 연남이 대신 걸어주겠다고 제안하는 모습이 나온다. 2화에서 연남은 술에 취한 충남이 지수와 함께 떠났다는 사실을 알고 급히 식당을 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (33, 24922, 15934, '7회(전체 2회)에서 카페에서 애프터눈 티를 먹은 후 정서현(노을, 전 레인보우)과 홍대영(김성규)이 이 거리를 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (34, 24927, 16383, '안서연(박시은 분)과 고민재(윤찬영 분)는 1회에서 이 역에서 지하철을 타고 집으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (35, 24928, 17204, '12회에서 한밤중 수영을 못하는 정다정(김하늘)이 한 곳에 걸려 넘어지고, 고우영(이도현), 예지훈(위하준)은 그녀를 구하기 위해 직접 물속으로 뛰어들었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (36, 24929, 17204, '1회에서 홍대영(윤상현)이 두 자녀 홍시아(노정의), 홍시우(려운)와 함께 이 식당에서 점심을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (37, 24930, 17204, '홍대영(윤상현)은 두 아이 홍시아(이남경)과 홍시우(오자훈)는 4회에서 어려서 인연을 맺지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (38, 24931, 17204, '18회에서 절친 고덕진(김강현)과 이 이자카야에서 하트 투 하트 토크를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (39, 24932, 17204, '18회에서 홍대영(윤상현)과 정다정(김하늘)은 두 자녀 홍시아(노정의), 홍시우(려운)와 함께 이곳에서 가족 회식을 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (40, 24933, 17204, '최일권(이기우)은 5화에서 정다정(김하늘)과 만나 파티 친구들과의 내기에서 이기기 위한 작전을 세우고 홍대영(이도현)은 멀리서 이 광경을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (41, 24934, 17204, '12화에서 예지훈(위하준)을 모델로 한 고고플레이의 신작 \'스파르타 - 전쟁의 왕\' 게임은 이 정원에서 런칭된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (42, 24935, 17204, '12회에서 추애린(이미도)이 고덕진(김강현)을 불러 이 술집에 술을 들이키는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (43, 24936, 17204, '7, 9회에서 이 건물의 외관은 최일권(이기우)이 정기적으로 선생님과 학부모 간담회를 하는이 클럽의 하나로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (44, 24937, 17204, '이 장소는 2회에서 정다정(김하늘)과 절친 추애린(이미도)이 술을 마시러 왔다가 그날 저녁 아르바이트생으로 오빠를 도와주는 일권과 마주치는 최일권(이기우)의 동생이 운영하는 레스토랑 바로 등장한다..');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (45, 24938, 17204, '12회에서 정다정(김하늘)은 홍대영(윤상현)과 함께 입구 옆 쉼터를 찾았던 순간을 회상한다. 그리고 그는 우산과 꽃을 들고 돌아오기 위해 잠시 떠납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (46, 24939, 17204, '고우영(이도현)과 홍시우(려운)는 18회7화에서 정다정(김하늘)과 추애린(이미도)을 마주치고, 우영은 애린이 자신의 정체를 밝혀내는 것을 두려워한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (47, 24940, 17204, '추애린(이미도)이 일하는 방송사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (48, 24941, 17204, '8회에서 예지훈(위하준)이 정다정(김하늘)을 저녁식사로 이 식당에 데려온다. 그리고 그들이 떠날 때 여사장님(박은영 카메오)과 지훈은 모두 다정에게 사인을 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (49, 24942, 17204, '8회에서 정다정(김하늘)은 절친 추애린(이미도)을 이 식당에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (50, 24943, 17204, '5화에서 일권이 정다중(김하늘)을 유혹하겠다는 내기를 수락했다는 것이 밝혀지는 장면이 나온다. 7회에서 일권은 이 클럽에서 사모회를 열고 정다중(김하늘)과 고덕진(김강현)의 아들이 들어갈 수 있도록 돈을 갈취하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (51, 24944, 17204, '15회에서 정다정(김하늘 분)과 예지훈(위하준 분)은 이 장소에서 만나 마음을 터놓고 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (52, 24945, 17204, '8회에서 홍대영(윤상현)은 이 거리에서 아버지 홍주만(이병준)과의 결별의 순간을 회상한다. 그의 아버지가 철로를 건너는 동안 그는 뒤에 머물렀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (53, 24946, 17204, '정다정(김하늘 분)과 고우영/홍대영(이도현 분)이 함께 점심을 먹은 후, 다시 12화 이 장소에서 그랜드 메르큐어 호텔의 문을 통해 식당을 빠져나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (54, 24947, 17204, '14회에서 오해가 풀린 후 홍대영(이도현)은 정다정(김하늘)과 이 곳에서 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (55, 24948, 17204, '1회에서 젊은 홍대영(이도현)과 정다정(한소은)이 젊은 부모가 되는 것이 무엇을 의미하는지 토론하는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (56, 24949, 17204, '홍씨 가족은 이 아파트에 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (57, 24950, 17204, '8회에서 홍대영(윤상현 분)은 아들 홍시우(려은 분)와 길을 건너던 이 거리에서 자신이 아이들과 얼마나 소원해졌는지 회상하며 특정 순간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (58, 24951, 17204, '정다정(김하늘)과 청소년 홍대영(이도현)은 15회에서 밤에 이 다리와 다리 사이를 건너며 데이트한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (59, 24952, 17204, '8회에서 술을 마시러 식당에 찾아와 홍대가 희생한 모든 것을 생각하며 눈물을 흘린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (60, 24953, 17204, '12회에서 정다정(김하늘)은 이 식당에 점심식사를 초대받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (61, 24954, 17204, '8회에서 추애린(이미도)의 전 남자친구(허정민)가 그녀를 이 호텔로 데려와 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (62, 24955, 7259, '영화 1987 초반부 김윤석이 차례를 지내는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (63, 24956, 7259, '영화 1987에 나온 부산역 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (64, 24957, 7259, '영화 1987에서 김태리가 다닌 대학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (65, 24958, 7259, '1987 촬영지이자 박종철이 고문으로 사망한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (66, 24959, 7259, '1987에서 김승훈 신부가 성명서를 발표한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (67, 24960, 7259, '영화 1987 마지막 장면의 배경이 된 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (68, 24961, 7259, '영화 1987에서 연희네 슈퍼로 나온 가게');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (69, 24962, 7259, '1987에서 문성근과 김윤석이 만나는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (70, 25015, 15235, '드라마의 포스터 촬영이 있던 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (71, 25016, 15235, '20세기 소녀의 포스터 촬영은 이 아파트 단지 앞에서 찍혔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (72, 25017, 15235, '1화에서 사진진(한예슬)이 이곳을 떠나 상하이로 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (73, 25337, 13437, '드라마의 \'서울호텔\'로 등장. 2회에서 신지현(남규리)이 송이경(이요원)의 몸으로 직장 증명서를 받는 모습이 그려졌다. 호텔을 산책하던 중 그녀는 약혼자의 외도를 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (74, 25338, 13437, '이 건물은 신인정(서지혜)의 직장으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (75, 25339, 13437, '드라마 등장인물인 한강(조현재)이 운영하는 천국카페로 등장한다. 한강 건축사무소는 이 건물 1층에 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (76, 24951, 13437, '1회에서는 신지현의 모습을 볼 수 있다. (남규리) 이 다리를 건너 결혼식장으로 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (77, 25340, 13437, '11회에서 한강(조현재)과 송이경(이요원)의 몸을 사용하는 신지현은 학창시절을 추억하기 위해 진안으로 각자 여행을 떠났을 때 이 학교에 와서 우연히 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (78, 25341, 13437, '2회에서 잠에서 깨어난 송이경(이요원)의 몸으로 이 거리를 걷던 신지현(남규리)은 갑자기 스케줄러(정일우)가 나타나 누군가의 몸을 빌릴 때의 법칙을 상기시킨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (79, 25342, 13437, '23회에서 거북당에서 박주현(장영함)이 오태석(주석래)에게 선물로 자켓을 사고 싶어하는 \'영천시장\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (80, 25343, 13437, '11회에서 한강(조현재)은 암살자를 찾으러 이 절에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (81, 25344, 13437, '11화에서 마이산 탑사로 가는 길에 이 거리를 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (82, 25345, 16313, '국회 폭발 직후의 장면이 이곳에서 촬영됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (83, 25346, 16313, '5회에서 \'국회의사당 폭탄 테러 희생자 추도식\'이 열리는 곳이 바로 이 묘지다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (84, 25335, 16313, '박무진(지진희 분)은 1회에서 이 다리 위를 운전하던 중 차를 들이받는다. 교통이 완전히 멈추고 그들은 차에서 내려 이 다리에서 국회의 폭발을 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (85, 25347, 16313, '윤찬경(배종옥 분)은 5회에서 이 공원에서 운동을 하는데, 그 뒤를 따라가지 못하는 비서가 따라온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (86, 25348, 16313, '1화에서 이 건물에서 폭발이 일어난다. 여기에서 일하는 박무진(지진희)이 대통령이 되려 준비하다가 폭발사고 당일 현장 견학을 다녀온 딸이 살아 있는지 확인하기 위해 급히 달려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (87, 25349, 16313, '3회에서 탈북자들이 국회 폭탄 테러 배후에 있다는 소문을 선동자들이 퍼뜨리는 곳이 바로 이 공원 앞이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (88, 25600, 4405, '이 단지의 일부는 김서원(최강희)의 회사로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (89, 25601, 15067, '2회에서는 이 정자로 통하는 돌다리 위에서 어린 이역(백승환), 어린 신채경(박시은), 어린 서노(최민영)가 친구가 되기로 맹세한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (90, 25602, 15067, '3화에서 어린 이역(백승환)과 어린 신채경(박시은)이 이 들판에서 비를 피할 곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (91, 25603, 15067, '10화에서 이영(이동건)은 이 연못에서 신채경(박민영)에게 강제 키스를 한다. 한편 이역(연우진)은 멀리서 현장을 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (92, 25604, 16635, '정서연(조여정)과 남편 홍인표(정웅인)는 친구 윤희주(오나라)와 남편 이재의 초청으로 주말 별장에 초대된다. 술에 취한 희주가 서연을 얕잡아보는 저녁, 상황이 어색해지면서 서연도 가차 없이 대답한다. 4회(2회)에서는 강태우(김강우)가 집주인에게 말을 걸기 위해 찾아와 말을 꺼리는 모습이 그려졌다. 태우는 별장에서 차를 몰고 나온 여자가 짐작대로 관리인의 딸이 아니라는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (93, 25605, 16635, '2회(전체 1회)에서 강태우(김강우)는 임시 십자가에 묶인 채 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (94, 25606, 16635, '1회에서 정서연(조여정)은 친구 윤희주(오나라)의 남편 이재훈(이지훈)과 비밀리에 만나기 위해 이 호텔에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (95, 25607, 13006, '1화에서 홍난희(수애)와 변형태(이정진)가 조깅을 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (96, 25608, 13006, '평생 친구였던 홍난희(수애)와 변형태(이정진)는 당시 정해진 관계가 아닌 서른이 되기 전에 이곳에서 함께 섣달 그믐날을 보내기로 약속했었다. 1화에서 남산타워에 있는 자신들을 발견하고 난희는 형태에게 이곳에서 결혼하라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (97, 25609, 13006, '21화(전체 11화)에서 구의회 건물 밖에서 만난 권우영(김미수)과 김민재(한준우)는 마원신용협동조합 가입을 위해 이 카페로 향합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (98, 25017, 13006, '1회에서 홍난희(수애)가 변형태(이정진)를 이곳으로 보내는 장면이 나왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (99, 25610, 13006, '경기가 중단된 후, 조폭 장덕수(허성태)는 이 놀이동산 밖에서 지인을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (100, 25607, 4186, '마지막 20회에서 딸과함께 서은하(수애)와 최민우(주지훈)를 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (101, 25611, 4186, '이곳은 최민우(주지훈)가 약혼자 서은하(수애)를 물에 빠뜨린 수영장으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (102, 25612, 4186, '9화에서 최민우(주지훈)가 변지숙(수애)을 데려온다. 그는 그녀가 이곳에 와서 바람 소리와 새소리를 듣고 정신을 맑게 하는 방법을 설명하고 지숙의 미래 계획과 꿈을 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (103, 25613, 4186, '5회에서 인영과 송설희(박준금)가 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (104, 25614, 4186, '6회에서 지숙의 형이 온 직후 민우도 그 자리를 찾아 화장실 청소를 하고 있는 지숙을 발견하고 당장 일을 그만두라고 명령한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (105, 25615, 4186, '변지숙(수애)의 어머니는 간 이식 수술을 위해 이곳에 입원했다. 17화에서 어머니는 최미연(유인영 분)의 방해로 제때 이식을 받지 못해 그녀 앞에서 사망한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (106, 25616, 4186, '1화에서 강동하(이현진)의 직장으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (107, 25617, 4186, '강을 건널 때 민우는 어머니의 죽음에 죄책감을 느끼고 그 이후로 물을 건널 수 없다고 고백한다. 그녀는 그들이 한 단계씩 함께 나아가면서 시도할 것을 제안합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (108, 25618, 4186, '14회에서 변지숙(수애)과 최민수(주지훈)는 서울 외곽으로 여행을 갔을 때 이곳을 지나고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (109, 24981, 4186, '4회에서 변지숙(수애)과 최민수(주지훈)가 산책을 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (110, 25619, 4186, '약혼을 앞둔 황희태(이도현)와 이수련(금새록)은 이모(김현)의 손에 이끌려 장보기로 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (111, 25620, 17579, '8회에서 저녁 데이트를 위해 찾아왔지만 보름달이라 떠나야 한다고 말하며 그녀의 눈앞에서 곧 사라진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (112, 25621, 17579, '민유정(박정연)은 1회에서 이 장소에서 사적인 행사를 위해 해금을 연주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (113, 25622, 8458, '영화 가장 보통의 연애에서 순대국 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (114, 25623, 8458, '공효진의 통쾌한 복수신을 찍은 고기집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (115, 25624, 8458, '영화 가장 보통의 연애에서 회식 장면을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (116, 25625, 8458, '김래원과 공효진이 소주잔을 기울인 돼지고기 구이집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (117, 25626, 8458, '영화 가장 보통의 연애에서 회식장소로 나온 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (118, 25627, 8458, '김래원의 취중 고백신을 찍은 문정동 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (119, 25628, 8458, '공효진과 김래원의 취중진담신을 찍은 술집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (120, 25629, 8458, '김래원의 고백신을 찍은 닭갈비 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (121, 25630, 8458, '공효진과 김래원이 술게임을 즐긴 수제 맥주 펍');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (122, 25631, 8458, '영화 가장 보통의 연애에서 회사 워크샵으로 온 산');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (123, 25632, 14122, '13년의 혼수상태에서 깨어난 우서리(신혜선)는 자신의 집을 찾아보지만 이미 공우진(양세종) 가족의 소유임을 알게 된다. 오해가 풀린 후 그녀에게 떠나달라고 부탁하고 서리는 노숙자처럼 떠돌아다닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (124, 25633, 14122, '강서울(남지현)은 1회에서 남산타워를 방문하기 위해 이 계단을 이용한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (125, 25634, 9479, '9회에서 나영원(정소민)과 유지성(김지석)이 직장 설문조사를 하러 이 사찰을 찾았다. 두 사람은 함께 일을 잘하기로 결정한다. 자성은 이 사찰과 관련이 있는 속담이 있는데, \'합심하면 폐허에도 꽃이 핀다\'는 말이 있다고 한다. 이어 그녀는 “거대한 산불로 인해 폐허가 된 곳인데, 이렇게 견고하게 복원된 모습을 보니 딱 들어맞는 표현인 것 같다”고 말했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (126, 25607, 9479, '11회에서 봉해령(김소연)과 서지건(이상우)은 메타세쿼이아 골목길을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (127, 25635, 9479, '13회에서 봉해령(김소연)은 저녁을 먹기 위해 이곳으로 갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (128, 25636, 9479, '기가 봉삼식(윤다훈)과 봉삼숙(지수원)이 행복한 집(가화만사성)(MBC, 2016)에서 일하는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (129, 25637, 9479, '9회에 나오는 병원의 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (130, 25638, 9479, '봉준호 가족이 사는 행복한 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (131, 25639, 13672, '서관의 정면은 기서회의 이시용 백작(안석환)과 이화경 백작부인(김정난)의 저택이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (132, 25640, 17758, '6회에서 양혜선(김한나)가 보낸 문자를 본 후 혜선이 도착하자마자 도재진(김도완)이 땅에 물을 뱉어내고 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (133, 25641, 17758, '11화에서 도재진(김도완)과 양혜선(김한나)이 데이트를 하러 이곳을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (134, 25642, 17758, '4화에서 같은 임무를 맡은 도재진(김도완)과 양혜선(김한나)과 마주치게 되고 결국 함께 영화를 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (135, 25643, 17758, '최종회 16회에서 신우여(장기용)와 이담(걸즈데이 혜리)이 이곳에서 몰래 데이트를 하기 위해 학교수업을 빼먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (136, 25644, 17758, '최종회 16회에서 주인공인 신우여(장기용), 이담(혜리), 양혜선(강한나)이 이 곳에서 축하연을 펼친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (137, 25645, 17758, '6화에서 이담(걸스데이 혜리)의 실망스러운 미팅 이후, 신우여(장기용)는 그녀를 응원하기 위해 이 (폐쇄된) 박물관으로 순간이동하여 개인 방문할 수 있도록 해준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (138, 25646, 17758, '4회에서는 이담(걸스데이 혜리)과 계선우(배인혁)가 카페를 찾아 과제를 진행하였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (139, 25647, 17758, '7회에서 신우여(장기용)와 함께 이 동네를 걷던 중 이담(걸스데이 혜리)은 한 여자를 발견한다. 조선시대 우여의 첫사랑(정소민)과 많이 닮았다. 우여는 처음 그녀를 보고 당황했지만 결국 그녀가 실제 모습이 아님을 정확하게 감지한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (140, 25648, 17758, '1화에서 이 건물은 신우여(장기용)가 회의에 참석하기 위해 가는 \'해밀 출판사\'로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (141, 25649, 17758, '12화에서 이담(걸스데이 혜리)과 동료 학생들이 이곳에 사는 역사가와의 1박 여행을 위해 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (142, 25650, 17758, '15회에서 도재진(김도완)과 양혜선(김한나)이 데이트를 하는 모습. 떠날 때 문을 열 때 실수로 손이 닿습니다. 재진은 반사적으로 손을 떼고 혜선은 만지지 않으려고 당황한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (143, 25651, 17758, '이담(걸스데이 혜리), 도재진(김도완), 양혜선(김한나)은 만나서 소개팅 전 담에게 조언을 구하기 위해 이곳에서 술을 마신다. 6 of 내 룸메이트는 구미호/무서운 동거 (간동거) (tvN, 2021).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (144, 24932, 17758, '3회에서 계선우(배인혁)가 이담(걸스데이 혜리)을 이곳으로 데려와 식사를 시켰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (145, 24972, 17758, '계선우(배인혁)는 4회에서 이담(혜리)이 데이트 과제를 하러 가기를 기다린다. 하지만 실제 담수진이 몸이 좋지 않았던 것처럼 위장한 것이 신우여(장기용)라는 사실을 그는 알지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (146, 25652, 17758, '6화에서 소개팅을 앞두고 이담(걸스데이 혜리)은 도재진(김도완)을 만난다. 4회에서 계선우(배인혁)는 이 건물(이하 옴프하우스)의 식당 밖에서 담을 기다립니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (147, 24934, 17758, '11화에서 도재진(김도완)과 양혜선(김한나)이 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (148, 24901, 17758, '이담(걸스데이 혜리)은 몰래 친구 최수경(박경혜)과 도재진(김도완)의 조언을 구하려고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (149, 25653, 17758, '신우여(장기용)의 역사시대 전생의 일부 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (150, 25654, 17758, '1화에서 신우여(장기용)가 트레이드타워와 코엑스를 지나 이 길을 드라이브한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (151, 25655, 17758, '신우여(장기용)는 1회에서 여기서 혼자 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (152, 25656, 17758, '서점에서 책을 찾던 중 의문의 백인 남자(스페셜 게스트)와 우연히 마주쳤다. 고경표에게 조심하라고 경고한다. 바로 그때 신우여(장기용)가 나타나지만 그 남자는 어디에도 보이지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (153, 25657, 17758, '1화에서 보듯 이담(혜리)이 대학생으로 다니는 \'서과대\' 캠퍼스로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (154, 25658, 17758, '6회에서 이담(걸스데이 혜리), 최수경(박경혜), 도재진(김도완)이 담의 소개팅에 대한 이야기를 듣기 위해 이 곳에서 식사를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (155, 25659, 17758, '이담은 13회에서 자신과 계선우(백인혁 분)를 묶는 \'빨간 실\'을 끊으려다 이 공원 주변을 걷는다. 갑자기 그녀는 다른 곳으로 이동하여 산신(고경표)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (156, 25660, 17758, '5회에서 이 거리에서 이담(혜리)과 신우여(장기용)가 포창마차(텐트바)를 발견하고, 우여는 함께 술을 마시며 기세를 올리자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (157, 25661, 17758, '15화에서 이담(걸스데이 혜리)과 신우여(장기용)는 무엇을 먹을지 고민하며 와우산로 35길 끝자락을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (158, 25662, 17758, '4회에서 이담(걸스데이 혜리)과 최수경(박경혜)은 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (159, 25663, 17758, '최종회 16화에서 신우여(장기용)와 이담(걸스데이 혜리)이 문어볶음집(본명 \'할머니낙지볶음\')에 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (160, 24905, 17758, '최종회 16회에서 계서우(김도연)는 이 공원 옆에서 이단(최우성)과 마주치며 금메달 획득을 축하하고, 이날부터 교제를 시작했음을 알려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (161, 25664, 17758, '2화에서 신우여(장기용)가 이담(걸스데이 혜리)을 위해 피자를 포장하고 이 길을 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (162, 25665, 17758, '15회 장기용과 혜리의 야경 데이트 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (163, 25666, 17758, '6회에서 이담(걸즈데이 혜리)과 소개팅을 식사를 마치고 이 카페로 향하는데, 신우여(장기용)도 여기에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (164, 25667, 17758, '7회에서 걸스데이 이담(걸스데이 혜리)이 신우여(장기용)를 만나자고 하는 \'핫플레이스\'의 모습이 그려졌다. 그가 카페에서 한 잔 하자고 제안한 후 그날 저녁, 이전에 교제했던 여성과 관련된 미스터리한 살인 사건을 조사하다가 늦게 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (165, 24942, 17758, '2회에서 이담(걸스데이 혜리)은 고기를 먹고 싶어서 친구 최수경(박경혜), 도재진(김도완)과 함께 이곳에서 역사학부 모임에 참석하지만 프라이드 치킨(그녀가 먹으면 안되는 음식)을 먹고 있는 자신을 발견합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (166, 25335, 17758, '신우여(장기용)는 3회에서 자신이 다니던 대학에서 이담(혜리)을 태우고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (167, 25668, 17758, '5회에서 이담(걸스데이 혜리)과 신우여(장기용)는 이 다리 근처에서 한강을 따라 오후를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (168, 25630, 17758, '1회에서 이담(걸스데이 혜리), 최수경(박경혜), 도재진(김도완)이 술집에서 만난다. 저녁식사를 같이 하고, 여자친구가 바람을 피웠다는 재진의 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (169, 25669, 17758, '4화에서 쇼핑몰 옥상이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (170, 25670, 17758, '4회에서 이담(걸스데이 혜리)은 이 카페에서 신우여(장기용)를 발견한다. 3화의 시뮬레이션 데이트 장소이기도 하다. 감정을 참지 못한 담이 다가와 자신의 테이블에 앉아 우여에게 예전처럼 편안하게 소통하고 싶다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (171, 25671, 17758, '3회에서 이담(걸스데이 혜리)과 신우여(장기용)는 이 카페로 향한다. 계선우(백인혁)가 그 뒤를 이었다. 선우가 담에게 손을 뻗으려 하자 우여는 본능적으로 선우를 끌어당겨 보호한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (172, 25672, 17758, '16회 신우여(장기용 분)와 이담(걸스데이 혜리 분)이 학교를 빼먹고 지하 데이트를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (173, 25017, 17758, '6화에서 이담의 어머니(오현경)가 미국에서 입국한 뒤 공항에서 이담(걸스데이 혜리)에게 연락한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (174, 24981, 17758, '5회에서 이담(걸스데이 혜리)과 신우여(장기용)가 나무가 늘어선 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (175, 25673, 17758, '1화에서 학교 프로젝트로 이담( 혜리)과 최수경(박경혜)이 이곳을 찾는다. 그들은 한복을 입고 광한루 바로 앞에 있는 다리에 앉아 사극의 한 장면을 연기하는 시늉을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (176, 25674, 17758, '1화에서 이담(걸스데이 혜리)은 이 거리 인생사진관 바로 바깥에 위치한 빨간 포르쉐 위에 쓰러져 있는 취한 도재진(김도완)을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (177, 25675, 17758, '1회에서 이담(걸스데이 분)과 신우여(장기용 분)가 카페를 나온 뒤 이 거리를 걷는다. .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (178, 25676, 17758, '1회에서 걸스데이 이담(혜리)이 술에 취한 도재진(김도완)을 업고 이 거리를 걷고 있다. 그녀가 그의 무게로 쓰러지고, 재진은 몸을 일으켜 거리를 뛰쳐나가는데 이담이 뒤를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (179, 25677, 17758, '신우여(장기용)의 1950년대 전생의 일부 장면을 여기서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (180, 25673, 16640, '1화에서 여왕 자리를 노리는 자들이 오작교를 건너고, 왕 이경(김민구 분)이 지망생 중 한 명인 강은기(진세윤 분)와 데이트를 하는 곳이다. 11화에서는 왕과 강은보(진세연)가 이 정원의 봉래섬에서 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (181, 25678, 14046, '6화에서 류태오(이준)가 마리아오(김민정)를 데리고 이 놀이공원에 왔다. 그리고 두 사람은 회전목마를 타며 그가 살인자일 수 있는 자신을 얼마나 용감하게 쫓고 있는지 말했고 하무염(윤상현)은 멀리서 상황을 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (182, 25718, 13310, '10회 엔딩에서 전진호(이민호)는 이 공원 야외 포장마차에서 동료 노상준(정성화), 김태훈(임슬옹)과 함께 술을 마시며 자신의 짝사랑 나혜미(최은미)를 두고 진호에게 도전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (183, 25607, 13310, '박개인(손예진)의 절친 이영선(조은지)이 6회에서 자신과 전진호(이민호)에게 이 공원에서 사진을 찍고, 아들과 함께 가족처럼 포즈를 취하도록 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (184, 25719, 13310, '이 건물은 최도빈(류승룡) 감독의 담미술관 일명 DAAM 프로젝트로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (185, 25720, 13310, '1회 엑스포에서 공개됐다. 동시에 전진호(이민호)와 한창렬(김지석)이 함께하는 이 컨벤션홀에서 \'드림아트센터\' 건립을 위한 건축사 간 입찰이 진행된다. 각 회사의 입찰자입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (186, 25721, 13310, '4회에서 전진호(이민호)가 약국으로 가는 길에 \'마포원조주물리\' 레스토랑 앞 사거리에서 이 길을 건너는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (187, 25722, 13310, '“사람들은 이렇게 높은 곳에 오르면 마음이 가벼워집니다. 그래서 그들은 고백할 용기를 얻습니다. 나중에 프러포즈 받고 싶으면 이런 곳으로 가세요.” - 8화에서 전진호(이민호)가 박개인(손예진)에게 이 곳에 대해 한 말이다. 그녀에게 간접적으로 좋아한다고 말한 것이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (188, 25723, 13310, '이 건물은 전진호(이민호)가 자란 집으로 아버지가 돌아가신 후 떠나야 했던 집으로 등장한다. 그는 어머니와 어린 한창렬과 아버지 한윤섭(안석환)이 이사를 오기 위해 도착한 순간을 떠올리며(2,6회) 계속 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (189, 25724, 13310, '한창렬(김지석)은10회에서 전진호(이민호)에게 다리 밑에서 만나자고 한다. 그가 진호에게 박개인(손예진)의 집에서 나가달라고 부탁하는 곳이 바로 여기다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (190, 25725, 13310, '박개인(손예진)의 집과 그녀와 함께 이사 온 전진호(이민호)의 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (191, 24913, 13310, '3화에서 전진호(이민호)는 술 취한 박개인(손예진)을 이 벽을 따라 집으로 업고 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (192, 25726, 15073, '1회에서 나지영(민효린)과 박벽수(공명)가 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (193, 25727, 15073, '2회에서 나지영(민효린)과 박벽수(공명)의 이야기는 이 식당 앞에서 키스로 행복하게 끝을 맺는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (194, 25728, 17957, '신민아와 김선호가 미술관 데이트를 즐긴 미술관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (195, 25729, 17957, '신민아와 김선호가 골프 데이트를 즐긴 골프장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (196, 25730, 17957, '신민아가 김선호에게 사랑 고백했던 방파제');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (197, 25731, 17957, '갯마을 차차차의 슈퍼, 철물점, 카페가 있는 5일장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (198, 25732, 17957, '신민아와 김선호가 갔던 언덕 위에 배가 있는 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (199, 25733, 17957, '신민아와 공민정이 근사한 식사를 한 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (200, 25734, 17957, '3화에서 윤혜진(신민아)과 홍두식(김선호)이 서울을 떠나 이 다리를 건너는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (201, 24944, 17957, '10회에서 서울 여행길에 오른 윤혜진(신민아)과 절친 표미선(공민정)이 이곳에서 저녁을 먹는다. 혜진은 공진 바다에서 살다 보니 한강이 예전보다 좁아진 것 같다고 말한다. 이들이 식당을 나서자 갑자기 비가 내리기 시작하자 혜진은 홍두식(김선호)과의 달콤한 순간을 떠올리고 미선을 뒤로 한 채 자신의 차로 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (202, 24946, 17957, '3화에서 서울로 당일치기 여행을 갔을 때 윤혜진(신민아)과 홍두식(김선호)이 이 건물을 지나가다 누군가 건물 안에서 몰래 사진을 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (203, 25735, 17216, '16회에서 강지민(연정훈) 가족과 지은수(이유리)가 이 카페에서 함께 저녁을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (204, 25736, 12933, '167회에서는 박해미(박해미), 이준하(정준하), 아들 이민호(김혜성), 절친 김범(김범)이 제주 여행으로 이 호텔에 머문다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (205, 25738, 5041, '서연(배수지)과 승민(이제훈)이 이곳에 와서 자신의 생일(11월 11일)이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (206, 25739, 5041, '이제훈의 엄마가 운영했던 순대국밥집이 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (207, 25740, 5041, '수업을 마친 유연석과 수지가 대화하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (208, 25741, 5041, '이제훈과 수지가 처음 만난 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (209, 25742, 5041, '한가인이 집을 지어달라며 엄태웅을 찾아오는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (210, 25743, 5041, '영화 속 정릉독서실로 나온 건물');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (211, 25744, 5041, '수지와 이제훈이 과제를 위해 살펴보던 마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (212, 25745, 5041, '엄태웅이 한가인에게 지어준 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (213, 25746, 5041, '한가인이 어릴 적 피아노를 배웠던 학원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (214, 25766, 15548, '26회 회상 장면에서는 치매에 걸린 죽은 염상구의 기억 중 하나가 아이들을 따는 기억으로 비오는 날 이 학교에 오는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (215, 25767, 15548, '최종회 32회(전체 16회)에서는 팀을 추적하던 용의자가 이 호텔에 있었던 것으로 밝혀져 붙잡혔다. 한편 강현(박은석)은 성진그룹의 변호사 윤태준(홍서준)을 만나기 위해 식당에서 만나자고 제안을 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (216, 25768, 15548, '27회에서 강현(박은석)은 이 건물 뒤에서 한 통의 전화를 받고 법의학 수사관 백범(정재영)의 아버지인 것으로 밝혀졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (217, 25769, 15548, '19회 플래시백에서 강현(박은석)과 서정민(백승훈)이 술을 마시러 온다. 강현은 정민에게 생일 선물을 주고, 강현은 정민이가 돌아가신 양언니 소희의 생일이기도 하다고 언급한다. 21화에서는 강현이 수상한 사람이 다른 테이블에서 자신들을 보고 있었다는 사실을 깨닫는 플래시백이 또 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (218, 25770, 15548, '검사 은솔(정유미)이 입주하고, 그 옆에는 법의학박사 백범(정재영)이 살고 있다. 11회에서는 강현(박은석), 강동식(박준규), 천미호(박희진)가 은솔의 새로운 집에 찾아오지만 실수로 옆집의 벨을 누르는 바람에 백범이 대신 문을열고 나오자 집단 충격에 휩싸인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (219, 25771, 15548, '드라마의 국과수 본부 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (220, 25772, 15548, '강현(박은석)이 검사를 그만둔 후 찾아온 어머니의 가게이다. 31-32회에서 백범(정재영)은 엄마를 만나기 위해 하루 쉬고 같이 식사한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (221, 24933, 15548, '검사 은솔(정유미)이 최화자를 자신의 카페에서 만나 이야기를 나눈다. 은솔은 실수로 커피를 자신에게 쏟고 화장실로 청소를 하러 가지만, 그녀가 나가면서 그녀에게 약을 먹이고 싶어하는 광란의 화자의 말을 듣게 된다. 그런 가운데 경찰관 강동식(박준규)과 차수호(이이경)가 카페로 향하며 아슬아슬하게 은솔을 구해낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (222, 25773, 15548, '13회에서 메르스 피해자로 의심되는 사람이 이곳에 상륙하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (223, 25774, 15548, '11회(전6회)에서는 이 지하철역 화면에 \'연습생\'에 대한 소식이 전해진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (224, 25775, 15548, '28회에서 살인 용의자 강치수가 일상의 일부로 이 식당을 찾아온 가운데 수사관 양(김영웅)이 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (225, 25335, 15548, '1회에서 은솔 (정유미)이 이 다리를 건너면서 새로운 검사를 소개한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (226, 25776, 15548, '이 식당은 의 25화와 26화에서 여러 번 등장한다. 백범(정재영 분) 법의학과 조교 정성주(고규필 분)와 함께 이곳에 온 것은 바비큐 돼지고기 껍질이 아니라 증거 검사로 생가죽을 얻기 위해서다. 그는 두 번째로 이곳에 와서 검사 은솔(정유미)과 합류하지만, 그는 피부를 얻자마자 떠난다. 성주와 형사 차수호(이이경 분), 독극물학자 스텔라 황(스테파니 리 분), 은솔이 세 번째로 제대로 된 식사를 하러 오는 모습이 이곳에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (227, 25777, 15548, '30회에서 법의학 수사관 백범(정재영)과 법의학 조수 정성주(고규필)가 특정 유형을 찾기 위해 찾아온다. 용의자가 살인 무기에 사용했을 수 있는 라이플 클리너 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (228, 25778, 15548, '법의학 수사관 백범(정재영)이 이곳을 자주 찾는다. 한 에피소드에서 그는 검사 은솔(정유)에게 이렇게 말한다. 자신의 오른손(교통사고로 다친)이 아직 건재하다는 것을 스스로 증명하기 위해 촬영에 임했다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (229, 25779, 15548, '22화(전체 11화)에서 천미호(박희진)가 정신분열증 환자 조한수의 언니가 운영하는 미용실 \'진헤어\'에 잠입하는 모습이 그려졌다. 머리를 손질하던 중 조가 들어와 누나를 공격하려 한다. 미호는 조에게 가위를 내려놓으라고 말하려 하지만 말을 듣지않고, 바로 그때 형사 양수동(김영웅)이 들어와 조가 공격을 가하기도 전에 그를 잡는 데 성공한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (230, 25780, 15548, '21회에서 형사 강동식(박준규)은 형사 차수호(이이경)와 강(민필준)을 이 곳으로 인도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (231, 25781, 15548, '검사 은솔(정유미)은 가족과 함께 이 \'아파트\'에 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (232, 25782, 15548, '살인 용의자 강치수가 소유한 스튜디오 \'검법남녀\' 속 \'수포토 스튜디오\'. 28회에서는 수사관 양(김영웅)이 스튜디오를 나와 여권 사진을 찍고 있는 손님으로 포즈를 취하고 있다. 29화에서 수사팀은 스튜디오에서 강치수를 체포한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (233, 25783, 15548, '11회에서 수사관 강동식(박준규)은 이 건물을 통해 용의자 김준태(김민규)를 쫓고 철교를 건넜다. 건물 뒤편으로 이어지는 곳. 검사 강현(박은석)과 팀원들이 멀리서 지켜보는 가운데 결국 동식은 반대편에서 준태를 계단 난간에 수갑으로 묶는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (234, 25784, 15548, '11회에서는 강현(박은석)이 이끄는 팀이 이 건물 앞에 도착하는 모습이 그려졌다. 두 사람은 항구에서 용의자 김준태(김민규)를 쫓던 은솔(정유미)과 강동식(박준규)을 찾기 위해 헤어졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (235, 25800, 13314, '이 건물 6층(6F)에 있는 HEM Cafe(현재 폐업)는 서인우의 사무실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (236, 25801, 13314, '2회에서 마혜리(김소연)와 서인우가 이 식당에서 만나 과거에 빌려준 돈을 갚는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (237, 25802, 13314, '마혜리(김소연)와 윤세준(한정수)은 오후 데이트를 위해 이 거리를 찾았다. 혜리는 남자친구가 생기면 하고 싶은 버킷리스트가 있었는데 그 중 하나가 바로 4월 중순에 손을 잡고 벚꽃 길을 걷는 것이었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (238, 25803, 13314, '드라마 제작 스틸컷 촬영 현장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (239, 25639, 13314, '9, 14, 15회에서 천수호(김정현)는 이 건물 입구에서 변호사 겸 검사 친구와 만나 도움을 청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (240, 25804, 13314, '마혜리(김소연)는 검사프린세스(SBS, 2010)에서 부모님과 함께 이 저택에 산다. 5화에서 서인우가 그녀를 집에 데려와 그녀가 차 안에서 잠을 자고 있을 때 그가 그녀를 편안하게 하기 위해 그의 가디건을 그녀의 목 아래에 두었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (241, 25805, 6335, '죄수 황정민과 검사 박성웅이 면담한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (242, 25806, 6335, '이성민이 천식호흡기를 한강에 던져 버린 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (243, 25807, 6335, '극중 강동원이 삼겹살을 쏜 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (244, 25808, 6335, '강동원이 검사에서 건달로 변장했던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (245, 25809, 6335, '검사외전에서 교도소로 나온 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (246, 25810, 16270, '7화에서 배타미(임수정)와 박모건(장기용)은 일이 끝난 뒤 데이트를 하러 이 해변에 방문한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (247, 25811, 16270, '임수정이 권해효를 만나러 갔다가 장기용과 만난 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (248, 25812, 16270, '두 사람은 처음에 몰려드는 팬의 숫자에 놀랐지만, 이날 입대하는 한민규(변우석)에게 눈물의 이별을 고하고 있다는 사실을 깨닫는다. 스칼렛은 결국 여운의 입맞춤과 따뜻한 포옹으로 지환을 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (249, 25813, 16270, '마지막 16회에서 여기 회의실에서 여성들은 정부와 기업의 개입으로부터 사용자를 보호하기 위해 포털 회사의 도덕률을 발표합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (250, 25814, 16270, '4화에서 배타미(임수정)와 이미 전 남자친구인 표준수와 식사를 했다는 사실을 알게 된질투심 많은 박모건(장기용)이 이 식당에서 밥을 먹습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (251, 25815, 16270, '9회 회상 장면에서는 설지환(이재욱)이 아직 살아있는 반려견 설리를 산책시키는 모습이 그려졌다. 이 모습은 Barro 거리 카메라로 촬영된 지도앱의 일부로 사용된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (252, 25816, 16270, '8화에서 오진우(지승현)는 고려대 본사 로비로 보이는 이 센터 로비 AMG 스피드웨이에서 사장의 아들을 고의로 피하다 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (253, 25019, 16270, '7회에서 차현/스칼렛(이다희)이 배타미(임수정)와의 대화를 생각하며 이 건물 뒤 도시공원을 거닐고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (254, 25817, 16270, '7회에서 배타미(임수정)와 잠자는 박모건(장기용)이 차를 몰고 이 다리를 건넌다. 그녀는 그가 그녀와 함께 있어서 기뻤다고 말하면서 잠들었음을 깨닫는다. 잠시 잠에서 깨어나 그녀에게 반복해 달라고 말하고, 그는 그녀의 손을 잡고 다시 잠이 듭니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (255, 25818, 16270, '2, 6화에서 본 태미가 살고 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (256, 25819, 16270, '박모건(장기용 분)이 유니콘 계정을 잃었다는 소식을 들은 배타미(임수정 분)는 3회에서 이 공원에서 박모건과 만난다. 그는 그녀에게 전화와 메시지에 응답하고, 그녀가 보고 싶을 때마다 만나라고만 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (257, 25820, 16270, '3화에서 배타미(임수정 분)가 자신의 생각을 정리하러 오는 장면. 여기에서 그녀는 박모건(장기용)의 전화를 받기 전까지는 휴대폰 서비스를 받을 수 없다. 모건은 비오는 날 그녀를 데리러 여기에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (258, 25821, 16270, '8회 슈퍼레이스 챔피언십을 보기 위해 KU그룹 오진우(지승현)가 경마장을 찾았다. 놀랍게도 그의 아내 송가경(전혜진)이 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (259, 25822, 16270, '1화, 2화에서는 스님이 아침기도를 마치고 고요한 사찰 밖을 걸어가는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (260, 25823, 16270, '차현/스칼렛(이다희) 집 외관, 그리고 9화에서 설지환(이재욱)이 첫 방문. 이후 12화에서 그녀를 다시 여기로 안내하고 에피소드 14에서 그녀를 만나려고 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (261, 25824, 16270, '15화에서 스칼렛/차현(이다희)과 설지환(이재욱)이 이 건물 앞에서 만나 데이트를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (262, 25825, 16270, '14회에서 송가경(전혜진)은 유니콘 대표이사 자리에 나인경(유서진)을 대신하고 나인경(전혜진)을 찾아 이 건물의 모임에 나타난다. 밖에서 가경은 인경을 초대해 고려대 회장인 장희은(예수정)에 맞서게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (263, 25826, 16270, '임수정이 전혜진의 막말에 상처 받는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (264, 25827, 16270, '차현(이다희)과 설지환(이재욱)은 이곳 주차장에서 차 뒤에서 일출을 함께 본다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (265, 25828, 16270, '설지환(이재욱 분)은 배우로 이 묘지에서 촬영을 하고 있다.  스칼렛(이다희)이 카메라 뒤에서 지켜보고 있다. 작업을 마친 그는 자신 있게 카메라를 향해 성큼성큼 걸어가 스칼렛에게서 1센티미터 떨어진 곳에서 멈춥니다. 손가락을 입술에 대고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (266, 25829, 16270, '5회에서 가경(전혜진)이 갑자기 나타나 상황을 엿듣는다. 그런 다음 타미는 유리벽에 있는 건물의 특징적인 십자형 계단을 걸어 내려갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (267, 25830, 16270, '11회에서 송가경(전혜진)이 차를 타고 이 카페를 지나가다가 스칼렛/차현(이다희)이 카페에 앉아서 열심히 일에 집중하는 모습을 발견한다. 이에 가경은 둘이 학창시절을 보낸 과거를 떠올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (268, 25831, 16270, '5회 회상장면에서 남일여고 학생이던 차현(이다희)은 이곳에서 송가경(전혜진)을 어떻게 만났는지 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (269, 25832, 16270, '드라마에서 바로오피스의 일부로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (270, 25833, 16270, '15회에서는 배타미(임수정), 송가경(전혜진), 브라이언 민(권해효)이 대통령의 초청으로 행사장에 참석하는 모습이 그려졌다. 청와대 요구에 따라 이용자 개인정보 누출사고에 동의한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (271, 25834, 16270, '7회에서 유니콘의 송가경(전혜진)이 웹툰작가 모임을 이 식당에서 연다.. 배타미(임수정)와 차현(이다희)이 아티스트 중 한 명을 바로로 쟁취할 방법을 찾기 위해 첩보하러 온 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (272, 25835, 16270, '1회에서 송가경(전혜진)이 이 술집에 찾아와 여자들과 어울리고 있는 남편 오진우(지승현)를 만난다. 남편에 대한 관심도 애정도 보이지 않는 가경의 모습은 \'일반적인\' 상황인 듯하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (273, 25836, 16270, '6회 배타미/태미(임수정)와 조아라/엘리(오아연)가 최고 만화 제작자를 만나지 전에 이 컨벤션홀 주차장에 도착해 주차를 하고 있다. 그의 열렬한 팬인 Ellie는 너무 긴장하지 않으려고 노력합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (274, 25837, 16270, '11회에서 배타미(임수정)는 방송 기수 윤동주(조)를 우연히 만나 식료품점에 옵니다. 동주는 타미를 보고 걸음을 멈추고 박모건(장기용)에 대해 묻기 위해 달려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (275, 25838, 16270, '차현(이다희)의 차로 청문회를 떠난 배타미(임수정)는 성수대교 바로 앞에서 내려달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (276, 25839, 16270, '임수정이 장기용에게 전혜진에 대해 털어놓는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (277, 25840, 16270, '12회에서 박모건(장기용)은 고등학교 친구 정다인(한지완)의 피아노 연주회에 참석합니다. 자신도 모르는 사이 배타미(임수정)도 참석한다. 복도에서 그를 발견했을 때, 그녀는 모건이 다인이 그녀에게 말한 첫사랑임을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (278, 25724, 16270, '검색어 16화에서 TV 인터뷰를 통해 마지막 일격을 날린 송가경 (전혜진), 배타미 (임수정), 차현 (이다희)는 컨버터블을 타고, 여의도에서 여의도로 좌회전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (279, 25797, 16270, '1회에서 자신의 삶을 마감하려고 하지만 결국 자신이 어디까지 추락해야 할지 깨닫고 닭을 잡는다. 10화에서 타미와 엘리(오아연)는 이 다리를 건너는 택시를 함께 타고, 서로 마음을 터놓고 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (280, 25669, 16270, '12회에서 박모건(장기용)과 배타미(임수정)는 이 쇼핑몰 4층 에스컬레이터에서 우연히 어머니와 마주쳤을 때, 타미는 자신을 모건의 여자친구라고 소개하는 대신 자신을 동료라고 소개하는데 실망을 금치 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (281, 25841, 16270, '14회에서 송가경(전혜진)과 오진우(지승현)는 이 영화관에서 열린 진우의 영화 시사회에서 팔짱을 끼고 등장합니다. 두 사람의 이혼 소식이 전해지자 그들은 주요 복도에서 떨어진 구석에 숨어 기자들을 피한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (282, 25842, 16270, '드라마의 등장하는 등장인물들이 이 지하철역에서 주로 출퇴근 하는 장면을 많이 촬영했다. \'유니콘 본사역\', \'선남역\', \'서울역\' 시리즈에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (283, 25843, 16270, '11회에서 박모건(장기용)이 동창회에 참석한다. 이번 동창에는 배타미(임수정)의 피아노 선생님 정다인(한지완)도 참석하고, 모건이 타미에게 말했던 \'첫사랑\'으로 밝혀진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (284, 25017, 16270, '13화에서 송가경(전혜진)은 유니콘 본사 직원과의 만남을 마치고 상하이에서 돌아온다. 입국장에 들어서자 자신을 기다리고 있는 오진우(지승현)를 보고 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (285, 25844, 16270, '임수정과 장기용이 처음 만난 로맨틱한 오락실');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (286, 25845, 16270, '아케이드에서 치열한 철권 배틀을 한 뒤 만난 배타미(임수정)와 박모건(장기용)은 1화(2화 회상)에서 이 카페에서 식사를 하고 방금 작곡한 음악을 그녀에게 들려준다. 그들은 또 다른 철권 배틀을 마치고 8화에서 이곳으로 돌아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (287, 25846, 16270, '1화에서 UNICON 대표로 배타미/타미(임수정)의 상원 청문회가 본 대학 벤처산업협력동 회의실에서 열린다. 태미는 주변 취재진을 빠져나갈 때 스칼렛이 경쟁사에 다니는 줄도 모르고 차현/스칼렛(이다희)의 차에 뛰어든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (288, 25847, 6222, '영화 검은 사제들 속 강동원의 숙소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (289, 25848, 6222, '강동원이 신학생으로 학교생활을 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (290, 25849, 6222, '박소담이 김윤석에게 테스트를 받는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (291, 25850, 6222, '영화 검은 사제들에 나오는 수녀원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (292, 25851, 6222, '강동원이 성당으로 들어갈 때 나온 부감 샷');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (293, 25734, 6222, '영화 검은 사제들의 차량 추격씬 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (294, 24959, 6222, '강동원이 김윤석의 부탁으로 성물을 가지러간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (295, 25852, 6222, '영화 검은 사제들에서 맨 처음에 등장하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (296, 25853, 18044, '6회에서 한지혁(남궁민 분)과 린웨이(옥자연 분)가 이 공원의 \'석주권필 기념비\'에 온다. 린웨이는 지혁에게 계속 하지 말라고 경고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (297, 25854, 18044, '도진숙(장영남)은 9회에서 백모사(유오성)를 이곳에서 만나 대치하고, 국정원팀은 이들이 해킹한 공개 카메라를 통해 현장을 대기하고 관찰한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (298, 25855, 18044, '8회에서 린웨이(옥자연)가 이 식당에서 그녀에게 정보를 얻기 위한 만남을 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (299, 25856, 18044, '5화에서 서수연(박하선)과 한지혁(남궁민)이 이곳에서 만난다. 수연은 그들이 과거 나눈 추억에 대해 이야기하기 시작하고 중요한 정보를 공유하려고 할 때 그녀는 지혁 앞에서 총에 맞는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (300, 25857, 12604, '18회에서 데이트 장소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (301, 25858, 12604, '첫 두 에피소드에서 고등학생 강준상(배용준)의 어머니가 그를 미국으로 데려오기 전에 사는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (302, 25859, 12604, '강준상, 정유진 동상 앞에서 셀카를 찍는다. 이 섬은 강준상(배용준)과 정유진(최지우)의 비밀장소다. 1화에서 처음으로 손을 잡고 첫 키스하고, 2화에서 눈사람을 만든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (303, 25860, 12604, '이민형(배용준)이 실명한 뒤 리하우스에서 은둔 생활을 하고 있는 곳이다. 정유진(최지우)은 마지막 20화에서 그를 찾아 키스로 재회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (304, 25335, 12604, '강준상/이민형(배용준)은 15화에서 어머니와 전화통화를 할 때 이 다리를 건너 운전을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (305, 25861, 12604, '16회에서 이민형(배용준)이 정유진(최지우)에게 별목걸이로 프러포즈한 곳. 그리고 그들은 제단 앞에서 입맞춤을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (306, 25862, 12604, '3회에서 정유진(최지우)이 약혼식장에 가는 길에 이민형(배용준)이 죽은 줄 알았던 곳이기도 하다. 약혼 파티는 까맣게 잊고, 그녀는 미친 듯이 그의 뒤를 쫓아가서 그를 찾으려고 한다. 헛수고다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (307, 25340, 12604, '정유진(최지우), 김상혁(박용하), 오채린(박솔미) 등이 모두 이 고등학교에 다닌다. 강준상(배용준)은 1화에서 신입생으로 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (308, 25863, 12604, '이민형/강준상(배용준)은 잠시 이 호텔에 거주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (309, 25796, 14625, '12회에서 한지훈(이서진)과 강혜수(유이)가 이혼하러 여기까지 온 후 바깥 계단에서 운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (310, 24944, 18814, '1회에서 서준형(이진욱)과 김나은(이연희)은 이곳에서 저녁식사를 하며, 그녀가 결혼 이야기를 꺼낼 때마다 그가 어떻게 결혼이라는 화제를 회피하는 것처럼 보이는지 알아차리기 시작한다. 화가 난 그녀는 밤을 끝내려고 하지만, 준형이 행운의 스크래치 쿠폰으로 그의 손을 시험해 보기로 결정했을 때, 결국 그들 모두에게 무료 식사와 요트 타기를 얻게 되면서, 그녀는 믿지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (311, 25864, 18814, '2회에서 서준형(이진욱)과 김나은(이연희)의 부모님의 만남은 이곳 이뤄진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (312, 25865, 13871, '4회에서 송지혜(남상미)와 강태욱(김지훈)은 이곳을 산책하며 서로를 알아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (313, 25866, 13871, '1회에서 송지혜(남상미)와 김현우(이상우)가 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (314, 25827, 17501, '드라마 두 주인공인 송원(이민영)과 서반(문성호)이 이곳에서 결혼식을 올린다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (315, 25867, 17501, '1화에서 판사현(성훈)과 송원(이민영)이 임신 사실을 알게 된 후 그는이 꽃집 앞에 멈춰서 그녀에게 축하 꽃다발을 사달라고 부탁합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (316, 25868, 17213, '최종화 16화에서 절친 김영희(안은진), 신현재(최찬호), 경우연(신예은), 이수(옹성우), 한진주(백수민), 진상혁(표지훈)이 여행 중 이 길을 따라가는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (317, 25869, 17213, '8화에서 이수(워너원 옹성우)는 경우연(신예은)을 따라 영화사 미팅을 하지만 계약이 불발되자 슬프고 화가 난 경우연을 위로해주기 위해 이 버스 정류장으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (318, 25827, 17213, '14회에서 이수(옹성우)는 미국을 통한 포토여행 중 밤에 이곳(미국의 도시인 듯)에서 정우연(신예은)에게 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (319, 24932, 17213, '9화에서 한진주(백수민)와 그녀의 단골 타이거슈가 직원(강윤제)이 데이트를 위해 이 식당을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (320, 24933, 17213, '이수(옹성우)가 8회에서 이 갤러리에 전시하는 어머니 최원정(김희정)을 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (321, 25870, 17213, '1회에서는 고교 시절 자원봉사를 위해 고교 동창들과 함께 이곳을 찾은 경우연(신예은)과 이수(옹성우, 전 워너원)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (322, 25871, 17213, '5화에서 경우연(신예은)과 온준수(ZE:A 김동준)가 이 식당에서 저녁을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (323, 24902, 17213, '온준수(김동준) 대표는 경우연(신예은)은 4회에서 저녁 데이트를 위해 이 식당을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (324, 25872, 17213, '8회 밤 이 다리에서 이수(워너원 옹성우)가 다리로 나온다. 자신이 찍은 경우연(신예은)의 지갑에 넣고 다니는 것 외의 사진을 불태운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (325, 24937, 17213, '10회 진상혁(표지훈)의 타이거슈가 직원(강윤제)과의 데이트로 이곳에 온다. 화장실에 있는 동안 상혁은 진주의 남자친구가 다른 여자와 통화하는 것을 우연히 듣고 그가 금을 캐는 사람이라는 것을 이해하고 진주와 헤어지자고 요구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (326, 25873, 17213, '경우연(신예은)과 이수(워너원 옹성우)는 연남동을 탐험하며 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (327, 25659, 17213, '크리스마스 이브 저녁 외출 중인 정우연(신예은)과 이수(옹성우)는 1화에서 이 공원을 건너며 다음날 미국으로 유학을 떠난다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (328, 25726, 17213, '3회에서 이수(워너원 옹성우)가 술에 취한 경우연(신예은)을 집으로 데려가면서 이 거리를 걸었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (329, 24939, 17213, '3화에서 술에 취한 경호연(신예은)이 회식 후 이 길을 따라 집으로 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (330, 25874, 17213, '경우연(신예은)과 이수(워너원 옹성우)는 이 \'가게\'와 브런치 카페를 지나 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (331, 25875, 17213, '경우연(신예은)과 이수(워너원 옹성우)는 연남동 구경한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (332, 25661, 17213, '4화에서 이수(워너원 옹성우)와 경우연(신예은)이 은유출판사의 커먼북 프로젝트를 위해 이 거리에서 첫 동네 탐방을 마무리한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (333, 25876, 17213, '1회에서 신현재(최찬호)와 김영희(안은진)가 이 식당에서 심야 회식을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (334, 25877, 17213, '9회에서 온준수(김동준)가 이 식당으로 경우연(신예은)을 저녁 데이트에 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (335, 25667, 17213, '7화에서 경우연(신예은)과 온준수(ZE:A 김동준)가 이 카페에 온다. 여기에서 그녀는 영화의 서예 작업에 합격했다고 그에게 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (336, 25878, 17213, '7회에서 경우연(신예은)과 온준수(ZE:A 김동준)는 이 역 3번 출구 근처 성수동 카페거리를 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (337, 25879, 17213, '경우연(신예은)과 온준수(ZE:A 김동준)는 7화에서 신화인쇄사 옆을 지나가고 있다. 이 때, 준수도 바이산이 있는 건물을 사진에 담는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (338, 25880, 17213, '7화에서 경우연(신예은)과 이수(워너원 옹성우)는 이 루프탑 카페에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (339, 25881, 17213, '7회에서 경우연(신예은)과 이수(워너원 옹성우)가 역 1번 출구에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (340, 25727, 17213, '데이트 중에 경호연(신예은)과 온준수(김동준)가 이 식당을 지나간다. 에피소드 후반, 우연은 서울 이야기에서 다룬 일곱 번째 동네인 문래동을 탐험하던 이수(옹성우)와 함께 이곳으로 돌아와 소나기가 내리는 동안 이 식당 맞은편에 은신처를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (341, 25882, 17213, '마지막 16회에서 경우연(신예은)과 이수(워너원 옹성우)가 데이트를 위해 이 공원을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (342, 25335, 17213, '8화에서 정우연(신예은 분)과 온준수(김동준 분)가 자신의 차를 타고 이 다리를 두 번이나 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (343, 25347, 17213, '15화에서 절친 경우연(신예은), 김영희(안은진), 한진주(백수민)가 밤이 되면 이 공원 멀티플라자에서 시간을 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (344, 25883, 17213, '경우연(신예은)과 이수(워너원 옹성우)는 후암동 탐방 중 노가리팩토리 등의 가게들을 지나며 이 시장을 걷게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (345, 25884, 17213, '5화에서 경우연(신예은)과 이수(옹성우)가 후암동 탐방을 마친 후, 그들의 책 \'서울의 이야기\'에서 세 번째로 다룬 동네다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (346, 25885, 17213, '16회 최종회에서 경우연(신예은 분)과 이수(워너원 옹성우 분)가 이 거리를 따라 다투는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (347, 25886, 17213, '15회에서 경우연(신예은)과 이수(옹성우)가 결별한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (348, 25887, 17213, '경우연(신예은)과 이수(워너원 옹성우)가  6화에서 자신들의 책 \'서울이야기\'에 수록된 다섯 번째 동네인 북촌을 탐험하는 동안 이 거리에서 사진을 찍고 있다. 이수가 우연의 사진을 찍는 곳도 이곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (349, 25888, 17213, '경우연(신예은)과 이수(워너원 옹성우)는 이 카페를 찾는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (350, 25889, 17213, '6회에서 경우연(신예은)과 이수(워너원의 옹성우)는 책 서울의 이야기에서 다룬 네 번째 동네인 청운효자를 탐험하는 동안 이 아케이드에서 철권을 즐깁니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (351, 25890, 17213, '6회에서 경우연(신예은)과 이수(워너원 옹성우)는 책에서 다뤘던 다섯 번째 동네인 북촌을 탐방하던 중 숙소 외벽 벤치에 잠시 앉아 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (352, 25891, 17213, '6회에서 경우연(신예은)과 이수(워너원 옹성우)는 청운효자를 찾아 이 집을 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (353, 25892, 17213, '김영희(안은진)는 이 패스트푸드점 아르바이트를 하는 모습을 보여준다. 남자친구 신현재(최찬호)는 퇴근 후 그녀를 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (354, 25893, 17213, '김영희(안은진)와 신현재(최찬호)는 10회에서 이 식당에서 그의 생일을 축하한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (355, 25894, 17213, '이곳에서 정우연(신예은)과 이수(옹성우)가 만남을 갖고 주차장에 막 도착하려 할 때 이수는 교통사고를 당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (356, 25895, 17213, '5화에서 경우연(신예은)과 이수(워너원 옹성우)는 이 카페를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (357, 25896, 17213, '8회에서 경우연(신예은)과 이수(워너원 옹성우)가 책 <서울의 이야기>를 위해 서울을 탐방하던 마지막 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (358, 25897, 17213, '이수(옹성우)는 이 자리에서 최종회 16회에 전시회를 열고,  경우연(신예은)이 드디어 재회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (359, 25781, 17213, '10회에서 이수(워너원 옹성우)가 부모님과 함께 이 식당에서 저녁을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (360, 25864, 17213, '9~10화에서 이수(워너원 옹성우)와 정우연(신예은)의 서예와 서울 화보집이 완성된 후, 온준수(제아 김동준)는 10화에서 이 식당에 두 작가를 포함한 스태프들을 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (361, 25017, 17213, '1화에서 이수(워너원 출신 옹성우)가 미국행 비행기에 오르기 직전 경우연(신예은)이 이수 공항에서 마중을 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (362, 25898, 17213, '제주도에 있는 동안 이수(워너원 옹성우)는 이 서점 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (363, 25899, 17213, '경우연(신예은)은 2회에서 이 호스텔에서 2주간 알바를 하고 있다. 이곳은 이수(워너원 옹성우)가 제주도에서 화보 촬영 중 머물던 곳이기도 하다. 주인은 이수의 오랜 친구이자 오랜 친구다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (364, 25900, 17213, '이수(워너원 옹성우)와 동행한 경우연(신예은)은 2회에서 자신의 서예 엽서 전시 및 판매에 대해 서점에 문의한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (365, 25901, 17213, '2회에서 3년 후 경우연(신예은)과 이수(워너원 옹성우)는 제주도를 탐험하던 중 우연히 이 언덕에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (366, 25902, 17213, '2회에서는 이수(워너원 옹성우)가 제주도 여행 중 발목을 삐끗한 경우연(신예은)을 업은 장소로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (367, 25903, 17213, '경우연(신예은)은 2회에서 자신의 서예 엽서 전시 판매에 대해 서점에 문의하고, 예의없는 주인이 이를 알아차린다. 그녀는 떠나면서 온준수(ZE:A의 김동준)와 마주친다. 우연은 자신의 엽서 중 하나가 바닥에 떨어져 상점 주인이 밟고 있는 것을 보고 상점에 다시 들어가 엽서를 가지고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (368, 25904, 17213, '2회에서 이수(옹성우)와 경우연(신예은)이 가게를 찾았다. 그녀와 함께 다른 서점에 가서 그녀의 캘리그라피 엽서를 전시할 수 있도록 두고간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (369, 25905, 17213, '경우연(신예은)은 친구 이상의 사건 14회에서 이수(워너원)와 이별한 뒤 이 버스로 가는 길에 멍하니 앉아 버스를 기다린다. 그녀의 눈에 띄지 않는 온준수(김동준) 그녀를 따라 이곳으로 온 거예요');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (370, 24913, 17213, '1회에서 열애 100일째 되는 날, 경우연(신예은)은 이곳에서 남자친구와 헤어져 전 남자친구를 12명으로 늘린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (371, 25906, 17360, '1회에서 고(故) 장철정(성지루 분)의 장례에 최장물(안석환 분) 대표가 조문하는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (372, 25907, 17360, '4회에서 신명희 시장(최광일)이 언론에 정진시의 향후 발전 방향을 언론에 알리는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (373, 25908, 17360, '드라마 안에서 등장하는 악마에게 살해를 당하는 전기환 전 태신그룹 회장(이도경)의 사무실로 4-6회에서 자주 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (374, 25909, 17360, '7화에서 신명휘 시장(최광일)과 대표(김귀선)가 이 식당에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (375, 25910, 17360, '11회에서 가모탁(유준상)은 7년 전 형사 소권(전석호)가 이 식당의 그의 테이블에 합석하여 그에게 신 시장과 김영님 사이의 관계에 대해 알려주던 일을 회상합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (376, 25799, 17360, '1화에서 절친 소문(조병규), 임주연(이지원), 김웅민(김은수)이 등교길에 이 집을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (377, 25911, 17966, '경찰학교 캠퍼스 인근에 위치한 펍 \'고씨맥주\'로 등장한다. 학원의 학생들은 물론 교직원들 사이에서도 인기가 많다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (378, 24945, 17966, '1회에서 강선호(B1A4 출신 정진영)와 그의 양가인 윤승범(최우성), 윤택일(오만석)은 외식을 마치고 집으로 돌아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (379, 25912, 17966, '오강희(정수정)와 강선호(정진영)는 7회에서 강희의 어머니 재판에 참석한 후 이 \'정원\'에 앉아 마음을 터놓고 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (380, 25913, 17966, '유동만(차태현)은 1회에서 불법 카지노 조직의 흔적을 찾아 이곳에서 잠복 도박을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (381, 25914, 17966, '2화에서 경찰대학 입학 시험을 위해 강선호(전 B1A4의 정진영)와 그의 동료들이 훈련하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (382, 25915, 15865, '1회에서 정이현(윤현민)과 김금(서지훈)은 선옥남(고두심/문채원)이 선옥남(고두심/문채원)을 우연히 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (383, 25916, 15865, '최종화 16회에서 이함숙(전수진)이 정이현(윤현민)에게 코트를 사러 왔다. 점원에게 사치스럽지만 시골에서 입기 불편하고 부끄럽다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (384, 25917, 15865, '출판사가 회의에서 그녀의 에로스 소설을 출판하고 싶어한다는 사실을 알게 되어 조금 당황스럽습니다. \'점순호랑이\' 기사가 나온 후 출판사 관계자는 금에게 전화를 걸어 \'점순호랑이\'라는 기사가 좋은 반응을 보인 탓에 마케팅 활동을 할 의향이 있는지 물었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (385, 25918, 15865, '3회에서 \'계룡역\'에 오선생 삼인조(안길상), 산신/마법사 박(김민규), 선녀 오(황영희)가 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (386, 25919, 15865, '이 정자는  요정 왕국의 일부로 등장한다. 산신/마법사 박(김민규)은 다른 요정의 등 뒤에서 요정의 세레나데를 부르다 붙잡혀 북극성의 주인이 그를 인간계로 추방한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (387, 25653, 15865, '이곳은 인간계에서 선옥남(문채원)과 그녀의 나무꾼 남편이 살았던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (388, 25920, 15865, '마마요정과 계령의 \'선녀다방\'이 있는 곳입니다. 1회에서 정이현(윤현민)과 김금(서지훈)은 우연히 숲 속에 숨겨진 카페를 발견하고 그곳에서 선옥남(고두심/문채)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (389, 25921, 15865, '11회에서 점순(구구단 강미나)의 행방을 걱정하는 김금(서지훈)의 운전 선옥남(문채원)은 그녀의 수제맥주집을 알아보기 위해 이 거리로 향한다. 차 안에서 기다리던 옥남은 술에 취한 소녀가 술집에서 나와 입구 앞에서 쓰러지는 것을 보고 즉시 그녀를 도우러 나갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (390, 25922, 15865, '12회에서 학원에 레슨을 받기 위해 찾아온다. 학교에 다니던 중 갑자기 나타난 조봉대(안영미)를 보고 수업을 건너뛰고 학교를 떠나고 싶어질 정도로 겁이 난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (391, 25923, 15865, '12회에서 엄경술(유정우)은 학원을 나와 이곳에 왔다. 조봉대(안영미)를 보고 충격받아 가게에서 소시지를 먹고, 경술은 봉대를 즐겁게 하기 위해 비명을 지르며 뛰쳐나갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (392, 25924, 15865, '이는 정이현(윤현민 분)이 강의하는 곳이자 선옥남(고두심 분/문채원 분)이 근무하는 베이반스 커피 카페가 위치한 \'엄마요정과 나무꾼\' 이원대의 배경이다. 2회에서는 옥남과 딸 전섬(구구단 강미나)이 머리 약속을 마치고 카페로 돌아가기 위해 대학 입구를 향해 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (393, 25925, 15865, '9회에 정이현(윤현민)과 김금(서지훈)이 등장한다. 술 취한 이함숙(전수진)의 부탁으로 콜라를 산다. 무뚝뚝한 금이 이현에게 사전 예고도 없이 선옥남(문채원)을 초대하자고 말하지만, 이현은 자신에 대한 감정 때문에 이를 무시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (394, 25926, 15865, '7-8회 선옥남(문채원)과 이함숙(전수진) 이현을 만나러 가는 길에 집 밖에서 여러 번 김금을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (395, 24939, 15865, '선옥남(문채원 분)은 8회에서 자신의 집 밖 이 거리에서 김금(서지훈 분)과 이야기를 나눈다. 이후 김금은 이현을 만나러 가는 이함숙(전수진 분)과 마주치며 자신의 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (396, 25927, 15865, '3회에서 과식비를 내고 간신히 탈출한 정이현(윤현민)과 김금(서지훈)은 이 공원의 쉼터를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (397, 25928, 15865, '2회에서 선옥남(고두심)과 그녀의 딸 전섬(구구단 강미나)은 호기심 어린 시선으로 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (398, 25929, 15865, '11회에서 점순(구구단 강미나)은 세빛섬을 바라보며 데이트를 한다. 16회 마지막회에서 김금(서지훈)은 육식을 금하고 마침내 인간의 모습을 유지한 점순을 만나기 위해 이곳을 찾는다. 푸드트럭에서 소시지를 사고 있던 중 경술은 점순에게 다가가 사과한다. 김금은 천계에서 지상으로 돌아온 선옥남(문채원)과 재회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (399, 25930, 15865, '14화에서 구선생(안길상)과 산신/박마법사(김민규)가 계룡으로 돌아가는 기차를 이 역에서 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (400, 25931, 15865, '3화에서 요정이 인간의 영역에서 어떻게 살고 자신의 정체도 모를 수 있는지에 대한 호기심으로 사람들을 보고있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (401, 25932, 15865, '3화에서는 아직 실현되지 않은 오류의 코미디에서 구사부(안길상), 산신 박(김민규), 오요정(황영희)의 3인조 요정이 역에서 의기양양하게 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (402, 25933, 15865, '5화에서 구사부(안길상), 산신/마법사 박(김민규), 오요정(황영희)의 3인조 요정 오(전)남편을 찾아 \'공감고용공감\'에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (403, 25934, 15865, '1회에서 정이현(윤현민)과 김금(서지훈)이 마을 파티에 참석하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (404, 25935, 15865, '이 정자는 4회에서 머리요정이 선녀의 죽음을 알릴 때 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (405, 25936, 13491, '드라마의 주요 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (406, 25859, 14087, '9회에서 이민석(서인국 분)과 정수영(이하나 분)이 데이트 장소로 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (407, 25937, 14087, '13회에서 두 자매인 정수영(이하나)과 정유아(이율음)는 이 시장에서 아이스크림을 사서 비가 그치기를 기다리며 먹는다. 이후 에피소드에서 이민석(서인국)과 수영은 밤에 그녀를 집으로 데려다 줄 때 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (408, 25938, 14087, '이민식(서인국)이 다녔던 고등학교이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (409, 25939, 15238, '고독재(이이경) 아버지의 식당이 있는 거리다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (410, 24934, 15238, '12화에서 마진주(장나라)와 최반도(손호준)의 두 신 도플갱어가 하늘정원에서 싸우는 장면은 바로 이곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (411, 25940, 15238, '최종회 12화에서 마진주(장나라 분), 최반도(손호준 분)가 아들, 부모님과 함께 외식했던 식당이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (412, 25941, 15238, '이곳은 드라마 안에서 최반도(손호준)와 마진주(장나라)가 다닌 학교로 1999년으로 시간여행을 다녀온 뒤 다시 다니는 \'한국대학\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (413, 25942, 15238, '2회에서 시간여행을 마치고 참석한 최반도(손호준). 이곳이 처음 만난 곳임을 깨닫고 역사가 되풀이되지 않도록 최선을 다한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (414, 25943, 15238, '4화에서 민서영(고보결)과 최반도(손호준)가 데이트를 하러 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (415, 25944, 15238, '3회에서 1999년으로 돌아가보면 안재우(허정민)와 윤보름(한보름)이 여기서 첫 데이트, 재우가 취해서 기절할 때까지 막걸리를 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (416, 25796, 15238, '1회에서 이혼 후 마진주(장나라 분)와 최반도(손호준 분)가 법원을 나서는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (417, 25945, 15238, '하늘에서 데이트 후 최반도(손호준)는 행복한 작별인사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (418, 25946, 15238, '2회에서 최반도(손호준)가 마진주(장나라)를 차버렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (419, 25947, 9461, '김지후(신현수)와 서윤찬(고민시)은 지후의 사진교실에서 사진을 찍으러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (420, 25819, 9461, '김지후(신현수)가 이 공원에서 1일 사진반 학생을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (421, 25794, 9461, '사진 수업이 끝난 후 밥을 먹을 곳을 정하던 중 김지후(신현수)와 서윤찬(고민시)은 그녀가 대학 시절 그에게 말했던 그녀가 가장 좋아하는 음식을 모두 회상하며 이 건물(비벌리힐스 치즈케이크 바로 앞)을 지나게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (422, 25948, 18530, '차영민(비/정지훈)과 장세진이 8회에서 유이(Uee)가 故 장광덕(이문수) 회장에게 조의를 표하는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (423, 25949, 18530, '11회에서 한승원(태인호)은 이 카페에서 안태현 박사(고상호)를 만나 한 번의 잘못된 움직임이 곧 깨어난 환자가 그렇게 할 수 없도록 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (424, 25950, 18530, '드라마의 \'은상대학교 의료원\' 병원 건물로 차영민(비/정지훈), 고승탁(김범) 등 주요 캐릭터들이 의사로 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (425, 25951, 18530, '11회에서 회상을 하며 차영민(비/정지훈)은 눈물을 흘리는 장세진(유이)에게 어떻게 그의 손이 다쳤는지 설명합니다. - 복면을 쓴 남자에게 맞았다는 것과 같은 중요한 디테일을 빠뜨린채 이 카페 빌딩의 바깥 벤치에 앉아 이야기합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (426, 25613, 18530, '16화에서 장세진(유이)은 이 식당에서 장민호(이태성)와 만나 아버지 변호사의 도움을 받아 경찰에 체포된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (427, 25952, 18530, '7,8회에서 이 술집에서 만남을 갖는다. 8회에서는 장민호(이태성)의 계획이 무산되자 승원과 함께 술을 마신다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (428, 25953, 18530, '8화에서 고승탁(김범)이 장광덕(이문수) 회장의 변호사를 만나러 이 호텔 입구로 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (429, 25724, 18530, '3, 4화에서 고승탁(김범)의 몸으로 차영민(비)은 이 다리를 건너 도심으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (430, 25668, 18530, '15회에서 한승원(태인호)은 이 다리에서 장민호(이태성)를 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (431, 25954, 18530, '2회에서 전 여자친구 장세진(유이)의 문자를 받은 것으로 추정되는 차영민(정지훈)이 만남의 장소로 달려가 그녀를 기다리는 모습이 그려진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (432, 25955, 18530, '이 집에서 경제학 교수와 그의 아내가 살해당했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (433, 25956, 18530, '드라마의 \'은상병원\'의 일부로 나온다. 1회에서 차영민(비/정지훈)과 고승탁(김범)이 처음 만나는 장면에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (434, 25957, 18530, '차영민(비/정지훈) 고승탁(김범) 몸에 빙의하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (435, 25641, 14517, '13회에서 최세훈(정일우)이 유이령(진세연)을 이 식당으로 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (436, 25607, 14517, '이 공원의 메타세콰이어 길은 도해강(김현주)과 최지에게 특별한 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (437, 25958, 14517, '10회에서 유이령(진세연)은 장 대표(문세윤)와 \'비스트맨\'(태항호)을 만나기 위해 이 북카페에 온다. 장 대표가 최세훈(정일우)을 욕하기 시작할 때, 그 뒤에 세훈 자신이 나타나 목을 조른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (438, 25959, 14517, '1회에서는 이 역과 연결되는 고가도로에서 불구가 된 거지를 지나가다가 경찰을 피해 달아난 후 그가 가짜임을 깨닫는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (439, 25960, 14517, '2회에서 유이령(진세연)을 쫓기 위해 소속사 사옥을 빠져나와 이 건물의 야외 공간으로 뛰어드는 장면에 나왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (440, 25335, 14517, '최세훈(정일우)과 허부장(이시언)은 6화에서 이 다리를 건너 운전을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (441, 25961, 14517, '1회에서 지효의 부서장 허(이시언)가 지하철역 1번 출구에서 강민주(씨스타 윤보라)를 쫓는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (442, 25962, 14517, '1화에서 카페 파스쿠찌를 지나 대명길을 향해 이 길을 따라 강민주(보라)를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (443, 25026, 14751, '12회에서 상사와 촬영지를 한 번, 15회에서 강태호(김영광)와 첫 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (444, 25963, 14751, '16화의 플래시백에서 보듯 황지훈(이지훈)은 부모님의 사업이 파산할 때까지 부모님과 함께 살았던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (445, 25640, 14751, '13화에서 강태호(김영광), 고호(소녀시대 권유리)는 팀장인 박진희와 미팅 후 자신을 데리러 오도록 주선했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (446, 25607, 14751, '최종 10회에서의 데이트하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (447, 25964, 14751, '1화에서 금요일 밤, 고호(권유리/소녀시대 유리)가 8번 출구에서 버스를 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (448, 25965, 14751, '주인공들이 일하는 광고회사 \'이우\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (449, 25966, 14751, '8회에서 황지훈(이지훈)을 중심으로 한 팀은 로맨스를 소재로 한 가능한 광고 주제를 상상하고 이 광장에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (450, 25967, 14751, '5화에서 강태호(김영광)가 이 스튜디오 문 앞에서 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (451, 25662, 14751, '11회에서 모델 양진경과 함께 고객의 소식을 전합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (452, 25910, 14751, '14화에서 고호(권유리), 강태호(김영광), 그리고 그의 라이벌들이 저녁식사를 위해 이곳에서 모입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (453, 25968, 6426, '곡성 홍보 사진에 등장했던 길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (454, 25969, 6426, '곽도원이 집을 부수며 마을을 떠나라고 외친 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (455, 25989, 18417, '윤재희(수애)가 운영하는 \'아트스페이스 진\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (456, 25990, 6736, '공조 엔딩장면에서 북한 장면으로 나온 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (457, 25991, 6736, '공조에서 현빈이 창문으로 뛰어내린 장면을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (458, 25992, 6736, '공조 첫 장면에서 회의가 열린 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (459, 25993, 6736, '공조에서 도심 추격 액션장면을 촬영한 거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (460, 25994, 6736, '공조에서 추격신을 촬영한 이태원의 먹자거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (461, 24948, 6736, '공조에서 유해진이 거짓말을 들키는 장면을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (462, 25945, 13494, '드라마 예고편과, 홍보 포스터에 CG처리된 근정전의 전각과 인공섬의 돌 난간만 남아있는 이곳의 장소가 촬영되었다. 이세령(문채원)은 돌 난간만 남아 있는 이 정자 난간에서 뒤로 넘어지는 장면을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (463, 25605, 14808, '4회에서 서도우(이상윤)가 죽은 양녀 애니(박서연)의 흔적을 따라 이곳을 찾았다. 애니는 그녀의 진짜 아버지를 찾으러 여기로 왔었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (464, 25995, 14808, '이곳은 고은희(예수정), 서도우(이상윤)와 그의 아내 혜원(장희진)이 일하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (465, 25996, 14808, '10-11회에서 최수아(김하늘 분)가 남편과 만나 이사를 가는 이야기를 하는 곳이다. 그녀가 떠나면서 서도우(이상윤)는 우연히 같은 장소에 들어선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (466, 25997, 14808, '3, 4회에서 애니(박서연)가 안식처가 된 곳이고 서도우(이상윤)가 그녀를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (467, 25998, 14808, '최수아(김하늘)의 딸 박효은(김환희)이 다니는 제주도 학교이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (468, 24933, 14808, '16회에서 수아가 남편과 헤어진 뒤 오후 2시에 최수아(김하늘)와 서도우(이상윤)가 만나는 곳이기도 하다. 그들은 그 순간에 그들의 관계를 당분간 문자 메시지 관계로 유지하기로 결정한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (469, 25999, 14808, '3화에서 주인공 김하늘이 여기서 서도우(이상윤)와 통화를 자주 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (470, 26000, 14808, '1회 서도우 (이상윤) 밤늦게 딸을 보여주기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (471, 26001, 14808, '4화에서 김혜원(장희진)이 약속을 잡으러 가는 곳 공항 가는 길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (472, 26002, 14808, '최수아(김하늘)는 8회에서 일을 그만둔 직후 딸과 함께 이곳으로 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (473, 26003, 14808, '최수아(김하늘)와 박진숙(신성록)이 드라이브한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (474, 25017, 14808, '이곳은 최수아(김하늘)와 박진숙(신성록)의 작업실이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (475, 26004, 14808, '최수아(김하늘)와 딸이 제주도에서 사는 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (476, 26005, 14808, '10회에서 최수아(김하늘 분)가 딸과 함께 제주도에 도착해 이곳으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (477, 26006, 14808, '10회에서 최수아(김하늘)가 딸과 함께 제주도에 도착해 등대를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (478, 26016, 14105, '지해수(공효진)은 이 병원에서 근무하고 있으며, 이곳이 장재열(조인성)이 입원하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (479, 25829, 14105, '2회에서 문강태(김수현)가 책을 읽다가 부상을 당하고 이상인(김주훈)이 출판사를 찾아가는 모습이 그려졌다. 강태가 작가로 인한 피해로 소송을 제기할까 걱정돼 출판사를 찾아달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (480, 26017, 14105, '지해수(공효진)와 장재열(조인성)의 첫 키스가 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (481, 26018, 14105, '이곳의 벽화는 지해수(공효진)의 환자 중 한 명이 그린 것이다. 7회에서 그녀는 그를 이곳에서 만나고 그는 자랑스럽게 벽화들을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (482, 25722, 14105, '4화에서 장재열(조인성)과 한강우(도경수)가 두 번 만나는 곳이다. 먼저 재열은 강우에게 화를 내며 돈을 던지고 아버지가 또 때리려 하면 도망치라고 한다. 다음에 이곳에서 피범벅이 된 강우를 보고 강우는 재열에게 자신이 아버지를 상대로 어머니를 지켰다고 자랑스럽게 말하고 재열은 반박합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (483, 26019, 14105, '4회에서 데이트 앱에서 해준(김무준)이 한 여자를 만나러 떠나는 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (484, 26020, 3940, '극 중 신하균과 여진구가 앉아있던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (485, 26021, 3940, '마지막회 이동식 형사(신하균)과 한주원 경위(여진구) 애틋한 인사를 나누던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (486, 26022, 3940, '극 중 신하균과 여진구가 거닐며 대화를 나누던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (487, 26023, 3940, '만양파출소에 근무하는 경찰들의 회식장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (488, 26024, 3940, '이동식 형사(신하균)과 한주원 경위(여진구)가 생선국수를 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (489, 26025, 3940, '극 중 신하균과 여진구가 식사한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (490, 26026, 3940, '첫회 이동식 형사(신하균)의 동생 유연이가 등장한 성당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (491, 26027, 3940, '괴물 영화 인트로 다음 씬으로 나온 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (492, 25882, 3940, '영화 괴물이 가장 많이 등장한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (493, 25797, 3940, '괴물이 나타나 강두네 가족과 사투를 벌이는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (494, 26028, 18158, '10,11회에서 허성태(최대철)는 여행길에서 이 다리를 건너던 중 떨어지고 만다. 목소리가 들려 올려다보니 K/송이경(김혜준)이 보인다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (495, 26029, 18158, '2회에서 구경이(이영애)가 동료 게이머 \'멜론머스크\'가 자해하는 것을 막기 위해 이 건물에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (496, 26030, 18158, '11회에서 용숙(김해숙)은 의원을 만나기 위해 이 식당의 프라이빗 룸에서 기다리지만 그녀는 오른팔 김과장(정석용)에게 의원이 나타나지 않을 것이라는 전화를 받게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (497, 26031, 18158, '7회에서 고담(김수로)의 \'PEACEHERO2\' 런칭 시사회가 이곳에서 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (498, 26032, 18158, '1회 과거 회상으로 구경이(이영애)가 김민규(김강현)와 가족들이 이 마을 벽화 앞에서 함께 사진을 찍는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (499, 26033, 18158, '7회에서 패닉에 빠진 K/송이경(김혜준)이 이 건물을 지나 거리로 달려가지만, 길 한복판에서 그녀를 향해 다가오는 차와 마주하며 선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (500, 26034, 18158, '3회에서 대학축제가 열리는 곳, 그곳에서 K/송이경(김혜준) 여자친구의 불법 영상물을 유포하고 벌금을 물고 풀려난 박규일(신윤섭)을 죽이기 위한 교묘한 음모를 꾸민다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (501, 26035, 18158, '현재 K는 그녀의 상실을 극복하거나 억누르는 방법으로 여기로 돌아옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (502, 26036, 18158, '11회에서 팀은 이 피자 전문점에 옵니다. 오경수(조현수)는 K/송이경(김혜준)이 건너편 상오빌딩에서 자신을 지켜보고 있다는 사실도 모르고 아울렛을 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (503, 26037, 18158, '이곳은 7화에서 변호사 고담(김수로)이 근무하는 사옥으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (504, 26038, 18158, 'JTBC \'구경이\'(구경이) 8회에서 송이경(김혜준)이 이 호텔에서 구경이(이영애)를 만나자고 한다. 그러나 K는 그녀를 직접 만나지 않는다. 오히려 그녀는 외부 \'이벤트홀\'에서 경이를 감시한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (505, 25773, 18158, '나제희(곽선영)가 이끄는 B수사대는 K/송이경(김혜준)을 잡기 위해 5회에서 이 공항에서 대규모 작전을 수행한다. 안타깝게도, 그녀는 나타나지 않지만, 구경이(이영애)는 그녀를 다른 곳으로 이끄는 예감이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (506, 26039, 18158, '11회에서 욱(이홍내)이 이 호텔의 콘래드 스위트룸에 입성했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (507, 25347, 18158, '8회에서 안건욱(이홍내)과 이대호(박강섭)는 대호에게 건욱의 \'사촌\'으로 알려진 K/송이경(김혜준)과 마주치고 이 공원으로 조깅을 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (508, 25854, 18158, '2화에서 K/송이경(김혜준)은 클럽에서 하룻밤을 보낸 후 다음날 이곳 보드워크에서 일어난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (509, 26040, 18158, '1회에서는 구경이(이영애)를 아파트에서 끌어낸 후 나제희(곽선영)가 이 식당으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (510, 26041, 18158, '4회에서 구경이(이영애)가 카페에서 시간을 보내는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (511, 26042, 18158, '7회에서 김부장(정석용)이 멸치국수 먹으러 포장마차 먹으러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (512, 25912, 18158, '건욱(이홍내)은 3화에서 동료 대호(박강섭)가 자신의 개를 산책시키자 이곳에서 만난다. 4화에서는 산타(백성철)가 이 산책로를 통해 K/송이경(김혜준)을 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (513, 25931, 18158, '여기에서 K/송이경(김혜준)이 용숙(김해- 숙) 인질이지만 어린 소녀는 결국 구경이(이영애)가 다시 한 번 자신의 계획을 망쳤다는 것을 알게 된다. 외부 건물은 경이가 강당에서 탈출하여 거리 수준으로 향하는 마지막 12화에서 볼 수 있습니다. 이후 행사장에 폭탄이 터졌다는 소식을 듣고 나제희(곽선영)와 오경수(조현수)가 달려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (514, 26043, 18158, '성추행 가해자 박규일(신윤섭)을 제지하면서 그녀의 친구 건욱(이홍내)은 보안 사무소 직원으로 일하면서 시스템을 도청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (515, 26044, 18158, '4회에서 구경이(이영애)와 산타(백성철)는 윤제영(박예영)을 따라 K/송이경(김혜준)의 부탁을 들어주며 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (516, 26045, 18158, '4화에서 구경이(이영애)는 윤제영(박예영)을 따라 이 놀이공원을 통해 K/송이경(김혜준)의 부탁을 수행한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (517, 26046, 18158, '4회에서 구경이(이영애)와 나머지 수사B팀이 지하철역 외관을 측설하는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (518, 26066, 14778, '이 정원은 왕궁의 일부입니다. 1회에서 이영(박보검)은 자신의 옆에서 즐겁게 식사를 하고 있는 여동생 명은공주에게서 러브레터를 가로챈다. 그리고 3화에서 홍라온(김유정)은 영은공주(허정은)와 함께 이곳에서 정원 가꾸기를 하고 있다. 이영이 다가오고 라온과 라온은 한동안 티격태격하다가 라온에게 "친구라고" 부른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (519, 26067, 14778, '구르미 그린 달빛 티저영상 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (520, 26068, 14778, '구르미 그린 달빛에서 흙구덩이 함정에 빠진 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (521, 26069, 14778, '차태현과 조여정이 특별 출연한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (522, 26070, 14778, '김윤성(정진영)이 가장 좋아하는 은신처는 이 장소의 고대 편백나무 아래입니다. 3화에서 홍라온(김유정)에게 보여주고 이후 7화에서 윤성이 그녀를 찾아 선물을 주는 것처럼 고민이 필요할 때 찾아오는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (523, 26071, 14778, '첫 티저에서 효명세자(박보검)가 붐바스틱 댄스를 추는 모습으로 이곳에서 촬영됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (524, 26072, 14778, '2회에서 (박보검) 왕과 내시가 나누는 대화를 엿듣습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (525, 25673, 14778, '1화에서 효명세자(박보검)는 아버지 왕이 지나갈 때 이 정원의 봉래섬에 있는 영주각에서 공부하는 척한다. 5화에서 명은공주(정혜성 분)는 홍라온(김유정 분)에게 정원의 호수 위에서 배를 타자고 부탁한다. 세자 효명은 옆을 지나 그 광경을 관찰하고 라온이 호수에 빠지자 곧바로 물에 뛰어들어 그녀를 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (526, 26073, 14778, '왕의 생일 축제는 4화에서 열린다. 솔리스트 리드댄서 애심(차주영 분)이 실종되자 홍라온(김유정 분)이 대신 나서 축하연을 살리고 결국 이영/효명세자(박보검 분)를 마법에 걸리게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (527, 26015, 14778, '드라마 배경의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (528, 26074, 14778, '1화에서 어린 정지운(고우림)이 기 서재 바로 앞에서 어린 담이(최명빈)를 쫓는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (529, 26075, 14778, '19회에서 이영(박보검 분)과 홍라온(김유정 분)의 데이트 장소로 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (530, 26076, 14778, '이영/효명세자(박보검)가 정약용(박보검)을 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (531, 25607, 17274, '1회에서 이연(이동욱)은 이 공원 벤치에 앉아 가족들이 함께 시간을 보내며 첫사랑에 대해 고민하는 모습을 지켜본다. 그는 어린 소녀를 위해 풍선을 꺼내들고, 귀엽고 건방진 김수오(정시율 분)와 함께 벤치에 앉아 친구가 되고 싶어하는 이연의 인생 선택에 의문을 품는다. 14화에서 표재환(김강민)과 김새롬(정이서)은 그녀의 생일을 맞아 공원에서 만나 다리아 꽃다발과 케이크를 가져오고 그는 그녀의 목에 발진이 있다는 것을 재빨리 알아차린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (532, 26077, 17274, '베네치아를 모티브로 만들어진 드라마 <구미호뎐> 5회 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (533, 26078, 17274, '5회에서는 이연(이동욱)과 남지아(조보아)가 맥주보다 좋아하는 것과 선호하는 것에 대해 이야기한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (534, 26079, 17274, '11회에서 구미호 이랑(김범)이 이연(이동욱)을 이곳으로 초대해 커피를 마시게 했다. 커피에 중독되어 의식을 잃게 만드는 가운데 권해룡(엄효섭)은 주차장에 세워진 자신의 차에 연결된 카메라를 통해 모든 것을 관찰한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (535, 24992, 17274, '이연(이동욱)은 600년 전 연희가 산신령이자 비바람의 신이었을 때 지배했던 산인 백두산으로 남지아(조보아)를 데려간다. 이곳은 \'연과 아음의 이야기가 시작되고 끝난 곳\'이다. 그들이 이 폭포에 도착했을 때, 이야기는 전생의 배신, 그리고 폭포와 관련된 사랑과 상실에 대한 많은 신화로 바뀐다. 에피소드 후반부에서 우리는 연이 그의 형을 위해 산철쭉의 잔가지를 남기는 것을 보게 되는데, 이 몸짓이 여행의 진짜 이유였던 것으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (536, 25680, 17274, '5회에서는 이연(이동욱)과 남지아(조보아)가 민속촌을 함께 방문한다. 가게에서 한복을 본 지아는 그 옷을 입기로 결심한다. 이연이 그녀를 찾자 그는 한복을 입은 지아를 보고 곧바로 아름의 기억으로 돌아간다. 그는 그녀가 나무다리 위에 있는 것을 발견하고, 그녀에게 키스하기 위해 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (537, 26080, 17274, '2회에서 이연(이동욱)의 첫사랑이 세상을 떠난 후, 이연(이동욱)은 수세기 동안 그녀의 환생을 찾아 나선다. 그리고 이 공원에서 그는 그녀와 닮은 누군가를 보았지만 다시 실망했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (538, 24934, 17274, '2회 회상 장면에서 이랑(김범)에 의한 버스사고로 부모를 잃은 남자는 동전 던지기에 실패한 그를 소원의수대에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (539, 26081, 17274, '2회에서 이랑(김범)과 기유리(김용지)가 버스 사고로 숨진 이들의 장례식에 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (540, 26082, 17274, '구미호전/구미호전 2화에서 이연(이동욱)과 남지아(조보아)가 배를 타고 해금강마을(개명 \'어화도\')에 도착한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (541, 26083, 17274, '2회에서는 이연(이동욱)과 남지아(조보아)가 어부가 발견한 잘린 머리의 미스터리를 조사하기 위해 \'어화도\'로 여행을 떠난다. 지아 역시 자신이 태어나기 전에 부모님이 이 섬을 찾은 이유에 대한 답을 찾고 있으며, 바다 동굴 앞에 서 있는 부모님 사진을 들고 다닌다. 숲에서 그들은 잊혀진 영혼을 발견하고 지아는 장산사 동굴을 발견한 섬의 북쪽에서 그녀의 답을 찾을 것이라는 말을 듣는다. 지아는 그곳이 예전 사진과 같은 동굴임을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (542, 26084, 17274, '결혼을 앞둔 아홉살배기 여우(이정민)를 쫓기 위해 9살배기 1회에서 이연(이동욱)이 이 결혼식장에 온다. 한편 남지아(조보아 분)는 사연을 위해 이곳에 도착해 복도를 걸어나오면 연을 알아본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (543, 24936, 17274, '5회에서 못된 구미호 이랑(김봄)은 오랜 지상 생활의 즐거움을 만끽하기 위해 비싼 시계를 사기 위해 브라이틀링 부티크(\'브레네타노\'로 표기)를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (544, 26085, 17274, '5화, 8화에서 \'우진대학교병원\'으로 나오고, 남지아(조보아)는 이곳에서 이연(이동)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (545, 26086, 17274, '5화에 나오는 대학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (546, 26087, 17274, '4회에서는 이연(이동욱)이 옥상에서 동생 이랑(김범)과 대결을 펼치는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (547, 26088, 17274, '2회에서 이연(이동욱)이 남지아(조보아)와 영원히 이별하고 싶어하지만, 그의 세계에 소개되고 싶어하는 건물 앞이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (548, 25926, 17274, '14회에서 기유리(김용지)는 형사 백시우(장원형)에게 이 어린이 공원에서 만나자고 부탁한다.  그는 그녀가 자신을 미끼로 삼았다는 사실을 알고 가슴이 찢어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (549, 26089, 17274, '15회에서는 이연(이동욱)과 남지아(조보아)가 공원에서 만나고 평범한 커플처럼 데이트를 할 계획을 세웁니다. 완벽한 남자친구가 되기 위해 연은 지아를 지키려고 도로쪽에 서서 길을 걷습니다. 나중에 그들은 게임 아케이드에서 실력을 시험하고 아이스크림을 먹습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (550, 25724, 17274, '15회에서는 이연(이동욱)과 남지아(조보아)가 다리 옆 한강공원에 앉아 데이트를 마무리한다. 그들 둘은 사악한 이무기와의 최후의 대결을 자신들에게 유리하게 해주기를 기원하며, 만약 무슨 일이 생기면 다음 생에 서로를 기다리겠다고 맹세한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (551, 26090, 17274, '1화에서 \'내세 출입국 관리소\'로 표기로 표기된 이 장소에 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (552, 26091, 17274, '이연(이동욱)은 이 찻집(\'달팽이 신부\'로 출연)의 단골이다. 1화에서 그는 이곳에서 전통 도시락을 먹고 절친이자 동료인 쿠미호 구신주(황희)와 함께 이 곳을 들른다. 주인 복혜자(김수진)는 그에게 수사극 감독 남지아(조보아)를 둘러싸고 조심하라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (553, 26092, 17274, '14회에서는 남지아(남보아)와 이연(이동욱)은 이무기를 죽이기 위한 최후의 결전을 앞두고 밤에 이 삼겹살 식당에서 식사를 한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (554, 25605, 14358, '5회에서 장화영(이윤지), 나지아(장지은), 라라(류화영)는 방명수(변요한)와 김수진(송지효)의 만남에 대해 서로 다른 가능성을 생각해본다. 이 모든 상상들이 이 농지에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (555, 25615, 14358, '김수진(송지효 분)이 이 병원에 있는 나지아(장지은 분)를 방문했을 때, 그녀는 9화에서 방명수(변요한 분)와 너무 가까운 동료인 그녀를 발견한다. 그녀는 그곳을 떠나 명수에게 병원 밖에서 제지를 당하자, 명수는 그에게 정말 사랑하는 사람이 누구냐고 물었고, 그는 키스로 대답한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (556, 26093, 14358, '6회에서 나오는데 이 공원의 성산대교 발 근처에는 방명수(변요한 분)와 김수진(송지효 분)이정기적으로 마음을 터놓고 이야기를 나누는 비밀 장소가 있다. 명수가 수진에게 사랑을 고백하고 12회가 끝날 무렵 두 사람이 키스를 하는 것도 이 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (557, 26088, 14358, '1화에서 본 것처럼 나지아(장지은 분)가 일하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (558, 24904, 14358, '7회에서 방명수(변요한)는 이 카페에서 도상우(조건)를 처음 만나 김수진(송지효)에게 고백하겠다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (559, 26094, 14358, '7회에서 방명수(변요한)가 술에 취한 장화영(이윤지)을 발견한 술집. 마침내 술을 그만 마시고 집으로 돌려 보내도록 설득하는 데 성공한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (560, 26095, 14358, '라라(류화영)는 11회에서 이 공원에서 촬영을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (561, 26096, 14358, '비가 내리기 시작할 때 방명수(변요한)는 버스를 타지 않고 이 정류장 벤치에 앉아 김수진(송지효)과 함께 비에 흠뻑 젖은 버스를 기다리던 순간을 회상한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (562, 25335, 14358, '하미아그룹의 현 대표 강현철(박영규)은 2화에서 이 다리를 건너게 된다. 최종회 16회에서는 강민호(정경호)가 이 다리를 건너 운전을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (563, 26097, 14358, '이 박물관은 방명수(변요한)의 집으로 등장한다. 2회에서 처음 보여진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (564, 26098, 14358, '7회에서 방명수(변요한)가 술에 취한 장화영(이윤지)을 발견하고 집으로 돌려보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (565, 26099, 14358, '8회에서는 김수진(송지효)이 이 거리에 멈춰 사진을 찍는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (566, 26100, 14358, '장화영(이윤지)의 직장 \'오즈코퍼레이션 투자\'로 나타난다. 예를 들어, 4화에서 방명수(변요한)가 그녀를 찾는 장면에서 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (567, 26101, 4522, '한상환(옥택연)과 그의 친구들이 다니는 식당 중 하나다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (568, 26102, 4522, '1회에서 한상환(옥택연)과 친구들이 투우를 보러 가는 곳. \'무인양품 투우장\'으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (569, 25868, 18616, '차우인(조보아)은 1화에서 군부대로 향하던 중 이 길을 운전해 내려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (570, 25816, 18616, '6회에서 공항에 도착한 노태남(김우석) \'는 국외탈출과 병역 기피를 목적으로 가지만 차우인(조보아)에게 쫓기는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (571, 26122, 18616, '6회에서 도배만(안보현)이 용문구(김영민)를 만난 후 이 건물을 나서고 그를 따라다녔던 차우인(조보아)의 전화를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (572, 25950, 18616, '4회에서 사지마비 일병 황철성(김기범)이 입원하는 \'정문대병원\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (573, 26123, 18616, '건물 외관은 \'물망초 카페\'로 보이며, 차우인(조보아)과 강하준(강영석)이 병무청 검사 비밀 은신처를 두고 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (574, 24933, 18616, '돈을 깎기 위해 용문구(김영민)를 만난 강연희(이승연) 검사는 이 건물을 빠져나가 자신과 거래를 끊는 도배만(안보현)과 마주친다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (575, 26124, 18616, '16회에서 윤문구(김영민)와 이재식(남경읍)의 재판이 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (576, 26037, 18616, '2화에서 도배만(안보현)이 노태남 회장(김우석)을 만나러 온 IM 디펜스 사옥으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (577, 26088, 18616, '도배만(안보현)은 5회 드라마에서 이 건물 밖에 주차된 자신의 차 안에서 새 사무실에서 강하준(강영석)이 자신에게 했던 말을 잠시 생각한 뒤 차를 몰고 떠난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (578, 25734, 18616, '2화에서 차우인(조보아)은 차를 운전하며 이 다리를 건너는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (579, 25776, 18616, '도배만(안보현 분)은 5회에서 설악(권동호 분)과 그의 부하들과 함께 이 식당에서 술을 마시다가 난투극을 벌이며 유리창을 박살낸다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (580, 24946, 18616, '노태남(김우석) 회장이 단골로 다니는 클럽으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (581, 25913, 18616, '도배만(안보현 분)이 2회에서 노태남(김우석 분) 회장을 만난 뒤 향하는 IM국방본부 건물 주차장으로 보인다. 그는 윤상기(고건한 분)가 운전하는 차에 올라탔고, 그들은 자신들이 쫓기는 것을 모른 채 차를 몰고 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (582, 26125, 18616, '1회에서 본 클럽 \'카르텔\'의 외관입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (583, 25809, 18616, '1회에서 도배만(안보현)이 이 군교도소에 있는 병장안수호(류성록)를 방문했다. 2화에서 수호는 설악(권동호)을 만나 배만을 죽여달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (584, 26126, 7015, '군함도의 포스터를 촬영한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (585, 26127, 7015, '배우들이 춘천 대표음식을 먹으러 온 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (586, 26128, 7015, '춘천에서 배우들의 머리식히는 공간으로 사용된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (587, 26129, 7015, '영화의 배경인 인민군의 생활을 엿볼 수 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (588, 26130, 7015, '소지섭이 섬을 탈출하는 장면을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (589, 26131, 13897, '8화에서 김도한(주상욱)은 강현태 차장(곽도원)을 이 식당에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (590, 26132, 13897, '드라마에서 \'성원대학교 병원\'으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (591, 24906, 13679, '16회에서는 차승혁(류시원)과 김현철(김민수)이 평화공원 유니세프광장에서 강선아(홍수현)를 놓고 다툰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (592, 26135, 19043, '이 카페는 1회에서 돈세라(권유리)의 절친 사나희(송상은)가 오픈한 베이커리 카페 \'빵야\'로 등장한다. 카페가 있는 건물 자체가 2층에 양진모(음문석) 법무법인이 있는 \'SW플라자\'로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (593, 26136, 19043, '양진모(음문석 분)는 9화에서 자신이 빌려준 이 식당의 사나희(송상은 분)에게 자신의 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (594, 26137, 19043, '1화에서 은선우(정일우 분)가 제보자를 쫓는 \'로열호텔\'이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (595, 26138, 19043, '이곳은 1화에서 은선우 회장(정일우)이 여기 회의에 참석할 때, 3화에서 \'억강공사\'로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (596, 26139, 19043, '이 건물 옥상에는 2회에서 처음 에 단짝 돈세라(유리)와 사나희(송상은)가 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (597, 26140, 19043, '1화에서 은선우(정일우)가 이 건물의 상가에 잠재적인 제보자를 찾아갔다가 경호원/갱단에게 쫓기는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (598, 26125, 19043, '1화에서 은선우(정일우)가 제보자를 쫓고 돈세라(권유리/소녀시대 유리)가 아르바이트를 하는 \'로열호텔\'이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (599, 26030, 16887, '3회에서 명계철(우현)은 탁상기(이상훈)와 이 식당에서 만나 다음 행보를 계획한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (600, 25906, 16887, '명계철(우현)은 13회에서 서국환(정인기)의 장례식을 위해 이곳에 와서 악의적인 생각을 하며 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (601, 25908, 16887, '6회에서는 \'호텔 스위트룸\'이 \'국정원 안전가옥\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (602, 25951, 16887, '4회(전체 2회) 에필로그에서 동관수(이종혁)가 이 곳에서 백찬미(최강희)와 헤어졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (603, 26051, 16887, '2회(전체 1회)에서 \'꿈의 세계\'로 등장하는 이 놀이공원에서 백찬미(최강희), 황미순(김지영), 권민석(성혁)이 힘을 합쳐 미션을 수행한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (604, 26141, 16887, '3회 구비서(한수진)은 전체에서 정보를 교환하기 위해 이 터널에서 랑데뷰를 하고, 제보자가 자신이 의지해야 할 정보를 넘기기 싫을 때 그녀의 무술실력을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (605, 26142, 16887, '13회에서 장사를 이야기하던 중 옆방에 앉은 윤석호(이상엽)와 변우석(허재호)의 말을 엿듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (606, 26143, 16887, '3회에서 피철웅(배진웅)이 로커로 소포를 배달하기 위해 이 역으로 파견된다. 6화에서 이 역으로 걸어 들어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (607, 26137, 16887, '윤석호(이상엽 분)는 14회에서 이 호텔 카페에 와서 전처 심화란(차수연 분)을 만나 두 번째 기회가 없다는 점을 분명히 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (608, 25335, 16887, '13회에서 강우원(이준영/유키스 준)이 임예은(유인영)의 이 다리 방향을 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (609, 26144, 16887, '11회(전체 6회)에서 윤석호(이상엽)는 전직 튜터 백찬미( 최강희)와 남학생이 작별인사를 남기고 이 서점으로 찾아왔다. 그녀가 그를 보았을 때 그는 경찰 기록 뒤에 소심하게 숨었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (610, 25913, 16887, '4회에서 백찬미(최강희)와 황미순(김지영)은 더블 미션에 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (611, 26145, 16887, '24화(전체 12화)에서 \'하명고등학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (612, 26146, 16887, '백찬미(최강희)는이 대학을 다녔고 윤석호(이상엽)는 정기적으로 캠퍼스를 방문했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (613, 24915, 16887, '팰리스 호텔\'로 나오는 이 호텔은, 7회에서 국정원 3팀이 옥철(김용희)이 \'마이클\'(생각대로)을 만나기로 했다는 사실을 알게 된다. 백찬미(최강희 분)가 우연히 이 호텔에서 동시에 소개팅을 하는 사이, 동관수(이종혁 분)가 호텔 밖 관측 밴에서 대기 중이던 옥철을 따라오는 임무를 수행하던 중 팀원 황미순(김지영 분)이 이곳에서 그녀와 마주친다. 그러나 옥철은 서국환(정인기 분)에게 국정원이 추적 중이라는 말을 듣고 호텔을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (614, 26147, 12868, '16회에서 두 사람이 가출로 처음 만났을 때 왕세자 이신(주지훈)과 민효린(송지효)은 그곳을 다시 찾았던 기억을 떠올리며 이 농장을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (615, 26148, 12868, '왕세자 이율(김정훈)은 14회에서 왕세자 신채경(윤은혜)을 이 놀이공원으로 데려오고, 왕세자 신채경(윤은혜)은 그녀의 기분을 풀어주려고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (616, 26149, 12868, '신채경(윤은혜)과 이신(주지훈)은 이 학교에서 공부하는 궁');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (617, 25021, 12868, '6회에서 친구들과 어울려 놀던 민효린(송지효)은 신채와 결혼하는 게 싫다며 질투를 느낀다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (618, 26150, 12868, '이율왕자(김정훈)는 이 장소에서 생일을 축하한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (619, 26151, 12868, '왕실이 돌아가며 시를 낭송하는 다과회 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (620, 26152, 12868, '이율 왕자(김정훈)는 5회를 통해 어머니(서울에 도착했을 때)와 함께 이 호텔 밖에서 리셉션을 위해 온 민효린(송지효)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (621, 26153, 12868, '이 단지는 왕궁의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (622, 26154, 12868, '신채경 왕세자비(윤은혜)는 14회에서 시간이 필요할 때 혼자 이 공원에 와서 자신을 알아보는 소년에게 풍선을 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (623, 26155, 12868, '20화에서 이 역에서 신채경(윤은혜)과 이신(주지훈) 왕세자가 TV 인터뷰를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (624, 26156, 12868, '14화에서 왕자 이율(김정훈)은 왕세자 신채경(윤은혜)을 이 공원의 서쪽 끝으로 데리고 갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (625, 25710, 12868, '15화에서 이신(주지훈)과 신채경(윤은혜)은 이 박물관에 있는 극장용 오페라를 보러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (626, 25945, 12868, '1회에서 신채경(윤은혜 분)은 궁이 \'지금은 텅 빈 곳\'이라는 것을 설명하고, 대한민국을 입헌군주제 국가로, 이 궁은 \'모든 소녀가 사랑하는 매력적인 왕자\'가 사는 곳으로 상상해보라고 초대한다. 그녀가 이렇게 하는 동안, 외벽의 일부와 흥례문은 물론 근정전의 내부가 잠깐 보이고, 이어서 군복을 입은 황태자 이신(주지훈)이 매력적인 왕자의 모습으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (627, 25639, 12868, '4화에서 \'사양당\'으로 등장한 이곳은 왕세자 이신(주지훈)과 왕세자 신채경(윤은혜)이 교육을 받던 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (628, 26157, 12868, '3회에서 신채경(윤은혜) 왕세자비는 이곳에서 공주 훈련을 받고 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (629, 26158, 12868, '이 장소에서 새로운 전시회 개막식에 참석한 신채경(윤은혜) 왕세자세자와 이신세자(주지훈).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (630, 26159, 12868, '17회에서 친한 친구와 나이트클럽에서 말다툼을 한 후 이신세자(주지훈)가 차를 몰고 공원으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (631, 25017, 12868, '2화에서 왕자 이율(김정훈)이 영국에서 이 공항에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (632, 26015, 12868, '왕실이 골프와 유사한 궁중 스포츠를 하는 장면을 이곳에서 촬영했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (633, 26160, 12868, '5회에서 이신(주지훈)의 생일 파티가 이곳에서 열립니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (634, 26161, 12868, '13화에서 이신왕자(주지훈)가 제주도를 방문할 때 이 호텔에 묵는다. 신채경(윤은혜)과 태태 황후(김혜자)가 테디베어 박물관 제주를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (635, 26162, 16718, '1화에서 주인공인 한보름(한보름)이 여행 가이드로 일하는 메인 장소로 남자 주인공 라동길(신원호)이 이 궁의 스태프로 합류하면서 드라마가 전개된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (636, 25796, 14998, '이동준(이상윤) 직장으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (637, 25335, 14998, '2화에서 아내 최수연(박세영), 비서와 함께 이 다리를 건너던 이동준(이상윤)은 자신이 통합할 장인 법무법인 태백을 소개받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (638, 26163, 13801, '5회에서 오수(조인성)는 바다에서 하룻밤을 보낸다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (639, 26164, 13801, '두 달째, 오영(송혜교)이 이 카페에 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (640, 26165, 13801, '오수(조인성)와 박진성(김범)은 1회에서 오영(송혜교)의 형 오수(이재우)의 유골을 정자 아래 강바닥에 뿌렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (641, 26166, 13801, '오영(송혜교)이 사는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (642, 26147, 13801, '5회에서 바다에서 하룻밤을 보낸 오수(조인성)는 조무철(김태우)을 만나러 가기 전 오영(송혜교)을 이 목장에 내려주었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (643, 26167, 13801, '이 정원의 유리집은 오영(송혜교)의 소유지로 등장하며, 4화에서 오수(조인성)와 오영(오영)이 이곳에 꽃을 심고, 꽃을 전달한 박진성(김범)과 문희선(정은지)이 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (644, 26168, 13801, '오영(송혜교)이 이 카페에 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (645, 26169, 13801, '2회에서 시각장애인 오영(송혜교)의 친구 손미라(임세미)가 오수(조인성)와 공모해 자신을 돌보게 한다 그가 영을 위해 목걸이를 사는 동안, 그녀는 미라를 기다리다가 화가 난 어머니에 의해 바닥으로 밀려난다. 지팡이를 찾으려 발버둥치던 그녀는 겨우 일어나 이 백화점 분수대에 빠진다. 여기가 수가 구조하러 오는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (646, 26039, 13801, '5화에서 이명호(김영훈)는 오수(조인성)를 찾기 위해 조무철(김태우)을 만나기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (647, 26170, 13801, '오영(송혜교)의 형 오수(이재우)는 도박꾼 오수(조인성)와 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (648, 26171, 16837, '12회에서 여하진(문가영)의 팬사인회가 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (649, 25906, 16837, '6회에서 이정훈(김동욱 분)이 \'수정대병원\'에 여하진(문가영 분)을 내려줄 때, 우연히 건물 밖으로 나오는 아버지를 보게 된다. 그는 그를 따라 연결된 장례식장으로 들어갔고, 한 방에서 그의 어머니의 장례식 초상화를 보았을 때 심한 충격을 받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (650, 24900, 16837, '촬영을 마친 여하진(문가영)은 언니이자 매니저 하경(김슬기)이 자신과 소속사 이사를 데리러 오길 기다립니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (651, 26172, 16837, '방송국에서 만난 이정훈(김동욱), 여하진(문가영), 유태은(윤종훈)은 11회에서 함께 어색한 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (652, 25613, 16837, '1화에서 여하진(문가영)은 이 식당에서 드라마 촬영을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (653, 25737, 16837, '2회에서 여하진(문가영)은 매니저 여하경(김슬기)과 함께 이곳에서 쇼핑을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (654, 26085, 16837, '12화(풀 6화)에서 장례식장 외관으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (655, 25659, 16837, '5회에서 저녁을 먹은 이정훈(김동욱)과 여하진(문가영)은 여하경(김슬기 분)이 몰고 온 술 취한 영화감독 지현근(지일주)을 PAN빌딩 바로 앞에서 이 공원 동쪽에서 하진의 승합차에 밀어넣어 집으로 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (656, 26173, 16837, '여하진(문가영 분)이 자신의 영화 \'첫사랑\'을 홍보하기 위해 이곳에서 팬 사인회를 열고 있다. 이정훈(김동욱 분)과 조일권(이진혁 분)이 그 지역을 지날 때 그녀를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (657, 26174, 16837, '5회(전체 3회)에서는 영화 \'첫사랑\' 홍보를 위해 출연했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (658, 25792, 16837, '이 곳은 드라마 주인공인 이정훈(김동욱)이 자신의 뉴스 프로그램으로 앵커로 활동하는 방송국 HBN의 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (659, 26175, 16837, '이정훈(김동욱)은 여하진(문가영)의 새 영화 \'첫사랑\'을 보기 위해 이 영화관을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (660, 26176, 16837, '이정훈(김동욱)은 7-8화에서 여하진(문가영)의 신작 \'연구를 위한 첫사랑\'을 보기 위해 이 영화관에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (661, 26177, 16837, '8회(전체 4회)에서 여하진(문가영)은 이 건물 밖 이 거리에서 이정훈(김동욱)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (662, 26178, 16837, '3회에서 여하진(문가영)과 정서연(이주빈), 여하경(김슬기)는  여하경이 여학생들의 공통 일기를 읽으며 고등학교 시절 땡땡이 칠 때 이 공원에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (663, 25722, 16837, '6화(전체 3화) 초반부에서 여하진(문가영)과 여하경(김슬기)은 하진이 잠이 오지 않을 때 이 야경을 보기 위해 이 전망대에 오른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (664, 26179, 16837, '여하진(문가영)의 소속사 엔젤로 등장하는 이 건물');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (665, 26096, 16837, '여하진(문가- 5화에서 여하경(김슬기)이 여하경(김슬기)의 일기장을 읽는 동안 고등학교 시절 흥청망청 놀다가 이 정류장에서 정서연(이주빈)과 버스를 탄다. 그 남자의 기억법(MBC, 2020) 3회.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (666, 26180, 16837, '9회(전체 5화)에서 이정훈(김동욱)이 보낸 스토커 사진을 조사하기 위해 이 골목을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (667, 26181, 16837, '9화에서 이정훈(김동욱)이 이 장소를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (668, 24907, 16837, '여하진(문가영 분)을 집으로 데려다주고 눈이 내리기 시작할 때, 이정훈(김동욱 분)은 전 여자친구의 죽음에 대한 기억이 떠오르고, 4회 마지막에 갑자기 이 계단 앞에 차를 주차한다. 그는 하진에게 집으로 가는 버스를 타라고 했고, 그들은 하진과 함께 이 계단의 꼭대기에서 그녀에게 그녀가 그의 전 여자친구를 아느냐고 물었고, 그녀는 이를 부인했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (669, 25884, 16837, '6회(전체 3회)에서 여하진(문가영)과 이정훈(김동욱)이 이 카페 2층에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (670, 25015, 16837, '6화에서 이정훈(김동욱)과 여하진(문가영)은 이 건물 옆 골목길을 걸어가며 제작진과 저녁식사를 마치고 이곳으로 온다. 술에 취한 채로 하진은 정훈에게 키스를 하고, 그는 나중에 정훈에게 그녀가 그들 사이를 더 이상 갈라놓은 것을 후회할 것이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (671, 25797, 16837, '교도소 문성호(주석태) 면회를 마치고 가던 이정훈(김동욱)이 이 다리에서 나와 한숨 돌리고, 9화에서 여하진(문가영)에게 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (672, 26182, 16837, '여하진(문가영)이 사는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (673, 26183, 16837, '31회(전체 16화)에서 소속사 상사를 만나기 위해 여하경(김슬기)과 함께 이 카페에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (674, 24910, 16837, '2회에서 최희상(장영남) 감독은 여하진(문가영)과 이정훈(김동욱)을 이 술집에서 만나게 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (675, 24951, 16837, '최종회 32회(전체 16회)에서 심야 데이트 산책을 하러 청계천로를 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (676, 24922, 16837, '31화(전체 16회)에 여하진(문가영)이 윈도우 샵에 왔고 배우 여하진이냐고 묻는 지나가는 커플에게 제지당한다. 그녀는 많은 사람들이 그녀가 그녀와 똑같다고 말했기 때문에 그들이 착각했다고 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (677, 26184, 16837, '여하진(문가영)은 서점에서 만난 뒤 거리로 나선다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (678, 26185, 16837, '31회(전체 16회)에서 이정훈(김동욱)과 여하진(문가영)이 이 서점에서 우연히 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (679, 26186, 16837, '이정훈(김동욱 분)과 여하진(문가영 분)은 최종회 32회에서 데이트를 위해 이곳에 오는데, 그녀는 다음에 어떤 영화 프로젝트를 선택해야 할지 그에게 의견을 물어본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (680, 24913, 16837, '9화 이정훈(김동욱)과 여하진(문가영)은 벚꽃나무에 불을 밝힌 채 이 거리를 활보한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (681, 24953, 16837, '12회(전체 6회)에서 이정훈(김동욱), 여하진(문가영), 정훈 어머니가 함께 식사하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (682, 26187, 16837, '2회(전체 1회) 회상 장면에서는 이정훈(김동욱)이 첫사랑을 어떻게 만났는지 회상하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (683, 25913, 16837, '이정훈(김동욱)과 여하진(문가영)은  11회에서 드라마 제작자 겸 감독과 이곳에서 식사를 위해 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (684, 26188, 16837, '여하진(문가영 분)이 영화 \'첫사랑\'의 공동 출연자이자 감독과 함께 진행하는 기자회견은 3화에서 진행된다. 마지막 16회에서 하진은 영화 \'기억 속의 나를 찾아라\'의 기자회견을 위해 이곳으로 돌아오고, 이정훈(김동욱 분)은 관객석에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (685, 26189, 16837, '8회에서 이정훈(김동욱)과 여하진(문가영)은 정훈의 뉴스 프로그램 복귀를 축하하기 위해 김철웅(이성준)과 함께 술을 마시러 이 술집에 온다. 술에 취한 뒤 아내 최희상(장영남)이 찾아와 그를 붙잡고 정신을 차린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (686, 26190, 18407, '8회에서 이 밭의 버려진 농장 옆에서 국연수(김다미)와 함께 다큐멘터리의 일부를 촬영');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (687, 26191, 18407, '솔이가 연수의 집에 머물렀던 다음날, 솔이는 자신의 길을 가기 전에 연수가 최웅(최우식)을 짝사랑하고 있다고 계속 주장한다. 고민에 빠진 연수는 잠시 발길을 멈춘 채 창에 비친 거울을 거울 삼아 생각에 잠긴 채 이 서점을 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (688, 26192, 18407, '톱 아이돌 NJ(노정의)와 김지웅 PD(김성철)가 14회에서 이 건물에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (689, 26193, 18407, '김다미가 아르바이트를 했던 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (690, 26067, 18407, '2회 웅이와 연수가 연애시절 다퉜던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (691, 26194, 18407, '최우식이 김다미 할머니의 짐을 들어준 시장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (692, 26195, 18407, '국연수(김다미)가 할머니 강자경(차미경)과 함께 사는 동네다. 하트 벽화 앞에서 말다툼을 벌인 최웅(최우식)은 국연수(김다미)에게 자기 집으로 와줄 의향이 있냐고 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (693, 26196, 18407, '이 곳의 외관(정원과 옥상 포함)은 1화에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (694, 26197, 18407, '최우식과 김다미가 첫 키스한 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (695, 26198, 18407, '15회에서 이 도서관은 크리에이티브 에이전시 RUN의 고객이며 첫 번째 아이디어를 위해  그녀의 팀과 함께 국연수 (김다미)가 여기에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (696, 26199, 18407, '최우식이 김다미에게 프러포즈한 도서관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (697, 26200, 18407, '최우식과 노정의가 만나 함께 식사한 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (698, 26201, 18407, '연수가 협찬한 매장 주변 홍보 프로젝트에 참여하는 최웅(최우식)은 2회에서 매장 위치를 확인하기 위해 매장을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (699, 26202, 18407, '8화에서 웅과 연수가 여행간 곳을 회상한 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (700, 26203, 18407, '8회에서 국연수(김다미)와의 여행을 회상하며, 최웅(최우식)이 버스 터미널에 도착하고 출발하던 그 날을 기억한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (701, 26204, 18407, '최우식이 전시회를 연 미술관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (702, 26205, 18407, '김다미와 이준혁이 함께 둘러본 생활용품숍');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (703, 26206, 18407, '초대받은 만찬에 무엇을 입어야 할지 고민하던 최웅(최우식)은 아이돌 NJ(노정의)에게 신발을 사러 이 쇼핑몰에 들르다가 결국 1층 화장품 코너를 빠져나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (704, 25625, 18407, '김지웅(김성철)은 11회에서 동료 박동일(조복래), 정채란(전혜원), 임태헌(이승우), 이민경(이선희)과 함께 이 식당에서 회식을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (705, 25659, 18407, '질투심 많은 국연수(김다미)는 11화에서 이 공원 서쪽 끝에 빨간불에 멈춰 서던 중 아이돌 NJ(노정의)와 함께하는 광고를 보고 자산이 있다는 사실을 인정해야 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (706, 26207, 18407, '이 카페에서 김지웅(김성철)은 국연수(김다미)에게 다큐멘터리 영화 출연을 설득하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (707, 26208, 18407, '국연수(김다미)의 소개팅이 이 카페에서 틀어진다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (708, 26209, 18407, '박진주가 운영하는 이자카야 외관으로 나온 술집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (709, 26131, 18407, '12회에서 톱 아이돌 NJ(노정의)와 PD 김지웅(김성철)이 이곳에서 첫사랑의 아픔을 달래며 술잔을 기울였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (710, 26002, 18407, '1회 회상장면에서 본 최웅(최우식)과 국연수(김다미)는 이 놀이공원에서 다섯 번째 이별을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (711, 26210, 18407, '9회 회상씬에서 어린 최웅(최우식)이 어릴 적 길을 잃은 곳은 바로 이 건물 옆문이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (712, 25335, 18407, '13화에서 최웅(최우식)과 국연수(김다미)가 데이트를 하며 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (713, 26211, 18407, '1회에서 최웅(최우식)과 국연수(김다미)가 다니던 고등학교로 이 학교의 일부가 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (714, 26212, 18407, '박진주가 방문했던 최우식 아빠가 운영하는 호프집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (715, 26213, 18407, '14회에서 최웅(최우식)이 국연수(김다미)와 산책을 하는 동안 그녀가 출근하는 버스를 타기 위해 이 주택단지 앞에 학생들을 제지한 학생들은 다큐멘터리에서 자신을 알아보며 웅과 셀카를 부탁했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (716, 26214, 18407, '3화에서 국연수(김다미)는 술에 취한 최웅(최우식)을 업고 이 골목으로 내려온다. 5화에서 최웅과 김지웅(김성철)이 이곳에서 자전거를 탄다. 또한 16화에서 이 골목은 최웅이 국연수에게 처음으로 사랑한다고 말하는 곳으로 이어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (717, 25937, 18407, '국연수(김다미 분)가 데이트할 때 벚꽃 구경을 함께 가지 않은 것을 만회하기 위해 모은 벚꽃 꽃잎을 최웅(최우식 분)에게 온몸으로 쏟아내며 깜짝 놀라게 하는 곳이 바로 이 식료품점 앞이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (718, 26215, 18407, '3화에서 크리에이티브 에이전시 런(RUN)이 사무실을 두고 있는 \'슈퍼 크리에이티브 센터\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (719, 26216, 18407, '드라마에서 동네 거리로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (720, 25340, 18407, '1회에서 학교 일부가 최웅(최우식)과 국연수( 김다미)의 고등학교로 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (721, 26217, 18407, '6회에서 연수가 이 곳 앞에서 할머니가 걸어서 집으로 돌아오길 기다리던 순간을 회상하는 장면에서 첫 번째로 보여진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (722, 26140, 18407, '최웅(최우식)과 국연수(김다미)는 이 곳에서 저녁 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (723, 26218, 18407, '최우식과 김다미가 스테이크 먹은 카페 겸 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (724, 26219, 18407, '1화에서 최웅(최우식)의 부모님이 운영하는 식당으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (725, 25674, 18407, '1화에서 최웅(최우식)이 스쿠터를 타고 서울 철물점 앞을 지나던 이 거리를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (726, 26220, 18407, '최우식과 김다미가 캠퍼스 데이트를 즐긴 대학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (727, 26221, 18407, '<그해 우리는> 8회 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (728, 26222, 18407, '8회에서 이 밭의 버려진 농장 옆에서 국연수(김다미)와 함께 다큐멘터리의 일부를 촬영');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (729, 26223, 18407, '최우식과 김다미가 키스한 아름다운 청보리밭');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (730, 26224, 18407, '삶에 대한 다큐멘터리 촬영을 계속하기 위해 1박 여행을 하던 중 국연수(김다미)와 고등학교 친구이자 PD 김지웅(김성철)은 자전거 여행을 떠나 이 마을로 오게 된다. 그는 이 마을의 은행나무 아래 벤치에서 쉬기 위해 그녀의 자전거 타기를 먼저 촬영합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (731, 26225, 15473, '오수(씨엔블루 이종현)가 할아버지 오만수(박근형)와 함께 사는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (732, 26226, 15473, '5화에서 오수(씨엔블루 이종현)와 서유리(김소은)이 밤에 이 꽃집을 지나가다가 유리는 길을 멈추고 화분에 스카프를 두른 식물을 돌본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (733, 26227, 15348, '하문수(원진아)와 이강두(이준호)가 마지막 데이트를 위해 이 박물관 앞에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (734, 26228, 14995, '16회에서 강인우(최민수)와 유현정(박지영)이 전화를 걸어 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (735, 26141, 14995, '그래피티 터널은 윤수림(레드벨벳 조이)이 강한결(이현우)을 찾으며 여우야를 부르던 배경 중 하나로 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (736, 26229, 14995, '1화에서 윤소림(조이)은 이 단지 앞에서 강한결(이현우)에게 첫눈에 반한다. 그녀는 2화에서 그녀의 밴드와 함께 이곳으로 돌아와 그를 다시 만날 수 있기를 희망한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (737, 25795, 14995, '2회에서 윤소림(조이)이 강한결(이현우)을 따라잡을 때 직접 묻는 말이다. 그는 그녀가 그의 노래의 녹음을 가지고 있다는 사실에만 집중했고 그녀가 단기간에 그것을 삭제하도록 설득할 수 있도록 그녀의 번호를 요청했고 그는 "첫눈에 사랑을 믿기 때문이다."라고 답했고, 그래서 그들의 관계는 거짓말로 시작되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (738, 26230, 14995, '마지막 16회에서 머쉬앤코(Mush & Co.)와 크루드 플레이(Crude Play)의 콘서트가 열리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (739, 26231, 14995, '가상의 \'행복한 청과\', 윤소림 할머니의 가게로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (740, 26211, 14995, 'Mush&Co가 이 학교를 다닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (741, 26232, 14995, '9, 10회에서 윤수림(박수영)이 머쉬앤코 데뷔 쇼케이스 후 강한결(이현우)을 추격한 뒤 한결이 이 다리 위에서 자신이 좋아한다고 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (742, 26233, 14995, '2회에서는 강한결(이현우)과 윤수림(조이)이 이 쇼핑몰에서 만났다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (743, 25862, 14995, '5회에서 윤수림(조이)이 솔로N뮤직 에이전시에서 강한결(이현우)에게 스카우트 된 기쁜 소식을 전했던 카페 배경.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (744, 26215, 14995, '3화에서 이곳은 드라마에서 \'솔른 음악 스튜디오\'로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (745, 26234, 14459, '드라마에서 황정음과 박서준이 차를 마시던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (746, 25973, 14459, '박서준과 황정음이 함께 출장을 간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (747, 26235, 14459, '황정음, 최시원, 박서준이 맥주를 마시던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (748, 26066, 14459, '최종회 16회에서 김혜진(황정음)과 지성준(박서준)이 이 정원으로 소풍을 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (749, 26236, 14459, '황정음과 박서준의 키스신 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (750, 25865, 14459, '13, 14회에서 소풍을 마친 지성준(박서준)은 이곳에서 김혜진(황정음)에게 영원한 사랑을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (751, 25612, 14459, '황정음을 위로하려 최시원이 데리고 간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (752, 26237, 14459, '드라마에서 황정음과 고준희가 방문한 음식점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (753, 26238, 14459, '최종 16회에서 김선우가 혜진(황정음)과 지성준(박서준)이 함께 이 집으로 이사한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (754, 24900, 14459, '김혜진(황정음)이 동화책을 읽고 간 후 지성준(박서준)도 다녀간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (755, 26239, 14459, '박서준과 황정음이 성인이 되어 처음 만나는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (756, 25773, 14459, '12회에서 김혜진(황정음)이 지성준(박서준)을 데리러 간다. 불행하게도, 그들은 서로를 그리워하고 성준은 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (757, 25615, 14459, '지성준(박서준)은 12회에서 쓰러진 뒤 이곳에서 치료를 받는다. 김신혁(최시원)은 그가 쓰러진 소식을 듣자마자 김혜진(황정음)을 이곳으로 데려온다. 그녀는 성준의 방으로 가서 그를 껴안고 그들은 마침내 키스를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (758, 26240, 14459, '16회 마지막 장면에서 지성준(박서준)이 501호 건물을 마주한 길을 아이와 함께 걷는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (759, 26241, 14459, '11회에서 김혜진(황정음)이 절친 민하리(고준희)를 찾으러 찾아왔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (760, 25882, 14459, '11화에서 김신혁(최시원)이 이 공원에서 기자와 인터뷰를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (761, 25668, 14459, '11화에서 정체를 알게 된 지성준(박서준)이 김혜진(황정음)을 처음 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (762, 26242, 14459, '11회에서 지성준(박서준)과 김혜진(황정음)이 마침내 서로를 찾은 후, 성준은 자신이 민하리(고준희)에게 속아왔다는 사실을 알게 된다. 성준은 혜진에게 이 곳 크루즈를 태워준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (763, 24907, 14459, '11회에서 지성준(박서준)에게 반해 서럽게 운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (764, 26243, 14459, '황정음과 최시원이 소원을 빈 계단이 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (765, 25887, 14459, '8회에서 함께 보낸 오후 시간 동안 김신혁(최시원)과 김혜진(황정음)은 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (766, 26244, 14459, '10회에서 김준우(박유환)와 한슬(신혜선)이 이 식당에서 저녁을 먹고 사귀기로 시작한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (767, 26245, 14459, '11회에서 김신혁(최시원)은 이 박물관에 속한 시인의 언덕이나 윤동주 언덕을 산책하며 남한을 떠나는 것에 대해 고민한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (768, 26246, 14459, '이 건물은 혜진이 취업 면접을 보러 오는 1회에서 보듯 지성준(박서준), 김혜진(황정음), 김신혁(최시원)이 일하는 \'진성매거진\'의 사옥으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (769, 26248, 15001, '5회에서 김정남(김지훈)과 그의 친구(고규필)는 정익수(형원)를 따라 이 거리로 나와 할로윈의 나머지 관중들과 어우러질 수 있도록 분장을 해보자는 생각을 갖고 있다. 이 세 사람은 하영(오하영), 잔(이현재)과 함께 아이스크림을 사기 위해 8화에서 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (770, 26249, 15001, '1회에서 이은비(김소현)와 한이안(남주혁)은 이 건물 밖 공원에서 솜사탕을 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (771, 25347, 15001, '1화에서 김정남(김지훈)은 익수(형원 of MONSTA X/몬스타엑스)와 조깅을 하며 경쟁을 시도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (772, 26250, 15001, '1회에서 정익수(몬스타엑스 형원)가 조깅을 하고 있을 때 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (773, 26251, 15001, '1화에서 김정남(김지훈)이 이 지하철역을 향해 조깅하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (774, 26252, 10615, '차라리(김소혜)와 미경(김우린)은 이 기차역에서 강한솔(나인우), 류하준(빅톤 임세준)을 만나기 시작한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (775, 26253, 10615, '6화 춘천 로드 트립에서 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (776, 26254, 10615, '2화에서 \'브릿지 엔터테인먼트\' 건물로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (777, 26217, 10615, '1화에서 차라리(김소혜)의 남자친구 건형(차건)의 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (778, 26122, 16150, '이 건물의 옥상과 외관은 성덕미(박민영)의 옥상 아파트 \'별빛 테라스\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (779, 26255, 16150, '라이언 골드(김재욱)는 12회에서 강변 산책로에서 성덕미(박민영)에게 한국 이름 해윤재를 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (780, 26256, 16150, '마지막 16회에서 성덕미(박민영) 남동생 성덕수 기일에 대가족이 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (781, 26257, 16150, '4회에서는 어린이대공원에서 가짜 데이트를 한 후 성덕미(박민영)와 라이언 골드(김재욱)가 시냇물(연인의 정원)을 따라 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (782, 25719, 16150, '3회에서 이 건물을 나오면 이미 차시안(ONE/정제원)의 열애설이 사실인지 팬들이 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (783, 26258, 16150, '8회에서 최다인(홍서영)과 라이언 골드(김재욱), 성덕미(박민영)가 등장한다. ), 나무 테이블을 맞춤 제작합니다. 결국 라이언과 덕미는 식탁에서 스스로 일을 하게 되고, 다인은 함께 일하면서 둘 사이의 불꽃을 알아차린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (784, 26259, 16150, '4회에서는 이선주(박진주)가 성덕미(박민영)와 라이언 골드(김재욱)의 가짜 커플 사진을 이 공원에서 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (785, 26260, 16150, '남은기(안보현)의 유도 스튜디오 \'최강 유도체육관\'입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (786, 26261, 16150, '12화에서 성덕미(박민영)를 향한 마음으로 고군분투하는 남은기(안보현)는 진심 어린 마음으로 엄마 남세윤(박명신)과 함께 이 공원을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (787, 26262, 16150, '11/12회에서 차시안(정제원)의 녹음실 모습으로 나온다. 서울로 돌아온 그의 어머니 공은영(이일화)도 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (788, 25661, 16150, '6화에서 성덕미(박민영)가 라이언골드(김재욱)에게 욕을 먹은 후 절친 남은기(안보현), 이선주(박진주)와 함께 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (789, 26263, 16150, '마지막 16회에서 라이언 골드(김재욱)와 성덕미(박민영)가 데이트로 이 공원의 이 코너에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (790, 26264, 16150, '6회에서 주혁(유용민)은 이 클럽(이하 \'UG 클럽\'으로 등장)에서 밴드 쇼케이스를 갖고 성덕미(박 민영), 남은기(안보현), 이선주(박진주)가 그를 응원하러 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (791, 25795, 16150, '9회에서 이선주(박진주)와 강승민(임지규)이 점심을 먹고 이곳을 산책한다. 그들이 DMC 건물을 향해 달려가는 한 무리의 팬걸들을 보며 선주는 승민을 만났던 때를 회상한다. "팡걸링이 우리의 관계를 만들었어요,"라고 그녀는 그에게 상기시킵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (792, 26265, 16150, '가구 만들기로 바쁜 하루를 보낸 성덕미(박민영 분), 라이언 골드(김재욱 분), 남은기(안보현 분), 최다인(홍서영 분)이 8회에서 이곳에서 저녁을 먹는다. 식당 밖에서 은기는 다인에게 덕미와 라이언이 지금까지 가짜 연애를 했다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (793, 24941, 16150, '4회에서 이순주(박진주)가 가족들과 함께 이 식당에서 점심을 먹는 모습을 목격한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (794, 26266, 16150, '13회에서 성덕미(박민영)가 이 대학 도서관을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (795, 26267, 16150, '성덕미(박민영)는 라이언과 함께 시안을 만나기 위해 이곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (796, 26268, 16150, '6회에서 금박물관팀이 회식을 하기 위해 이곳을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (797, 26269, 16150, '8회에서 라이언 골드(김재욱)가 성덕미에게 고백하기 위해 이곳에서 꽃을 고르는 모습이 그려졌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (798, 26270, 16150, '시니어 아트 큐레이터 성덕미(박민영)가 일하고 있는 미술관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (799, 25335, 16150, '라이언 골드(김재욱)와 성덕미(박민영)는 5회에서 자신들의 연예인 컬렉션 전시회에 포함시키고 싶은 사진을 소유하고 있는 작가를 방문하고 돌아올 때 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (800, 25797, 16150, '14회에서 남은기(안보현)는 성덕미(박민영)에 대한 우정과 감정이 마음을 짓누르고 있다고 생각하기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (801, 26271, 16150, '10회에서 라이언 골드(김재욱)에게 서울의 밤 풍경을 보여주고 싶지만 운전면허가 없는 성덕미(박민영)가 그를 이 버스 정류장으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (802, 26272, 16150, '아티스트 안명섭이 여기에 살고 성덕미(박민영)가 그를 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (803, 25715, 16150, '1회에서 라이언 골드(김재욱)가 귀국 후 이 호텔의 로열 스위트룸에 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (804, 26273, 16150, '12회에서 차시안(정재원/원/ONE)의 어머니는 아들을 찾기 전에 기도하러 이 교회에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (805, 25637, 16150, '4회에서 성덕미(박민영)의 할머니가 치료를 받고 덕미가 남은기(안보현)와 함께 그녀를 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (806, 26274, 16150, '성덕미(박민영)와 남은기(안보현)의 고등학교로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (807, 26275, 16150, '7회에서 성덕미(박민영)와 신디(김보라)가 차시안(ONE/전제원) 팬사인회에  참석할 기회를 갖는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (808, 25017, 16150, '1화에서 뉴욕에서 한국으로 돌아온 차시안(ONE)의 공항사진을 찍기 위해 성덕미(박민영)가 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (809, 26125, 16150, '1화에서 성덕미(박민영)와 라이언 골드(김재욱)가 참석한 상하이 미술품 경매가 열리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (810, 26276, 17086, '7회에서 \'세종병원\'의 외관으로 보여진다. 김선희(최명길)가 회장으로 있는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (811, 26277, 17086, '웹툰 업계에서 예전 회사로부터 욕을 먹어서 취업이 더 어려워진 것을 한탄한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (812, 26278, 17086, '서현주(황정음)는 9회에서 이 계단으로 향하다가 그곳에서 자신을 기다리는 황지우(윤현민)를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (813, 26279, 17086, '4회에서 한식집 \'이탈리아 밥상\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (814, 26280, 17086, '9회 회상 장면에서 서현주(황정음)는 과거를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (815, 25824, 17086, '정석우 신경정신과 전문의가 근무하는 재담병원 외관으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (816, 25908, 17086, '황지우(윤현민)가 자신과 상담하는 4회와 7회, 서현주(정황음)가 심리치료를 받으러 오는 8회에서 본 것처럼 남성 재담병원 신경정신과 전문의 정석우의 진료실로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (817, 24901, 17086, '8회에서 웹툰 작가 조미옥(백주희) PD가 웹툰 작가 \'유교걸\'을 만나러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (818, 25345, 17086, '전생으로 나오는 1920,30년대와 1970년대 장면을 촬영했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (819, 25613, 17086, '1회에서 서현주(황정음)가 이 장소에서 반전으로 \'결혼식\'을 올렸는데, 부모님은 많이 실망하셨지만 친구들의 응원을 받고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (820, 25655, 17086, '서현주(황정음 분)는 1회에서 당시 남자친구(윤선우 분)의 제안을 받는 모습이 공개됐지만, 2회에서는 거절당한 사실이 드러났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (821, 26281, 17086, '2화에서 김선희(최명길)가 황지우(윤현민) 대표를 만나러 오고, 서현주(황정음)가 의논하러 오는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (822, 24937, 17086, '서현주와 여자친구들은 김선희(최명길)가 보낸 사람이 자신을 염탐하고 있다는 사실을 모른 채 9회에서 황지우(윤현민)와 술을 마시러 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (823, 26282, 17086, '10화에서 황지우(윤현민 분)는 서현주(황정음 분)를 이곳 도서관으로 불러들여 과거의 삶(각각 강진호와 송민주의 모습으로)을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (824, 25660, 17086, '그녀는 땡스오트(땡스오트) 앞에서 하차하고 그도 그녀와 함께 내리자 놀라는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (825, 26283, 17086, '황지우(윤현민)가 멀리서 그들을 지켜보고 있다는 것을 모르고 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (826, 26284, 17086, '서현주(황정음)와 박도겸이 술마시며 수다를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (827, 26136, 17086, '한서연(조우리 분)은 9화에서 박도겸(서지훈 분)을 이 식당으로 초대하는데, 그는 자신과 인터뷰를 하는 것을 전제로 하고 있다. 긴장한 그녀는 결국 자신이 그를 좋아한다고 고백하지만 도겸은 부드럽게 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (828, 24957, 17086, '2회에서 김선희(최명길)가 대학 모교인 \'한국대학교\'를 산책하러 오며 선배에게 짝사랑했던 시절을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (829, 26285, 17086, '4회에서 김선희(최명길)가 딸 한서윤(조우리)을 식사에 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (830, 25335, 17086, '서현주(황정음 분)는 1회에서 해외에서 \'휴식\'을 한다고 주장했던 박도겸(서지훈 분)을 잡기 위해 공항으로 향하던 중 이 다리를 급히 건넌다. 3화에서 황지우(윤현민 분)는 이 다리를 건너 차를 몰고 현주와 도겸이 향하고 있던 강원도 산간 지역의 거센 폭풍에 대한 이야기를 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (831, 25797, 17086, '4회에서 서현주(황정음)는 술 취한 황지우(윤현민)와 함께 이 다리를 지나는 택시를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (832, 24910, 17086, '4화에서 서현주(황정음)는 이곳에 소개팅을 하러 오고 대신 만나기로 한 황지우(윤현민)를 만나게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (833, 26286, 17086, '황지우(윤현민)와 김선희(최명길)는 그들의 전생에 대한 이야기를 들려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (834, 25913, 17086, '2회에서 김선희(최명길 분)가 선우의 대표 황지우를 만나러 오는 선우제약 사무실 미팅룸 중 하나로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (835, 26287, 17086, '5회에서 황지우(윤현민)가 이 등대를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (836, 25017, 17086, '1화에서 박도겸(서지훈)이 그가 실제로 공항 내의 해변 테마 전시장에서 빈둥거리고 있을 때서현주(황정음)에게 휴식을 취하러 간다고 건방지게 암시했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (837, 26288, 17086, '5회 황태경이 사내 MT(멤버십 트레이닝)를 간 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (838, 26289, 17698, '13-14회에서 후준(최태준)과 이근영(최수영)이 촬영한 예능 프로그램의 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (839, 26290, 17698, '16회에서 이근영(최수영/소녀시대 수영)과 최기자가 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (840, 26079, 17698, '16회 최종회에서 후준(최태준)이 이근영(최수영/구 소녀시대 수영)에게 프러포즈하는 카페이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (841, 26291, 17698, '1회에서 남자친구 로이(김형민)를 만나기 위해 이 아파트 단지에 왔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (842, 25951, 17698, '2회에서 이근영(최수영)은 이곳에서 예능 PD 두 명을 만나 후준(최태준)과 함께 \'그래서 나는 안티팬과 결혼했다\' 출연을 제안합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (843, 24935, 17698, '8회에서 이근영(최수영)이 예능 프로그램 \'그래서 나는 안티팬과 결혼했다\' 촬영을 위해 이곳을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (844, 26292, 17698, '14회 5번 출구가 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (845, 24937, 17698, '최재준(황찬성)과 오인형(한지안)은 1회에서 시상식에서 대상을 받는 JJ가 지켜보는 가운데 이곳에서 식사를 한다. 7화에서는 JJ와 인형이 함께 저녁 식사를 하러 돌아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (846, 26093, 17698, '2PM의 황찬성(황찬성)과 이근영(최수영)은 4화에서 이 공원에서 만나 그녀가 소속사에 합류할 것을 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (847, 25659, 17698, '9화에서 이근영(최수영)은 이 공원에 위치한 카페에서 원더우먼의 전 동료를 만난 후, 후준(최태준)의 전화를 받고 이 공원을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (848, 26293, 17698, '15회에서 이근영(최수영)은 절친 고수환(김민규)을 이 카페에서 만나 자신을 조명하는 후준(최태준)에 대한 기사를 게재할 수 있도록 도와달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (849, 25661, 17698, '7회에서 후준(최태준)과 이근영(최수영)은 촬영 중인 예능 프로그램 \'그래서 나는 안티팬과 결혼했다\'를 위해 이 거리에서 데이트를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (850, 24904, 17698, '마지막 16회에서 한재원 감독(동현배)이 예능 프로그램 \'그래서 나는 안티팬과 결혼했다\'의 노도윤 작가(임도윤)에게 이 카페에서 프러포즈를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (851, 26294, 17698, '14회에서 이근영(최수영)이 이 건물 맞은편 버스를 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (852, 26295, 17698, '3회에서는 이근영(조수영) 대표와 최재준(JJ(황찬성) 2PM)이 노래방에서 밤을 보낸 후 점심으로 숙취해소를 위해 이 식당에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (853, 26296, 17698, '16회 마지막회에서 한재원 감독(동현배)과 작가 노도윤(임도윤)이 결혼식장에서 결혼식을 올린다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (854, 25722, 17698, '13회에서 후준(최태준)과 이근영(최수영)이 심야 데이트 장소로 찾아왔다. 이후 그들의 비밀 장소가 되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (855, 25724, 17698, '10화에서 경찰은 오인형(한지안)이 이 다리 아래 차 안에서 의식을 잃은 것을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (856, 25335, 17698, '오인형(한지안 분)은 1회에서 자신의 승합차가 이 다리를 건너자 후준(최태준 분)에게 일찍부터 대리점에서 도와준 것에 대한 감사의 메시지를 쓴다. 7화에서 그녀의 밴이 다시 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (857, 25854, 17698, '후준(최태준 분)은 5화에서 BM그룹 막내아들이기도 한 팬의 생일파티에 참석해 자리를 뜨면서 최재준(황찬성  분)과 오인형(한지안 분)의 말다툼을 감독하고 개입을 시도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (858, 24944, 17698, '1회에서 최재준(찬성)의 클럽 오픈에 초대받았을 때, 후준(최태준)은 자신의 멤버 오한스(크러쉬)였던 걸그룹 \'플로스\'의 데뷔를 위해 이 장소에서 파티를 열었을 때를 회상한다. 그녀가 JJ와 사귀고 있다는 것을 그가 알게 된 것은 그때였다. 5화에서 인형은 여기서 JJ와 화해하려 하지만 허사다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (859, 26297, 17698, '최종회 16화에서 후준(최태준)이 시상식에 참석하기 위해 이 건물로 계단을 올라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (860, 25017, 17698, '1화에서 플래시백으로 준(최태준)은 미국에서 한국으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (861, 26125, 17698, '1화에서 이곳의 외관은 JJ(황찬성/2PM 찬성)가 연 클럽으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (862, 26308, 18620, '2화에서 신비한 유령(이시영)이 이 거리를 걷고 있는데, 그녀의 시선을 사로잡는 카페.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (863, 26309, 18620, '3화에서 김마녹(김성균)은 사복경찰들의 뒤를 쫓으면서 이 기차역으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (864, 26310, 18620, '2에서 신비한 유령(이시영)은 김만옥(김성균)이 그녀에게서 탈출하는 것을 이 곳 위에서 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (865, 25335, 12803, '이정현(고수)은 17회에서 오수아(이다해)와 이 다리의 정상에 올라 내적 대화를 이어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (866, 26313, 7976, '강력반과 마약반이 소고기 회식을 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (867, 26314, 7976, '수원왕갈비 통닭집과 마약반의 아지트가 있던 거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (868, 26315, 7976, '고반장이 딸에게 치킨을 사주던 치킨집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (869, 26316, 7976, '영화 \'극한직업\' 엔딩에 나온 컨테이너 부두');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (870, 26317, 7976, '영화에서 이무배 배를 타러 도망가던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (871, 26318, 7976, '고반장이 이무배을 잡기 위해 달리던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (872, 25936, 13399, '드라마의 주요 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (873, 25737, 13830, '1회에서 손유나(한지혜) Escada의 가게에 와서 떠나면서 결혼반지를 기증한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (874, 24948, 13830, '15회에서 박현수(연정헌)는 여기서 정몽희(한지혜)에게 진정제 과다복용으로 아내가 병원에 입원했다는 사실을 이제 막 알게 됐다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (875, 26319, 13830, '2화 시작장면에서 이 미술관이 보여진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (876, 26278, 19124, '이승천(육성재)은 1회에서 부잣집에서 이 숟가락으로 밥을 먹으면 인생이 누더기에서 부자로 바뀐다는 약속으로 이 거리의 가판대에서 금수저를 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (877, 26320, 19124, '16화 최종회에서 법원으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (878, 24933, 19124, '16회에 이 갤러리에서 \'인생을 탐험하라, 황금수저를 꿈꾼다\'라는 북콘서트가 열렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (879, 25737, 19124, '사람들이 이 거리에서 쇼핑을 하고 거리의 입구에서 수레를 끄는 어르신이 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (880, 26214, 19124, '1화에서 이승천(비투비 육성재/성재)과 나주희(정채연/채연)가 퇴근 후 이 골목을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (881, 25913, 19124, '1화에서 황도신그룹 회장 일가 자택 내부 일부로 등장하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (882, 26321, 14844, '6화에서 환동(EXO 도경수, 디오)이 영화를 촬영하는 공원이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (883, 26322, 15559, '단새우(정려원)의 말 임마는 이 마구간에 거주하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (884, 26323, 15559, '4화에서 단새우(정려원)가 결혼식을 올리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (885, 26324, 15559, '드라마에서 \'자이언트 호텔\' 버스 정류장으로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (886, 26325, 15559, '7화에서 칠성(장혁)은 버스 정류장 \'금토천교\'에 앉아 있는 새우(정려원)를 잡기 위해 이 육교를 가로질러 달려간다. 11화에서 술자리를 마친 서풍(이준호 분)과 칠성이는 칠성이의 집으로 가는 길에 이 육교를 지나다가 여행 가방 안에 있는 신우를 끌고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (887, 26326, 15559, '29회에서 서풍(이준호)은 단새우(정려원)의 엄마 진정혜(이미)를 만나자고 한다. 서풍(이준호)은 진정혜(이미)에게 자신의 식당에서 일을 그만두게 한 것에 대해 사과했다. 한편 그녀는 자신의 딸을 사랑하지 말아달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (888, 24932, 15559, '16회에서 채설자(박지영)는 돈을 벌기 위해 몇 년 전 떠났던 \'자이언트 호텔\'의 셰프이자 전 남자친구 왕춘수(임원희)를 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (889, 26327, 15559, '1화에서 이 웨딩홀의 외관은 서풍(이준호)과 석달희(차주영)가 이곳에서 결혼하는 \'M타워 웨딩홀\'의 외관으로도 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (890, 25659, 15559, '13회에서 모두가 떠나고 나니 풍은 기분이 남다르다. 이때 길 잃은 고양이 딤섬을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (891, 26328, 15559, '30회에서 오맹달(조재윤)이 채설자(박지영)의 전 애인 왕춘수(임원희)에게 주먹을 날린 후 둘 다 이 공원으로 달려와 그가 마침내 그녀에게 키스하고 그녀에게 데이트 신청을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (892, 25722, 15559, '24회에서 이 곳에서 서풍(이준호)이 단새우(정려원)에게 고백한 뒤 입맞춤을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (893, 26329, 15559, '5화에서 서풍(이준호)이 강제로 잉크를 칠하게 된 타투샵의 외관이 이 거리에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (894, 25910, 15559, '5회에서 두칠성(장혁)의 남자들은 서풍(이준호)을 어떻게 할지 상의하고 하나의 제안은 그를 죽이겠다는 것이었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (895, 26330, 15559, '칠성빌딩으로 보이는 건물은 전 조폭 두칠성(장혁)이 소유하고 있다. 1층에서 중식당 \'배고픈 프라이팬\'을 운영하고 있으며 4회 말에서 셰프를 맡은 서풍(이준호)이 16회 \'배고픈 웍\'으로 이름을 바꾼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (896, 26331, 15559, '드라마에서 \'자이언트 호텔\'로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (897, 26395, 18614, '1회에서 수도권 호우 예보 발표 여부를 결정할 때 진하경(박민영 분)은 지도를 확대해 호우 가능성이 있는 지 살펴본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (898, 26396, 18614, '3회에서 이시우(송강)가 출근길에 나현빌딩과 베스트 웨스턴 청담 사이 이 길을 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (899, 25654, 18614, '7회에서 이시우(송강)과 진하경(박민영)은 퇴근 후 이 길을 걷고 각자 함께 사는 것에 대해 이야기를 이어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (900, 26036, 18614, '11회에서 이튿날 제주도로 떠나기 직전 이시우(송강)는 진하경(박민영)의 어머니 배수자(김미경)와 이점에서 저녁식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (901, 26397, 18614, '드라마 전체 배경이 되는 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (902, 26114, 18614, '4회에서 동료 엄동한(이성욱)과 고봉찬(권해효)은  이 술집에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (903, 26398, 18614, '1화에서 진하경(박민영)과 한기준(박윤)이 아파트 분할 협상을 위해 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (904, 26399, 18614, '9회에서 주말에 찾아온 신석호(문태유)와 바텐더가 찾을 수 없는 저번에 여기에 두고 왔던 쿠폰을 전액 지불하라고 주장한다. 진하경(박민영)의 언니 진태경(정운선)이 진하경(박민영)의 대가를 치르러 오는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (905, 26000, 18614, '4회에서 진하경(박민영)이 조깅을 하며 이 다리를 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (906, 26400, 18614, '이시우(송강)는 3화에서 출근길에 이 식당을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (907, 24942, 18614, '3회에서 이시우(송강)와 진하경(박민영)이 저녁식사를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (908, 26401, 18614, '2회에서 혹독한 기상예보팀장으로 취임한 첫날 이시우(송강)가 이 다리를 건너고 있다. .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (909, 26402, 18614, '2화에서 이시우(송강)와 진하경(박민영)이 따로 술을 마시러 온 술집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (910, 26403, 18614, '진하경(박민영)의 어머니 배수자(김미경)와 언니 진태경(정운선)이 함께 살고 있다. 하경은 한기준(윤박)과의 이별 후 짧은 기간(4회까지) 그들과 다시 동거한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (911, 26404, 18614, '7회에서 이시우(송강)는 아버지가 모텔로 등장하는 이 게스트하우스로 데려간 어린 시절을 회상한다. 어린시절 아버지는 그에게 밖에서 기다리라고 한다. 오랜 기다림 끝에 꼬마 시우는 마침내 아버지를 찾기로 결심하고 안에서 도박을 하고 있는 아버지를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (912, 26405, 18614, '마지막회, 주인공들이 복잡한 사내 연애로 흔들렸던 마음을 다시 확인하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (913, 26090, 18614, '3회의 서곡에서 이시우(송강)는 다가오는 날씨 발전에 대비하여 가까운 곳에 있는 산의 가시성을 제어하기 위해 이 기상 관측소를 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (914, 26406, 18614, '퇴근이 늦어진 하경(박민영)은 옷가게 앞 이 거리에서 택시를 부르려고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (915, 26407, 18614, '이시우(송강 분)는 1회에서 여자친구 채유진(김아영/걸즈데이 유라 분)을 이 식당으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (916, 26408, 18614, '9회에서 이시우(송강)와 진하경(박민영)은 만리재로를 따라 걸어가다가 이 식당 앞에서 주말 계획을 세운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (917, 26409, 18614, '2회에서 사람들이 잠시 이 문으로 걸어가는 모습이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (918, 26410, 14942, '8화에서 TQ의 두 매니저가 박현도 대표(박영규)와 계약을 해지할 예정이라 이 식당에서 기다린다. 하지만 대신 서율(이준호)이 도착해 그들에게 3년 더 계약을 연장하라고 협박한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (919, 26411, 14942, '4회에서는 하경(남상미)이 추 감독(김원해)에게 TQ그룹 창업 이후의 고민을 묻는 식당이다. 감독님은 이실장(권혁) 때문에 묻는다면 그냥 잊어버리고 이성적이고 현실적이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (920, 25335, 14942, '4화에서 TQ사로 향하는 중국인 고객들이 이 다리를 건너간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (921, 25347, 14942, '18회에서 김성룡(남궁민)은 감옥에서 막 출소한 서율(이준호)을 이곳에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (922, 26232, 14942, '김성룡(남궁민)이 5회에서 서율(이준호)에게 자신의 회계장부를 돌려받는 것은 바로 이 다리 밑에서다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (923, 26250, 14942, '김성룡(남궁민 분)과 서율(이준호 분)이 18회에서 만나면서 성룡은 율에게 진영을 바꾸라고 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (924, 26412, 14942, '이것은 TQ Corporation이 운영하고 모두가 일하는 건물입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (925, 26413, 15607, '7회에서 김미소(박민영)와 나머지 경영진은 퇴근 후 이 펜션에 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (926, 25025, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 간 휴양림');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (927, 26414, 15607, '김비서가 왜 그럴까에서 박민영과 동료들이 간 휴게소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (928, 26415, 15607, '김비서가 왜 그럴까에서 박서준이 간 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (929, 26030, 15607, '16회에서 이 식당의 외관은 김미소(박민영)가 시어머니(김혜옥)와 첫 공식 오찬을 하는 장소로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (930, 26416, 15607, '김비서가 왜 그럴까에서 박민영이 친구를 만난 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (931, 26417, 15607, '6회에서 이영준(박서준)은 오후 시간에 김미소(박민영)의 계획을 따랐을 때 이 버스 정류장에서 자신을 발견한다. 미소는 서울 일주 버스를 계획하고 시내를 가로질러 이동하는 길이 아니기 때문에 어쨌든 버스를 타지 않을 것이라고 그를 놀린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (932, 26418, 15607, '드라마 안에서 이영준(박서준) 가족의 저택으로 등장한다. 은 4화에서 이곳에서 부모님과 저녁을 먹고, 결국 형 이성연(이태환 분)과 대립하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (933, 26419, 15607, '김비서가 왜 그럴까에서 박서준의 집으로 촬영된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (934, 26420, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 간 꽃게 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (935, 26421, 15607, '김비서가 왜 그럴까에서 박민영,박병은이 간 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (936, 25678, 15607, '2회에서 김미소(박민영)는 저녁에 박유식(강기영)과의 만남을 위해 이곳을 찾았다. 하지만 마침내 미팅은 취소가 되고 모든 것은 이영준(박서준)이 그녀의 마음을 얻기 위해 갑자기 그녀 앞에 등장하려고 한 계획이었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (937, 26422, 15607, '3회에서 김미소(박민영)가 이성연(이태환)을 데리러 오는 길이다. 이 동네가 어린 시절 유괴당한 버려진 장소와 비슷해 기억을 되찾길 바란다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (938, 26423, 15607, '11회-12회에서 영미소(김지유)의 집과 어린 이영준(문우진)이 납치된 집.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (939, 25827, 15607, '드라마 티저에 나오는 장면중 한 장면이 이곳에서 촬영됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (940, 25635, 15607, '12회에서 김미소(박민영)과 이영준(박서준)은 \'소울 푸드\'를 위하여 이곳에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (941, 24932, 15607, '5회에서 이성연(이태환)과 김미소(박민영)가 이 식당에서 밥을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (942, 24933, 15607, '김미소(박민영 분)는 7회에서 이성연(이태환 분)을 이곳에서 만나 점심을 먹고, 이영준(박서준 분)은 오빠와 필요 이상의 시간을 보내지 않는다는 변명을 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (943, 24900, 15607, '4회에서 이영준(박서준)과 김미소(박민영)는 정전이 되자 도서관을 둘러보았다. 그들은 잠시 동안 앉아 있고 그녀는 그녀가 가장 좋아하는 책을 읽습니다. 영준은 그녀를 위해 가짜 손전등으로 전화기를 들고 그녀에게 “내가 예쁘다고 한 적이 있니”라고 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (944, 25830, 15607, '3회에서 영준의 엄마(김혜옥)은 아들의 일정을 그녀에게 물어보기 위해 김미소(박민영)와 이 갤러리 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (945, 26424, 15607, '김비서가 왜 그럴까\' 12회에서 김미소(박민영)가 이영준(박서준)에게 새 컵을 사주기 위해 이 경쟁사에 데려온다. 판매원이 신혼부부에게 좋다며 가정용품을 통째로 사달라고 하자 깜짝 놀랐지만, 영준은 그런 생각을 아주 좋아하는 듯했다. 16화에서 영준이 미소를 데리고 온다. 이번에는 실제로 신혼부부 세트를 구입한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (946, 26049, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 간 전망대');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (947, 26425, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 간 계단');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (948, 26426, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 간 전통 시장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (949, 26427, 15607, '김미소(박민영)와 이영준(박서준) 점심을 먹으러 이 건물을 지난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (950, 26228, 15607, '2회에서 청담동 노래방 \'발렌시아\'로 이곳이 등장한다. 이영준(박서준)은 팀을 위해 노래방을 예약했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (951, 26428, 15607, '1회에서 이영준(박서준)과 김미소(박민영)가 참석한 파티 장소다. 16화에서 건물의 일부는 마스터 디자이너(특별 게스트 이병준)의 패션 디자인 스튜디오 외부로, 영준과 미소가 웨딩 의상 피팅 약속을 했던 곳이다. 그러나 미소는 야근을 해야 했고, 영준을 기다리게 했고, 일을 마치고 달려갔지만 모든 것이 닫혀 있었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (952, 26410, 15607, '최종회 김미소(박민영)가 16회에서 이곳에서 미래의 시어머니(김혜옥)와 첫 공식 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (953, 24918, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 회식한 펍');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (954, 26429, 15607, '14회에서 이영준(박서준)은 김미소(박민영)를 주인인 소꿉친구 정유미(스페셜 게스트 정유미)를 응원하기 위해 이 파스타집(유앤미 파스타)에 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (955, 25659, 15607, '김미소(박민영)는 6회에서 박유식(강기영)이 차를 옆에 두고 멈춰 서서 타라고 하는 장면에서 이 공원을 걷는다. 7회에서 미소는 이영준(박서준)과 차를 타고 있는 유식이 공원 주변에서 이성연(이태환)을 만나는 모습이 포착된다. 유식은 영준의 눈을 가린 채 뭔가를 발견할까 봐, 영준의 눈을 가린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (956, 25661, 15607, '3회에서 김미소(박민영)는 이 거리에서 소개팅 상대인 박병헌(박병은)을 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (957, 26430, 15607, '김비서가 왜 그럴까에서 박민영,박병은이 간 일식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (958, 25795, 15607, '김미소(박민영)와 이영준(박서준)은 11회에서 밤산책을 하러 왔다가 프리즈(첫 번째)를 먹기 위해 자리에 앉게 되는데, 김미소가 밤에 악몽을 꾸고 불면증에 시달릴까봐 자기 집에서 하룻밤을 묵으라고 한다.그녀의 기억을 되찾은 것처럼. 그들은 CJ E&M 빌딩의 측면 입구 맞은편에 위치한 THE 조형물의 동쪽에 심어진 나무들 중 하나 앞에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (959, 26431, 15607, '11회에서 이성연(이태환)이 이 거리를 걷다가 잠시 멈춰 섰다. 유괴 사건의 피해자는 자신이 아니라 동생 이영준(박서준)이였다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (960, 26177, 15607, '7회에서 김미소(박민영)는 밖에서 이성연(이태환)을 만난다. 이영준과 함께 차에 탄 박유식(강기영)의 모습이 포착됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (961, 26136, 15607, '박유식(강기영 분)은 11회에서 전처의 생일날 혼자 이 식당에 찾아와 부부로서 이곳에서 보냈던 좋은 시간들을 기억한다. 전처 서진(서효림 분)도 식당에 왔으나 남자와 함께 있을 때, 그는 그녀가 문제의 남자가 자신의 사촌이라고 말하며 그를 바로잡을 때까지 그녀에게 화를 낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (962, 25734, 15607, '1회에서 스페인 대사관저 기능을 마친 이영준(박서준)과 김미소(박민영)가 탄 차는 이 다리 위를 주행한다. 장면 후반, 미소는 영준에게 회사를 그만둘 계획이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (963, 25909, 15607, '5화에서 이영준(박서준)과 박유식(강기영)이 점심을 먹으러 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (964, 25335, 15607, '이영준(박서준)의 동생 이성연(이태환)은 3화에서 귀국하자마자 부모님께 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (965, 26242, 15607, '3회에서 이영준(박서준)은 김미소(박민영)를 이 배에 태우고 블록버스터급 데이트를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (966, 26432, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 간 미술관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (967, 24910, 15607, '여기는 이영준(박서준)과 박유식(강기영)의 만남의 장소이다. 4화와 8화에서 유식이 영준에게 관계는 항상 정직하고 투명해야 한다고 설명하면서 술을 마시러 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (968, 26433, 15607, '김비서가 왜 그럴까에서 박서준,강기영이 간 바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (969, 26434, 15607, '이곳은 김미소(박민영)의 집이다. 1화에서 이영준(박서준)은 그녀의 집에 찾아와 이직을 말리고 대신 결혼하자고 제안하는데 미소는 억울하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (970, 26435, 15607, '김비서가 왜 그럴까에서 박서준,강기영이 간 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (971, 25913, 15607, '1회에서 이영준(박서준)이 친구들(그리고 그의 \'여자친구\')과 파티를 하기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (972, 25637, 15607, '11회에서 김미소(박민영)가 기억을 되찾은 충격으로 실신한 후 병원에 입원한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (973, 25781, 15607, '2회에서 이 곳은 이영준(박서준)이 출장 중 묵었던 미국 호텔 스위트룸으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (974, 25017, 15607, '13화에서 이영준(박서준)과 박유식(강기영)이 터미널 2로 보이는 곳에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (975, 26125, 15607, '11회에서 김미소(박민영)는 동료 김지아(표예진), 봉세라(황보라)와 함께 스파 서비스에 대한 피드백을 구한다는 전제로 휴식을 취하기 위해 유명 일루전 호텔 스파를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (976, 26436, 15607, '김비서가 왜 그럴까에서 박서준,박민영이 간 고깃집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (977, 26437, 15607, '10회에서 이 곳은 김미소(박민영)와 언니들이 매년 어머니의 생일을 축하하기 위해 찾아온 곳으로 이영준(박서준)이 찾아온 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (978, 26030, 16955, '4회에서 남궁준수(박기웅)은 이 곳에서 새로운 인턴 이만식(김응수)를 만납니다. 남궁준수는 이만식에게 만약 가열찬(박해진)의 일을 방해하는 모든 행위를 하는 것" 즉 "아무것도 하지 않으면" 정규직을 약속한다고 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (979, 26122, 16955, '1회에서 \'옹골식품\' 사옥으로 등장하는 건물. 가열찬(박해진)이 2015년 인턴으로 있었던 곳 .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (980, 24934, 16955, '2회(전체 1회)에서 이 정원은 가열찬(박해진)이 주연을 맡은 인도 마켓의 뭄바이 푸드 CF \'뜨거운 치킨 누들\'의 배경으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (981, 25720, 16955, '이 건물의 외관은 에피소드에서 한국무역협회의 \'2020 제61회 무역의 날\'이 열리는 장소로 보인다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (982, 26441, 16955, '16회에서 주윤수(노종현)은 탁정은(박아인)이 이 카페(그랜드마마카페)의 창가자리에서 혼자 술을 마시는 것을 목격한다. 그는 그녀와 합류하기 위해 들어왔고 그들이 일에 대해 이야기할 때 그는 그녀에게 키스하기 위해 손을 뻗고 그녀는 보답한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (983, 26136, 16955, '5회(전편 3회)에서는 남궁준수(박기웅 분)가 아버지 남궁표(고인범 분), 구자숙(김선영 분)과 함께 이 식당에서 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (984, 26399, 16955, '4회에서 만식은 주문을 어려워한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (985, 26442, 16955, '3회에서 준수푸드 직원들이 동네 주민들에게 무료 라면박스를 나눠주고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (986, 26039, 16955, '15회에서 이태리(한지은)와 주윤수(노종현)가 출연했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (987, 26443, 16955, '가열찬(박해진)이 마음을 비워야 할 때 자주 찾는 준수푸드 사옥의 옥상으로 보인다. 3회, 12회에서 선배 인턴 이만식(김응수)과 대화를 나누는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (988, 25882, 16955, '2회(전체 1회)와 엄한길(김응수)과 이만식(김응수)이 이 공원에서 취업활동을 잠시 쉬고 있다. 이 공원에서 쉬고 있는 엄한길(김기천)도 그에게 다가가 경비원 지원에 동참하겠느냐고 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (989, 25335, 16955, '2회(1회)에서 사임서를 제출하고 엄한길(김기천)의 죽음 소식을 들은 가열찬(박해진 분)은 정처없이 서울을 걸어 이 다리에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (990, 26444, 16955, '가열찬(박해진) 마케팅팀장으로 일하는 준수푸드 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (991, 26445, 16955, '3회에서 준수푸드 직원들이 동네 주민들에게 무료 라면박스를 나눠주고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (992, 25937, 16508, '이는 강여원(최윤소)이 유치원 버스에 태우기 위해 딸과 조카를 데리고 가는 길을 따라가는 1회에서 \'재하 슈퍼\'로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (993, 26446, 13177, '금잔디(구혜선)와 윤지후(김현중)는 지후의 할아버지가 함께 시간을 보내기 위해 계획하고 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (994, 26447, 13177, '금잔디(구혜선)의 부모인 금일봉(안석환)과 나공주(임예진)가 운영하는 드라이클리닝 배달업체 \'잔디토탈클리닝\'이 이 건물에 위치하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (995, 26448, 13177, '금잔디(구혜선) 집 앞의 근린공원으로 등장한다. 금잔디(구혜선)의 퇴학 여부를 놓고 경쟁이 벌어지던 중, 구준표(이민호)가 그녀의 집 앞에 뜻밖에 도착한다. 그리고 그들은 이 놀이터에 와서 이야기하고 그는 그녀를 끌어안는다. 9회에서는 준표가 놀이터 전체를 장식하며 이벤트를 준비한 곳 이다. 그렇게 두 사람은 가까워지고 마침내 첫키스를 나눴다. 12화에서 잔디는 여기서 아버지와 마음의 대화를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (996, 26449, 13177, '10회에서 구준표(이민호)의 엄마 강희수(이혜영)가 금잔디(구혜선) 집안의 드라이클리닝 사업을 접은 후, 그들은 이 고가 아래에서 떡을 팔았다. 같은 에피소드에서 준표의 어머니가 자신의 회사에 있는 준표가 가족과 함께 이곳에서 일하는 금잔디(구혜선)를 볼 수 있도록 그녀의 운전사를 지나게 하는 곳이기도 하다. 준표는 잔디와 합류하기 위해 차에서 내려 키스를 한다. 잠시 후 두 사람은 이 고가도로를 건너며 어머니 때문에 절대 헤어지지 않겠다고 약속한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (997, 26450, 13177, '15회에서 금잔디(구혜선)는 이 사거리를 지나 구준표(이민호)를 본다. 이 갈림길 한쪽에 있는 가게의 텔레비전 화면에서 그들의 최근 이별이 진정으로 스며든다. 여기에서 준표의 여동생이 차를 몰고 잔디를 알아보고 멈춰 서서 그녀를 자기 집으로 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (998, 24930, 13177, '22,23회에서 금잔디(구혜선)는 이웃 소년 찬의 돌보기를 수락하고 구준표(이민호)에게 동물원에서 데이트를 하자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (999, 25021, 13177, '잔디(구혜선)가 퇴학을 당하는것을 막기 위한 1부 대결이 펼쳐진다. 구준표(이민호)는 자신의 말 쥬피터를 조건으로 이 대회에서 이기더라도 강제로 말을 잃는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1000, 25611, 13177, '신화고의 배경으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1001, 26451, 13177, '소이정(김범) 도예공방으로 등장하며, F4 멤버들과 추가을(김소은)이 러브콜을 받고 있다. 추가을(김소은)이 자주 방문한다. 23화에서는 이정과 가을의 키스신 같은 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1002, 26452, 13177, '4회에서 약에 취해 의식을 잃은 금잔디(구혜선)를 데려온 장소로 이 호텔의 일부가 나온다. 그녀의 \'절친\' 오민지(이시영)는 이 계획의 배후에 잔디가 임의의 남자와 침대에서 찍은 사진(다음날 학교 화면에 표시됨)을 추가로 배치했습니다. 구준표(이민호)는 민지와 맞서기 위해 이 곳에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1003, 26453, 13177, '2, 3회에서 이 리조트는 신화 고등학교 수학여행의 파티가 열리는 장소입니다. 잘 차려입은 금잔디(구혜선)이 다른 이와 춤을 추자 질투가 난 구준표(이민호)는 밖으로 나가버리고 리조트 밖에 있던 수영장에 빠지게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1004, 25337, 13177, '17회에 등장하는 호텔이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1005, 25848, 13177, '동서문화회관(계명대학교 대명캠퍼스) 신화고등학교 건물 계단이 금잔디(구혜선)의 은신처 역할을 한다. 구준표(이민호)는 본관에서 공부하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1006, 26454, 13177, '18회에서 금잔디(구혜선)는 동생 금강산(박지빈)과 함께 이 건물의 옥상으로 이사를 하며 19회까지 머물게 된다. 18화에서 구준표(이민호)는 바로 옆 옥상으로 이사를 온다. 19화에서는 건물이 무너져 예고 없이 이곳을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1007, 25661, 13177, '9회에서 추가을(김소은)과 금잔디(구혜선)는 우연히 이 거리에서 가을의 남친을 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1008, 26455, 13177, '2, 3화 신화고등학교 여행의 무도회는 이 호텔의 에메랄드 룸에서 열린다. 금잔디(구혜선 분)는 코스튬볼이라는 말을 듣고 슈퍼우먼 코스튬을 차려입고 나타나자 자신이 엉망이라는 것을 알아차린다. 민서현(한채영 분)이 도움의 손길을 내밀며 행사에 어울리는 옷을 입힌다.2, 3화 신화고등학교 여행의 무도회는 이 호텔의 에메랄드 룸에서 열린다. 금잔디(구혜선 분)는 코스튬볼이라는 말을 듣고 슈퍼우먼 코스튬을 차려입고 나타나자 자신이 엉망이라는 것을 알아차린다. 민서현(한채영 분)이 도움의 손길을 내밀며 행사에 어울리는 옷을 입힌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1009, 26154, 13177, '금잔디(구혜선)와 윤지후(김현중)는 21회에서 지후의 할아버지가 함께 시간을 보낼 수 있도록 계획한 이 공원에서 만난다. 이 공원에서 진행되는 커플 웨딩촬영대회 2등상(한우세트)을 본 잔디는 자발적으로 참가를 결심하고 자신과 지후가 가장 아름다운 커플로 우승한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1010, 25797, 13177, '송우빈(김준)은 18회에서 이 다리에 들러 F4의 우정을 의심하고 소이정(김범)이 자신에게 이성을 이야기했던 순간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1011, 25910, 13177, '9회에서 구준표(이민호)와 금잔디(구혜선)는 추가을(김소은)과 현재 남자친구와 더블 데이트를 하기 위해 이 식당을 찾았습니다. 준표가 장기를 먹는 것을 마주한 것은 처음이었고 가을의 남자친구는 그가 떠날 때까지 끊임없이 준표를 자극했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1012, 26319, 13177, '12회에서 구준표(이민호) 아버지의 심각한 상태가 이 스크린을 통해 공개됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1013, 25633, 13177, '소이정(김범)과 추가을(김소은)은 24회에서 이 계단 꼭대기에서 대화를 나누며, 가을은 그가 달리 말하고 싶어도 더 이상 그를 찾지 않을 것이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1014, 25893, 13177, '2회에서 금잔디(구혜선 분)가 이 건물 앞에 멈춰 서서 여기서 더 아래층 대형 스크린에서 보는 CF에서 민서현(한채영 분)을 감탄한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1015, 25894, 13177, '4화에서 이곳 주차장 차 안에 갇혀 하룻밤을 함께 보내는 구준표(이민호)와 금잔디(구혜선)가 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1016, 25956, 13177, '23회에서 잔디(구혜선)와 손자 윤지후는 재단이 위험하다는 전화를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1017, 25017, 13177, '2회에서 금잔디(구혜선)를 제외한 전원은 신화고 수학여행 때 이 공항을 통해 출발하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1018, 26456, 13177, '등장인물인 구준표 (이민호)의 집으로 나오는 장소이다. 매 화 이 장소의 외관이 구준표의 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1019, 26457, 13177, '21회에서 금잔디(구혜선)에게 이 식당을 찾아오게 하고, 그는 구준표(이민호)와 하재경(이민정)의 결혼을 막을 수 있는 마지막 기회라고 주장하면서도 자신의 우정을 쌓아 행복해질 수 있다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1020, 26160, 13177, '22회에서 결혼식이 무산되고난 후, 하재경(이민정)은 금잔디(구혜선)와 구준표(이민호)를 위해 로맨틱한 하룻밤을 이 호텔에서 준비해줍니다. 그들은 촛불이 빛나는 저녁과 왈츠를 즐기고 키스를 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1021, 26458, 13177, '금강산(박지빈)이 누나 금잔디(구혜선)를 찾아 F4 멤버들을 찾는 곳으로, 1회에서 \'우송박물관\'으로 등장한다. 이 박물관은 F4 멤버 소이정(김범)의 할아버지인 독립운동가 소윤허가 소장한 문화재를 전시하고 있다고 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1022, 26285, 14085, '23회에서 천혜진(서효림)이 한광훈(류수영)을 이 호텔에서 발견하고 한광훈(류수영)에게 직접 다가가 서로 아는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1023, 26459, 14756, '13회에서 강민주(김희애)와 고상식(지진희)이 이곳에서 산책을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1024, 26460, 16773, '4회에서 한소연(고성희)은 하우스메이트인 유람(강승현)과 짧은 여행을 떠난다. 그들은 이 카페에 와서 어울리고 사진을 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1025, 26461, 16773, '1회에서 고유진(최여진)이 수상한 남자들에게 쫓기는 동안 AI 홀로그램 홀로(윤현민)는 한소연(고성희)이 그들을 지나치는 것을 알아차린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1026, 24934, 16773, '6회에서 한소연(고성희)이 AI 홀로그램 \'홀로\'(윤현민)와 함께 \'데이트\'를 위해 이 정원을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1027, 25719, 16773, '3회에서 이곳은 백찬성(2PM의 황찬성)이 이끄는 인공지능 기업 매직미러 본사의 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1028, 25773, 16773, '11화에서 잠복을 시도하던 중 고난도(윤현민)가 포착되고 단말기 안에서 추격전이 이어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1029, 24937, 16773, '3화에서 한소연(고성희)은 짝사랑하는 선배이자 매니저 연강우(이기찬 분)를 만나기 위해 이곳에 출연한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1030, 26462, 16773, '1회에서 한소연(고성희)은 가방에 넣은 안경을 발견하고 커피를 사러 이 카페에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1031, 26463, 16773, '11회에서 식사를 위해 한소연(고성희) 어머니의 식당으로 소연이 고난도(윤현민)를 데려온다. 이곳에서 그는 어렸을 때 그를 돌보던 어머니와 재회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1032, 26464, 16773, '한소연(고성희)이 마케팅 부서에서 일하고 있는 안경 회사의 사옥. 1화에서는 AI 홀로그램 홀로(윤현민)와 함께 건물로 걸어가는 그녀의 모습이 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1033, 25335, 16773, '2회에서는 연강우(이기찬 분)가 한소연(고성희 분)을 퇴근 후 집으로 태워다 주고, 이 다리를 건넌다. 인공지능 홀로그램 \'홀로\'(윤현민)가 소연의 뒤에 앉아 연애 조언을 해주려 하지만 소연은 자신의 방식대로 일을 처리하기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1034, 24908, 16773, '한소연(고성희)이 AI 홀로그램 홀로(윤현민)를 옆에 두고 허탈하게 이 거리를 걷는다. 비가 많이 내리기 시작하고 홀로의 최선의 노력에도 불구하고 그녀는 계속해서 눈물을 흘리는 자신을 비에 흠뻑 젖게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1035, 26465, 16773, '1회에서 고난도(윤현민)는 오토바이를 타고 이 터널을 통과한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1036, 24922, 16773, '5회에서 한소연(고성희)이 눈 내리는 AI 홀로그램 \'홀로\'(윤현민)와 함께 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1037, 26466, 16773, '진정이 된 후 흠뻑 젖은 한소연(고성희)은 AI 홀로그램 홀로(윤현민)를 옆에 두고 이 카페 문앞에 앉아 명상을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1038, 25781, 16773, '2화에서 고유진(최여진)이 이 호텔에서 투자자를 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1039, 24932, 15868, '3회에서 김회장(강성진)이 미르(지호성), 진세라(애프터스쿨 리지)를 만나 강준혁(성훈)의 행방을 알아봤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1040, 24933, 15868, '진세라(리지)가 강준혁(성훈)과의 결별을 앞두고 이 갤러리 카페에서 기자회견을 하는 곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1041, 25659, 15868, '1회에서 이연서(김가은)가 강준혁(성훈)과 과자를 사러 이 도시공원을 가로질러 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1042, 25335, 15868, '8회에서는 강준혁(성훈)이 이연서(김가은)에게 이 다리 기슭에서 운전하는 법을 가르친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1043, 25668, 15868, '10화에서 강준혁(성훈)은 진세라(애프터스쿨 리지)와 헤어지고 이 다리를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1044, 26467, 15868, '이 장소는 1화에서 보듯 강준혁(성훈)의 소속사 \'옥수수 엔터테인먼트\'의 본사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1045, 26186, 15868, '황남구(김종훈 분)는 8회에서 둘 다 백범광장을 걷다가 놀란 이연서(김가은 분)에게 자신의 감정을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1046, 25799, 15868, '이연서(김가은)는 이 옥탑방에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1047, 25017, 15868, '1회 팬미팅을 마치고 귀국하는 강준혁(성훈)이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1048, 25917, 17280, '3회에서 강아름(유인나)이 대표인 김동란(이주우)과 함께 이 갤러리(DDK 갤러리)에서 1일 비즈니스 미팅을 하고 있다. 그녀의 뒤를 이어 전지훈(문정혁)과 그의 동료 김영구(배인혁)가 뒤따른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1049, 25908, 17280, '전지훈(문정혁)이 CIA 관계자(존 D)와 스파이 사건을 논의하기 위해 오는 CIA 블랙사이트로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1050, 25827, 17280, '10회에서  강아름(유인나)은 \'인터폴\'과 \'스파이\' 역으로 이 건물 앞에서 전 남편 전지훈(에릭)과 현 남편 데릭 현(임주환)을 서로 소개한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1051, 26468, 17280, '1회에서 강아름(유인나)과 전지훈(문정혁)은 이혼 후 이 교회에서 처음으로 재회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1052, 26469, 17280, '강아름(유인나)이 운영하는 럭셔리 웨딩드레스숍.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1053, 26000, 17280, '1회에서 강아름(유인나)과 남편 데릭현(임주환)이 이 다리를 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1054, 26470, 17280, '15회에서 한 제보자가 전지훈(문정혁)과 동행, 강아름(유인나)의 발치에서 증거를 얻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1055, 25347, 17280, '7회에서 데릭 현(임주환)은 밤에 피터(전승빈)와 함께 이 공원에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1056, 26471, 17280, '8회에서 전지훈(문정혁)은 이 거리에 세워둔 자전거에 앉아 생각에 잠겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1057, 26456, 17280, '10회에서 김동란(이주우)의 결혼식은 \'DDK 재산\'으로 등장하는 이 장소에서 열리며, 강아름(유인나)이 결혼식을 주관해 전 남편 전지훈(에릭)과 현란(현림)을 붙잡는 데 도움을 준다.r김동락 (장재호).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1058, 25866, 17280, '1화에서 전지훈(문정혁)과 전지훈(전승빈)이 동시에 공항에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1059, 26066, 17637, '2회에서 이 정원은 심덕출(박인환)의 절친 교석(이영숙)이 안치된 곳으로 등장한다. 덕출은 찾아와서 그에게 발레를 시작했다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1060, 26472, 17637, '이채록(송강)과 심덕출(박인환)이 훈련을 받기 위해 기승주(김태훈)의 댄스 스튜디오를 오가는 버스를 타는 정류장이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1061, 26473, 17637, '6,7회에서 심덕출(박인환)과 최해남(나문희)이 결혼기념일 데이트로 수족관을 찾는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1062, 26474, 17637, '1회에서 이채록(송강)은 이 브런치 카페에서 알바를 하고 있고 심은호(홍승희)가 인턴으로 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1063, 26475, 17637, '심은호(홍승희)의 부모님이 일하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1064, 25345, 17637, '심덕철(박인환 분)은 1회에서 발레 무용수(이 공원에 위치한 무용학교에서)를 어렸을 때 처음 보고 이 춤에 직접 빠져들었을 때를 회상한다. 하지만 그의 아버지는 그에게 이것은 그가 소년으로서 결코 생각해서는 안 되는 스포츠가 아니라고 말했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1065, 26085, 17637, '이 건물의 외관은 1화에서 기승주(김태훈)가 방문하는 은소리(윤지혜)의 사무실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1066, 26476, 17637, '2회에서 덕출(박인환)은 이 마을에서 우체국 직원으로 경력을 시작했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1067, 26477, 17637, '2회에서 심은호(홍승희)와 삼촌 심성관(조복래)은 이 술집에서 단 둘이서 정기적으로 수다를 떤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1068, 26137, 17637, '심덕철(박인환 분)은 1회에서 가족과 함께 이 호텔에서 70번째 생일을 맞는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1069, 26478, 17637, '심덕철(박인환)은 2회부터 나빌레라의 이 공원에서 정기적으로 훈련을 받는다. 4화에서 조카에게 차를 선물하는 것, 8화에서 이채록(송강)을 처음 알아보지 못하는 것, 11화에서 양호범(김권)과 친분을 쌓고 그가 정상 궤도에 오를 수 있도록 응원하는 것도 여기에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1070, 25335, 17637, '11화에서 타는 버스 이채록(송강)이 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1071, 25347, 17637, '이채록(송강)과 심은호(홍승희)는 5회에서 저녁에 이 공원에 와서 마음을 터놓고 이야기를 나누는데, 이는 그가 그녀에게 꿈에 대해 묻고, 그녀는 대답하려 하지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1072, 25797, 17637, '1화에서 이 다리를 건너는 이채록(송강)의 휴대전화는 아버지가 그날 출소한다는 사실을 상기시킨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1073, 24945, 17637, '10화에서 이채록(송강)과 심덕출(박인환)이 밤에 이 길을 건너고 덕출이 철로 근처에서 채록을 위해 춤을 춘 후 둘 다 발레 수업을 계속하기로 결정합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1074, 26479, 17637, '3회에서 이채록(송강)은 이 아파트에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1075, 26480, 17637, '심은호(홍승희)가 부모님 심성산(정해균), 김애란(신은정)과 함께 사는 아파트 건물로 보여진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1076, 26481, 17637, '2회에서 기승주(김태훈)의 댄스 스튜디오가 있던 곳으로, 이채록(송강)과 심덕출(박인환)이 있는 곳으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1077, 25017, 17637, '12회에서 이채록(송강)이 해외 3년 만에 이 공항에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1078, 26482, 17637, '6화에서 심덕출(박인환)과 최해남(나문희)이 결혼기념일에 이 식당에서 외식을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1079, 26483, 17637, '1회에서 이채록(송강)과 심덕출(박인환)이 이 장소에서 백조의 호수를 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1080, 26069, 16510, '1회에서 서휘(양세종)는 부하들과 함께 이 호수를 건너 이방원(장혁)과의 만남의 장소를 향해간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1081, 26484, 16510, '1회에서 서휘(양세종)와 남선호(우도환)가 1388년 어린 시절 이 절벽에서 겨루는 장난을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1082, 26485, 16510, '3화에서 한희재(설현)가 이 마을의 통나무 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1083, 26486, 16510, '2화에서 나오는 군대 선발 시험이 이곳에서 치뤄진다 최종 상대는 서휘(양세종)와 남선호(우도환)로, 후자가 승리한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1084, 25603, 16510, '2회에서 서휘(양세종)과 그의 누이동생 서연(조이현), 남선호(우도환), 한희재(설현)가 축제에서 연 날리기 위해 이 연못에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1085, 26487, 16510, '서휘(양세종)와 한희재(설현)는 제2화에서 서휘의 여동생 서연(조이현)이 궁남지에서 날리다 잃어버린 연을 찾다가 이 나무로 오게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1086, 26488, 15459, '6회에 이지아와 김영민이 불륜 장소였던 캠핑장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1087, 26489, 15459, '극 중에서 아이유와 이선균이 자주 가는 술집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1088, 26490, 15459, '3회에 이선균이 아이유에게 술 사준 일식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1089, 26491, 15459, '이곳은 형제청소방, 박상훈(박호산)과 박기훈(송새벽) 형제가 운영하는 청소업체 사무실이 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1090, 26492, 15459, '5회에 김영민이 혼자 술을 먹었던 바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1091, 26493, 15459, '11회에서 이지안(아이유)은 이 곳에서 친구 송기범(안승균)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1092, 26494, 15459, '5회에 나온 회사 전체 회식하는 고깃집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1093, 26495, 15459, '4회에 나온 안전진단 3팀이 술 먹는 고깃집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1094, 26496, 15459, '박동훈(이선균)이 근무하는 엔지니어링 회사, 삼안건설 사옥의 외관이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1095, 25734, 15459, '9화 초반에 박동훈(이선균)과 이지안(이지은)이 지하철을 타고 다리를 건너는 장면 등 여러 번 목격된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1096, 26497, 15459, '1회에서 아이유와 이선균이 소주를 먹었던 술집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1097, 26498, 15459, '7회에서 박동훈(이선균)과 이지안(아이유)이 하차하는 가상의 후계역이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1098, 25909, 15459, '6화에서 왕영국 상무(전국환)가 동료들과 이 식당에서 점심 회식을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1099, 26499, 15459, '박동훈(이선균), 박상훈(박호산), 박기훈(송새벽) 에피소드에서 이 다리 발치에 있는 텐트 바에서 한 잔 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1100, 26500, 15459, '2회에 이선균이 아이유에게 술 사준 일식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1101, 26232, 15459, '박동훈(이선균)은 6회에서 이 다리에 와서 자살을 고민하는 것 같다. 이지안(이지은)은 스마트폰으로 추적 장치를 한 동훈의 행방을 쫓다가 동훈이 이 다리 옆에 너무 오래 서 있는 것을 보고 달려든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1102, 25776, 15459, '박동훈(이선균)은 2회에서 두 형제와 함께 이곳에 술을 마시러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1103, 24945, 15459, '4회에서 박동훈(이선균)이 막내 동생(손새벽)을 이 거리에서 쫓는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1104, 25910, 15459, '6회에서 안전진단 3팀 부하직원끼리 먹었던 고깃집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1105, 24948, 15459, '5회에 할머니를 카트에 넣어 달을 보여준 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1106, 26157, 15459, '4회에 아이유와 김영민이 사람들 틈에 접선하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1107, 24951, 15459, '14회에서는 이지안(이지은)이 자신의 휴대전화를 도청했다는 사실을 알게 된 후 과거를 회상하는 모습이 그려졌다. 그동안 그의 대화에 귀를 기울였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1108, 26501, 15459, '3회에서 이지안(아이유)은 이 건물 밖 보도에 서 있다. 그녀는 박동훈(이선균)이 저녁을 사달라는 문자 메시지를 거부하자 잠시 하늘을 응시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1109, 26502, 15459, '8회에서 상무로 올라가자고 말한 장면인 중식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1110, 26503, 15459, '9회에서 이지안(아이유)은 박동훈(이선균)과 광일(장기용)의 말다툼을 엿듣고 이 고가도로에서 눈물을 흘린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1111, 26504, 15459, '9회에 3형제가 참치 먹은 참치집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1112, 26505, 15459, '16회에서 이지안(아이유)은 동료들과 함께 여기에 온다. 카페에 가던 중 우연히 박동훈(이선균 분)과 같은 소리를 하는 사람을 보고 확인하러 올라갔다. 그리고 둘은 1년 만에 서로를 보고 유쾌하게 놀란다. 그들은 카페 밖에서 대화를 나누며 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1113, 26506, 15459, '9회에서 아이유가 도청하며 이동하다 오열하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1114, 26507, 15459, '이선균의 친한 친구인 박해준이 지내는 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1115, 26508, 15459, '동훈(이선균)의 절친 겸덕(박해준)이 승려로 머물고 있는 사찰이다. 동훈 외에 동훈의 어머니(고두심)와 겸덕의 전 여자친구 정희(오나라)가 그를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1116, 26509, 15459, '소꿉친구인 정희(오나라)가 운영하는 술집으로 자연스럽게 그 장소는 그녀의 이름을 따서 명명되었다. 그녀는 위층에 머물며 낮에는 박형제의 어머니(고두심)가 일하는 곳이기도 하다. 15회에서는 박동훈(이선균)이 중상을 입은 이지안(아이유)을 이곳으로 데려오고, 정희는 그녀를 기꺼이 받아들인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1117, 26510, 15459, '6회에 이지아와 이선균이 밥 먹은 설렁탕집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1118, 26511, 15459, '16회에서 사임 가능성에 대해 이야기했지만, 동훈에게 \'지기 싫다\'는 이유로 재검토를 요청한다. 대표이사는 도준영 사장(김민영)과 동훈의 아내(이지아)의 불륜을 알게 됐다고 털어놨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1119, 26121, 15459, '9회에서 박동훈(이선균)은 광일(장기용)을 찾아 여기까지 오게 되고 그와 말다툼을 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1120, 26512, 18735, '1회에서 회사 볼링 동호회가 끝난 뒤 동호회 회원들이 이 식당에서 저녁식사를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1121, 26256, 18735, '8회에서 구자경(손석구)과 염미정(김지원)이 저녁에 이 곳을 찾아온다. 그리고 그들 앞에 펼쳐지는 무지개를 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1122, 26513, 18735, '8회에서 염기정(이엘)이 니바르나의 네버마인드 음반을 건네주기 위해 이 카페에서 조태훈(이기우)을 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1123, 26514, 18735, '2회에서 현아(전혜진)가 생일을 맞아 고향 친구들과 함께 술을 마시는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1124, 25659, 18735, '15회에서 구자경(손석구)은 염미정(김지원)을 밤에 이 공원에 있는 포장마차에서 뭐 좀 먹기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1125, 26478, 18735, '15회에서 시간이 흐른 뒤 만난 구자경(손석구)과 염미정(김지원)은 이 공원을 거닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1126, 25802, 18735, '14회에서 구자경(손석구)이 염미정(김지원)에게 (드디어) 전화를 걸기로 결심한다. 국회 워터프론트 주차장 아치교에서 만난 두 사람은 이 길을 걸어가다가 마침내 그의 전체 이름을 묻는다. 그들은 15화에서 이 거리를 계속 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1127, 26515, 18735, '14회에서 구자경(손석구)이 염미정(김지원)에게 전화를 걸기로 결심한다. 그리고 그들은 이 다리에서 만난다. 함께 다리를 건너 여의동로에 들어서다 드디어 본명을 묻는 그녀.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1128, 25348, 18735, '손석구와 김지원의 재회 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1129, 26516, 18735, '15회에서 구자경(손석구)은 밤에 이 공원에 위치한 포장마차에서 식사를 하고 있는 염미정(김지원)에게 전화하기로 결심한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1130, 26517, 18735, '염미정(김지원), 염창희(이민기), 염기정(이엘) 3남매가 서울로 이사온 후 사는 곳이다. 15,16회에서 볼 수 있듯이 7호에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1131, 24913, 18735, '15화에서 염창희(이민기)는 지역 도서관에 책을 가지고 오는 길에 이 거리를 자전거로 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1132, 26518, 18735, '15회에서 염창희(이민기)는 자전거를 타고 이 거리의 도서관에 책을 반납한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1133, 26125, 18735, '손석구가 2022 새해를 맞이하며 나오는 클럽');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1134, 26519, 18735, '김지원이 손석구와 함께 있던 그 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1135, 26520, 18735, '1화에서 염미정(김지원 분)이 서울행 기차를 타고 가는 당미역으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1136, 26521, 15809, '기유진(김영광)은 9호실에서 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1137, 26522, 15809, '을지해이(김희선)가 근무하는 법무법인이 이 건물에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1138, 26523, 15809, '1회에서 을지해이(김희선)와 그녀의 동료 방상수(임원희)는 맞은편 거리에서 음주 단속을 위해 경찰 오봉삼(오대환)에게 제지된다. 두 사람은 봉삼을 아는 사이로 밝혀졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1139, 25896, 15809, '1회에서 을지해이(김희선)는 남자친구 기유진(김영광)이 이 놀이동산 앞에 주차된 자동차 안에서 숨진 것을 발견하는 꿈을 꾸었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1140, 26524, 15809, '장화사(김해숙)가 9호실에 수감되는 \'청원형무소\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1141, 25640, 16440, '9회에서 마동찬(지창욱)과 미리란(원진아)을 위해 아이스크림을 사다준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1142, 26030, 16440, '4회에서 고미란(원진아)은 잃어버린 20년에 대해 700만 달러의 배상금 요청하기 위해 김홍석(정해균), 손현기(임원희), 마동찬(지창욱)을 이 식당에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1143, 26536, 16440, '7회에서 팀 만찬 후 방송국 팀 전원이 2차 노래방으로 모인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1144, 25865, 16440, '8회에서 마동찬(지창욱)이 촬영장을 물색할 때 이 공원을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1145, 25997, 16440, '3화에서 마동찬(지창욱)의 돌아가신 아버지가 이곳 묘지에 안장되고 그를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1146, 26256, 16440, '2회에서 고미란(원진아)의 어머니 유향자(길해연)가 지칠 때까지 딸의 안녕을 기원하는 절이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1147, 26537, 16440, '3회에서 마동찬(지창욱분)과 나하영(윤세아분)이 20년만에 다시만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1148, 25908, 16440, '운성그룹 김홍석 대표(정해균 분)의 저택 내부 모습으로, 4회에서 비서가 이곳에서 김송석 대표에게 보고하는 것을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1149, 25827, 16440, '유튜브 채널을 통해 공개된 최종회 16회 장면중 마동찬(지창욱)과 고미란(원진아)이 시애틀 장면은 미국이 아니라 실제 이 장소에서 벌어지는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1150, 24932, 16440, '마동찬(지창욱)과 나하영(윤세아)은 이날 저녁식사를 위해 이 식당에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1151, 26538, 16440, '11회에서 마동찬(지창욱)과 고미란(원진아)이 구상한 새로운예능 \'고고99\' 첫 회 촬영이 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1152, 25848, 16440, '2회에서는 길 잃은 고미란(원진아)이 과거의 흔적을 찾기 위해 캠퍼스를 배회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1153, 26539, 16440, '2회에서 오영선(송지은)과 박경자(오하늬)는 고미란(원진아)과 함께 대학 시절 학창 시절을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1154, 26540, 16440, 'TBO방송국 사옥으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1155, 25661, 16440, '8회에서 고미란(원진아)과 황지훈(최보민)이 \'데이트\'를 위해 이 거리를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1156, 25795, 16440, '영선의 남편 황병심(심형탁)이 추궁하는 6회에서는 절친 오영선(서정연)과 박경자(박희진)가 경찰서로 향할 때 공원을 가로질러 간다. 12화에서는 손현기(임원희)와 박경자(박희진)가 이 공원에서 만나 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1157, 26541, 16440, '고미란(원진아)과 그녀의 가족은 공원 근처에서 생선구이를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1158, 26153, 16440, '마동찬(지창욱 분)이 냉동 실험에 참여했다는 소식을 전한 후, 6화에서 마동찬(지창욱 분)을 파티에 초대하는 것에 대해 논의하는 정치인들이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1159, 26542, 16440, '4회에서 고미란(원진아) 동생 고남태가 그녀를 이 탑의 발치로 데려와 지난 20년간 서울이 얼마나 달라졌는지 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1160, 26543, 16440, '10화에서 운성회장(김법래)은 황갑수 교수가 아직 살아있다는 정보를 이 건물 옥상에서 입수한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1161, 26156, 16440, '1화에서  마동찬(지창욱) 감독의 예능 \'풍선으로 날 수 있니\' 에피소드에서 고미란(원진아)이 여러 개의 풍선에 매달린 기니피그로 등장하고 이것은 이 공원에서 촬영됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1162, 24944, 16440, '마필구(김원해)의 59번째 생일 파티는 2회에서 이 식당에서 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1163, 26544, 16440, '2회에서 마동찬(지창욱)과 고미란(원진아)이 2019년 처음 서울을 재발견한 곳은 바로 이 공원 앞 산책로이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1164, 26545, 16440, '1,3회에서 고미란(원진아)은 실험 전에 가족과 함께 이 집에 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1165, 26546, 16770, '10회에서 굿나잇 서점 벼룩시장이 열린다. 14회에서는 봄이 오는 것을 축하하기 위해 매년 혜천 떡 축제가 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1166, 26547, 16770, '굿나잇서점 북클럽 회원들과 북현 마을 사람들은 보통 이 길을 다닌다. 또한 이 거리에는 마을버스 정류장 북현삼거리가 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1167, 26548, 16770, '이장우(이재욱)가 일하는 혜천시청 건물이다. 2회에서는 공중전화 부스에서 마주친 목혜원(박민영)을 이곳으로 초대해 커피를 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1168, 26549, 16770, '박힌돌(윤상화 분)이 운영하는 \'산들서점\'으로 등장한다. 5회에서 임은섭(서강준)은 책을 사러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1169, 26550, 16770, '북현마을에 위치한 임은섭(서강준) 양부모의 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1170, 26190, 16770, '이 통나무 다리는 목혜원 (박민영)이 그녀의 이모가 쓴 시를 낭송 할 때 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1171, 26551, 16770, '임은섭(서강준)의 비밀장소 숲속 오두막은이 마을의 숲에 있으며 이 마을은 목해원(박민영)이 생을 마감하기 위해 마을에 온 것처럼 어두운 삶의 배경이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1172, 26552, 16770, '3회에서 \'혜천역\'으로 나오는 역이다. 심명녀(문정희)와 차윤택(황건)이 만나 명녀가 윤택과 헤어진 이유를 다시 이해해보는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1173, 26553, 16770, '목혜원(박민영), 임은섭(서강준), 이장우(이재욱)등 혜천고교 동창들이 재회한다. 은섭이 고등학교 시절 혜원을 좋아했다고 고백하는 곳도 이곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1174, 26554, 16770, '11회에서 임은섭(서강준)과 목해원(박민영)과 함께 공원에 오고 그녀는 그에게 자신의 과거를 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1175, 26555, 16770, '북현 마을 사람들이 다니는 동네 고등학교 혜천고등학교로 등장한다. 주최 이장우(이재욱)의 혜천고 50주년 합동동창회 장소이기도 하다. 6회에서는 목해원(박민영)이 당황한 임은섭(서강준)에게 자신의 마음을 고백하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1176, 26556, 16770, '14회에서 2010년 9월 4일의 사건을 회상하던 중 목해원(박민영)의 이모 심명여(문정희)는 여동생 심명주(진희경)를 만나기 위해 지하철 1번 출구로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1177, 26040, 16770, '5회에서 목해원(박민영)은 임은섭(서강준)과의 관계를 이야기할 때 어머니 심명주(진희경)와 함께 이 식당에 오게 된 경위를 회상한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1178, 26214, 16770, '13회에서 어른 심명여(문정희)는 이 골목에서 차윤택(황건)과 완전히 이별한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1179, 26557, 16770, '이장우(이재욱)와 동료 공무원들은 2020년 새해를 맞아 이 정자까지 하이킹을 한다. 8회에서 임은섭(서강준)과 목해원(박민영)이 처음으로 키스하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1180, 26558, 15857, '남자친구에서 박보검이 혼자 간 항구');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1181, 25868, 15857, '9회에서 김진혁(박보검)에 대한 구설수와 꼬치꼬치 캐묻는 시선을 도망친 차수현(송혜교)은 죽은 남편을 그리워하는 찻집 주인의 한탄을 듣고 이 해안도로를 따라 그를 만나러 달려간다. 차수현은 이 길에서 차를 세우고, 심곡항에서 진혁이 책을 읽고 있는 것을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1182, 24923, 15857, '남자친구에서 송혜교, 박보검이 간 바닷가');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1183, 26559, 15857, '4화에서 차수현(송혜교)을 데리고 커피를 마시러 왔다가 서울에서 여기까지 오면서 그녀의 어깨 위에서 거의 바로 잠이 든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1184, 26560, 15857, '남자친구에서 송혜교, 박보검이 만난 산책로');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1185, 26561, 15857, '3회에서 이 호텔은 차수현(송혜교)이 묵고 김진혁(박보검)을 만나는 동화호텔 속초점으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1186, 25641, 15857, '12회에서 이대찬(김주헌)이 장미진(곽선영)에게 저녁식사를 하며 데이트를 승낙하겠냐고 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1187, 25948, 15857, '수현의 아버지 차종현(문성근)의 오랜 친구인 정표가 이곳에 안장되고 그를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1188, 26030, 15857, '7회에서 김화진 회장(차화연)은 당대표 차종현(문성근)과 함께 정당을 합병하는 의미로 이 식당에서 저녁을 먹었습니다. 차씨는 거절하고 자리를 뜨지만 이 식당을 나오려다 기자들에게 기습을 당합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1189, 25607, 15857, '차수현(송혜교)과 김진혁(박보검)이 11화에서 데이트를 위해 메타세쿼이아 골목길로 향한다. 진혁은 수현에게 사진 찍는 법을 보여주고 나중에 수현에게 커플링을 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1190, 26562, 15857, '5회에서 차수현(송혜교)은 함께 찻집을 찾은 뒤 이 지하철역 5번 출구에서 김진혁(박보검)을 내려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1191, 26563, 15857, '남자친구에서 송혜교, 박보검이 간 휴게소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1192, 26564, 15857, '2회에서 김진혁(박보검)과 차수현(송혜교)이 라면을 먹으러 이곳에 들렀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1193, 26325, 15857, '김진혁(박보검)이 친구이자 동료인 조혜인(전소니)과의 대화를 나누며 차수현(송혜교)과의 지난 대화를 곰곰이 생각하며 호텔 옥상정원에서 이 다리를 내려다본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1194, 26554, 15857, '8회에서 김진혁(박보검)이 차수현(송혜교)를 데려와 함께 시를 쓰고 사진을 촬영한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1195, 26565, 15857, '8회에서 김진혁(박보검)과 차수현(송혜교)이 이별하고 속초로 가기전 이곳에서 데이트를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1196, 25728, 15857, '14회에서는 김진혁(박보검)과 차수현(송혜교)이 이 곳으로 데이트를 하러 오고 진혁이 그의 휴가 계획을 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1197, 24935, 15857, '5회에서 정우석(장승조)은 이 술집 B2 라운지 옆 개인실 캐빈에서 비서로부터 김진혁(박보검)에 대한 비밀 보고를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1198, 24902, 15857, '차수현(송혜교)은 2회 말미에서 여기에서 비즈니스 미팅을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1199, 26428, 15857, '8회에서 동화의 새해 복면 무도회는 이 장소에서 열리는데, 김진혁(박보검)과 차수현(송혜교)이 키스로 재회하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1200, 26566, 15857, '3회에서 겁에 질린 차수현(송혜교)은 술집에서 전남편 정우석(장승조)을 만나 엄마의 재혼 음모를 막고 무고한 직원 김진혁(박보검)의 명예를 훼손하지 말라고 경고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1201, 26038, 15857, '9회에서 차수현(송혜교)은 설날 식당에서 아버지에게 값비싼 한식을 대접한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1202, 26567, 15857, '11회에서는 차수현(송혜교)과의 관계를 걱정하는 아버지 김장수(신정근)와 함께한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1203, 26568, 15857, '남자친구에서 박보검, 고창석이 식사한 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1204, 26569, 15857, '남자친구에서 송혜교, 김호창이 만난 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1205, 26570, 15857, '5회에서 차종현(문성근)은 오랜 친구이자 운전사 남명식(고창석)과 함께 술 한 잔을 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1206, 26571, 15857, '5화에서 김진혁(박보검)이 차수현(송혜교)을 데리고 산책을 온다. 수현은 진혁이가 어떻게 미술 전시회를 보고 싶다고 말했는지에 대해 크게 궁금해했고, 그는 그녀에게 다리 자체에 미술 전시회가 있다는 것을 보여주었다. 속았다고 주장하면서도 \'홍제동 도감\'이 길을 따라 다양한 예술 작품을 계속 설명하자 수현은 진혁의 뒤를 따랐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1207, 26572, 15857, '남자친구에서 고창석, 김혜은이 간 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1208, 26573, 15857, '남자친구에서 송혜교가 박보검 데려다준 터미널');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1209, 25734, 15857, '2회에서 차수현(송혜교)의 아파트 창문으로 한강의 야경이 찍히는데 이 다리를 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1210, 26574, 15857, '남자친구에서 송혜교, 박보검이 만난 베이커리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1211, 26575, 15857, '10회에서는 김진혁(박보검)이 차수현(송혜교)에게 속초호텔로 보내졌다는 사실을 알게 된 정우석(장승조)이 전처 수현에게 구애하기 위해 \'꽃의 바다\' 캠페인을 펼친다. 그를 거절하기 위해 수현은 일부러 그를 이곳 \'스카이바\'에서 몇 시간 동안 기다리게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1212, 25909, 15857, '4화에서 전 시댁 진미옥(남기애)과 김화진(차화연)이 여기서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1213, 25335, 15857, '4회에서 김진혁(박보검)은 차수현(송혜교)과 함께 주말에 미술관을 방문하고, 차를 몰고 이 다리를 건너 집으로 돌아온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1214, 26576, 15857, '2화에서 김진혁(박보검)이 이 도서관을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1215, 25797, 15857, '15회에서 차수현(송혜교)은 김진혁(박보검)과 헤어지고 식당 \'더 리버\'가 내려다보이는 강을 내려다보며 잠시 멈춰선 뒤 이 다리 위를 혼자 걷는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1216, 25910, 15857, '11회에서 남명식(고창석)은 이 식당에서 기자 시절 제보자를 만나고 그에게 무언가를 찾아달라고 부탁합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1217, 26577, 15857, '16회에서 차수현(송혜교)과 김진혁(박보검)이 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1218, 26578, 15857, '김진혁(박보검)의 집으로 등장한다. 12회에서 차수현(송혜교)은 어머니의 경고를 무시하고 진혁의 집에 처음으로 가족을 만나기 위해 방문하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1219, 26579, 15857, '김진혁(박보검)의 집으로 등장한다. 12회에서 차수현(송혜교)은 어머니의 경고를 무시하고 진혁의 집에 처음으로 가족을 만나기 위해 방문하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1220, 26580, 15857, '14회에서 김진혁(박보검)이 동생을 구해준다. 이 경찰서에서 고민에 빠진 수현은 밖에서 기다리며 멀리서 그 광경을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1221, 26581, 15857, '최종 16회에서 김진혁(박보검)은 앞에서 차수현(송혜교)의 사진을 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1222, 26582, 15857, '남자친구에서 박보검, 전소니가 간 팬시점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1223, 26583, 15857, '7회에서 정우석(장승조)과 여자친구 수아(장희령)가 이곳에서 식사를 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1224, 26584, 15857, '남자친구에서 송혜교, 박보검이 간 국밥 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1225, 26585, 15857, '13회 엔딩에서 주연자(백지원 분)는 이곳 백미당 지점에서 차수현(송혜교 분)과의 만남을 주선하고 김진혁(박보검 분)과 헤어지기를 간청한다. 이 말을 들은 수현은 가슴이 찢어지는 듯한 울음소리로 대답할 수밖에 없었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1226, 26098, 15857, '마지막 16회에서 김진혁(박보검)과 차수현(송혜교)은 재회 데이트에서 이 집 앞에 앉아 아빠와 아이의 모습을 흉내냈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1227, 26586, 15857, '16회에서 차수현(송혜교)의 어머니가 딸이 제안한 대로 새 삶을 시작하고 꿈을 이루기 위해 이 갤러리를 구입한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1228, 26587, 15857, '남자친구에서 송혜교, 박보검이 데이트한 마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1229, 26588, 15857, '차수현(송혜교)의 아버지인 정치인 차종현(문성근)의 당사무실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1230, 26185, 15857, '14회에서 차수현(송혜교)과 김진혁(박보검)이 서점을 둘러보며 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1231, 26589, 15857, '남자친구에서 박보검이 간 사진관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1232, 26590, 15857, '남자친구에서 박보검이 졸업 기념 사진 찍은 학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1233, 26591, 15857, '남자친구에서 박보검과 동료들이 회식한 호프');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1234, 26592, 15857, '남자친구에서 박보검이 간 헌책방');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1235, 26593, 15857, '이 가게는 김진혁(박보검)의 부모님이 운영하는 과일가게로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1236, 26594, 15857, '남자친구에서 박보검이 아르바이트한 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1237, 26595, 15857, '2회에서 김진혁(박보검)이 집 밖 길을 따라 차수현(송혜교)을 기다리고 있다. 데리러 온 후 라면을 먹기 위해 휴게소에 함께 가자고 한다. 나중에 밤에 돌아올 때 수현은 진혁을 여기에 다시 내려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1238, 25841, 15857, '9회에서 김진혁(박보검)과 차수현(송혜교)은 이 영화관에서 영화를 보았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1239, 26596, 15857, '남자친구에서 송혜교, 박보검이 간 영화관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1240, 26597, 15857, '남자친구에서 박보검, 백지원이 만난 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1241, 25017, 15857, '1회에서 운명적인 여행을 마치고 한국으로 돌아온 차수현(송혜교)과 김진혁(박보금)이 이 곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1242, 26598, 15857, '13회에서 차수현(송혜교)과 김진혁(박보검)이 가족들과 저녁식사 후 손을 잡고 이 거리를 걷는다.');
