INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1246, 26602, 7145, '영화 남한산성의 배경이 된 도립공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1247, 26073, 7145, '영화 남한산성을 촬영한 테마파크');
