INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1249, 26603, 14868, '김사부에서 유연석이 자주 가던 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1250, 26604, 14868, '돌담병원 프리랜서 마취과 의사 남도일(변우민)이 운영하는 식당 오다가다로 등장한다. 이곳은 돌담병원 직원들이 퇴근 후 저녁식사를 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1251, 26605, 14868, '유연석과 최진호가 함께 차를 마신 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1252, 26606, 14868, '돌담병원의 정면이다. 김사부(한석규)가 수석 외과의로 일하는 시골의 작은 병원이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1253, 26607, 14868, '김사부,부용주(한석규)가 돌담병원으로 옮기기 전까지 일했던 거산대병원이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1254, 24935, 17362, '4화에서 백야재단 오정환(김태우)이 동료들을 이 술집에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1255, 25661, 17362, '최종회 16회에서 공혜원(김설현) 일행은 이 거리 마포관광정보센터 앞에서 범인을 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1256, 25335, 17362, '오프닝 추격 장면이 이 다리에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1257, 26613, 15738, '23회에서 고애린(정인선)이 부모 집으로 돌아와 아르바이트를 하는 모습이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1258, 26253, 15738, '16회에서 전 국정원장 권영실(서이숙)이 이 다리에 설치했다. 탈출을 위해 다리에서 뛰어내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1259, 26614, 15738, '6회에서부터 유지연(임세미)이 이끄는 국정원 \'왕의 가방\' 작전센터가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1260, 25604, 15738, '18회에서 이 건물은 진용태(손호준)의 저택으로 진용태가 살인 용의자로 수색된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1261, 25948, 15738, '31화에서 김본(소지섭), 고애린(정인선)은 이곳에서 휴식을 취하고 있는 진용태(손호준)를 만나기 위해 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1262, 26615, 15738, '5화에서 김본(소지섭)과 그의 전 국정원 동료 유지연(임세미)이 이곳에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1263, 26616, 15738, '김본(소지섭)과 그의 전 국정원 동료 유지연(임세미)이 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1264, 26493, 15738, '8회에서 애가 걱정하는 핸드백 사건에 대해 이야기하고 그녀가 일하는 회사에 문제가 있다고 결정한다. 10회에서는 김본과 애린이 아이들과 점심을 먹으러 왔고, 11회에서는 KIS가 여기서 다시 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1265, 26617, 15738, '이것은 \'J 인터내셔널\'이 29층에 사무실을 둔 \'스타 타워\' 빌딩이다. 16회에서는 J인텔 대표 진용태(손호준)가 살인범에게 쫓기며 이 건물을 뛰쳐나오고, 이후 김본(소지섭 분)이 모래시계를 가지러 이곳에 오다가 국정원 요원들에게 쫓기는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1266, 26618, 15738, '26회에서 김본(소지섭)이 이 곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1267, 26177, 15738, '31회에서 김본(소지섭)은 함정을 늦게 깨닫고 총살당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1268, 26153, 15738, '진용태(손호준)는 김본(소지섭)과 유지연(임세미)의 미행을 받으며 문성수(김명수) 의원과 8회 방송분에서 업무상 만남을 갖는다. 9화에서 지연은 식당 직원들과 만남을 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1269, 25668, 15738, '17회에서 권영실(서이숙)이 \'더 보이스\'를 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1270, 25608, 15738, '김본(소지섭)은 남산타워 발치에서 모히토와 함께 성공적인 미션의 종료를 축하하는 강박관념을 가지고 있다. 그는 이미 전 여자친구에게 이것을 제안했지만 그녀는 임무 중에 죽었다. 그런 그가 고애린(정인선)에게 같은 제안을 한ㄷ');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1271, 26501, 15738, '7회에서 김본(소지섭)과 전 국정원 동료 유지연(임세미)이 이 건물 앞에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1272, 26273, 14455, '3화에서 오혜상(박세영)과 주세훈(다상우)은 여기서 결혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1273, 25607, 13448, '장준하(남궁민)는 20회에서 이복 여동생 봉우리(황정음)를 데리고 온다. 그가 마지막으로 우리 앞에 봉마루로 등장하는 것과 우리도 모르게 마지막 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1274, 26619, 13448, '차동주(김재원)는 어머니 태현숙(이혜영), 그의 계부 최진철(송승)과 함께 이 저택에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1275, 26620, 13448, '5회에서 이 식물원은 봉영규(정보석)가 일하는 \'예술정원\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1276, 26621, 13448, '5화에서 차동주(김재원)가 해외여행을 마치고 서울을 돌아다닐 때 자전거를 타고 이 카페를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1277, 26622, 13448, '5화에서 어른 봉우리(황정음)가 이승철(이규한)과 함께 길을 걷고 있을 때 로데오현대아파트 쇼핑센터에서 자신의 \'오빠\' 장준하(남궁민)를 본 것 같은 생각이 든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1278, 26623, 13448, '차동주(김재원)의 아버지가 운영하는 우경그룹 사옥으로 등장하는 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1279, 26093, 13448, '차동주(김재원 분)는 밤이면 성산대교 근처 이 공원에 차를 대고 14화에서 베토벤의 제2교향곡을 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1280, 24948, 13448, '장준하(남궁민)가 16회에서 이복 여동생 봉우리(황정음)를 데리고 온다. 그녀는 그가 몇 년 전에 헤어졌고 그녀의 오빠에 대한 기억을 그와 공유하기 때문에 그가 그녀가 찾고 있는 이복동생이라는 것을 알지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1281, 25894, 13448, '16회에서 장준하(남궁민 분)는 이복 여동생 봉우리(황정음 분)를 데리고 와서 항상 이 케이블카를 타고 싶다고 말했다. 손에 든 표를 가지고 돌아오지만, 유리가 정신이 없는 것을 눈치채고, 유리가 표를 이미 샀는지 묻자, 그는 그것을 숨기고 그녀가 곧 떠나야 할 것이라는 것을 알고 그것을 부인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1282, 25715, 13448, '6회에서 이 호텔에서 축하파티를 연다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1283, 26624, 13448, '6회에서 장준하(남궁민)가 이 병원에서 일하기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1284, 25017, 13448, '15회에서 봉우리(황정음)가 나미숙(김여진)을 이곳으로 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1285, 25605, 14146, '강동하(감우성)의 목장으로 등장한다. 4화에서 이봄이(최수영)가 그에게 계약을 받으러 왔을 때, 그는 자신의 땅을 보여주었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1286, 26625, 15651, '1,2회에서 한국대학교 화학과 1학년 학생들의 오리엔테이션이 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1287, 26626, 15651, '도경석(차은우)의 절친 우진(이태선)이 운영하는 베를린바 입구로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1288, 26627, 15651, '강미래(임수향 분), 도경석(아스트로 차은우 분), 현수아(조우리 분)가 화학 전공자로 등록하고 연우영(곽동연 분)이 조교로 활동하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1289, 26278, 15651, '6회에서 강미래(임수향 분)가 이곳에서 친구 용철에게 고백했던 고등학교 시절을 회상한다. 하지만 그는 그녀가 \'강 헐크\' 또는 \'강 오크\'라고 부르며 그녀가 징그럽다고 거부한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1290, 26628, 15651, '1,6회에서 그녀의 시간 회상에서 고등학생 강미래(임수향)는 친구 용철에게 고백했다. 그러나 그는 그녀가 그녀를 \'강헐크\' 또는 \'강옥\'이라고 부르는 것을 거부하고 모두가 그녀를 \'그럭저럭\'이라고 부른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1291, 26256, 15651, '4회에서 도경석(아스트로 차은우)은 보궐선거를 앞두고 아버지 도상원(박성근), 누나 도경희(김지민)와 함께 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1292, 26086, 15651, '드라마 안에서 이 곳은  "한국대학교"로 나온다. 드라마 주인공들은 해당 대학의 학생으로 강미래(임수향 분), 도경석(아스트로 차은우 분), 현수아(조우리 분)가 화학 전공자로 등록하는 장면과 연우영(곽동연 분)이 조교로 있는 곳이다. 2화에서 미래와 경석은 법학부 건물 앞에서 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1293, 26629, 15651, '3회에서 현수아(조우리)는 이 카페에서 아르바이트를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1294, 24937, 15651, '4회에서 켈룬 퍼퓸 이벤트가 열리는 곳이 바로 이 공간이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1295, 26630, 15651, '12회에서 이곳의 버스를 기다리던 강미래(임수향)에게 도경석(아스트로 차은우)이 오더니 그냥 지나친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1296, 25877, 15651, '도경석(차은우)은 14회에서 강미래(임수향)를 이 식당에 초대해 저녁을 먹는다. 그녀는 그와 공공장소에 있는 것이 두렵고 그들의 두 반 친구들도 여기서 밥을 먹는 것이 도움이 되지 않는다는 것을 느낀다; 미래는 달리기를 하러 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1297, 26631, 15651, '1회에서 강미래(임수향)가 아틀리에 코롱 강남점을 찾는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1298, 26632, 15651, '9회에서 도경석(차은우)이 아침을 사러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1299, 26633, 15651, '이 계단은 룸메이트인 강미래(임수향 분)와 오현정(민도희 분)은 물론 이웃 연우영(곽동연 분)과 도경석(아스트로 출신 차은우 분)의 귀가길에 놓여 있다. 여기 8, 9, 10화에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1300, 26634, 15651, '6회에서 연우영(곽동연)은 이 옥상 아파트로 이사한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1301, 26635, 15651, '15회에서 도경석(차은우), 강미래(임수향)는 현수아(조우리)가 불편해하는 것을 원하지 않기 때문에 지렁이에서 여름 프로그램을 마치고 점심시간에 외출할 때 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1302, 25864, 15651, '도경석(아스트로 차은우)의 어머니 나혜성(박주미)은 3화에서 한국으로 돌아올 때 택시를 타고 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1303, 25017, 15651, '3화에서 도경석(아스트로 차은우)의 엄마 나혜성(박주미)이 이 곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1304, 26636, 13363, '차대웅(이승기)과 구미호(신민아)는 이 정류장에서 버스를 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1305, 26637, 13363, '차대웅(이승기)이 구미호(신민아)를 처음 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1306, 26638, 13363, '이 대학은 \'국제대학교\'로 나온다. 채대웅(이승기)이 다니고 학교와 그 체육관[중앙대학교]의 내부가 액션 스쿨의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1307, 25338, 13363, '내 여자친구는 구미호에서 이 건물 로비가 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1308, 25802, 13363, '3회에서 구미호(신민아)는 버스에서 아이와 장난을 치고(딸이 너무 귀여워서 잡아먹을 수 있다던 엄마 흉내를 내며) 갑자기 당황한 차대웅(이승기)은 질질 끌고 그녀와 이 거리에서 내렸습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1309, 25797, 13363, '6화에서 차대웅(이승기)과 박선주(신민아)가 타는 버스가 이 다리를 지나간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1310, 26273, 13363, '11화에서 차태웅(이승기)은 마침내 그가 미호(신민아)를 좋아한다는 사실을 인정하고 이 성당 앞에서 그녀에게 고백한다. 그리고 마침내 그들은 서로의 감정에 확신을 주었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1311, 26639, 12820, '15회에서 현진헌(현빈)은 김삼순(김선아), 현미주(서지희)를 이곳에 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1312, 26640, 12820, '9회에서 모두가 현진헌(현빈)의 형의 기일을 기념하기 위해 가는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1313, 26185, 12820, '훈진헌(현빈)과 유희진(정려원)은 현미주(서지희)와 함께 이 서점을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1314, 25633, 12820, '이 계단은 16화의 유명한 마지막 키스 장면을 통해 유명해졌고 결국 그 이름을 따서 지어졌다. 그 이후로, 그들은 정기적으로 촬영 장소로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1315, 25863, 12820, '김삼순이 일하는 파티시에 장소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1316, 26558, 5147, '16회에서 오예지(임수향)와 서환(지수)은 이 항구에서 조개를 먹고 등대 근처에서 1박 여행을 하며 셀카를 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1317, 25868, 5147, '오예지(임수향 분)와 서환(지수 분)은 최종회 16회에서 하룻밤 여행으로 이 길을 따라 자전거를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1318, 26641, 5147, '16회에서 오예지(임수향)는 그녀의 삶을 회상하기 위해 이 등대에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1319, 26642, 5147, '4회에서 서진(하석진)이 오예지(임수향)에게 프러포즈하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1320, 26030, 5147, '11회에서 김연철(권혁)과 방영근(이재용) 회장이 이 식당에서 오찬을 하며 진환건설의 적대적 인수 방안을 논의를 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1321, 25907, 5147, '4회에서 오예지(임수향)와 서진(하석진)이 결혼식을 올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1322, 25865, 5147, '1회에서 오예지(임수향)가 야외 드로잉 수업을 위해 자신의 반을 이곳으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1323, 26643, 5147, '13회에서 서환(지수)과 엠버(스테파니리)는 이 카페에서 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1324, 26644, 5147, '2회에서 캐리정(황승언)이 이 술집에서 친구들을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1325, 26645, 5147, '6회에서 오예지(임수향)는 도자기 전시회 \'서성곤, 흙의 기억\'에 참석하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1326, 25797, 5147, '8회에서 서환(지수)은 오예지(임수향)를 동생 차에 태우고 이 다리를 건너자, 대화가 점점 뜨거워지고 카페 구럼에 바짝 다가서며 오예지(임수향)에게 조금만 양해를 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1327, 25781, 5147, '5화에서 서진(하석진)이 오예지(임수향)와 결혼한 후, 그의 전 여자친구인 캐리 정(황승언)은 이 펜트하우스에서 그를 다시 한 번 잡으려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1328, 24954, 5147, '14회에서 캐리가 강기석(김태겸)에게 칼에 찔린 곳이 바로 이 호텔이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1329, 25017, 5147, '7화에서 서환(지수)이 오예지(임수향)를 찾기 위해 미국 유학을 쉬고 공항에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1330, 26646, 5147, '남편 서진(하석진)이 실종된 지 3년 만에 오예지(임수향)는 6~7회에서 신혼여행지인 제주도로 돌아가 이 예배당을 비롯해 그들이 함께 했던 모든 장소로 돌아간다. 서환(지수)이 미국에서 날아온 뒤 처제가 걱정돼 그녀를 발견한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1331, 26647, 5147, '6회에서 오예지(임수향)는 이 숲길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1332, 26648, 5147, '6,7회에서는 남편 서진(하석진)이 실종된 지 3년 후 신혼여행지 제주도로 돌아온 오예지(임수향)의 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1333, 26649, 5147, '오예지(임수향)는 6회에서 제주도에 머무는 동안 산책로 관광지 \'오예지\'에 찾아와 그리운 남편과 이곳에서 함께했던 시간들을 추억한다. 7화에서 제주도 예지를 깜짝 방문한 서환(지수)은 낚시를 하자고 제안하고 두 사람은 이 해변으로 오게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1334, 25800, 13464, '3회에서 현기준(강지환)은 변호사 천재범(류승수)을 찾기 위해 이 건물을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1335, 25953, 13464, '이 호텔은 현기준(강지환)이 운영하는 \'월드호텔\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1336, 26285, 13464, '현기준(강지환)은 어머니 현명진(오미희)과 이 식당에서 점심식사를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1337, 26650, 13464, '2회에서 공아정(윤은혜)이 이 거리를 걷다가 자신을 따라온 현상희(성준)의 발을 멈추게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1338, 26651, 13464, '이 공원은 현명진(오미희)의 맨션으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1339, 25017, 13464, '1회에서 현상희(성준)가 이곳에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1340, 26652, 14147, '윤세나(정수정)은 이곳에서 아르바이트를 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1341, 26030, 6220, '우장훈과 고기자가 이야기를 나눈 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1342, 26623, 6220, '우장훈의 기자회견 장면을 촬영한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1343, 26653, 6220, '안상구가 옥상에서 라면을 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1344, 25809, 6220, '안상구가 수감된 교도소로 나온 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1345, 26654, 6220, '내부자들의 파티장면을 촬영한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1346, 26655, 6220, '안상구가 조상무에게 공격당한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1347, 25641, 14945, '5화에서 강우일(윤박)은 약혼녀 은이수(공승연)와 비록 오래 머물지 않더라도 이곳에서 크리스마스 이브에 저녁 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1348, 25607, 14945, '2, 3회에서 은환기 대표(연우진)가 메타세쿼이아 골목길을 지나며 자신의 의지에 반해 의류 택배기사가 된 패션 촬영을 진행한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1349, 25824, 14945, '11화에서 사일런트 몬스터 팀이 이벤트를 위해 사용하는 예술 공간입니다. 공간을 준비하던 중 모두가 식중독에 걸리고 은환기(연우진)가 그들을 병원으로 데려다준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1350, 24933, 14945, '은환기(연우진)는 5회에서 비서 채지혜(한채아)와 함께 이곳에서 데이트 연습을 한다. 그곳은 또한 그가 같은 에피소드의 후반부에서 크러쉬하기로 되어 있는 장소이기도 하지만 모든 것이 다르게 진행된다. 13화에서는 채로운(박혜수)이 전시회를 위해 이곳에 왔다가 강우일(윤박)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1351, 25870, 14945, '6회에서 고아원으로 등장하는 이 센터로 채로운(박혜수)을 태우고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1352, 25613, 14945, '13화에서 여기서 강우일(윤박)은 약혼자의 부모에 대한 태도를 끊는다. 채로운(박혜수)은 이 말을 듣고 그녀에게 또렷이 다가와 은환기(연우진)는 이 말을 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1353, 26327, 14945, '은환기(연우진)는 7회에서 딸 은이수(공승연)가 강우일(윤박)과 결혼할 때가 됐다고 판단하고 모두에게 빨리 결혼식장으로 와달라고 당부한다. 비난을 받고 누명을 쓴 우일은 이수의 간청에도 아랑곳하지 않고 달려나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1354, 25943, 14945, '10화에서 은환기(연우진)와 채로운(박혜수)이 웅변연습하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1355, 26039, 14945, '1화에서 은환기(연우진)가 수영을 하고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1356, 26443, 14945, '2회에서 은환기(연우진)가 이곳에서 자살한 전직원에게 사과하기 위해 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1357, 26656, 14945, '이 곳은 \'뉴욕 이발소\'로 나타납니다. 이곳은 채로운(박혜수)과 그녀의 가족이 사는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1358, 26080, 18737, '11회에서 구련(김희선), 박중길(이수혁), 임령구(윤지온)가 궁궐 일대에서 한복판 사신복을 입고 등장하는 짧은 장면.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1359, 26657, 18737, '6회에서 이 집에는 한국전쟁 참전용사 이영천(전무송)이 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1360, 26174, 18737, '2회에서 최준웅(김석우/SF9 로운)과 구련(김희선)이 스튜디오 앞에서 브리핑을 하고 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1361, 25792, 18737, '외관은 내일의 NBS 빌딩으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1362, 26399, 18737, '9회에서 최준웅(로운)이 카페에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1363, 26658, 18737, '1화에서 옥황상제(김해숙)가 아침 정기점검차 이곳을 지나가는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1364, 26542, 18737, '구련(김희선) 6회에서 이 곳을 손가락으로 톡톡 두드려 그가 편히 죽게 해준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1365, 26659, 18737, '4회에서는 최준웅(김석우/SF9 로운)과 임륭구(윤지온)가 강우진의 사건을 해결하기 위해 연이어 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1366, 26660, 18737, '4회에서 싱어송라이터 강우진(강승윤)과 허나영(이노아)이 사는 곳으로 최준웅(로운)과 임령구(윤지온)가 강우진의 사건을 해결하기 위해 연이어 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1367, 26139, 18737, '싱어송라이터 강우진(강승윤)과 허나영(이노아)이 살고 있는 곳이다. 4회에서는 최준웅(로운)과 임령구(윤지온)가 잇따라 이곳에 와서 강우진의 사건을 해결한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1368, 26661, 18737, '4회에서 이상엽 마푸구청장 일행이 허나영(이노아)을 찾기 위해 이 계단을 걸어 내려가고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1369, 25797, 18737, '1회에서 최준웅(로운)이 이 다리에 와서 목숨을 끊으러 온 사람을 구하려다가 둘 다 함께 한강에 빠진다. 이 모든 것을 죽음의 천사 구련(김희선)과 임령구(윤지온)가 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1370, 26662, 18737, '6회 에필로그에서 최준웅(로운)은 한국전쟁 참전용사 이영천(전무송)을 데리고 이 스튜디오로 나와 사진을 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1371, 26041, 18737, '4회에서 구련(김희선)은 최준웅(김석우/SF9 로운)에게 커피를 마시러 오라고 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1372, 26663, 18737, '9회에서 저승사자 임령구(윤지온)가 길 건너편에서 임산부를 지켜보는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1373, 25799, 18737, '4회에서 과거 미션을 수행하던 중 최준웅(김석우/SF9 로운)과 구련(김희선)은 이 치킨집을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1374, 26043, 18737, '구련(김희선)과 저승사자 임령구(윤지온)는 이 카페에서 김훈(차학연)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1375, 26664, 18737, '3,4회에서 사건을 해결하기 위해 최준웅(김석우/SF9 로운)과 구련(김희선)이 맡은 과거로의 포털로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1376, 25989, 14972, '유소준(이제훈)은 4회에서 송마린(신민아)을 호화로운 식사에 초대했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1377, 26122, 14972, '1화에서 유소준(이제훈)이 회장으로 있는 투자회사 마이리츠 사옥으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1378, 25827, 14972, '2회에서 송마린(신민아 분)은 온라인 쇼핑몰 촬영을 위해 막판 사진작가 교체로 나선다는 연락을 받고 마지못해 유소준(이제훈 분)과 함께 이곳으로 달려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1379, 26665, 14972, '2019년 송마린(신민아) 개인전이 열리는 곳. 15회에서 유소준(이제훈)의 모습이 담긴다. 그는 시간에 맞춰 현재로 돌아와서 직접 전시를 관람한다. 소준은 갤러리 입구 맞은편 계단에서 마린의 아버지이기도 한 시간여행 \'마스터\' 두식(조한철)과 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1380, 26178, 14972, '2회에서 송마린(신민아)은 유소준(이제훈)을 만나기로 한 카페 콤마를 떠난 뒤, 이곳에 와서 소준에게 전화를 걸어 자신이 못 갔다고 말한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1381, 24927, 14972, '유소준(이제훈)은 지하철을 타고 시간여행을 한다. 1회 시작 부분에서 그는 9번 출구에서 서울역으로 들어가 지하철 1호선을 타고 2018년으로 여행을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1382, 25661, 17824, '1회에서 박세완(박세완)과 제이미(신현승)는 이른 아침 북적이는 이 거리를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1383, 26666, 15546, '27회에서는 강하니(서인아 분)가 이 주점에서 이한결(진주형 분), 황지은(하승리 분)과 만나 이야기를 나눈다. 하니는 술이 너무 많아 박도경(이창욱 분)이 도착하자마자 한결과 티격태격하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1384, 24928, 14176, '6,7화에서 음악 부트캠프가 열리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1385, 26418, 14176, '차유진(주원)이 자란 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1386, 26419, 14176, '여기는 설내일(심은경), 차유진(주원)과 연후(박보검)가 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1387, 26667, 14176, '한음음대 콘서트홀 중 하나이다. 12회, 13회에서 설내일(심은경)은 이 홀에서 열린 피아노 리사이틀 준결승과 결승에 진출했고 유진(주원)과 윤후(박보검)가 응원하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1388, 26668, 14176, '유진(주원) 엄마 카페의 배경이 되었다. 외관은 원래 매장의 모습이지만 매장 내부는 세트장에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1389, 26249, 13192, '8회에서 은소현(선우선)이 운영합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1390, 26213, 13192, '천지애(김남주)의 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1391, 25607, 14329, '16화 마지막 장면에서 최무각(박유천)과 오초림(신세경)이 이곳에서 함께 자전거를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1392, 26669, 14329, '2회에서 최무각(박유천)이 오초림(신세경)의 냄새 보는 능력을 이곳에서 테스트한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1393, 25827, 14329, '15회에서는 최무각(박유천 분)이 이 건물 앞 분수대에서 오초림(신세경 분)에게 청혼할 계획을 세웠다. 그는 여자친구에게 청혼하는 남자를 보고 반대를 결심했고, 초림은 그런 공개적인 제안을 싫어한다고 말했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1394, 25636, 14329, '3회에서 최무각(박유천) 오초림(신세경)은 극단 오디션을 보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1395, 25608, 14329, '최무각(박유천)과 최은설(신세경)은 이곳에서 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1396, 26670, 17896, '1회에서 시신이 실린 파손된 자동차와 검은 고양이가 이 자리에 주차되어 있는 모습이 목격된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1397, 25930, 17896, '2화에서 강다정(서현진)은 채준(윤박)이 카페에 두고 간 장미 종이냅킨에 “용산역, 99, 7641”이라는 쪽지를 보고 이 역으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1398, 26671, 17896, '6화 이후 눈은 사람에게 미친 짓을 할 구실을 준다고 생각한 강다정(서현진 분)과 주영도(김동욱 분)는 한복 대여점에서 의상을 빌려 입고 거리를 누빈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1399, 24922, 17896, '6회에서 강다정과 주영도는 저승사자, 처녀귀신 의상을 입고 이 거리를 활보한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1400, 24915, 17896, '드라마 주인공인 강다정(서현진)은 이 호텔의 컨시어지 매니저로 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1401, 25948, 15605, '30화에서 남신은 이곳에서 서종길(유오성)을 만나고, 감독은 그에게 회장과 아버지의 녹음을 건네준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1402, 26672, 15605, '25회에서 남신3(서강준)과 인간 남신(서강준)은 카페 \'채소\' 밖에서 마주친다. 강소봉(공승연)이 안에서 기다리고 있는 모습이 나온다. 이후 남신3은 소봉의 눈을 사로잡았고, 그녀는 카페 발코니에서 소봉에게 말을 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1403, 26673, 15605, '18~19회에서 서예나(박환희)와 남신3세(서강준)의 결혼식이 치러질 예정인 곳이다. 남신3세가 강소봉(공승연)을 구하기 위해 떠나는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1404, 26674, 15605, '이 병원은 5회에서 로봇 남신Ⅲ(서강준)가 병동을 맡았던 \'PK의료원\'으로 등장하며, 6회에서 남신(서강준)은 체코에서 한국에 도착했을 때 이사를 온다. 19회에서는 강소봉(공승연)도 이곳으로 병동을 옮겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1405, 26675, 15605, '강소봉(공승연)의 아버지 강재식(김원해)과 그의 두 연습생은 이 카페에서 자신과 남신Ⅲ(서강준)의 관계를 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1406, 24935, 15605, '4화에서 지영훈(이준혁)과 남신Ⅲ(서강준)가 조 기자를 이 술집으로 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1407, 26228, 15605, '4회에서 PK모터스의 M-Car 런칭 애프터 파티가 이곳에서 열렸습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1408, 26676, 15605, '15회에서 서예나(박환희)와 로봇 남신Ⅲ(서강준)가 웨딩드레스를 보러 오는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1409, 26622, 15605, '15회에서 남신Ⅲ(서강준)와 강소봉(공승연)은 서예나(박환희)와의 브라이덜 숍 실패 후 이곳을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1410, 26677, 15605, '13회에서 건호 회장(영규)와 지영훈(이준혁)은 레스토랑 우마이(우마이)에서 남신Ⅲ(서강준)를 만나기로 한다. 그리고 자신의 자리에 진짜 남신이 왔다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1411, 25795, 15605, '33회에서 조 기자(김현숙)가 이 광장 한가운데 당당히 서 있다. 인공지능 로봇이 주변 건물 액정화면에 뜬 뉴스 스토리로 올라가는 남신Ⅲ(서강준)의 모습을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1412, 25722, 15605, '18회에서 강소봉(공승연)과 남신Ⅲ(서강준)가 자전거를 타고 이곳으로 향한다. 그는 그녀 주변에 있을 때 자신이 로봇이라는 사실을 숨길 필요가 없다고 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1413, 26678, 15605, '11회에서 강소봉(공승연)은 남신Ⅲ(서강준)를 바라보며 식사를 하고 있다. 아줌마는 남신Ⅲ를 초대해달라고 소봉에게 잔소리를 하지만 그녀는 식사를 계속한다. 비가 내리기 시작하고 소봉은 도망치려 하지만 남신Ⅲ는 소봉을 보호하기 위해 우산을 들고 돌아온다. 식당 밖 소봉은 자신의 \'로봇노예\'가 되겠다고 말했고, 소봉은 일찍이 청혼을 거절하는 것을 도왔기 때문에 이에 동의한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1414, 25724, 15605, '17회에서 서종길(유오성)은 이 다리 밑에서 자신의 키다리와 만나 정보를 얻는다. 종길의 제보자여야 할 강소봉(공승연)이 자신들에게 뭔가를 숨기고 있다고 판단한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1415, 25335, 15605, '로봇 남신 3세(서강준)는 8화에서 오토바이를 타고 이 다리를 건너간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1416, 26679, 15605, '강소봉(공승연 분)이 이곳에서 지영훈(이준혁 분)을 만난다. 그녀에게 회장이 로봇으로서 남신Ⅲ(서강준)의 진정한 정체성을 알고 있다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1417, 26656, 15605, '18회에서 강소봉(공승연)과 남신Ⅲ(서강준)가 자전거를 타고 이 집 앞 계단을 올라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1418, 26680, 15605, '오로라(김성령)와 데이비드(최덕문)가 혼수상태에 빠진 인간 남신(서강준)을 돌보기 위해 머무르는 은신처');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1419, 25856, 15605, '3화에서 PK그룹은 이 행사장에서 마하 자율주행차(또는 M-Car)에 대한 대규모 프레젠테이션을 한다. 서준길(유오성)은 \'사정\'으로 인해 남신(서강준)이 자리를 잡지 못하자 앞장서지만, 남신의 갑작스러운 등장은 자신을 대신해 로봇 남신Ⅲ인 줄 모르고 모두를 놀라게 한다. 강당 밖에서 강소봉(공승연)은 자신이 당한 억울함에 항의하지만, 나중에 남신이 있었다는 것을 알게 되면서 자신의 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1420, 26188, 15605, '1화에서 과학자 오로라(김성령)가 이 자리에서 인공지능의 미래에 대해 강연한다. 도중에 그녀는 7살 난 아들 남신(오한결)에게 방해를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1421, 25621, 15605, '10회에서는 남신(서강준)과 서예나(박환희)의 결혼 만남이 펼쳐진다. 남신Ⅲ가 결혼을 거부하기 위해 강소봉(공승연)에게 키스하는 모습을 보여주는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1422, 26100, 15605, '29회에서 남신(서강준)은 지영훈(이준혁)에게 이 건물의 옥상/헬리패드로 불려갔다. 그러나 그를 만나고 싶었던 것은 남신Ⅲ(서강준)였다. 남신Ⅲ는 인간 상대에게 자신을 내버려두라고 설득하고 옥상에서 몸을 던지려 하지만, 후자는 수동 모드로 자신을 제어하는 책임자를 보여주게 된다. 30화에서는 수동 모드 남신Ⅲ와 남회장(박영규)의 대결도 펼쳐진다. 말다툼 중에 강소봉(공승연)이 도착하고 남신Ⅲ는 그녀를 대신 돌린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1423, 26681, 15605, '남신Ⅲ(서강준)는 오토바이를 타고 이 다리를 건너 납치된 강소봉(공승연)을 구한 후 서종길의 에이전트(최병모)를 쫓게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1424, 26146, 15605, '19회에서 남신Ⅲ(서강준)는 서종길의 에이전트(최병모)를 추격해 이 대학 바로 앞 거리로 나왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1425, 25957, 15605, '28회에서 남신3(서강준)과 강소봉(공승연)이 데이트하러 온다. 그들이 산책하는 지역은 가을대에 있으며, 이 장면의 피아노 위치는 스무디킹 바로 앞에 있다. 29회에서는 인간 남신(서강준)이 이들을 따라 이곳까지 왔다는 사실이 공개됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1426, 26682, 15605, '남신Ⅲ(서강준)은 19회에서 납치당한 강소봉(공승연)을 구한 뒤 이 거리에서 서종길의 에이전트(최병모)를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1427, 26412, 15605, '이 건물의 내부 로비와 외부 모두 \'PK그룹\' 사옥으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1428, 26683, 15605, '17회에서 오로라(김성령)가 묵는 호텔이다. 지영훈(이준혁)은 서종길의 스파이가 자신을 호텔까지 미행한 사실도 모르고 남신Ⅲ의 행동에 대해 이야기하기 위해 찾아온다. 이후 로라는 택시를 타고 강소봉(공승연)을 만나기 위해 호텔을 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1429, 26684, 15605, '18회에서 (공승연)이 자전거를 탄 후 남신Ⅲ(서강준)와 전화통화를 하고 있다. \'노예로 돌아가라\'고 지영훈(이준혁)과 오로라(김성령)의 말을 듣고 다시는 만나지 않겠다고 한다. 잠시 후 남신Ⅲ는 소봉에게 어머니의 목걸이를 건네주고 마지막 작별인사를 하기 위해 번화한 길을 건너 소봉에게로 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1430, 26685, 15605, '36화 마지막회에서 강소봉(공승연)이 로봇 마이보와 함께 남신Ⅲ(서강준)에게 마지막 작별을 고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1431, 25017, 15605, '1회에서 남신(서강준)이 터미널에서 경호원의 호위를 받으며 이곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1432, 26686, 14378, '1회에서 차지안(장나라)은 이 거리를 따라 택시를 탄 이현(서인국)을 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1433, 25809, 14378, '1회 회상 장면에서 여기에서 이현(서인국)의 아빠가 죄수를 면회하는 장면(엑소 디오의 카메오)이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1434, 26687, 18157, '박영선(김보연) 병원장이 이끄는 \'태림병원\'으로 등장한다. 딸 안민서(장혜진 분)는 신경외과 원장, 정희주(고현정 분)는 형 정선우(신동욱 분)는 물리치료사로 활동하고 있다. 예를 들어 희주와 선우가 커피를 마시며 대화를 나누는 에피소드 1에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1435, 26688, 18157, '주영(신혜지)이 일하는 카페다. 4화에서 희주(고현정)는 리사(김수안)에게 고장난 후 7화에서 주영을 만나 새 전화기를 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1436, 26689, 18157, '이 단지의 입구는 드라마 안에소 안현성의 가족주택이 위치한 주택단지 \'산정\'의 일부로 등장한다. 3회에서 희정(고현주)과 해원(신현빈)이 설전을 벌인 장면 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1437, 25905, 18157, '1회에서 해원(신현빈)이 버스 정류장 유리에 비친 입술을 확인하고 있다. 같은 장면에서 이 버스 정류장에는 희주(고현정)의 개인 초대전 포스터가 광고되어 있는 것이 보인다 때까지 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1438, 26690, 18157, '2회에서는 교회에서 우연히 마주친 해원(신현빈)과 선우(신동욱)가 식당에서 점심을 먹는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1439, 26691, 18157, '1회에서는 정선우(신동욱 분)와 조카 안리사(김수안 분)가 이 식당에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1440, 24922, 18157, '5회 회상에서 해원(신현빈)은 모르는 번호로 걸려온 전화를 받았는데 사실 우재(김재영)였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1441, 24913, 18157, '3회에서 회상장면에서 이 산책로에서 데이트 중인 희주(고현정)와 우재(김재영)의 모습이 나온다 .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1442, 26273, 18157, '2회에서 고해성사를 받으려던 정선우(신동욱)는 이 교회 신도석에 앉아 있는 환자 구해원(신현빈)을 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1443, 26692, 18157, '신혼부부 정희주(고현정)와 안현성(최원영)의 집이 있는 곳이다. 현성의 어머니 박영선 김보연 분과 여동생 안민서(장혜진 분)는 남편 이형기(홍서준 분)와 함께 같은 주택단지다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1444, 26693, 18157, '윤상호(김상호 분)가 운영하는 바입니다. 예를 들어 1화에서 구해원(신현빈)이 이곳을 밥집으로 착각하는 장면이 나온다. 아직 간판을 교체하지 않았고 음식도 제공한다고 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1445, 26694, 18157, '1회에서 해원(신현빈)이 집으로 돌아가는 길에 터널을 걷고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1446, 26695, 18157, '안씨 일가가 운영하는 태림재단 태림여중으로 등장한다. 이곳은 안리사(김수안)가 다니던 학교이자 구해원(신현빈)이 전직 미술교사였던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1447, 26696, 18157, '6화에서 봤던 희주(고현정)와 우주(김재영)의 전시가 열리는 미술관 \'화인갤러리\'다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1448, 25737, 14379, '1회에서 오하나(하지원)가 질샌더(442호)와 구찌(462호) 매장을 지나며 이 거리를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1449, 26540, 14379, 'SS홈쇼핑 외관으로 나타납니다. 1회에서 마케팅 매니저 오하나(하지원)가 방문하여 담당자에게 제품을 계속 방영하도록 설득하는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1450, 26697, 14379, '드라마에서 탠디 본사로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1451, 25017, 14379, '최원(이진욱)은 아리아항공 승무원으로 이 공항을 여러 번 경유한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1452, 26698, 16160, '1회에서  이다솜(김유진)과 남자친구 고은태(이종원)는 오미리(김지인), 가짜 남자친구 한대강(재민)과 함께이 체인점 지점에서 더블 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1453, 26699, 16160, '엔씨소프트 식당이 서중대 식당으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1454, 26459, 16160, '13회에서 한대강(NCT 드림 재민)은 이로써 오미리(김지인)와의 가짜 연애를 끝낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1455, 26700, 16160, '이 대학교는 대오미리(이지인)와 한대강(NCT 드림 재민)이 다니는 \'서중대학교\'로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1456, 26701, 16160, '1회에서 그룹 동료 오미리(김지인), 한대강(NCT 드림 재민), 태민 선배가 이 식당에서 웹툰 그룹 프로젝트 브레인스토밍을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1457, 25641, 18739, '6화에서 육성환(김무준)이 이유민(서지혜)을 이 식당에 초대했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1458, 25718, 18739, '주니스의 커플이자 프로 배드민턴 선수인 양성실(전혜원)과 이지호(이채민)가 6화에서 팀을 위해 테이크아웃 버거 주문을 받고 이 공원을 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1459, 24931, 18739, '11회에서 남매 박태준(채종협)과 박준영(박지현)이 이 식당에 술을 마시러 오는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1460, 26702, 18739, '이태상 코치(조한철 분)가 박태영을 제지한 후  6회 초반 이 거리를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1461, 24932, 18739, '12회에서 육성환(김무준)은 이유민(서지혜)을 이 식당으로 초대해 봄까지만 기다려달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1462, 24900, 18739, '박태양(박주현)이 희망은행 배드민턴팀의 감독(김희창)과 재계약을 앞두고 있을 때 나오는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1463, 25829, 18739, '15회에서 연승우(최승윤)가 가족 회사의 신제품 샘플을 나눠주고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1464, 26703, 18739, '5회에서 육정환(김무준)과 이유민(서지혜)이 이 식당에서 라면을 먹는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1465, 26000, 18739, '1회에서 박태양(박주현)이 밤에 이 다리를 자전거로 집으로 돌아오는 모습을 보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1466, 26704, 18739, '2회에서 박태양(박주현)은 이 센터 앞에서 스타트 파인트를 들고 마라톤을 한다고 팀원들로부터 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1467, 25797, 18739, '2화에서 박태양(박주현 분)은 반포한강공원을 통해 자신의 마라톤을 할 때,  이 다리의 남쪽 아래에 멈춰 선다. 5화에서는 경쟁 상대인 소망은행 배드민턴팀 감독(김희창)과 계약 협상을 마친 박태환(박주현)과 아버지 박만수(전배수)가 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1468, 25776, 18739, '강유성(윤종훈 분)과 한대수(김대곤 분)는 5화에서 이 식당에서 한 잔 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1469, 26705, 18739, '1회에서 박태준(채종협)이 이 술집에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1470, 24950, 18739, '12회에서 자신의 어머니와 만나는 것을 막기 위해 경희궁 3가길을 따라 이 거리로 뛰어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1471, 26706, 18739, '3, 4회에서 유니스 배드민턴 선수들의 숙소로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1472, 26214, 18739, '8화에서 박태준(채종협), 박태양(박주현), 육정환(김무준)이 팀원 중 한 명을 위한 막바지 깜짝 생일 파티에 참여하기 위해 케이크를 들고 서둘러 이 거리를 내려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1473, 26707, 18739, '물에 빠진 후, 박태양(박주현)과 박태준(채종협)은 이 카페의 지붕 아래에서 피난처를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1474, 24914, 18739, '4화에서 박태양(박주현)이 박태준(채종협)에게 팀을 떠날지도 모른다고 말하는 카페 앞이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1475, 26708, 18739, '1회에서 박태양(박주현)과 박태준(채종협)은 우정을 다시 한 번 이 경기장을 지나며 걷는다. 말다툼을 하던 중 건물 앞 분수대가 출발해 잠시 물놀이를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1476, 25868, 18529, '11회에서 신입생들은 강릉으로 배낭여행을 갈 때 이 길을 따라 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1477, 26709, 18529, '11회에서 유대일(박성준)이 기획한 강릉으로의 배낭여행으로 신입생들이 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1478, 26710, 18529, '11화에서 민한지(이세미 분)가 조한솔(김권 분)을 위협하는 장면에서 \'클럽알토\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1479, 26711, 7712, '너의 결혼식에서 사랑을 확인한 바다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1480, 26712, 7712, '너의 결혼식에서 김영광이 박보영을 데리고 간 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1481, 26713, 7712, '너의 결혼식에서 김영광이 대학친구와 먹은 고기집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1482, 26714, 7712, '너의 결혼식에서 김영광이 친구들과 낚시한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1483, 26715, 7712, '너의 결혼식에서 김영광과 박보영이 헤어진 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1484, 26072, 7712, '너의 결혼식에서 김영광과 박보영이 데이트한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1485, 26716, 7712, '너의 결혼식에서 박보영이 송어 잡는 장면을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1486, 26717, 7712, '너의 결혼식에서 박보영과 김영광이 고기를 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1487, 26718, 7712, '너의 결혼식에서 박보영의 학교 배경이 된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1488, 26719, 16374, '홍이영(김세정)이 신영필하모닉 오케스트라 플루티스트 왕미향(황효은)과 마주치고 8회에서 빵을 사러 온다. 미향이가 빵집을 나서자 소매치기에게 털리고, 이영은 망설임 없이 미향이를 쫓아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1489, 26720, 16374, '1회와 4회, 7횡에서 \'신영필하모닉\'이 연습 중인 \'SY아트센터\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1490, 26698, 16374, '4회에서 홍이영(김세정)이 전 남자친구 문재형(김상균)의 춤을 보기 위해 이곳 카페(카페베네)에 앉아서 기다리는 장면이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1491, 25607, 16374, '홍이영(김세정)의 삼촌은 그녀가 어렸을 때 이 공원에서 열린 해양 구조 퍼레이드에 그녀를 데려갔는데, 이것은 그녀를 드럼에 매료시켰고, 그녀는 나중에 그것을 그녀의 직업으로 만들었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1492, 25019, 16374, '12회(본편6회)에서 장윤(연우진)과 홍이영(김세정)은 그들의 \'보통 데이트\'에 이 단지 상가에 있는 이 가게를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1493, 26721, 16374, '13화에서 홍이영(김세정)과 하은주(박지연)는 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1494, 24935, 16374, '10화 말미에 남주완(송재림)은 강명석(송영규) 교수와 이 술집에서 만났고 수상한 남자가 이후 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1495, 26722, 16374, '12회(6회)에서 장윤(연우진)은 변호사 친구 최기상(김창회)을 이 술집에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1496, 26000, 16374, '3회에서 하은주(티아라 박지연)는 남주완(송재림)과 함께 무지개다리를 감상하기 위해 남주완(송재림)을 이 다리로 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1497, 26285, 16374, '신영필하모닉 원장 강명석(송영규) 교수와 윤미래(윤주희)의 만남. 이 식당에서 장윤의 아버지이자 회장인 장석현(정성모)과 함께한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1498, 25909, 16374, '13회(7화)에서 서수향(조미령)이 윤미레(윤주희)를 이 식당으로 초대했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1499, 26499, 16374, '남자친구 문준형(김상균)과 바람을 피운 뒤 결국 헤어진 후 홍이영(김세정) 2회(전체 에피소드 1)에서 눈물을 흘리며 다리를 건넜다. 6화에서는 장윤(연우진)이 남동생의 죽음과 홍이영의 죽음에 대해 논의하기 위해 다리 위에서 변호사 친구 최기상(김창회)을 만나던 순간을 회상한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1500, 25668, 16374, '10화(전체 5화)에서 하은주(티아라 박지연)가 이 다리 밑에서 사람을 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1501, 25608, 16374, '4화(전체 2화)에서 장윤(연우진)이 겁에 질린 홍이영(구구단의 김세정)에게 다가가는 이 등산로를 배경으로 타워가 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1502, 26723, 16374, '홍이영(구구단 김세정)이 자주 찾는 편의점이다. 1화(전편 1화)처럼 비를 맞고 우산을 사러 갔지만 너무 비싸서 결국 사지 않았다. 그곳에서 그녀는 장윤(연우진)을 처음 만났는데, 그곳에서 장윤이 그녀에게 우산을 건네고, 그가 그녀를 가벼운 사고에서 구해준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1503, 24948, 16374, '4화에서 장윤(연우진)은 홍이영(김세정)을 이 공원으로 데려와서 그에게 키스해도 되냐고 묻는다. 윤씨는 키스를 하기 위해 몸을 기울이기 전까지는 그녀의 감정과 어색함에 보답하지 않는 것 같다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1504, 26724, 16374, '홍이영(구구단 김세정)이 자주 지나치는 식당이다. 2화(전편 1화)에서  남자친구 문재인(김상균)에게 밥을 사줬던 회상이 떠올랐을 때, 밥이 모자랐을 때. 술에 취한 그녀는 바깥에서 장면을 연출하고, 결국 지나가던 그녀를 본 장윤(연우진)에게 끌려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1505, 26725, 16374, '하은주(박지연)는 장윤(연우진)에게 이 카페에서 만나자고 한다. 18화(전체 9화)에서 장윤은 동생이 살해당했다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1506, 25633, 16374, '4회에서 장윤(연우진)은 홍이영(구구단 김세정)을 오토바이에 내려준 뒤 그와 함께 계단을 오른다. 이 일이 있기 전에, 그는 그녀를 친구의 스튜디오로 보내 한국에 단 두 개밖에 없는 독특한 팀파니 세트를 볼 것이라고 그녀에게 말했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1507, 25894, 16374, '2화에서 수많은 계단과 비탈길을 오른 장윤(연우진)과 홍이영(김세정)은 드디어 케이블카의 정상역에 도달한다. 이영은 장윤이 이 사실을 처음부터 알고 있었다는 것을 알고 더욱 화를 낸다. 그녀가 떠나자마자, 그는 그녀에게 내일 같이 케이블카를 타자고 요청했고, 바로 이 대사가 갑자기 기억의 섬광을 불러일으켰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1508, 26726, 16374, '여기가 신영필하모닉오케스트라의 본거지이며, 홍이영(구구단/아이오아이 출신 김세종)이 팀파니 오디션을 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1509, 26727, 16374, '7화(전체 4화)에서 윤미래(윤주희)가 호텔방에서 전화를 받으며 준비를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1510, 26728, 13868, '장혜성(이보영)과 동료 변호사 차관우(윤상현)의 사무실은 길 건너편에 있는 법원 바로 앞이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1511, 26729, 13868, '너의 목소리가 들려에서 혜성치킨으로 나온 치킨집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1512, 26639, 13868, '너의 목소리가 들려에서 나온 수족관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1513, 26730, 13868, '장혜성(이보영)은 박수하(이종석)와 함께 이 집에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1514, 25796, 13868, '극 중 법정 모습 대부분의 배경이 되는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1515, 26145, 13868, '박수하(이종석)와 고성빈(김가은)은 이 학교에 다니고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1516, 26731, 13868, '2회에서 고성빈(김가은)은 학교 사건 이후 살인미수 혐의로 기소된 후 국선변호사에게 자신이 결백하다고 믿게 만들지 못한다. 속상한 가운데 동수역 승강장에서 기다리던 그녀는 동창인 박수하(이종석)를 보고 자살을 생각하는 그녀의 생각을 듣게 된다. 수하는 다른 승강장으로 달려가 기차가 도착하자마자 그녀를 붙잡고 돌이킬 수 없는 일을 하기 전에 그녀를 붙잡는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1517, 26732, 18290, '4회에서 LUNA의 헤드라인을 장식할 \'그린 콘서트\'는 야외극장에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1518, 25815, 18290, '1회에서 인윤주(정인선)이 뉴스를 읽는다. 루나(LUNA)의 보컬 리더 윤태인(이준영/유키스 준)이 지난 밤 몽유병으로 팬들을 피해 달아났다는 소식이 전해졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1519, 26733, 18290, '9-10회에서 그녀의 29번째 생일을 축하하기 위해 루나의 보컬 리더 윤태인(이준영/유키스 준)이 이 카페에 인윤주(정인선)를 데려온다.  그리고 자신의 감정을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1520, 26734, 18290, '3회에서 김유찬(윤지성, 전 워너원) 엄마가 이 가게에서 그를 부른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1521, 26735, 18290, '8회에서 서우연(장동주)은 인윤주(정인선)를 이 식당으로 초대해 저녁식사를 함께 하며, 얼마 전 서점에서 만난 적이 있지만 그녀가 스스로 기억하기를 바라는 마음에 미리 말을 하지 않았다고 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1522, 26260, 18290, '인윤주(정인선)는 보컬 리더 윤태인(이준영/유키스 준)을 데려와 밴드메이트 우가온(AB6IX 김동현)과 함께 훈련을 시킨다. 4회에서 태인의 치료의 일환으로 이 유도 체육관에 단골로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1523, 26736, 18290, '1화에서 밤에 이 공원에서 루나 보컬 리더 윤태인(이준영/유키스 준)은 노래를 부르기 시작하고 인윤주(정인선)는 사채업자에게 붙잡혀 이 공원에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1524, 26737, 18290, '3회에서는 밤에 이곳에서 만나고, 4회에서는 이신이 지연의 합류를 밤새도록 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1525, 26659, 18290, '인윤주(정인선)은 1회에서 가장 친한 친구 정바른(서혜원)과 함께 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1526, 26139, 18290, '인윤주(정인선)는 1화에서 절친 정바른(서혜원 분)과 함께 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1527, 26215, 18290, '1화에서 LUNA의 소속사 \'MM Entertainment\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1528, 26272, 18290, '11회에서 인윤주(정인선)는 루나의 집을 나간 후 초롱(이용이)과 함께 이사한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1529, 26738, 18290, '인윤주(정인선)는 5화에서 LUNA\'s로 가는 길에 이 건물을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1530, 26739, 18290, '2회에서 인윤주(정인선)가 보컬 리더 윤태인(이준영/유키스 준)의 입주 의사로 입주하는 장면에서 처음 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1531, 26740, 18290, '이곳의 외관은 K-Pop 그룹 LUNA가 살고 있는 맨션으로 나타난다. 2회에서 인윤주(정인선)가 보컬 리더 윤태인(이준영/유키스 준)의 입주 의사로 입주하는 장면에서 처음 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1532, 26741, 18290, '10화에서 서우연(장동주)이 인윤주(정인선)를 사채업자로부터 숨길 수 있도록 도와준 후 이 곳에서 우연은 윤주의 비밀을 알게된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1533, 26592, 18290, '서우연(장동주)의 서점 \'소심당\'으로 출연한다. 2화에서 책을 읽고 있는 동안 인윤주(정인선)는 비를 피하기 위해 잠시 이 가게로 들어선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1534, 26742, 13476, '극 중 이규원(박신혜)이 사는 곳으로 10화에서 이신(씨엔블루 정용화 분)이 집 밖에서 기다리고 있는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1535, 26743, 13476, '이것은 등대가 이규원(박신혜)과 이신(정용화)이 1화에서 거의 만날 뻔했다. 둘 다 등대를 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1536, 26551, 12884, '1회에서는 최승희(김래원)가 이 마을로 이어지는 제장길(제장길) 다리 밑에서 뮤직비디오 촬영을 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1537, 26744, 12884, '최승희(김래원)는 16회에서 이 다리 발치에서 김복실(정려원)에게 프러포즈를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1538, 26285, 12884, '김복실(정려원)은 한정훈과 함께 이 식당에서 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1539, 26185, 12884, '7회에서 최승희(김래원)의 절친 상욱이 이 서점에서 사인회를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1540, 26745, 12884, '최승희(김래원)는 7회에서 사인회를 마치고 절친 상욱과 함께 공원에 온다. 그리고 두 사람은 김복실(정려원 분)에게 흘러가는 승희의 생각으로 뭉클한 대화를 나눈다.');
