INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1793, 26991, 13091, '신재하(진이한 분)가 운영하는 누구갤러리로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1794, 25335, 13091, '1화에서 손영인(고아라)과 아버지 손일건(강남길)이 이 다리를 건넌다. 오토바이를 타고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1795, 26992, 13091, '신재하(진이한 분)가 차호중(김보강 분)과 만나 계략을 짜는 곳이다. 9화, 11화, 15화에서 차승효(윤계상)가 아버지의 그림과 교환하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1796, 26245, 13091, '손영인(고아라)의 집에 직접 초대해 요리를 해 준 신재하(진이한)와 영인은 3화에서 집근처 이 공원에서 저녁 산책을 한다. 그들은 여기에서 더 가까워지고 그는 그녀를 위해 노래하고 춤을 추기도 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1797, 26993, 13091, '김영애(박지영)의 십자수 가게로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1798, 26994, 12939, '김보라(성유리)와 한득구(현빈)가 다니는 대학으로 등장한다. 첫째, 득구는 수학 교수(김벌래)가 관심을 가질 때까지 보라의 운전사로 캠퍼스를 배회하고, 득구에게 개인 교습을 주면서 그를 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1799, 26527, 12939, '1회에서 한태웅(현빈)과 어린 김보라(고주윤)가 이 놀이동산에 모인다. 그들은 함께 관람차를 탄다. 집에 가기 위해 택시를 탈 때, 그녀는 왜 아직 내 이름을 묻지않느냐고 물어본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1800, 25848, 12939, '1화에서 \'곽가 고등학교\'의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1801, 26995, 12939, '13회에서 김보라(성유리)와 한득구(현빈)가 이 역의 11번 출구로 걸어갈 때 그는 그녀의 집에 걸어갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1802, 26263, 12939, '1화에서 한득구(현빈) 어머니의 노점상이 있는 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1803, 24904, 12939, '서건호(임주환)와 김보라(성유리)가 이 카페에서 두 번 만났다. 7화에서 보라는 이곳에서 뜻밖에 엄마를 만나고, 9화에서 건호는 이 카페에서 엄마와 이별한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1804, 26996, 12939, '태웅(현빈)은 대전으로 출근하기 위해 서울을 떠나기로 결정한 어머니를 이 버스 터미널에 배웅한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1805, 26541, 12939, '3화에서 한득구(현빈)이 이승리(성유리)와의 술자리 후 만취를 위해 술에 취한 김보라(성유리)를 공원으로 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1806, 25723, 12939, '김보라(성유리)는 아버지 김장수(천호진)와 함께 이 저택에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1807, 26249, 12939, '3화에서 서건우(임주환)는 김보라(성유리)를 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1808, 25882, 12939, '6회에서 김보라(성유리)와 서건우(임주환)가 이 공원을 찾아와 선물을 주고받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1809, 25335, 12939, '5화에서 김보리 (성유리)와 한득구 (현빈)가 그녀의 집으로 돌아갈 때 버스를 타고 이 다리를 건넌다. 그는 그녀를 보며 "예쁘다"고 말하고 그녀는 그가 그녀를 진심으로 생각하지만 그는 그것이 물론 그가 언급하고 있는 야경이라고 반박한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1810, 26997, 12939, '4회에서 김보로(성유리)가 참석한 패션쇼가 열린다. 이곳은 한득구(현빈)가 그녀를 추적하고 사생팬으로부터 그녀를 구하는 곳이기도 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1811, 25910, 12939, '2화에서 한득구(현빈)는 절친한 친구 최충식(진태현)과 그들의 트레이너와 함께 이 식당에 옵니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1812, 26998, 12939, '1화에서 한득구(현빈)가 이 시장의 헌책방에서 눈의 여왕을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1813, 26745, 12939, '김보라(성유리)와 한득구(현빈)가 13화에서 그녀의 집을 산책할 때 이 공원을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1814, 26999, 12939, '2-3회에서 한득구(현빈)는 절친의 죽음으로 학교를 그만둔 후 이 체육관에서 일하고 훈련한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1815, 27000, 12939, '1회에서 한태웅(현빈)과 김정규(이선호)가 1위를 다투는 제39회 국제수학올림피아드가 중앙도서관에서 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1816, 25948, 16022, '9화에서는 준하(남주혁)와 친해진 어린이집 선배가 이 곳에 안장됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1817, 27001, 16022, '김혜자(김혜자/한지민)의 집으로 등장한다. 그녀의 어머니(이정은)는 미용실 \'행복미용실\'을 운영하고 있다. 이곳에는 아버지(안내상)와 형 김영수(손호준)도 살고 있다. 1회에서는 혜자의 절친한 친구 이현주(김가은)와 윤상은(송상은)이 옥상에서 혜자의 데이트를 즐기는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1818, 26061, 16022, '여기서 김혜자(김혜자/한지민)의 과거 장면이 촬영됐으며, 이현주(김가은)와 윤상은(송상은)도 출연했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1819, 24934, 15120, '18회에서 성해성(여진구)은 정원(이연희)을 데리고 공원으로 나갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1820, 25830, 15120, '18회에서 차태훈(김진우)은 카페를 그만둔 이서원(한소희)과 이 카페에서 소개팅을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1821, 27033, 15120, '정정원(이연희)이 보조 셰프로 일하는 차씨(안재현)가 운영하는 이탈리안 레스토랑 \'SNT\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1822, 25615, 15120, '성수지(김혜준 분)는 36회에 \'태산의료원\'으로 출연하는 이 병원에 입원하고 성영인(김가은 분)은 자신의 병을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1823, 26263, 15120, '25화에서 세희는 지호에게 귀걸이를 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1824, 25668, 15120, '22회에서 성해성(여진구)과 차씨(안재현)는 SMT 레스토랑을 퇴사한 후 새로운 사업을 시작하는데 그들은 먼저 여기 다리 아래에서 차 파스타 트럭을 엽니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1825, 27034, 15120, '정정원(이연희)과 홍진주(박진주)가 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1826, 27057, 14066, '드라마 주인공인 오수현(강소라)의 집으로 등장하는 곳으로 드라마에서 자주 등장 하는 곳 중 하나이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1827, 25637, 14066, '드라마 안에서 박훈(이종석 분)은 의사로 일하고 있는 \'명우병원\'으로 나온다 오수현(강소라 분)의 아버지가 소유하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1828, 26165, 13678, '3회에서 춘홍(이소연)은 김병희(김응수) 목사와 그의 친구들을 이 강바닥에서 즐겁게 한다. 진혁(송승헌)이 조금 뒤 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1829, 26080, 13678, '이 장소는 궁전의 일부로 등장합니다. 예를 들어 17회의 대관식에서 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1830, 25797, 16080, '나이재(남궁민)는 오정희(김정난)의 전화를 받고 서서울교도소로 향하던 중 이 다리를 건넌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1831, 25017, 16080, '1회에서 당황한 이재인(이다인)이 이 공항에 도착하고 어머니 모이라(진희경)에게 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1832, 26524, 16080, '원래 의료사고로 3개월간 수감되었던 나이재(남궁민)가 닥터 프리즈너로 \'서서울형무소\'로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1833, 26642, 14719, '2회에서 여기서 유혜정(박신혜)과 홍(김래원)이 갑작스러운 폭우를 피해 자전거를 타고 피신한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1834, 27058, 14719, '2회에서 유혜정(박신혜)과 홍지홍(김래원)가 자전거를 타고 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1835, 25612, 14719, '홍지홍(김래원) 20회에서 유혜정(박신혜)에게 프러포즈했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1836, 25824, 14719, '이곳에는 홍지홍(김래원)과 정윤도(윤균상)가 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1837, 24933, 14719, '3화에서 홍지홍(김래원)은 갤러리 카페에서 학교장을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1838, 27059, 14719, '순이하와이 카페의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1839, 26097, 14719, '유혜정(박신혜)이 살았던 곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1840, 26999, 14719, '유혜정(박신혜)은 4회에서 그녀를 찾아온 홍지홍(김래원)과 함께 이곳에서 훈련을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1841, 27060, 14719, '6회에서 홍지홍(김래원)과 유혜정(박신혜)이 빗속에서 춤추고 키스하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1842, 25938, 14719, '이 학교는 유혜정(박신혜)이 다니고 홍지홍(김래원)이 가르치는 남양여자고등학교로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1843, 25781, 14719, '6화에서 진서우(이성경)의 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1844, 27061, 16080, '9회에 등장하는 고양시에 위치한 화전동 골목갤러리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1845, 27062, 16080, '이다인과 박은석이 촬영한 파주의 베이커리 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1846, 27063, 16080, '김정난과 장현성이 놀이공원 데이트를 즐긴 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1847, 27064, 16080, '7회에서 등장하는 건물 배경 촬영 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1848, 26166, 16197, '이연서(신혜선)의 저택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1849, 26732, 16197, '8화(전체 4화)에서 이연서(신혜선)와 김단(김명수)은 이날 행사장 야외극장에서 기자간담회를 마치고 만난다. 그녀는 그에게 뺨에 키스를 하며 감사를 표하고 싶지만 참을성이 없는 김단은 고개를 돌리고 그의 입에 키스를 한다. 결국 진짜 키스를 하고, 멀리서 바라보는 지강우(이동건). 바로 직후 9화(5화)에서 김단은 키스를 피하고 도망가는데, 연서는 막 시작하는 비에 몸을 움츠린다. 그녀를 우산 아래 이 상황에서 이끌어내는 것은 강우다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1850, 25948, 16197, '이연서(신혜선)와 김단(김명수/인피니트 엘)이 동행하여 5회에서 이곳에 안장된 부모님을 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1851, 26669, 16197, '5, 8회에서 지강우(이동건)와 금니나(김보미)가 그들의 댄스 회사에 대해 이곳에서 정기적으로 만나 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1852, 27065, 16197, '1화에서 이 다리 위에서 무너져 그녀가 이곳에 와서 난간 위에서 춤을 추던 때를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1853, 26242, 16197, '11, 12회에서 판타지아의 협찬 이벤트가 열리는 이곳에서 김단(김명수/인피니트 엘)과 지강우(이동건)는 그녀의 적들로부터.이연서(신혜선)를 지키기 위해 최선을 다한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1854, 27066, 16197, '김단(인피니트 김명수)의 비밀정원은 인천대공원의 벤치다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1855, 27067, 16197, '이 건물은  \'판타지아 발레 극장\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1856, 26170, 16197, '6화에서는 걷기 연습을 위해 김단(김명수/L 인피니트)과 함께 왔다가 7화에서 혼자 힘으로 다리를 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1857, 27068, 16197, '천사 김단(김명수/인피니트 엘)은 이 교회의 상사로부터 명령을 받는다. 그 교회는 2화에서 김단이 선배가 준 기한에 맞추지 못하는 모습이 처음으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1858, 27069, 18040, '이 곳은 김달리(박규영)의 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1859, 25816, 18040, '1화에서 진무학(김민재)이 도착한 \'암스테르담 공항\' 입국장으로 보인다. 14회에서는 여기가 달리의 삼촌 김홍춘(이도경)도 해외에서 도착하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1860, 27070, 18040, '4회에서 진무학(김민재)은 집까지 전 재산을 팔고 혼자 술을 마시러 온 김달리(박규영)와 함께 이 주점을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1861, 27071, 18040, '10회에서는 큐레이터 송사봉(우희진)이 기절한 나공주(송지원)의 집을 찾기 위해 김달리(박규영)와 함께 이 거리를 지나게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1862, 25917, 18040, '이 박물관의 내부는 김달리(박규영)가 과학 조수로 일하는 \'생 뮐러 박물관\'의 내부와 같다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1863, 25827, 18040, '드라마 중에서 이곳은 암스테르담의 일부로 나온다. 1회에서 무학을 유명 미술평론가 진 히토나리로 굳게 믿고 있는김달리(박규영)는 진무학(김민재)을 태운 뒤 이곳(암스테르담의 일부)을 지나간다. 미술품 수집가이자 \'세인트 뮐러 박물관\'의 후원자인 브롱크호르스트 부인(에이미 알레하). 2회에서 무학은 달리가 자신을 속였다는 확신을 점점 더 갖게 되면서 그녀를 찾아 다시 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1864, 24933, 18040, '4회에서 처음 본 대로 안찰희(이다빈/연우)가 일하는 \'갤러리 몬드\'로 등장한다. 10회에서 그녀는 이 갤러리 앞에서 \'청송미술관\'과 관련된 비리에 아버지가 연루되어 있다는 소식을 읽는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1865, 27072, 18040, '이 건물은 돈혼 F&B 사옥으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1866, 26038, 18040, '이곳에서 진무학(김민재)은 김달리(박규영)와 함께 밤에 체크인을 시도한다. 그는 자신의 모든 신용 카드(그의 \'검은 카드\' 포함)가 아버지에 의해 차단되었다는 사실을 알고 속상해합니다. 2화에서 무학은 신용카드가 복구된 후 이 호텔에 체크인을 하고 달리에게 손목시계를 선물하며 이별을 고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1867, 26142, 18040, '6화에서 진기철(이제연)은 이 식당에서 정치인 안상태와 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1868, 26540, 18040, '3화에서 제일신문으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1869, 25662, 18040, '6회에서 김달리(박규영)의 폭행 영상이 화제가 된 후 이 카페에 사람이 모인다. 12회에서 진무학(김민재)은 이곳에서 안학희(연우)를 만나 집에서 나와 홀로 살게 해달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1870, 27073, 18040, '12회에서 성공적인 전시가 끝난 후 진무학(김민재)은 김달리(박규영)와 청송미술관 팀원들을 데리고 이 식당에서 족발식사를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1871, 26138, 18040, '이 건물은 장태진(권율)이 달리에서 운영하는 SEGI 본사로, 김달리(박규영)가 전 약혼자를 방문하는 것을 15화에서 볼수있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1872, 27074, 18040, '3화에서 김달리(박규영)가 새 신발을 사러 이 가게에 오고 진무학(김민재)이 따라 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1873, 26499, 18040, '4화에서 노숙자 김달리(박규영)가 집을 팔고 이 다리를 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1874, 25335, 18040, '1회에서 진무학(김민재)이 타는 택시가 공항으로 가는 길에 이 다리를 건넌다. 2화에서 그는 다시 이 다리를 건너 운전하게 되고, 그의 차는 막 한국으로 돌아온 김달리(박규영)의 택시와 평행하게 운전하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1875, 27075, 18040, '8회에서는 김달리(박규영)와 소꿉친구 주원탁(황희)이 놀이터에 합류했다. 그리고 그들은 풍경을 즐기며 마음을 담았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1876, 24910, 18040, '1회에서 진무학(김민재)은 자신의 이복동생인 진기철(이제연)이 청송미술관 투자를 설득(실제로는 더 압박)했던 일을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1877, 26545, 18040, '이 건물의 주인은 진무학(김민재)이며, 2화에서  김달리(박규영)의 소꿉친구 주원탁(황희)은 옥상 아파트를 임대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1878, 27076, 18040, '8화에서 진무학(김민재)의 아버지와 새어머니, 이복형제가 달리와 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1879, 27077, 18040, '2회에서 김달리(박규영)는 주원탁(황희)의 집을 찾아 이 거리를 걷는다. 동시에 진무학(김민재)은 그의 비서 여미리(황보라)에게 차를 몰고 집으로 달려가 달리리를 지나쳐 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1880, 27078, 18040, '8회에서 장태진(권율/권세인)은 이 거리를 운전하며 옛 아파트 길을 따라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1881, 25781, 18040, '8회에서 김달리(박규영)는 전 약혼자 장태진(권율/권세인)의 손에 이끌려 이 \'아파트\'로 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1882, 25864, 18040, '4회에서 김달리(박규영)는 전 약혼자 장태진(권율)에게 이 식당에서 만나자고 한다. 그녀의 계획은 그에게 그녀의 갤러리 자금을 요청하는 것이다. 하지만 일단 그와 대면하게 되면, 그녀는 스스로 질문을 할 수 없게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1883, 24915, 18040, '4화에서 진무학(김민재)이 김달리(박규영)를 이 호텔에 내려준다. 그녀는 그럴 여유가 없어 무학이 헤어지자마자 떠난다. 5화에서, 그는 그녀를 다시 여기로 데려오고, 이번에는 그녀를 위해 돈을 지불한다. 그리고 다시, 그녀는 머물지 않고 리셉션에서 환불을 요청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1884, 27079, 18040, '이 마을은 네덜란드의 미술관에서 일하며 일하는 김달리(박규영)가 살고 있는 암스테르담의 동네로 등장한다. 2화에서 진무학(김민재)이 그녀를 찾으러 오는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1885, 27068, 18040, '김달리(박규영)는 11회에서 자신이 이 교회(고아원으로 등장)에 머물렀던 순간을 회상한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1886, 25336, 18040, '김달리(박규영)의 아버지, 故 김낙천(장광)이 달리에 세운 \'청송미술관\'의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1887, 27080, 14777, '보보경심려 촬영지 중 단연컨대 눈밭 레전드');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1888, 27081, 14777, '고려 시대로 온 아이유가 만난 무릉도원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1889, 27082, 14777, '이 공원은 궁궐의 일부로 등장한다. 5회에서 왕소(이준기)와 해수(이지은/아이유)가 대화를 나누는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1890, 27083, 14777, '4황자 왕소가 사랑한 해수의 처소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1891, 26167, 14777, '13대 왕자 백아(남주혁)가 8대 왕자 왕욱(강하늘 분)과 아내 해명희(박시은 분)의 간섭을 중단하라고 경고한 후, 해수와 욱은은 이 정원의 호수로 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1892, 26538, 14777, '해수(아이유)는 물에 빠진 소년을 구하려다 호수에 빠져 고려에 가게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1893, 27084, 14777, '왕소(이준기)가 휴식을 위해 찾는 은밀한 장소다. 이 비밀 은신처를 해수(아이유)에게 소개한 후, 그곳은 둘만의 시간을 보내는 만남의 장소가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1894, 26069, 14777, '왕소(이준기)와 해수(아이유)는 12회에서 데이트를 위해 찾아오고, 14외에서는 해수 혼자 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1895, 25790, 14777, '12회에서는 왕자 왕소(이준기)가 1년 만에 궁으로 돌아온 후 이 절벽을 지나게 된다. 8황자 왕요(홍종현)가 반역죄로 궁지에 몰리고 13화 마지막 싸움에서 왕소에게 살해당한 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1896, 27085, 14777, '4회에서 해수(이지은/아이유)와 왕정(지수)이 결투를 벌이던 남자의 부하들에게 납치되자 유왕후(박지영)는 남자의 팔을 베었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1897, 27086, 14777, '3화에서 왕소가 불태웠던 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1898, 26486, 14777, '제7화에서 네 번째 왕자 왕소(이준기)가 수행한 기우제를 올리는 장면을 촬영한 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1899, 27087, 14777, '왕소가 스님들을 몰살시키기전 고민한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1900, 25673, 14777, '4회에서 해수(이지은)를 구한 뒤 이 정원의 다리 위에서 그녀를 잃을까봐 얼마나 무서웠는지 말해주고, 키스를 위해 몸을 기울였을 때 그들은 방해를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1901, 27088, 14777, '고려 황궁 외관도 이곳에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1902, 25936, 14777, '고려 황궁의 외부 사진도 이곳에서 촬영되었다. 예를 들어, 사비궁은 13화에서 보듯이 \'천덕전\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1903, 27089, 14777, '우희(소녀시대 서현)가 몸 던졌던 그 산성');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1904, 25800, 12958, '오달자(채림)는 \'한다홈쇼핑\'의 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1905, 27090, 12958, '3회에서 이 건물은 오달자(채림)가 맡고 있는 엄기준(이현우)의 란제리 브랜드 \'더로:\' 런칭쇼가 열리는 장소로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1906, 27091, 12958, '3회에서 오달자(차림)는 여자로 밤늦게 혼자 지하철을 탄 술 취한 남자에게 이 역에서 모욕을 당한다. 강태봉(이민기)은 그녀를 구하기 위해 끼어들지만 다음 열차에서 사라지고 만다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1907, 26240, 12958, '달자는 아파트 단지 614호[614 상암월드컵파크 6단지]에 거주하고 있으며, 후반부에 강태봉(이민기)이 그녀와 함께 이곳으로 이사온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1908, 25734, 12958, '20화에서 오달자(차림)가 집으로 가는 길에 버스가 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1909, 26455, 12958, '오달자(채림)는 강태봉(이민기)과 함께 호텔방에서 자신을 발견한다. 1회에서는 다사다난한 크리스마스 이브가 끝나고 동료 신세도(공형-진). 3화에서는 엄기준(이현우)의 란제리 브랜드 더로그 달자가 맡은 런칭쇼가 이 호텔에서, 17화에서는 장 대표의 생일파티가 펼쳐진다. 이 호텔의 에메랄드룸에 강태봉(이민기)을 초대해 오달자(채림)와 동행하는 로펌 이앤창의 사건이 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1910, 26285, 12958, '위선주(이혜영)가 그에게 냉담한 태도를 보인 후, 신세도(공형진)는 오달자(채림)에게 합류를 간청한다. 8회에서 아버지와의 만남을 위해 이 식당에서 그를 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1911, 25335, 12958, '4화에서 오달자(채림)와 엄기정(이현우)은 운전기사에 의해 이 다리를 건너게 된다. 마지막 22회에서 서울로 돌아왔을 때, 오달자(채림)는 GPS의 안내를 받으며 스스로 이 다리를 건너간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1912, 25910, 12958, '16회에서 신세도(공형진)와 임신한 위선주(이혜영)은 이식당에 밥을 먹으러 와서 세도가 그녀가 이미 얼마나 먹었는지에 대해 언급하기 시작할 때, 그녀는 더 주문하기로 결정합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1913, 25633, 12958, '강태봉(이민기 분)이 다시 변호사로 나선 것을 기뻐하는 오달자(차림 분)는 20회에서 아침 조깅을 하며 이 계단을 뛰어올라 다른 아침 운동선수들의 황당함에 계단 꼭대기에서 \'로펌\'을 외친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1914, 27092, 12958, '2회에서 강태봉(이민기)이 할아버지가 보낸 깡패들로부터 벗어나기 위해 이 거리를 달리고, 뒤에서 눈물을 흘리는 오달자(채림)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1915, 27093, 12958, '오달자(차림)는 3회에서 여자로 밤늦게 혼자 지하철을 탄 술 취한 남자에게 모욕을 받았던 20대를 회상한다. 강태봉(이민기)은 그녀를 구하기 위해 발을 들이고 다음 기차에서 그가 나타나자마자 사라지고 만다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1916, 27094, 12958, '강태봉(이민기)의 절친 춘하(김재욱)가 쇼핑몰에서 일한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1917, 24939, 13131, '남유준(김영재)이 자전거를 타고 제인(진재영)을 태우고 이 길을 자전거로 내려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1918, 25661, 13131, '1회에서 남유희(문정희)와 오은수(최강희)는 친구들과 야유회를 마치고 미쓰족발 홍대본점 앞에서 해제인/재인(진재영)과 작별인사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1919, 25720, 12854, '4화에서 흑룡회의 상사는 이 호텔에서 이순애(남상미)와 한유일(데니스 오)을 기다리고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1920, 25909, 12854, '8화에서 송현철(김일우)은 강준(이주현)과 한유일(데니스 오)을 저녁식사에 초대하지만 유일은 이순애(남상미)의 손을 잡고 입장한다. 강준 일행이 순애를 보고 깜짝 놀란 장면이 나오는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1921, 25862, 3781, '최종회 24회에서 윤혜진(오연수)은 일본인 관광객 일행과 함께 이곳에 온다. 브레이크 댄서들을 보면 이준수(이동욱)가 떠오른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1922, 25633, 3781, '윤혜진(오연수)이 일본인 관광객들과 함께 남산공원 분수대로 데려가기 위해 마지막 회에서 계단을 오른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1923, 26159, 3781, '최종회 24회에서 윤혜진(오연수)이 일본인 관광객 일행과 함께 이곳에 와서 관광객들이 모두 오는 동안 이 장소를 발견하고 분수대에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1924, 26618, 7119, '최우진(이상엽)이 일하는 서점, 그리고 그곳에서 이서를 만난다. 방송에서는 \'야간책방\'으로 알려져 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1925, 27103, 7119, '최우진(이상엽)이 실종된 이서연(김소은)을 찾기 위해 찾아왔다. 그리고 이 가게에서의 데이트를 회상하게 된다. 방송에서 이 가게의 이름은 \'젤라또 하우스\'입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1926, 27104, 7119, '최우진(이상엽)이 여자친구 이서연(김소은)을 찾기 위해 3번 출구 근처 지하철역을 지나고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1927, 26066, 15648, '28회에서 김지운(하석진)은 임다영(보나)을 만나 첫 데이트를 하게 되고 그녀는 그에게 이곳이 아버지가 묻힌 곳이라고 소개한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1928, 27105, 15648, '26회에서 임다영(우주소녀 보나)과 윤상아(고원희), 강혜주(전수진)는 다영의 데이트 장소인 이곳으로 쇼핑을 하러 갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1929, 26093, 15648, '권진국(이지훈)이 윤상아(고원희)를 데이트에 데려가고 싶은 오징어 전문점이 문을 닫아서,  27회에서 윤상아(고원희)를 이 공원 성산대교 발치에 있는 세븐일레븐으로 데려와 오징어라면을 먹게 된다. 진국은 마침내 자신의 감정을 고백하지만, 그것은 여전히 상아가 기다리고 있는 프러포즈가 아니다. 그들은 30화에서 진짜 데이트를 하러 여기로 돌아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1930, 26540, 15648, '11회에서는 \'실버라이트\' 회사에서 임다영(우주소녀 보나)이 자신의 하우스헬퍼에 대해 설명하는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1931, 25726, 15648, '30회에서 권진국(이지훈)과 윤상아(고원희)가 이 거리로 데이트를 하러 나온다. 상아는 이 가게 앞에 놓인 수제 장신구를 보고 판매자와 대화를 시작하고, 진국은 푸드트럭에서 먹을 것을 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1932, 25661, 15648, '마지막회 32회에서 박가람(연준석)의 밴드 \'진짜가람\'이 여기서 연주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1933, 26000, 15648, '1, 26회에서 김지원(하석진)이 자전거를 타고 퇴근하는 다리.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1934, 25347, 15648, '10회에서 김지운(하석진)은 자신의 과거(사랑)를 회상하기 위해 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1935, 27106, 15648, '31-32화에서 윤상아(고원희)가 남자친구 권진국(이지훈)의 도움으로 다시 주얼리 디자이너의 길을 걷게 된 새로운 직장이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1936, 26215, 15648, '이 건물은 임다영(보나)이 근무하는 \'팸광고대행사\'로 등장하는데, 1화에서 이 건물이 출근을 서두르는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1937, 25913, 15648, '윤상아의 옛 친구의 약혼 파티는 6-7회에서 이곳에서 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1938, 25335, 19055, '윤계리(지창욱 분)는 8회에서 자신의 개 소니가 차를 타고 이 다리를 건너는 크리에이터 부부를 따라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1939, 27107, 4296, '24회에서 남홍주(배수지)와 정재찬(이종석)이 훅킹을 하고 해변으로 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1940, 27108, 4296, '남홍주(배수지)가 엄마(황영희)와 함께 사는 집입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1941, 27109, 4296, '8회에서 정재찬(이종석)은 남홍주(배수지)를 따라간다. 1회에서 진(장동건)과 서이수(김하늘)는 드레스가 찢어진 뒤 뒤를 바짝 바짝 걸어가다가 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1942, 26030, 4296, '28회에서 검사 최담동(김원해)은 퇴사한 이유범(이상엽)을 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1943, 25718, 4296, '3화에서 남홍주(배수지)는 이 공원을 걷는다. 풍선을 잃어버린 소녀를 볼 때, 그녀는 다리가 긴 남자인 정재찬(이종석)이 곧 나타나 그녀의 풍선을 되찾는 것을 도울 것이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1944, 26192, 4296, '이 건물은 23회 문 교수 재판에서 정재찬(이종석)과 이유범(이상엽)이 대립하는 법정으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1945, 27110, 4296, '13회에서  남홍주(배수지)는 한우탁(정해인)에게 일어난 일에 대해 자책하고 있다. 울다가 우연히 이런 상황을 꿈꾸며 휴지 한 상자를 들고 찾아온 정재찬(이종석)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1946, 25605, 4296, '21회에서 남홍주(배수지)와 정재찬(이종석)은 그날 일을 안하기로 하고 이곳을 찾는다. 그녀는 자신의 꿈에 따라 재찬이 속박될 상황으로부터 그를 보호하고 싶어한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1947, 27111, 4296, '8회에서 남홍주(배수지)와 정재찬(이종)이 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1948, 25908, 4296, '29회에서 다친 남홍주(배수지)가 이곳에서 치료를 받는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1949, 24932, 4296, '1회에서 남홍주(배수지)가 이유범(이상엽)을 데리고 밸런타인데이에 저녁을 먹으러 나갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1950, 24933, 4296, '남홍주(배수지)와 정재찬(이종석)이 꿈을 이루기 위해 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1951, 27112, 4296, '1회에서 이 식당의 주차장에서  남홍주(배수지), 이유범(이상엽), 정재찬(이종석)의 만난다. 홍주와 유범은 밥을 먹으러 나간다. 이 식당이 아닌 근처에 있는 \'파머스 테이블\'에서 먹는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1952, 24934, 4296, '최종회 32화에서 정재찬(이종석) 동료들의 결혼식이 치러지는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1953, 27113, 4296, '2회에서 남홍주(배수지) 엄마가 사고나기 직전 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1954, 27114, 4296, '정재찬(이종석)의 동생 정승원(신재하)이 다니는 학교. 4화에서는 학교 입구에서 짝사랑하는 이에게 가정폭력을 당했던 가정생활을 들킨다. 그녀의 아버지에 대한 기소 소식을 듣고 축하하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1955, 26086, 4296, '10회에서 남홍주(배수지)는 이 대학 학생회 건물에서 프로포즈가 잘못되는 꿈을 꾸고 이를 막기 위해 이곳으로 간다. 정재찬(이종석)은 분노한 군중들로부터 그녀를 구하기 위해 이곳으로 왔고, 그들은 건물 바로 뒤에 위치한 다리 위를 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1956, 27115, 4296, '3회에서 남홍주(배수지)가 이 역 1번 출구에서 지하철을 타고 있습니다. 바로 직전 어린이대공원 입구를 지나고 있는 그녀를 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1957, 27116, 4296, '3회에서 정재찬(이종석)이 이 역에서 지하철을 탄다. 그는 3번 출구로 역에 들어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1958, 25615, 4296, '정재찬(이종석 분)은 18회에서 밤톨이가 남홍주(배수지 분)라는 사실을 플래시백을 통해 비로소 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1959, 27117, 4296, '1회에서 남홍주(배수지)는 발렌타인 데이를 맞아 이유범(이상엽)을 위해 꽃을 사준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1960, 27118, 4296, '남홍주(배수지) 어머니의 식당으로 \'홍주삼겹살\'이라는 이름으로 등장한다. 7-8화에서 모두에게 중요한 만남의 장소가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1961, 27119, 4296, '25회에서 경찰이 휴대폰 딜러와 그의 피해자를 단속한다. 딜러는 남홍주(배수지)와 정재찬(이종석) 아빠를 살해한 살인범의 동생이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1962, 27120, 4296, '24회에서 교도소 앞에서 현(오의식)이 수감자가 스스로 목숨을 끊고 누명을 썼다는 유서를 남겼다고 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1963, 27121, 4296, '9화에서 이 건물은  남홍주(배수지)의 전 직장으로 \'SBC 방송\'이라는 이름으로 등장한다. 그녀는 이곳에서 다시 일을 시작해야 하는지 서울을 검색할 때 이 건물 앞에 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1964, 27122, 4296, '5회에서 피아니스트 박소윤(김소현)의 자택 로비로 등장한 아파트 단지 로비. 정재찬(이종석)과 남홍주(배수지)는 재찬의 동생이 살인 혐의로 유죄 판결을 받는 것을 막기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1965, 27123, 4296, '3회에서 남홍주(배수지)와 정재찬(이종석)이 지하철 안에서 이 다리를 건너고 있다. 그곳에서 홍주는 재찬이 꿈에서 이 지하철을 타는 것을 봤다고 말한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1966, 25335, 4296, '7화에서 정재찬(이종석 분)은 버스를 타고 직장으로 향하는데, 버스가 이 다리를 건너는 동안 그는 잠이 든다. 남홍주(배수지 분)는 꿈에서 이 장면을 보았고, 버스 정류장을 놓칠 거라는 생각에 이 버스를 타고 적절한 순간에 그를 깨워주기도 했다..');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1967, 25668, 4296, '25화에서 한우탁(정해인)과 그의 동료가 전화상으로 추정되는 사람을 체포한 곳은 바로 이 다리 밑이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1968, 26214, 4296, '3화에서 남홍주(배수지)가 이 길을 걷다가 안국동 윤보선의 집을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1969, 26226, 4296, '3화에서 남홍주(배수지)와 정재찬(이종석)은 둘 다 따로 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1970, 24922, 4296, '3화에서 남홍주(배수지)와 정재찬(이종석)이 이 고등학교 앞을 각자 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1971, 27124, 4296, '3회에서 남홍주(배수지)와 정재찬(이종석)이 각각 이곳을 지나간다. 그들은 멈춰서 벽화를 보고 계속 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1972, 25335, 13388, '서혜림(고현정 분)은 1화에서 남편의 실종 소식을 라디오로 전하기 위해 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1973, 26456, 13388, '장세진(이수경)의 고모 민씨(송옥석)가 운영하는 대한민국의 원퍼센터 클럽으로, 세진이가 해외에서 돌아와 이모에게 이곳의 미술관을 맡으라고 하는 등의 모습을 보인다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1974, 26484, 14627, '5회에서 이인좌(전광렬)가 이 절벽에서 백대길(장근석)을 치명상을 입히고 백대길을 죽게 만들었다. 그리고 거의 죽고 있는 대길에게 이 절벽에서 떨어질 때 살아남으면 소원을 들어주겠다는 내기를 제안하고 대길은 그렇게 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1975, 25945, 14627, '1회에서 인현왕후의 처소인 창경궁 경춘전(昌慶宮 京春殿)의 외전으로, 숙종이 현왕후(훗날의 최숙빈)의 뒤를 이어 퇴위한 황후 한 쌍을 배웅하는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1976, 26072, 14627, '2화에서 이 정자에서 숙종(최민수)과 함께하는 관객을 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1977, 25853, 17700, '2회에서 오인범(씨엔블루 정용화)과 허지철(강홍석)은 해가 뜰 무렵 이 공원에 모닝커피를 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1978, 24933, 17700, '허지철(강홍석)은 9회에서 배수정(최지우)과 이 카페에서 첫 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1979, 26657, 17700, '1-2회에서 이 마을의 집(54호)에 사는 2인조와 홍지아(장나라 분)와 오인범(정용화 분)이 단독으로 이곳에 오게 되면서 우연히 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1980, 26039, 17700, '1회에서 오인범(씨엔블루 정용화)과 허지철(강홍석)은 펜트하우스를 빌려 함께 술을 마시고 다음날 아침 호텔 수영장에서 휴식을 취하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1981, 27125, 17700, '3회에서 홍지아(장나라 분)가 이 식당(\'한옥 치킨 수프\'로 등장)에서 테이크 아웃을 받는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1982, 26487, 13063, '이 나무는 86화 마지막 장면에서 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1983, 27126, 13720, '드라마 <대왕의 꿈> 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1984, 26067, 12694, '대장금\' 속 수원화성문화제를 즐길 수 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1985, 25680, 12694, '장금(이영애)은 51회에서 이 마을에서 천연두 환자들을 돌본다. 54화에서는 민정호(지진희)의 아이를 이곳에서 낳는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1986, 26080, 12694, '우리나라 최대 규모의 오픈 세트장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1987, 26072, 12694, '1화에서 연산군 행진이 거행됩니다. 인정전 앞에 있고, 3화에서 중종(임호)의 행렬이 여기서도 일어난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1988, 27127, 12694, '여러가지 체험이 간능한 \'대장금\' 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1989, 27128, 12694, '대장금 어머니의 돌무덤, 용문굴이 있는 산');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1990, 27088, 12694, '대장금이 앉았던 연못이 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1991, 27129, 12694, '검은색 모래가 인상적인 \'대장금\' 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1992, 27130, 12694, '27회에서 최상궁은 궁궐의 모든 부엌을 총괄하는 감독이 되기 위해 음모를 꾸미고, 먹고 병에 걸린 중종을 위해 한상궁(양미경)과 장금(이영애)이 준비한 오리밥을 독살한다. 반역죄로 기소된 두 여인은 구타를 당하고 제주도로 유배되지만 여행 중 한씨는 병에 걸린다. 장금은 그녀를 업고 갈대밭과 낭떠러지 위의 오솔길을 따라가지만 슬프게도 그녀의 스승은 세상을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1993, 27131, 12694, '대장금\' 소품 전시관이 있는 민속촌');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1994, 27132, 12694, '대장금\' 세트장에서 이색 체험 활동을 할 수 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1995, 24987, 12694, '대장금과 민영호가 산책하며 이야기를 주고 받던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1996, 25640, 15804, '22회(전체 11회)에서 복승아(소녀시대 권유리)가 한정식을 위해 이 카페에서 기다립니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1997, 27133, 15804, '3화(2화)에서 한진미(이열음)에게 취직을 제안한 편의점으로 특유의 라면 레시피로 매출을 견인했다. 그곳에서 민혁(비투비 이민혁)을 만나 자신이 본 방송에서 그녀를 알아보게 된다. 두 사람 모두 진미가 다양한 편의점 제품을 만들기 위해 드라마 내내 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1998, 26990, 15804, '이는 한산해(신동욱)와 복승아(권유리)가 드라마 안에서 일하는 RG모터스 사무실의 외관으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (1999, 27134, 15804, '10회(전체 5화)에서 오래 기다리게 한 한산해(신동욱)와 복승아(소녀시대 권유리)에게 사과의 표시로 그들이 기다리던 의뢰인이 하룻밤 숙박을 제안한다. 이후 산해는 동생 진미(이열음)와 정식(김현준)을 불러 저녁식사를 놓치지 않기 위해 차를 몰고 그곳에서 만나자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2000, 27135, 15804, '한산해(신동욱 분)와 복승아(소녀시대 권유리 분)가 주말에 이 곳에서 외부 클라이언트를 만나기로 하는 모습이 9회(전체 5화)에서 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2001, 27136, 15804, '21-22회(전체 11화)에서 한산해(신동욱)는 사무실 선물 교환을 위해 복승아(소녀시대 권유리)가 받기를 바라는 마음으로 이 식당의 상품권을 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2002, 27137, 15804, '셔누는 민혁에게 한진미(이열음)와 친해지고 싶다며 한진미를 불러달라고 부탁한다. 잠시 후 진미가 나타나고 민혁은 셔누가 전문으로 하는 자신만의 완벽한 샌드위치 콤보를 그녀에게 보여주기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2003, 27138, 15804, '한산해(신동욱)는 복승아(소녀시대 권유리)에 대한 마음을 숨기기 위해 매니저 이나영(정이랑)에게 점심을 먹자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2004, 25659, 15804, '11-12회에서 한산해(신동욱), 복승아(소녀시대 권유리)와 그녀의 의뢰인/사촌(강남 특별 카메오)이 이 공원을 지나며 점심을 먹고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2005, 25792, 15804, '이 건물 내부의 일부는 RG 모터스의 사옥으로 나오고, 한진미(이열음)가 공동 진행하는 \'음악중심\' 스튜디오로도 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2006, 27139, 15804, '3회(2회 본편)에서 한산해(신동욱)는 복승아(소녀시대 권유리)의 집들이 파티에서 팀을 위해 프라이드 치킨을 사러 오겠다고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2007, 27140, 15804, '복승아(권유리)는 4회에서 술에 취한 두 남자에게 들킨다. 몸싸움이 벌어지고 한산해(신동욱)의 재킷이 반으로 찢어진다. 우연히 지나가던 한정식(김현준)이 끼어들려 하지만 구타를 당한다. 한편, 승아는 방금 산 과일을 술주정뱅이들에게 던지면서 유리하게 이용한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2008, 27141, 15804, '위기에 처할 뻔한 사업을 가까스로 구한 한산해(신동욱)는 신입 복승아(소녀시대 권유리)를 이 식당에 데려오기로 결심한다. 산해는 국수 대신 냉동 삼겹살(특선 메뉴라고 함)을 주문한 후 칼국수를 주문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2009, 27142, 15804, '5화(3회)에서 한산해(신동욱)는 신입생 복승아(소녀시대 권유리)를 버킷리스트에 있는 또 다른 식당으로 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2010, 24948, 15804, '최종회 16회에서 한산해(신동욱)가 이곳에서 복승아(권유리)에게 프러포즈를 하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2011, 27143, 15804, '한산해(신동욱)와 복승아(소녀시대 권유리)는 이 식당에서 RG 모터스의 VVIP 고객 중 한 명(양동근 특별 카메오)과 그의 세 식구를 만난다. 최종회 31회(최종 풀회 16회)에서 아이들이 점점 말썽을 부리기 시작하자, 승아는 밖에서 놀면서 아이들의 주의를 분산시키기로 하고, 산해는 점심을 먹으며 클라이언트와 이야기를 나눈다. 배려에 감동한 고객은 승아의 추천에 따라 차를 사기로 결정한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2012, 27144, 15804, '한산해(신동욱)와 RG모터스의 VIP 고객(양동근 특별 카메오)은 지난 31회(최종 16회) 이 골목길에서 방금 먹은 음식에 대해 서정적으로 랩을 펼쳤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2013, 27145, 15804, '한산해(신동욱)는 가까운 거리에 있는 복승아(권유리)를 따라 점심을 먹으러 가던 중, 17회에서 이 찻집을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2014, 27146, 15804, '17-18회(전체 9화)에서 복승아(소녀시대 권유리)는 한산해(신동욱)를 이 식당에 데려오기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2015, 27147, 15804, '16화(전체 8화)에서 복승아(소녀시대 권유리)가 짜장면 먹으라는 이야기를 듣고 한산해(신동욱)는 복승아를 여기로 데려오기로 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2016, 27148, 15804, '한산해(신동욱)는 복승아(소녀시대 권유리)의 고백을 거절한 뒤 다시 복승아(소녀시대 권유리)에게 자신의 상여금으로 \'건강식\'을 주기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2017, 27149, 15804, '복승아(소녀시대 권유리)는 한산해(신동욱)에게 사촌이기도 한 고객(강남 특별 카메오)을 데려오기 위해 식당을 추천해달라고 부탁하자 뜻밖에도 산해는 점심시간이라 따라가기로 한다. 따로 자리에 앉지만 승아는 산해가 자기 방식대로 주문하고 먹도록 하는 행동 하나하나를 주시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2018, 27157, 6741, '조인성이 뛰어가는 장면에 나온 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2019, 27158, 6741, '더킹에서 조인성이 좌천되어 지낸 별장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2020, 27159, 6741, '더킹에서 류준열과 조인성이 대화를 나눈 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2021, 25748, 6741, '영화 초반 조인성이 뛰어다니는 시장 골목');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2022, 25871, 6741, '정우성과 조인성이 스테이크를 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2023, 27160, 6741, '조인성이 더킹에서 다짐을 하며 걸은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2024, 24935, 15236, '1화에서 한소란(하시은)은 파리에 있을 때 이 바에서 주선한 소개팅을 떠올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2025, 26652, 13638, '17회에서 김봉구/존메이어(윤재무)가 운영하는 클럽입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2026, 27082, 13638, '16화에서 이 정원에서 이재하(이승기)가 존 메이어/김봉구(윤제문)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2027, 25678, 13638, '15~16회에서 이 공원에서 김항아(하지원)와 이재하(이승기)의 약혼식이 거행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2028, 26452, 13638, '16회에서 소말리아에 도착한 방영선(윤여정)과 김항아(하지원)가 거처하는 호텔로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2029, 25848, 13638, '이재하(이승기)가 다니는 왕립대학교로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2030, 24948, 13638, '7화에서 이재신(이윤지)과 왕실 경호원 은시경(조정석)이 이곳에 와서 성벽 위에 앉는다. 그들은 별똥별에게 소원을 빌고, 그녀는 첫사랑에 대해 노래하고, 그는 그녀를 보며 사랑에 빠진다');
