INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2032, 26456, 13638, '더킹 투하츠에서 왕실로 사용되었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2033, 25809, 13638, '18회에서 김항아(하지원)가 수감된 중국 감옥으로 등장한다.');
