INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2039, 25640, 15467, '9회에서 한승주(유이)의 동생 한승태(설정환)와 그의 새 사업에 참여하려는 민희(박민지)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2040, 27200, 15467, '에릭조(정상훈)의 회사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2041, 25719, 15467, '이 건물은 에릭 조(정상훈)의 회사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2042, 25659, 15467, '9화에서 한승주(유이)가 공원에서 에릭 조(정상훈)에게 오작두(김강우)가 항상 차가운 남자로 오해받는 것에 대해 불평한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2043, 25910, 15467, '2회에서 한승주(유이)는 오작두(김강우)가 좋은 남편이 될 수 있겟다고 결심했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2044, 25930, 15467, '2회에서 오작두(김강우)가 이 역에서 서울에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2045, 27201, 15467, '데릴남편 오작두에서 한승주(유이)는 이 집에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2046, 26559, 14904, '1회에서 이곳은 지은탁(김고은)이 도깨비에서 어린 소녀였을 때 사는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2047, 26711, 14904, 'SNS에서 유명한 도깨비 방파제 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2048, 27202, 14904, '공유가 중천의 허무를 걷는 장면 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2049, 27203, 14904, '김고은이 집 나와서 일하러 간 스키장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2050, 27204, 14904, '공유가 집 나온 김고은을 따라가던 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2051, 25980, 14904, '9화에서 이 사찰로 가는 길과 전나무길은 지은탁(김고은)의 아르바이트 마치고 집으로 가는 길로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2052, 27205, 14904, '공유가 풍등을 날리던 그 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2053, 27206, 14904, '수능보러 갈 때 데려다준 장면 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2054, 27207, 14904, '캐나다에서 공유와 김고은이 밥 먹은 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2055, 27208, 14904, '12화에서 유덕화(육성재)가 김신(공유)과 저승사자(이동욱)를 라운지 바에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2056, 26086, 14904, '지은탁(김고은)이 7화에서 여기서 통신전공 논술전형을 치른다. 일이 끝났을 때, 그녀는 우연히 대학 야구팀의 일원인 그녀의 짝사랑하는 태희(정해인)와 마주친다. 은탁을 데리러 이곳에 와서 건국대학교 박물관 앞에서 기다리는 김신(공유)은 그들의 재회를 마지못해 목격하게 된다. 12화 후반부에서, 우리는 그녀가 학생일 때 캠퍼스를 걷는 것을 본다. 13화에서 저승사자(이동욱)는 은탁(학생회 건물 옥상 카페일 가능성이 높다)을 만나기 위해 이곳에 와서 신정아가 가슴에 품고 있는 칼을 뽑아준다. 조금 후, 그리고 그들의 여행이 끝난 후, 신씨는 깜짝 방문을 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2057, 25953, 14904, '4회에서 지은탁(김고은)이 이 호텔에 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2058, 25941, 14904, '1화에서 지은탁(김고은)은 이곳에서 촛불을 불어주는 앱으로 김신(공유)을 부를 수 있다는 자신의 이론을 시도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2059, 27119, 14904, '10화에서 김신(공유)과 저승사자(이동욱)는 지은탁(김고은)을 위해 파(대파)를 사준 뒤 이 터널을 활주로를 걸었고, 오토바이 운전자가 그들 사이를 지나갔습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2060, 25929, 14904, '12회에서 혼란스러운 저승사자(이동욱)가 동료 저승사자를 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2061, 27209, 14904, '3회에서 저승사자(이동욱 분)와 써니(유인나 분)의 첫 만남이 이 육교에서 벌어졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2062, 27210, 14904, '이동국, 유인나가 처음 만난 곳이자 헤어지는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2063, 26329, 14904, '지은탁(김고은)이 그릴의 불을 끄면서 실수로 김신(공유)을 부르고, 제인 오스틴 벽화 바로 앞에 그를 소환한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2064, 26542, 14904, '4층에 위치한 생활용품 체인 미니소에서 가정용품을 파는 김신(공유)과 지은탁(김고은)을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2065, 26543, 14904, '13회에서 지은탁(김고은)의 눈에 띄지 않는 박중헌(김병철)은 탑의 옥상에서 지은탁의 몸을 빙의해 김신(공유)의 검을 뽑는다. 하지만 그러기 전에 저승사자(이동욱)가 나타나 그를 은탁의 몸 밖으로 불러내자 김신은 이를 기회로 삼아 몸에서 칼을 뽑고 마침내 중헌을 죽인다. 그 대가로 신은 잿더미로 사라지고 은탁은 이곳에 앉아 서럽게 운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2066, 27211, 14904, '7화에서 지은탁(김고은)과 김신(공유)이 이곳에서 밥을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2067, 26656, 14904, '2화에서 지은탁(김고은)이 귀신들과 대화하고 김신(공유)에게 감시당하는 곳도 바로 이 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2068, 25962, 14904, '2화에서 지은탁(김고은)이 아르바이트 구하러 가는 길에 혜화역 3번 출구를 향해 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2069, 25945, 14904, '2화 퀘벡에서 돌아온 김신(공유)과 지은탁(김고은)이 이곳에 도착해 함께 광화문을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2070, 25639, 14904, '김신(공유)가 사는 곳이다. 이후 저승사자(이동욱)와 지은탁(김고은)도 입주한다. 유덕화(육성재)가 이곳에 사는 것처럼 자주 드나든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2071, 26214, 14904, '1, 2회에서 지은탁(김고은)이 1화에서 이 골목에서 하교하고 2화에서 율곡로3길에 들어서는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2072, 27145, 14904, '11화에서 써니(유인나)와 지은탁(김고은)이 이곳에 온다.  써니는 은탁에게 전생을 믿느냐고 물었고, 그녀는 그렇다고 대답한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2073, 25037, 14904, '공유와 김고은이 처음 우연히 마주친 돌담길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2074, 27212, 14904, '12화에서 지은탁(김고은)이 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2075, 27213, 14904, '12회에서 지은탁(김고은)은 김신(공유)과 전화통화를 하며 이 장소로 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2076, 27214, 14904, '11회에 사신(이동욱)과 써니(유인나)가 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2077, 25340, 14904, '지은탁(김고은)이 다니던 학교이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2078, 27182, 14904, '9회에서 써니(유인나)는 새로운 위치에 자신의 바베큐 치킨 레스토랑을 열고 은탁(김고은)은 아르바이트에 지원한다. 이후 같은 회에서 김신(공유)과 저승사자(이동욱)는 치킨과 맥주를 두고 브로맨스와 부끄러운 말다툼을 이어가고, 사신은 써니와 함께 자신의 슬픈 과거를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2079, 27215, 14904, '공유와 김고은이 캐나다에서 돌아온 커다란 문');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2080, 24913, 14904, '저승사자(이동욱) 사무실은 이 곳 궁전 벽 안에 있습니다. 김신(공유)이 1편과 14편에서 이곳을 지나며 잡담을 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2081, 27216, 14904, '14회에서 김신(공유)과 지은탁(김고은)이 한미 서점에서 만난 후, 김신은 돌아왔지만 은탁은 기억을 잃었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2082, 27217, 14904, '김고은 집 앞 골목 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2083, 25841, 14904, '7회에서 지은탁(김고은)과 김신(공유)는 이 곳에서 영화 부산행을 본다. 신은 좀비를 보고 화들짝 놀라고, 은탁은 매우 당황한다. 블로그에 따르면 이 장면은 영화관 9번 홀에서 촬영되었으며 두 캐릭터는 G행 6, 7번 좌석에 앉았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2084, 27218, 14904, '납치당한 김고은을 구하기 위해 공유가 걷던 거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2085, 27219, 14904, '4회에서는 취한 신이 은탁에게 자신이 처음이자 마지막 신부라는 사실을 알리며 차분한 마음으로 했을 것 같은 자신에 대해 조금 더 이야기한다. 13회에서 은탁은 그를 보고 싶다고 그를 여기로 불렀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2086, 26412, 14904, '1회에서 도깨비 김신(공유)이 앉았던 건물이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2087, 25864, 14904, '13회에서 지은탁(김고은)이 머무는 장소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2088, 27220, 14904, '8화에서 김신(공유)과 지은탁(김고은)이 산책하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2089, 27221, 14904, '이동욱이 공유에게 사과를 한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2090, 27222, 14904, '14회에서 김신(공유)은 지은탁(김고은)은 한미서점에서 만난 후 이곳을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2091, 27223, 14904, '김고은이 불을 꺼서 공유를 부르던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2092, 26486, 14904, '1화에서 김신(공유)이 군대와 함께 이곳에 온다. 적들과 싸워 성공한 후 역적으로 왕에게 죽임을 당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2093, 25602, 14904, '공유와 김고은이 첫키스한 그 메밀밭');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2094, 25948, 17287, '2화에서 조윤실(서이숙)이 이 곳을 방문합니다. 그리고 이곳에서 사립탐정을 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2095, 26698, 17287, '15회 회상장면에서 본 것처럼 선우준(이재욱)이 추민수(최광제)와 함께 자신과 친구의 사진을 찍어 연애하는 것처럼 보이게 해달라고 애원하는 곳이 이 쇼핑몰 D동(104호) 앞이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2096, 25906, 17287, '구라라(고아라)의 아버지 구만수(엄효섭)의 장례식이 1회에서 이곳에서 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2097, 26308, 17287, '12회에서 구라라(고아라)가 선우준(이재욱)을 데리고 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2098, 25345, 17287, '김만복(이순재 분)이 11회에서 구라라(고아라 분)에게 아내와의 사랑 이야기를 설명할 때, 그는 몇 년 전(이재욱 분) 젊은 심선자에게 편지를 통해 서로가 얼마나 그리웠는지를 설명한다.이 공원에 있는 커피숍에서 만나기로 했지만 그녀는 나타나지 않았다. 그들은 몇 년이 지난 후에야 이곳에 위치한 \'제일 뮤직샵\' 앞에서 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2099, 26428, 17287, '1회에서 구라라(고아라)는 방정남(문태유)과 결혼할 예정이다. 이곳은 또한 같은 회에서 라라가 자신의 신부 부케를 전달한 선우준(이재욱 분)을 처음 만나 드레스 수정을 돕는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2100, 27224, 17287, '12회에서 구라라(고아라)는 이 북카페에서 선우준(이재욱)의 엄마 조윤실(서이숙)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2101, 26038, 17287, '16회에서 선우준(이재욱)이 크리스마스 만찬을 위해 연습을 부탁한 구라(고아라)의 피아노 곡을 녹음한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2102, 27090, 17287, '1회에서 구라라(고아라)가 어린 시절 이 공연장에서 처음으로 공연을 펼친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2103, 26263, 17287, '선우준(이재욱)을 찾던 중 구라(고아라)는 이 공원의 동쪽 끝을 미친 듯이 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2104, 26000, 17287, '선우준(이재욱 분)은 15회에서 백혈병에 걸렸다는 사실을 알고 밤에 이 다리를 걸어나와 구라라(고아라 분)와 헤어지기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2105, 27225, 17287, '15회에서 구라라(고아라)는 선우준(이재욱)을 처음 만난 게 사실 이 식당에서였다는 사실을 깨닫는다. 그녀는 그에게 문자를 보내면서 바로 이곳으로 온다. 같은 시각 준은 공항으로 가던 중 운전사에게 차를 세워달라고 부탁하고, 라라와 합류하기 위해 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2106, 25347, 17287, '구라라(고아라 분)가 기획하는 12회 오후 데이트에서 선우준(이재욱 분)을 이 공원으로 데려오다가 편지만 남기고 헤어지는 시늉을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2107, 24944, 17287, '선우준(이재욱)과 구라라(고아라)는 14회 기말고사가 끝난 후 이곳 카페 레스토랑에서 만나기로 약속했고, 라라는 밤 늦게까지 우준을 기다리지만 나타나지 않는다.사실, 그의 고등학교 친구들은 15화의 플래시백에서 보듯이 그를 여기까지 태워다 주지만, 그가 도착했다는 것을 알아차리기도 전에 그는 기절했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2108, 25930, 17287, '11회에서 진하영(신은수)과 이승기(윤종빈)가 서울로 올라와 선우준(이재욱)을 만나기로 한 후 이 역에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2109, 27226, 17287, '6화에서 호기심 많은 진하영(신은수), 이승기(윤종빈), 진숙경(예지원)이 태그를 붙이며 상황을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2110, 27227, 17287, '6화에서 아버지가 어머니에게 우주의 행방을 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2111, 25712, 17287, '6화에서 선우준(이재욱)이 회상하는 장면에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2112, 26404, 17287, '1회에서 선우준(이재욱)이 사채업자들로부터 도망쳐야 할 때까지 이 게스트하우스 306호에 머물게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2113, 24922, 17287, '1회에서 선우준(이재욱)이 이 거리를 조깅하다 구라라(고아라)의 브라이덜 부케를 배달하기 위해 스쿠터를 타고 달려오는 사람과 부딪힌다. 그리고 다친 배달원은 우준에게 꽃다발을 대신 배달해 달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2114, 25340, 17287, '14회 회상 장면에서 선우준(이재욱)이 이 학교를 다녔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2115, 26217, 17287, '선우준(이재욱)의 저택이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2116, 24913, 17287, '선우준(이재욱)은 학생 친구 추민수(최광제)와 함께 이 산책로를 걸으며 데이트하는 듯한 모습을 촬영한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2117, 27228, 17287, '1회에서 선우준(이재욱)은 자신이 머물던 곳을 떠나 이 버스 정류장에서 버스를 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2118, 26273, 17287, '12회에서 구라라(고아라)와 차은석(김주헌)이 이 교회에서 결혼식을 올렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2119, 27229, 17287, '4회에서 구라라(고아라)가 새로 오픈한 피아노 학원 \'라라피아노랜드\' 전단을 이 초등학교 앞에서 배포하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2120, 27230, 17287, '2화부터 샌드위치 가게 \'봄날\'에서 아르바이트를 하는 선우준(이재욱)이 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2121, 27231, 17287, '1화에서는 우준이가 집으로 돌아가는 길에 이 길을 달리는 모습이 그려지는데, 이후 고아라(고아라)는 이곳에서 자신의 피아노 학원 \'라라랜드\'를 연다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2122, 27232, 17287, '1회에서 선우준(이재욱)과 차은석(김주훈)은 목포의 전망을 즐기기 위해 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2123, 27233, 15064, '10회에서 한준희(김지훈)가 이 카페에서 강소주(서주현/소녀시대 서현)와 협상을 시도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2124, 24900, 17432, '도시남녀의 사랑법 14회에서 이은오(김지원)이 이 도서관에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2125, 27234, 17432, '16회에서 박재원(지창욱)이 지은 건물로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2126, 26644, 17432, '8회에서 박재원(지창욱)은 고등학교 친구 성우와 함께 이 술집에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2127, 25737, 17432, '5회에서 오성영(한지은)은 텅 빈 브랜드 가방을 들고 이 거리를 걸어가다가 구찌점 앞에서 잠시 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2128, 26036, 17432, '서린이(소주연)가 일하는 이 식당이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2129, 25720, 17432, '1회에서 이은오(김지원)가 이 건물을 지나갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2130, 27235, 17432, '7회에서 박재원(지창욱)은 집으로 돌아가는 길에 비가 많이 내리기 시작하자 이 건물로 피신한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2131, 27236, 17432, '7회에서 이 카페에서 오선영(한지은)은 사귀던 강건(류경수)에게 이은오(김지원)나 서린이(소주연)를 더 사랑하는지 물었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2132, 25659, 17432, '러브스트럭 인 더 시티의 모든 등장인물들은 1화에서 이 광장을 운전하거나 걷는 모습을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2133, 25874, 17432, '3회에서 이은오(김지원)는 박재원(지창욱)과의 연애를 회상하며 이 \'가게\' 앞에 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2134, 25661, 17432, '1회에서 오성영(한지은)과 강건(류경수)은 추운 겨울날 마포관광정보센터 바로 앞에서 이 거리에서 헤어지게 된 사연을 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2135, 26263, 17432, '16회에서 오선영(한지은)과의 관계에 대한 인터뷰를 위해 강건(류경수)은 이 공원을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2136, 27237, 17432, '9회에서 서린이(소주연)가 전 남자친구를 만나 해명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2137, 25664, 17432, '5회에서 3년 전으로 돌아간 강건(류경수)은 여자친구 오성영(한지은)과의 이별을 다시 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2138, 27238, 17432, '3회에서 박재원(지창욱)이 사진을 찍기 위해 이 공원을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2139, 26399, 17432, '오성영(한지은)이 카페에서 도시남녀의 사랑법 1화에서 자신을 소개하고 그녀의 연애사를 이야기하는 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2140, 25880, 17432, '7화에서 이은오(김지원)가 이 카페를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2141, 27239, 17432, '6화에서 오선영(한지은)과 박재원(지창욱)은 이 공원에서 그들의 사랑에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2142, 25797, 17432, '9화에서 박재원(지창욱)은  우연히 전 남자친구와 마주쳤을 때 어떻게 할 것인가에 대한 인터뷰를 할 때 이 다리를 걷는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2143, 25669, 17432, '3회에서 이 영화관에서 서린이(소주연)와 최경준(김민석)이 영화를 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2144, 27240, 17432, '박재원(지창욱)이 이 한옥에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2145, 24951, 17432, '이은오(김지원)와 서울에서 만나기로 했던 곳. 그는 그녀가 자신에게 어떻게 전화를 걸었는지 이야기합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2146, 24913, 17432, '최경준(김민석)은 이 산책로에서 제대 당시 서린이(소주연)에게 자신의 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2147, 25620, 17432, '1화에서 이은오(김지원)는 분수에 앉아 과거 연애사를 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2148, 25913, 17432, '이은오(김지원)가 4화에서 이전 직장으로 돌아온다. 그녀의 오래된 상사를 만나 그녀가 새로 설립한 회사 O3에 대한 가능한 권한을 요청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2149, 26145, 17432, '1회에서 체육을 가르치는 오순영(한지은)의 학교이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2150, 25910, 14489, '남공대(장유상) )와 기여운(장희령)은 이 식당에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2151, 27161, 15742, '1화에서 강혁(세훈)이 이곳에서 상대 무리와 싸우는 장면을 촬영했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2152, 27241, 15742, '표태진(안보현 분)과 강다현(신수정 분)은 3화에서 첫 \'데이트\' 때 백조 패들보트를 타고 여행을 갔다가 이후 부두에서 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2153, 26242, 15742, '3회에서 표태진(안보현)과 강다현(신수정)은 여의도 선착장 앞에서 밴드의 연주를 듣는 첫 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2154, 27242, 15309, '1회에서 나모현(박세영)은 조류보호구역 가이드로 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2155, 26152, 15309, '16회에서 강필주(장혁)는 장부천(장승조)의 비밀연인 윤서원(한소희)을 이 호텔로 데려와 중요한 결정을 내린다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2156, 27243, 15309, '이 곳은 청아그룹의 본부입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2157, 27120, 15309, '정말란(이미숙)은 두부를 먹고 누군가를 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2158, 27244, 15309, '16회에서 나모현(박세영) 친가 건물로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2159, 25668, 15309, '8화에서 조금 전 아버지가 아버지가 아니라는 소식을 듣고 이곳으로 찾아온 장부천(장승조)은 무심코 자살을 시도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2160, 25347, 15309, '8화에서 강필주(장혁)가 장부천(장승조)에게 아버지가 아버지가 아니라고 말하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2161, 25854, 15309, '1회에서 장부천(장승조)은 이 클럽 요트에서 비밀연인 윤서원(한소희)과 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2162, 25913, 15309, '정말란(이미숙 분)은 1회에서 청아그룹 본사의 일원으로 등장하는 이곳에서 나기추(박지일 분) 의원을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2163, 25896, 15309, '1회에서 어린 장부천(채상우) 납치 사건에 가담한 어린 강필주(조병규)는 후회하며 이 공원에서 그를 탈출시킨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2164, 25864, 15309, '드라마에서 청아의 장국환 회장(이순재) 저택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2165, 25017, 15309, '8회에서 강필주(장혁)는 나모현(박세영)과 장부천(장승조)이 이곳에서 신혼여행을 떠나며 이별을 고했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2166, 24900, 14605, '14화에서 이해준(정지훈/비)이 도서관으로 달려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2167, 27245, 14605, '돌아와요 아저씨 2회에서 이 호텔에서 김영수(김인권)와 한은 기탁(김수로)은 새 몸으로 깨어난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2168, 27246, 14605, '3회에서 김영수(김인권), 신다혜(이민정), 김한나(이레)가 살고 있는 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2169, 25854, 14605, '한홍난(오연서), 최승재(이태환), 송이연(이하늬)은 12회에서 이연의 화보 촬영을 위해 이곳에 온다. 그들이 도착했을 때, 그들은 아무도 보이지 않기 때문에 의심스러워진다. 이것이 나석철(오대환 분)이 실행한 차재국(최원영 분)의 계획이었음을 매니저 홍난이 직접 알고 있다. 후자는 물에 빠지지만 매니저에 의해 구조되는 남겨진 이연을 겁주기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2170, 27247, 14605, '한기탁(김수로)의 식당으로 등장하며, 김영이 갔던 곳으로 첫 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2171, 24948, 14605, '7회에서 이해준(비)과 신다혜(이민정)는 같은 비탈길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2172, 26045, 14605, '13회 이해준(비)이 이 놀이공원에서 김한나(이레)를 찾는다. 이곳은 그녀가 아버지가 보고 싶다며 집을 뛰쳐나온 곳이다. 이에 마야는 한나에게 김영수(김인권)와 함께 보낼 시간을 조금 허락한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2173, 27288, 16435, '동백♥용식 커플이 새벽마다 데이트했던 시장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2174, 27289, 16435, '티저 영상에서 용식이가 달리던 바닷가길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2175, 27290, 16435, '옹산의 하마 동백이가 운영했던 까멜리아가 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2176, 27291, 16435, '용식이 엄마가 운영한 백두게장이 자리했던 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2177, 27292, 16435, '8화에서 황용식(강하)과 오동백(공효진)이 꼭대기 계단에 함께 앉아 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2178, 27293, 16435, '드라마의 중요한 배경인 옹산게장거리로 등장한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2179, 27294, 16435, '오동백(공효진), 그녀의 아들 필구(김강훈), 어머니 조정석(이정은)이 이 집으로 이사한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2180, 27295, 16435, '필구-종렬이 대화를 나누던 다리가 있는 작은 섬');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2181, 27296, 16435, '마지막회 동백♥용식 커플이 뽀뽀하던 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2182, 27297, 16435, '19~20회 동백이와 강종렬이 국수를 먹은 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2183, 25883, 16435, '동백꽃 필 무렵의 3~4화(전체 2화)에서 어머니 오동백(공효진)이 아들을 데리러 올 때까지 아들이 이 시장에서 머무른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2184, 26144, 16435, '이 서점은 2회(전체 1회)에 \'옹산북스\'로 등장한다. 여기에 황용식(강하늘)이 동백(공효진)에게 첫눈에 반해 동백의 \'sorry! 대답은 "that\'s ok"(해리포터에서 그녀가 방금 읽은 표현)라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2185, 27298, 16435, '3~4회 용식이가 동백이를 쫓아가며 꽁냥대던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2186, 27299, 16435, '향미가 오토바이를 타고 마지막으로 배달 가던 길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2187, 27300, 16435, '동백이와 용식이가 대성통곡하며 이별했던 옹산역');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2188, 27301, 13306, '동이에서 절로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2189, 26019, 6637, '차준호(이주승)가 녹사평대로 40길을 따라 집 바로 맞은편에 있는 고가도로를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2190, 25948, 17959, '36화에서 봉선화(엄현경)가 있을 때  출소한 그녀는 자신의 아들이 감옥에서 사망했다는 소식을 듣고 이 곳에 안장되었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2191, 26698, 17959, '1회에서 드라마 등장인물인 윤재민(차서원)이 이 장소에서 버스킹을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2192, 27302, 17959, '21회에서 윤재민(차서원/이창섭)은 \'한옥 빵집\' 아르바이트를 하던 소꿉친구와 우연히 마주쳤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2193, 26990, 17959, '드라마 안에서 \'태극그룹\'의 본사로 나오는 건물이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2194, 26078, 17959, '5회에서 트로트 가수 강풍기 역의 윤재민(차서원)과 그가 사랑스럽게 부르는 봉선화(엄현경)는 함께 아르바이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2195, 25826, 17959, '56화에서 빵을 맛보기 위해 봉선화(엄현경)를 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2196, 24900, 17959, '윤재민(차서원)은 3-4회에서 부모님이 보낸 경호원을 피해 도망치던 중 봉순화(엄현경)와 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2197, 27120, 17959, '36화에서 봉선화(엄현경)는 누명을 쓰고 수감된 감옥에서 출소합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2198, 26311, 17959, '5화에서 윤재경(오승아)과 문상혁(한기웅)이 이 공원에서 소풍 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2199, 25668, 17959, '120화에서 봉선화(엄현경)가 그와 헤어지고 그녀의 결정을 받아들이겠다는 메시지를 보낸 후 윤재민(차서원)이 이 다리 발치에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2200, 26659, 17959, '6회에서 그녀가 문상혁(한기웅)과 헤어진 후 집으로 돌아오는 모습에 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2201, 26660, 17959, '6회에서 봉선화(엄현경)가 문상혁(우찬혁)과 헤어진 후 집으로 돌아오는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2202, 26139, 17959, '문상혁(한기웅)이 헤어진 뒤 집으로 돌아오는 6회에서 보듯 봉선화(엄현경)는 둘째 남편에서 아이와 할머니 곱분(성병숙)과 함께 사는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2203, 24907, 17959, '윤재민(차서원/이창섭)은 봉선화(엄현경 분)가 살인죄로 재판을 받는다는 소식을 듣고 33회에서 이 계단을 오르내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2204, 27303, 17959, '이 집은 두 번째 남편에서 대구그룹 오너 윤 가문의 집으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2205, 27304, 17959, '40화에서 윤재민(차서원/이창섭)이 봉선화(엄현경)를 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2206, 27305, 17959, '두 번째 남편의 한옥 빵집 외관이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2207, 26273, 17959, '1화에서 봉순화(엄현경)와 문상혁(한기웅)은 이 성당 앞에서 서로의 사랑을 약속합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2208, 26030, 14429, '12회에서 김우철(최원영)은 이 식당에서 교수 친구들을 만나 이야기를 듣습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2209, 24933, 14429, '김우철(최원영)과 김이진(박효주)이 7회에서 이 장소 야외 카페에서 첫 \'운명의 만남\'을 가졌고 첫눈에 반한 것이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2210, 27307, 14429, '11회에서 하노라(최지우)가 남편 김우철(최원영)에게 이혼을 요구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2211, 25773, 14429, '3화에서 인생에서 가장 중요한 역을 떠올리면, 하노라(최지우)는 남편과 작은 아들을 데리고 이 공항에서 독일로 출발해 세 사람이 어떻게 이 공항에 도착했는지를 떠올린다. 한국으로 돌아가다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2212, 26086, 14429, '이 대학은 드라마에서 하노라(최지우 분)와 차현석(이상윤 분)이 강의하는 대학교로 드라마 메인 촬영지이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2213, 27090, 14429, '5, 6회에서 Massa Maren 쇼가 열리는 곳입니다. 하노라(최지우)와 김우철(최원영)은 VIP 티켓을 각자 따로 받았고, 그가 연인 김이진(박효주)과 함께 온 사이 노라는 혼자 온다. 그래서 그녀는 남편이 바람을 피우고 있음을 알게된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2214, 24926, 14429, '6화에서 차현석(이상윤)과 라윤영 (정수영) 이 카페에서 만나 하노라(최지우)가 가진 게 얼마 없다는 걸 그녀에게 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2215, 25942, 14429, '라윤영(정수영)과 서동철(김강현)은 친구와 함께 춤을 추기 위해 이 술집에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2216, 25734, 14429, '1회에서 하노라(최지우 분)는 \'우천대학교\'에 지원한 부정적인 결과를 받고 눈물을 흘리며 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2217, 27308, 14429, '10회에서 하노라(최지우)가 \'데이트 과제\'를 위해 학우를 만나러 갈 때 1번 출구로 나온다.15회에서 노라와 차현석(이상윤)은 \'데이트 아닌 데이트\'를 마치고 이곳(차를 두고)에서 지하철을 탔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2218, 27309, 14429, '하노라(최지우)는 어린 차현의 도움으로 자신과 가장 친한 친구의 기억을 떠올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2219, 25868, 13764, '1화에서 촬영 스케줄에 맞춰 앤서니 김(김명민)은 오토바이를 타고 서울 방향으로 속도를 내서 이 도로를 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2220, 26285, 13764, '4회에서 현민이 예상치 못한 안소니와의 작업을 계속하기로 결정한 후 안소니 김(김명민)과 강현민(슈퍼주니어 최시원)이 이 식당에 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2221, 27310, 11174, '4화에서 모두가 이사를 다니는 곳으로 이장석(김범)의 체육관이 결정된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2222, 26698, 13416, '최종회 16화의 드라마 모든 등장인물의 플래시몹을 이곳에서 촬영했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2223, 27318, 13416, '드림하이에서 방송국 건물로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2224, 27222, 13416, '이 건물 단지는 \'기린 미술 학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2225, 24905, 14695, '배우 영원(박원석)은 5화에서 며느리에게 괴물 시어머니 역을 맡아 이 공원에서 촬영을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2226, 26213, 14695, '디어 마이 프렌즈에서 난희(고두심)는 이곳에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2227, 26273, 14695, '희자(김혜자)가 미사에 참석해 봉사활동을 하고 있는 교회다. 4화에서 희자가 신부에게 범행을 자백하는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2228, 27319, 13976, '따뜻한 말 한마디에서 송미경(김지수)과 유재학(지진희)이 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2229, 26000, 18298, '구원빈(홍경)이 이 다리를 혼자 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2230, 27320, 18298, '오진(신예은)은 차민재(강태오)와 만나 헤어지는 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2231, 26706, 18298, '차민재(강태오)는 오진(신예은)에게 전날 밤에 있었던 일에 대해 사과하려고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2232, 26042, 18298, '오진(신예은)은 술 취한 차민재(강태오)와 우연히 마주친 구원빈(홍경)을 이 건물의 출구로 안내한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2233, 27321, 18298, '오진(신예은)과 구원빈(홍경)이 피자를 먹고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2234, 27322, 14659, '딴따라에서 여민주(채정안)와 신석호(지성)가 커피를 마시러 가는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2235, 27200, 14659, '신석호(지성)가 대표로 활약하는 회사 케이탑 엔터테인먼트 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2236, 25719, 14659, '신석호(지성)가 소속된 엔터테인먼트 회사 케이탑의 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2237, 25913, 14659, 'OK & GAP ENTERTAINMENT로 나온다.');
