INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2241, 27325, 7123, '땐뽀걸즈 10회에서 박혜진(이주영)이 엄마와 함께 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2242, 27100, 7123, '11회에서 댄스팀이 여행차 이곳에 온다. 그들은 이 해변의 가장 동쪽 끝에서 단체 사진을 찍습니다. 저녁이 되면 두 선생님 이규호(김갑수 분)와 한동희(장성범 분)가 이곳에 와서 술을 마시고 규호는 성범에게 댄스 동아리를 맡아달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2243, 25661, 7123, '1회에서 김시은(박세완)은 당시 남자친구 이태순(연재형)을 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2244, 25797, 7123, '5회에서 김시은(박세완)은 당시 남자친구였던 이태선(연재형)을 만나러 가는 기차를 타고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2245, 25678, 13173, '마지막 20회에서 김주혁(강태민)과 이우주(한혜진)가 프랑스로 떠나기 전 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2246, 27332, 15383, '11회에서 송그림(김소현)이 라디오 PD 이강(박윤)과 오후를 보낸 후 버스 정류장에서 버스를 타고 이동한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2247, 25607, 15383, '11회에서 송극림(김소현)과 라디오 PD 이강(윤박)은 오후를 함께 보내는 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2248, 24933, 15383, '7-8회에서는 지수호(윤두준)의 어머니 남주하(오현경 분)가 곧 오픈할 갤러리로 이 건물이 등장한다. 수호와 이강이 주하에게 찾아와 자신들의 의사를 밝히거나 자신의 의사를 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2249, 24935, 15383, '2회에서 송그림(김소현)은 퇴근 후 지수호(윤두준)의 동료 배우들과 그의 새 드라마 동료들의 초대로 이곳 모임에 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2250, 27266, 15383, '7화에서 라디오 PD 이강(윤박), 송그림(김소림) 현)과 지수호(윤두준)는 커피를 마시러 이곳에 오고, 수호는 이강이 무엇을 주문할지 알고 있는 그림에게 질투한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2251, 25613, 15383, '7회에서 이곳에서 남주하(오현경), 지수호(윤두준) 엄마, 진태리(유라)가 만나  거래를 성사시킨다. 주하는 태리에게 수호와 사귄다는 소문을 퍼뜨려달라고 부탁하고, 정다슬(서예슬)은 주하에게 약속을 지킬 수 있게 해달라고 애원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2252, 27333, 15383, '6회에서 베테랑 라디오 DJ 문성우(윤주상)에게 스튜디오를 빌려달라고 설득하기 위해 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2253, 25661, 15383, '5회에서 옛 상사에게 강요당해 산타리나장을 한 송그림(김소현)은 방송 중 마포관광정보센터[마포관광정보센터] 앞에서 제이의 \'오후 6시에 만나자\' 광고를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2254, 27334, 15383, '13회에서 이강 PD(박윤)의 휴식처이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2255, 26039, 15383, '1회 지수호(윤두준) 새 드라마 제작발표회가 이곳에서 열렸다. 라디오 DJ 겸 아이돌 강민우(카메오 유권)가 라디오를 하지 않고 펜트하우스 스위트룸에서 친구들과 파티를 하고 송그림(김소현)이 찾아온 이 호텔도 이곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2256, 27318, 15383, '이곳은 송그림(김소현)이 이강(윤박) 밑에서 작가로 활동하는 라디오 방송국으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2257, 27335, 15383, '16회 회상으로, 어린 지수호(윤두준), 그의 가장 친한 친구 우지우(이의웅), 송그림(김소현)이 여기에서 버스를 타는 것을 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2258, 25910, 15383, '3회에서 라디오팀이 드디어 결성되었을 때, 첫 팀 나들이를 하러 이곳에 옵니다.');
