INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2374, 24930, 3923, '9회에서 관우의 이복동생인 최공명(이병준)과 함께 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2375, 27505, 3923, '2회에서 김채원(김하늘)과 최관우(김재원)는 벚꽃이 만발하는 이 개울을 산책하고 잠시 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2376, 25921, 3923, '7회에서 누나가 술집에서 일하는 것을 알게 된 후, 최관우(김재원)은 멍하니 이 거리를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2377, 25872, 3923, '4회에서 이 다리 북쪽 기슭에서 최관우(김재원)가 학교의 짱과 싸운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2378, 25773, 3923, '2회에서 김채원(김하늘)의 가족은 최장수(현석)의 장례식에 참석한 뒤 공항을 떠난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2379, 27506, 3923, '최관우(김재원 분)는 2회에서 오빠의 전화를 받고 드디어 이 경찰서에서 오빠와 엄마를 발견하고 아버지가 세상을 떠났다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2380, 27507, 3923, '8화에서 최관우(김재원)는 옛 친구들과 밴드 동료들을 이 곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2381, 26311, 3923, '13~14회에서 김채원(김하늘)과 김봉균(고명환)이 이 공원에서 웨딩 사진을 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2382, 24945, 3923, '16회에서 고열을 앓는 김채원(김하늘)의 방문을 거절한 최관우(김재원)는 작업실을 나와 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2383, 25017, 3923, '11회에서 최관우(김재원)가 미국에서 3년 만에 귀국해 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2384, 27508, 16850, '4회에서 박도연(김서연)이 놀러가는 현우(최경훈)의 아파트이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2385, 27509, 13474, '로맨스가 필요해 1회에서 선우인영(조여정)과 배성현(최진혁)이 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2386, 25737, 13474, '1회에서 스트레스를 받은 선우인영(조여정)은 질 샌더스 가게 앞에서 길을 건너기 위해 빨간불에서 기다리고 있다. 갑자기 배성현(최진혁)이 그녀의 손을 잡고 길을 가로질러 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2387, 27510, 13474, '8화에서 선우인영(조여정)과 배성현(최진혁)이 이 다리의 첫 번째 부분에 찾아와 심야 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2388, 26088, 13474, '1화에서 선우인영(조여정)은 전 남자친구 김성수(김정훈)가 다른 여자아이들과 함께 이 건물 앞에서 춤을 추는 모습을 상상한다. 이곳은 강현주(최송현)의 전 약혼자가 일하는 곳이기도 하며, 3화에서 강현주(최송현)가 결혼식 날 바람맞힌 복수를 하기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2389, 25636, 13474, '14회에서김성수(김정훈)는 선우인영(조여정)을 이 카페의 뒷골목으로 데리고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2390, 26136, 13474, '선우인영(조여정)과 배성현(최진혁)은 8화에서 저녁 데이트를 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2391, 24906, 13474, '8회에서 선우인영(조여정)과 배성현(최진혁)이 데이트를 하러 이곳에 와서 그녀가 자신의 삶에 전혀 관심이 없다고 느껴 화를 낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2392, 25877, 13474, '11회에서 선우인영(조여정)과 배성현(최진혁)이 김성수(김정훈), 배우 윤강희(하연주)와 함께 이 식당에서 더블 런치 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2393, 26285, 13474, '2회에서 선우 인영(조여정)은 그녀의 남자 친구가 어떻게 될 지 상상한다. 김성수(김정훈)는 배우 윤강희(하연주)와의 로맨틱한 저녁식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2394, 25882, 13474, '10화에서 선우인영(조여정)과 배성현(최진혁)은 이 공원에서 피크닉 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2395, 25910, 13474, '14회에서 김성수(김정훈)와 가슴 아픈 선우인영(조여정)은 이 곳에서 술을 마십니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2396, 27048, 15973, '5회에서 눈이 내리는 날 바닷가에서 작가와 이야기를 나누는 차은호(이종석)의 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2397, 27512, 15973, '강병준(이호재) 작가의 가평저택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2398, 25811, 15973, '로맨스는 별책부록에서 나온 단이의 결혼식 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2399, 25718, 15973, '16화에서 강병준 작가의 책 \'4월 23일\' 재발매 편지에서 영감을 받아 두 청년이 이 공원을 거닐며 할머니를 돕는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2400, 27513, 15973, '2회에서 강단이(이나영)가 차은호(이종석)의 집으로 향하기 위해 내리는 버스정류장이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2401, 27514, 15973, '9회에서 차은호(이종석)가 목걸이를 사기 위해 이곳을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2402, 25612, 15973, '마지막 장면에서 강단이(이나영)와 차은호(이종석)가 이 정원을 거닐며 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2403, 27354, 15973, '9화에서 미술전을 관람하기 위해 지서준(위하준)과 송해린(정유진)이 따로 미술관에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2404, 24932, 15973, '12회에서 이 식당은 남산 근처에 위치한 것으로 추정되는 \'로맨틱 테이블\' 레스토랑으로 등장합니다. 차은호(이종석)와 강단이(이나영)는 이곳에서 로맨틱한 저녁 데이트를 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2405, 26135, 15973, '2회 겨루 출판사 주차장에서 멀리 떨어져 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2406, 24900, 15973, '이 건물 외관은 차은호(이종석) 편집장으로 일하고 있는 \'겨루 출판사\'로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2407, 25951, 15973, '8-9화에서 지서준(위하준)과 송해린(정유진)이 여기서 만납니다. 서준이 겨루출판사와 책 계약을 맺는 이야기를 하는데 갑자기 눈이 내리기 시작합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2408, 24901, 15973, '11회에서 송해린(정유진)은 지서준(위하준)에게 이곳에서 만나자고 한다, 그의 표지 디자인에 대해 이야기하기로 했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2409, 25830, 15973, '9화에서 송해린(정유진)이  5권의 북 디자인 계약을 맺기 위해 지서준(위하준)과 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2410, 24935, 15973, '5화에서 싱글 고유선(김유미)은 친구들과 만나 술 한잔하고 친구들이 남편과 아이들에 대해서만 이야기하는 것을 듣고는 빨리 떠납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2411, 26427, 15973, '9회에서 오지율(박규영)의 어머니가 이 건물을 지날 때 전화한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2412, 27515, 15973, '로맨스는 별책부록 강단이가 간 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2413, 26282, 15973, '2화에서 차은호(이종석)오 김재민(김태우) 겨루출반 대표가 처음 만나는 곳으로 인터뷰에서 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2414, 25924, 15973, '차은호(이종석 분)가 13화에서 강연을 하러 온다. 강의 도중 송해린(정유진 분)으로부터 강단이(이나영 분)가 회사를 그만뒀다는 내용의 문자메시지를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2415, 26283, 15973, '3화에서 강단이(이나영)가 카페 밖 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2416, 27516, 15973, '11~12회에서 이 건물은 유명숙 작가의 북콘서트가 있었던 건물로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2417, 26293, 15973, '이 건물의 외관은 11~12화 유명석 작가의 북콘서트가 열리는 건물로 등장한다. 오지율(박규영)이 북 콘서트를 떠날 때 송해린(정유진)에게 자신이 이제부터 편집자로서 자신의 롤모델이라고 말하는 것도 여기에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2418, 26095, 15973, '강단이(이나영 분)와 차은호(이종석 분)가 \'로맨스는 보너스북\' 5회에 출연해 첫 월급을 받은 것을 축하하며 간식을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2419, 26265, 15973, '차은호(이종석 분)는 라디오 방송이 끝난 후 유명석 작가를 저녁 식사에 초대한다. 그녀는 그에게 두 명의 손님이 더 올 것이라고 말했는데, 그들은 송해린(정유진)과 지서준(위하준)으로 밝혀졌다. 유 작가는 은호에게 서준이가 자신의 책 표지를 디자인했으면 좋겠다고 말한다. 저녁 식사 후, 세 사람은 그녀를 배웅하고, 해린은 떠난다. 두 남자의 대화는 필연적으로 강단이(이나영 분)에게로 향하게 되고, 서준은 강제로 은호의 차에 올라타서 그들이 있는 곳으로 가서 그녀를 볼 수 있게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2420, 24957, 15973, '이 대학은 1회에서 보듯 차은호(이종석)가 문학창작을 강의하고 교수직을 맡고 있는 \'한국대학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2421, 26131, 15973, '15회에서 차은호(이종석)와 지서준(위하준)이 이 식당에서 술자리를 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2422, 27517, 15973, '로맨스는 별책부록에서 나온 돈가스집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2423, 27518, 15973, '로맨스는 별책부록 10화에 나온 일본 경양식 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2424, 27519, 15973, '로맨스는 별책부록 6화에서 나온 소바 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2425, 25909, 15973, '15회에서 지서준(위하준)과 그의 어머니가 이곳에서 저녁식사를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2426, 27520, 15973, '7화에서 지서준(위하준)이 강단이(이나영)를 이 서점에 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2427, 27056, 15973, '3회에서 연애를 시작하려는 김재민(김태우)의 우연한 접근으로 차은호(이종석)가 나타나 겨루 출판사 사장 김재민(김태우)의 정체를 폭로한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2428, 25910, 15973, '4회에서 차은호(이종석)은 그의 상사 김재민(김태우), 디자이너 지서준(위하준)과 함께 미팅 후 이곳에 와 술을 마십니다. 그들은 실종된 강병준 작가에 대해 이야기를 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2429, 26040, 15973, '이 식당은 드라마의 송해린(정유진)의 부모님이 운영하는 \'선하만두\'로 등장하는데, 4회에서 보듯 해린과 차은호(이종석)가 사랑에 병든 부모님을 찾아와서 밥을 먹는 모습을 10회에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2430, 24922, 15973, '4회에서 강단이(이나영)가 차은호(이종석)에게 술을 \'가르친\' 직후, 잠시 덕성여중 담벼락 앞에 앉았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2431, 27521, 15973, '13회에서 송해린(정유진)이 서점에서 지서준(위하준)과 화해하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2432, 25799, 15973, '7회에서 차은호(이종석)와 그의 동료 봉지홍(조한훌)은 최형수 시인의 집을 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2433, 27522, 15973, '1회에서 강단이(이나영)가 플래시몹 댄스에 참여하는 찜질방입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2434, 27523, 15973, '1회에서 강단이(이나영)가 취업인터뷰를 하고 돌아오는 길에 기숙사B동 앞 버스 정류장에서 하이힐을 잃어버렸다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2435, 26066, 15347, '12회에서 선혜(이민지)는 이 정원에서 김민규(유승호)에게 연애에 대한 아이디어를 가르친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2436, 26323, 15347, '9화에서 김민규(유승호)가 예리엘(황승운)과 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2437, 24933, 15347, '홍백균(엄기준)이 산타마리아팀 자금 조달을 위해 이곳에서 10회에서 사업 미팅을 갖는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2438, 24900, 15347, '김민규(유승호)와 조지아(채수빈)가 19회에서 마지막 데이트를 하는 장면. 조지아는 책 "The Velveteen Rabbit"을 집어들고 민규에게 이야기를 들려준다. 20화에서 민규는 지아를 떠올리며 책을 읽으러 이곳에 다시 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2439, 27524, 15347, '24,25회에서 주지아(채수빈)가 김민규(유승호)와 마지막 작별을 고한 후 해변으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2440, 27525, 15347, '21회에서는 조지아(채수빈) 할머니네 식당으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2441, 27526, 15347, '21회에서 주지아(채수빈)와 김민규(유승호) 그녀를 몰래 택시로 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2442, 27527, 15347, '21, 31회에서 황유철(강기영)과 예리엘(황승운)이 만나 술을 마시는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2443, 24935, 15347, '13회에서 황유철(강기영)이 술을 마시러 이곳에 오고 예리엘(황승운)에게 프로포즈 하기 위해 김민규(유승호)와 데이트를 하고 있던 예리엘(황승운)을 불러들인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2444, 25613, 15347, '16회에서 조지아(채수빈)와 홍백균(엄기준)이 저녁을 먹으러 이곳에 왔고, 백균은 마침내 그와 헤어진 이유를 이해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2445, 25735, 15347, '2회에서 조지아(채수빈)가 친구의 카페 \'여름눈\'으로 출연해 전 남자친구 홍백균이 로봇 아지3의 모델로 얼굴을 사용한 과정을 이야기한다. 에피소드 4에서 그녀는 오빠의 집에서 쫓겨난 후 이곳에서 밤을 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2446, 27528, 15347, '30회에서 파이(박세완)가 이 가게에서 스타일 변신을 위해 신발 한 켤레를 받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2447, 25659, 15347, '29회에서 스스로 서울을 발견하기 시작한 뒤 벤치에 앉아 옆에서 쉴 때 노숙자와 만나 이야기를 나누는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2448, 26173, 15347, '1회에서 조지아(채수빈)는 이 광장으로 향하는 길에 스쿠터를 주차했다(배경에 조형물이 보인다).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2449, 26328, 15347, '1회에서 주지아(채수빈)는 김민규(유승호)가 그를 위해 한정판 로봇을 사준 데 대해 고마워하지 않아 서운해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2450, 26263, 15347, '9회에서 김민규(유승)이 지아가 자신의 알레르기를 진정시키는 효과가 있다는 것을 알고 그녀를 안는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2451, 25636, 15347, '20회 김민규(유승호)가 제보자 장두삼을 만나러 왔지만 당황하고 제보자는 자리를 뜬다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2452, 27529, 15347, '29회에서는 김민규(유승호)와 조진배 단장(서동원)이 제보자 장두삼과 함께 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2453, 26992, 15347, '1회에서 조지아(채수빈)는 김민규(유승호)와 처음 만나 한정판 액션 피규어 메탈 기어 건너를 판매한다. 하지만 그는 포장이 손상되지 않았기 때문에 지불을 거부한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2454, 24927, 15347, '20화에서 김민규(유승호)는 환풍기가 필요하다고 판단하고 기차를 타고 이 역에서 회사까지 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2455, 26487, 15347, '26,27회에서는 조지아(채수빈)와 김민규(유승호)가 이 나무와 아름다운 유성우 밑에서 오해를 풀고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2456, 26152, 13042, '마리아/유소영(장진영)과 제임스 리(허준호)는 이곳에 잠시 머물며 마리아가 사무실을 두고 있는 곳으로 비밀회의를 열고 계약을 체결한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2457, 27151, 17702, '5화에서 보듯 딸 강솔B(이수경)가 학교 기숙사로 돌아가기로 결심하는 강주만(오만석)의 가족주택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2458, 25908, 17702, '이곳은 드라마 안에서 고형수(정원중) 의원의 비선실로 등장한다. 극중 여러 장면에서 이곳을 볼 수 있는데, 11회에서 이만호(조재룡)가 아들의 사진을 건네받았을 때의 장면도 이곳에서 촬영되었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2459, 27530, 17702, '16회에서 김교수(이정은) 박변호사(이춘희)과 동일(우현)이 이 술집에서 모의 재판을 마치고 자축한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2460, 27532, 16840, '드라마 <루갈> 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2461, 26670, 16840, '1화 초반, 한 남자가 아기를 안고 이 버려진 건물로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2462, 27331, 16840, '고용덕 회장(박정학)의 저택으로 보이는데, 2화에서 그의 장례식이 끝난 뒤 이곳에서 범죄조직 아르고스 회원들이 모여 회의를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2463, 25976, 17572, '8화에서 지오(김래원)와 하늘애구름(이다희)이 데이트를 위해 이곳에 온다. 그리고 이곳에서 그들의 결혼 사진도 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2464, 27533, 17572, '8화에서 그녀와 그녀의 추종자들의 수행자들이 사는 곳으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2465, 27534, 17572, '하늘에구름(이다희)이 지오(김래원)와 이손(김성오)을 따라 이곳 철교를 향해 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2466, 27189, 17572, '이곳은 지오(김래원)가 사는 곳으로, 1화에서 그가 쫓기는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2467, 26681, 17572, '2회에서 이 다리를 지나 왼쪽 다리와 평행한 육교에서 이손(김성오)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2468, 27535, 17572, '2회에서 최유나(정다은)가 오른쪽 건물을 지나 조깅한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2469, 25811, 16026, '성기준 대표의 아버지의 저택으로 등장한다. 11화에서는 송판사(김호정)가 기준의 새로운 악행을 보고하기 위해 방문하고, 나중에는 아버지의 하인에게 골프채로 두들겨 패라는 명령을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2470, 26320, 16026, '해당 드라마의 주요 법원으로 등장한다. 매회 법원 장면이 이곳에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2471, 24935, 16026, '6회에서 B&G 로펌은 강기석(윤박)이 고태림(진구)의 로펌을 이기고 이 술집에서 승리한 것을 자축했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2472, 26617, 16026, '1화에서 등장하는 법무법인 서재인(서은수)의 사무실이 이곳에 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2473, 27510, 16026, '10회에서 서재인(서은수)과 민주경(채정안)이 사건을 의논하기 위해 이 다리 발치에 오른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2474, 25796, 16026, '1회 출근길, 특정 \'변태\'와 돈에 미친 변호사 고태림(진구 분)에게 항의하는 할머니가 서재인(서은수 분)의 시선을 사로잡는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2475, 27536, 16026, '이 한옥은 10화에서 고태림(진구)의 아버지 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2476, 27537, 16026, '이 저택은 괴테의 일명 변호사 고태림(진구)의 로펌으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2477, 25781, 16026, '10회에서 부모를 상대로 소송을 제기하려는 천재 아역 배우 유하린의 거실이다. 이곳에서 고태림(진구), 서재인(서은수)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2478, 26030, 10890, '9회에서 남규만(남궁민)과 검사 채진경(오나라)은 그를 그들의 편으로 만들기 위해 변호사 박동호(박성웅)를 이곳에서 만났습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2479, 25824, 10890, '이 건물은 조폭 변호사 박동호(박성웅) 회사로 등장한다. 3회에서 서진우(유승호)는 아버지를 지키기 위해 서진우를 투입하려다 이곳에서 쫓겨난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2480, 25910, 10890, '19회에서는 박동호 변호사(박성웅)와 탁검사(송영규)가 남일호와의 동맹을 축하하며 이 식당에서 소주를 마십니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2481, 27331, 10890, '4화에서 남일호(한진희)의 저택으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2482, 25989, 6960, '리얼에서 김수현의 호화주택으로 나오는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2483, 27538, 6960, '리얼에서 수영 장면을 촬영한 수영장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2484, 27539, 6960, '리얼에서 성동일의 주 활동무대로 나오는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2485, 27540, 6960, '리얼에서 소방대원 장면을 찍은 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2486, 27541, 6960, '리얼의 주요 배경으로 나온 부산 호텔');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2487, 27542, 6960, '리얼에서 김수현의 타투장면을 촬영한 바버샵');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2488, 27543, 6960, '리얼에서 나온 송도의 호텔 바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2489, 25604, 15554, 'IT컴퍼니 넥스트인 이유찬(EXO-K 수호) 대표의 저택으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2490, 25640, 15554, '6화에서 김보라(하연수)는 가족과 친구들, 그리고 두 상사를 위해 첫 월급으로 산 선물을 가장 친한 친구와 함께 준비하는 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2491, 25787, 15554, '7회에서 박미소(윤다영)가 실직한 남자친구 차도진(박성훈 분)에게 소득과 저축, 미래 직업 선택지에 대한 온갖 질문을 던지며 결국 그와 헤어지는 것이 최선이라는 결론을 내리게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2492, 26673, 15554, '2화에서 넥스트인이 모든 공공 데이터를 관리하는 데 도움이 되는 새로운 컴퓨터 프로그램 DS를 제안하는 부처와의 만남이 이곳에서 이루어진다. 김보라(하연수)가 발을 헛디뎌 논란을 언급할 때까지 만남은 잘 진행됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2493, 25737, 15554, '2회에서 이유찬(EXO-K 수호)이 김보라(하연수)와 함께 업무상 미팅을 위해 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2494, 27544, 15554, '13회에서는 차도진(박성후)과 박미소(윤다영)가 미의 산부인과를 만나기 위해 병원으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2495, 25335, 15554, '이유찬 대표(수호)가 1회에서 오토바이를 타고 출근길에 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2496, 27211, 15554, '6회에서 박미소(윤다영)가 남자친구 차도진(박성훈)을 데리고 이 식당을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2497, 24922, 15554, '2화에서 이유찬(엑소 수호)이 이곳 노점에서 목걸이를 사는 모습이 나왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2498, 27545, 15554, '3회에서 이유찬(EXO-K 수호)이 김보라(하연수)를 식사에 초대하는 장소로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2499, 27535, 15554, '1, 2회에서 이유찬 대표(EXO-K 수호)가 사회를 맡은 신입사원 선발대회가 열리는 행사장에서 김보라(하연수)를 처음 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2500, 27222, 15554, '7회 이유찬(엑소 수호)이 그녀는 그가 자신의 직업에 대해 더 많이 알기를 원하기 때문에 여자친구 민태라(김예원)와 함께 이 곳을 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2501, 25796, 4140, '1회에서 독고영(이진욱)은 검사 최자혜(고현정)와 사건을 놓고 대립한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2502, 27556, 17898, '5회에서 정진(남지현)은 저녁 시간에 작가 톰 김(최성재)을 이곳으로 데려와 말한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2503, 26122, 17898, '5화에서 회사 건물로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2504, 27557, 17898, '1회에서 정진(남지현)이 절친 이윤미(윤다영)와 이 술집에서 술자리를 갖는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2505, 26399, 17898, '1화에서 정진(남지현)의 5년 사귄 남자친구는 이 카페에서 그녀와 헤어진 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2506, 26311, 17898, '5화에서 정진(남지현)과 작가 김톰(최성재)이 (찾은 만년필을 그에게 돌려주기 위해) 이 공원에서 오후에 만났을 때, 그는 88호수를 바라보며 벤치에 앉을 것을 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2507, 26679, 17898, '5화에서 이 버스정류장에서 버스를 기다리는 이복난(이주실)과 아내와 결혼길에 오른 배윤기(강기동)가 마녀의 만찬에서 이뤄낸 소원을 우연히 듣게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2508, 26040, 17898, '1화에서 정진(남지현)의 어머니 서애숙(소희정)이 이어받는 옛 \'진미식당\'이 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2509, 24947, 17898, '5회에서 정진(남지현)은 저녁 시간에 작가 톰 김(최성재)을 이곳으로 데려와 말한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2510, 27558, 17898, '4화에서 진선미(안은진)의 공연장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2511, 27559, 17898, '1화에서 조희라(송지효)는 정진(남지현)이 커피를 마시는 곳으로 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2512, 27560, 17898, '정진(남지현)은 엄마 서애숙(소희정)과 함께 이 집에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2513, 27561, 15234, '1화에서 마이점(정려원)과 여진욱(윤현민)이 엘리베이터를 타는 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2514, 26320, 15234, '11화에서 이상현 재판은 이 건물 \'법정\'에서 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2515, 24935, 15234, '11회에서 양유진(손담비)이 이상현 셰프와 술자리를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2516, 27562, 15234, '2회에서 남우성(장정연)의 사건으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2517, 26229, 15234, '7회에서 여진욱(윤현민)은 마이듬(정려원)을 이곳 공원 암벽등반에 데리고 와서 그녀를 격려한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2518, 25795, 15234, '7화에서 마이듬(정려원)과 여진욱은 고향에 대해 서로 수다를 떨며 DMC를 지나 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2519, 26137, 15234, '8회에서 마이듬(정려원 분), 여진욱(윤현민 분), 수사관 구석찬(윤경호 분)이 접수원에게 호텔 내 \'K\'방에 대해 문의하는 모습을 볼 수 있다. 이후 포주 \'장어\'(김기무)와 길을 건너며 추격전이 이어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2520, 27120, 15234, '16회에서 전 시장이 어디에 있는지를 나타내기 위해 건물의 외관이 짧게 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2521, 25798, 15234, '8회 방송분에서 여진욱(윤현민)과 수사관 구석찬(김기무)이 이 지하철역 6번 출구 근처에서 포주 \'장어\'(김기무)를 쫓는다. 지친 석찬이 부분적으로 포기하면서 진욱은 계속해서 일을 쫓게 된다. 6번 출구는 7회에서도 진욱이 \'추가 서비스\'를 제공하는 여고생을 만나기 위해 잠복하는 모습이 포착됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2522, 27563, 15234, '8회에서 여진욱(윤현민)은 포주 \'장어\'(김기무)를 쫓던 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2523, 27564, 15234, '8회에서 여진욱(윤현민)과 수사관 구석찬(윤경호)이 이 건물을 지나는 장면으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2524, 27361, 15234, '8화에서 여진욱(윤현민)과 수사관 구석찬(윤경호)이 이 거리를 따라 포주를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2525, 25913, 15234, '6화에서 시장 후보와 만나 협박을 당하는 장면으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2526, 25641, 14050, '6회에서 반지연(엄정화)은 이 식당에서 어머니가 정한 두 번의 소개팅을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2527, 26030, 14050, '16회 마지막회에서 윤동하(박서준)가 아버지 윤세준(조선묵)을 이곳에서 만났습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2528, 24933, 14050, '방지연(엄정화)과 윤동하(박서준)가 정은채(정연주)와 용수철(윤현민)이 열애 중이라는 사실을 알게 되면서 최종회 16회에 네 명 모두 이곳으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2529, 24900, 14050, '1,5화에서 잡지 \'트러블\'의 본점 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2530, 27565, 14050, '4회에서 반지연(엄정화)은 기금 마련을 위해 이 가게에서 드레스를 입는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2531, 26152, 14050, '1-2화에서 반지연(엄정화)의 동료들이 그녀를 농락하는 곳은 이 호텔의 전 조 바이다. 실제로 이들은 용수철(윤현민)에게 돈을 주고 그녀를 매력적으로 만든 뒤 공개적으로 차버렸다. 그가 하는 것. 하지만 그 다음에는 윤동하(박서준)가 달려들어 키스한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2532, 26173, 14050, '최종회 16회에서 반지연(엄정화)은 자신의 삶을 되돌아보며 이 광장을 향해 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2533, 24906, 14050, '4회에서 배우 김정도(전노민)가 이 공원 유니세프 광장에서 촬영을 하고 있다. 그녀의 팀과 함께 반지연(엄정화 )은 기회를 이용하여 물병에 DNA를 얻기 위해 누군가를 보냈습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2534, 27566, 14050, '6화에서 반지연(엄정화)의 연례 동창회가 열리는 식당장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2535, 25909, 14050, '지난 16회에서 반지연(엄정화)과 윤동하(박서준)는 동하의 아버지 윤세준(조선묵)을 이곳에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2536, 25335, 14050, '윤동하(박서준)는 6화에서 반지연(엄정화)과 실랑이 끝에 스쿠터를 타고 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2537, 27567, 14050, '6화에서 과일가게&카페에 과일 사러 오는 반지연(엄정화)을 윤동하(박서준)와 함께 그녀를 태우고 드라이브하는 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2538, 26273, 14050, '1화 회상 장면에서 웨딩드레스 차림으로 제단 앞에 홀로 서 있는 반지연(엄정화)의 모습이 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2539, 27568, 4414, '옛 70년대의 모습을 고이 간직한 오래된 마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2540, 27569, 4414, '오프닝에서 김혜자가 춤을 추던 억새 만발한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2541, 25611, 15744, '6회에서 공마성(최진혁)은 \'힐링 빌리지\' 비전을 설명하기 위해 의료와 함께 출장을 간 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2542, 25612, 15744, '힐링 빌리지\'의 정원으로 등장한다. 주기쁨(송하윤 분)이 공원에 앉아 드라마 \'시간을 지운 남자\'의 원고를 읽는 동안, 6화에서는 공마성(최진혁 분)이 이 식물원을 거닐고 있는 모습을 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2543, 26256, 15744, '8회에서 상처받은 성기준(인피니트 호야)은 이 절에 숨는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2544, 24934, 15744, '5, 6회에서 청춘 슈퍼리치들의 파티인 오드볼 이벤트가 열리는 이곳에서 무도회의 진행자인 명석환(안세하)이 수표를 태우고 재를 와인잔에 넣어 건배하고 이어 주기범(송하윤)을 무대로 불러 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2545, 27105, 15744, '7회에서 성기준(인피니트 호야)은 주기범(송하윤)을 이곳으로 데려와 그녀의 뜻에 반해 그녀를 \'다시 보게\' 합니다. 그녀는 항상 드라마에서 보던 대로 하고 싶었기 때문입니다. 하지만 옷을 계산하려고 할 때, 그는 그의 카드가 정지되었음을 알게됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2546, 27510, 15744, '12화에서 성기준(인피니트 호야)은 사촌 공마성(최진혁)이 기억상실증에 걸렸다는 사실을 알고 이곳에 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2547, 25662, 15744, '7회에서 성기준(인피니트 호야)이 주기범(송하윤)에게 고백하려고 하는 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2548, 26399, 15744, '6회에서 공마성(최진혁)이 주기범(송하윤)을 위해 식탁을 사주는 장면으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2549, 25909, 15744, '7화에서 공마성(최진혁)과 성기준(인피니트 호야)이 점심 먹으러 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2550, 25724, 15744, '11회에서 공마성(최진혁)은 김범수가 3년 전 하이난에서 만난 적이 있는지 묻기 위해 이곳에서 만나자고 하는데, 이를 범수는 부인한다. 13화에서 두 사람은 여기서 다시 만나 이제 마성은 자신이 범수를 봤다고 확신한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2551, 25335, 15744, '공마성(최진혁 분)은 10회에서 자신의 비서이자 운전기사인 양우진(장인섭 분)에 의해 이 다리를 건너게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2552, 27442, 15744, '4회에서 공마성(최진혁)과 주기쁨(송하윤)은 배우 이하임(이주연)의 인조모피 사고 굴욕에서 그녀를 구한 뒤 다투며 이 공원을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2553, 24945, 15744, '11화에서 주기범(송하윤)의 형이 아르바이트를 하는 테이크아웃 식당이 이 거리에 있는데 배달을 마치고 돌아오는 주기범이 그곳을 지나는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2554, 27593, 15744, '10화에서 주기범(송하윤)의 동생 주자랑(강윤제)이 아르바이트를 하는 테이크아웃 치킨집장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2555, 27594, 15744, '14회에서 주기범(송하윤)은 공마성(최진혁)을 이 식당에 데려와 집에서 만든 음식을 먹는 장면이 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2556, 27595, 15744, '3회에서 공마성(최진혁)은 주기범(송하윤)을 따라 이곳을 찾는 장면으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2557, 27596, 15744, '6회에서 주기범(송하윤)은 성기준(호야)의 행방을 알아보기 위해 전화를 받으며 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2558, 27511, 15744, '2회에서 주기범(송하윤)이 서도현(정진영)에게 가는 길로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2559, 27597, 15744, '공마성(최진혁)의 자택으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2560, 27598, 15744, '2,10화에서 송하윤이 주문한 옷을 배달하는 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2561, 25633, 15744, '공마성(최진혁 분)과 주기쁨(송하윤 분)이 7화 마지막에 이곳에서 데이트를 한다. 옷을 다 차려입은 그녀는 계단을 올라가 기다리고 기다리고 또 기다린다. 헛수고다. 계단이 시작되는 길 바로 옆 주차장에 서 있는 마성은 그가 왜 이곳에 왔는지 기억이 나지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2562, 24959, 15744, '15회에서 주기범(송하윤)과 공마성(최진혁)은 데이트를 위해 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2563, 27599, 15744, '15회에서 주기범(송하윤)과 공마성(최진혁)은 데이트를 위해 이 박물관의 공원에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2564, 27600, 15744, '3회에서 섬유시장을 찾은 주지범(송하윤)은 공마성(최진혁)과 함께 이곳에서 버스를 타는 장소로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2565, 27601, 15744, '10회에서 공마성(최진혁)이 사고 전의 기억을 되찾는 곳이 바로 이 갈림길이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2566, 25781, 15744, '5회에서 공마성(최진혁)은 이 곳에서 주기쁨(송하윤)에게 저녁 7시에 만나자고 한다. 도착한 기쁨은 이하임(이주연)과 마주치고, 하임은 기쁨과 마성의 관계(하임의 첫사랑)가 무엇인지 묻고 바로 창피를 준다. 기쁨이 큰 소리로 변명하자 마성도 끼어들어 말대꾸를 하고 자리를 뜬다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2567, 25017, 15744, '1회에서 성기준(인피니트 호야)이 이곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2568, 26044, 15744, '12회에서 성기준(인피니트 호야)의 짝사랑이 끝나고 그와 주기범(송하윤)의 첫 \'데이트\'가 이곳에서 성사됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2569, 27602, 11765, '1화에서 보듯 어린 재훈(김강훈 분)이 가족과 함께 살고 있는 집으로, 이 집 바로 뒤에 위치한 개화12길 일부도 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2570, 26524, 11765, '헤드헌터로도 알려진 한서준(안재욱)이 수감 중인 교도소로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2571, 27603, 14758, '1회에서 패밀리하우스로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2572, 26285, 14758, '조석(이광수)과 최애봉(정소민)은 이곳에서 첫 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2573, 26652, 18151, '2회에서 윤지우/오혜진(한소희)와 최무진(박희순)은 이 장소의 입구에서 만났습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2574, 26710, 18151, '정태주(이학주)는 4화에서 바이어 대표들을 만난 후 이 건물을 떠난다. 그는 그의 차에 올라타고, 직원의 안내로 입구로 와서 계기판 위에 봉투가 있는 것을 발견했다. 5회에서는 전필도(안보현 분)가 도강재(장률 분)에 대해 스태프들을 심문하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2575, 24917, 18151, '8회에서 깡패들을 따돌린 후, 전필도(안보현)는 윤지우/오혜진(한소희)을 이 해변으로 데리고몰아 \'피난처\'를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2576, 25824, 14127, '구해영(김지한)의 건축사무소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2577, 25635, 14127, '남상효(유인아)는 이 빌딩의 아파트에 살고 있습니다. 10회에서 조성겸(남궁민)이 그녀를 데려옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2578, 27605, 14127, '7회에서 조성겸 총지배인(남궁민)이 리조트 운영 방식을 확인하러 온다. 8회에서는 구해영(김지한)과 남상효(유인나)가 가짜 결혼 후 이곳에 방을 예약하고 도망친 신부 정수아(하연주)가 합류하면서 상황은 더욱 복잡해진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2579, 25790, 15126, '온달(씨엔블루 이종현)은 13화에서 송수정(공승연)에게 이 절벽 꼭대기에서 연을 날리는 방법을 보여주고, 결국 키스를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2580, 25773, 4153, '제임스 박(다니엘 헤니)은 이곳에서 미국에 있는 양부모에게 가기 위한 비행기를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2581, 27606, 13422, '이 정원은 드라마에서 이설(김태희)의 어머니가 운영하는 \'햇볕 가득한 펜션\'으로 등장한다. 예를 들어 1화에서는 박해영(송승헌)이 이설을 찾아 이곳에서 그녀를 발견하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2582, 26080, 13422, '1화에서 박해영(송승헌)과 이설(김태희)은 아르바이트를 하던 중 이 공원에서 처음 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2583, 25737, 13422, '15회에서 박해영(송승헌)과 이술(김태희)이 평범한 시민으로서의 마지막 날에 데이트를 하며 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2584, 25723, 13422, '이 건물은 박동재(이순재) 회장의 저택으로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2585, 27226, 13422, '15회에서 박해영(송승헌)과 오윤주(박예진)의 만나는 장면으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2586, 24922, 13422, '11회에서 박해영(송승헌)이 이술(김태희)을 데리고 덕성여중 앞 이 길을 산책했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2587, 27607, 13422, '1회에서 오윤주(박예진 분)는남정우(류수영 분)에게 이 박물관에서의 전시회를 미리 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2588, 25620, 13422, '5회에서 박해영(송승헌)이 옥상정원카페에서 오윤주(박예진)를 만나는 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2589, 25715, 13422, '3회에서 이설(김태희)을 기자들로부터 보호하기 위해 데려오는 장면에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2590, 26651, 13422, '4회에 이설(김태희)이 입주하는 황궁으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2591, 25017, 13422, '3, 4회에서 이설(김태희)은 이 공항에서 이집트로 출국하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2592, 27608, 17759, '이 단지의 내부(도서관 및 실내 수영장)는 효원 맨션의 인테리어의 일부로 나타난다. 예를 들어, 1, 4화에서 볼 수 있듯이  5화에서 외관은 서희수(이보영)가 아들의 가정교사 강자에 대해 자세히 알아보기 위해 방문하는 김 대표의 이연 저택으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2593, 26652, 17759, '12화에서 하진호(박혁권)의 하원맨션으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2594, 26609, 17759, '13회에서 정서현(김서형)과 남편 한진호(박혁권)가 이 식당에서 점심을 먹으며 자신의 비밀과 앞으로의 계획을 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2595, 25948, 17759, '13회에서 한지용(이현욱)의 장례식이 이곳에서 거행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2596, 27609, 17759, '6회에서 8살 하준(정현준)은 학교를 마치고 사라지며 그가 납치됐을 가능성을 의심해 재벌가에서 그를 찾아 난리다. 새엄마 하이수(이보영)는 자신의 휴대전화를 추적해 \'안남아레나\'로 개명한 체육관에 갔다가 자신이 좋아하는 래퍼 우원재의 콘서트에 참석했다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2597, 27610, 17759, '16회에서 서희수(이보영)의 복귀작 \'마인\' 촬영 현장을 방문하는 장면으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2598, 25021, 17759, '이 클럽에서 승마 강습을 받으며 여기에 그의 튜터인 강자경(옥자연)과 동행하는 장면으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2599, 26419, 17759, '수녀 엠마(예수정)와 서희수(이보영)는1회에서 미혼모 재단에 대한 이야기를 나누며 이 건물을 지나간다. 실제로 1화에서는 김유연(정이서)이 심야 산책을 하다 길을 잃었기 때문에 그를 찾아가는 \'젊은 달인\' 한수혁(차학연)이 입주하는 건물이다. 이때도 수혁이가 밤에 방을 바꾸자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2600, 27611, 17759, '13회에서 마더엠마(예수정)의 성경독서단 멤버들이 새 멤버 한진희를 환영하기 위해 이곳에서 커피를 마시는 모습으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2601, 25612, 17759, '최종 16회에서 정서현(김서형)이 수녀 은퇴 후 이 정원에서 일하기 시작한 마더 엠마(예수정)를 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2602, 27612, 17759, '12회에서 한진호(박혁권)와 파이트클럽 제보자 곽수창(길금성)이 버스정류장에서 만나는 장면으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2603, 25917, 17759, '11회에서 박물관으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2604, 24933, 17759, '이 갤러리의 카페는 \'하원갤러리\' 중 하나로 등장한다. 2화 초반, 서희수(이보영 분)가 서진경(송선미 분) 하원갤러리 관장에게 전시 콘셉트를 제안하는 것이 여기에 있다. 11회에서는 마더 엠마(예수정 분)가 이곳에서 성경 읽기 모임을 연다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2605, 24935, 17759, '4회에서 이마리(홍지윤)는 라파엘의 아버지의 뜻에 따라 다이아몬드화장품 대표이사로 부임한 전 남자친구를 이 술집에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2606, 27613, 17759, '11회에서 서희수(이보영 분)가 이 건물에 위치한 아크레도 청담점에서 장신구를 구입하는 장면으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2607, 26037, 17759, '이 건물의 외관은 한지용(이현욱)의 사옥으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2608, 27614, 17759, '4회에서 김유연(정이서)의 집으로 등장하며 한수혁(차학연)이 찾아오는 장소다.');
