INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2968, 26540, 15237, '8회에서 현기(최원영). 김민준(우도환)은 메인 로비로 등장핟나.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2969, 28088, 15237, '3화에 최강우(유지태),온누리(김혜성)는 퇴직한 소방서장을 만나는 장면에 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2970, 27120, 15237, '16회에서는 최강우(유지태)가 10개월 만에 출소하는 장면으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2971, 27530, 15237, '8회에서 김민준(우도환)이 술을 마시러 찾아오고 최강우(유지태)가 합류하면서 지금부터 그 둘은 동업할 것이라 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2972, 25668, 15237, '2회에서 태양화재에서 근무하는 최강우 과장(유지태), 박무신 대리(장혁진), 신입 손해사정사 박재수(윤종석)가 몰래 이곳으로 와 보험 사기꾼 고진철(박성훈)을 사진찍었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2973, 25864, 15237, '3화에서 주현기(최원영)는 차준규(정보석) 회장과 딸 홍수(홍수현)의 방문으로 중단된 비즈니스 미팅을 이곳에서 진행한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2974, 25017, 15237, '6회에서 김민준(우도환)의 동생이 이곳에서 조종사로 일했다(그리고 그의 비행기 추락 사고가 이 드라마의 모든 수사의 근원이다).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2975, 26066, 13402, '정인(김재욱)은 매리(문근영)을 이곳으로 데려온다. 왜냐하면 그녀가 이런 곳을 좋아한다고 했기 때문이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2976, 25661, 13402, '강무결(장근석)이 공차 홍대점 앞을 겨울에 걸었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2977, 28089, 13402, '강무결(장근석)은 이날 방송에서 매니저를 만나 밴드 계약에 대해 논의하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2978, 26095, 13402, '메이리/메리(문근영)와 강무결(장근석)이 자주 찾는 곳이다. 2화에서 그녀는 그를 따라 이곳으로 와서 그가 소수의 관중들을 위해 노래를 연주하는 동안 메리는 정인(김재욱)과 결혼하자는 아빠의 제안을 백일 동안 생각한 후 자신에게 맞는 남자가 누구인지 결정한다. 8화에서 메리와 정인의 약혼식이 있던 날, 무결은 그녀에게 여기서 만나자고 요청하고, 그녀는 그가 다시 군중을 향해 연주하는 것을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2979, 26285, 13402, '변정인(김재욱)은 메리와 결혼하자 6회에서 아버지 정숙(박준규)과 이 식당에서 만난 점심식사 한다. 그리고 직업과 결혼 계획에 대해 심문을 받는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2980, 25797, 13402, '아버지가 택시를 타고 이 다리를 건너고 있는데, 자신이 차비가 부족하다는 것을 눈치채고 다리 위로 내려선다.자살을 시도한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2981, 26184, 13402, '매리(문근영)와 변정인(김재욱)이 만나는 장면에 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2982, 27361, 13402, '매리(문근영)는 메리 외박 해남빌딩 1층에 위치한 홀리 카페 태평로점에서 근무하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2983, 25017, 13402, '1화에서 정인(김재욱)이 아버지와 함께 이곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2984, 25856, 14357, '1회에서 이정주(강소라)는 이 랜드마크 건물 바깥의 사거리에 서서 깊은 생각에 잠겨 있고, 맞은편 굿모닝시티 건물의 LED 스크린에는 제주 광고가 깜박인다. 곧이어 백건우(유연석)가 택시를 타고 그녀를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2985, 25866, 14357, '1화에서 백건우(유연석)가 이정주(강소라)를 공항으로 데려다준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2986, 27287, 14357, '김현우(이상우)가 백건우(유연석)가 운영하는 식당이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2987, 26006, 14357, '8회 마지막 장면에서 이정주(강소라)와 백건우(유연석)가 이곳에서 만나 서로를 껴안았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2988, 25908, 14950, '1회에서 한진아(정인선)가 부모가 살해되던 날 밤의 자세한 내용을 떠올리기 위해 최면을 받는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2989, 28091, 14950, '오성진(박훈)의 옥상 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2990, 24953, 14950, '최종회 4회에서 한진아(정인선)가 강철수(이준혁)를 이곳으로 데려와 식사를 시켰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2991, 25635, 15035, '11회에서 Last Love 팀은 이 카페에서 영화를 준비합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2992, 28092, 15035, '김설우(박해진)가 경호원으로 자신을 소개하는 장면에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2993, 26037, 15035, '이 건물의 외관은 모승재(연정훈)의 사무실이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2994, 25910, 15035, '11회에서 김설우(박해진)는 차도하(김민정)의 아버지 차명석(김)을 이곳에서 만나 술을 마십니다.아버지는 먼저 그들의 관계를 인정하지 않는다는 인상을 주고 나서 그가 설우를 얼마나 아끼는지 설명합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2995, 25804, 15035, '3화에서 여운광(박성웅)의 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2996, 26273, 15035, '11회에서 라스트 러브 팀이 이 교회에서 위치 스카우트를 한다. 그들은 다른 교회에 왔지만, 김설우(박해진)와 차도하(김민정)가 그 교회에 들어가자 한 자매가 그들을 기다리고 있으며 내부에서 그들을 기다리고 있는 미카엘 신부에게 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2997, 28095, 17833, '1회에서 여준(박지훈)이 김소빈(강민아)과 함께 번지점프를 하자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2998, 25787, 17833, '7화에서 남수현(배인혁)은 이 역 3번 출구 앞에서 전단지를 나눠주며 또 다른 아르바이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (2999, 25019, 17833, '3회에서 여준(박지훈)이 김소빈(강민아)을 이 상가로 데리고 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3000, 28096, 17833, '1회에서 지하철을 타고 이 역 4번출구에서 공부하는 대학으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3001, 26689, 17833, '2~3회에서 여준(박지훈)은 형 여준완(나인우)과 함께 찾는 부모님 집으로 이어지는 이 주택 단지 입구에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3002, 28097, 17833, '4회에서 김소빈(강민아)과 결별 후, 여준(박지훈)은 친구의 전화를 받고 이 바에서 파티에 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3003, 25335, 17833, '부모님을 찾아뵙고 돌아온 여준(박지훈)과 형 여준완(나인우)은 3회에서 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3004, 28098, 17833, '1회에서 여준(박지훈)이 동료들과 함께 클럽을 가는 장면으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3005, 27092, 17833, '1회에서 남수현(배인혁)은 이 사거리 앞에서 아르바이트를 하는 장면으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3006, 28099, 17833, '1회에서 명일대학교 경영학과 신입생들이 캠핑장에서 MT를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3007, 28100, 17833, '2회에서 여준은 형 여준완(나인우)에게 데리러 이곳 앞에서 부모님을 모시는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3008, 28101, 17833, '주인공 여준(박지훈) 등 학생인 \'명일대학교\'로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3009, 28102, 17833, '10회에서 김소빈(강민아)은 이 카페에서 엄마(정서연)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3010, 28103, 17833, '멀리서 보면 푸른 봄(KBS2, 2021) 3회에서 여준(박지훈)은 김소빈(강민아)을 이 쇼핑몰에 데려오는 장면으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3011, 25908, 16839, '15-16화에서 이신웅(조성하)이 황필선(이휘향)과 손을 잡고 음모를 꾸미는 장소로 황필선(이휘향)의 사무실로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3012, 25796, 16839, '10회에서 한선미(이세영)가 멀리서 지켜보는 장면으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3013, 26278, 19130, '스피드레이서 차가을(이유미)이 1회 아침 조깅을 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3014, 25795, 19130, '제갈 길(정우)은1화에서 이 공원을 거닐다 스피드레이서 차가을(이유미)이 대회에서 다친 사연을 공개 화면에서 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3015, 26002, 19130, '스피드레이서 차가을(이유미)과 제갈길(정우)이 이곳 아이스링크에서 만나 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3016, 27535, 19130, '1화에서 제갈길(정우)이 이 센터에서 공개토크를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3017, 28105, 18288, '16회에서 지윤수(임수정)의 집으로 등장하며 최시안(신수연)이 자신의 논문을 발표하기 위해 방문하는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3018, 28106, 18288, '16회에서 윤수(임수정)가 수학을 가르치는 초등학교로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3019, 27069, 18288, '2회에서 노정아(진경)가 아버지 노인형(장광)을 만나러 이 집에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3020, 28107, 18288, '7회에서 성예린(우다비)은 이 거리를 건너 전직 교사 한명에게 치일 뻔한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3021, 25019, 18288, '1회에서 성민준 의원(장현성)과 아성고 노정아 이사장(진경)이 이곳에서 만나 골프를 치고 다가오는 수학 올림피아드에 대해 은밀히 토론한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3022, 28108, 18288, '이 건물의 외관은 지윤수(임수정)가 과외선생님으로 일하는 \'태양석 건물\'로 등장한다. 7화와 8화에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3023, 28109, 18288, '13회에서 성예린(우다비)은 지윤수(임수정)에게 이 카페에서 만나자고 했고 백승우(이도현)에게 무슨 일이든지 일어난다면 그녀의 잘못이라고 경고했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3024, 28110, 18288, '8회에서 류성재(최대훈)는 지윤수(임수정)를 따라 이 건물로 찾아가 대화를 시도한다. 9회에서 백승유(이도현)는 오토바이를 몰고 오는 그들을 발견한다. 14회에서 윤수는 최성한 교감(전진기)을 만나 2017년 학력을 증거로 제출하라고 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3025, 28111, 18288, '3회에서 윤수(임수정 분)와 백승유(이도현 분)가 이 식당에서 저녁을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3026, 26199, 18288, '13회에서 지윤수(임수정)와 백승유(이도현)가 이 음악실에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3027, 28112, 18288, '9회에서 백승유(이도현)와 지윤수(임수정)는 이 공원에서 만나 학교 상황에 대처하는 방법에 대해 이야기하는 장면.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3028, 26034, 18288, '4회에서 지윤수(임수정)가 수학 세미나에 함께 참석했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3029, 28113, 18288, '4회에서 2017년 국제 수학자 올림픽\'이 열리는 곳. 성예린(우다비)과 백승유(이도현)가 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3030, 28114, 18288, '3회에서 지윤수(임수정)는 약혼자 앞에서 웨딩드레스를 입는장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3031, 25606, 18288, '노연우(오혜원)는 11화에서 이 호텔 방에서 지윤수(임수정)의 행방을 알게 된 류성재(최대훈 분)와 마주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3032, 28115, 18288, '3회에서 지윤수(임수정)가 웨딩드레스 피팅 약속을 위해 택시를 타고 달려가는 신부집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3033, 28116, 18288, '6회에서 백승유(이도현)는 지윤수를 뒤쫓아 이 거리를 따라 뒤늦게 다음날 결혼을 축하한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3034, 28117, 18288, '6회에서 지윤수(임수정)와 류성재(최대훈)의 결혼식이 이 예배당에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3035, 24902, 18288, '2회에서 성민준(장현성) 의원은 노정아 아성고 교장(진경)을 만나 학생 참여 제한 방안을 논의하자고 제안했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3036, 28118, 18288, '8회에서 백승유(이도현)는 지윤수(임수정)를 이 식당에서 만나기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3037, 26410, 18288, '국회의원 성민준(장현성)은  2화에서 아성고 감독 노정아(진경)를 만나 토론을 벌인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3038, 26085, 18288, '7, 8회에서 보듯 류승재(최대훈)가 비서실장으로 근무하는 교육청 건물로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3039, 26282, 18288, '이 건물은 극중 감독 노정아(진경)가 학부모와 학생들에게 연설하는 \'아성고등학교\'의 일부로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3040, 25924, 18288, '백승유(이도현)는 2화에서 배치 테스트를 하기 위해 UOS 센테니얼 기념관에서 아버지 백민식(김호진)에게 내려준다.3화 초반에 아성고등학교를 대표하기 위한 경쟁을 벌였고, 몇 년 후 7화에서 학교 모임에서 그가 선생님으로 소개되었다. 또한 3화에서는 승유와 선생님 지윤수(임수정 분)가 미술전을 관람한 후 이 홀 옆에 있는 대학 박물관 밖에 모습을 드러낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3041, 26088, 18288, '생방송 TV 인터뷰를 마친 백승유(이도현)와 그의 친구 이현재(이강지)는 7화에서 이 건물을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3042, 26137, 18288, '노정아(진경)는 7회에서 백승유(이도현)를 이 호텔 \'테라스 라운지\'에서 만나 아성수학예술영재학교 멘토로 정식 초청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3043, 25802, 18288, '9회에서 백승유(이도현)를 따라가던 류성재(최대훈)가 승유에게 지윤수(임수정)의 삶에서 벗어날 것을 간청하기 위해 자신의 차를 타고 이 거리를 가로막는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3044, 26515, 18288, '11회에서 지윤수(임수정) 그리고 류성재(최대훈)는 이 다리 위에서 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3045, 25348, 18288, '2회에서 성민준 의원이 운전기사가 있는 차를 타고 이 건물을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3046, 28119, 18288, '5회에서 대통령상을 수상한 후, 성예린(우다비)과 가족들은 이 식당 1층에서 교육자 2명과 함께 기념식을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3047, 25797, 18288, '10화에서 백승유(이도현)와 지윤수(임수정)가 오토바이를 타고 이 다리를 건너간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3048, 26232, 18288, '백승유(이도현)는 2회에서 밤에 혼자 이 다리를 걸으며 수학 방정식이 머릿속과 시야를 계속 채우고 있다. 7화에서 그는 4년 후에 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3049, 28120, 18288, '12회에서 백승유(이도현)는 이 거리를 따라 지윤수(임수정)에게 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3050, 26710, 18288, '8화에서 백승유(이도현)와 함께 아성고등학교 2017 동창들이 이 술집에 모인다. 그는 도착 직후 밖에서 전화를 받고, 그곳에서 성예린(우다비)과 마주친다. 12회에서 승유는 이곳에서 장규영(최우성 분), 이현재(이강지 분), 박형도(이상진 분)와 만나며, 규영은 지윤수(임수정 분)와의 관계에 대해 계속 압박을 가한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3051, 28121, 18288, '11회에서 지윤수(임수정)가 백승유(이도현)의 서점 사인회에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3052, 26407, 18288, '지윤수(임수정 분)는 12화에서 이 식당에서 노연우(오혜원 분)를 만나 다가오는 이사회에서 사용할 주주 정보를 전달한다. 놀랍게도 얼마 지나지 않아 류성재(최대훈 분)가 들어서고, 연우는 두 사람이 결혼한다고 발표해 성재를 안타깝게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3053, 28122, 18288, '9,10회에서 노정아(진경)를 피해 일식당 옥상 공간까지 올라갔다. 정이 옥상으로 다가가자 승유는 윤수를 뒤에서 껴안고 보이지 않게 숨긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3054, 25856, 18288, '백승유(이도현)가 해외 유학 시절의 비극을 지윤수(임수정)에게 들려주는 곳이 바로 여기다. 윤수를 찾던 류성재(최대훈)는 멀리서 울고 있는 승유를 위로하는 그녀의 모습을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3055, 26518, 18288, '1회에서 지윤수(임수정)가 가방을 교환하기 위해 \'한곡아트홀\' 밖에서 백승유(이도현)를 만나기 위해 가로수길을 달리는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3056, 28123, 18288, '1화에서 지윤수(임수정)와 백승유(이도현)는 가방을 교환하기 위해 이 건물(본명 \'한곡아트홀\') 밖에서 만나기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3057, 26188, 18288, '백승유(이도현)가 7회에서 2021년 루시퍼상 수상 관련 토크에 참석한다. 질의응답 시간에 \'캘큘러스\'라는 별명을 가진 누군가의 질문을 받고, 그는 그녀를 지윤수(임수정)로 착각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3058, 28124, 18288, '12회에서 지윤수가 백승유(이도현)를 집으로 데려다 줄 때 그의 아파트로 가는 길로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3059, 26125, 18288, '1회에서 노정아(진경)의 생일을 축하하기 위해 아성고 학생 엄마들의 모임이 이곳에서 열렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3060, 26457, 18288, '4화에서 지윤수(임수정)와 백승유(이도현)가 제주 당일치기 여행으로 이 갤러리를 찾는다. 참고: 갤러리의 외관만 전시되었으며, 이 전시회는 벙커 드 뤼미에르[Bunker de Lumieres]에서 열렸을 것으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3061, 25866, 18288, '4화에서 지윤수(임수정)와 백승유(이도현)는 제주행 비행기를 타기 위해 이 공항에 체크인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3062, 28125, 18288, '4회에서 지윤수(임수정)와 백승유(이도현)가 \'별\' 전시를 관람하기 위해 이 갤러리가 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3063, 25846, 18288, '지윤수(임수정 분)와 백승유(이도현 분), 성예린(우다비 분) 등 주인공들이 수학교사로 일하는 \'아성고등학교\'로 나온다. 1회에서 지윤수(임수정 분)가 익명으로 어려운 시험문제를 푼 백승유(이도현 분)를 찾다가 미술관 옆 헛간에서 자고 있는 그를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3064, 28126, 18288, '7회에서 백승유(이도현)는 이 노인의 집에 교수를 찾아와 지윤수(임수정)를 처음 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3065, 28127, 16366, '감독 이은정(전여빈)은 배우 이소민(이주빈)의 일상을 담은 다큐멘터리를 촬영하고, 6회에서는 여배우가 일에 몰리기 전 시간을 보내는 썰매장으로 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3066, 24935, 16366, '8회에서 황한주(한지은)와 드라마 제작사 대표 이소진(김영아)은 신인 극작가 임진주(천우희), 손 범수 감독(안재홍)과의 회식을 마치고 이 곳에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3067, 28128, 16366, '정혜정 작가님과 JBC 국장님의 단골일식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3068, 25871, 16366, '천우희가 떠나고 이유진 혼자 식사한 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3069, 27515, 16366, '천우희와 한지은이 스테이크 먹은 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3070, 26735, 16366, '12화에서 진주(천우희)가 이 식당 카리노홀에서 언니 지영(백수희)과 유대감을 형성한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3071, 28129, 16366, '9회에서 은정(전여빈)은 고인이 된 남자친구 홍대(홍대)와 함께 식당으로 향하는 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3072, 28130, 16366, '윤지온과 전신환이 쫓겨난 김치찌개 전문점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3073, 28131, 16366, '전여빈과 손석구가 소주 마신 돼지 고깃집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3074, 26247, 16366, '한주(한지은)와 그녀의 전 남편 노승효(이학주)가 이 카페에서 두 번 만난다. 15회에서 승효는 자신이 아파서 가족들을 다시 모으고 싶다고 설명하지만 한주는 이를 거부한다. 그녀는 결국 그를 여러 번 때리려고 하지만 그는 모든 공격을 피합니다. 재훈(공명)이 들어와서 바닥에 주먹을 날리자 곧바로 사과하며 선배가 해결하지 못한 것은 자신의 몫이라고 말한다. 16회에서 승효는 여름 방학을 맞아 아이 인국(설우형)을 제주로 보내기 위해 흥정을 한다. 그는 서울에서 살던 아파트를 제안하기도 하고 한주는 이를 황급히 받아들인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3075, 28132, 16366, '진주-환동 과거 회상 장면에 등장한 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3076, 25882, 16366, '11회에서 손범수(안재홍)가 이 곳에서 임진주(천우희)에게 마음을 고백했다. 그리고 12화에서 그녀는 데이트를 시작하기 위해 여기에서 제안하고 나중에 그에게 키스한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3077, 26666, 16366, '8화에서 진주(천우희)와 범수(안재홍)는 이 술집/레스토랑에서 맥주를 마시며 사람들의 감정과 복잡한 감정에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3078, 28133, 16366, '16회에서 두 주인공이 함께 걷는 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3079, 28134, 16366, '9회에서 은정(전여빈)은 자신과 故 홍대(한준우)가 그의 새 식당을 찾기 위해 이곳을 거닐던 모습을 회상하며 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3080, 28135, 16366, '6회에서 재훈(공명)은 자신과 같은 뮤지컬을 보고 있는 전 여자친구 하윤(미람)을 이 아트센터에서 우연히 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3081, 26044, 16366, '14회에서 진주(천우희), 한주(한지은), 범수(안재홍), 재훈(공명)은 프리 프로덕션 작업을 마치고 휴식을 취하기 위해 이곳으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3082, 28136, 16366, '범수가 가시 바르기 기술을 뽐낸 생선구이 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3083, 28137, 16366, '14회에서 진주(천우희), 한주(한지은), 범수(안재홍), 그리고 재훈(공명)은 월미도 여행을 위해 이 식당에서 해산물 모듬을 삽니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3084, 28138, 15313, '이곳은 예리(진경수)가 전 남자친구의 사진을 바다에 던지러 간 곳이다. 은호(유노윤호)가 예리가 바다 밑으로 떨어져 자살하고 싶다는 생각에 달려온 곳이기도 하다. 이 장면은 3화에서 나왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3085, 26559, 15313, '은호가 예리의 추억여행(모두 소름끼치는 스토커가 관찰)을 따라간 뒤 한예리(경수진)와 유은호(정윤호)가 함께 이곳 앞 해변에 앉았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3086, 24933, 15313, '한예리(경수진)는 2회에서 이 장소에서 진행되는 아트 설치의 일부(겉모습 그대로)인 침대에서 일어난다. 어떻게 여기 왔는지 기억을 못 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3087, 26086, 15313, '이 대학은 드라마에서 \'은하대학교\'로 등장한다. 유은호(윤호)는 이곳에서 심리학을 공부하고 이후 조교로 일한다. 한예리(경수진)에 대해서는 이곳에서 공부하는 것은 물론 심리학과 김주승(최대철) 교수의 조교로도 활동하고 있다. 은호의 첫사랑 선언이 실패한 곳이기도 하다. 이것은 그를 사랑의 전문가로 변화시켰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3088, 25668, 15313, '1회에 차인 지 얼마 안 된 유은호(동방신기 유노윤호)가 이 곳을 찾아오고 한예리(경수진)를 처음 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3089, 27442, 15313, '2회에서 한예리(경수진)와 유은호(동방신기 유노윤호)가 절친의 이별을 돕는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3090, 27329, 15313, '4회에서 유은호(유노윤호)가 한예리를 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3091, 26404, 15313, '1회에서 유은호(정윤호)는 집 앞에서 머리를 맞았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3092, 24922, 15313, '4화에서 한예리(경수진)와 유은호(동방신기 유노윤호)의 데이트 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3093, 27216, 15313, '4회부터 김선호(한주완)의 서점으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3094, 28139, 5719, '명량에서 이순신과 일본 적장의 대결이 펼쳐진 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3095, 25983, 5719, '명량의 촬영지이자 한국을 대표하는 영상지원센터');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3096, 28140, 5719, '명량에서 거북선에 불을 지르는 장면을 촬영한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3097, 28141, 5719, '명량의 촬영지이자 이순신대교가 보이는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3098, 28142, 5719, '명량에서 왜구를 피해 피신하는 동백 숲 전경');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3099, 25005, 5719, '명량 저잣거리, 선착장 전투 장면 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3100, 28143, 5719, '이순신이 명량대첩을 승리로 이끈 역사적 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3101, 28144, 5719, '영화 명량 촬영지이자 명량대첩 기념지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3102, 28145, 5719, '영화 명량과 KBS 드라마 이순신의 세트장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3103, 26249, 15161, '10회에서 허임(김남길)과 오하라(노정의)의 데이트장면에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3104, 25347, 15161, '10회에서 허임(김남길)이 오하라(노정의)와 데이트를 하기 위해 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3105, 25608, 15161, '15회에서 허임(김남길)과 최연경(김아중)은 여기서 헤어진다. 그것은 그들 둘 다에게 상처를 주는 순간이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3106, 25797, 15161, '9회에서 허임(김남길 분)은 순간이동을 한 뒤 이 다리를 걸으며 평생 모욕을 당하거나 굴욕을 당하지 않겠다고 다짐한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3107, 26232, 15161, '2회에서 최연경(김아중)을 병원으로 이송하는 구급차가 이 다리를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3108, 24951, 15161, '1화에서 조선침술사 허임(김남길)이 과거에 화살을 맞고 시냇물에 떠올랐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3109, 26071, 15161, '제1화에서 시간여행 끝에 자신의 진료소 해민서의 유해를 발견했을 때 배경으로 그려진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3110, 28146, 15161, '7회에서 유재하(유민규)와 최연경(김아중)이 저녁식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3111, 25955, 15161, '9회에서 허임(김남길)의 첫 개인 고객이 사는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3112, 25894, 15161, '15회에서 허임(김남길)과 최연경(김아중)이 케이블카를 타고 남산타워에 간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3113, 25912, 15161, '9화에서 2017년으로 순간이동할 때, 허임(김남길 분)과 최연경(김아중 분)이 이곳에 도착한다. 마지막 16화에서 허임이 다시 이곳에 도착하는데 연경이가 바로 그곳에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3114, 25637, 15161, '11회에서 신재이(이유영)는 살인 용의자에게 습격을 받아 이곳에 입소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3115, 28147, 12574, '명성황후(문근영)의 어린 시절 장면에서 두드러지게 등장하는 곳 이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3116, 26080, 12574, '공원 전체에서 촬영된 장면 중 고종(박상훈)과 민자영(미래의 명성황후)의 결혼식장면.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3117, 25945, 12574, '이 곳은에서 처음에는 고종의 후궁 이씨의 개인 공간으로, 다음에는 건청궁(建淸宮) 고종과 명성황후의 개인공간으로 사용되었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3118, 25345, 9447, '구신득(진지희)과 그녀의 하녀 영이(김시은)가 이 거리를 걷는다. 영이가 붙잡힌 \'종로경찰서\' 건물도 이곳에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3119, 24957, 9447, '구신득(진지희)과 시녀 영이(김시은)가 다니는 학교 건물이다. 남우진(윤지온)은 선생님이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3120, 28385, 17695, '김도기(이제훈 분)의 어머니가 조문을 오는 마지막 16회에서 보듯 영면하는 곳이다. 틈새시장 바로 밖에서 장성철(김의성)이 그를 기다리고, 그들은 그 후 산책을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3121, 28386, 17695, '1회에서 살인범 조도철(조현우)이 언론 스크럼 속에서 이 감옥에서 풀려나 김도기(이제훈)이 운전하는 모범택시에 탑승한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3122, 25908, 17695, '13회에서 백성미(차지연)를 집으로 데려오는 회상장면 속 한 회장(박근형)의 거실로 등장한다. 이곳에서 그가 그녀를 돌봐줄 것이라고 안심시킨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3123, 27510, 17695, '13회에서 무지개모형택시팀은 이 다리 위에서 백성미 회장(차지연) 일행과 만나 범인을 구영태(이호철)와 맞바꾼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3124, 25876, 17695, '2회 회상장면에서 장성철(김의성)은 이 식당에서 김도기(이제훈)를 만나고, 선배는 도기를 \'레인보우 디럭스 택시\' 승무원이 되어 타인을 대신해 복수할 것을 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3125, 25335, 17695, '1화 오프닝 장면에서 김도기(이제훈 분)가 이 다리 위를 질주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3126, 25854, 17695, '최종회 16회에서 김도기는 이곳에서 강한나 검사(이솜)를 만나 자신의 행위를 구속해달라고 요청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3127, 26139, 17695, '안고은(표예진)의 아파트 출입구와 김도기(이제훈)의 옥탑방으로 보인다. 그들은 드라마에서 서로 다른 장소에 있어야 한다는 것을 명심하세요.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3128, 26232, 17695, '강마리아(조인)는 1화에서 이 다리에서 뛰어내려 생을 마감할 생각을 한다. 가장자리에 서 있는 그녀는 맨 아래 난간에서 \'모범택시\' 번호를 발견한다. 이후, 그녀는 김도기(이제훈)에게 픽업되어 그들이 다리를 건너면서 그녀의 이야기를 들려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3129, 24913, 17695, '7회에서 안고은(표예진)은 언니 정은(표예진)과 함께 저녁 산책을 온 뒤 다음날 목숨을 끊는다. 만약 당신이 곤경에 처해있고 귀를 기울이는 것이 필요하다면, 이 국제 자원 목록에서 핫라인을 찾으세요.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3130, 26189, 17695, '7회에서 김도기(이제훈)가 유데이타 회장 박양진(백현진)을 만나기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3131, 25864, 17695, '12회에서 백성미 회장(차지연)이 비서 구석태(이호철)를 만나러 오면서 이 호텔을 노리지만 나중에 이들의 주의를 분산시키기 위한 계략이었음을 알게 된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3132, 25809, 17695, '11회에서 구영태(이호철)가 이 감옥에서 풀려난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3133, 28387, 17079, '모범형사에서 손현주가 방문한 일산의 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3134, 26537, 17079, '모범형사에 나온 분위기 있는 파주 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3135, 28388, 17079, '모범형사를 촬영한 대전의 순대국집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3136, 26314, 17079, '모범형사에서 추격씬을 찍은 거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3137, 27522, 17079, '모범형사에서 장승조와 이하은이 방문한 찜질방');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3138, 28389, 17079, '모범형사에서 양현민이 이야기를 나눈 이자카야');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3139, 28390, 17079, '모범형사에서 이하은이 미술도구를 사려고 간 거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3140, 25610, 17079, '모범형사에서 이하은을 찾기 위해 찾은 놀이동산');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3141, 27223, 17079, '모범형사에서 장승조와 이엘리야가 데이트한 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3142, 27104, 17776, '2회에서 이소현(김환희)은 이 역에서 소매치기를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3143, 26650, 17776, '2회에서 이 거리를 걷는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3144, 28391, 17776, '1회에서 이소현(김환희)과 조윤호(김도훈)가 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3145, 25799, 17776, '이 집은 이재영(류수영)이 운영하는 \'해피 프라이드 치킨\'집으로 등장하며 조윤호가 일하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3146, 27222, 17776, '1회에서 이재영(류수영)과 이소현(김환희)이 모두 이곳에서 버스를 탔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3147, 26521, 5615, '오수연(이열음)의 형이 치료를 받는 병원과 강기탄(이기광)과 수연이 처음 만났던 병원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3148, 26456, 5615, '젊은 강기탄(이기광)이 사는 곳으로 나오는 장소이다. 드라마 초반에 마지막에 어른이 된 그는 그 곳을 다시 구매해 여기서 다시 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3149, 28402, 13056, '6회에서 부모님을 방문하기 전 강용기(권상우)는 나인정(이요원)을 이 쇼핑몰에 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3150, 26744, 13056, '1회에서 나인정(이요원)이 이수환(김성수)과 헤어질 때, 그녀는 완전히 지쳐 이 다리의 발치에 다다른다. 그녀를 따르던 강용기(권상우 분)가 그녀를 격려하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3151, 25882, 13056, '15회에서 나인정(이요원)과 이수환(김성수)이 이 공원에서 담소를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3152, 27361, 13056, '강용기(권상우)와 나인정(이요원)은 부모님을 뵈러 가는 길에 광화문 광장에 있는 이순신 장군 동상의 정상에서 차를 몰고 이 거리를 내려간다. 그가 그들의 미래와 그들이 얼마나 많은 아이를 낳을지에 대해 과장하는 동안, 그녀는 그가 자신에게 너무 좋다고 혼자 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3153, 26312, 13056, '이 건물은 5화에서 \'대한 건설회사\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3154, 25736, 13056, '1회(김성수)에서 장인 강우택(박근형)과 함께 이 호텔을 개업하고, 같은 회에 나인정(이요원)이 하룻밤을 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3155, 26064, 13056, '1회에서 승진에 실패해 실망한 이수환(김성수)과 나인정(이요원)은 제주로 여행을 가다가 우연히 이 공원에서 만난디.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3156, 26166, 14576, '윤시우(이현우)와 왕치앙 (VIXX 홍빈)가 등장하는 본 학교다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3157, 26253, 15561, '1회에서 봉상필(이준기)은 안개가 자욱한 날 동료와 함께 이 다리를 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3158, 28403, 15561, '봉상필(이준기 분)은 1회에서 이곳에서 훈련을 받고, 세션을 마치면 소포를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3159, 26188, 15561, '15회에서 차문숙의 책 출간 파티가 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3160, 28404, 15561, '1회에서 봉상필(이준기)이 이 건물 5층에 자신의 로펌 \'무법변호사\'를 차리는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3161, 26044, 15561, '4회에서 하재이(서예지)와 봉상필(이준기)이 해변을 걷고 있었다. 그녀의 어머니에 대해 상필은 자신이 비슷한 일을 겪었다는 사실을 알려줌으로써 (자세히 말하지 않고) 그녀에게 마음을 열기 시작합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3162, 25719, 13858, '김현수(윤현민)의 회사가 있는 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3163, 26744, 13858, '1화에서 김현수(윤현민)와 정시현(정경호)은 이 다리 밑에서 만나 \'할리벗 형님\'이 체포돼 해결책을 찾아야 한다고 파트너에게 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3164, 28411, 13858, '1회에서 윤수민(남규리)과 의사의 아들(정경호)은 의사의 아들이 데려간 후 처음으로 이곳에서 교차한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3165, 25896, 13858, '1화에서 정시현(정경호)은 이곳으로 잠시 쉬러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3166, 26028, 12805, '555회 유재석이 청소한 엄청난 높이의 출렁다리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3167, 25980, 12805, '526회 조세호가 묵언 수행 체험을 했던 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3168, 26066, 12805, '24회 가을소풍 박명수 딱따구리가 탄생한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3169, 28412, 12805, '368회 무도 탐정 사무소 7인의 탐정 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3170, 28413, 12805, '253회 유재석과 이적의 음악여행 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3171, 28414, 12805, '253회 서해안가요제 정재형이 피아노 연주한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3172, 25682, 12805, '459회 무도공개수배 변장용 옷을 찾으러 간 마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3173, 24935, 12805, '499회 신들의 전쟁 특집 촬영한 강남 라운지 바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3174, 25321, 12805, '320회 택시기사 체험 중 돼지불백 먹은 기사식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3175, 28415, 12805, '399회 언어생활 몰래카메라를 진행한 점심식사장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3176, 28416, 12805, '363회 만약에 특집 송은이-길 데이트 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3177, 28417, 12805, '533회 배우 김수현과 볼링대결을 한 볼링장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3178, 26311, 12805, '271회 별주부전 특집 추격전 촬영 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3179, 26154, 12805, '348회 100 빡빡이의 습격 촬영 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3180, 28418, 12805, '414회 끝까지 간다 특집에서 돈가스를 먹은 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3181, 24984, 12805, '150회 유재석이 마라도에서 짜장면을 먹은 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3182, 28419, 12805, '534회 이효리와 요가 클래스를 진행한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3183, 28420, 12805, '534회 이효리를 만난 제주 오션뷰 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3184, 25734, 18918, '1화 오프닝 장면에서 남한준(서인국)이 이 다리를 타고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3185, 26992, 18918, '2회 에필로그에서는 경찰 첫날 1번 게이트 밖 버스정류장 근처에 서 있던 남한준(서인국 분)의 플래시백을 본다. 그는 한재희(김민서)가 이별 선물을 건네고, 그가 그의 당시 여자친구(루시)와 함께 달아나기 전에 다가온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3186, 25781, 18918, '이 펜트하우스는 공수철(곽시양)의 아파트로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3187, 25607, 13259, '고미녀(박신혜)가 3화에서 아침 조깅을 하던 황태경(장근석)을 멈추게 하고 자신의 가장 소중한 소지품인 반지를 그에게 선물한다. 그는 반지를 가져다가 그들 뒤에 있는 인공 호수에 던져서 미녀가 밤새 그것을 뒤지게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3188, 27178, 13259, '3회에서 황태경(장근석)이 이 타워를 지나 운전하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3189, 28421, 13259, '3회에서 고미녀/젬마(박신혜)는 어머니를 닮은 누군가가 이 역으로 걸어 들어가는 것을 보고 그녀를 따라 13번 출구로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3190, 28422, 13259, '1회에서 젬마가 이 교회에서 기도하는 고미녀/젬마(박신혜 분)가 사는 수도원 중 하나로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3191, 28423, 14694, '19회에서 공심(걸스데이 민아)이 이 카페 앞에 서 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3192, 25635, 14694, '이 장소는 석준수(온주완)이 운영하는 레스토랑입니다. 개업식을 위해 안단태(남궁민)와 그의 가족이 찾아옵니다. 반대로 공심의 누나는 초대를 받아도 단태를 보고는 감히 들어가지 못합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3193, 25773, 14694, '9회(방민아)에서 공심(여아)은 이곳에서 제주도로 날아가고, 석준수(온주완)는 그녀를 배웅하기 위해 공항에서 대기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3194, 26744, 14694, '안단태(남궁민 분)는 9화에서 혼자 이 장소에 와 생각에 잠긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3195, 26697, 14694, '드라마에서 스타그룹 본관으로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3196, 28424, 14694, '5화 회상장면에서 어린 석준표(남궁민)가 이 장소의 에메랄드 홀에서 길을 잃거나 납치된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3197, 28425, 14199, '4회에서 이강준(정겨운)과 교채연(왕지혜)이 이 곳에서 비밀리에 재혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3198, 27057, 14199, '드라마 주인공인 한태희(주상욱) 할머니인 박만덕(김용림)의 집으로 등장한다.14회 등 할머니가 주로 전원생활을 즐길때 나오는 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3199, 26100, 14199, '극 중 위너그룹 HQ로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3200, 25957, 14199, '1회에서 사금란(한예슬)이 이 상가를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3201, 26030, 18904, '10회에서 김마샤 대표(황보라)가 이곳에서 취재진을 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3202, 28426, 18904, '제5화에서 루이스(강찬희)를 심문하는 \'국세청\'으로 본 대학의 본부 건물이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3203, 26200, 18904, '2회에서는 마샤 김(황보라 분) 대표가 민시우(김영균)와 함께 오고, 최종회 14회에서는 루이스(강찬희), 이수린(강민아 분)과 함께한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3204, 25953, 18904, '4회, 5회에서 루이스(SF9 찬희/찬희)가 이 방에 머문다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3205, 25659, 18904, '5화에서 루이스(강찬희)는 이 공원을 따라 걸으며 삼성SDS 상암데이터센터의 화면에서 이 탈세에 대한 뉴스를 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3206, 28427, 18904, '4회에서 루이스(SF9 찬희/찬희)가 차오 엔터테인먼트로 향하는 길에 이 다리를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3207, 25954, 18904, '1화에서 호텔에서 탈출한 후, 루이스 (찬희)는 이 거리를 따라 택시에서 내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3208, 26319, 18904, '건물 꼭대기에 있는 공공 스크린 3화에서 루이스(강찬희)에 대한 뉴스가 이 화면을 통해 방송된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3209, 26214, 18904, '2회에서 민시우(SF9 김영균/휘영)가 이수린(강민아)을 자전거에 태우고 루이스(SF9 강찬희/찬희)의 팬사인회를 가기 위해 이 골목길을 달렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3210, 24922, 18904, '1화에서 호텔에서 탈출해 택시를 타고 이곳에 도착한 루이스(차니, SF9)는 경호원들에게 쫓기며 이 거리에서 몇도 못 버텼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3211, 26186, 18904, '이수린(강민아)은 2화에서 자전거를 되찾기 위해 루이스(강찬희)를 따라 이 광장으로 온다. 그는 그녀가 그의 팬이라는 것을 알게 되지만, 그가 그녀에게 그가 누구인지 말하려고 할 때, 그녀는 그가 그녀의 \'오빠\'와 절대 경쟁할 수 없다는 것을 그에게 알려준다. 그들은 그녀가 곧 데뷔하고 싶다고 그에게 말함에 따라 이 광장에서 춤 동작을 연습하며 저녁을 보내게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3212, 25017, 18904, '1회에서 이소린(강민아)은 좋아하는 아티스트 루이스(SF9 찬희)가 공항에 도착하는 것을 보고 푹 빠졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3213, 28428, 13935, '1회에서 나미래(윤은혜)와 김신(이동건)은 이 골프장 교회에서 벌어지는 일을 다룬다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3214, 26161, 13935, '1회에서 나미래(윤은혜)는 소속사 노래 대회 1등으로 제주도 여행에 당첨되었고 이 호텔에 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3215, 28429, 14173, '임성민이 전 직장선배에게 도움을 준 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3216, 28430, 14173, '15회에서 장그래(임시완)와 장백기(강하늘)는 10만원 미션을 위해 어떤 아이템을 사고 팔지 결정하기 위해 10번 출구 앞에 서 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3217, 28431, 14173, '7회에서 영업팀 3팀이 술을 마시러 이 식당에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3218, 25929, 14173, '13회에서 장백기(강하늘)가 강해준(오민석)에게 밤에 술 한잔 하자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3219, 25879, 14173, '12회에서 장백기(강하늘)는 안영이(강소라)와 술을 마신 후 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3220, 25668, 14173, '14회에서 장그래(임시완)는 집에 오는 시끄러운 친척을 피하기 위해 편안한 곳으로 가라는 어머니(성병숙)의 권유로 설날 아침 이 다리를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3221, 28432, 14173, '5회에서 장그래(임시완)는 B/L 문제를 해결한 후 이 식당에서 술을 마시러 나가자고 오과장(이성민)으로부터 뜻밖의 초대를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3222, 24948, 14173, '8화 오프닝 장면에서 장그래(임시완)가 이곳에서 아침 조깅을 하는 모습이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3223, 28433, 14173, '13회에서 전날 저녁에 혼자 술을 마신 장백기(강하늘)는 다음날 늦게 눈을 뜨고 일에 늦지 않기 위해 택시를 부르기 위해 이 거리를 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3224, 27511, 14173, '4회에서 장그래(임시완)이 이 역 4번 출구로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3225, 28434, 14173, '임시완의 동기들이 함께 숙박하며 일을 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3226, 24913, 14173, '14회에서 소개팅을 한 김동식(김대명)은 이 산책로를 산책하다가 상대방에게 거절당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3227, 28435, 14173, '미생에서 나오는 회사 건물');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3228, 27110, 15560, '3회에 박차오름(고아라)과 임바른(L 인피니트, 김명수)이 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3229, 25613, 15560, '10화에서 보고왕(류덕환)은 이도연(이엘리야)과 데이트를 한다. 뒤늦게 달려온 그는 식당에 도착한 도연에게 식당에 도착한 순간 한 문장만 남기고 자신이 가는 모든 장애물을 가까스로 이겨내고 도연에게 직접 떠날 정도로 상처를 준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3230, 28436, 15560, '박차오름(고아라)과 임바른(김명수)이 출근길에 내리는 역이다. 1회에서 고등학교 동창생이 되어 성인이 된 두 사람이 처음 만나는 곳이다. 4회에서 둘은 이 역 11번 출구 밖에서 전단을 나눠주는 아줌마들을 돕는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3231, 25796, 15560, '차오름(고아라)과 임바른(김명수)이 이곳에서 판사로 활동하고 있는데 동관 앞에서 자주 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3232, 26039, 15560, '9회에서 결혼식에 참석하기 위해 방문하다 민용준(2018)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3233, 25910, 15560, '5회에서 성공충(차순배)은 자신이 여성 판사에게 과로한 혐의를 왜곡하기 위해 이곳에서 기자들과 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3234, 24951, 15560, '10회에서 저녁 공기를 쐬며 바른은 오름에게 그녀의 잘못을 지지하고 싶다고 말하고, 그들은노한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3235, 25016, 15560, '12회에서 사건을 접하고 사건의 가해자인 어머니가 스스로 목숨을 끊을 때까지 어머니와 동거를 했다는 사실을 알게 된다. 술에 취했을 때 훨씬 더 폭력적이 된다. 13회에서 보미를 지키려던 경찰에 의해 발각된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3236, 26152, 12896, '윤현서(안재욱)와 최영인(이보영)은 드라마에서 이 호텔에서 호텔리어로 일한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3237, 25831, 16312, '이 고등학교는 드라마 주인공인 변호사 기무혁(윤균상)이 살인사건으로 인해 학교 교사로 위장취업을 하는 \'춘명고\'로 등장한다. 매 회 드라마에 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3238, 26085, 16312, '3회에서 임시 교사로 잠복한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3239, 25662, 16312, '6화에서 기무혁(윤균상)은 이 카페에서 전 동료 신혜수(김예원)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3240, 25884, 16312, '1화에서 기무혁(윤균상)은 동료 변호사이자 동창생을 만나 이 곳에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3241, 27000, 16312, '11회에서 거짓말의 수업 임시 교사로 잠복한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3242, 28425, 14206, '달팽이를 먹으며 정이건(정석원)은 여전히 아무것도 기억하지 못한다고 확인하고 홍지윤(박예진)에게 듣고 싶지 않은 약혼에 대해 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3243, 28437, 15642, '6화 황매산성 근처. 이 산에서 부모님의 묘를 찾기 위해 노력하는 유진 최(이병헌). 그와 함께 슬퍼하기 위해 찾아온 장승구(최무성)를 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3244, 28438, 15642, '고애신이 총포술을 연습하던 갈대숲');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3245, 25345, 15642, '고애신이 열차 안에서 일본군에게 총을 겨눴던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3246, 28439, 15642, '어린 유진이 추노꾼들을 피해 달아나는 장면의 숲');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3247, 28440, 15642, '6회에서 유진은 도예가 은산의 집에서 기다리며 새로운 적 희성을 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3248, 24993, 15642, '2화에서 최유진(이병헌)은 이 정자 맞은편 강둑에 찾아와 어린 시절 인도교에서 배를 타고 숨었던 모습을 회상한다. 그리고는 가까운 식당에 앉아 백숙을 대접받는다. 10화에서는 고애신(김태리)과 유진 최(이병헌)가 이곳 앞의 얼어붙은 강을 산책하며 조선시대의 기원과 그가 북아메리카로 도피한 이유를 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3249, 25848, 15642, '16회에서 이 캠퍼스의 본관이 등장했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3250, 26539, 15642, '화와 11화에 등장하는 여러 건물 성서캠퍼스의 전통마을 \'한학촌\'이다. 그 중 하나가 경천당(경천당/敬天堂)이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3251, 28441, 15642, '고애신이 \'잉글리시\'를 배우겠다 찾아갔던 목화학당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3252, 24956, 15642, '미국인 \'로건 테일러\'가 암살당한 기생집 화월루');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3253, 28442, 15642, '5화에서 일본군이 일본군에게 피해를 준 미군병을 붙잡기 위해 행사장 밖으로 보이는 미 의회로 행진하는 일본군. 양 당사자의 통역이 일련의 언어 번역을 한 후, 츠다(이정현)는 교전을 선언하고 두 세트의 군인은 서로 무기를 겨누고 있습니다. 유진 최(이병헌)는 일본에게 미국에 선전포고를 했는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3254, 28443, 15642, '총 분실로 고애신이 미군에게 모욕을 당한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3255, 26073, 15642, '가면의 주인을 확인할 때의 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3256, 28444, 15642, 'SBS에서 제작한 미스터 션샤인의 세트장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3257, 25677, 15642, '유진 최(이병헌)와 김희성(변요한)의 공식 행선지이자 거주지이자 사나운 쿠도 히나(김민정)가 소유한 장엄한 글로리 호텔이 있다. 두 번째는 구동매(유연석)가 어렸을 때부터 고애신(김태리)을 처음 본 블랑제리가 있는 장터다. 또한 미국 장관이 암살된 밤에 유진 최와 고애신이 처음 만나는 다리도 등장한다. 세 남자가 만나 술을 마시는 일본식 술집인 활룰루, 두 명의 노예 사냥꾼이 운영하는 전당포, 고애신의 집, 한성 최초의 철도 차량(서울 옛 이름) 등이 등장하는 장면들 등이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3258, 26654, 15642, '2화에서 유진초이(이병헌)의 임시 사무실인 미국 대사관으로 등장하는 곳으로 고애신(김태리)을 불러 심문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3259, 24933, 15427, '강태욱(지진희)은  5화에서 이곳에서 어머니와 만난다. 그녀는 그가 그의 아내에 대해 험담하는 그의 뒤의 테이블에 있는 두 남자에게만 귀를 기울이는 동안 그가 마침내 그의 아내와 헤어지도록 설득하고 싶어한다. 그는 아내 옆에 서겠다고 결심하면서 그 중 한 명을 치고 카페를 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3260, 28445, 15427, '마지막 16회에서 강태욱(지진희)의 친정집으로 등장한다. 그리고 아버지에게 그가 누군가를 죽였다고 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3261, 26273, 15427, '3화 회상장면에서 고혜란(김남주)과 강태욱(지진희)은 이 교회에서 결혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3262, 26125, 15427, '1회에서 고혜란(김남주)이 공로상을 수상한 \'2017 대한민국 방송대상\'이 이곳에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3263, 26524, 15427, '5회에서 하명우(임태경)가 출소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3264, 26171, 17155, '9회에서 다른 곳에서 금전을 날려버린 뒤, 제보자 황두철(박노식)을 형사 신준호(하준)와 이 쇼핑몰 밖에서 만남을 갖는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3265, 24934, 17155, '9회에서 형사 신준호(하준)는최여나(서은수)의 행방을 파악하기 위해 이곳에서 정보원 겸 사기꾼 황두철(박노식)을 만나기로 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3266, 25678, 3899, '3회에서 최윤(정경호)은 이곳에서 콘서트를 열었고 송은채(임수정)는 따라왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3267, 28446, 3899, '미안하다 사랑한다에서 소지섭이 아침운동 하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3268, 28447, 3899, '오들희(이혜영)가 아들 최윤(정경호)과 함께 이 저택에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3269, 26132, 3899, '14회에서 최윤(정경호)은 이 병원 별관[서울성모병원별관]에서 치료를 받고 차무혁(소지섭)은 들른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3270, 28448, 3899, '14회에서 차무혁(소지섭)은 고가도로에서 송은채(임수정)에게 전화를 걸어 다음과 같이 말한다. \'건강해요, 좋아해요\' 그녀를 그리워하던 그는 갑자기 그녀를 찾으러 달려갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3271, 27308, 3899, '4회에서 여의도종합상가 앞 5번출구에서 본 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3272, 25335, 3899, '4화에서 최윤(정경호)이 차무혁(소지섭)과 수영을 하기 위해 이 다리를 건너고, 두 사람은 수영을 마치고 돌아와 다시 다리를 건너 운전석에 앉은 무현을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3273, 26160, 3899, '15회에서 병들어 죽어가는 차무혁(소지섭)은 어떻게든 그를 찾아오는 송은채(임수정)에게 부담을 주지 않기 위해 그를 도와주는 문지영(최여진)의 도움으로 이 리조트로 피신한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3274, 25988, 3899, '5화에서는 가수 듀오 최윤(정경호)이 등장한다. 최윤과 오들희(이혜영)가 이곳에서 화보 촬영을 하고 있다. 윤의 신임 로드 매니저 차무혁(소지섭)과 윤의 패션 코디네이터 송은채(임수정)도 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3275, 28449, 14775, '김희철이 독립 후 아르바이트를 했던 고깃집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3276, 27399, 14775, '김종국이 안양 살던 시절 자주 간 설렁탕집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3277, 28450, 14775, '미운 우리 새끼 158회 중 이상민이 방문한 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3278, 28451, 14775, '김희철, 정준하, 소유가 방문한 강남 닭발집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3279, 28452, 14775, '김희철, 정준하, 소유가 방문한 강남 고깃집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3280, 28453, 14775, '이상민과 탁재훈이 방문한 압구정 브라질 식당');
