INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3282, 28454, 14775, '홍진영, 홍선영 자매가 방문한 고급 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3283, 28455, 14775, '정준하가 운영하는 강남 닭꼬치 가게');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3284, 28456, 14775, '이상민과 김보성이 운동 후 다녀온 족발집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3285, 28457, 14775, '홍진영, 홍선영 자매가 방문한 감자탕집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3286, 28458, 14775, '"살아있네~" 배정남이 감탄한 동묘 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3287, 28459, 14775, '미운우리새끼 164회 곱창떡볶이');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3288, 28460, 14775, '김종국이 춘뚱형제와 방문한 돈가스집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3289, 28461, 14775, '김희철, 이상민, 김영철 남매가 방문한 꽃게집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3290, 28462, 14775, '김종국이 백령도 냉면투어때 첫번째로 간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3291, 28463, 14775, '김종국이 춘뚱투어에서 방문한 삼치회집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3292, 28464, 14775, '미운 우리 새끼 158회, 이상민이 방문한 중국집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3293, 28465, 14775, '김종국이 방문한 논산 젓갈 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3294, 25599, 14775, '김종국이 춘뚱투어로 가다가 방문한 횟집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3295, 28466, 14775, '미운 우리 새끼 188회에서 김강훈이 방문한 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3296, 25796, 15302, '1화에서 홍석표(이성열)는 이곳에서 재판을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3297, 24939, 15562, '1화에서는 래완이 사우나를 방문하기 위해 이 거리를 걷고, 3화에서는 술에 취한 한은성(이유영)이 프랑스 노래를 부르며 래완의 집으로 걸어간다. 그녀가 신발을 잃어버린 곳이기도 하다. 4화에서 래완과 은성은 모두 이 거리에서 의견이 맞지 않아 1년 내내 말을 하지 못하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3298, 28467, 15562, '김래완(김선호)의 단골 술집이다. 그의 아티스트 선배인 현지(박효주)가 운영한다. 이곳에서 아르바이트생 서정(권도운)과 대화를 나누며 잠시 데이트를 하게 됐다. 4화에서 한은성(이유영)은 래완과의 대화를 끊은 지 1년 만에 이곳에 와서 그의 개인전을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3299, 25944, 15562, '4화에서 김래완(김선호)이 한은성(이유영)의 당시 남자친구 현성을 처음 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3300, 28468, 15562, '2회에서 김래완(김선호)과 한은성(이유영)은 집 밖에서 나무에 달린 살구를 따려고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3301, 26188, 15562, '김래완(김선호)은 4회에서 한은성(이유영)이 프랑스 감독을 위해 통역하는 것을 보기 위해 이곳에 온다. 그는 그녀를 보면서 스케치한다. 나중에 그는 그녀에게 다가가려 하지만 그는 희남(성주)과의 전화 통화를 엿듣는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3302, 28475, 4102, '밀양 영화 속 전도연이 다니던 교회');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3303, 28476, 4102, '전도연이 송강호에게 속물이라 놀리는 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3304, 28477, 4102, '신도들의 집회 장소로 나온 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3305, 28478, 4102, '밀양 영화 세트장으로 송강호, 전도연방 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3306, 25918, 4102, '전도연이 교회 신도들과 함께 노래 선교를 하던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3307, 26700, 14025, '12, 13회에서 나오는 대학교 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3308, 28479, 14025, '2014년 JTBC \'밀회\' 13회에서 오혜원(김희애)은 이선재(유아인)에게 친구 집에서 사적으로 만나자고 한다. 그녀가 당분간 그와 거리를 둘 것이라고 그에게 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3309, 28480, 14025, '이곳은 \'밀회\'에서 이선재(유아인)가 사는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3310, 28481, 17830, '1회 촬영지로 나왔던 복합 대여공간');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3311, 26080, 16974, '고종(박상훈)과 민자영(훗날 명성황후)의 결혼식이 이 공원의 궁궐 일대에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3312, 25601, 13153, '드라마 홍보 포스터를 촬영한 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3313, 26015, 13153, '드라마에 등장하는 화원의 일부로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3314, 28482, 9743, '1회에서 이수진(김하늘)이 이 카페에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3315, 25641, 9743, '1화에서 이수진(김하늘)이 이 식당을 찾아오고 한참을 기다리다 일어나 남편 권도훈(감우성)에게 전화로 바람을 피우겠다고 알린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3316, 25906, 9743, '3회에서 권도훈(감우성 분)이 장례식에 참석하고 브라이언 정(김성철 분)과 조수 손예림(김가은 분)이 운전사로 변신해 마지막 테스트를 하는 \'소월장례식장\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3317, 28483, 9743, '1화에서 이 건물은 이수진(김하늘)이 개관식을 위해 방문하는 브라이언 정(김성철)이 운영하는 미디어 회사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3318, 25910, 9743, '4회에서 성당을 나온 후 권도훈(감우성)과 최항서(이준혁)는 항서와 함께 도훈을 설득해 가족들에게 자신의 알츠하이머병을 알리려고 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3319, 28484, 9743, '이곳은 이수진(김하늘)이 생각해낸 화장으로 \'다른 여자\'로 변장한 유정과 \'불륜\'으로 이혼을 꾀하는 극장. 남편 권도훈(감우성)을 만나 열애 한 달 만에 불륜을 시작하게 된 것. 바람이 분다 5회에서 그는 이날 현장으로 달려갔다. 그러나 그녀는 그를 만나지 않기로 하고 맞은편(13시 20분경) 식당 소바의 온도 위층에서 지켜보고 있다. 그들은 각자의 길을 갑니다. 두 사람은 이혼하고 5년 후 같은 연극 \'도둑들 이야기\'를 공연하던 중 우연히 이곳(8화)에서 그를 만나지만 그는 현재 치매에 걸려 처음에는 그녀를 알아보지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3320, 28485, 9743, '8회에서 이수진(김하늘)이 역 3번 출구로 나와 자신의 차가 어떻게 견인되는지 확인한다. 길을 건너다 우연히 이혼 직후 전남편 권도훈(감우성)을 만난다. 간단히 인사를 나누고 이 역 2번 출구로 나와 눈물을 흘린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3321, 26273, 9743, '2회에서 권도훈(감우성)은 이 교회에 찾아와 동행하는 절친 최항서(이준혁)를 안타까워 1시간 넘게 기도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3322, 28486, 17439, '강여주(조여정)의 \'오싹한 북콘서트\' 후 회식이 이 야외 식당에서 진행된다. 강여주는 그녀의 \'구원자\' 차수호(김영대)와 마주 앉아 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3323, 25908, 17439, '8회에서 마동균(오민석) 국정원장이 배일에 싸여있는 자신의 상사와 만남을 갖는 장면에서 이 곳이 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3324, 24900, 17439, '강여주(조여정)가 1회에서 이곳에서 \'오싹한 책 콘서트\'를 선보인다. 안티들에게 습격을 받자 차수호(김영대)가 그녀를 현장에서 구해낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3325, 26257, 17439, '8회에서 차수호(김영대) 요원은 앞서 강여주를 담당했던 다른 국정원 요원을 따라잡아 사건에 대한 정보를 얻으려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3326, 28487, 17439, '6, 7회에서 한우성(고준)이 고미래(연우/전 모모랜드 이다빈)를 이 장소에 내려주고 미래는 이곳에서 아르바이트를 하고 있다고 설명한다(일반 학생 신분을 속이려는 것이 아니다). 실제로 그녀는 이 미용실의 단골이며 중요한 공식석상을 준비하기 위해 이곳에 약속이 있었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3327, 28488, 17439, '5회에서 우성이 백수정(홍수현)의 죽음을 알게 된 후 한우성(고준)과 절친 손진호(정상훈)가 술을 마시던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3328, 25737, 17439, '6회에서 고미래(전 모모랜드 연우)가 이 거리에 쫒겨난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3329, 28489, 17439, '6화에서 강여주(조여정)와 한우성(고준)은 저녁 식사를 한 곳이다. 저녁 식사는 내내 어색했고 그는 이곳이 5년 전 처음으로 키스한 곳이라는 것을 그녀에게 상기시켜위기를 밝게 하려고 노력합니다. 그리고 그녀가 나중에 아이를 낳아야 하는지 묻자 당황합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3330, 26085, 17439, '한강병원\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3331, 28490, 17439, '1회에서 \'국민남편\' 한우성(고준)과 백수정(홍수현)의 TV 인터뷰가 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3332, 26441, 17439, '한우성(고준)은 이 카페에서 절친이자 비서인 손진호(정상훈)를 만난다. 4회에서 자신이 처한 협박 상황에 대한 해결책을 찾기 위해 노력하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3333, 26247, 17439, '1회에서 한우성(고준)의 연인들이 이 건물에 살다가 그녀의 아파트에서 이별하는 모습이 방송됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3334, 28491, 17439, '7회에서 차수호(김영대)가 절판된 책을 이 가게에서 찾고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3335, 28492, 17439, '한우(고준) 변호사는 이 건물에 법무법인 회사가 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3336, 28493, 17439, '차수호(김영대)는 3회에서 을지다락을 지나 이 거리를 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3337, 25853, 16843, '7,8회에서는 문하원(정해인)이 이 공원에서 그녀가 그와 결혼하지 않을 것이라는 것을 알고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3338, 28505, 16843, '4회에서 최진무(이승준)와 함께 강인욱(김성규)이 슬픔에 잠기려 찾아온다. 진무는 지나가던 문순호(김하나)를 발견하고 돌연 급한 일이 생겨 그녀가 대신하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3339, 28506, 16843, '10회에서 한서우(채수빈)이 벚꽃이 만발한 거리 모퉁이에서 문하원(정해인)에게 전화를 걸어 만나자고 한다. 그녀가 치유가 필요하기 때문입니다. 잠시 후, 하원은 그녀와 합류하기 위해 와서 꽃 아래에서 포옹을 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3340, 28507, 16843, '2회에서 최진무(이승준)가 피아니스트 강인욱(김성규)의 피아노 연주를 도우러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3341, 28508, 16843, '7회에서 한서우(채수빈)가 부모님과 가족을 태운 버스 화재로 돌아가신 후 한번도 가보지 못한 고향 양양행 티켓을 끊으려 망설이고 있다. 극 후반부에서는 용감하게 매표소로 걸어가 마침내 양양행 버스표를 샀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3342, 28509, 16843, '9회에서 문하원(정해인)과 AH연구소 동료들이 점심을 먹으러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3343, 26281, 16843, '문하원(정해인)이 운영하는 AH연구소 사옥. 3회에서 한서우(채수빈)는 비를 피하기 위해 여기로 돌진하고 하원이 실제로 하는 일에 대해 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3344, 28510, 16843, '이곳에서 서우는 자신에게 접시를 배달하러 온 김지수(박주현)를 처음 만난다. 이어 문하원(정해인)은 이 거리를 지나 집으로 향한다. 4화에서 서우는 가로등 옆에 구불구불한 의자를 놓고 집에 돌아오기를 기다리던 돌아가신 어머니가 놓았다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3345, 28511, 16843, '8회에서 문하원(정해인)과 한서우(채수빈)가 \'반의반\'에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3346, 25721, 16843, '9회에서 강인욱(김성규 분)이 한서우(채수빈 분)를 태워줄 것을 제안한다. \'욱인4길\' 입구에 내려달라고 부탁하고 무거운 가방을 메고 문하원(정해인)의 집으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3347, 24906, 16843, '9화에서 문하원(정해인)이 생각을 정리하기 위해 메타세쿼이아 나무 사이를 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3348, 28512, 16843, '6회 회상 장면에서 문순호(이하나)는 문하원(정해인)을 보며 웃는다. 이 식당(본명 \'날아라분식\')에서 순호는 미국에서 돌아온 김지수(박주현)에게 거절당했다고 털어놨다. 순호가 가장 친한 친구를 위해 약혼자에게 거절당하는 자신의 비참함에 뒹굴면서 웃음은 곧 무의식적인 눈물로 바뀐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3349, 28513, 16843, '5화, 10화에서 영화 \'피스\'에서 이 식당에서 몇 번이나 만난 문순호(김하나)와 강인욱(김성규).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3350, 26311, 16843, '2회에서 대학시절 문하원(정해인)과 김지수(박주현)는 서울에서 만나(그는 그녀를 보기 위해 미국에서 날아왔다) 이 공원을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3351, 28514, 16843, '9화에서 최진무(이승준)는 이 더 큰 스튜디오에서 녹음을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3352, 24950, 16843, 'M커피카페에서 만난 한서우(채수빈)와 김지수(박주현)는 1회에서 이 거리를 걸으며, 문하원(정해인)을 호기심에 뒤를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3353, 26585, 16843, '문하원(정해인)과 한서우(채수빈)는 서우가 버스를 타기 위해 이 건물을 지나 큰길로 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3354, 28515, 16843, '5회에 대로변에서 만난 한서우(채수빈)와 문하원(정해인)은 AI 버전의 김지수(박주현)와 대화하기 위해 골목으로 몸을 숨긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3355, 26071, 16843, '4회에서 한서우(채수빈)와 문하원(정해인)이 타는 버스가 광장을 지나는데, 이 불빛 동상이 배경으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3356, 28516, 16843, '11회에서 문순호(김하나)가 강인욱(김성규)을 카페로 데려온다. 문하원(정해인)과 마주한 후 나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3357, 28517, 16843, '4회에서 한서우(채수빈)가 문하원(정해인)에게 말을 걸고 이 버스 정류장으로 향하고 있다. 그녀가 버스를 타려고 할 때, 하원은 그녀를 집에 보내주겠다고 제안합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3358, 28518, 16843, '4회에서 문하원(정해인)이 김지수의 한국에서의 마지막 발걸음을 따라 녹음실을 빠져나와 이 거리를 걷고 있다. 스튜디오에서 그를 본 한서우(채수빈)는 그의 뒤를 따르기로 한다. 2화에서 지수는 스튜디오에서 서우를 만난 후 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3359, 28519, 16843, '4회에서 문하원(정해인)이 김지수의 한국에서의 마지막 발걸음을 따라 이 육교를 향하고 있다. 자신이 찍은 사진을 풍경과 비교하며 자신의 사진을 찍는 동안 한서우(채수빈)는 지상에서 그를 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3360, 28520, 16843, '2회에서 김지수(박주현)는 한서우(채수빈)를 만난 뒤 버스정류장에서 기다립니다. 그녀가 길 건너 공항 버스를 보고있다. 그런 다음 그녀는 마음을 바꾸고 버스로 달려갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3361, 28468, 16843, '문하원(정해인)이 묵는 집으로, 한서우(채수)가 사는 집으로 보인다. 한서우는 그와 함께 시간을 보내기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3362, 28521, 16843, '4회에서 김창섭(강봉성 분)이 전은주(이상희 분)를 만나 한 끼 식사를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3363, 28522, 16843, '한서우(채수빈)와 김지수(박주현)는 1회에서 서우가 지수 목소리 녹음을 마친 후 이 집에서 몇 걸음 떨어진 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3364, 26140, 16843, '한서우(채수빈)와 김지수(박주현)는 1회에서 서우가 지수 목소리 녹음을 마친 후 이 곳에서 몇 걸음 떨어진 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3365, 25017, 16843, '2회에서 미국에서 유학 중인 문해원(정해인)은 김지수(박주현)의 부름을 받고 한국으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3366, 28523, 5237, '한효주와 고수가 고기를 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3367, 28524, 5237, '고수, 한효주가 술 마시러 간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3368, 25806, 5237, '한효주가 자살소동을 벌이는 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3369, 28525, 5237, '한효주와 고수가 바닷가 데이트 한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3370, 28526, 5237, '한효주가 고수를 기다리는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3371, 25347, 12713, '5화에서 강인욱(소지섭)과 최영주(박예진)는 차를 타고 이 공원으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3372, 28527, 14483, '여기는 강연두(정은지) 엄마의 카페.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3373, 28528, 14483, '아들과 눈을 마주치는 것이 불편한 아버지에게 연두의 어머니가 “미안해요, 고마워요, 사랑해요”라는 말은 아이가 잘 자라기 위해 들어야 할 세 가지 요소라고 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3374, 28529, 14483, '8,9회에서 세빛고 치어리딩팀이 여행을 갑니다. 여기서 김열(이원건)은 강연두(정은지)에게 마음을 고백한다. 하지만 그녀가 대답을 하려 하자 서하준(지수)이 다가와 어색하게 만든다. 고백은 캠핑장으로 이어지는 다리에서 이루어졌습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3375, 26487, 14396, '김성열(이준기)과 조양선(이유비)이 20회 마지막 장면에서 재회하는건 이 나무 아래다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3376, 28530, 13129, '허초희(김선아)는 10회에서 이 역 맞은편 승강장에서 돌아가신 아버지를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3377, 26038, 13129, '4회에서 허초희(김선아)와 김범상(이동건) 친구가 이 호텔 경매에 참여한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3378, 25710, 13129, '허초희(김선아)는 이 박물관에 있는 문화재청 문화재 단속반에서 일한다. 2회에서 김범상(이동건)이 차관으로 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3379, 26992, 13129, '강지윤(김병옥)은 14회에서 부하들과 함께 이 현장에서 거래를 하는 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3380, 25797, 13129, '16화에서 CHA 팀의 일부가 이 다리 위를 운전합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3381, 28531, 13129, '1회에서 인사동 한옥 찻집 앞 이 거리에서 허초희(김선아)가 문화재 밀수업자 화자지를 검거한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3382, 27212, 13129, '14화에서 허초희(김선아)와 김범상(이동건)이 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3383, 28532, 13129, '16화에서 이 집은 장오성(김용건) 회장의 저택으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3384, 28533, 13129, '허초희(김선아)는 동생 허균(박기웅)과 함께 이 집에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3385, 28534, 13129, '13회에서 왕주현(김정화)은 허초희(김선아)의 아버지가 실종되기 전 마지막으로 만났던 사람을 찾는 상사 강시완(이주현)을 따라갔다. 이를 계기로 이 거리로 오게 된 주현은 평소와 다름없이 일을 진지하게 받아들이지 않고 입구 앞에서 셀카를 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3386, 27048, 15462, '4화에서 1박 출장을 함께하던 서준희(정해인)와 윤진아(손예진)가 저녁에 이 해변을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3387, 28535, 15462, '6화에서 서준희(정해인)의 친구들과 여행을 떠난 윤진아(손예진)와 준희는 잠시 그들을 두고 이 숲을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3388, 28536, 15462, '아파트 단지에 가족과 함께 사는 윤진아(손예진).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3389, 28537, 15462, '9화에서 윤진아(손예진)는 컴퓨터 문제로 동생을 도와주는 서준희(정해인)를 만나러 집으로 가는 길에 분당선 1번 출구로 나와 버스로 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3390, 27561, 15462, '서준희(정해인)와 여동생 서경선(장소연)은 각각 이 아파트 단지의 아파트에 살고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3391, 28538, 15462, '5화에서 서준희(정해인)와 윤진아(손예진)는 이 곳을 통해 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3392, 28539, 15462, '이 커피베이 카페에서 서준희(정해인)의 여동생 서경선(장소연)이 운영하는 지점으로 등장한다. 정기적으로 만남의 장소로 등장하며 거의 모든 캐릭터가 이 카페를 지나갑니다. 윤진아(손예진)는 1회에서 전 남자친구가 두 번 있었다는 사실을 알게 된 후 이곳에 와서 술에 취해 한 번도 사랑한 적이 없다는 이야기를 하고 두 사람의 어두운 미래를 그린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3393, 25997, 15462, '서준희(정해인)와 장소연(서경선)의 어머니가 이곳에 안치됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3394, 24902, 15462, '1회에서 윤진아(손예진)이 바람 피운 전남친에게 분노한 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3395, 26567, 15462, '윤진아(손예진)는 그녀를 돕기 위해 본사에서 이 매장으로 파견됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3396, 26514, 15462, '13회에서 윤진아(손예진)가 아버지를 만나는 것을 본 직후 서준희(정해인)가 좌절하며 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3397, 28540, 15462, '윤진아(손예진)는 12회에서 점심시간에 동료들과 함께 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3398, 26088, 15462, '서준희(정해인)는 사무실로 향하던 중 이 건물 앞에 차를 세우고 윤진아(손예진)에게 "사랑한다"는 음성 메시지를 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3399, 25791, 15462, '2회에서 윤진아(손예진)가 술에 취한 동료 문대리를 위해 이 건물 앞에서 택시를 부른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3400, 28541, 15462, '7회에서 윤진아(손예진 분)는 서준희(정해인 분)가 운전을 제안했음에도 지하철을 타고 집에 간다고 주장한다. 그래서 그는 그녀를 이 역의 2번 출구로 데려오고 작별 인사를 한 후 그녀에게 전화기를 빌려주기 위해 그녀를 쫓아온다. 13화에서 진아는 7화에서 그 순간을 회상하며 기차를 기다리며 홀로 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3401, 28542, 15462, '8회에 윤진아(손예진)가 퇴근길에 이 정류장에서 버스에서 내린다. 그리고 진아가 버스 정류장에 있는 CU 편의점에 들어가는 것을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3402, 25795, 15462, '윤진아(손예진)와 서준희(정해인)가 1화에서 다시 만나는 곳은 이 공원의 맨 동쪽 끝이다. 배경에 있는 계단은 디지털 미디어 시티 건물에 속한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3403, 26400, 15462, '윤승호(위하준)와 서준희(정해인)는 7회에 이곳에서 한 잔 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3404, 27442, 15462, '15회에서 휴식 시간에 윤진아(손예진) 진)은 서준희(정해인)와 함께 행복했던 시절을 이 공원에서 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3405, 26577, 15462, '서씨 남매 아빠를 만난 윤진아(손예진)는 12회에서 이 정류장에서 조금 더 아래로 택시에서 내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3406, 28133, 15462, '손예진과 정해인이 우산을 함께 쓰고 걷던 거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3407, 28543, 15462, '2회에서 서준희(정해인)와 윤진아(손예진)가 저녁식사를 하는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3408, 28544, 15462, '윤진아(손예진)는 12회에서 소개팅과 서남매 아빠와의 뜻밖의 만남 이후 반성하기 위해 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3409, 28545, 15462, '이 건물은 윤진아(손예진)의 전 남자친구 이규민(오룡)이 일하는 사옥으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3410, 28546, 15462, '카페를 지나는 서준희(정해인)와 윤진아(손예진). 4회에서는 저녁을 함께 먹고 난 직후, 준희가 진아의 손을 잡거나 어깨에 팔을 두르지 않으려고 필사적으로 몸을 움츠렸다. 11화에서는 이 카페를 편안하게 걷고 있는 두 사람의 모습. 팔짱을 끼고 12화에서 여기를 지나가는 사람은 진아 혼자다.. 14화에서 이 카페 앞에서 둘이 말다툼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3411, 28547, 15462, '윤진아(손예진)와 서준희(정해인) 단골집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3412, 28548, 15462, '4회에서 서준희(정해인)는 윤진아(손예진)와 마주보고 서서 방금 전에 식당에서 손을 잡은 이유에 대해 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3413, 28549, 15462, '4회에서 윤진아(손예진)와 서준희(정해인)가 하룻밤 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3414, 26140, 15462, '4화에서 상사 중 한 명과 함께 이 쇼핑몰에 와서 그녀의 커피 회사 지점 중 한 곳에 체크인합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3415, 24954, 15462, '서남매의 아버지(김창완)가 한국에 머무는 동안 이곳에 머물렀다. 이 호텔의 입구는 11~12회에서 윤진아(손예진)가 이 호텔에서 소개팅을 했던 호텔 입구로도 등장한다. 그녀는 우연히 같은 장소에서 아버지를 만나려는 절친 서경선(장소연)과 마주친다. 경선은 진아가 동생과 데이트하면서 이럴 수 있다는 것에 분노하고 모든 상황이 매우 어색해진다.');
