INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3576, 26652, 14005, '1회에서 유학을 마치고 돌아온 직후, 최강주(이홍기)는 그의 약혼자 장이경(양진성)을 이 장소로 부르고 저녁 데이트를 갖습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3577, 25865, 14005, '2회에서 최강주(FT아일랜드 이홍기)의 아버지 최일도(최일화)가 이 공원을 찾아오고 웨딩드레스를 입은 여자를 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3578, 27565, 14005, '7회에서 최강주(FT아일랜드 이홍기)는 장이경(양진성)이 웨딩드레스 차림으로 오자 두 사람의 결혼식을 파기한다. (CSTV, 2014).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3579, 28447, 14005, '장이현(성혁)은 이 집에 산다. 가출한 누나 장이경(양진성)을 대신해 고용됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3580, 26659, 14005, '나두림(양진성)의 절친 오진주(박진주)는 이 집의 옥상 아파트에 산다. 4화에서 저녁에 두림이 찾아와 진주를 도우며 사채업자를 쫓는 모습이 처음으로 등장한다. 모두 이루미(장아영)가 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3581, 26139, 14005, '나두림(양진성)의 절친 오진주(박진주)는 4화에서 처음으로 두림이 저녁에 찾아와 진주를 도와 고리대금업자들을 쫓는 장면에서 보듯 이 집 옥상 아파트에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3582, 27335, 14005, '나두림(양진성)은 6회에서 서울로 출발하며 이 정류장에서 버스를 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3583, 26577, 14005, '4회에서 이루미(장아영)은  나두림(양진성)의 집을 찾아 이 거리를 걸어간다 .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3584, 28805, 14005, '5회에서 최강주(FT아일랜드 이홍기)의 어머니 김명희(김서라)가 이곳에 집 점쟁이를 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3585, 25862, 14005, '3화에서 나두림(양진성)은 이 공원에서 빵집이 여는 케이크 먹기 대회에 참가해 우승자로 불리는 최강주(이홍기)의 모습을 우연히 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3586, 27212, 14005, '3회에서 장이현(성혁)이 나두림(양진성)을 이 거리로 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3587, 26273, 14005, '나두림(양진성)과 최강주(이홍기)가 게스트 없이 비공개로 결혼식을 올리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3588, 26275, 14005, '최강주(FT아일랜드 이홍기)가 운영하는 백화점(\'태양그룹 백화점\'으로 등장)이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3589, 25017, 14005, '1화에서 최강주(FT아일랜드 이홍기)가 유학을 마치고 돌아와 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3590, 26654, 14005, '이 장소는 최강주(FT아일랜드 이홍기)의 자택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3591, 25680, 15739, '홍심(남지현)이 10년 전 서로 약속했던 한 달에 한 번씩 오빠를 기다리는 \'나무다리\'가 이곳에 있다. 2회에서 정제윤(김선호 분)과 어른 이율(엑소 도경수 분)이 홍심을 처음 보는 곳이기도 하다. 5화에서 제윤은 자신의 소원이 이루어진 떠다니는 등불을 준비한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3592, 26485, 15739, '이 마을은 연홍심(남지현)이 사는 마을로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3593, 27085, 15739, '100화 6회에서 연홍심/윤이서(남지현)가 \'솔루션 에이전시\'를 통해 이율/나원득(도경수/EXO D.O)의 의뢰인 애인 역을 맡아 달라는 의뢰를 수락했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3594, 25673, 15739, '드라마에서 주인공들이 궁궐 정원에서 만나 대화를 하는 장소 (특히 광한루, 오작교, 완월정) 로 나온다');
