INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3713, 29152, 7528, '버닝의 주연들이 첫 술자리를 가진 곱창집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3714, 29153, 7528, '스티븐 연이 전종서의 손금을 봐주던 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3715, 25608, 7528, '전종서의 방 안에서 늘 바라보이던 상징적 건물');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3716, 26696, 7528, '유아인이 스티븐 연을 미행한 미술 갤러리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3717, 25661, 16958, '6화에서 강무영(이선빈)은 이 거리에서 고등학생 무리에게 정보를 얻으려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3718, 26030, 19127, '11회에서 김유리(이세영)와 김정호(이승기)는 황 대표(김원해)에게서 도한그룹에 대한 포인터를 얻기 위해이 식당에서 그를 만났습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3719, 26323, 19127, '16회에서 김정호(이승기)와 김유리(이세영)는 폭풍우가 몰아치는 늦은 오후 이 장소에서 결혼식을 올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3720, 25605, 19127, '11회에서 김유리(이세영)가 김정호(이승기)와 의뢰인을 포함한 로카페팀을 이곳으로 데려왔다. 의뢰인은 아버지로부터 이 땅(이전에는 개발되지 않았지만 지금은 우주로 가득 찬)을 물려받았고 팀은 의뢰인의 아버지의 유언을 이해하기 위해 시 수수께끼를 풀어야 했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3721, 26320, 19127, '1회에서 김정호(이승기 분)와 김유리(이세영 분)는 이 대학에서 공부하는데 유리가 정호에게 연애를 하자고 말한 것은 이 광장을 따라 늘어선 벚꽃길에서다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3722, 24934, 19127, '1회에서 한세연(김슬기)과 도진기(오동민)가 이 곳에서 결혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3723, 29154, 19127, '김정호(이승기)는 4화에서 아버지(그와 인연을 끊은)에게 속아 이 식당에서 만남을 갖게 되고, 몇 번의 교류 끝에 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3724, 27074, 19127, '16회에서 김유리(이세영)는 단짝 친구 한서연(김슬기), 김정호의 엄마 이연주(이미숙)와 함께 쇼핑을 하며 그들의 이혼 계획을 다시 생각하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3725, 26652, 6101, '영화 초반 유아인과 황정민이 처음 본 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3726, 29155, 6101, '베테랑 초반 나온 항만의 부두');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3727, 25913, 6101, '영화 후반 유아인이 마지막 파티를 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3728, 26030, 15244, '11회에서 양과장은 변혁(최시원)을 민 대표(송영규)와의 업무상 미팅을 위해 이곳으로 데리고 왔고, 혁은 눈에 띄게 불안해합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3729, 25908, 15244, '변혁(최시원)의 영상 스캔들과 관련된 항공기 장면이 이 곳에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3730, 24933, 15244, '14화에서 모두가 함께 오후 계획을 세우기 위해 이곳에 모인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3731, 27243, 15244, '이 곳은 강수그룹의 본부입니다. 6회에서 변혁(최시원)이 건물 외부에서 창문을 닦는 일을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3732, 29167, 15244, '백준(강소라)는 이 집 옥상 아파트에 산다. 아파트 지하에 사는 권제훈(공명). 변혁(최시원)은 잠시 후 백준과 동거한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3733, 28088, 15244, '7회에서 민 대표(송영규)에게 서비스 계약이 해지되었다는 통보를 받은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3734, 26002, 15244, '14회에서는 변혁(최시원), 백준(강소라), 권재훈(공명)이 이 놀이공원으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3735, 26039, 15244, '1회에서 변혁(최시원)이 호텔 수영장에서 수영을 하고 있다. 그는 전 여자친구를 보고 그녀를 쫓고, 사물함 열쇠를 잃어버리고 재빨리 수영장 근무복으로 갈아입고 그녀를 따라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3736, 25862, 15244, '1회 회상장면에서 여기서 백준(강소라)이 권제훈(공명)에게 어떤 고백을 했는지 확인할 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3737, 27331, 15244, '9화에서 이 건물은 변혁(최시원)의 가족 저택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3738, 29168, 15244, '2회에서 집으로 돌아가는 길. 현은 민들레를 보고 멈춰서서 민들레 씨를 날려버린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3739, 26125, 15244, '1회에서 강수그룹 잔치가 펼쳐진 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3740, 27363, 5494, '변호인 속 부산법원으로 나온 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3741, 29169, 5494, '영화 변호인에서 송강호가 요트를 타는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3742, 25682, 5494, '영화 변호인의 80년대 촬영 배경이 된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3743, 25748, 5494, '송강호가 헌책방에 팔았던 고시 책을 되찾는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3744, 29170, 5494, '영화 변호인의 촬영지이자 군산 최초의 성당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3745, 29171, 5494, '변호인에서 진우와 친구들이 고문당한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3746, 29172, 5494, '변호사 명함을 뿌리는 아파트 모델하우스 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3747, 28104, 11786, '11,12회에서 공태성(김영대)은 기자간담회에서 은시우(최지우)가 계단을 내려가는 모습을 보고 깜짝 놀란다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3748, 25816, 11786, '한승욱/장알렉스(이상윤)가 피해자 아내의 정보를 얻기 위해 방문하는 곳이기도 하다. 13화에서 시우(최지우)가 어린 공태성(장재하)에게 자신은 그의 어머니가 아니라고 말할 때 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3749, 25908, 11786, '12, 13회에서 은시우(최지우)와 당시 매니저였던 최지훈(하도권)은 10대 공태성(문성현)으로부터 도움이 필요하다는 연락을 받았을 때 이미 비행기에 탑승한 상태였다. 불행하게도, 그녀는 비행기가 막 이륙하려고 할 때 떠날 수 없다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3750, 24934, 11786, '8회 파티가든수대 앞에서 윤재현(신현승)과 진유나(이시우)가 그들의 키스 장면을 기다리며 어색하게 서있는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3751, 24900, 11786, '5회에서 이 도서관의 그녀의 유니스 코치, 이태상(조한철)이 마지막으로 발을 내딛는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3752, 29173, 11786, '9회에서 공태성(김영대), 오한별(이성경)이 LU42가 한창일 때 이 거리를 독립적으로 운전해 내려간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3753, 25953, 11786, '1회에서 오한별(이성경)은 박조은(박정민)과 이 호텔 델비노 레스토랑에서 소개팅을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3754, 26282, 11786, '5회에서 공태성(김영대)은 시상식 장면을 이곳에서 촬영하는데, 이 장면 역시 오한별(이성경 분)이 눈치채지 못하는 것에 점점 더 화가 난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3755, 26540, 11786, '14화에서 강유성(윤종훈)이 dvN 사옥(본명 \'DCMC\') 로비에서 박호영(김윤혜)을 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3756, 25659, 11786, '5회에서 공태성(김영대)은  드라마 \'별똥별의 세계\' 장면을 이곳에서 촬영한다. 그는 결국 하루 종일 자신을 무시하던 오한별(이성경)에게 화를 낸다. 14화에서, 새롭게 헤어진 윤재현 (신현승)과 진유나 (이시우)는 그들의 캐릭터로서 그들의 마지막 장면을 찍는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3757, 26178, 11786, '공태성(김영대)은  극중 촬영하는 드라마 \'별들의 세계\'의 한 장면을 촬영하며 필사적으로 오한별(이성경)의 신발끈을 묶으려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3758, 26137, 11786, '이 호텔의 알파인 카페는 4회에서 강유성(윤종훈 분)이 비즈니스 미팅을 갖는 해외 로케이션으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3759, 26002, 11786, '11회에서 공태성(김영대)은 이곳에서 촬영을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3760, 26311, 11786, '14회에서 박호영(김윤혜)을 만난 강유성(윤종훈)은 사옥에서 박호영(김윤혜)을 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3761, 26499, 11786, '15회에서 강유성(윤종훈)과 박호영(김윤혜)이 밤에 다리를 따라 산책을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3762, 29174, 11786, '9화에서 공태성(김영대)과 오한별(이성경)은 밤에 이 쇼핑몰 앞에서 만나기로 했다. 하지만 한별이 도착하기도 전에 한 무리의 팬들에게 인질로 잡혀간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3763, 25608, 11786, '14회 공태성(김영대)과 백다혜(장희령)가 \'사랑의 자물쇠\' 구역에서 \'별똥별\' 촬영을 마무리한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3764, 24913, 11786, '13회에서 여기서 강유성(윤종훈)과 박호영(김윤혜)은 저녁 식사 후 이곳을 산책하다가, 호영이 유성에게 고백을 하면 대신 자신의 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3765, 26188, 11786, '12화에서 공태성(김영대)은 이 자리에서 기자회견을 하던 중 계단을 내려오는 은시우(최지우)를 보고 깜짝 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3766, 25017, 11786, '13회에서 최지훈(하도권)이 은시우(최지우)를 이곳으로 배웅했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3767, 24915, 11786, '태성이 이 호텔에서 가진 기자회견 이후 오한별(이성경 분)과 공태성(김영대 분)의 지속적인 언쟁을 하는데 그가 아프리카에서 봉사활동을 한 이유가 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3768, 25866, 11786, '16회 최종회에서 태성(김영대)과 오한별(이성경)이 비밀 도주를 위해 따로 도착한다.  하지만 공항 입국장에서 드라마 촬영 중임을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3769, 26647, 11786, '마지막 16회에서 공태성(김영대)은 오한별(이성경)을 이곳으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3770, 24900, 15086, '최한주(강경준)는 1화에서 이 건물에 들어오는 김태기(김재현)를 뒤에서 막으려 한다. 한주는 여자화장실에 숨은 태기를 의심하고 그곳에서 황은별(함은정)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3771, 29175, 15086, '황은별(함은정)과 황금별(이주연)이 사는 황씨네 집입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3772, 27308, 15086, '1화에서 최한주(강경준)는 달려와서 이 역 5번 출구로 들어오는 김태기(김재현)를 막으려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3773, 25026, 13972, '전지현과 김수현이 촬영지에서 키스를 한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3774, 25963, 13972, '21화 마지막 장면에서 도민준(김수현)과 천송이(전지현)의 새 보금자리로 등장한다. 8회에서 유세미(유인나 분)를 찾아가 12년 전 도민준(김현수 분)이 어떻게 구했는지 목격했다고 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3775, 25607, 13972, '17회에서 천송이(전지현)와 도민준(김수현)은 이곳에서 논쟁을 벌인다. 21회에서는 천송이(전지현)가 이 공원 폭포 광장에서 드라마 촬영을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3776, 26562, 13972, '20회 도민준(김수현 분)이 집에 문을 열어준다. 하지만 실수로 이 지하철역 6번 출구 밖 광장으로 순간이동 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3777, 26418, 13972, '17회에서는 천송이(전지현)와 도민준(김수현)이 이 펜션에서 하룻밤을 보내는 모습이 그려졌다. 송이는 민준이 사라진 줄 알고 미친듯이 운다. 그리고 그가 그녀를 위로하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3778, 25865, 13972, '21회에서 별들이 UFO처럼 날아가는 유성우를 보기 위해 많은 인파가 이 공원에 모였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3779, 25635, 13972, '유인나와 김수현, 전지현이 만난 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3780, 29176, 13972, '김수현과 전지현이 캠핑을 간 눈덮힌 저수지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3781, 26607, 13972, '에피소드 3에서 도민준(김수현)은 천송이(전지현)를 급성 맹장염으로 이 병원으로 데려갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3782, 29177, 13972, '도민준 (김수현)은 천송이(전지현)를 따라갑니다. 6,7회에서 이희경(박해진)과 데이트를 하기 위해 이 놀이동산으로 갔다. 희경과 송이가 관람차를 타고 송이가 희경의 고백에 답하려 하는 동안, 민준은 시간을 멈추고 모든 것이 다시 작동하자 그녀는 그녀의 반응을 부정으로 변경했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3783, 27319, 13972, '김수현과 전지현이 기자들에게서 도망친 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3784, 26995, 13972, '5회에서 도민준(김수현)은 자전거를 타고 이 역 출구를 지날 때 천송이(전지현)의 광고판이 철거되는 것을 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3785, 29178, 13972, '7화 천송이(전지현)가 이 부티크에 초대되어 도민준(김수현)이 천송이를 데려간다. 부티크 안에서 우연히 유세미(유인나)와 마주치며 둘 사이에 불편한 이야기가 이어진다. 건물을 나서던 중 암살자가 옥상에서 송이에게 무거운 유리잔을 떨어뜨리고 민준는 송이를 겨우 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3786, 25615, 13972, '8화에서 이희경(박해진 분)은 차가 절벽에서 떨어질 뻔하자 천송이(전지현 분)를 다시 병원으로 데려간다. 그들이 입구에서 도민준(김수현 분)과 마주치자 천송이는 도민준에게 그 순간 절벽에 있었던 적이 있는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3787, 26247, 13972, '14회에서 도민준(김수현)은 이 건물 옥상에서 이재경(신성록)을 위협한다. 그리고 재경이 잠시 공중에 매달려 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3788, 29179, 13972, '5회에서 천송이(전지현)가 도민준(김수현)에 대해 설명하면서 현재 살고 있는 아파트로 이사한 곳이 바로 이 건물이다. 그녀의 아파트 발코니에서 그와 합류. 천송이에 따르면 이곳은 빌보드를 위한 가장 비싼 장소이며 이 나라에서 가장 핫한 스타가 여기에 삽니다(현재 그녀입니다).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3789, 26285, 13972, '1회에서 엄마 양미연(나영희)이 친구들과 함께 이 식당에서 회식을 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3790, 25347, 13972, '4회에서 도민준(김수현)이 자전거를 타고 119구조대 여의도 구조대와 가까운 이 공원을 지나는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3791, 25608, 13972, '김수현과 전지현이 자물쇠를 걸며 사랑을 다짐한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3792, 29180, 13972, '자물쇠를 건 후 식사를 한 회전식 레스트랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3793, 29181, 13972, '1회와 10회에서 도민준(김수현)과 변호사 친구 장영목(김창완)이 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3794, 26319, 13972, '1화에서 새문안로를 건널 때 도민준(김수현)이 이 빌딩 화면에서 \'세기의 혜성\' 뉴스를 읽는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3795, 29182, 13972, '20화에서 김민준(김수현)은 힘이 약해지면서 이 공원으로 순간이동한다. 그는 추위에 떱니다. 급하게 달려온 변호사 장영목(김창완)에게 전화를 걸고 민준은 이곳이 장영목을 처음 만난 공원이었다는 사실을 기억한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3796, 29183, 13972, '전지현에게 사줄 냄비우동을 포장한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3797, 24913, 13972, '7회에서 도민준(김수현)은 자신의 메시지를 듣기 위해 긴급 호출기를 받고 옆 공중전화 부스로 들어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3798, 24953, 13972, '1회에서 이희경(박해진), 천송이(전지현), 유세미(유인나)가 이 식당에서 저녁을 먹은 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3799, 29184, 13972, '사라진 김수현이 돌아와서 전지현과 키스한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3800, 26146, 13972, '도민준(김수현)은 이 대학에서 가르치고 천송이(전지현)는 이 캠퍼스에서 수업을 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3801, 24980, 13972, '13회에서 천송이(전지현)는 도민준(김수현)과 함께 밤바다를 거닐고 있다. 불꽃놀이를 배경으로 송이는 도민준에게 잠시라도 사랑을 느낀 적이 있는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3802, 29185, 15162, '김재걸 박사(이서원)의 아버지가 운영하는 병원으로 등장한다. 송은재(하지원)와 곽현(강민혁)은 병원선이 아닌 이곳에서 일한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3803, 25860, 15162, '13-14회에서는 아버지 곽성(정인기)이 치료를 받고 있는 중에 재활원을 찾은 곽현(강민혁)이 송은재(하지원)를 데리고 생일을 축하하는 모습이 그려졌다. 서울에서는 그런 곳을 찾을 수 없다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3804, 26674, 15162, '4화에서 이 병원은 \'서울 대한병원\'으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3805, 26132, 15162, '송은재(하지원)가 일하는 병원으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3806, 29186, 17206, '안은영(정유미)과 홍인표(남주혁)가 등장한다. 2화에서 이 사찰에 기도하러 갔더니 둘 다 친구가 없다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3807, 25608, 17206, '4화에서 안은영(정유미)과 홍인표(남주혁)는 어색한 저녁 외출에 함께 이 랜드마크에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3808, 26412, 17206, '4회에서 안은영(정유미)은 이곳에 묵고 있는 학교 영어교사 매켄지에 대해 알아보기 위해 이 호텔을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3809, 28107, 13509, '차봉만 회장(박영규 분)이 최종회 18회에서 이 거리의 횡단보도에서 사회봉사를 하고 있다. 그곳에서 노은설(최강희)과 차지헌(지성)의 데이트를 허락해달라고 애원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3810, 25958, 13509, '7회에서는 차지헌(지성)과 차무원(김재중)이 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3811, 29188, 13509, '2화, 18화에서 차지헌(지성)은 아버지 차봉 회장과 함께 이곳에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3812, 26249, 13509, '2회에서 이 무도회장에서 기념일을 축하한다. 그리고 차지헌(지성)은 공황 발작으로 인해 방을 떠나야 할 때까지 자신을 새로운 최고 재무 책임자라고 소개한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3813, 25910, 13509, '1회에서 김비서(김형범)이 이곳에서 술을 마시다가 기자의 말을 듣게 되면서 언론에 차지훈(지훈)을 둘러싼 새로운 스캔들이 발생합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3814, 25604, 10055, '15, 16회에서 모태구(김재욱)의 살인으로 공개된 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3815, 24935, 10055, '6화에서 무진혁(장혁)과 강권주(이하나)는 클럽 마담을 살인 사건으로 인터뷰하기 위해 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3816, 26084, 10055, '15화에서 배경으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3817, 24933, 10355, '김재화(곽선영)가 이 카페에서 전 절친 박수진(오혜원 분)을 만난다. 수년 전 수진과의 첫사랑 불륜을 알게 되면서 했어야 할 일을 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3818, 24905, 10355, '김재화(곽선영)는 이 공원을 따라 걷다가 껌을 밟고 동시에 지나가던 개가 다리에 오줌을 싼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3819, 26285, 10355, '김재화(곽선영)는 이 식당에서 첫사랑 하정우(오동민)를 만나 집 리모델링을 도와달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3820, 29203, 10355, '김재화(곽선영)가 탄 택시가 강변고속도로를 지나갈 때 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3821, 29204, 10355, '어린 김재화(차정현)가 간식가게에서 엄마(강애심)를 바라보며 길가에 서 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3822, 27326, 15925, '22회에서 강복수(유승호)가 이곳에서 박선생님(천호진)을 만나 진심을 증명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3823, 26030, 15925, '22회에서 오세호(곽동연)는 엘리트 학생의 엄마들의 리더를 이곳에서 만납니다. 두 사람은 뇌물 얘기를 하고 강복두(유승호)와 이경현(김동영)이 옆방에서 대화를 엿듣고 녹음합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3824, 25635, 15925, '4회에서 강복구(유승호)는 이 카페에서 고객의 남자친구를 대신합니다. 이 곳에서 우연히 동시에 이곳에 온 손수정(조보아)과 오세호(곽동연)의 이야기를 엿듣습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3825, 29205, 15925, '4회에서 화난 강복수(유승호)가 밤에 이 쇼핑몰을 지나간다. 혼잣말을 하며 그는 자신의 과거를 회상하고 이전과 같은 사람들에 의해 자신의 삶이 다시 망가지는 것처럼 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3826, 29206, 15925, '1회에서 강복수(유승호)가 학창시절 살던 곳으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3827, 26211, 15925, '1회에서 이 학교 옥상은 2009년 모두가 다녔던 \'설송고\' 옥상으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3828, 26098, 15925, '4회에서 강복수(유승호)가 손수정(조보아)에게 김명호(신담수) 사과를 하게 만드는 것은 계단 위에 있는 \'죽기 전에 벽화\' 앞에서이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3829, 27124, 15925, '1회에서 강복수(유승호)가 2009년 모두가 다녔던 등교길인 이곳을 지나친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3830, 26145, 15925, '손수정(조보아)이 선생님으로 돌아온 강복수(유 승호) 학생, 오세호(곽동연) 이사장이 있는 학교.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3831, 25017, 15925, '3화에서 오세호(곽동연)가 이 곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3832, 29207, 15925, '강복수(유승호)와 손수정(조보아)은 14화에서 수학여행지로 이 공원을 방문했다. 방문하는 동안 둘은 가까워지고 오후는 데이트로 변했는데 이경현(김동영)과 양민지(박아인)가 합류해 두 사람의 달콤한 시간을 방해한다. 그러나 복수는 경현과 민지를 남겨두고 저녁 시간에 적절한 순간을 찾아 수정과 이 공원의 탑 앞에서 키스로 하루를 마감한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3833, 25604, 17364, '김태온(유선)의 별장. 그리고 그녀는 그녀의 연인 이훈석(정욱)을 여기로 보내 휴식을 취한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3834, 25026, 17364, '9화에서 강해라(김사랑)의 아들 이가온(정현준)을 찾아 헤매는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3835, 26030, 17364, '6회에서 김상구(정만식)와 차민준(윤현민)이 이 식당에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3836, 24900, 17364, '강해라(김사랑)는 1회에서 이 도서관 카페에서 사인회를 한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3837, 29208, 17364, '이 건물의 외관은 이훈숙(정욱)의 사무실/에이전시 공간으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3838, 25796, 17364, '차민준(윤현민)은 1회에서 이 법정에서 비서의 성폭행 혐의를 벗는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3839, 29209, 17364, '1번출구에서 1화에 사인회에 늦는 강해라(김사랑)는 밴을 두고 지하철을 타기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3840, 26039, 17364, '이 호텔은 김현성(정의재)이 마약에 취해 강해라(김사랑)와 불륜을 저질렀다는 증거를 만들기 위해 데려온 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3841, 25882, 17364, '2화에서 강해라(김사랑)는 전 동료와 만나 복직 가능성을 논의하기 위해 공원의 이 부분을 거닐었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3842, 29174, 17364, '1화에서 이 쇼핑몰 외관은 강해라(김사랑) 사인회가 있었던 곳이다. 내부 장면은 다른 곳에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3843, 25910, 17364, '1회에서 김현성(정의재)은 기자 시절 그의 선배 강해라(김사랑)를 이 식당으로 데려와 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3844, 26412, 17364, '3회에서 기자회견이 이 호텔에서 일어난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3845, 26125, 17364, '6회에서 김상구(정만식)는 차민준(윤현민)을 사적인 모임에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3846, 26475, 16889, '5,8회에서 나오는 카페이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3847, 24905, 16889, '5회에서 김수혁(이수혁 분)은 정사빈(진세윤 분)과 통화를 하면서 이 공원을 거닐고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3848, 26144, 16889, '1회 오프닝 장면에서 천종범(장기용), 정사빈(진세윤), 김수혁(이수혁)이 서점 안팎에서 서로를 만난다. 특히 사빈은 예약해두었던 책을 종범이 손에 넣자 짜증을 내고, 수혁은 나가자 마자 부딪칠 뻔한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3849, 27216, 16889, '1화에서 80년대 정하은(진세연)이 운영하는 \'늙은미래서점\'으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3850, 29211, 3385, '봄날은 간다에서 이영애, 유지태가 간 유적지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3851, 29212, 3385, '봄날은 간다에서 이영애가 살던 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3852, 28553, 3385, '봄날은 간다에서 이영애, 유지태가 간 바다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3853, 29213, 3385, '봄날은 간다에서 이영애, 유지태가 머문 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3854, 27069, 3385, '봄날은 간다에서 이영애가 놀러 갔던 리조트');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3855, 29214, 3385, '봄날은 간다에서 이영애, 유지태가 간 마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3856, 29215, 3385, '봄날은 간다에서 이영애, 유지태가 간 강');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3857, 25026, 12316, '13화에서는 유지호(정해인)와 이정인(한지민)이 은우(하이안)를 공원으로 데려가는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3858, 26030, 12316, '31회(전체 16회)에서 권기석(김준한)은 아버지 권영국(김창완), 예비 시아버지 이태학(송승환)과 함께 이 식당에서 저녁 약속을 잡는다. 그리고 두 사람에게 가을에 이정인(한지민)과 결혼할 계획을 밝혔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3859, 29216, 12316, '봄밤에서 정해인과 한지민이 간 치킨집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3860, 26198, 12316, '19회(전체 10화)에서 유지호(정해인)와 이정인(한지민)이 저녁 산책을 하며 미술관을 몰래 엿본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3861, 27307, 12316, '이 카페에 유지호(정해인)와 이정인(한지민)이 온다. 18회의 끝에서 두 사람의 첫 입맞추고 19화 시작 부분엔 이 카페에 함께 있던 권기석(김준한)의 동료 중 한 명이 두 사람을 보고 기석에게 직접 전화를 걸어 이들과 마주하기 위해 이 카페 앞으로 달려가게 만든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3862, 26493, 12316, '봄밤에서 한지민과 이상희가 간 샌드위치 전문점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3863, 26410, 12316, '빚쟁이 남시훈(이무생)은 \'친형\' 권기석(김준한)을 3회에서 이 식당으로 초대해 부탁을 들어준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3864, 29217, 12316, '이 아파트 3층에 유지호(정해인)와 2층에 이정인(한지민)의 절친 송영주(이상희)가 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3865, 25735, 12316, '6화에서 이 카페는 유지호(정해인)가 집에 가는 길목에 있는 곳으로, 그가 이곳을 지나치는 장면이 종종 드라마에 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3866, 29218, 12316, '봄밤에서 정해인이 간 치킨집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3867, 29219, 12316, '봄밤에서 정해인과 한지민이 차 마신 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3868, 29220, 12316, '이 약국은 유지호(정해인)가 약사로 일하는 \'우리약국\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3869, 29221, 12316, '봄밤에서 한지민이 간 일식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3870, 29222, 12316, '이정인(한지민)은 14회에서 이 건물의 식당에서 권기석(김준한)의 아버지인 권영국(김창완)을 만난다. 15회에서 아들이 추구하는 결혼에 관심이 없다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3871, 29223, 12316, '권기석(김준한)은 봄밤 20회에서 이정인(한지민)에게 술을 마시러 왔다. 누나 이서인(임성언)은 정인이 아이가 있는 누군가를 만나고 있다는 사실을 알게 된다. 술을 마시며 정인의 전화를 기다리지만 이번에는 자신의 전략이 통하지 않는다는 사실을 곧 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3872, 29224, 12316, '봄밤에서 김준한이 혼술한 LP바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3873, 29225, 12316, '12회에서는 농구 경기가 끝난 후 권기석(김준한), 유지호(정해인), 그의 절친 최현수(임현수)가 술을 마시러 이 식당에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3874, 29226, 12316, '봄밤에서 한지민이 간 멕시칸 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3875, 25661, 12316, '최종회 32회(16회)에서 유지호(정해인)와 그의 아들 유은우(하이안)이 처음으로 만난 후, 이정인의 엄마 신형순(길혜연)은 윈도우 쇼핑을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3876, 29227, 12316, '봄밤에서 김준한과 김창완이 간 설렁탕집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3877, 29228, 12316, '봄밤에서 정해인과 한지민이 산책한 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3878, 29229, 12316, '4에서 이정인(한지민)은 이 도서관에서 일하고, 유지호(정해인)는 아들 유은우(하이)와 함께 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3879, 29230, 12316, '6회에서 저녁을 먹으러 이정인(한지민)과 권기석(김준한)이 여기에 온다. 기석은 결혼을 언급하며 다른 사람에게 끌리는지 묻는데 그녀의 강한 거부에 의심을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3880, 26577, 12316, '유지호(정해인 분)가 남산공공도서관 앞에 차를 세운 이 거리에서 이정인(한지민 분)과 심경과 앞으로의 관계에 대해 흉금을 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3881, 29231, 12316, '봄밤에서 정해인과 한지민이 간 도서관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3882, 29232, 12316, '1화에서 이정인(한지민)이 사는 아파트 단지로 나온다. 권기석(김준한)이 그녀를 내려주거나 언니와 함께 이 건물 앞에서 택시를 부르는 모습으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3883, 26071, 12316, '최종회 32회(16회)에서 이정인(한지민), 유지호(정해인), 아들 유은우(하이안)는 정인의 가족을 만난 후 집으로 돌아가는 길에 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3884, 29233, 12316, '봄밤에서 김준한과 임현수가 간 치킨집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3885, 29234, 12316, '11화에서 이 세탁소는 유지호(정해인)의 부모님이 운영하는 세탁소로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3886, 26158, 12316, '19회에서 유지호(정해인)와 이정인(한지민)이 저녁 산책을 하며 미술관을 몰래 엿본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3887, 24913, 12316, '10화에서 유지호(정해인)와 이정인(한지민)은 에서 저녁 산책을 하며 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3888, 25621, 12316, '유지호(정해인)는 이 장소에서 처음으로 이정인(한지민)의 엄마 신흥선(길해연)과 두 자매 이서인(임성언), 이재인(주민경)을 만나는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3889, 25953, 12882, '필립 로젠탈(다니엘 헤니)이 이 호텔에 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3890, 26173, 15971, '이봄(엄지원)은 2화에서 광장의 상징적인 조각품과 나란히 대형 LED 스크린에서 자신(이유리)을 봅니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3891, 25792, 15971, '이 건물의 외관은 1화에서 보듯 김봄이(이유리) 기자가 근무하는 곳으로 나온다. 로비 인테리어는 이형석(이종혁)이 불행한 사고를 당하는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3892, 25661, 15971, '6화(전체 3화)에서 박시원(이서연)이 스트릿 버스킹 공연을 보러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3893, 29235, 15971, '1회에서 방송된 연예기획사 외관으로 나온다. 방광규(김광규)는 제임수 (김성현)에게 자신의 때가 온다는 말과 함께 아이돌이 탄 밴을 둘러싼 팬들을 멀리서 바라보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3894, 25910, 15971, '9회(전체 5회)에서 김보미(이유리)는 바보처럼 행동하고 술에 취해 원치 않는 관심을 받고 있는 이봄(엄지원)을 찾으러 이곳에 왔습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3895, 29236, 4678, '영화 부당거래 파티 장소로 나오는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3896, 29237, 4678, '부당거래에서 황정민이 상을 받는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3897, 26071, 4678, '부당거래가 시작할 때 펼쳐지는 서울 한복판');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3898, 29238, 16835, '극 중 고산역으로 등장하는 기차역');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3899, 29239, 16835, '이무생이 김희애을 구하러 뛰어든 바다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3900, 27480, 16835, '8회에서 배경의 한 장소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3901, 26147, 16835, '15회에서 지선우(김희애)는 이 곳에서 망연자실한 이태오(박해준)를 발견하고 그와 말다툼 후에 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3902, 29240, 16835, '지선우(김희애)와 그녀의 이웃 고예림(박선영), 손제혁(김영민)은 이 주택 단지에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3903, 26719, 16835, '15회에서 벨라시타가 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3904, 26030, 16835, '9, 10회에서는 여병규 대표(이경영)와 김윤기 박사(이무생)를 이곳에서 만났습니다. 사위 태오(박해준)와 태오의 전 부인 선우(김희애) 사이에 어떤 감정이 남아 있을지 궁금증을 자아냅니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3905, 29241, 16835, '이 단지(3호 24/25)의 집 중 하나가 여다경(한소희)의 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3906, 25907, 16835, '1회에서 볼 수 있듯이 엄효정(김선경)가 이 장소의 문화라운지에서 개인전을 개최하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3907, 26416, 16835, '김희애가 아들 전진서와 시간을 보내던 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3908, 29242, 16835, '1, 2화에서 이태오(박해준)의 생일파티가 열렸던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3909, 29243, 16835, '5회에서는 태오(박해준 분)가 아들 준영(전진서 분)을 데리고 카페로 가는 모습이 그려졌다. 그를 학교에서 데리러 온 후. 다경(한소희)이 들어오고 태오는 그녀를 멍하니 쳐다본다. 그녀는 짜증스럽게 뒤를 돌아보고 카페를 떠납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3910, 26238, 16835, '지선우(김희애)의 전 동료이자 마강석 박사(박충선)의 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3911, 24900, 16835, '선우(긴희애), 다경(한소희)은 책값을 치르면서 서로 부딪힌 후 이 건물 밖에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3912, 29244, 16835, '1,2회에서 이곳은 선우(김희애)의 환자 민현서(심은우)가 일하는 술집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3913, 26644, 16835, '8회에서 제혁(김영민)과 그의 아내 예림(박선영)이 이 술집에서 외식을 하는 모습. 바 웨이트리스가 제혁에게 시시덕거리고, 커플이 체크아웃하자 그녀는 그가 보관했던 영수증 뒷면에 뭔가를 적는다. 9화에서는 그는 웨이트리스를 만나 술을 마시기 위해 이 술집으로 돌아갑니다. 태오(박해준)가 갑자기 들어와 제혁과 합류한다. 술에 취한 두 사람은 화기애애한 대화를 나누며 난투극을 벌였다. 남편의 외도를 알게 된 예림, 10화에서 제혁은 미친 듯이 여종업원을 찾다가 그녀가 사표를 냈다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3914, 28442, 16835, '1화에서 \'희원\'이라는 식당으로 등장한 이 웨딩홀의 외관. 남편이 바람을 피우고 있다는 생각에 선우(김희애)는 이 식당에서 남편의 휴대전화로 “아름다운 밤에 또 보고 싶어 희원.”라고 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3915, 25924, 16835, '창공관의 외관은 지선우(김희애 분)가 부원장으로 근무하는 \'가족사랑병원\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3916, 28382, 16835, '김희애가 남편의 생일에 떡을 주문한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3917, 29245, 16835, '7회에서 선우(김희애)는 퇴근 후 이 식당을 들르다 우연히 제혁(김영민)을 만난다. 그런 다음 그는 음식을 다 먹지 않고 떠납니다. 한편, 우연히 지나가던 윤기(이무생)는 혼자 술을 마시는 선우를 본다. 그는 그녀와 합류하고 2년 전 고산을 떠나 새 가족과 함께 돌아온 태오(박해준)에 대한 이야기를 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3918, 25893, 16835, '선우(김희애)는 16화에서 아들 준영(전진서)을 따라다니며 이 식당에서 친구들과 재회한다. 같은 에피소드에서 선우는 전 남편 태오(박해준 분)의 삶이 어떻게 비참해졌는지 안타까워하며 아들과 함께 이 식당에서 저녁식사를 함께 하자고 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3919, 29246, 16835, '김희애와 이무생이 데이트하던 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3920, 26189, 16835, '9회에서 태오(박해준)와 다경(한소희)이 갑자기 도착하자 선우(김희애)와 윤기(이무생)가 이 식당에서 저녁을 먹으며 대화를 나누고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3921, 29247, 15240, '1화에서 이 휴양지의 수영장은 이수겸(유키스 준)과 김정혜(이요원) 저택의 수영장으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3922, 25640, 15240, '5회에서 박승우(김사권)의 가구 공방을 지나며 짝사랑하지 않으려고 자책했다. 9회에서는 이미숙(명세빈)이 자신의 아들이 자살했다고 설명하는 인터뷰가 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3923, 26030, 15240, '8화에서 사회적 어벤저스 클럽의 적과 적 3인방 남편 이병수(최병모)와 배영표(정석용), 학교장 홍상만(김형일)은 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3924, 29248, 15240, '2회에서 홍도희(라미란)가 분장을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3925, 29249, 15240, '부암동 복수자들 예고편이 이곳에서 촬영되었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3926, 29250, 15240, '홍도희(라미란)가 생선을 팔던 장터가 이곳에 있다. 부암동 어벤져스 소셜클럽의 정기 만남의 장소가 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3927, 25635, 15240, '6회에서 이수겸(유키스 준)의 엄마 한수지(신동미)는 김정혜(이요원)을 만나 수겸이 와서 그들을 방해하기 전까지 정혜로 부터 돈을 얻을 수 있는 다른 거래를 제안합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3928, 24933, 15240, '1화에서 부암동 소셜 어벤져스 클럽이 창단되어 그들의 본부가 되는 곳이다. 두 사람이 두 번 만나는 김정혜(이요원) 누나의 갤러리는 이곳 2층 미술관이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3929, 24900, 15240, '1회에서 이미숙(명세빈)의 대학교수 남편이 그의 새 책을 축하하는 공개 간담회를 이곳에서 한다. 그 자리에 김정혜(이요원)도 나타나 미숙이 남편에게 학대를 받고 있는 것을 보고 복수하고 싶은지 묻는다. 부암동 사회복수를 향한 첫걸음.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3930, 24935, 15240, '3회에서 사회적 어벤저스 클럽의 적과 적 3인방 남편 이병수(최병모)와 배영표(정석용), 학교장 홍상만(김형일)은 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3931, 29251, 15240, '부암동 소셜 어벤져스 클럽의 세 여자들이 4회에서 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3932, 27120, 15240, '12회에서 이병수(최병모)와 백영표(정석용)이 수감된 장소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3933, 26211, 15240, '우리 부암동 소셜 어벤저스 클럽의 아이들인 이수겸(유키스 준), 김희수(최규진), 백서연(김보라)이 여기 어벤저스 소셜에서 학교를 다닙니다. 1화에서 보듯이 \'세빛고교\'로 나옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3934, 29252, 15240, '홍도희(라미란)는 아들 김희수(최규진), 딸 김희경(윤진솔)과 함께 살고 있다. 부암동 어벤져스 사교클럽의 단골 모임 공간이 되고 이미숙(명세빈)은 남편과 헤어진 뒤 딸 백서연(김보라)과 함께 이곳으로 이사한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3935, 27522, 15240, '이곳은 부암동 어벤져스 소셜클럽에서 김정혜(이요원)가 운영하는 \'BJ 스파\'로 등장한다. 에피소드 12에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3936, 25864, 15240, '드라마에서 김정혜(이요원)의 여동생이 운영하고 장소로 나온다. 그녀는 4화에서 그녀를 방문하고 같은 화에서 저녁 늦게 가족 만찬이 이곳에서 열린다. 정혜가 사생아라는 사실을 알게 되는 대목이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3937, 25017, 15240, '12회에서 이수겸(유키스, 유앤비 준)이 생모를 이곳으로 배웅하고 그녀에게 한 번만 어머니가 되어 아버지에 대한 진실을 말해달라고 간청합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3938, 26125, 15240, '12회에서 이병수(최병모)는 다음날 아침 고객을 만나기 위해 이곳에서 하룻밤을 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3939, 25607, 15457, '24회에서 김명하(김민규)가 벚꽃이 핀 가운데 나무그네에서 몽몽(엘키)을 만나 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3940, 26086, 15457, '김명하(김민규)는 이곳에서 공부하는 학생으로, 귀국 비행기에서 우연히 와인을 쏟은 교환학생 몽몽(엘키)과 \'친구\'가 되는 임무를 맡는다. 9화에서 다시 소개된 후, 그들은 그녀가 그에게 그녀의 사진을 찍어달라고 부탁하는 대학 호수 옆을 걷는다. 에피소드 11에서 그들은 학생 사무실 밖에서 만나는데, 그곳에서 그녀는 그에게 그녀의 뜨거운 물이 다 떨어졌다고 말하고, 그녀를 공중목욕탕으로 데려오라고 요청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3941, 27341, 15457, '판촉물을 성공적으로 받은 후 18회에서 김명하(김민규)와 몽몽(CLC의 엘키)이 여기로 향합니다. 첫 번째 시도에 발톱 게임에서 성공적으로 이겼습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3942, 26116, 15457, '18회에서 김명하(김민규)가 몽몽(CLC 출신 엘키)을 만난다. 그는 그녀가 2개의 비투비 포스터를 포함하여 판촉물을 수집하기 위해 미용실에 줄을 서도록 도와주었습니다. 몽몽은 기뻐서 놀란 명하를 끌어안고 노력에 대한 감사 인사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3943, 29253, 15457, '18회에서 김명하(김민규)가 달려와 몽몽(CLC 엘키)을 찾는 모습이 그려졌다. 아팠음에도 불구하고 그녀는 가게에서 무료 비투비 포스터를 받기 위해 줄을 서 있었습니다. 결국 명하는 명물길에서 기다리는 동안 그녀를 위해 줄을 섭니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3944, 25347, 15457, '46화에서 남수희(이승연)가 이곳으로 소풍을 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3945, 26250, 15457, '46회 남수희 (이승연) 산책 나갈 때 배경으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3946, 29204, 15457, '11회에서 교환학생 친구 몽몽(CLC 출신 엘키)을 위해 이 거리의 카페앞에서 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3947, 29254, 18411, '옥을태(이준)의 집 외경으로 촬영된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3948, 29255, 18411, '8회에서 방영된 레트로 분위기 파주 술집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3949, 29256, 18411, '9화에 나온 오묘한 분위기의 섬');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3950, 29257, 14872, '12회에서 서이경(이요원)과 이세진(유이)이 만나 게임을 하는 모습이 그려졌다. 수표를 수락할지 여부에 관한 모든 것을 이경이 싣는다. 이후 박건우(진구)와 합류해 세진을 변호한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3951, 24935, 14872, '1회에서 서이경(이요원)은 조폭과 함께 1층에서 러시안룰렛을 펼쳤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3952, 26744, 14872, '4화에서 서이경 (이요원)과 이세진 (유이)이 이 다리 아래로 와서 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3953, 29258, 14872, '7회에서 이세진(유이)이 갤러리S에서 해고된 후 아르바이트를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3954, 26080, 18809, '첫 번째 티저 예고편에서 볼 수 있는 궁전과 감옥인 지역.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3955, 26069, 18809, '1회에서 어린 왕세자 이태(박지빈)와 유연(신은수)의 첫 만남이 연못가에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3956, 26072, 18809, '이 궁의 일부가 본궁으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3957, 29259, 18809, '2회에서 사찰의 한장면으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3958, 25936, 18809, '1회에서 어린 왕세자 이태(박지빈)와 유연(신은수)이 연못가의 축제에서 처음 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3959, 25603, 18809, '16회 마지막회에서 이 연못이 궁궐의 일부로 등장하고 고민에 빠진 태태왕후 최가연(박지연)이 이곳에서 피난처를 찾는다. KBS2, 2022).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3960, 26712, 6102, '서현진과 이민기가 둘만의 깜짝 여행을 간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3961, 25026, 6102, '서현진과 이민기가 둘 만의 데이트를 즐긴 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3962, 25607, 6102, '서도재(이민기)가  4화에서 아침 조깅을 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3963, 25907, 6102, '신명희 시장(최광일)이 언론에 정진시의 향후 발전 방향을 언론에 알리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3964, 29260, 6102, '9회 이민기 가족 식사 장면에 등장한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3965, 26236, 6102, '이민기와 일행들이 벤치에 앉아 대화 한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3966, 25865, 6102, '9회에서 한세계(서현진)는 절친 유우미(문지인)와 류은호(안재현)가 지켜보는 가운데 이곳에서 무당의 굿을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3967, 26421, 6102, '8회에서는 서도재(이민기)와 한세계(서현진)의 이별을 두고 다투는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3968, 26256, 6102, '9회에서 한세계(서현진)는 매니저 유우미(문지인)와 함께 이곳으로 기도하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3969, 25827, 6102, '한세계(서현진 분)가 처음으로 인물 변신을 경험한 곳(\'유럽\' 여행 중 할머니 때문에)과 서도재(이민기 분)가 얼굴 인식을 못하는 사고를 당한 곳(분수가 있는 영어마을 부분)이 이 곳에서 촬영 되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3970, 24933, 6102, '5회에서 \'한세계(서현진)와의 데이트를 준비하면서 정말 데이트를 다 했느냐\'는 질문에 서도재(이민기)는 여자친구를 알아보지 못한 이곳에서 데이트가 잘못됐던 것을 떠올린다. 13화에서 채유리(류화영)는 여기서 기자와 만나 세계에게 놀림만 받고 라이벌인 한세계(서현진)와 뭔가 이상하다는 것을 암시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3971, 25870, 6102, '3회 음식 기부 행사가 펼쳐진다. 서도재(이민기)와 강사라(이다희)가 라이벌 모드인 가운데, 류은호(안재현)는 사라에게 다가가기 위해 온갖 노력을 다한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3972, 26151, 6102, '1화에서 한세계(서현진)는 서도재(이민기)의 명령에 저항하기 위해 출장길에 이곳을 방문한다. 그녀는 놀랍게도 비행기에서 그녀가 책을 읽고 있는 것을 본 책자 덕분에 여기로 그녀를 데리러 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3973, 29261, 6102, '11화에서 서도재(이민기)와 한세계(서현진)는 그들의 여행에서 이곳에서 하룻밤을 보냅니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3974, 25613, 6102, '6회에서 강사라(이다희)는  티웨이항공 일부 이사진들의 비공식 오찬 모임에 참석해 남성그룹으로부터 여성혐오 발언을 많이 듣고 최대한 빨리 휴가를 떠난다. 류은호(안재현)가 자신에 대해 어떻게 생각하는지 묻고, 신부가 되고 싶다고 이야기하는 모습으로 잠시 후 그녀가 돌아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3975, 25720, 6102, '4회에서 한세계(서현진)와 서도재(이민기)가 영화를 보러 오는 곳으로 등장한 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3976, 25941, 6102, '5화에서 한세계(서현진)가 이 대학에서 영화 오디션을 본다. 건물 밖으로 나와 꽃다발을 들고 자신을 기다리는 서도재(이민기)를 보자 그녀는 그의 품으로 달려든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3977, 27510, 6102, '8회에서 서도재(이민기)와 한세계(서현진)가 각자의 차를 타고 이곳에 커플로서 첫 비밀 만남을 갖는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3978, 26540, 6102, '예고편 1편은 이 건물 서쪽 출입구 앞 길가에서 촬영됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3979, 25659, 6102, '한세계(서현진)와 그녀의 좋은 친구이자 매니저인 유우미(문지인)의 만남의 장소가 되는 또 다른 포장마차 유우미(문지인)과 류은호(안재현)가 함께 술을 마시며 삶의 답답함을 토해내는 장소가 여기에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3980, 25791, 6102, '류은호(안재현)는 건물 내 카페에서 아르바이트를 한다. 3화에서 강사라(이다희)가 바닐라라떼를 먹으러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3981, 24941, 6102, '1화에서 서도재(이민기)와 한세계(서현진)가 출장 먹으러 왔다. 15회에서는 강사라(이다희 분)와 류은호(안재현 분)가 밥을 먹으러 찾아와 그녀에게 판사가 되고 싶다고 자신이 서울대 졸업생이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3982, 25335, 6102, '서도재(이민기)는 1회에서 비서가 한세계(서현진)의 행방을 알고 있다고 말하자 차를 타고 이 다리를 건넌다. 이에 도재는 이 다리를 반대 방향으로 건너가 유턴을 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3983, 24944, 6102, '5회에서는 한세계(서현진)와 서도재(이민기)의 어머니 임정연(나영희)이 이 식당에서 만나 아들을 사들이려 한다. 둘 다 6화에 다시 등장하고 정연은 세계에게 이번에는 아들과 데이트를 해달라고 애원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3984, 25797, 6102, '6회에서 변신한 한세계(김민석)와 서도재(이민기)가 경찰서에서 \'그녀\'를 태운 뒤 자신의 차를 타고 이 다리를 건넌다. 그녀는 그에게 화가 났고 그는 이 다리의 한쪽 끝에 차를 잠깐 세웠고 그들은 그것을 이야기했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3985, 29262, 6102, '선호그룹 사옥 카페로 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3986, 27227, 6102, '15화에서 한세계(서현진)가 자신을 고립시키는 집으로 비춰진다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3987, 26286, 6102, '5회에서 서도재(이민기)가 한세계(서현진)와 첫 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3988, 24913, 6102, '5회에서 저녁 식사를 마친 서도재(이민기)와 한세계(서현진)가 모두 조율된 첫 공식석상에서 이 산책로를 거닐고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3989, 26651, 6102, '4회에 이다희의 가족 만찬 장면에서 나오는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3990, 26140, 6102, '4회에 등장한 이민기, 서현진이 함께한 영화관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3991, 25957, 6102, '2회에서 변신한 한세계(서현진)가 혼자 밥을 먹으러 찾아온다. 5회에서는 서도재(이민기 분)와 세계가 처음으로 공식석상에 올라 모든 것이 조율된 상태로 점심 식사 후 데이트를 하며 진심 어린 대화를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3992, 27535, 6102, '이 센터 발치에 놓인 포장마차는 한세계(서현진)와 그녀의 절친한 친구이자 매니저 유우미(문지인), 류은호(안재현)의 만남의 장소다. 함께 술을 마시며 삶의 고충을 털어놓는다(특히 세계)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3993, 26170, 6102, '4회에 서현진이 친구들과 갔던 포장마차 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3994, 25781, 6102, '3회에서 서도재(이민기)가 할아버지 임회장(이문수)을 찾아간 \'병실\'이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3995, 26412, 6102, '15화에서 서도재(이민기)가 서울로 돌아올 때 이곳을 지나가다가 이 건물의 대형 광고 화면에서 한세계(서현진)를 처음 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3996, 25017, 6102, '티웨이항공 상무이사로서 서도재(이민기)가 근무하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3997, 26132, 14721, '이영오(장혁)가 신경외과 의사로 일하는 \'현성병원\'.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3998, 25882, 14721, '11회에서 계진성(박소담)과 이영오(장혁)가 이곳에서 농구 한두 판을 하게 됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (3999, 26030, 17159, '8회에서 이정경(박지현)은 송정희(길해윤) 교수를 이 식당에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4000, 26323, 17159, '10회에서 송아(박은빈), 정경(박지현), 이교수(백지원)는 이 식당에서 열린 송교수(길해연)의 파티에 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4001, 26566, 17159, '6회 이정경(박지현 분)이 이 술집에서 혼자 술을 마시면서 그녀는 병원에서 박준영(김민재 분)과 그의 엄마(김정영 분) 사이에 우연히 들었던 대화를 떠올린다. 술집 피아니스트가 자신에게 특별한 곡인 트뢰메레이를 연주하고, 준영의 상처받은 말이 뇌리를 스치기 시작해 눈물을 자아낸다. 11화에서 준영은 현호(김성철)에게 정경이 혼자 술을 마신다는 사실을 알려준다. 현호는 이 술집으로 가서 그녀와 함께 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4002, 26410, 17159, '6화에서 나문숙 회장(예수정)이 이 식당 박준영(김민재)에게 전화를 건다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4003, 27179, 17159, '1화에서 이 공연장의 외관은 서령대학교 개교 60주년 기념 음악회가 열렸던 장소로 등장합니다. 이 곳에서 박준영(김민재)는 피아노를 치고 채송아(박은빈)는 피아노를 치기로 했지만 지휘자에게 쫓겨납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4004, 25854, 17159, '한국으로 돌아온 저녁, 한현호(김성철 분)는 2회에서 친구 박준영(김민재 분)과 이 클럽 구내에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4005, 29268, 17159, '이 갤러리의 외관은 드라마에서 나문숙(예수정) 경후문화재단 이사장 집으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4006, 25797, 17159, '4화에서 박준영(김민재)은 이 다리를 건너는 버스를 타고 집으로 돌아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4007, 26402, 17159, '2, 3회에서 먹을 것을 챙겨 집에 가기로 한 채송아(박은빈)에게 박준영(김민재)은 이 식당 앞에 멈춰 서서 작별인사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4008, 26040, 17159, '4회에서는 박준영(김민재)이 채송아(박은빈)에게 저녁 식사를 청한다. 그리고 그들은 이 식당으로 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4009, 29269, 17159, '박준영(김민재)이 서울에 있을 때 위안을 찾는 장소. 3회에서 박준영이 채송아(박은빈 분)에게 오후에 이 궁에 가서 "궁터에서 해바라기와 논다"고 설명 하는 장면에서 묘사되는 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4010, 24951, 17159, '4회에서 박준영(김민재)과 채송아(박은빈)는 이 다리 근처 개울에서 신선한 공기를 쐬고 있습니다. 그리고 그녀는 윤동윤(이유진)과 어떻게 사랑에 빠졌는지 그에게 이야기합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4011, 24922, 17159, '6화에서 함께 이 거리를 걸을 때, 채송아(박은빈)는 박준영(김민재/래퍼 리얼.비)에게 그들의 소위 우정에 만족하지 않는다는 것을 알린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4012, 28424, 17159, '12화에서 이 교수(백지원)의 실내악 만찬이 열렸던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4013, 26244, 17159, '9회에서 한현호(김성철)은 그의 고등학교 친구를 이 식당에서 만나고, 박준영(박민재), 이정경(박지현)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4014, 26186, 17159, '밤에 아이스크림 데이트를 하던 박준영(김민재)과 채송아(박은빈)는 9화에서 이 산성 구간에서 서울시교육청 과학전시관 남산 별관으로 이어지는 계단에 주저앉는다. 그리고 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4015, 24913, 17159, '5회에서 박준영(김민재)이 이 길을 걷다가 채송아(박은빈)가 와 만나 이야기를 나누며 걸어가는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4016, 25864, 17159, '2회에서 나문숙(예수정) 회장이 채송아(박은빈)와 김해나(이지원)를 이곳으로 초대해 식사를 한다. 경호문화재단 인턴십 기간 동안 열심히 해달라고 부탁한다. 송정희 교수(길해연)의 생일파티도 4화 이 장소에서 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4017, 25017, 17159, '1~2회에서 박준영(김민재/래퍼 리얼비)과 채송아(박은빈)는 각자 친구들을 도착장에서 기다리고 있는 자신들을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4018, 26066, 15230, '18회에서 구버전 강하람(고아라)이 준의 팔찌를 쥐고 벤치에 앉아 숨진 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4019, 25797, 15230, '1화에서 블랙(송승헌)과 강하람(고아라)이 집으로 데려올 때 차를 타고 이 다리를 건넌다. 하람은 다리에서 뛰어내려 자살하려는 누군가의 뒤에 있는 검은 그림자를 본다. 블랙은 그녀를 믿지 않는다. 하지만 돌아오는 길에, 이 다리를 다시 건널 때, 그는 그 사람이 뛰어내리는 것을 봅니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4020, 25637, 14283, '극중에서 \'태민암병원\'이라는 이름으로 박지상(안재현 분)이 근무하는 곳으로 이번에는 주로 1층 로비에서 촬영했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4021, 26072, 14143, '늙어가는 영조(한석규)가 마지막회에서 이 연못 일대를 걷던 중 그의 손자 이산을 동반한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4022, 25076, 15092, '황시목과 김정본이 함께 간 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4023, 29167, 15092, '드라마에서 한여진(배두나)의 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4024, 29378, 15092, '3화에서 시목은 카페를 찾기 위한 사냥을 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4025, 29379, 15092, '박무성(엄효섭)의 집으로 김가영/권민아(김가영/권민아)가 살해된 범죄 현장이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4026, 29380, 15092, '황시목이 수사를 위해 방문했던 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4027, 25910, 15092, '3회에서 이창준(유재명)과 김우균(최병모)은 술을 마시러 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4028, 28287, 15092, '황시목이 동태찌개를 먹은 해물탕집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4029, 27321, 15092, '한여진, 황시목이 강진섭과 추격전을 벌인 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4030, 29381, 15092, '3화에서 영일예(이호재) 교수의 법무부 장관 임명식에 참석하기 위해 이창준(유재명) 차장(유재명)이 인하대학교로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4031, 29382, 15092, '검찰청 식당으로 나온 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4032, 29383, 15092, '황시목이 짜장면을 먹은 중식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4033, 25803, 13691, '8회에서 길충식(백성현)이 집에서 이 사우나로 도망친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4034, 29386, 13691, '(이민정)과 서윤재(공유)는 1회 결혼식에서 처음 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4035, 29387, 18974, '주인공 박창호(이종석)이 고등학교 장대 높이뛰기 선수로 활약하는 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4036, 25621, 18974, '검사 최중탁(장혁진 분)이 2회에 이곳 청우정에서 공지훈(양경원 분)을 만나 \'천재 사기꾼\' 빅마우스 포획에 대해 논의한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4037, 29388, 18974, '주인공들의 사건을 찾아 배회하던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4038, 26059, 18974, '주인공들의 과거씬에 나온 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4039, 29389, 18974, '주인공 박창호(이종석)가 사건의로를 받고 찾아간 구천교도소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4040, 25809, 18974, '주인공 박창호(이종석)이 지내던 주 교도소 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4041, 26521, 16081, '빅이슈(빅이슈)에서 한석주(주진모)의 딸 세은(서이수)이 수호되는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4042, 27161, 17559, '10화에서 바벨제약 피해자 가족의 차가 이 터널(\'양동지하차도\'라고 함)에서 쫓기는 모습이 포착됐다. 또 이곳에서 빈센조 카사노(송중기 분) 자신도 네 사람의 목숨을 앗아간 같은 히트남녀와 마주하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4043, 26652, 17559, '12회에서 장준우/장한석의 아버지의 별장입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4044, 29420, 17559, '빈첸조 카사노/박주형(송중기)과 홍차영(전여빈)은 이 카페에서 우상(양승원)의 변호사를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4045, 26672, 17559, '16회에서 박주형(송중기)은 길을 가다가 이 가게 바로 밖에 있는 붕어빵 노점으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4046, 29191, 17559, '8회에서 황민성(김성철)과 빈첸조 카사노/박주형(송중기)은 이 놀이동산(이하 \'무무랜드\')에서 하루를 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4047, 26291, 17559, '17화에서 상천구 초고층 개인을 위한 바벨탑 이벤트가 열리는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4048, 26419, 17559, '빈센조 카사노/박주형(송중기)은 어머니를 살해한 범인의 뒤를 바짝 쫓으며 이곳으로 향하는 돌담길을 질주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4049, 29421, 17559, '9회에서 빈센조 카사노/박주형(송중기), 남주성(윤병희)의 만남. 바벨제약 피해자 가족들이 식사를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4050, 25612, 17559, '빈센조 카사노/박주형(송중기), 홍차영(전여빈), 빈센조의 어머니 오경자(윤복인)는 16회에서 함께 사진을 찍고 잠시 차영이 엄마와 아들이 함께 시간을 보낼 수 있도록 자신을 변명한 후 이 정원을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4051, 29422, 17559, '조영운(최영준)은 비밀금고에서 배신 행위를 한 뒤 빈센조/박주형(송중기)과 나눈 대화를 떠올리며 13회에서 이곳에서 혼자 술을 마신다. 14회에서 본 플래시백에서는 정인국(고상호 분) 검사가 조씨가 홍채인식 장치를 잘못한 것에 대해 질책하기 위해 이곳으로 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4052, 29423, 17559, '7회에서 박주형(송중기)은 이 카페에서 바벨 그룹의 나덕진(나철)을 만나 금가 플라자에 대한 소식을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4053, 25117, 17559, '14회에서 빈첸조 카사노/박주형(송중기), 홍차영(전여빈)이 이 식당에서 검사 정인국(고상호)과 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4054, 29424, 17559, '18회에서 장준우/장한석(2PM 옥택연)의 급한 조립 메시지를 받은 바벨탑 주주들');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4055, 26670, 17559, '형(송중기)은 부패한 형사들을 이 버려진 건물에서 인질로 잡고 그의 요구를 따르지 않으면 가장자리에서 밀어내겠다고 위협한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4056, 29425, 17559, '8화에서 바벨그룹과 신광은행의 협약식이 열리는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4057, 29426, 17559, '5회에서는 홍차영(전여빈), 장준우(2PM 옥택연), 빈센조 카사노/박주형(송중기)이 짬뽕을 먹으러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4058, 29427, 17559, '9회에서 남동부 검사들과의 만남이 진행되는 동안 장준우(2PM 옥택연)가 이곳에서 햄버거를 먹으면서 몰래 귀를 기울인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4059, 29428, 17559, '7회에서 박주형(송중기), 홍차영(전여빈)이 양복을 사러 이 양복점에 온다. 차영이가 바벨화학을 상대로 한 심리에서 차영을 도운 후 재단사를 만들었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4060, 29429, 17559, '6화에서 홍차영(전여빈)과 빈센조 카사노/박주형(송중기)이 이 술집에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4061, 28097, 17559, '1회에서 장준우/장한석(2PM 옥택연)과 최명희(김여진)가 이 호텔 바에서 술자리를 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4062, 29430, 17559, '5화에서 홍차영(전여빈)과 빈첸조 카사노/박주형(송중기)이 뜨거운 논쟁을 통해 바벨화학에 대한 차기 행보에 대해 논의한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4063, 29431, 17559, '18회에서 조영운(최영준)은 깡패들에게 쫓기면서 이 지하도와 골목길을 미친 듯이 달리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4064, 27557, 17559, '4회에서 홍차영(전여빈)이 빈센조 카사노/박주형(송중기)을 만나 Babel Group과 Wusang Law Firm을 무너뜨리기 위해 함께 일하자고 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4065, 25662, 17559, '15회에서 가방 쇼핑을 한 후, 홍차영(전여빈) 이 카페에서 한잔하면서 가방을 어머니에게 직접 선물해야 한다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4066, 26136, 17559, '빈센조 카사노/박주형(송중기 분)과 검찰관 정인국(고상호 분)의 마지막 만남에서 이 식당 발코니는 물론 건물 외관이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4067, 27120, 17559, '앞서 교도소 내 살인사건에 연루됐던 강호철은 16회에서 이 건물에서 풀려나 최명희(김여진 분)에게 픽업된다. 19화에서는 장한석(옥택연)이 이곳에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4068, 29432, 17559, '18, 19회에서 박주형(송중기)과 장한서(곽동연)가 \'쌀의 힘\'으로 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4069, 26631, 17559, '11회에서 이 건물에서 정보를 교환했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4070, 25667, 17559, '4화에서 빈센조가 이탈리아에서 아메리카노는 구정물이나 다름없습니다라고 홍차영에게 말하는 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4071, 27472, 17559, '3회에서는 실종된 바벨약품 연구원의 연락처를 찾지 못한 홍차영(전여빈)과 장준우(2PM 옥택연)가 아이스크림을 먹으러 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4072, 26138, 17559, '13화에서 보듯 장한석(옥택연)이 바벨그룹 회장 취임식에 참석하기 위해 도착하는 \'바벨그룹\' 본사로 보인다. 준우의 사무실은 이 단지의 투 IFC 빌딩에 있는데, 예를 들어 에피소드 15에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4073, 26039, 17559, '17회에서 박주형(송중기)은 이 방에서 장준우/장한석(2PM 옥택연)에게 전화를 걸어 이렇게 말한다. \'체스 게임\'이 시작되었습니다. 이후 그와 홍차영(전여빈)은 이 방에서 원격으로 상황이 전개되는 것을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4074, 29154, 17559, '빈센조 카사노/박주형(송중기)은 18회에서 한승혁(조한철)과 만나 거래를 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4075, 26543, 17559, '바벨 화학 희생자의 사무실이 이곳에 있다. 5회에서 홍차영(전여빈)과 빈센조 카사노/박주형(송중기)가 이곳에서 그들을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4076, 26992, 17559, '13회에서 한승혁(조한철)이 이 장소에서 장한서(곽동연)와 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4077, 25797, 17559, '최종회 20회에서 장한석(옥택연)이 이 다리에서 최명희(김여진)에게 말을 건넨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4078, 29433, 17559, '12회에서 Vincenzo Cassano/박주형(송중기)이 클래식 음악을 들으며 이 장소에서 휴식을 취하고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4079, 25910, 17559, '2회에서 빈센조(송중기)는 조영은(최영준)과 중국인 투자자에게 이 빌딩의 시크릿룸에 대해 하는 사람은 특별 대우를 받는다는 사실을 말해주면서 싸움이 납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4080, 24946, 17559, '장준우(김여진 분)가 동료이자 상급 검찰총장 황진태(서진원 분), 서웅호(황태광 분)와 함께 저녁을 먹었던 장소로 보인다. 8회에서는 장한서(곽동연 분)와 신광은행장 황민성(김성철 분)의 만남이 이곳에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4081, 27227, 17559, '바벨그룹과의 계약을 파기한 길종문(홍서준) 박사는 9화에서 검찰 보호 요청의 일환으로 이 집으로 옮겨진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4082, 24910, 17559, '8화에서 황민성(김성철)과 빈센조 카사노/박주형(송중기)이 이 술집에서 만나 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4083, 24951, 17559, '20회 마지막회 김영호(강채민)가 친구들과 수다를 떨고 있는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4084, 29434, 17559, '10화에서 강철 눈의 빈센조 까사노/박주형(송중기)이 자신의 \'목표물\'을 찾아 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4085, 26407, 17559, '18화에서 장준우/장한석(옥택연)의 집이 침입자의 공격을 받고 그가 돌계단을 뛰어내려가는 모습의 외관으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4086, 29435, 17559, '10화에서 빈센조 카사노/박주형(송중기)은 이 건물 옥상에서 두 부패한 경찰에 대한 수색을 끝내고 그들과 싸운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4087, 25855, 17559, '19회에서 검사 정인국(고상호)이 진급을 축하하고 박주형(송중기)의 깜짝 방문을 받는 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4088, 29436, 17559, '홍차영이 다니는 대형로펌 우상의 건물');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4089, 25856, 17559, '20회에서 홍차영(전여빈)이 이 장소에서 열린 행사에서 1년 만에 빈센조 카사노/박주형(송중기)과 재회한다. 그들이 오랫동안 기다려온 두 번째 키스를 나누는 것도 여기에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4090, 28409, 17559, '이 건물은 Vincenzo(빈센조)에서 \'금가 플라자\'로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4091, 25912, 17559, '빈센조 카사노/박주형(송중기)은 3회에서 자신을 집으로 초대하는 변호사 홍유찬(유재명)의 메시지를 받고 이 고가도로를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4092, 26189, 17559, '2회에서 박주형(송중기)는 호텔 이 라운지에서 금가광장 계획을 동맹들에게 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4093, 25621, 17559, '9회에서 한승혁(조한철), 최명희(김여진), 장한서(곽동연) 바벨그룹 회장이 이곳에서 동남지검을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4094, 25864, 17559, '한승혁(조한철)은 이 곳 진입로에서 허 판사(차순배)와 허 판사가 모르는사이에 마주치게 되는데, 그를 집까지 태워다 줄 사람은 다름 아닌 빈첸조 카사노(송중기)이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4095, 25017, 17559, '1화에서 한국에 도착한 빈센조 카사노/박주형(송중기)은 그를 일상적으로 검문하는 경찰에게 제지당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4096, 29437, 17559, '3회에서 Vincenzo Cassano/박주형(송중기)이 바벨 그룹의 나덕진(나철)과 만나 타협을 하는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4097, 24915, 17559, '14화에서 남주성(윤병희)이 아트갤러리에서 비공개 이벤트 예약을 한 커플을 픽업한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4098, 29438, 17559, '5화에서에서 홍차영(전여빈)은 박주형(송중기)과 함께 지푸라기 로펌의 일원으로 돌아와 바벨화학과 우성에 대한 재판에 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4099, 29439, 17559, '17회에서는 어머니의 장례를 치르고 빈첸조 카사노/박주형(송중기), 홍차영(전여빈)이 이 캠핑장의 강바닥으로 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4100, 27565, 13974, '101-102화에서 웨딩드레스를 입었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4101, 26654, 13939, '이곳은 민치호(정동현)의 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4102, 25948, 17889, '김진아(소이현)은 9회에서 이곳에 잠들어 있는 돌아가신 아버지를 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4103, 25787, 17889, '9회에서 윤기석(박윤재)은 김진아(소이현)에게 신장의 대가로 돈을 달라고 제안하자 이 역에 뛰어들어 눈물을 흘린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4104, 26273, 17889, '1회에서 민희경(최명길)이 자녀들과 함께 이 교회에서 기도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4105, 29440, 18610, '6회에서 강회장(이덕화 분)과 강태무(안효섭 분)가 취임하는 호텔 \'유니온\'으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4106, 29441, 18610, '9회 미국 출장 중 멤버들 라운지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4107, 27069, 18610, '1화에서 볼 수 있듯이 이 곳은 강태무(안효섭) 가족의 집입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4108, 26721, 18610, '신하리(김세종)는 5회에서 강태무(안효섭)를 만나기 위해 \'금희\'로 이 카페에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4109, 26325, 18610, '신하리(김세정)와 강태무(안효섭 분)는 이 다리에서 서로를 만나기 위해 달려가 마침내 9화에서 또 다른 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4110, 24931, 18610, '5회에서 진영서(설인아)는 이 인기있는 동네 식당에서 저녁을 먹기로 결정했습니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4111, 29442, 18610, '1회에서 강태무(안효섭)와 신하리(김세정)는 강회장(이덕화)을 따라 팥빵을 판매하는 길가 식당으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4112, 25648, 18610, '진영서(설인아 분)와 차성훈(김민구 분)과 차성훈(김민구 분)을 만나 사업 제안');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4113, 25649, 18610, '3회에서 처음으로 강다구 회장(이덕화)을 만나기 위해 이 식당에 강태무(안효섭)가 신하리(김세종)를 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4114, 25908, 18610, '11회 에서 입원 중인 강태무(안효섭)이 치료받는 병원으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4115, 25827, 18610, '3화 회상 장면에서 뉴욕에서 신하리(김세종)와 강태무(안효섭)의 첫 만남이 버스킹 바이올리니스트를 보며 이 건물 밖에서 벌어지는 모습이 그려진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4116, 25635, 18610, '2회에서 진영서(설인아)는 신하리(김세정)을 속여 강태무(안효섭)과 이곳에서 만나게 합니다. 그는 \'가짜\' 영서와 직접 만나게 되고 다른 사람을 사칭하고 시간을 낭비한 것에 대해 그녀에게 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4117, 26200, 18610, '2회에서 이곳에서 만나 가짜 소개팅을 논의하고, 영서는 이전에도 부인했지만 태무의 소개팅 상대가 누구인지 알고 있음을 무심코 밝힌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4118, 24932, 18610, '9회에서 차성훈(김민규)은 진영서(설인아)를 이 식당으로 초대해 고아원 원장인 어머니(김정영)를 소개했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4119, 24934, 18610, '5화에서 신하리(김세종)는 \'금희\' 역으로 이 장소 식당 밖에서 강태무(안효섭)와 마주치고 그가 그녀의 실명으로 그녀를 부를 때 충격을 받습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4120, 24901, 18610, '9회에서 강다구 회장(이덕화 분)이 가장 좋아하는 일일 드라마의 촬영 장소 중 하나로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4121, 29443, 18610, '드라마에서 일하는 \'고푸드\' 본사로 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4122, 25720, 18610, '신하리(김세종)는 이 호텔의 스카이라운지 레스토랑에서 강태무(안효섭)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4123, 25953, 18610, '1회에서 긴장한 신하리(김세정)는 친구 진영서인 척하면서 이 호텔에 방을 예약한 강태무(안효섭)의 소개팅을 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4124, 29444, 18610, '1회에서 신하리(김세종)와 진영서(설진아)가 만나 식사를 하는 모습');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4125, 26142, 18610, '11화에서 진영서(설인아)의 아버지(이기영)는 남자친구 차성훈(김민규)과 함께 딸을 이 식당으로 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4126, 25659, 18610, '6회에서 차성훈(김민구)이 술에 취한 진영서(설진아)를 따라 귀가하면서, 그에게 술에 취해 자신의 감정을 고백하고, 결국 키스를 하게 되는 것이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4127, 26399, 18610, '신하리(김세정)는 1회에서 자신과 진영서(설진아 분)가 이 카페에 있는 동안 엑소시즘을 이용해 재벌 데이트를 겁주던 과정을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4128, 26137, 18610, '강다구(이덕화 분)는 11화에서 신하리(김세정 분)에게 이 호텔 \'부페식당\'에서 만나자고 하고 손자 강태무(안효섭 분)와의 관계를 묻는다. 하지만 그녀가 자신을 설명하기 전에, 그는 그녀에게 관계를 끝내라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4129, 29445, 18610, '태무(안효섭)와 그의 비서 차성훈(김민규)과 함께 신하리(김세종)와 진영서(설인아)가 자주 찾는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4130, 29446, 18610, '8에서 식사를 위해 여기에서 식사를 하고 그녀는 청구서의 비용을 보고 충격을 받습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4131, 26002, 18610, '8회에서 강태무(안효섭)는 신하리(김세종)에게  이곳에서 만나자고 요청하고, 식사를 위한 것인 줄 알고 혼란스러워한다. 대신에, 그들은 회전목마와 범퍼카에서 재미있게 논다. 하리는 또한 그녀와 그의 부모님에 대한 어린 시절의 기억을 공유하는 태무를 위해 옥수수 개를 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4132, 29447, 18610, '강태무(안효섭)가 신하리(김세정) 일행과 함께 이 식당에서 비즈니스 만찬을 하는 모습이 9회에서 방송된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4133, 24944, 18610, '4화에서 강태무(안효섭)가 신하리(김세종)를 가짜 결혼기념일 데이트를 위해 이 식당으로 데려온다. 나중에, 태무가 하리를 존경하듯이, 그들은 밖으로 나가 아름다운 불꽃놀이를 감상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4134, 25797, 18610, '11화에서 영서는 진영서(설인아)의 아버지(이기영)와의 만남을 마치고 이 다리를 건너던 중 차성훈(김민구)의 차량 장갑실에서 약혼반지를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4135, 26577, 18610, '11화에서강태무(안효섭)는 신하리(김세정)를 찾기 위해 이 곳에 차를 잠시 세워둔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4136, 24922, 18610, '10화에서 강태무(안효섭)와 신하리(김세정)는 이 거리의 남쪽에서 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4137, 25621, 18610, '10회에서 신하리(김세종)가 비 오는 날 강태무(안효섭)를 데리고 전통한식집에서 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4138, 25781, 18610, '3회에서 신하리(김세종)는 이 방에서 강태무(안효섭)와 차성훈(김민규)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4139, 25017, 18610, '1화에서 강태무(안효섭)와 그의 비서 차성훈(김민규)이 공항에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4140, 27220, 18610, '최종회 12회에서 강태무(안효섭)가 신하리(김세정)에게 이 산책로에서 청혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4141, 27222, 18610, '2회에서 진영서(설인아)와 강태무(안효섭)가 만나 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4142, 29448, 18610, '1회에서 강태무(안효섭)는 이 카페에서 신하리(김세종)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4143, 25848, 12878, '1회에서 오수재(서현진)는 TK 로펌에서 강등된 후 이 대학의 법대 교수로 임명된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4144, 26166, 13643, '이 장소의 웨딩 가든은 정하나(임윤아)가 어머니와 함께 머물렀던 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4145, 26485, 13643, '2~3화에서 대학생 여섯 친구 정하나(임윤아), 서준(장근석), 이선호(김시후), 황인숙(황보라), 김창모(서인국), 백혜정(손은서)이 이곳으로 당일치기 여행을 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4146, 25848, 13643, '4화에서 대명캠퍼스는 \'한국대학\'으로 등장한다. 모두가 1970년대 대학에 진학하는 곳이자 서인하(장근석 분)가 김윤희(윤아 분)에게 3초 만에 빠져드는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4147, 29449, 13643, '서인하(장근석), 이동욱(김시호), 김창모(서인국), 백혜정(손은수), 황인석(황보라)는 이곳에서 어울려 놉니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4148, 25953, 13643, '10회에서 서준(장근석)이 이 호텔에 체크인했다. 더 이상 정하나(윤아/임윤아)와 연락이 닿지 않자 잠옷 차림으로 호텔을 뛰쳐나와 택시를 부른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4149, 24904, 13643, '유기농 카페 제너럴닥터로 등장하는 이곳은 서준(장근석)의 사무실이자 정하나(윤아)의 정원이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4150, 26095, 13643, '서준(장근석)과 정하나(윤아)는 11회에서 이곳에서 만나 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4151, 26285, 13643, '14회에서 김윤희(김미숙)와 서준(장근석), 정하나(소녀시대 윤아)의 모습이 그려졌다. 서로의 연인이 아닌 부모의 자식으로 마주하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4152, 26061, 13643, '어린 김윤희 (임윤아)가 1970년대에 살고 있다. 예를 들어 서인하(장근석 분)가 결핵으로 인해 피를 토하며 일기를 읽은 것에 대해 사과하는 4화에서 그녀의 집을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4153, 25655, 12964, '9회에서 백수영(유인영)이 노고진(김재욱)과 이신아(크리스탈 정)를 이 식당에 초대해 저녁식사를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4154, 26030, 16434, '44, 45회에서 도진우(오민석)는 이 곳으로 옛 시아버지 김영웅(박영규)과 처제 김연아(조유정)는 물론 김청아(설인아)와 구준휘(김재영)까지 초대해 식사를 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4155, 29450, 16434, '구준휘(김재영)와 김청아(설인아)가 데이트를 위해 카페를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4156, 29451, 16434, '자신도 모르는 사이 설아의 뒤를 이은 전남편 도진우(오민석)는 이 공원에서 \'친구\'와 마주하고 태랑은 전남편에게 털어놓기 위해 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4157, 25939, 16434, '도진우(오민석)가 그녀를 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4158, 29452, 16434, '이 식당은 설아(조윤희)와 청아네가 운영하는 작은 식당 \'영웅 치킨\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4159, 25738, 16434, '김청아(설인아)와 구준겸(진호은)은 이 역을 만남의 장소로 꼽았다. 이 역으로 가는 기차 안에서 청아는 구준휘(김재영 분)를 만나 함께 기차에서 내리라고 강요하고, 청아는 마지막 1분 만에 가까스로 기차에 오른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4160, 26320, 16434, '8화에서 구준휘(김재영)가 공부하는 \'옥스퍼드 대학교\'로 등장하며, 그의 형 고(故) 구준겸(진호은)의 편지를 받는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4161, 29453, 16434, '이 아파트 단지의 외관은 드라마에 나오는 준휘(김재영)의 가족 거주지로 등장한다. 시리즈에서 여러 번 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4162, 29454, 16434, '설아(조윤희)가 변호사를 만나 다음과 같은 조언을 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4163, 29455, 16434, '4화(전체 2회)에서 김설(조윤희)이 아나운서로 입을 옷을 이 가게에서 찾는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4164, 26735, 16434, '이 식당의 벨레즈자 방에서 청아(설인아)는 판사 홍(나영희)에게 저녁을 대접하고, 크리스마스 선물로 겨울 장갑을 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4165, 29456, 16434, '6회에서는 설아가 교회 밖에서 전단지를 나눠주는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4166, 29457, 16434, '64회(32회)에서는 김청아(설인아)와 강시월(이태선)이 이곳에서 만나 과거 형사 사건에 대해 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4167, 29458, 16434, '영웅(박영규)과 강시월(이태선)이 아르바이트를 하는 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4168, 24939, 16434, '구준휘(김재영 분)와 김청아(설인아 분)는 16회에서 1년간 미국으로 떠나기 전 이 집 앞 거리에서 작별을 고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4169, 24905, 16434, '13회에서는 구준휘(김재영)와 김청아(설인아)가 이 공원에 찾아와 고장난 카메라의 상환 계약에 대해 이야기를 나눈다. 청아는 준휘에게 카메라를 할부로 달라고 애걸할 수 있는 윗집 사람을 아느냐고 묻는다. 그녀는 우연히 차강수 비서실장이 된 백림에게 과일 바구니를 건넨 사람을 기억한다. 준휘는 사촌 도진우(오민석 분)가 교통사고를 당했다는 사실을 알게 된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4170, 27179, 16434, '48회(전체 24회)에서 구준휘(김재영)는 김청아(설인아)를 데리고 \'매그넘 인 파리\' 전시회(전시기간:2019년 9월 25일 - 2020년 2월 9일)를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4171, 26132, 16434, '97회, 98회(49회)에서는 \'선림대학교 의료원의 설정\'이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4172, 25796, 16434, '선우영애(김미숙 분)는 1화에서 심사위원으로 이 법정에서 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4173, 26541, 16434, '구준휘(김재영)의 회사가 주최하는 \'클라이밍 빅 매치\'가 열리는 곳이 바로 이 공원입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4174, 25929, 16434, '구준휘(김재영)는 김청아(설인아)를 \'클라이밍 빅매치\' 경쟁자로 착각하고 여기로 끌고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4175, 26285, 16434, '45화(23화)에서 김영웅(박영규)과 김연아(조유정)가 저녁식사 후 이 식당에서 차를 마시는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4176, 27318, 16434, '김설아(조윤희)가 일하는 방속국이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4177, 25882, 16434, '39화(전체 20화)에서 김설아(조윤희)가 엄마 선우영애(김미숙)와 이 공원 한켠에서 진솔한 대화를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4178, 26499, 16434, '구준휘(김재영)는 김청아(설인아)를 이 다리 밑으로 몰아넣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4179, 25668, 16434, '45화에서(전체 23화)문태랑(윤박)에게 집까지 태워다 주겠다고 제안한 도진우(오민석)는 김설아(조윤석)에 대해 태랑과 맞서기 위해 이 다리 발치에 멈춰달라고 기사에게 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4180, 25347, 16434, '40회(전체 20회)에서 김청아(설인아)와 구준휘(김재영)의 첫 데이트를 위해 이 공원을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4181, 29459, 16434, '50화에서 강시월(이태선)이 이 건물 앞에서 판사 홍(나영희)과 부딪친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4182, 24948, 16434, '65회에서는 김청아(설인아)와 구준휘(김재영)가 구준겸(진호은)의 죽음과 강시월(이태선)의 연관성에 대한 논의를 이어간다. 이어 청아는 자신이 위에서 보고 있는 풍경을 높이 평가하며 앤 셜리의 말을 인용하며 준휘에게 좋은 추억으로 남을 것이라고 말했다. 그 장면은 준휘가 청아를 차버린 그날 밤을 잊어달라고 부탁한 후 청아와 포옹하는 것을 끝냈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4183, 25781, 16434, '3화(전체 2회)에서 도진우(오민석)와 그의 어머니 홍화영(박해미)이 이 술집을 찾아온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4184, 29460, 13418, '서혜진(박주미)은 이 박물관에서 큐레이터로 일하고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4185, 24965, 16633, '둘리커플 만남의 시작이 된 패러글라이딩 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4186, 27344, 16633, '11-12회에서 이 사우나는 리정혁(현빈)의 4남 표치수(양경원), 박광범(이신영), 김주묵(유수빈), 금은동(탕준상)과 정만복(김영민)이 서울에 머무는 동안 그들의 숙소가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4187, 24897, 16633, '시누이 도혜지(황우슬혜)는 남편이 가문의 회사를 상속받을 수 있도록 바라는 한편, 세리의 비서 홍씨(고규필)과 동시에 그녀가 가능한 한 빨리 발견되기를 기도합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4188, 29461, 16633, '둘리커플이 대화를 나눈 아찔한 높이의 하늘다리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4189, 27541, 16633, '둘리커플이 함께 다녀간 평양의 고급 호텔');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4190, 25871, 16633, '이 식당의 외관은 1화에서 윤세리(손예진)가 남자친구인 차상우(정경호)와 저녁을 먹으러 가는 곳으로 등장한다. 세리-상우 데이트 파파라치가 찍힌 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4191, 27613, 16633, '윤세리(손예진)가 운영하는 뷰티 회사 지점입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4192, 28421, 16633, '12회에서 금은동(탕준상)이 역 3번출구에서 리정혁(현빈)을 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4193, 26540, 16633, '이 건물은 \'세리의 선택\' 사옥 중 하나로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4194, 29462, 16633, '세리 회사 홍보팀장님 닭 다리 뜯던 단골 치킨집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4195, 25909, 16633, '12회에서 오매니저(윤상훈)는 같은 목표를 공유하는 조철강(오만석)과 윤세형(박형수), 고상아(윤지민) 부부를 이 식당으로 데려오려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4196, 27227, 16633, '3화에서 구승정(김정현)이 북한에서 임대하는 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4197, 29463, 16633, '북한의 고위층 여성만 가는 비밀스러운 양장점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4198, 29198, 16633, '10회에서 이 식당 앞에는 표치수(양경원), 박광범(이신영), 김주먹(유수빈)의 네 명의 부하 리정혁(현빈)이 있다. 그리고 금은동(당준상)과 정만복(김영민)은 영화에서 북한 첩보원으로 카메오로 등장한 북한의 비밀요원 동구(김수현)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4199, 25864, 16633, '10화에서는 북한 스포츠팀이 이 호텔에 도착한다. 세리가 운영하는 호텔로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4200, 29464, 16633, '탈북 성공한 리정혁이 처음 만난 남한 바닷가');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4201, 26456, 16633, '이 곳은 등장인물 윤세리(손예진)의 할아버지의 저택으로 나오는 장소이다. 1화에서 윤회장(남경업)이 자녀들을 모두 이곳에 불러 가족 만찬을 하는 장면의 저택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4202, 27916, 16633, '저세상 멋짐 리정혁 동무 수트 입고 액션하던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4203, 29465, 16633, '세리가 하늘에서 불시착해 리정혁을 만난 숲');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4204, 29466, 16633, '4회에서 윤세리(손예진)는 리정혁(현빈)처럼 대령의 아내의 생일파티를 떠나고 있다. 세리가 어둠 속에서 어떻게 안전하게 집에 갈 수 있을지 걱정하면서 두 번째, 세 번째로 자전거를 타고 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4205, 29467, 16633, '세리가 \'찬 바람이 불면~\'을 열창했던 억새밭');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4206, 29207, 16633, '정혁네 부대원들과 세리가 재회 후 눈물 쏟던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4207, 29468, 16633, '서단♥구승준 커플이 키스를 나눈 무지갯빛 다리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4208, 26636, 15198, '34회에서 온정선(양세종)이 이 역 바로 옆 공항버스 정류장에서 공항으로 가는 버스를 타려고 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4209, 29469, 15198, '28회에서 박정우(김재욱)를 만나 감사 인사를 전하고 수제 유자차를 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4210, 25640, 15198, '17회에서 이현수(서현진)와 지홍아(조보아)가 만나 이런저런 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4211, 26192, 15198, '31화에서 현수의 어머니 박미나(정애리)가 운영하는 이 건물의 일부가 병원 로비로 등장한다. 박정우(김재욱)가 수술이 잘 되었음을 확신하기 위해 들르는 모습이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4212, 27514, 15198, '1화에서 이현수(서현진)는 그녀에게 전화를 걸어 목걸이를 입어보게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4213, 26522, 15198, '38화에서 보던 박정우(김재욱)의 사무실이 있는 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4214, 25908, 15198, '최원준(심희섭)은 지홍아(조보아)가 사고를 당해 이곳에서 치료를 받고 있다고 말한 뒤 이곳으로 달려오지만, 그는 그녀가 멀쩡하다는 것을 발견한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4215, 25829, 15198, '2화에서 박정우(김재욱)의 사무실로 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4216, 29470, 15198, '온정선(양세종)은 이 카페 앞에서 이현수(서현진)의 전화번호를 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4217, 25926, 15198, '34회에서 볼 수 있듯이 이 공원은 현수의 아파트와 마주하고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4218, 29471, 15198, '5회에서 현수(서현진)는 실직한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4219, 27154, 15198, '11회에서 이현수(서현진)와 온정선(양세종)이 이곳에서 조깅을 하다가 굿스프 스태프와 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4220, 25335, 15198, '3회에서는 이현수(서현진)가 절친 지홍아(조보아)의 컨버터블을 타고 이 다리를 건너며 김을 푼다. 5화에서 박정우(김재욱 분)는 현수를 부르는 동안 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4221, 25668, 15198, '7회에서 온정선(양세종)과 이현수(서현진)가 조깅 후 다리 밑에서 스트레칭을 한다. 그 후 그들은 앉아서 술을 마시고 현수는 사랑에 대한 그녀의 견해를 설명하기 위해 Amy Winehouse 노래를 연주합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4222, 25347, 15198, '7화에서 이곳은 이현수(서현진)가 온정선(양세종)에게 사귀고 싶지 않다고 말하는 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4223, 29472, 15198, '온정선(양세종)은 친구 지홍아(조보아)와 최원준(심희섭)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4224, 29204, 15198, '9회에서 이현수(서현진)와 저녁 식사를 마치고 집으로 돌아오는 길을걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4225, 29473, 15198, '2회에서 이현수(서현진)는 런닝 그룹을 놓쳐 이 건물의 돌담에 핀 꽃 앞에 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4226, 25670, 15198, '29회에서 박정우(김재욱)와 유영미(이미숙)가 카페 2층에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4227, 25945, 15198, '1화에서 런닝 그룹에 참여할 때 온정선(양세종)과 이현수(서현진)가 이 앞에서 인사를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4228, 26214, 15198, '26화에서 이현수(서현진)는 자전거를 타고 지유가오카 카페 삼청점을 지나 온중선(양세종)의 집으로 달려갔다. 왜냐하면 그가 울고 있었기 때문이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4229, 29474, 15198, '26회에서 그녀가 울고 있는 온정선(양세종)의 집으로 달려가는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4230, 25890, 15198, '1회에서 달리기 조에 참가하다 숨을 헐떡이는 이현수(서현진)는 이 저택의 입구 앞에서 멈춰 감탄한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4231, 29475, 15198, '온정선(양세종)의 식당 \'굿 스프\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4232, 25340, 15198, '온정선(양세종)과 이현수(서현진)가 비를 피해 이곳을 찾는 배경으로 이 고등학교의 문이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4233, 25017, 15198, '7화에서 온정선(양세종)이 이곳에서 프랑스로 출국하고 마지막으로 이현수(서현진)에게 전화를 시도한다. 그녀는 받지 않고 그들의 첫 \'사랑\'은 이렇게 끝납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4234, 24982, 15198, '온정선(양세종)과 이현수(서현진)는 4회에서 당일치기로 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4235, 28648, 15198, '16회에서 온정선(양세종)과 이현수(서현진)는 이 곳으로 여행을 갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4236, 29476, 14716, '이 웨딩홀은 나민수(고세원)와 이은희(김지영)의 직장이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4237, 29477, 14716, '여기에서 나민수(고세원)는 이해인(공다임)의 아버지에게 만나자고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4238, 25668, 14716, '26회에서 나민수(고세원)가 이은희(김지영)를 데려가는 데이트 현장은 바로 여기다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4239, 28107, 13165, '조미수(이유리)와 한영민(이정진)이 우연히 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4240, 26990, 13165, '36회에서 조미수(이유리)는 한영민(이정진)을 이 건물에서 열리는 서울시향 공연에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4241, 25654, 13165, '31화에서 장현우(이상윤)와 조미수(이유리)가 어머니와 함께 카페를 다녀온 후. 이 거리를 드라이브한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4242, 25719, 13165, '한영민(이정진)의 방에는 이 건물의 모형이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4243, 26093, 13165, '상심한 한영민(이정진 분)은 50회에서 이 공원으로 차를 몰고 가다가 강가에서 가까운 곳에서 사고가 난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4244, 29478, 13165, '한영민(이정진)과 조미수(이유리)는 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4245, 29479, 13165, '한영민(이정진)의 약혼자인 민서영(오승현)은 이 단지의 아파트에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4246, 29480, 13165, '2화에서 장현우(이상윤)가 어머니의 멋진 스포츠카를 타고 조미수(이유리)를 태우고 차로 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4247, 26242, 13165, '39회에서 한영민(이정진)은 연인으로 함께 할 수 없다는 사실을 알게 된 후 조미수(이유리)를 (지금은 문을 닫은) 엘페 레스토랑으로 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4248, 25017, 13165, '132회 최종회에서 장현우(이상윤)가 이곳을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4249, 26154, 13165, '15회에서는 한영민(이정진 분)과 차를 마시며 대화를 한 뒤 조미수(이유리 분)가 이 공원에 와서 장현우(이상윤 분)와 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4250, 26103, 17286, '이정환(고경표)이 묵는 모텔로, 차주은(소녀시대 서현)은 9화에서 그녀를 구한 후 그를 따라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4251, 27302, 17286, '정복기(김효진)는 변호사 오현을 만나기 위해 이 카페에 온다. 오현경(차수연)은 11화에서 차주은(서주현)을 대리하도록 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4252, 29481, 17286, '8화에서는 이 건물 지하에 이정환(고경표)과 최윤석(장원혁)이 버려진 카페에 새로운 은신처를 마련했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4253, 29482, 17286, '차주은(소녀시대 서현/서주현)은 1화에서 이 카페에서 친구들과 친구들과 어울리고 새 휴대폰을 보여줍니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4254, 25908, 17286, '1차 예고편에서 정복기(김효진)와 김재욱(김영민)이 함께 휴일을 맞아 이 비행기를 타고 해외로 간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4255, 25830, 17286, '10회에서 정복기(김효진)가 이 카페에 들어와 실수로 국세청 배정홍에게 스무디를 흘린다. 그녀는 보상을 하기 위해 그에게 그녀의 전화번호를 주겠다고 제안했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4256, 29483, 17286, '차현태(박성근)는 1회에서 유명한 설교자 행세를 하며 이 교회 건물로 끌려온다. 대본을 잃어버렸음에도 불구하고 격앙된 설교를 하고, 이후에도 많은 설교가 이어지며 교인들이 교회 건축 기금에 아낌없이 기부할 것을 독려한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4257, 25613, 17286, '2010년을 회상하면, 차주은(서주현)은  1회에서 부모님 차현태(박성근), 김미숙(송선미)과 함께 이 식당에서 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4258, 29484, 17286, '5회에서 정복기(김효진)가 이 출구에서 \'호텔\'을 나서는 모습이 이정환(고경표)의 모습으로 포착됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4259, 29485, 17286, '우석호(김바다 분)는 꽃을 사러 꽃집을 찾아오다가 차주은(소녀시대 서현)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4260, 26037, 17286, '이곳은 이중환(고경표)이 다니는 회사인 GK테크놀로지의 본사다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4261, 26038, 17286, '차주은(소녀시대 서현/서주현)이 우석호(김바다)와 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4262, 29486, 17286, '2회에서 이정환(고경표)과 차주은(소녀시대 서현/서주현)이 피자와 막걸리를 먹으러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4263, 25794, 17286, '2회에서 환(고경표)이 차주은(소녀시대 서현/서주현)과 함께 커피를 사러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4264, 29487, 17286, '김 단장(김민상)은 차를 타고 이 건물과 거리를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4265, 27119, 17286, '7회에서 이 터널은 박태주(유제희)가 한손(태원석)이 자신의 이름을 갖게 된 이야기를 들려주는 과정에서 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4266, 29488, 17286, '차주은(서주현)은 2회에서 출근 첫 날 점심을 먹으러 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4267, 26285, 17286, '15회에서 김단장(김민상)이 GK 회장(한태일)의 지시를 받기 위해 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4268, 29489, 17286, '차주은(소녀시대 서현/서주현)은 1회에서 이 지역의 길을 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4269, 25335, 17286, '12회에서 정복기(김효진 분)가 유병준 의원과 함께 탄 차가 이 다리를 건너간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4270, 24945, 17286, '6회에서 이정환(고경표)과 최윤석(장원혁)이 잠복하는 \'PC묘지\' 승합차가 꼬꼬닭 맞은편 이 길가에 주차돼 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4271, 29490, 17286, '10회에서 이정환(고경표)의 명령으로 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4272, 29203, 17286, '이정환(고경표)과 차주은(소녀시대 서현/서주현)이 오토바이를 타고 이 다리를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4273, 29491, 17286, '8화에서 김 부장(김민상)의 부하가 한손(태원석)을 만나기 위해 이 식당에 찾아와 지케이테크놀로지에 반항하고 질문하지 말라고 경고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4274, 26214, 17286, '2회에서 차주은(소녀시대 서현/서주현)이 이중환(고경표)의 손을 잡고 이 골목으로 돌진한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4275, 24922, 17286, '2회에서 이정환(고경표)과 차주은(소녀시대 서현/서주현)은 야시장을 구경하다가 고등학교를 지나(안국역 방면으로 향함) 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4276, 29492, 17286, '한손(태원석)이 차주은(소녀시대 서현/서주현)을 위해 고른 옥상 아파트');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4277, 29493, 17286, '정복기(김효진)는 차주은(소녀시대 서현/서주현)을 만나기 위해 이 시장의 골목을 걸어 \'신비식당\'으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4278, 25913, 17286, '이정환(고경표)은 6화에서 김 총지배인(김민상)을 따라 이 건물 \'원호텔\'로 이동하며 CCTV 시스템에 원격으로 접근하기 위해 보안실에 잠입한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4279, 29494, 17286, '3화에서 이정환(고경표)은 원격으로 여러 팀을 가동해 표적을 쫓는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4280, 29495, 17286, '차주은(소녀시대 서현/서주현)은 2화에서 연락처에서 일부 파일을 데리러 이 식당("몽국할머니")에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4281, 25621, 17286, '7회에서는 김재욱(김영민)이 유병준(민지오) 의원을 만나 이곳에서 술을 마신다. 그리고 재욱은 그를 해린관으로 알려진 입구에서 배웅한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4282, 29496, 17286, '5회에서 만난 이정환(고경표)은 차주은(소녀시대 서현/서주현)의 뒤를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4283, 29497, 17286, '김재욱(김영민)은 이정환(고경표)의 마음이 얼마나 부드러운지 반성하며 정환이 이 시장의 \'D\' 빌딩에 불을 끄고 싶었을 때를 회상한다. JTBC 2020년 사생활/사생활/사생활 9회. 재욱은 "그럴 경우 GK가 위험에 처할 것"이라며 그를 막았다. 15회에서는 정복기(김효진)와 함께 소피아 정 의원과 함께 홍보차 유병준(민지오) 의원이 시장을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4284, 25017, 17286, '1화에서 차주은(소녀시대 서현/서주현)이 스튜어디스 역할로 공항을 성큼성큼 걸어가는 모습이 잠깐 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4285, 26121, 17286, '차주은(소녀시대 서현/서주현)이 은행 거래를 마친 뒤 두 사람을 만나기 위해 이곳으로 오는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4286, 29498, 17286, '이정환(고경표)이 자주 찾는 최윤석(장원혁)이 운영하는 컴퓨터 가게 \'PC 묘지\'로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4287, 25809, 17286, '1회에서 차주은(소녀시대 서현/서주현)이 이 감옥에서 출소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4288, 29384, 18681, '작곡가 우일(김주훈)이 운영하는 사옥으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4289, 24941, 18681, '이은수(한소희)와 한선우(박)의 친구인 결한(박훈)과 마리(박민정) 부부가 운영하는 식당이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4290, 29499, 18681, '이은수(한소희)는 이 꽃집을 지나다가 꽃다발을 발견하고 그녀의 길을 멈춥니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4291, 26464, 18681, '작곡가 우일(김주훈)은 작사가 이은수(한소희)를 이 식당에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4292, 29168, 18681, '한선우(ZE:A 박형식)는 이 카페 옆 계단을 걸어 내려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4293, 29507, 16086, '3회에서 윤재인(신예은)은 편의점 아르바이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4294, 26699, 16086, '3회에서 이안(GOT7 진영)이 이 건물 앞 횡단보도에서 차를 세웠다. 신호가 바뀌기를 기다리던 중, 2년 동안 보지 못한 윤재인(신예은)이 길을 건너는 것을 보았다. 그는 그녀에게 다가가기 위해 차에서 뛰어내렸지만, 그가 잘못된 사람에게 다가갔다는 것을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4295, 29508, 16086, '3화에서 이안(진영)이 운전 중 횡단보도에 정차해 차에서 이 다리를 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4296, 29509, 16086, '이 역은 연쇄 살인범의 은신처로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4297, 25335, 16086, '검사 강성모(김권)가 2회에서 이안( 진영)과 이야기를 나누며 이 다리를 따라 운전을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4298, 29510, 16086, '사이코메트리 그놈(사이)에서 윤재인(신예은), 이안(박진영/갓세븐 진영)이 하차하고 출발하는 버스 정류장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4299, 24927, 16086, '1회에서 강성모(김권) 검사가 LED 스크린을 통해 입장을 밝히고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4300, 25912, 16086, '윤재인(신예은 분)은 1회에서 서울역을 나와 이 다리에서 길가로 내려와서 이모의 트럭에 올라탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4301, 29511, 16086, '3회에서 은지수(김다솜)와 강성모(김권)가 아파트 단지에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4302, 25957, 16086, '9화에서 이 가게에 와서 둘 다 맞춤 오르골을 만든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4303, 26558, 17013, '문강태(김수현)가 오토바이를 빌려 오게되는 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4304, 25868, 17013, '아픈 아버지의 공격을 받은 고문영(서예지 분)은 멍하니 걷다가 몇 시간 뒤 큰 폭풍우 속에 심곡항 방파제 인근 해안도로에 다다른다. 문강태(김수현)가 오토바이를 빌려 이곳에서 그녀를 발견하는 장면은 4화에서 나온다. 이 길의 풍경은 이후 병원으로 출근할 때의 에피소드와 문영 형제가 캠핑카 휴가를 떠나는 마지막 에피소드에서도 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4305, 29512, 17013, '서울에서 직장을 잃었을 때, 자폐증이 있는 형 상태(오정세), 친구 재수(강기동)와 함께 그의 바닷가 고향으로 돌아간다. 그들은 강순덕(소꿉친구의 어머니)의 집으로 이사를 가서 2층과 반지하에 방을 빌린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4306, 27556, 17013, '이 카페의 외관, 정원, 내부 식사 공간은 OK정신병원으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4307, 26163, 17013, '9회에서 문강태(김수현)가 \'싸구려 게스트하우스\'(싸구려 민박)로 개명한 게스트하우스로 향하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4308, 26446, 17013, '고문영이 도로를 벗어나 고속으로 좁은 방파제를 콘크리트 테트라포드 쪽으로 몰고 내려가면서 비명을 지르며 죽이겠다고 위협하는 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4309, 26164, 17013, '고문영(서예지)의 저주성으로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4310, 29513, 17013, '문강태(김수현)와 고문영(서이제)이 바베큐 고기를 먹고 공원 산책을 하는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4311, 26198, 17013, '13회에서 가족사진을 찍고 문강태(김수현)와 그의 형 상태(오정세), 작가 고문영(서예진)은 아이들의 그림을 조사하기 위해 이 미술도서관을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4312, 25829, 17013, '문강태(김수현)가 책을 읽다가 부상을 당하고 이상인(김주훈)이 출판사를 찾아가는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4313, 26036, 17013, '식당은 조재수(강기둥)가 운영하는 피자가게로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4314, 29514, 17013, '1회에서 고문영(서예지)이 이 호텔에서 묵는 모습이 나온다. 이후 이 호텔 외부의 모습도 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4315, 26039, 17013, '1회에서 문영(서예지)이 이 방에 묵는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4316, 26232, 17013, '1회에서는 문강태(김수현)의 절친 조재수(강기둥)가 오토바이 등에 태워 집으로 데려다준다. 불행하게도, 한강 다리에서 자전거가 고장이 난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4317, 29515, 17013, '문강태(김수현)와 조재수(강기둥)가 집으로 돌아가는 길에 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4318, 26445, 17013, '1화에서 동화책 작가 고문영(서예지)이 혼자 카페에서 밥을 먹다가 엄마와 아이가 그녀에게 다가가 사인을 받는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4319, 29516, 17013, '문강태(문우진)가 고문영을 쫓는 모습이 회상된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4320, 28138, 14941, '26회 초반에 서지윤(이영애)은 이곳에서 로드(노영학)와 만나 민교수가 그림 \'금강산\'을 불태우지 않았을 가능성에 대해 논의한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4321, 26484, 14941, '2회 마지막 장면에서 이겸(송승헌)은 어린 이겸(양세종)과 어린 신사임당(박혜수)이 이곳에서 놀던 때를 회상하며 이겸에게 노래를 들려주었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4322, 27483, 13874, '양영웅(이종현 분)은 이곳에 안치되어 있으며, 3화에서 여동생 양아영(이세영 분)과 절친한 친구 이역호(최태준 분)의 방문을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4323, 26999, 13874, '최정우(곽동연)가 오는 체육관.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4324, 29521, 17366, '하경훈(남윤수)과 조은정(박하선)은 마지막 8회에서 그녀가 있던 카페 밖에 넘어졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4325, 25718, 17366, '최종화 8회에서 하경훈(남윤수)과 조은정(박하선)은  자신들이 있던 카페 밖에서 넘어진 뒤 이 공원에 앉아 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4326, 27302, 17366, '하경훈(남윤수)과 박하선(박하선)의 만남이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4327, 29522, 17366, '현진(엄지원)은 퇴원 후 남편 김도윤(박윤)이 산후조리원으로 가는 다리를 건너 이 다리를 건너게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4328, 26418, 17366, '1회에서 오현진(엄지원)이 출산 후 머무는 산후조리원인 \'세레니티 산후조리원\'으로 등장하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4329, 29523, 17366, '조은정(박하선)은 하경훈(남윤수)의 초대로 피아노 연주회를 관람하기 위해 이곳을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4330, 25335, 17366, '김도윤(윤박 분)은 1화에서 아내 오현진(엄지원 분)으로부터 출산 예정이라는 연락을 받고 동료들을 이 다리를 따라 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4331, 24944, 17366, '3회에서 오현진(엄지원)과 남편 김도윤(윤박)이 산후조리원을 나와 밤을 지새우고 이곳에서 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4332, 26679, 19119, '10회에서 김건우(이유진)가 이 카페에서 후용실(장희정)에게 자신의 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4333, 27435, 13934, '16~18회에서 차은상(박신혜)은 어머니와 함께 이 거리의 집으로 이사한다. 김탄(이민호)과 최영도(김우빈)가 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4334, 26652, 13934, '수학여행의 장소이며 유레이첼(김지원)이 머무는 장소입니다. 이곳은 또한 11회에서 수영장 사건이 일어나는 장소입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4335, 25718, 13934, '차은상(박신혜)과 김탄(이민호)은 이 공원에서 눈 오는 길을 걷습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4336, 25607, 13934, '11회에서 차은상(박신혜)과 김탄(이민호)은 캠핑을 마치고 아침 이 공원 메타세콰이어 골목길 단풍 나무 사이를 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4337, 26418, 13934, '이곳은 드마라 안에서 차은상(박신혜)의 어머니가 근무하는 집으로 김탄(이민호)의 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4338, 29524, 13934, '12회에서 이 골프장에서 여느 때와 같이 업무 회의가 있습니다. 김탄(이민호)은 동생 김원(최진혁)과 함께 제국그룹을 경영하고, 최영도(김우빈)는 아버지와 함께 제우스호텔그룹의 후계자로 나섭니다. 그러나 김원과 영도의 아버지가 골프를 치는 동안 김탄과 영도는 구두 대결을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4339, 25827, 13934, '16화에서 김씨 가족이 \'가족 나들이\'를 하는 식당으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4340, 25831, 13934, '드라마 등장인물들의 이야기가 그려지는 \'제국고등학교\'로 설정되어 등장한다. 드라마의 메인 촬영지 중 하나이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4341, 29525, 13934, '2013년 SBS \'상속자들\' 18회 김탄의 생일 회견에 가기 직전');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4342, 25953, 13934, '19회에서 이 호텔은 김탄(이민호)의 생일파티 장소로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4343, 29526, 13934, '17회에서 김탄의 동생 김원(최진혁)이 소개팅을 하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4344, 29181, 13934, '19화에서 윤창영(강민혁)의 아빠와 유지라헬(김지원)의 엄마가 혼자 카페를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4345, 29527, 13934, '차은상(박신혜)은 4회에서 말 그대로 잠을 자고 있는 반면 최영도(김우빈)는 신기하게 쳐다본다. 이후 두 사람의 공통된 만남의 장소가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4346, 29528, 13934, '박신혜가 낮잠을 자는 장소의 배경');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4347, 29529, 13934, '드림캐쳐샵으로 등장한다. 18회에서 차은상(박신혜)과 김탄(이민호)은 서로가 그리워 개별적으로 찾아오지만 말 한마디 없이 스쳐 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4348, 29530, 13934, '이 집의 외벽과 입구는 한국의 김탄(이민호) 집의 외벽으로 사용되었다.이곳은 차은상(박신혜) 어머니가 일하고 은상이 이사가는 곳이기도 하다. 하우스씬 자체는 힐하우스에서 촬영됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4349, 25913, 13934, '6화에서 그녀를 \'신부 부자\'로 만들기 위해 등장했다. 19회에서는 김탄(이민호)의 생일파티가 이 장소(외관은 다른 곳에서 촬영됨)에서 열리고 차은상(박신혜)과 손을 잡고 모두를 놀라게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4350, 25017, 13934, '4회에서 김탄(이민호)과 함께 도착한 비행기가 보인다. 유지라헬(김지원)은 조금 더 일찍 도착했고 \'이복동생\' 최영도(김우빈)은 그녀를 픽업하기 위해 마중나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4351, 26165, 3876, '어린 한이수(연준석 분)가 이 강바닥에 아버지의 유골을 뿌린다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4352, 25738, 3876, '3화에서 어린 한이수(연준석 분)는 자신이 소지하고 있는 열쇠가 이 역의 사물함에 맞는다는 것을 알고 이 역으로 오게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4353, 26428, 3876, '3회에서 조해우(손예진)와 오준영(하석진)의 결혼 후 파티가 열리는 호텔의 일부로 등장한다. 해우가 이곳 발코니에서 바람을 쐬면 요시무라 준(김남길)이 합류해 보름달을 언급할 때 그의 말은 첫사랑의 말과 매우 흡사하다. 그녀는 준영이 현장에 도착하기 직전에 그가 그녀를 끌어안았을 때 더욱 놀랐다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4354, 24906, 3876, '17화에서 변방진 형사(박원상)는 조해우(손예진)와 김수현(이수혁)에게 이 공원에서 중요한 정보를 채워 넣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4355, 26131, 3876, '5화에서 조해우(손예진)는 이 식당의 단골로 이곳을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4356, 26039, 3876, '조해우(손예진)와 오준영(하석진)의 결혼식 후 파티가 펼쳐지는 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4357, 25335, 3876, '17화에서 오준영(하석진)이 이 다리를 건너 운전을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4358, 26232, 3876, '3화에서 형사 정만철(김민상 분)이 죄를 지은 시계를 한강에 직접 던지는 조의선(김규철 분)에게 건네주는 것이 바로 이 다리 밑이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4359, 25910, 3876, '4회에서 조해우(손예진)는 형사 변방진(박원상) 형사와 함께 이 식당에서 술을 마십니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4360, 28491, 3876, '어린 조해우(경수진)와 한이수(연)가 대결하는 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4361, 30504, 17165, '설유상(최경훈)의 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4362, 30505, 18613, '김선우(연우진)가 카페빵집을 빌려 차미조(손예진)를 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4363, 27302, 18613, '정찬영(전미도)과 김진석(이무생)이 이 식당에서 점심식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4364, 28385, 18613, '정찬영(전미도)은 12화에서 이곳에 안장되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4365, 26201, 18613, '장주희(김지현)의 직장으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4366, 30506, 18613, '김선우(연우진)가 이 가게에서 누나에게 줄 손목시계를 사주는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4367, 29186, 18613, '김선우(연우진)는 봉은사로 이 사찰의 동쪽 끝에 있는 봉은사거리에서 저녁식사를 함께하기 위해 여동생 김소원(안소희)을 데리러 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4368, 26293, 18613, '7화에서 김선우(연우진)와 정찬영(전미도)이 이 카페에서 만나 친구에 대한 이야기를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4369, 30507, 18613, '차미조(손예진)와 정찬영(전미도)은 세탁소 앞에서 따로 택시를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4370, 30508, 18613, '박현준(5urprise의 이태환)이 운영하는 식당 \'차이나타운\'입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4371, 25664, 18613, '장주희(김지현)가 MBC 방송국 사옥 앞 길을 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4372, 27140, 18613, '김선우(연우진)가 회식 후 술에 취한 차미조(손예진)를 모란을 찾아 꽃가게로 끌고 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4373, 30509, 18613, '박현준(이태환), 조혜진(오세영)과 이 카페에서 결별');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4374, 30510, 18613, '1회에서 김선우(연우진)와 차미조(손예진)는 각자 이곳에서 라흐마니노프 콘서트를 관람한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4375, 25909, 18613, '4회에서 석훈(전광진)은 절친한 친구 김선우(연우진)를 이 식당에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4376, 25015, 18613, '1~2회에서 정찬영(전미도)이 김진석(이무생)과 함께 관계를 끊자고 애원하는 곳은 이 식당 2층 옥상이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4377, 26710, 18613, '이 건물의 외관은 4화에서 절친한 친구 차미조(손예진), 정찬영(전미도), 장주희(김지현)가 클럽에 가는 나이트클럽으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4378, 26144, 18613, '1~2회에서 장주희(김지현)의 직장으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4379, 26524, 18613, '8회에서 차미조(손예진)는 친어머니가 감옥에 수감되어 있다는 사실을 알게 되고 절친인 정찬영(전미도)과 장주희(김지현), 그리고 김선우(연우진)와 함께 그녀를 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4380, 25640, 15641, '유찬(안효섭)은 자신을 만나러 가는 길에 사고를 당했다는 사실을 모른 채 고등학교 친구 한덕수(조현식), 동해범과 함께 집으로 돌아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4381, 26720, 15641, '우서리(신혜선)가 오디션을 봤던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4382, 28385, 15641, '31회에서는 우서리(신혜선)와 공우진(양세종)이 이곳 서리 삼촌의 묘를 찾는다. 2년 후, 32화 마지막 회에서 서리는 그녀의 이모와 사촌과 함께 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4383, 26699, 15641, '6, 7화에서 우서리(신혜선)는 공우진(양세종)과 함께 동물병원에 가던 차에서 이 회사 건물 바로 앞 사거리에서 삼촌을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4384, 26672, 15641, '8회에서 공우진(양세종)이 한 고객에게 여성을 부적절하게 만진 혐의로 고소를 당하는 곳은 바로 이 거리의 카푸친 오키친 앞이다. 그러나 그는 실제로 레스토랑의 야외 가구를 측정하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4385, 30511, 15641, '공우진( 양세종)이 벤치를 측정하는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4386, 29508, 15641, '14회에서 유찬(안효섭)은 길 건너편에서 우서리(신혜선)를 보고 또 한 번 운동을 하러 가는 길이다. 그는 그녀를 따라잡기 위해 이 육교를 가로질러 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4387, 26615, 15641, '5화에서 이곳은 아르바이트생을 구하는 식당이고 우서리(신혜선)은 알바에 지원하지만 나이 때문에 거절을 당한다. 이곳에서 업무 회의가 있는 공우진(양세종)은 서리도 모르게 모든 것을 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4388, 26616, 15641, '나이 때문에 거절당하는 자신을 드러내는 반면 공우진(양)은 나이 때문에 거절당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4389, 30512, 15641, '공우진(양세종)의 절친한 친구 강희수(정유진)가 운영하는 디자인 회사 \'채움\'으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4390, 30513, 15641, '13회에서는 공우진(양세종)이 9회에서 나온 고객들을 이 카페에서 만난다. 회의가 끝난 뒤 이 카페 밖에서 우서리(신혜선)로부터 A.Y로 다른 의뢰인의 전화를 돌려주겠다는 전화를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4391, 30514, 15641, '유찬(안효섭)과 이리안(조유정)이 만나 점심을 먹습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4392, 28538, 15641, '1회에서는 영우가 탄 버스를 타기 위해 영공우진(윤찬영)이 이 지하보도를 지나게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4393, 30515, 15641, '혼수상태에서 깨어난 우서리(신혜선)는 이곳에서 하룻밤을 보냅니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4394, 30516, 15641, '9화에서 공우진(양세종)은 이 카페에서 고객들과 만나지만 길 건너 우서리(신혜선)가 지나가는 것을 보고 정신이 몽롱해진다. 그는 그녀가 빨리 돈을 벌고 싶었기 때문에 그곳에서 일하기로 결심했다고 생각하며 카페 맞은편에 있는 \'바문\'으로 보이는 업소에 들어가는 것을 보고 최대한 빨리 회의를 마친다. 13화에서 (현실적인) \'바 세븐\'의 입구는 이 카페 바로 옆에 있으며 배경으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4395, 30517, 15641, '우서리(신혜선)가 아르바이트를 위해 양말을 사러 가는 양말 공장 입구로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4396, 25738, 15641, '공우진(양세종)과 우서리(신혜선)는 동료의 차가 고장 나자 \'훈물역\'으로 등장하는 기차를 타고 작업장으로 간다. 기차를 기다리는 동안, 그들은 재빨리 먹을 것을 먹습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4397, 26320, 15641, '일부 CG처리 되어 독일 제국주의 건축물의 상부와 결합된 이 건물의 하부는 젊은 우서리(박시은 분)가 베를린에서 바이올린 연주자로서의 경력을 되돌아볼 때 참석하는 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4398, 26459, 15641, '25화에서 이 호수가 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4399, 24899, 15641, '우서리(신혜선)와 공우진(양세종)이 첫키스를 나누는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4400, 30518, 15641, '공우진(양세종)은 사진을 찍고 건물을 측정하고 그림을 그리기 위해 이 공원의 중국정원[교하중앙공원 중국정원]에 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4401, 27353, 15641, '우진(양세종)은 조카 유찬(안효섭)이 실신했다는 전화를 받고 이곳으로 달려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4402, 24933, 15641, '17회에서 이곳은 자신이 제주도에서 운영하는 공우진(양세종) 아버지의 갤러리로, 우진이 아버지에게 서울 집을 팔지 말아달라고 부탁하러 오는 곳으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4403, 27165, 15641, '유찬(안효섭)은  태산고등학교 조정부와 함께 훈련한다(SBS, 2018).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4404, 24935, 15641, '13회에서 채움의 고객 중 한 명인 박모씨는 우서리(신혜선)에게 그녀를 개인적으로 만나기를 바라며 일부러 사무실에 두고 온 휴대폰을 돌려달라고 이 바에 와달라고 부탁했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4405, 30519, 15641, '어린 우서리(박시은)는 학교 가는 길에 악보를 낭독하며 이 거리를 지납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4406, 30520, 15641, '제니퍼(예지원)가 셰프를 하고 있는 식당입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4407, 26285, 15641, '우서리(신혜선)와 공우진(양세종)은 이를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4408, 25909, 15641, '11회에서 공우진(양세종)은 심리학자와 만나 자신의 우서리(신혜선)에 대해 커지는 마음에 대해 어떻게 반응해야할 지 상담합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4409, 30521, 15641, '우서리(신혜선)가 1,000원 지폐를 떨어뜨린 여자를 따라 이 역으로 들어옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4410, 30522, 15641, '극 중 \'우리슈퍼마켓\'으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4411, 30523, 15641, '공우진(양세종), 우서리(신혜선), 동료 진현(안승균)은 이 식당 앞에서 워크숍에 참석하고 싶어한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4412, 30527, 17092, '서울촌놈 광주편에서 김병현의 단골 한식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4413, 30528, 17092, '서울촌놈 광주편에서 홍진영이 픽한 빵집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4414, 30529, 17092, '서울촌놈 광주편에 나온 나주곰탕집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4415, 30530, 17092, '서울촌놈 대전편에 나온 대나무숲 속 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4416, 30531, 17092, '서울촌놈 청주편에 나온 설렁탕집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4417, 30532, 17092, '서울촌놈 청주편에 나온 보양식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4418, 26017, 13214, '24화에서 김유신(엄태웅)이 축복받은 미실(고현정)을 이곳에 숨기는 아지트 역할도 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4419, 30533, 17521, '3회에서 이 리조트로 MT를 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4420, 28428, 17521, '9회에서 이 회장(전국환)의 저택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4421, 25654, 17521, '채현승(SF9 김석우/로운)과 윤송아(원진아)는 1회 초반 업무상 미팅을 마치고 버스를 타기 위해 이 길을 걷습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4422, 30534, 17521, '7회에서 채현승(SF9 로운)이 시장 조사를 위해 이 거리에 오고 윤송아(원진아)와 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4423, 26428, 17521, '1회에서 채현승(로운)은 이 웨딩홀 웨딩드레스 가게를 운영하는 친구 채지승(왕빛나)을 찾아가 이현욱이 약혼자와 함께 이 장소에 도착하는 과정을 지켜본다. 그들은 3개월 후에 이곳에서 결혼하려고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4424, 26617, 17521, '1화 채현승(로운)과 윤송아(원진아)가  첫머리에 자신들이 근무하는 화장품 브랜드 KLAR의 최신 제품을 소개하는 비즈니스 미팅을 갖는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4425, 26037, 17521, '이 건물에는 KLAR 본사가 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4426, 30535, 17521, '채현승(SF9 로운)과 윤송아(원진아)는 카페 앞에서 동료들과 작별인사를 한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4427, 30536, 17521, '윤송아(원진아)는 채현승(로운)과 그의 동료 이세림(박한솔), 고명진(최우성)과 함께 온 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4428, 26265, 17521, '채현승(로운), 채지승(왕빛나), 채연승(하윤경) 3남매는 5회에서 이 식당에서 함께 저녁을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4429, 26399, 17521, '2회에서 이재신(이현욱)과 윤송아(원진아)의 만남');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4430, 30537, 17521, '16회에서 효주는 이 가게 앞에서 재신이 우산을 건넨 순간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4431, 26137, 17521, '채지승(왕빛나 분)이 친구 딸 강하은(박소이 분)을 돌봐줄 때, 두 사람은 3회에서 하은의 테니스 레슨이 끝난 후 이 호텔 라운지에서 시간을 보낸다. 이재운(이규한 분)은 테니스장에서 이들을 따라오다가 지승이 잠시 자리를 비우자 하은에게 지승이 결혼했는지 알아보려 하고, 하은은 돌아오는 지승을 보고 \'엄마\'를 불러낸다. 15회에서는 이효주(이주빈 분)가 이 호텔에서 소개팅을 했던 것을 회상하며, 자신이 어떻게 이재신(이현욱 분)과 건물을 나설 때 마주쳤는지에 대해 생각해본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4432, 26096, 17521, '남자친구 이재신(이현욱)과의 대화를 마치고 귀가하던 윤송아(원진아)는 2회에서 \'달맞이 역\'으로 출연해 이번 정류장에서 하차한다. 그 순간 어머니의 전화를 받고 버스정류장 벤치에서 눈물을 흘리며 어머니에게 진작 하고 싶었을 말을 모두 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4433, 26311, 17521, '8회에서 채지승(왕빛나)과 이재운(이규한)은 저녁 식사를 마치고 이 공원 입구 구간을 걸어 내려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4434, 29154, 17521, '이효주(이주빈 분)는 7회에서 할아버지 이회장(전국환 분)과 함께 이 식당에서 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4435, 26499, 17521, '2회에서 채현승(김석우/SF9 로운)이 저녁 조깅을 하던 중 이 다리에서 멈칫합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4436, 24944, 17521, '10회에서  채연승(하윤경)과 남편 강우현(이동하)은 이곳 식당 히로타에서 외식을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4437, 27092, 17521, '6회에서 검사를 위해 이 거리에 있는, 윤송아(원진아) )와 이재신(이현욱)은 최대한 순조롭게 방문을 하기 위해 서두른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4438, 24913, 17521, '16회에서 채현승(로운)과 윤송아(원진아)는 데이트를 하며 이 산책로를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4439, 27331, 17521, '6화 KLAR 화보 촬영 이벤트가 이곳에서 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4440, 25345, 18409, '이 공원은 1회에서 서울의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4441, 26539, 18409, '드라마에 나오는 대학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4442, 29449, 18409, '1화에서 은영로(블랙핑크김지수)와 임수호(정해인)은 이곳에서 처음 만나게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4443, 25773, 18409, '1화에서 이강무(장승조)는 이 공항에 착륙한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4444, 24957, 18409, '은영로(김지수)가 다니는 호수여자대학교로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4445, 29438, 18409, '1회에서 은영로(블랙핑크 김지수/지수)가 다니는 호수여자대학교 기숙사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4446, 24962, 18409, '1회에서 동심회를 결성한 애민당 의원들이 학회 30주년을 축하하기 위해 이 자리에 모였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4447, 30538, 15805, '이 건물은 윤유정(윤은혜)과 한재경(한고은)의 아파트');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4448, 25929, 15805, '7화에서는 실제 데이트로 등장한 윤유정(윤은혜)과 차우현(천정명).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4449, 26624, 15805, '4화에서 차우현(천정명)의 어머니가 이곳에 입원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4450, 25857, 13723, '가장 가슴 아픈 장면 중 하나는 여기에서 8화 끝, 9화 시작: 강마루(송중기) 서은기(문채원)는 자신이 그동안 자신을 속이고 있었다고 말하고 그녀는 필사적으로 그와 그들의 사랑을 붙잡으려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4451, 26032, 13723, '마지막 20화에서 강마루(송중기)와 서은기(문채원)는 이 마을로 이사한다. 그녀가 동피랑 코너샵에서 \'실버탑 베이커리\'를 운영하는 동안, 그는 그녀의 가게 바로 옆에 자리잡은 이곳에서 그의 가게를 연다. 그들은 그가 그녀에게 청혼할 때까지 자주 길을 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4452, 27120, 13723, '안민영(김태훈)은 최종회 20회에서 이 감옥에서 풀려나 한재희(박시연)가 기다려도 페이지를 넘기로 결심하고 그녀를 뒤로한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4453, 30588, 13723, '강마루(송중기)는 여동생 강초코(이유비)와 함께 이곳에 살고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4454, 27615, 13723, '드라마 주인공인 강마루(송중기) 집으로 이어지는 골목입니다. 여동생 강초코(이유비)와 함께 사는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4455, 30589, 13723, '7화에서 강마루(송중기)와 서은기(문채원)는 음료를 마시기 위해 공원의 정자 [오동공원 팔각정]에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4456, 25668, 13723, '17회 말미 강마루(송중기)가 결혼식을 무산시킨 서은기(문채원)를 데려가는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4457, 25735, 4817, '1회에서는 여자친구 재영(손나은)과 함께 클럽에 갔다가 돌아온 뒤 술에 잔뜩 취한 정정수(최민호)가 이 카페 계단에서 기절할 뻔했고, 여동생 정윤수(최지우)는 집에 가고 싶냐고 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4458, 30591, 4817, '2회에서는 재영(손나은)과 정수(샤이니 최민호)가 데이트를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4459, 25859, 16079, '25회에서 박선자(김해숙)가 친구들과 당일치기로 이곳에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4460, 30592, 16079, '최종화에서 박선자(김해숙)가 이 나무 아래 묻혔다. 이후 그녀의 세 딸 강미리(김소연), 강미혜(김하경), 강미선(유선)이 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4461, 25948, 16079, '6회 김우진(기태영)이 돌아가신 아버지께 조의를 표하기 위해 찾아왔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4462, 30593, 16079, '이곳은 강미리(김소연)이 근무하는 한성그룹 사옥입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4463, 29257, 16079, '4회에서 전인숙(최명길)이 주문한 드레스 셔츠를 집는 엠볼테일러로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4464, 28428, 16079, '사장 한종수(명계남)가 사는 한성그룹 하우스 외관입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4465, 25908, 16079, '16회에서 전인숙(최명길)이 다양한 만남이나 대화를 위해 불려가는 한종수 대표(동방우) 사무실로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4466, 25835, 16079, '2회에서 혼자 술을 마시러 온 강미리(김소연)는 전인숙(최명길)과의 만남을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4467, 25662, 16079, '강미선(유선)이 39회에서 시어머니 하미옥(박정수)을 만나기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4468, 30594, 16079, '강미리(김소연)와 한태주(홍종현)은 역시 절인 고기 브랜드인 어묵집 안주야의 본점에 자주 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4469, 24941, 16079, '28회에서 한태주(홍종현)와 강미리(김소윤)가 저녁 식사를 위해 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4470, 30595, 16079, '박순자(김해숙 분)가 운영하는 한촌 설렁탕 매장으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4471, 26002, 16079, '10회 오후 나들이. 다빈이 회전목마를 타는 동안 미리는 친어머니에게 버림받았다는 회상을 겪는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4472, 26039, 16079, '31회에서 미리(김소연)와 한태주(홍종현)가 결혼식 후 이 호텔에서 하룻밤을 보내는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4473, 25909, 16079, '15회에서 하미옥(박정수)이 스승님과 다른 학생들과 함께 이곳으로 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4474, 25668, 16079, '마지막 108화(전체 54화)에서 김해숙(김해숙)이 잠시 이 다리 밑으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4475, 25347, 16079, '50회(전체 25회)에서 한태주(홍종현)가 이 공원(한강철교를 배경으로)에서 강미리(김소연)를 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4476, 30596, 16079, '강미리(김소연)와 한태주(홍종현)가 사는 아파트 단지입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4477, 24946, 16079, '24회에서 한성그룹 40주년 기념식이 열린다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4478, 30597, 16079, '10회에서 조카와 강미리(김소연)와 함께 놀이동산에 있는 동안 친어머니가 버린 이곳의 과거 회상이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4479, 25945, 16079, '4회에서 강미혜(김하경 분)이 담길출판사에 가는 길에 이 궁궐의 벽을 따라 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4480, 25349, 16079, '정대철(주현)이 공원을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4481, 25017, 16079, '40화(20화)에서 한태주(홍종현)는 중국 출장으로 강미리(김소연)를 배웅하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4482, 26078, 18615, '소년법정 2,4회에서 판사 심은석(김혜수)과 차태주(김무열)가 이 건물 1관 맞은편에 위치한 포차차(텐트바)에서 술을 마시고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4483, 26124, 18615, '이곳은 심은석(김혜수 분)과 남편 남궁이환(김주헌 분)이 판사 나근희(이정은)를 처음 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4484, 28112, 18615, '드라마에서 심은석(김혜수)은 이 공원에서 자주 조깅을 하는 장면이 나온다. 1회참조.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4485, 25791, 18615, '3회에서 강원중(이성민) 판사가 방송 인터뷰 후 이 건물 로비에서 기자회견을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4486, 25913, 18615, '심은석 판사(김혜수)는 2화에서 허 변호사(김영아)를 보기 위해 이 건물 로비로 걸어 들어가 15층 거보법률사무소로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4487, 25621, 18615, '3,4회에서 이 식당에서 국회의원 엄준기(유재명)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4488, 30629, 18615, '이 건물은 1화에서 \'연화지방법원\'으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4489, 26220, 18615, '9회에서 심은석석(김혜수)은 어린 아들이 사고를 당했다는 소식을 듣고 이곳 법원에서 뛰쳐나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4490, 30631, 15463, '17화의 끝과 18화의 시작에 밴드 모두가 머무는 별장이다. 카일(공명)이 그린(이혜리)에게 자신이 첫사랑이었다고 고백하는 곳도 이곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4491, 24932, 15463, '23회에서 남현주(한혜진)가 장석준(김태훈)과 이 곳에서 만날 예정이었지만 그녀가 그에게 전화를 걸었을 때-이미 식당에서 기다리고 있었다-그는 병원에 입원해 있으니 기다리지 말라고 그녀에게 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4492, 26040, 15463, '23회에서 장석준(김태훈)은 김도영(윤상현)과 카페로 등장하는 이 식당에서 만난다. 그는 도영에게 남현주(한혜진)가 한 달밖에 남지 않았다고 말하고 죽기 전에 발리로 데려와 달라고 애원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4493, 26326, 14806, '9회에서 백마리(임세미) 차종원(윤상현)에게 사랑을 고백하고 직설적으로 거절당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4494, 30636, 14806, '12회에서는 조인성(오대환)이 백마리(임세미)를 테라스로 초대해 관람하는 모습이 그려졌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4495, 27100, 14806, '쇼핑왕 루이 5회에는 고복실(남지현), 루이(강지성)(서인국), 조인성(오대환)이 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4496, 25748, 14806, '8화 서점 골목에서 루이(서인국)와 고복실(남지현)의 부드러운 키스가 바로 여기서 벌어졌다.그 전에, 그들 둘 다 루이가 책을 뒤적일 때 일본어, 프랑스어, 독일어, 스페인어를 이해하고 읽는다는 것을 배운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4497, 27173, 14806, '5회에서 고복실(남지현)이 부산으로 떠나 여행을 하는 장소 중 하나이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4498, 25719, 14806, '2회에서 고복실(남지현)은 한 블록 떨어진 직장 \'골드그룹\'에서 퇴근하는 길에 이 건물을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4499, 26617, 14806, '차중원(윤상현)이 여기서 술을 마시는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4500, 26501, 14806, '2화에서 기억상실 후 고복실(남지현)이 그를 찾는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4501, 25640, 15061, '22회에서 이들의 관계에 대해 이야기했다. 그러나 그녀는 결국 그러지 않기로 결심하고 카페에 들어가기 직전에 몸을 돌린다. 그녀는 지욱의 전 여자친구 차유정(나라)을 핑계를 댄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4502, 25607, 15061, '15회에서 지은혁(최태준)의 생일을 축하하기 위해 이곳에서 캠핑을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4503, 24933, 15061, '은봉희(남지현)는 8화에서 이곳의 채용 제안을 훑어본다. 같은 시간 노지욱(지창욱)의 전 여자친구 차유정(권나라)이 이곳에서 식사를 하며 자신의 옆 테이블에서 컴퓨터를 훔치는 사람들을 목격한다. 그녀는 하이킥으로 그들을 막고 봉희는 마지막으로 그녀를 도와준다. 이것이 그들 둘이 만나는 방법이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4504, 30637, 15061, '12회에서 노지욱( 지창욱)과 은봉희(남지현)는 정맥으로 찾아와 자신들이 진행 중인 살인 사건에 대해 직원들을 심문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4505, 26137, 15061, '그녀의 남자 친구가 바람을 피우는 것을 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4506, 30638, 15061, '4회에서 은봉희(남지현)가 재판을 받는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4507, 25335, 15061, '12회에서 은혁이 봉희를 집으로 데려다주면서(실제로 노지욱(지창욱 분) 이 다리를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4508, 25910, 15061, '7회에서 노지욱(지창욱)은 방과장(장혁진)과 술을 마시러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4509, 30639, 15061, '노지욱(지창욱)의 사무실로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4510, 26146, 15061, '9회에서 노지욱(지창욱)과 차유정(권나라)은 둘 다 2018년 이 도서관에서 함께 공부하던 학생 시절을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4511, 30640, 14255, '양구 콩으로 만드는 손두부 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4512, 30641, 14255, '바다에서 잡은 싱싱한 재료로 만든 섭국');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4513, 25058, 14255, '수요미식회 13회 닭갈비/닭내장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4514, 29270, 14255, '수요미식회 코다리 편 김포 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4515, 30642, 14255, '수요미식회 132회 오징어보쌈/오징어');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4516, 25139, 14255, '수요미식회 113회 멍게회/멍게비빔밥');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4517, 30643, 14255, '수요미식회에 방영된 복어요리 맛집');
