INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4628, 24906, 14331, '14회에서 상처받은 강민호(정경호)는 이 공원에서 행복한 커플을 모욕해 그를 마중하러 온오우식(이시운)과 나옥현(조은지)를 실망시켰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4629, 30711, 14331, '4화에서 강민호(정경호)는 김순정(김소연)에게 자신의 친구이자 골드파트너스의 컨설턴트인 위고 위안(김리키)이라는 프랑스 고객을 보여달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4630, 26285, 14331, '강민호(정경호)가 골드파트너스와 함께 친구이자 컨설턴트와 점심을 먹는곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4631, 30712, 14331, '2회에서  마동욱(진구)은 이 거리를 통해 제품 변조자를 추적하고 그를 본다. 이준희(윤현민)로 밝혀진 검은 차의 운전자로부터 무언가를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4632, 26478, 14331, '그녀에게 마동욱(진구)의 죽음을 둘러싼 그의 꿈에 대해 이야기합니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4633, 25335, 14331, '방명수(변요한)와 김수진(송지효)은 9회에서 첫 데이트를 위해 차를 타고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4634, 26581, 14331, '5화에서 강민호(정경호)가 사과를 하기 위해 해바라기를 그녀의 집 앞에 두고두고 반복하는 모습을 볼 수 있습니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4635, 25955, 14331, '2화에서 처음 보았던 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4636, 25781, 14331, '1-2회에서 강민호(정경호)가 머무는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4637, 27531, 14331, '1회에서 처음 보던 것처럼, 이 건물은 허미아 사옥으로 보인다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4638, 25017, 14331, '4화에서 김순정(김소연)이 위고위안(김리키) 일행을 이 공항으로 데리러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4639, 29459, 18154, '9회에서 안소희(이선빈)는 이 식당에서 성 PD(송재림)와 함께 식사를 하던 중 자신이 담당하는 새 프로그램의 메인 작가를 시켜주겠다는 제안을 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4640, 27061, 4986, '내 도현숙(서주희) 집이 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4641, 26698, 4986, '2화에서 길을 가던 중 박해란(조미령)은 납치된 딸 수아를 떠올리게 하는 어린 소녀를 보게 된다. 그녀는 아이를 따라 이곳으로 오게 된다. 해란은 어린 소녀를 발견한 후 그녀와 함께 달아나려 하지만, 그녀의 딸 중 한 명인 하금주(김혜지)와 함께 지나가던 도현숙(서주희)에 의해 좌절된다. 5화에서 해란은 수아가 다른 여자를 자신의 어머니로 인정하는 장면을 보게 된다. 해란이 그들을 쫓으려 할 때, 그녀는 납치범으로 보이는 조필두(이원종)에게 들른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4642, 30593, 4986, '이곳은 민씨 가족이 운영하고 있는 코스메틱 회사 메이크퍼시픽 사옥입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4643, 26418, 4986, '5회에서 보듯 태산그룹 문태산 회장(윤주상)의 친정집이 이곳에 있다. 민채린(이유리)은 두 살배기 아들 문재상(김영민)과 결혼 후 이곳에 머문다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4644, 26256, 4986, '2회에서 나해금(정혜선)이 하연주(엄현경)의 안부를 빌기 위해 찾아온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4645, 24932, 4986, '18회에서 하연주(엄현경)가 차은혁(송창의)이 집에 두고 간 옷을 건네주기 위해 이곳에서 차은혁(송창의)을 만나자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4646, 26644, 4986, '29회에서는 부자 친구들에게 자신을 자랑하는 문재인(김영민)을 만나러 가는 모습이 그려졌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4647, 25792, 4986, '2회에서 볼 수 있듯이 메이크퍼시픽의 나해금 여주인(정혜선)이 방문했을 때');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4648, 30713, 4986, '26회에서 조필두(이원종)가 이 거리에 들어서자 뒤에서 두 남자가 다가오고 본능적으로 움찔한다. 하지만 그들은 이 곳에 다른 사람을 만나기 위해 온 것이었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4649, 26399, 4986, '민채린(이유리 분)은 11회에서 엄마 박해란(조미령 분)의 요청으로 이곳에 온다. 채린이 도착하자, 그녀는 해란이 민수아로 분장한 하연주(엄현경 분)를 볼 때까지 자신을 그곳으로 불러줘서 얼마나 기뻤는지를 말해준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4650, 27120, 4986, '백도훈(안보현)은 이미 그를 기다리며 마침내 그의 질문에 대한 답을 얻기 위해 그를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4651, 25929, 4986, '4회에서 차은혁(송창의)에게 이 건물로 이어지는 다리 난간에서 떨어지겠다고 위협하며 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4652, 30714, 4986, '36회에서 문재상(김영민)과 차은혁, 40회 하연주(엄현경)가 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4653, 30715, 4986, '2화에서 민채린(이유리)은 이 노래방(\'여왕비 호스테스 바\')에서 제작사 최씨를 추적한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4654, 25909, 4986, '19회에서 해금(정혜선)과 김숙영(윤다경)은 조필두(이원종)를 위해 이 식당의 한 개인실에서 기다렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4655, 30716, 4986, '4회에서 차은혁(송창의)이 이 건물 맞은편에 문재인(김영민)을 떨어뜨려 양아버지를 죽인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4656, 26543, 4986, '태산그룹 사옥 옥상/헬리패드로 보여지며, 차은혁이 혼자 또는 문재상(김영민)과 상의하러 자주 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4657, 25797, 4986, '결혼식장을 떠난 뒤 문재상(이영민)에게 이곳에 내려준 뒤, 신부 하연주(엄현경)는 다리에서 뛰어내리려 한다. 재상이 백미러에 비친 그녀를 발견하고는 뒤로 돌아 그녀를 제지한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4658, 24951, 4986, '24회에서 민채린(이유리)을 기다리며 시내에 동전을 던지고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4659, 25906, 15516, '최종 16회에서 고연우(박형식) 할머니가 이곳에 안장되었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4660, 26325, 15516, '13회에서 고연우(박형식)와 김지나(고성희)가 이 다리를 건너고 연우는 반지를 끼고 지나에게 청혼한 뒤 키스를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4661, 24936, 15516, '11회에서 최강석(장동건)이 시계를 구경하고 구매하기 위해 이 태그호이어 매장을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4662, 26141, 15516, '9화의 뺑소니(살인) 사건은 이 터널 직전에 발생했고, 고연우(박형식)는 혼자 현장을 보러 이곳에 와서 피해자가 의뢰인의 차에 치이기 전에 터널에서 그림을 그렸다는 사실을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4663, 26667, 15516, '7회에서 채근식(최귀화)은 홍다함(채정안)에게 콘서트를 보러 오라고 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4664, 30717, 15516, '친구 철순(이상이)과 함께 집으로 돌아오는 이 골목을 자전거로 자주 봅니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4665, 25722, 15516, '최종회(16회)에서 고연우(박형식)는 김지나(고성희)에게 자신이 \'가짜이자 사기꾼\'이라고 고백했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4666, 25335, 15516, '3회에서 고연우(박형식 분)가 사무실로 출근하는 길에 자전거를 타고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4667, 25668, 15516, '15회에서 최강석(장동건)은 자신에게 사건의 증거를 제시하는 역대급 상대 김다윗(손석구)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4668, 24944, 15516, '13회에서 석(장동건)과 고연우(박형식)가 데이비드 김(손석구)과 함께 \'테라스홀\'에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4669, 24951, 15516, '최강석(장동건)은 이 다리에서 만난다. 그의 사무실에서 도보 거리에 위치하며 2회에서 자신의 정보원과 함께 조 의원에 대해 알아본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4670, 25893, 15516, '강앤햄 로펌으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4671, 26188, 15516, '법무법인 강&함에 소속된 장소로 등장한다. 5, 6회에서 고연우(박형식)가 모의재판을 하는 곳으로, 11~12회에서 함기택(김영호)이 회사 재통합을 선언하고 14회에서 최강석(장동건)을 상대로 한 워크 재판이 벌어지는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4672, 29168, 15516, '최강석(장동건)은 이 카페로 올라가는 계단에서 멘토 오병욱 부검장을 만난다. 8화에서 자신의 실수를 인정하라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4673, 27447, 15516, '4화에서 최강석(장동건) 나주희(장신영)을 클라이밍 벽에서 만납니다. 사건에 대해 논의하기 위해 그는 그녀가 서있는 인공 바위 맨 꼭대기에 올라가야 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4674, 25809, 15516, '12년 전 검사 시절 최강석(장동건)이 거짓으로 감옥에 보낸 인물이 이곳에 수감됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4675, 25637, 14303, '극중 대학병원 신경외과 과장인 차미래(이유리)가 근무하는곳으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4676, 25931, 12899, '2회에서 박하진(이동건)이 이 역을 통해 서울에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4677, 25017, 13993, '마지막회 40회에서 서준(공유)이 이곳을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4678, 30770, 18612, '백이진(남주혁) 외삼촌의 집으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4679, 27435, 18612, '5회에서 부모님의 사업이 망해 백이진(남주혁)과 그의 동생은 이 마을로 이사 온다. 생계를 위해 이곳 시장에서 일을 시작한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4680, 26253, 18612, '9회에서 이 다리를 건너던 나희도(김태리)는 갑자기 무지개를 발견하고 백이진에게 묻는다. (남주혁) 잠시 감탄할 수 있도록 차를 세웁니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4681, 30771, 18612, '9회에서 이 다리를 건너던 나희도(김태리)는 갑자기 무지개를 발견하고 백이진에게 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4682, 30772, 18612, '15회에서 나희도(김태리 분)와 백이진(남주혁 분)은 이 극장에서 영화 데이트를 하는데, 요즘도 여느 때처럼 40분이나 늦는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4683, 30773, 18612, '6회에서 1999년 경주에서 열린 제13회 경주 아시안게임(가상일자)에 출전하던 중 나희도(김태리)는 자신의 울타리를 찾아 이곳에서 기차를 탄다.  희도가 대회장을 떠났다는 소식을 들은 백이진(남주혁)은 희도가 다음 대회 시간에 맞춰 도착할 수 있도록 그녀를 이 역으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4684, 26141, 18612, '10화에서 문지웅(최현욱)이 브레이크 댄서들과 함께 (힙합은 미래이기 때문에) 여기에서 어울리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4685, 25625, 18612, '5회에서 도(김태리)가 양찬미(김혜은) 코치와 함께 이 식당에서 술을 마시며 펜싱 국가대표 입단을 축하하고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4686, 30589, 18612, '15화에서 백이진(남주혁)은 나희도(김태리)를 이 공원에 데려가 2001년 새해 전야제를 축하합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4687, 30632, 18612, '6회에서 연습생 기자 백이진(남주혁)이 공중전화 박스를 위해 이 골목을 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4688, 25862, 18612, '나희도(김태리)의 오랜 채팅 친구는 8화에서 노란 장미를 사인으로 이 공원에서 만나자고 제안한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4689, 30774, 18612, '3회에서 나희도(김태리)가 이 공원에서 훈련을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4690, 26544, 18612, '백이진(남주혁)은 이 파빌리온에서 2001년 새해 전야제에 대해 보고하고 나희도(김태리)는 15회에서 여기에 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4691, 26217, 18612, '2회 회상에서 국연수(김다미)는 할머니가 걸어서 집으로 돌아오길 기다리던 순간을 회상하는 장면에서 첫 번째로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4692, 27361, 18612, '6회에서는 노동자들이 더 나은 고용 조건을 요구하는 가운데 나희도(김태리)와 백이진(남주혁)이 잠시 헤어진 후 재회하는 장면이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4693, 26044, 18612, '10화에서 문지웅(최현욱)이 브레이크 댄서들과 함께 (힙합은 미래이기 때문에) 여기에서 어울리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4694, 30775, 18612, '6회에서 나희도(김태리)는 짧은 이별 후 재회한 후 백이진(남주혁)에게 이 식당에서 누군가를 만나자고 제안한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4695, 30776, 18612, '2회 엔딩 장면 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4696, 25844, 18612, '13회에서 고유림(김지연)과 문지웅(최현욱)이 데이트를 하기 위해 이곳에 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4697, 30777, 18612, '1회에서 백이진(남주혁)은 집에서 나오는 유쾌한 나희도(김태리)를 마주치고 입가에 미소를 지으며 이 거리에서 자전거를 타고 내려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4698, 30778, 18612, '드라마 등장인물 백이진(남주혁 분), 나희도(김태리 분), 고유림(보나)이 다니거나 졸업한 \'태양고등학교\'로 등장한다.드라마 주인공인  나희도(김태리 분)와 고유림(김지연 분)이 이 고등학교의 펜싱팀의 일원으로 활약한 것으로 확인된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4699, 25607, 14201, '1회에서 김봄(송하윤)과 용수철(10대 탑 최종현/창조)은 중고 밥솥 구매자와 판매자로 이 공원에서 처음 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4700, 27161, 17427, '9화에서 군대에 대한 공격이 이 터널에서 발생합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4701, 30779, 17427, '1회에서 고등학생 차현수(송강)는 자살 충동을 느끼며 이 건물 옥상으로 올라온다. 그러나 근처 옥상에서 발레 연습을 하고 있는 이은유(고민시)를 발견하고 마음을 바꾼다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4702, 25016, 17427, '이 아파트는 1회에서 고등학생 차현수(송강)가 집을 나서 이사하는 \'그린홈\'으로 등장한다. 그와 다른 주민들은 끊임없는 좀비의 공격에도 불구하고 살아남기 위해 노력한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4703, 25910, 5619, '강동수(정지훈/비)와 이선빈(유시현)은 이 식당에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4704, 25614, 13171, '김철수(유지태)는 이 대학 현대문학과 대학원생이자 조교로 재직 중이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4705, 30784, 13171, '이마리(최지우)의 소속사는 이 건물에 위치하고 있으며, 1회에서 김철수(유지태)가 이 곳에 와 그녀를 위한 여행책 대필을 논의한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4706, 25335, 13171, '이마리(최지우)의 인기를 설명하기 위해 전병준(정운택)은 1회에서 전 남자친구 중 한 명이 이 다리에서 자살을 시도한 과정에 대해 친구들에게 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4707, 24944, 13240, '최종회 16회에서는 패션 매거진 \'스타일\'의 편집팀이 이곳에서 웨딩드레스를 입은 박기자(김혜수)의 모습이 담긴 화보 촬영을 위해 함께한다. 마지막 장면에서 서우진(류시원)이 찾아와 청혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4708, 26190, 17272, '14회에서 남도산(남주혁)은 서달미(배수지)에 대한 감정에 혼란스러워하고 아버지는 자전거를 추천한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4709, 25718, 17272, '서달미(배수지)는 벤처캐피털 회사와의 성공적인 만남이 끝난 뒤 언니와 싸운 뒤 이 공원 벤치에 앉아 아버지(김주헌)와 전화 통화를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4710, 30785, 17272, '1회에서 보듯 어린 시절 서달미(허정은)는 벚꽃이 만발한 이 거리를 걸었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4711, 25966, 17272, '2회에서 서달미(배수지)가 이 광장에서 남도산(남주혁)을 만나기로 했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4712, 26031, 17272, '마지막 16화에서 스마트시티 입찰 접수가 이루어지는 곳입니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4713, 30786, 17272, '독실한 천주교 신자인 할머니 최원덕(김해수)과 서달미(배수지)가 가끔 동행하는 교회이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4714, 30787, 17272, '마지막 16회에서 서달미(배수지)와 남도산(남주혁)은 5개 업체가 선주 스마트시티 자율주행 플랫폼 입찰에 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4715, 30788, 17272, '8회에서 서달미(배수지)가 이 카페에 앉아 전화로 이효원(배해선)과 통화를 하고 있다. 그녀의 회의 요청을 아래로. 그렇게 하던 달미는 엘리베이터에서 걸어가는 자신을 발견하고 눈길을 던질 기회를 잡으라고 설득한다. 효원 따라가면 가평까지 ///');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4716, 26037, 17272, '이곳은 7화에서 보았던 원인재(강한나)의 의붓아버지 원두정(엄효섭)이 운영하는 모닝그룹 본사로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4717, 30789, 17272, '1회에서 본 것처럼 최원덕(김해수)이 예전에 이곳에 살았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4718, 27602, 17272, '남도산(남주혁)의 부모(김원해, 김희정)는 여기에서 산다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4719, 30790, 17272, '이곳은 스타트업에서 2STO 한국 지사로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4720, 25795, 17272, '루카 더 티에서 일을 그만둔 후, 서달미(배수지)는 자신이 내린 결정에 기뻐하며 새로운 공장을 손에 들고 이곳을 지나간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4721, 30791, 17272, '8회에서 서달미(배수지)는 삼산테크 팀을 이 치킨집(칠칠켄터키 신촌점)에 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4722, 27104, 17272, '2회에서 서달미(배수지)가 이 역에 내립니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4723, 30792, 17272, '12에서 정사하(Stephanie Lee)는 이 가게를 지나가다가 아이스 바닐라 라떼 1+1 프로모션을 발견한다. 이철산(유수빈)을 염두에 두고 그녀는 바닐라라떼를 사러 갔다가 서달미(배수지)와 남도산(남주혁)과 마주치게 된다. 14회에서는 철산(유수빈)과 사하가 삼산테크팀의 신사업 편입을 논의하기 위해 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4724, 30793, 17272, '14회에서는 이철산(유수빈)이 자신이 일하는 로펌 밖에서 정사하(스테파니 리)와 \'우연히\' 마주치기 위해 이 건물 밖에서 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4725, 26039, 17272, '1회에서 한지평(김선호)의 아파트로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4726, 24942, 17272, '9회에서 남도산(남주혁)은 사촌으로부터 도산이 더 이상 도산의 대표가 아니라는 사실을 부모님이 알게 된 후 도산은 아버지를 만났을 때 하고 싶은 말을 연습하기 시작하고 결국 눈물을 흘린다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4727, 25335, 17272, '1화에서 한지평(김선호)은 샌드박스 빌딩으로 가는 길에 이 다리를 건너 패널 토론에 참여한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4728, 26992, 17272, '드라마에 나오는 스타트업 허브 \'샌드박스\'는 이 섬에 위치해 있다. 드라마 메인 촬영 장소중 하나이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4729, 25797, 17272, '3화에서 남도산(남주혁)이 서달미(배수지)를 태우고 이 다리를 건너고, 이어서 택시를 탄 한지평(김선호 분)이 이 다리를 지나간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4730, 26232, 17272, '윤선학(서이숙 분)은 6화에서 이 다리를 건너다가 레일 한쪽에 묶여 있는 하얀 카네이션을 보고는 발을 멈춘다. 에피소드 후반부에서, 그녀는 누군가가 바로 그 지점에서 다리에서 뛰어내리는 것을 어떻게 보았는지 기억한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4731, 26250, 17272, '남도산(남주혁 분)이 탄 지하철이 이 다리를 건넌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4732, 30794, 17272, '1,2회에서 볼 수 있듯이 이 건물 옥상에는 남도산(남주혁)이 운영하는 삼산테크 사무실이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4733, 27076, 17272, '원인재(강한나)가 사는 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4734, 30795, 17272, '2회에서 원인재(강한나)가 엄마 차아현(송선미)과 하이티를 마신다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4735, 25893, 17272, '8회에서 원인재(강한나)는 어머니 차아현(송선미)을 만나 식사를 한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4736, 25913, 17272, '서달미(배수지 분)가 8화에서 시각장애인들을 위한 시각인식 기술을 홍보하기 위해 찾아온 \'월드오일\'의 건축물로 잠깐 보여진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4737, 27001, 17272, '서달미(배수지)가 할머니 최원덕(김해숙)과 함께 지내는 집입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4738, 26681, 17272, '16회 마지막회에서 남도산(남주혁)과 서달미(배수지)가 스마트시티 입찰을 하러 가는 길에 이 다리를 운전한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4739, 25017, 17272, '1화에서 원인재(강한나)가 여행을 마치고 이곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4740, 30796, 16638, '조한선이 동생들에게 보고를 받는 헬스장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4741, 30797, 16638, '골든글러브 시상식 촬영장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4742, 24935, 16638, '2화(전체 1화)에서 드림즈 감독 최용구(손광업) 주변 세력이 이 술집에서 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4743, 24902, 16638, '이세영(박은빈)과 그녀의 동료 한재희(조병규)는 에필로그에서 집으로 돌아가는 길에 이 식당을 지나며 백승수를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4744, 30798, 16638, '홍기준이 미래에 대한 이야기를 나누는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4745, 30799, 16638, '김강민의 부모가 운영하는 해장국집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4746, 28431, 16638, '4화 미공개 영상에서 고기를 구워먹은 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4747, 25776, 16638, '야구팀 드림스 작전팀은 1회에서 올해 마지막 경기에서 팀이 패한 후 술을 마시러 이 식당에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4748, 25910, 16638, '2회에서 이세영(박은빈)은 동료이자 친구 한재희(조병규)와 함께 술을 마시러 이 식당에 온다. 말다툼을 하던 중 드림즈의 선수 임동규(조한선)가 조폭 두 명을 데리고 이곳에 들어온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4749, 30800, 16638, '14회에서 백승수(남궁민)는 전처인 유정인(김정화)을 이 카페에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4750, 30801, 16638, '스토브리그 주요 촬영지인 드림즈의 홈구장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4751, 25017, 16638, '11회에서 한재희(조병규)와 양원섭(윤병희)은 유민호(채종협)를 공항으로 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4752, 26044, 16638, '13화에서 백승수(남궁민)가 임동규(조한선)에게 다시 드림즈에 합류할 것을 부탁한 곳은 바로 이 \'문화의 거리\'의 끝 월미도 등대다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4753, 30802, 16638, '남궁민이 선수의 트레이드를 논의하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4754, 30543, 13495, '이곳은 극 중 등장인물인 강우(에릭)의 집으로도 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4755, 26285, 13120, '최종 16회에서 태석의 아버지 오승환과 함께 있는 이 식당에서 자신도 연루된 사건에 대해 더 이상 수사하지 말라고 애원하며 태석은 이를 거부한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4756, 24951, 13120, '11회에서 대규모 촛불집회');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4757, 26990, 18619, '1회에서 이 건물에서 한채린(한채영)과 만나 자신에 대한 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4758, 26459, 18619, '1회에서 현승훈(구자성)은 이 공원에서 아침 조깅을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4759, 25908, 18619, '1회에서 한채린(한채영)이 자신을 찾아왔던 박 회장(박근형)의 집으로 찾아가는 저택 내부 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4760, 26524, 15307, '스타 야구선수 김제혁(박해수)이 감옥에 갇히는 교도소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4761, 30803, 16833, '석형이 엄마랑 밥 먹다가 긴급콜 받고 나오던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4762, 30804, 16833, '11화에 나온 5인방 노래방');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4763, 26609, 16833, '10회에 \'병원 앞 스테이크집\'으로 식당이 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4764, 30805, 16833, '9화에서나온 익순준완커플 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4765, 30806, 16833, '8회에서 정원(유연석)은 이 떡볶이집에서 친구들을 대접하지만, 정원의 효행을 듣고 석형(유대명)은 결국 저녁값을 대신 내겠다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4766, 30807, 16833, '9화에서 익준이와 송화가 간 수제비집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4767, 30808, 16833, '5회에서 송화(전미도)는 가슴 덩어리를 검사하기 위해 이 병원에서 의사를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4768, 25729, 16833, '3화에서 준완이가 골프를 친 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4769, 30809, 16833, '7회에서 석형(김대명)은 아버지의 여주인의 임신 사실을 어머니에게 말할까 고민하던 중 이 식당에서 저녁을 먹으며 친구들에게 조언을 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4770, 30810, 16833, '7회에서 준완(정경호)이 익순(곽선영)의 캠프장까지 차를 몰고 이 식당에서 자장면을 먹으러 가는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4771, 26607, 16833, '이 병원은 1회에 강운대 병원(강운대 병원)으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4772, 30811, 16833, '9화에서 석형(유대명)은 어머니(문희경)가 병원에 입원해 있는 동안 그녀를 검진해줘서 고맙다고 말하자 친구들을 이 식당에 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4773, 30812, 16833, '율제병원 의료진들이 커피와 과자를 받는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4774, 30813, 16833, '드라마 속 율제 병원에 있는 병원 내 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4775, 30814, 16833, '익준(조정석)과 준완(정경호)이 3번 출구로 나오면서 역전이 벌어지는 장면이 그려졌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4776, 24918, 16833, '11회에서 전미도를 두고 진실게임을 한 술집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4777, 30815, 16833, '6회 회상 장면에서 송화(전미도)의 거절로 마음이 상한 석형(김대명)은 이 식당에서 익준(조정석)과 술에 취한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4778, 30816, 16833, '3회에서는 익주(조정석)가 아들을 데리고 노래방 건물 앞으로 가서 처음 공연한 곳을 보여줬다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4779, 26263, 16833, '3회에서 안정원(유연석)이 장겨울(신현빈)을 집에 데려다주며 입맞춤을 하는 부분.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4780, 29324, 16833, '10회에서는 안정원(유연석)과 장겨울(신현- 빈)이 식당에서 저녁을 먹고 그녀는 학대하는 아버지가 있는 가정에서 자랐다고 그에게 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4781, 30817, 16833, '1화에서 \'락킹파이브\'가 밴드 연습을 위해 모이는 양석형(김대명)의 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4782, 30818, 16833, '익준 우주 부자가 간 토스트집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4783, 29202, 16833, '10화에서 석형(김대명)이 민하(안은진)를 이 역 4번출구에 내려준다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4784, 29459, 16833, '10회에서 (전미도) 치홍(김준한)을 만나기 위해 이 이탈리안 레스토랑에 도착한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4785, 24934, 16025, '윤마리(박한별)와 강인욱(류수영)이 사는 숙소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4786, 25613, 16025, '1회에서 윤마리(박한별)와 강인욱(류수영)이 이 식당에서 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4787, 27245, 16025, '서정원(지현우)은 이 호텔의 카페에서 아내 우하경(박한별)을 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4788, 25641, 3952, '12회에서 은채아(황승언)는 아버지 친구와의 점심 회식에 친구 신민석(김준한)을 데리고 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4789, 26030, 3952, '3회에서 천수호(김정현)의 형 천수철(서현우)과 가족 변호사 남대철(최덕문)은 이곳에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4790, 26521, 3952, '1~2회에서 그녀의 집 앞에서 쓰러지고, 6개월밖에 남지 않은 매우 희귀한 난치성 뇌암 진단을 받은 후 이 병원에 입원하였다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4791, 25997, 3952, '3회에서 천수호(김정현)의 엄마는 이곳에서 쉬고 있다고 거짓말을 하고 수호는 그녀를 찾으러 이곳으로 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4792, 30819, 3952, '이 곳 천수호(김정현)의 아파트에는 전용 수영장이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4793, 27243, 3952, '10회에서 천수호(김정현)는 이 건물 옥상에서 막 삶을 끝내려는 설지현(소녀시대 서현)을 발견합니다. 그는 그녀의 옆에 서서 그녀에게 행복한 삶을 선택할 것인지 아니면 그와 함께 같이 뛰어내릴 것인지 선택하라고 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4794, 25659, 3952, '10화에서 자살을 앞둔 설지현(서현)에게 가는 길에 이 공원을 지나다가 월드컵북로56길을 건너다 차에 치인다. 그녀는 이제 죽을 수 없다고 스스로에게 말하고 일어서서 계속 수호를 향해 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4795, 30755, 3952, '설지현의 여동생(서현) 사건을 조사하던 기자는 동료들과 함께 식사를 하던 중 오토바이 운전자에게 습격을 당한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4796, 26000, 3952, '4회에 자신이 \'범죄 현장\'에 있었다는 유일한 증거를 없애기 위해 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4797, 29472, 3952, '천수호(김정현)가 운영하는 식당 \'트르스비엔\'으로 등장하며 거의 매회 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4798, 25639, 3952, '9, 14, 15회에서 천수호(김정현)는 이 건물 입구에서 변호사 겸 검사 친구와 만나 도움을 청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4799, 28133, 3952, '11회에서 현(소녀시대 서현)은 어머니와 여동생의 사망 소식을 전하기 위해 이 거리에서 전단지를 나눠준 뒤 이 거리 입구 맞은편에서 휴식을 취하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4800, 25016, 3952, '드라마 주인공인 설지현(서현)이 이곳에서 언니, 엄마와 함께 살고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4801, 25957, 3952, '9회에서 오영희(안지현)가 절친 설지현(서현)을 돌보기 위해 몰래 신용카드를 건네준다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4802, 25864, 3952, '천수호(김정현) 아버지 천(최종환) 대표가 W그룹 이사회와 비공개로 만나 아들의 W푸드 대표이사 선임을 축하하는 시간');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4803, 26125, 3952, '1~9회에서 천수호(김정현)가 사는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4804, 26558, 14570, '형사 수현은 지난 15년 동안 그녀의 선배인 형사 재한이 죽은 것이 아니라 그를 침묵시키려는 사람들로부터 숨어 있다고 믿었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4805, 25868, 14570, '최종회 16회에서 박해영(이제훈)과 차수현(김혜수)은 이 길을 따라 차를 몰고 이재한(조진웅)이 있다고 하는 병원으로 향하면서 생각과 기대에 잠겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4806, 30820, 14570, '차수현이 이재한의 행방을 찾으려고 방문한 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4807, 30821, 14570, '프로파일러 박해영(이제훈)이 기자와 이야기를 하다가 형사 차수현(김혜수)에게 체포되는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4808, 27326, 14570, '4화의 \'신다해\' 사건의 일부다. 죽은 줄 알았던 그녀가 살아 있는 모습을 한 남자가 보고 사진을 찍었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4809, 30822, 14570, '바닷가 식당에서 만난 박해영(이제훈)과 차수현(김혜수)은 새로운 타임라인과 이재한(조진)을 찾기 위해 이곳에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4810, 30823, 14570, '4회에서 이재한(조진웅)이 짝사랑하는 원경이가 예매한 영화를 보러 왔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4811, 30824, 14570, '9회에서 1997년으로 돌아가 차수현(김혜수)은 홍원동 살인사건 희생자들의 발자취를 쫓다가 이 건물을 지나게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4812, 26521, 14570, '과거가 마지막으로 변한 후, 형사 이재한(조진웅)이 지난 15년 동안 숨어 있었던 곳 \'정현재활병원\' 으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4813, 25322, 14570, '박해영이 탐문수사를 위해 방문한 카페 겸 와인바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4814, 25348, 14570, '2화에서 이 건물 앞에서 감기 사건에 반대하는 시위대를 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4815, 25910, 14570, '박해영(이제훈)은 이재한(조진웅)이 몰래 오므라이스를 사주던 어린 시절 장소로 돌아간다. 마지막 16화에서 해영은 어린 시절의 추억을 떠올리며 시간을 거슬러 재한과 차수현(김혜수)의 첫 데이트로 시간 여행을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4816, 30825, 14570, '형사 이재한(조진웅)의 아버지 시계 가게입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4817, 25798, 14570, '2화에서 차수현(김혜수)은 이 역에서 범인을 쫓다가 이 출구 밖에서 체포한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4818, 30826, 14570, '이재한이 경찰들과 수사를 위해 방문한 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4819, 30827, 14570, '이재한의 짝사랑이 거닐던 현풍역 철길과 기차역');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4820, 25809, 14570, '7회에서 박해영(이제훈)은 80년대 절도 누명을 쓴 죄수 오경태(정석영)에 대해 알아보기 위해 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4821, 30828, 14570, '11회에서 홍원동 살인사건이 해결된 후 무기징역자 김진우(이상엽)가 이 병원으로 옮겨진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4822, 30829, 15466, '11회에서 우보영(이유비)이 정연복(one)의 \'상처\'를 낭송하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4823, 25612, 15466, '15회에서 우보영(이유비)과 예재욱(이준혁)이 데이트를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4824, 28639, 15466, '7회에서 예재욱(이준혁)은 우보영(이유비)과 신민호(장동윤)를 미술관으로 데려가 저녁 식사를 대접한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4825, 26229, 17657, '1,5화에서 게임 디자이너 장재영(박서함)은 스케이트를 타고 친구들과 어울리기 위해 정기적으로 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4826, 26030, 15036, '3회에서 홍소희(조경숙)가 한세주(유아인)의 스토커에 관한 기사를 읽고 송종욱(이규복) 기자와 이곳에서 만납니다.그리고 또 다른 \'스캔들\'형 기사를 쓰도록 그에게 뇌물을 줍니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4827, 25995, 15036, '1930년대 장면 중 일부에 붉은 벽돌집[하우스]이 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4828, 24900, 15036, '3회에서 민(곽시양)이 책 광고를 찍는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4829, 25345, 15036, '이 곳에서 1930년 배경인 모든 장면을 촬영했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4830, 27562, 15036, '전설(임수정)이 도와주는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4831, 26086, 15036, '7회에서 8년 전 회상으로 한세주(유아인)를 만나 유명 작가가 되도록 도와주겠다고 약속했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4832, 26116, 15036, '소원의 일환으로 전설(임수정)은 한세주(유아인)를 데려온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4833, 27211, 15036, '5회에서 한세주(유아인)와 전설(임수정)이 식사를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4834, 25945, 15036, '9회에서 한세주(유아인), 전설(임수정)과 함께 산책을 하던 고경표(고경표)가 사라진다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4835, 24910, 15036, '9회에는 전설(임수정)과 백태민(곽시양)이 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4836, 24951, 15036, '한세주(유아인)와 전설(임수정)은 데이트를 위해 이곳을 산책한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4837, 25778, 15036, '이곳에서 세주가 설에게 80년 전 독립투사로서의 삶에 대해 이야기한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4838, 30834, 15036, '7회에서 전설(임수정)은 한세주(유아인)와 이 카페에서 마주치게 된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4839, 26409, 15036, '9회에서 한세주(유아인)와 전설(임수정)은 데이트를 하면서 서울시티투어 버스를 타고 유령 유진오(고경표)와 함께 이 문을 지나갑니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4840, 26188, 15036, '5화에서 한세주(유아인)의 기자간담회가 열린 곳입니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4841, 25026, 4573, '김주원(현빈)과 길라임(하지원)이 처음으로 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4842, 26147, 4573, '7회에서 김주원(현빈) 그리고 길라임(하지원)은 어머니를 만나러 이 길을 드라이브한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4843, 25948, 4573, '2회에서 이 장소로 이동합니다. 액션스쿨 원장에게 경의를 표하기 위해 신입생들을 데려온 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4844, 30835, 4573, '김주원(현빈)은 1회에서 윤슬(김사랑)과 데이트 하는 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4845, 26419, 4573, '드라마 남자주인공인 김주원(현빈)의 서재 겸 도서관으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4846, 30836, 4573, '8회에 등장하는 경찰서입니다. 김주원(현빈)의 몸에 들어간 길라임(하지원)은 오스카(윤상현)를 구출한다. 경찰서에서 나올 때, 우연히 둘은 키스를 하게 되는데 길라임은 부끄러워하고 오스카는 역겨워한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4847, 25827, 4573, '최종회 20회에서 길라임(하지원)이 출연한 촬영장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4848, 28403, 4573, '길라임(하지원)이 일하는곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4849, 26000, 4573, '최종회 20회에서 김비서(김성오)와 민아영(유인나)은 눈 내리는 겨울날 이 다리의 발치에 오다가 우연히 김비서가 아영에게 보낸 편지가 담긴 병을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4850, 30837, 4573, '10화의 거품키스를 이곳에서 했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4851, 25723, 4573, '김주원(현빈) 어머니의 집으로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4852, 25802, 4573, '길라임(하지원)은 시크릿 가든 3화 끝에서 이 거리를 걷는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4853, 25910, 4573, '2회에서 김주원(현빈), 길라임(하지원), 민아영(유인나)은 이곳에 밥 먹으러 한다. 주원은 두 여자가 음식을 파헤치는 동안 음식이 싫다는 것을 숨길 수 없다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4854, 30838, 4573, '이 집은 길라임(하지원)이 그녀의 가장 친한 친구 임아영(유인나)과 함께 이곳 시크릿가든(시크릿가든)의 쉐어 아파트에서 살기 때문에 유명해졌습니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4855, 26160, 4573, '6화의 유명한 벤치 장면을 촬영한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4856, 27604, 4573, '이 호텔은 드라마에서 설공찬(이동욱)이 소유한 호텔 중 하나로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4857, 27605, 4573, '길라임(하지원)은 워크샵을 위해 그녀의 액션 스쿨과 함께 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4858, 26030, 16439, '20회(전체 10회)에서 제니장(김선아)이 이 식당에서 이주호(창률)를 잠간 누워있으라고 협박했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4859, 24897, 16439, '7화에서 고 DEO 그룹 회장을 위한 연례 기일 미사는 이 성당에서 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4860, 26418, 16439, '드라마 주인공인 제니장(김선아)와 위정혁(김태훈이) 결혼식을 올리는 장소로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4861, 30839, 16439, '6화 제니 장(김선아)이 윤선우(김재영)와 함께 어린 시절의 장소를 방문한다. 그들은 둘 다 여기에 와서 이 방파제를 따라 여러 가판대에서 해산물을 맛봅니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4862, 27324, 16439, '1회에서 박주현(장영남)은 딸 이현지(고민시)와 룸메이트 김미진(윤지인), 아들을 데리고 주말 여행을 떠난다.잠시 후 그녀는 가까운 항구에서 마지막으로 목격된 사람을 찾기 위해 모두를 뒤로 하고 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4863, 30840, 16439, '10회에서 윤선우(김재영)는 이현지(고민시)에게 이 카페에서 만나자고 한다. 하지만 현지는 그녀를 산업스파이로 경찰에 연행하기 위한 것이라는 사실을 무시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4864, 24935, 16439, '1화에서 DEO 그룹의 만찬이 열리는 곳은 바로 이 술집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4865, 25613, 16439, '18화에서 김여옥(장미희)이 장제니(김선아)를 식당에 전화를 걸어 그녀를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4866, 30841, 16439, '10화(전체 5화)에서 제니 장(김선아)이 역 1번 출구에서 왕양(김영아)을 데리러 오는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4867, 30842, 16439, '3,4회에서는 이현지(고민시) 엄마의 친구 김미진(윤지인)이 아들 예윤(최동화)과 함께 이 집 옥상 아파트에 머문다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4868, 26000, 16439, '20화에서 위정혁(김태훈)이 장제니(김선아)의 역사와 그녀를 위해 기꺼이 희생할 것을 알고 있다는 것을 이해한 후 윤선우(김재영)가 이 다리 위에서 눈물을 흘린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4869, 26039, 16439, '1회에서 장제니(김선아)가 출소 후 목욕을 하고 데오그룹의 만찬을 준비하는 이 호텔.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4870, 26156, 16439, '16회(전체 8회)에서 좌절한 위예남(박희본)이 이 공원에 찾아오고 오태석(주석태)은 멀리서 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4871, 24945, 16439, '6회(전체 3회)에서 “바둑판에는 361개의 갈림길이 있습니다. 매 순간 살아남기 위해서는 선택을 해야한다."라고 이 길에 선 이현지(고민시)는 혼잣말을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4872, 25910, 16439, '8회(전체 4회)에서 살인사건 루머가 돌고 있는 동네 식당이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4873, 24946, 16439, '스파이&파티룸\'에서 제니장(김선아)은 1화에서 욕을 하는 장군 김진석에게 수업을 지시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4874, 24952, 16439, '16회에서 이현지(고민시)와 윤선우(김재영)는 식당에서 함께 저녁 식사를 하며 제니 장(김선아)을  짝사랑하며 그를 일으켜 세웠다고 고백했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4875, 25913, 16439, '1화에서 제니 장(김선아)이 운영하는 \'J 부티크\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4876, 25342, 16439, '이 곳은 23회에서 거북이 시장에서 박주현(장영함)이 오태석(주석래)에게 선물로 자켓을 사고 싶어하는 \'영천시장\'으로 등장한다. 그러나 그녀는 건물을 떠나면서 건설 현장의 금속 파이프에 맞아 주현이 비슷한 파이프로 자신을 죽일 뻔했던 기억을 되찾는다. 그녀는 목숨을 걸고 달리다가 그녀는여러 무리의 사람들에 의해 제지당합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4877, 26140, 16439, '이 건물의 일부가 오태석(주석태)의 사무실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4878, 26246, 16439, '이 건물의 일부가 오태석(주석태)의 사무실로 등장한다. 6화에서 위예남(박희본)이 그를 찾으러 이곳으로 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4879, 30843, 16439, '장제니(김선아)는 우연히 이 카페를 지나가게 된다. 12화에서 이현지(고민시)가 샌드위치를 먹고 있는 모습을 보고 깜짝 놀라는 모습을 보였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4880, 25864, 16439, '그의 새로운 후원자는 누구인지 확인합니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4881, 24915, 16439, '4화에서 더호텔의 27번째 세미나 위크는 이 호텔 웨스트타워에서 열리며, 위예남(박희본)은 두 기자와 함께 오빠 위정혁(김태훈)을 미행한다. 그녀는 오빠의 비밀 중 하나를 발견하기를 바랐지만, 대신 그녀가 발견하는 것은 그녀가 상상할 수 있었던 것보다 더 큰 놀라움이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4882, 29460, 4573, '김사랑과 현빈이 선보러 만난 미술관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4883, 30844, 4573, '워크숍 따라가려 현빈이 사극 분장하고 촬영했던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4884, 30845, 4573, '현빈이 하지원의 열렬한 팬이라고 말했던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4885, 30846, 4573, '1층에서 현빈이 입술에 거품 묻힌 평창동 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4886, 30847, 4573, '현빈이 하지원 비명소리 듣고 찾으러 다닌 숲길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4887, 25724, 13459, '이윤성(이민호)과 김나나(박민영)가 독립적으로 이 다리 밑에 있는 서울컬러파크에 와서 그들의 시간을 회상하고 그들이 다시는 함께하지 못할 것이라는 것을 그녀에게 알려주는 것이 16화이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4888, 27239, 13459, '3회에서 이윤성(이민호)과 김나나(박민영)가 이곳에서 만나 분수대에서 아이들과 노는 모습이 그려졌다. .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4889, 25854, 13459, '이경완(이효정) 의원의 책 출간은 3화에서 이 클럽에서 이뤄지며, 이윤성(이민호)은 특정 아동복지기금을 착복한 5인의 협의회에 대해 지속적으로 조치를 취하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4890, 30848, 13459, '3~4화에서 윤성이(이민호) 이 식당에 몰래 찾아와 어머니 이경희(김미숙)를 멀리서 바라보는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4891, 26071, 13459, '1화에서 이윤성(이민호)이 서울에 도착해 이곳에 들렀고, 김나나(박민영)는 불과 몇 미터 떨어진 곳에서 전단지를 나눠준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4892, 25348, 13204, '17회에서 조국(차승원)이 이 건물을 앞을 지나가는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4893, 26654, 13204, '조국(차승원)의 아버지인 장로(최일화)가 이곳에 산다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4894, 26604, 4160, '24화에서 음식 축제를 마치고 돌아오는 길에 이성찬(김래원)과 김진수(남상미)가 배고픈 배를 채우기 위해 이 식당에 갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (4895, 26153, 4160, '1회에서 오 셰프(최불암)의 거주지이자 레스토랑인 \'우남정\'으로 나타난다.');
