INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5191, 26322, 13417, '4화에서 아다모(성훈)가 승마를 하러 오고, 단사랑(임수향)이 친구 금라라(한혜린)와 함께 이 클럽에 합류해 다모와 사란이 다시 만나는 방식이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5192, 27536, 13417, '단사란(임수향)이 입주할 기생수련원은 이곳에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5193, 25976, 14779, '12회에서 푸른 초원을 걷다. 두 사람의 첫 무알콜 키스가 이루어지는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5194, 26652, 14779, '1화에서 강현민(안재현)은 이곳에서 두 친구와 클럽 파티를 합니다.그는 클럽에 처음으로 본소녀를 데려와 할아버지의 결혼식을 망치기로 결정합니다. 그리고 여기에 피자를 배달하는 은하원(박소담)이 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5195, 25811, 14779, '신데렐라와 네 명의 기사 4회에서 강지운(정일우)이 이 회장의 수영장에서 수영을 하고 있다. 그리고 여기에서 은하원(박소담)이 그를 찾아 모두와 함께 아침식사를 하도록 설득한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5196, 30821, 14779, '2, 7회에서 은하원(박소담)이 아르바이트를 하는 장소로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5197, 27326, 14779, '14회에서 옛 가족사진을 훑어볼 때 강지운(정일우 분)이 알아차리는 카페다. 사실 그곳은 그의 부모님이 자주 방문했던 카페이고 그의 어머니가 그와 그의 아버지를 위해 메모를 남긴 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5198, 28405, 14779, '15회에서 은하원(박소담)과 강지운(정일우)이 수술 전 이곳을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5199, 25612, 14779, '8회에서 승마를 마치고 돌아오는 길에 은하원(박소담)과 강지운(정일우)이 마주치는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5200, 25908, 14779, '강종두(김용건) 회장의 사무실로 7회에서 은하원(박소담)이 강종두(김용건) 회장을 만나러 찾아간 장면에서 이 곳을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5201, 28092, 14779, '이 곳은 하늘의 집으로 등장한다. 한글그룹 강회장의 손자 강지운(정일우), 강현민(안재현), 강서우(이정신) 여기에 살고 은하원(박소담)은 그들과 함께 이사한다. 2화에서는 이 건물이 \'세계 100대 근대건축물\' 중 하나라고 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5202, 25615, 14779, '9회에서 은하원(박소담)은 그녀가 비누에 미끄러져 기절 한 후이 병원에서 치료를 받는다. 믿기지 않을 정도로 그녀는 경미한 뇌진탕과 염좌밖에 없다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5203, 25668, 14779, '1화에서 강현빈(안재현)이 은하원(박소담)에게 이곳에서 세 시간 동안 약혼녀가 되어 달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5204, 27535, 14779, '16화에서 강서우(씨엔블루 이정신)가 콘서트를 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5205, 31537, 14779, '5회에서 은하원(박소담)은 올해 세 남자가 모두 참석하는 두 번째 미션으로 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5206, 24948, 13624, '14회에서 최재하(주상욱)가 고준을 배신하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5207, 31538, 3712, '남여 두 주인공이 결혼식을 올리는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5208, 26253, 18039, '6회 영부인과 미국에 도착한 직후 김안나는 이 다리에서 사고를 당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5209, 26166, 18039, '주인공 이용국(지현우)이 사는 집으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5210, 31539, 18039, '36회에서 조사라(박하나)와 차건(강은탁)은 이 카페에서 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5211, 27326, 18039, '김안나(이일화)는 11회에사 이 카페를 방문했을 때 박수철(이종원)과의 데이트를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5212, 31540, 18039, '48회에서 봉준오(양병열)와 강미림(김이경)은 이 식당에서 점심을 먹는다. 다른 테이블의 남자아이가 음식을 먹고 숨이 차서 숨이 막혀오자, 준오는 의대생 시절 배운 것을 적용해 도움을 주기 위해 달려와 소년의 생명을 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5213, 25816, 18039, '5화에서 박수철(이종원)은 미국에서 막 도착한 아가씨 안나 김(이일화)을 데리러 공항으로 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5214, 26721, 18039, '이용국(지현우) 이 카페에서 조사라(박하나)를 만난다. 48회에서 선배님과 가족들과 떨어져 달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5215, 25643, 18039, '36회에서 이영국(지현우)은 아이들과 박단단(이세희)과 함께 이 동굴을 찾는다. 이것은 영국과 단이가 데이트를 시작한 직후이고 그들이 혼자 있을 때, 그가 키스를 하려고 몸을 기울였을 때, 그녀가 그의 아이들이 다가오는 것을 듣고 그를 밀어내는 것에 그들은 방해를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5216, 26078, 18039, '박단단(이세희)은 저녁식사를 마치고 카페에서 디저트를 사달라고 이용국(지현우)에게 제안한다. 11회에서 그녀는 이 장소에서 아르바이트를 한 적이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5217, 31541, 18039, '30,31회에서 약혼을 깨기 위해 이용국(지현우)은 이 카페에서 조사라(박하나)를 만나자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5218, 25612, 18039, '박수철(이종원)은 1회에서 전 여자친구이자 박단단(이세희)의 어머니와 함께 이 정원에서 보냈던 행복한 순간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5219, 26200, 18039, '차건(강은탁)과 조사라(박하나)는 51회에 이 식당에서 점심 데이트를 즐긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5220, 24932, 18039, '14회에서 이용국(지현우)과 이세련(윤진이)이 이 식당에서 점심을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5221, 24933, 18039, '8회에서 이 카페에서 이용국(지현우)은 안나 김(이일화)을 만나 가족과 함께 입주할 수 있는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5222, 24900, 18039, '47회에서 이영국(지현우)와 박단단(이세희)은 책 출간 행사에 참석하기 위해 따로 찾아왔다. 특히 단단이 그녀의 대학 친구 마현빈(이태리)과 함께 여기에 있기 때문에 둘 모두에게 어색하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5223, 26051, 18039, '7회에서 이용국(지현우)의 아이들은 용돈을 모아 박단단(이세희)을 놀라게 하고 그녀를 아빠 용국과 함께 공원에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5224, 25655, 18039, '이영국(지현우 분)은 36회 엔딩에서 박단단(이세희 분)을 이 식당에서 저녁식사에 초대해 반지를 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5225, 26085, 18039, '41화에서 안나 김(강세정)이 운영하는 패션 회사의 본사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5226, 27072, 18039, '이용국(지현우)이 운영하는 FT그룹 사옥으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5227, 29457, 18039, '36회에서 이세련(윤진이)은 이 카페에서 오승호(한재석)를 만나 사실 아가씨는 숨은 아이가 있다는 사실을 알고 결혼식을 취소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5228, 31542, 18039, '3번출구 이영국(지현우)의 아이들은 박단단(이세희)을 위해 서프라이즈를 생각해냈고, 막내 이재니(최명빈)는 단단에게 물었다.7회에서 이 역 3번 출구에서 만나기로 했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5229, 26136, 18039, '28회에서 이용국(지현우)과 조사라(박하나)는 이 식당에서 낭만적인 저녁 식사를 한다. 알리고, 그들은 데이트 중입니다. 단단이 그들과 합류하자 안나는 충격을 금치 못하고 최대한 빨리 변명한다. 47회에서 안나는 이 식당에서 단단을 다시 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5230, 26137, 18039, '이세련(윤진이 분)은 12회에서 이 호텔 \'테라스 라운지\'에서 소개팅을 진행한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5231, 25909, 18039, '12화에서 박수철(이종원)은 이곳에서 자신이 장미숙(임예진)의 오래전 잃어버렸던 동생이라는 사실을 알게된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5232, 25668, 18039, '46화에서 길을 잃은 박단단(이세희)은 이 다리 발치에 앉았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5233, 24944, 18039, '34회에서 박대범(안우연)이 이세련(윤진이)을 이 식당으로 초대해 식사를 하는 장면이 나온다. 이것은 작별 선물입니다."라고 그는 둘 다 식당을 떠날 때 그녀에게 노란 튤립 꽃다발을 건네면서 말합니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5234, 31543, 18039, '1화에서 이용국(지현우)은 어린 박단단(지현우)을 집에 데려다 주는 길에 이 건물을 지납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5235, 31544, 18039, '박단단(이세희)이 자란 동네 골목으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5236, 31545, 18039, '33회에서 박단단(이세희)은 이 카페에서 옛 동네 사람을 만나고, 친어머니에 대해 알아본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5237, 27109, 13673, '1회에서 진(장동건)과 서이수(김하늘)는 드레스가 찢어진 뒤 뒤를 바짝 바짝 걸어가다가 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5238, 31546, 13673, '홍세라가 골프연습을 하던 골프장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5239, 25641, 13673, '김도진(장동건), 서이수(김하늘), 콜린(이종현)이 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5240, 25607, 13673, '16회에서 김도진(장동건)과 서이수(김하늘)가 이 공원으로 소풍을 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5241, 25605, 13673, '1화에서 갑자기 초록빛 풍경(안성팜랜드)으로 변신해 도진이의 기분을 잘 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5242, 31547, 13673, '김도진이 서이수에게 프로포즈한 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5243, 26308, 13673, '신사의 품격 1회와 마지막회를 촬영한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5244, 27111, 13673, '김도진(장동건)과 서이수(김하늘) 1회에서 드레스가 찢어진 뒤 바짝 뒤를 바짝 뒤쫓던 그가 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5245, 31548, 13673, '3회에서 김도진이 출사를 나간 테마파크');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5246, 25958, 13673, '5회에서 서이수(김하늘)와의 데이트를 떠올리며 가슴 아픈 김도진(장동건)이 이 카페에 찾아온다. 잠시 후 김은희(박주미 분)가 합류하면서 아들이 있다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5247, 31549, 13673, '김도진과 임태산이 방문한 인테리어 매장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5248, 26095, 13673, '13회에서 콜린(이종현)이 도주한 후 무료 콘서트를 한다. 4명의 신사는 그를 찾기 위해 홍대로 나섰고, 마지막 회에서 콜린 앞에 서게 된다. “네 명 중 누군가가 내 아버지라고 들었다. 누구세요”라고 인사를 건넨다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5249, 31550, 13673, '서이수와 김도진이 벚꽃키스를 한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5250, 31551, 13673, '15회에서 서이수(김하늘)는 김도진(장동건)에게 이 건물 앞 사거리(\'강남사거리\'로 등장)에서 만나자고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5251, 25882, 13673, '18회에서 임메아리(윤진이)와 최윤(김민종)은 이 공원에서 데이트를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5252, 31552, 13673, '이 학교에서 서이수(김하늘)는 선생님으로 등장합니다.');
