INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5291, 31583, 13709, '드라마에서 나오는 타임슬립 포털 중 하나가 위치한 곳이 바로 이 사찰이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5292, 26487, 13709, '최종회 24회에서 최영(이민호)은 기철(유오성)과 이 나무 밑에서 마지막 싸움을 벌인다. 최영이 마지막 장면에서 유은수(김희선 분)와 재회하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5293, 26080, 16308, '한양의 일부로 등장합니다. 예를 들어, 1화의 공개 낭독 장면이나 2화(전체 1화)에서 이림(차은우)이 도시를 탐험했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5294, 26070, 16308, '6회(전체 3회)에서 이림(차은우)이 그의 지루한 궁전 생활 탈출을 위해 향하는 \'온양 별장\'으로 등장한다. 여기에서 그는 묘비를 보았지만 밤에 다시 찾을 수 없었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5295, 26072, 16308, '이 정원은 궁전의 일부로 등장. 2화(전편 1화)에서 이림(차은우)이 허삼보(성지루)와 함께 형 이진(박기웅)의 허락을 받아 궁에서 몰래 빠져나오며 이 정원을 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5296, 25673, 16308, '1회에서는 역사가 구해령과 허삼보(성지루)와 함께 다리를 건너는 이림(차은우)의 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5297, 26521, 14744, '12, 13회에서 김현지(김소현)은 이곳에서 치료를 받는다 다룬다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5298, 31592, 14744, '박봉팔(옥택연)과 김현지(김소현)는  9회에서 이 다리를 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5299, 25633, 14744, '8화에서 김현지 (김소현)는 계단 위에서 사라진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5300, 25016, 14744, '이 아파트는 박봉팔(옥택연)이 있는 곳이다. 삼촌 스님 명철(김상호)과 유령 친구 김현지(김소현)와 함께 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5301, 26045, 14744, '7회에서 현지(김소현)는 봉팔(택연)이 주관하는 모의고사에서 80%를 돌파하며 놀이공원에 가고 싶다는 소원을 빌었다. 봉팔은 내일 시험을 마치고 그녀를 데려가기로 했지만 오토바이에 치인 서연(백서이)을 병원으로 데려가느라 아쉽게도 현지를 만나지 못했다. 8화에서 봉팔은 그날 엑소시즘 공연을 마치고 현지를 놀이공원으로 데려가 현지와의 약속을 지킨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5302, 25814, 16573, '심보경(정인선) 부모님이 운영하는 카페 \'심심한 날\'로 등장한다. 육동식(윤시윤)은 아버지의 개 산책을 돕기 위해 이곳에 온다. 3화에서 동식이 내리는 가상의 \'버스정류장\'은 카페에서 조금 더 아래에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5303, 29693, 16573, '주인공 아버지의 식당 \'육군의 미트공화국\'으로 등장한다. 1화에서는 동식이 기억을 잃었다는 사실을 알게 된 동식의 가족들이 이곳에서 긴급회의를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5304, 26400, 16573, '육동식(윤시윤)이 동료이자 친구인 박재와 함께 온다. 1회에서 재호(김기두)가 회사 스캔들로 자신도 모르게 희생양이 된 후 모습을 보였다. 재호는 동식에게 보고의 책임을 물으라고 애원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5305, 31598, 16573, '11회에서 육동식은 서인우(박성훈)와 이 술집에서 만난다. 남자들이 이야기하고 동식은 절망에 빠져들자 인우는 동식에게 약을 먹이고 그에게서 빨간 일기장을 훔쳐 심보경(정인선)이 찾도록 술집에 심는다. . 12회에서 동식은 일기장을 들고 있던 보경과 마주한다. 이 장소는 10화에서도 인우가 술을 마시는 장면이 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5306, 25937, 16573, '육동식(윤시윤)이 런닝 후 목이 말라 비틀거리며 이 마트를 향한다, 심보경(정인선)이 지켜보는 가운데,');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5307, 26125, 16573, '4화에서 서인우(박성훈)가 육동식(윤시윤)과 함께 이 재즈라운지를 찾아온다. 동식은 입구에서 멈춰 서지만 인우는 동식을 손짓한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5308, 25809, 16573, '12, 13회에서 윤동식(윤시윤)이 살인 혐의를 모두 자백한 후 이 곳에 갇힌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5309, 30593, 15063, '7화에서 이 건물은 김주만(안재홍)이 일하는 회사로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5310, 31599, 15063, '쌈마이웨이에서 항상 나온 계단식주택가 골목');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5311, 31600, 15063, '쌈마이웨이에서 남일바로 나온 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5312, 24935, 15063, '2회에서 최애라(김지원)의 결혼식이 끝나고 모두가 술을 마시러 가는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5313, 26228, 15063, '쌈 마이웨이 6회에서 술 취한 김탁수(김건우)가 이 곳에서 여자친구와 어울린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5314, 25613, 15063, '2회에서 이곳은 최애라(김지원)가 참석하는 결혼식이 진행되는 곳으로, 나타나지 않는 아나운서를 대신한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5315, 31601, 15063, '쌈마이웨이 1회에 나온 열무 보리밥 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5316, 31602, 15063, '15회에서 고동만(박서준)이 병원에서 치료를 받고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5317, 26018, 15063, '4회에서 이 카페 앞 거리는 회상 장면이 나온 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5318, 26294, 15063, '7-8회에서 고동만(박서준)은 낙심한 최애라(김지원)를 찾는다. 신발도 벗지 않은 채 이 건물 앞 벤치에 앉아 있다. 그녀가 편안하게 울 수 있도록 그녀의 얼굴에 스웨터를 던졌습니다. 그녀는 잘 울고 나서 일어서서 동만에게 그런 행동을 할 때마다 상처가 나기 때문에 그만하라고 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5319, 24941, 15063, '쌈마이웨이에서 나온 가정집 분위기의 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5320, 27318, 15063, '2화에서 고동만(박서준)은 친구의 부름을 받아 방송에서 누군가를 대신해 이곳으로 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5321, 26741, 15063, '2회에서 최애라(김지원)와 고동만(박서준)이 김밥을 먹으러 이 공원에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5322, 30501, 15063, '쌈마이웨이에서 격투경기가 진행된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5323, 31596, 15063, '쌈마이웨이 3화에 나온 옥상정원의 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5324, 27361, 15063, '4회에서 고동만(박서준 분)이 직장을 그만두는 결정적인 결정을 내리는 곳은 바로 이 거리의 사거리에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5325, 25620, 15063, '최애라(김지원)가 이곳에서 직원으로 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5326, 25912, 15063, '3화에서 박무빈(최우식)은 최애라(김지원)에게 반했고, 최애라에게 운동화를 선물하기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5327, 25637, 15063, '박무빈(최우식)이 일하는 곳입니다. 7회에서 최애라(김지원)가 치료를 받고, 무빈이 그녀에게 진짜 동의 없이 키스를 하고, 고동만(박서준)이 이를 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5328, 26165, 16959, '4화에서 이 계곡은 \'드림 월드\'의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5329, 31603, 16959, '쌍갑포차 4회에서 나온 망자 운동회 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5330, 24930, 16959, '쌍갑포차 6회에서 나온 꿈 속 이야기');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5331, 26080, 16959, '1회에서 조선시대 왕세자 이헌(송건희)이 사는 궁궐의 일부이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5332, 26256, 16959, '11회에서 이 사찰에 기도하러 온 귀부장/한강배(육성재)와 그가 떠날 때, 그는 자신을 기다리고 있는 월주(황정음)를 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5333, 31604, 16959, '이 카페에서 매니저 한강배(육성재)와 월주(황정음)가 만난다. 11회에서 녹차 한 잔을 마시며 500년 동안 간직한 옥반지를 드디어 그녀에게 건넸다. 같은 시간 동안 그를 미워하게 만든 오해를 마침내 풀고 난 뒤의 일이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5334, 25659, 16959, '1화에서 월이우(황정음 분)가 운영하는 이 공원의 포차 세트장이 이 공원에 있다. 한강배(육성재 분)가 다가오는 트럭 앞에서 침착하게 걸어가는 송미란(박하나 분)과 마주치는 것도 이 공원에서다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5335, 26683, 16959, '쌍갑포차 3회에서 \'상일호텔\'의 실제 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5336, 31605, 16959, '쌍갑포차 2회에서 고씨가 술 마시던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5337, 31606, 16959, '쌍갑포차 3회에서 나온 대나무숲');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5338, 26487, 16959, '왕세자 이혼(송건희)이 병에서 회복하고 있다. 이후 어린 월주(박시은)는 나무에서 생을 마감한다. 11화에서 세자는 월주를 위해 이 나무 아래에서 슬퍼하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5339, 31607, 4830, 'MT 가는 기차의 배경이 되는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5340, 25345, 4830, '영화 속 길거리 등장 장면의 배경');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5341, 31608, 4830, '나미의 첫사랑과 수지의 키스 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5342, 26518, 4830, '주인공 두 나미의 위로와 만남의 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5343, 31609, 4830, '극중 나이(심은경)가 빙의 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5344, 31610, 15070, '이곳은 한정연(공승연)의 집으로 등장하며, 그녀와 김우진(여진구)이 한교수의 서재에서 무언가를 찾기 위해 찾아온 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5345, 27535, 15070, '1회에서 스마트 어스에서 김준혁(김강우)이 살인 사건 해결과 그의 형제를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5346, 25866, 17644, '1회에서 선우찬(이정신)과 박광복(권현빈)이 공항에 도착합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5347, 31617, 6425, '숙희가 물감과 이젤을 들고 올라오는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5348, 31618, 6425, '영화 아가씨에서 스님이 돈을 건넬 때 나온 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5349, 31619, 6425, '영화 아가씨에서 저택을 향하는 길에 등장하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5350, 25612, 6425, '백작과 숙희가 은밀한 거래를 두고 다투는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5351, 27207, 6425, '영화 아가씨에서 백작과 히데코가 식사한 공간');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5352, 28571, 6425, '히데코와 숙희가 열심히 달리기 하는 갈대밭');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5353, 27120, 6425, '영화 아가씨에서 정신병원 씬으로 나왔던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5354, 25931, 6425, '숙희와 히데코의 탈출문이 되는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5355, 31620, 6425, '히데코의 이모가 목을 멘 나무가 있는 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5356, 31621, 6425, '변태성욕자인 남작의 집으로 가는 길을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5357, 31622, 6425, '아가씨 1부 초반부 바닷가 옆 절벽장면 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5358, 31623, 6425, '영화 아가씨에서 보트타고 호수를 건너는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5359, 27048, 15699, '우연한 기회에 차주혁(지성)이 서우진(한지민)과 함께 온다. 12회에서 이야기를 더 하고 추억을 되새기기 위해 다시 이 장소를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5360, 31624, 15699, '13회에서 우진의 엄마(이정은)는 집 밖에서 차주혁(지성)을 이 카페로 데려온다. 그녀가그에게 제품을 판매합니다. 반면에 주혁은 그녀의 건강한 모습을 보고 기뻐하며 그녀의 제품을 사게 되어 기쁘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5361, 31625, 15699, '이 버스 정류장은 차주혁(지성)과 서우진(한지민)이 함께 일하는 은행 근처에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5362, 25718, 15699, '회사 회식을 마친 후 차주혁(지성)과 서우진(한지민)은 이 공원 맞은편 쇼핑 거리를 걷는다. 그때 오토바이 운전자가 우진을 향해 돌진하고 주혁은 우진을 안전하게 끌어내기 위해 손을 뻗는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5363, 31626, 15699, '11회에서 종후는 주혁이 서우진(한지민)에게 거절당한 이유를 알고 로비에서 주혁을 때렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5364, 31627, 15699, '3회에서 서우진(한지민)은 은행 글로벌 본사 직원으로 이 카페에서 일본인 고객을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5365, 28423, 15699, '12회에서 차주혁(지성)이 길 건너 서우진(한지민)을 발견하고 이 가게 밖 거리에 선다. 극중 \'아메리트 카페\' 로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5366, 25908, 15699, '차주혁(지성)의 현 아내인 이혜원(강한나)의 아버지의 사무실로 등장한다. 12화에서 이곳에서 대화를 나누는 등장인물을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5367, 24932, 15699, '15회에서 극 중 차주혁(지성)과 서우진(한지민)이 비밀 데이트를 위해 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5368, 26135, 15699, '차주혁(지성)은 이 카페에서 이혜원(강한나)을 만난다. 14회에서 주혁이 점심을 먹고 은행으로 돌아오는 길에 우연히 마주쳤다. 그녀는 대학 때 그를 좋아했다고 그에게 고백하지만, 이번에는 그에 따라 행동하지 않기로 하고 대신 자리를 뜬다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5369, 27602, 15699, '한지민(한지민)의 집은 모든 시간/우주를 통해 어머니(이정은)와 함께 산다. 차주혁(지성)은 드라마 전반에 걸쳐 여러 번 등장하고 윤종후(장승조)는 첫 번째 대안 우주에서 우진을 구애할 때 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5370, 31628, 15699, '드라마에서 두 주인공 서우진(한지민) 과 혁(지성)이 가끔 손을 잡고 집으로 가는 길에 이 마트를 여러 번 지나간다 .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5371, 25773, 15699, '13회에서 어린 서우진(한지민)은 아버지의 중국 출장을 막고 대신 건강 검진을 받으러 갑니다. 그곳에서 윤종후(장승조)가 자신의 불행한 여자친구에게 사과하려고 하는 것을 목격한다. 이후 그녀는 그들을 다시 모으기 위해 미니소동을 일으킨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5372, 31629, 15699, '12회에서 차주혁(지성)과 서우진(한지민)이 자신들의 상황을 이야기하기 위해 찾아왔고, 주혁은 무릎을 꿇는다. 그리고 과거 우진을 대했던 것에 대해 눈물을 흘리며 사과한다. 우진은 한 번만 더 기회를 주기로 하고 이번에는 진심임을 보여주라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5373, 25924, 15699, '차주혁(지성)과 이혜원(강한나)이 다녔던 대학으로 나온다. 마지막 16화에서 주혁은 서우진(한지민)의 산책을 다시 데려오고 그들의 미래에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5374, 26540, 15699, '14회 차주혁(지성)이 이 건물에 있는 남자 화장실에 몸을 숨기고 서우진(한지민)이 그를 찾아라.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5375, 31630, 15699, '2회에서 차주혁(지성)과 윤종후(장승조)가 테이크 아웃 아침식사를 위해 이 건물 밖에 있는 보도에서 만난다. 그들이 게임 콘솔에 대해 이야기할 때, 주혁은 종후가 항상 거기에 있었다고 말하는 자신의 새로운 흉터를 발견합니다. 4화에서는 출근길에 서우진(한지민)을 만나고 종후는 조각품 근처 벤치에서 서우진(한지민)을 아침식사로 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5376, 26000, 15699, '서우진(한지민)은 2회에서 여기로 달려갑니다. 동시에 차주혁(지성)은 자신이 첫사랑인 이혜원(강하나)과 결혼했다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5377, 30638, 15699, 'KCU 가현점으로 등장하며 차주혁(지성), 서우진(한지민), 윤종후(장승조)가 이 장소에 자주 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5378, 26002, 15699, '15회에서는 우진이 항상 롤러코스터를 타고 프로포즈를 받고 싶어했던 차주혁(지성)과 서우진(한지민)이 이곳에 온다. 우진의 기쁨과 주혁의 불편함에 롤러코스터 프로포즈는 (두 번) 일어나고, 이후 지상에서 그녀는 공식적으로 그에게 두 번째 승낙을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5379, 31631, 15699, '서우진(한지민)과 차주혁(지성)이 처음 만난 대전의 버스정류장으로, 시리즈 내내 다양한 회상장면에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5380, 24952, 15699, '차주혁(지성)의 친구 오상식(오의식)이 운영하는 식당이다. 1화에서 주혁은 아내와 싸운 후 밥을 먹으러 찾아와 친구들에게 이혼하고 싶다고 말한다. 2화에서는 또 한 번의 싸움 끝에 주혁이 이곳에 다시 왔지만 문이 닫혀있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5381, 31632, 15699, '서우진(한지민)이 학창시절 즐겨 먹던 떡볶이집. 5회에서는 차주혁(지성)이 일찍 퇴근한 뒤 식사를 위해 이곳으로 데려왔다. \'대체\' 세계에서도 그는 그곳을 생생하게 기억한다. 나중에 그들은 떠날 때 비가 왔지만 식당에서 화분을 들고 버스 정류장을 향해 함께 거리를 달리면서 비를 피합니다. 그들은 15화에서 강선우(조정석)의 식당을 나와 두 번째 \'대체\' 세계로 돌아옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5382, 31633, 15699, '12회에서 차주혁(지성)이 과자와 소주 한 병을 들고 의문의 남자를 찾아 지하철역으로 온다. 주혁은 자신이 주변 사람들을 불행하게 만든다는 사실을 깨닫고 자신이 (다른 우주에서) 겪고 있는 것은 욕심이 너무 많은 것에 대한 "하늘의 징벌"이었다는 것을 어떻게 깨닫는지 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5383, 31634, 15699, '7회에서는 차주은(박희본)과 오상식(오의식)이 이 팬션으로 차주혁(지성), 이혜원(강한나), 서우진(한지민), 윤종후(장승주)를  초대하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5384, 25680, 13704, '2회에서 짜증과 좌절에 빠진 아랑(신민아)이 이 \'나무다리\'를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5385, 25790, 13704, '그는 5화에서 아랑(신민아)이 자신의 \'입냄새\' 때문에 어머니가 분명히 떠났다고 말하자 화가 나서 이곳에 온다. 7화에서 은오는 이 절벽의 나무에 올려놓은 부적을 찾아 다시 찾아온다. 부적을 얻기 위해 나무 위로 올라가다가 절벽에서 떨어져 그를 찾으러 온 아랑의 손에 구해진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5386, 25831, 13707, '4회에서 구재희(설리)는 오빠가 찾아왔을 때 이곳이 자신이 다니는 고등학교라고 속였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5387, 31635, 13707, '이 펜션은 "파라다이스 펜션"으로 등장한다. 3,4회에서 하승리(서준영)의 어머니가 운영하며 주말에 모두를 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5388, 25607, 12564, '이선재(류시원)와 김세나(이정현) 14회에서 가수 데뷔 전 연습을 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5389, 25678, 12564, '23회에서 김연수(최지우)와 이민철(이병헌)이 이곳으로 신혼여행을 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5390, 26621, 12564, '이 카페는 중요한 만남의 장소다. 1층은 극 중 주인공들이 여러 번 만나고, 16화에서 이민철(이병헌)이 김연수(최지우)와 헤어진 곳이기도 하다. 2층은 \'뮤즈레코드\' 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5391, 25720, 12564, '이민철(이병헌)이 6회에서 이민지(신민아)와 김연수(최지우)가 번 첫 \'월급\'(쇼핑몰에서 그림을 그려 번 돈, 연수 주최)을 축하하기 위해 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5392, 26639, 12564, '15회에서 이선재(류시원)가 뮤즈 사장 양경희(이휘향)의 \'약속 데이트\'로 왔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5393, 28421, 12564, '4회 초반, 이민철(이병헌)은 10번 출구에서 택시에서 김연수(최지우)를 내려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5394, 27507, 12564, '19회에서 김연수(최지우) 미술학원이 이 거리와 가깝고 고민에 빠진 이민철(이병훈)이 이곳으로 온다. 버스를 타고 학원을 나온 연수는 버스 창밖으로 민철을 보고 버스 기사에게 멈춰달라고 부탁하고 민철을 향해 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5395, 25793, 12564, '이선재(류시원)의 비밀 장소는 이 역 5번 출구 위에 있다. 11회에서 김연수(최지우)를 이곳에 데려와 훈훈한 대화를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5396, 31636, 12564, '11회에서 이순재(류시원)는 친아버지가 누구인지 알게 된 후 여행을 가기로 결심한다. 상황의 심각성을 이해한 김연수(최지우)는 그에게 작별인사를 하러 찾아와 이민철(이병헌)의 중요한 저녁 초대를 무시한다. 결국 순재는 여행을 가지 않기로 하고 연수와 함께 역에서 나와 역 앞 택시 승강장에서 잠시 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5397, 26000, 12564, '10회에서 이순재(류시원)와 김세나(이정현)가 음반 계약을 하지 못한 것에 대해 좌절감을 느끼며 등장한다. 24회 마지막회에서 김연수(최지우)의 삶에 대한 두려움으로 술을 마시러 돌아왔다. 13회에서 이민철(이병헌)과 연수는 여기에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5398, 31637, 12564, '김연수(최지우)의 절친 강나래(이유진)는 이 건물의 아파트에 산다. 연수는 결국 그녀와 함께 여기로 이사한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5399, 26002, 12564, '7회에서 이선재(류시원)는 김연수(최지우)를 데리고온다. \'언니\' 김세나(이정현)가 처음으로 무대에 오르는 모습을 볼 수 있다. 연수와 함께 시간을 보낸다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5400, 26311, 12564, '14회에서 이민철(이병헌)과 김연수(최지우) 그리고 민철의 누나 이민지(신민아)가 자전거를 타고 오후에 이곳을 찾는다. 민지는 이 공원의 조각공원에서 드로잉 연습을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5401, 25335, 12564, '이 다리는 이민철(이병헌)이 집에서 출근하는 길에 놓여 있습니다. 3화에서는 혼자 차로 건너고, 5화 초반에는 김연수(최지우)와 함께 차를 타고 출근한다. 7화에서 연수는 다시 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5402, 26242, 12564, '14회에서 김세나(이정현)의 뮤즈 가수 소개는 선상콘서트 현장에서 이뤄졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5403, 25347, 12564, '9화에서 이선재(류시원)는 밤에 이곳을 찾아와 고민한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5404, 25608, 12564, '1화에서 김연수(최지우)와 김세나(이정현)는 크리스마스 이브에 이곳에서 만나기로 약속한다. 세나가 추위 속에서 기다리는 동안, 연수는 탑에 뛰어오다가 사고를 당해 나타나지 않아 세나의 눈에는 \'배신자\'가 된다. 그가 회복된 후, 그녀는 세나를 만나기 위해 정기적으로 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5405, 26997, 12564, '2화에서 여기에서 열린 Victory Records 파티의 촬영장으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5406, 25797, 12564, '이선재(류시원)가 이 다리를 건넌다. 아름다운 날들 9회에서 오토바이를 탄 다리.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5407, 26232, 12564, '이선재(류시원)는 11화에서 아버지가 누구인지 알게 된 직후 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5408, 26286, 12564, '양경희(이휘향)는 12회에서 이선재(류시원)와 만나고, 14회에서는 선재, 김세나(이정현)와 다시 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5409, 24959, 12564, '22회에서 그녀의 건강을 빌며 그녀에게 프러포즈를 했다. 그녀가 공항에 두고 온 반지가 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5410, 31638, 12564, '3회에서 이민철(이병헌)이 신인 스카우트를 위해 찾아와 김세나를 떠나려 할 때 (이정현) 무대에 올라 처음 만났을 때를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5411, 26159, 12564, '22회에서 김윤수(최지우)와 이민철(이병헌)의 결혼이 이곳에서 진행된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5412, 25017, 12564, '13회에서 이민철(이병헌)은 누나 이민지(신민아)와 함께 이곳을 떠나 파리로 떠날 예정이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5413, 31639, 17428, '우대성(여회현)은 차헌(WEi 김요한), 신솔이(소주연), 강하영과의 노래방 세션이 끝난 후 전화를 받기 위해 이 건물에서 나온다. 2회에서 (조혜주), 정진환 (정진완) from The Man BLK. 근처에서 학장을 발견하면 서둘러 자전거를 출발시키려고 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5414, 25644, 17428, '12회에서 유일고 선배들의 바베큐 파티가 열렸다. 여기에 신솔이(소주연)가 차헌(김요한)에게 고백을 하려 하지만 계획이 틀어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5415, 31640, 17428, '신솔이(소주연)는 8회에서 차헌(WEi 김요한)과 축구 경기를 관람하기 위해 이 경기장 서쪽 입구 1B 밖에서 기다리고 있다. 나중에야 티켓이 가짜였음을 알게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5416, 31641, 17428, '8회에서 신솔이(소주연)는 이 패스트푸드점에 온다. 아르바이트를 하던 우대성(여회현)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5417, 25958, 17428, '강하영(조혜주 분)이 18화에서 이곳(한국대학교 일부로)에서 공부하면서 남자친구 정진환(맨블럭 정진환 분)이 자신의 관심을 끌려는 팬들을 보고 짜증을 낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5418, 24939, 17428, '이 길의 인접한 길을 걸어간 후, 18화에서 차헌(김요한)과 신솔이(소주연)는 이 건물 바로 밖에 손을 잡고 서 있는데, 그곳에서 헌은 설이를 여자친구라고 부르며 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5419, 26293, 17428, '17회에서 강하영(조혜주)과 정진환(더 맨BLK 정진환)이 이곳에서 만나 서로의 대학 합격증을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5420, 25662, 17428, '15회에서 가방 쇼핑을 한 후, 그녀는 그에게 가방을 어머니에게 직접 선물해야 한다고 말한다. 18화에서 신솔(소주연)과 강하영(조혜주)이 커피를 마시며 만난다. 그리고 설이가 마침내 차헌(WEi의 김요한)과의 데이트에 대해 내뱉는 곳. 같은 회에서 우대성(여회현)이 매니저를 만나면서 손님들의 시선을 사로잡는 장면도 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5421, 26399, 17428, '차헌(김요한), 신솔이(소주연), 강하영(조혜주), 정진환(정진환)이 18회에서 더블 데이트를 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5422, 31642, 17428, '30년의 여름, 신솔이(소주연), 우대성(여회현), 강하영(조혜주), 정진환(정진환)은 20회에서 식당에서 재회한다. 진환은 한 사람이 없어졌다고 말하고, 그래서 차헌(WEi의 김요한)에게 전화를 걸었고, 결국 차헌이 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5423, 31643, 17428, '18회에서 차헌(WEi 김요한)은 이 건물 밖 아르바이트 전단배부 아르바이트를 하던 신솔이(소주연)를 만나러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5424, 31644, 17428, '2회에서 신솔이(소주연)는 차헌(WEi 김요한)과 같은 식당에서 그의 \'후광\'을 보게 되는 꿈을 꾼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5425, 27530, 17428, '8회에서 신솔에게 다시 거절당한 후 우대성(여회현)이 이곳에서 정진환(정진환)을 만나 슬픔에 잠기게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5426, 31645, 17428, '20회에서 30일 가을, 유일고 급우들이 강하영(조혜주)과 정진환(정진환)의 결혼식에 참석하기 위해 이곳에 모인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5427, 25910, 17428, '8회 신솔이(소주연)이 아르바이트를 문의했지만 이미 다른 사람을 고용한 주인에게 거절당한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5428, 27329, 17428, '헌은 용서를 구하지만 설이는 생각할 시간을 더 달라고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5429, 24922, 17428, '23회에서 신솔이(소주연)가 차헌(위아이 김요한)과 함께 이 거리를 걷고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5430, 31646, 17428, '7화에서 차헌(위아이 김요한), 신솔이(소주연), 우대성(여회현), 강하영(조혜주), 정진환( 더맨 BLK 정진환) 이 마을 건물에서 하룻밤을 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5431, 31647, 17428, '차헌(WE 김요한)은 2회에서 어린 시절 길을 잃은 차헌(김요한)이 다리를 건너 그를 찾았다고 회상했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5432, 30778, 17428, '차헌(김요한), 신솔이(소주연), 우대성(여회현), 강하영(조혜) 등 등장인물들이 다니는 \'유일고등학교\' 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5433, 30770, 14871, '37회에서 오동희 할머니가 이사를 가자고 해서 바다가 보이는 아름다운 집에 오게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5434, 31648, 14871, '오동희(박은빈)가 잠시 살고 있는 주문진읍 항구입니다. 집에서 항구가 보이고, 특히 한성준(이태환)이 보고 싶을 때 자주 온다. 동희가 도망친 곳을 성준이 알게 되면서 두 사람이 만나는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5435, 31649, 14871, '17회에서 이현우(김재원)는 이곳으로 형을 찾으러 옵니다. 그러나 그에게서 돈을 얻으려는 또 다른 사기꾼이 있을 뿐입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5436, 30518, 14871, '30회 오동희(박은빈)가 이 공원에 옵니다. 이곳은 한성준(이태환)이 그녀를 찾는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5437, 24900, 14871, '27회에서 글쓰기에 대한 두려움을 줄이기 위해 등장했습니다. 그는 알퐁스 도데가 그의 어깨에 기대고 잠들어 있는 동안 그가 가장 좋아하는 단편 소설 "별"을 그녀에게 읽어줍니다. 그리고 30화 동희는 글쓰기 대회에서 떨어졌다는 소식을 듣고 부끄러워 여기에 숨어 있습니다. 성준은 그녀를 발견하고 격려하고 결국 키스를 한다. 이 둘은 사랑스럽습니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5438, 31650, 14871, '한씨 가족이 사는 곳이다. 오동희(박은빈)도 한성준(이태환)과 한성준(이태환)의 만남의 장소이자 그녀의 할머니와 함께 옥상 아파트에 잠시 살고 있다. 성준의 형 이현우(김재원)는 성준의 양아버지가 부분적으로 일으킨 아버지의 부당한 죽음에 대한 복수를 위해 한 집안의 화목과 행복을 파괴할 목적으로 이곳의 거의 반대편으로 이사한다. .');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5439, 31651, 14871, '13회에서 한성준(이태환)과 오동희(박은빈)가 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5440, 25737, 14871, '12회에서 한성준(이태환)이 이곳에서 오동희(박은빈)에게 키스를 했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5441, 26667, 14871, '31회에서 이수경 분 아버지가 콘서트를 하고 어머니 한애리(윤미- Ra)남편이 공개석상에서 그녀를 부르자 홀을 그만두었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5442, 26178, 14871, '6회에서 결혼한 한성식(황동주)도 이 곳에서 조깅하는 척하며, 동료를 때리고 싶어하는 상상을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5443, 31652, 14871, '5회에서 오동희(박주혁) 은빈과 한성준(이태환)이 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5444, 25811, 14992, '이 장소는 마지막 52회에서 부모님 변한수(김영철)와 나영실(김해숙)의 결혼식의 입구이자 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5445, 24935, 14992, '37화에서 안중희 (이준)는 그가 자신의 아버지라고 생각했던 사람이 아버지가 아니라는 사실을 알게 된 후 이 곳으로 술을 마시러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5446, 25719, 14992, '여기는 소속사 가비엔터테인먼트 안중희(이준)와 계약하고 변미영(정소민)이 인턴으로 일하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5447, 25668, 14992, '38회에서 나영실(김해숙)이 안중희(이준)에게 신분 도용 사실을 폭로한 직후 이곳으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5448, 25347, 14992, '41회에서 안중희(이준)가 변미영(정소민)에게 아버지가 다르다고 말하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5449, 26125, 14992, '이 호텔&리조트는 드라마 30회 안중희(이준)의 촬영지인 일산 팔레스 호텔과당 그랜드 호텔로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5450, 31653, 16260, '아스달 연대기 (tvN, 2019)에 나오는 가상의 마을 \'아스달\'을 배경으로 한 촬영장입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5451, 26069, 16260, '4회에서 엄마의 뒤를 이어 백늑대가 펼쳐지는 모습을 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5452, 25641, 14601, '33화에서 이상태(안재욱)와 안미정(소유진)은 이곳에 식사를 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5453, 27179, 14601, '47화에서 이연태(신혜선)가 미래의 시어머니와 함께 \'프리다 칼로 & 디에고 리베라 전시회\'를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5454, 31667, 14601, '41회에서 이연태(신혜선)와 헤어지고 이곳에서 무리한 운동으로 기절했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5455, 25608, 14601, '12회에서 들은 대로 남산 데이트라고도 한다. 그곳에 가면 보통 연인끼리 자물쇠를 잠그곤 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5456, 24951, 14601, '이연태(신혜선)와 김상민(성훈)이 데이트를 한다. 46화에서 상민이 도시락을 가져오는 장면이 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5457, 25909, 13676, '염나리(임수향)는 부모님과 함께 이곳에서 저녁식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5458, 26150, 13256, '개인 병실에서 치료를 받는 조명호(이정길)의 피난처다. 13~14회에서 김현준(이병헌)과 김선화(김소연)가 부지에 도착하기 직전 백산(김영철)과 진사우(정준호)에게 위협을 받아 모두 살해당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5459, 27153, 13256, '14회에서는 이 전망대에서 이병헌) 대통령 조명호(이정길)와 비밀 회담을 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5460, 24957, 13256, '1회에서 김현준(이병헌)과 최승희(김태희)가 학생 시절 처음 이곳에서 만났고 그는 그녀에게 커피를 마시자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5461, 26071, 13256, '김현준(이병헌)과 김선화(김소연)는 17회에서 테러범들이 이곳에 설치한 폭탄을 찾기 위해 광화문 광장으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5462, 26273, 13256, '3화에서 기억을 되찾기 위해 김현준(이병헌)이 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5463, 26682, 13256, '14회에서 김현준(이병헌)이 이 거리를 운전하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5464, 26743, 13256, '마지막 20회에서 김현준(이병헌)과의 만남의 장소에 최승희(김태희)는 이 등대에 온다. 그녀가 이곳에서 그들의 낭만적인 순간을 회상하는 동안, 그는 그녀를 만나러 가던 길에 도착하기 직전에 그의 차에서 총에 맞았습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5465, 25736, 13256, '최종회 20회에서 김현준(이병헌)이 이 장소 로비에서 프러포즈를 했을 때의 사랑스러운 시간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5466, 25678, 14149, '9화에서 주홍빈(이동욱)이 회전목마에서 손세동(신세경)을 위해 깜짝 이벤트를 준비했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5467, 28092, 14149, '주홍빈(이동욱)의 저택으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5468, 29247, 16018, '2회에서 이곳은 조세황(김강우)의 저택으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5469, 26652, 16018, '1회에서 화원 사건의 성공을 축하하는 유력 변호사들이 이 클럽에서 건배한다. 그러나 조세황(김강우)의 합류로 그들의 즐거운 저녁 시간이 바뀌었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5470, 26032, 16018, '강곤(주지훈)은 1회에서 딸과 함께 이 마을에 살고 있다. 1회에서는 딸과 함께 동파루에 소원을 담아 등불을 실어 보내기도 한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5471, 26539, 16018, '동산도서관과 본관, 캠퍼스 입구는 신화고등학교의 일부로 등장한다. 신화고등학교의 다른 부분들은 대명캠퍼스와 체인지업캠퍼스-양평캠프(옛 경기영어마을의 일부)에서 촬영된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5472, 31713, 4638, '원빈이 김희원의 협박으로 마약을 전달하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5473, 31714, 4638, '김희원이 원빈에게 전화를 받은 부두');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5474, 27413, 4638, '원빈이 전당포로 출퇴근 했던 골목');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5475, 31715, 4638, '원빈이 남자아이를 추격하는 골목');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5476, 28499, 4638, '원빈이 김새론에게 나오라고 부른 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5477, 26990, 13286, '이신영이 프러포즈를 받고 남자친구에게 하차하는 1회에서 리포터로 활동하는 방송사 \'UBN\'의 본사로 보인다. 이후 신영은 사내 카페에서 남자친구의 연하 애인을 만나 자신이 받은 다이아몬드 반지를 돌려달라는 요청을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5478, 26002, 13412, '이정우(권상우)와 윤혜인(수애)은 아테나의 여신 1화에서 우연히 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5479, 31719, 14124, '3화에서 강진구(김영광), 마세영(경수진) 일할 곳을 찾을 때 바닷가 레스토랑에서 식사를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5480, 31720, 14124, '3회에서 출장 중 마세영(경수진)과 강진구(김영광)는 이 카페에서 잠시 휴식을 취한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5481, 27435, 14124, '3화에서 마세영(경수진)과 강진구(김영광)는 여행사를 위해 \'데이트 코스\'를 짜기 위해 이 거리들을 걷는다. 이 지역은 그들에게 데이트를 위한 \'비밀 핫스팟\'으로 추천된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5482, 31721, 14124, '3회에서도 마세영(경수진 분)과 강진구(김영광 분)가 여행으로 이곳에 온다. 사실 그들은 여행사에 여행 제의를 받으러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5483, 25976, 14124, '4회에서 그들이 일하는 여행사에 여행 제안을 할 때 진구의 키스로 세영과 끝난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5484, 24904, 14124, '여기는 주다인(유다인)의 카페이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5485, 26253, 16322, '1화에서 모태강(박성웅)은 어느 날 갑자기 사고를 당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5486, 30593, 16322, '1회에서 하립(정경호)은 송회장과 만나기 위해 이 사옥에 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5487, 25908, 16322, '8회에서도 건의 사임 기자간담회에서 화해했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5488, 31722, 16322, '지서영(이엘)이 대표이자 하립(정경호)이 대표하는 \'소울 엔터테인먼트\' 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5489, 26540, 16322, '1회에서 지서영(이엘)이 하립(정경호)을 라이브로 끌고 가는 \'tvS\' 방송사 사옥의 내외부 모습이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5490, 25661, 16322, '1화에서 하리브(정경호)와 지서영(이엘)은 버스커들로 가득한 이 거리를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5491, 26499, 16322, '악마가 너의 이름을 부를 때 1화에서 갑옷을 입은 하리브(정경호)가 이 다리를 뛰어넘는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5492, 25335, 16322, '중상을 입은 하립(정경호)을 태운 구급차가 2회 때 이 다리를 건넌다. 3회에서는 김이경(이설)이 하립을 몰고 다리를 건너는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5493, 25797, 16322, '1화 회상장면에서 하립(정경호)이 등장하는 장면에서 이 다리를 잠깐 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5494, 25842, 16322, '3화에서 아파트에서 쫓겨난 김이경(이설)은 소지품을 들고 이 지하철역으로 온다. 그녀는 한국에 막 도착했지만 소매치기를 당한 자신과 같은 짐을 든 여행자(송강)와 마주친다. 그녀는 결국 그가 지하철 요금을 내는 것을 돕게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5495, 24915, 16322, '2회에서 이란주(EXID 하니)가 호텔 로비에서 형준영(백성철)과 소개팅을 한다. 7화에서 란주는 차를 호텔 입구에 두고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5496, 26030, 17892, '1회에서 김가온(GOT7 진영)은 강요한(지성)의 의도를 파악하기 위해 그를 따라 자전거를 타고 이 식당까지 추적합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5497, 26449, 17892, '12회에서 강요한(지성)의 명령으로 술에 취한 김가온(박진영/구 갓세븐 진영)을 쫓은 K(이기택)는 그를 이 고가 아래로 몰고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5498, 25816, 17892, '16회 최종회에서 가온에게 경례를 한 뒤 요한은 미지의 미래로 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5499, 28405, 17892, '8회에서 강요한(지성)이 김가온(박진영/GOT7)을 자신의 팀에 소개하는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5500, 31723, 17892, '12~13회에서 정선아(김민정)는 지성(강요한)을 이 복합문화공간으로 오라 명령하고 절친의 목숨을 위협한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5501, 28426, 17892, '드라마 등장인물인 차경희 법무부 장관(장영남)이 출근하는 곳으로 드라마에서 법무부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5502, 26459, 17892, '12화에서 가온(박진영, 전 갓세븐)과 사이가 나빠진 리얼리티 TV 심사위원 강요한(지성)이 밤에 이 공원을 뛰어다닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5503, 25908, 17892, '이곳은 1화 라이브 코트 쇼의 첫 방송 동안, 이 쇼의 재정 후원자들과 사람미디어의 박두만 회장 (이서환)은 샴페인을 마시며 이 쇼가 시청률 히트를 쳤다는 것을 축하하는데, 이때 박두만회장의 사무실로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5504, 24900, 17892, '김가온(진영)의 행보, 카페에서 윤수현(박규영) 교수 민정호(안내상)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5505, 25661, 17892, '4회에서 김가온(GOT7 출신 박진영)은 이 거리에서 강요한(지상)의 2차 공개심판에 놀란 반응을 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5506, 25876, 17892, '3회에서 이영민(문동혁)이 여자친구(배은우)와 함께 이곳을 찾는다. 왜 자기를 이 더러운 곳으로 데려왔느냐고 묻자 그녀는 “TV에 골목 맛집이라고 방송에 나왔던 곳이다. 핫플레이스다."라고 말했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5507, 25734, 17892, '2화에서 두 사람의 자동차 경주가 진행되는 동안, 강요한(지상)은 이영민(문동혁)을 바짝 따라 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5508, 26232, 17892, '11회에서 대통령과 불쾌한 만남을 가진 후, 차경희(장영남)를 집으로 데려다주는 사람은 그녀의 평소 운전자가 아니라 K(이기택)이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5509, 31724, 17892, '8회에서 혼란에 빠진 김가온(박진영, 전 GOT7)은 이 주차장 서쪽 끝에 있는 포장마차에서 전 교수 민정호(안내상)를 만나 조언을 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5510, 31725, 17892, '이 곳은 김가온(구 GOT7 출신 박진영)의 집으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5511, 26071, 17892, '1화에서 \'광화문 폭동\'은 이 광장에서 일어났다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5512, 26214, 17892, '8회에서 그녀를 그리워하는 김가온(박진영/갓세븐 진영)은 늦은 밤 윤수현(박규영)에게 전화를 걸고 그들은 이 길을 걸어 술집으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5513, 24922, 17892, '8화에서 김가온(GOT7 박진영/진영)은 윤수현(박규영)이 보고 싶어서 늦은 밤 전화를 걸고 만나서 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5514, 25856, 17892, '1회에서 사회책임재단(SRF)이 라이브 법정 쇼 ‘국민 참여 재판\' 를 발표하는 행사가 진행되는데, 이 자리에서 메인 심사위원인 강요한(지성 분)이 특별 게스트로 참석하고, 고위 정치인과 CEO들이 대거 참석한 가운데 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5515, 25913, 17892, '이 건물 로비는 7화에서 강요한(지성 분)이 민용식(홍서준 분)에게 예고 없이 찾아오는 민보그룹의 하나로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5516, 31726, 17892, '이 건물의 외관은 제2화에서 사람미디어그룹과 사회책임재단이 공동 주최하는 자선 패션쇼가 열리는 장소로 등장한다. 이 행사의 명예 게스트로는 강요한(지성)과 리얼리티 텔레비전 쇼 \'라이브 코트 쇼\'의 동료 심사위원 오진주(김재경)가 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5517, 26681, 17892, '2회에서 자동차 경주를 하던 중 강요한(지상)은 이영민(문동혁)을 따라 이 다리를 바짝 바짝 따라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5518, 25864, 17892, '7회에서 사회책임재단(SRF)의 모든 남성 회원들이 이 식당에서 만나 현 재단의 비서 정선아(김민정)를 상대로 음모를 꾸민다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5519, 26524, 17892, '7회에서 가온(진영)이 이 교도소로 찾아와 사기꾼 도영춘과 맞붙게 된다. 다른 사람이 형을 집행하고 있어 가온은 이 교도소 법정에서 무너진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5520, 27000, 17892, '7화 꿈에서 김가온(박진영)은 사기꾼의 삶을 끝내기 위해 결심한 사기꾼 도영춘(부모의 죽음에 책임이 있는 사기꾼)의 심판에 참석하기 위해 다시 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5521, 24962, 17892, '7회에서 허중세(백현진)가 사는 곳, 강요한(지성)을 맞이하거나 11회에서 자신의 계획을 생각하는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5522, 26030, 17077, '14회에서 백만우(손종학)가 이 식당에서 상원의원의 도움으로 강수서장 오영준(홍서준)을 만난다. 그들은 도현수(이준기)와 백박사가 어둠 속에서 가만히 앉아 아무것도 하지 못하는 차지원(문채원)을 몹시 걱정하는 이야기를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5523, 31728, 17077, '백희성(이준기)은 딸 은하(정서연)에게 이 카페에서 에그타르트를 먹게 한다. 2회에서 유치원에서 데리러 온 후 일찍이 난투극을 벌이던 아버지가 급우의 편을 드는 모습에 당황한 은하는 그녀가 가장 좋아하는 디저트를 먹기를 거부한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5524, 28426, 17077, '이 대학 본부 건물은 최종회 16회에서 도현수(이준기)와 함께 도해수(장희진)가 재판을 받는 법원 건물 외관으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5525, 27355, 17077, '10회 도현수(이준기) 강수경찰서 중대3팀에 일그러진 목소리로 전화해서 이 카페에서 연주시 연쇄살인사건에 대한 정보를 준다고 함');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5526, 31729, 17077, '7화에서 공미자(남기애)는 찢어진 워크북을 보고 하소연한 후 백은하(정서연)를 행복하게 해 주기 위해 백은하(정서연)를 이 카페로 데려간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5527, 28513, 17077, '16회에서 도해수(장희진)는 도현수(이준기), 김무진(서현우)에게 이 식당에서 저녁식사를 하며 유학 계획을 밝혔다. 무진은 해수에게 그녀의 지지가 있다고 말하고 서둘러 자리를 뜬다. 해수는 오빠에게 밖에서 무진을 따라오라고 지시한다. 현수는 무진의 우는 모습을 보고 결국 친구를 위로하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5528, 31730, 17077, '2회에서는 살인 피해자의 아들 안종구(허재호)가 사회복지사 서영(이주연)에게 다가가 압력을 가하는 모습이 그려졌다. 이 아파트 단지의 5번동 앞에서 어머니의 장부가 어디에 있는지 묻는다. 그는 그녀를 맹렬히 붙잡았지만 경찰 형사 재섭(최영존)은 제때 그를 막을 수 있었다. 재섭은 나중에 벤치에서 그녀에게 말을 걸고 피해자 아들의 도박 습관에 대해 듣게 된다. 또한 서영은 살인 피해자인 정문옥(박혜진)이 전날 밤 자신에게 전화를 걸어 유언을 묻는 이야기를 전했다. 이후 경찰서에서 재섭은 서영이 이야기를 나누던 중 아파트 옥상을 올려다 본 모습을 회상한다. 그 힌트와 함께 그는 옥상으로 달려가 액세스 패널을 열고 살인의 물리적 증거를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5529, 31731, 17077, '16회에 이 카페에서 도현수(이준기)와 차지원(문채원)은 도해수(장희진)의 증언을 위해 양진태(이현균)를 설득하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5530, 31732, 17077, '최종회 16회에서는 도현수(이준기)와 차지원(문채원)이 아파트 3동에 모인 모습이 그려졌다. 촌장의 조카인 양진태에게 도해수(장희진)를 위해 법정에서 증언해달라고 부탁하기 위해. 지원은 진태로부터 증언을 하지 않겠다는 문자를 받았지만 집에 올 때까지 기다리기로 한다. 진태는 도착했지만 현수를 보고 도망친다. 현수와 지원은 둘 다 그를 쫓고 엘리베이터에서 그를 궁지에 몰아넣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5531, 25335, 17077, '백희성(이준기)이 아내 차지원(문채원)과 딸 은하(정서연)은 1회에서 부모님과 함께 희성의 생일을 축하하기 위해 이 다리를 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5532, 31733, 17077, '2회에서 자신이 운영하는 중식당 남순길(이규복)이 김무진(서현우)의 연주연쇄살인 기사를 읽는 모습이 그려졌다. 그가 무진에게 음성사서함을 남기고 있을 때 검은색 후드티를 입은 인물이 식당으로 걸어들어가 칼을 뽑아 그를 찔러 죽인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5533, 27596, 17077, '4회 회상 장면에서는 백희성/도현수(이준기)와 차지원(문채원)이 빗속을 질주하는 모습이 그려졌다. 첫 데이트를 하고 이 골목을 따라 한옥으로 피신한다. 그녀를 지켜보던 그는 그녀의 미소에 반해 무의식적으로 미소를 짓지만, 멀리서 그를 지켜보고 있는 아버지 도민석(최병모)의 유령을 보고 기분이 달라진다. 마지막 16화에서는 혼수상태에서 깨어나 대부분의 기억을 잃은 현수가 여기로 돌아와 지원과의 기억을 되살리려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5534, 26404, 17077, '4회 회상 장면에서는 백희성/도현수(이준기)가 차지원과 대피하는 모습이 그려졌다. 비오는 날 데이트를 하던 중(문채원)은 아버지 도민석(최병모)의 유령이 개 목줄을 손에 쥔 채 게스트하우스 앞에 서 있는 것을 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5535, 25937, 17077, '드라마에서 차지원(문채원)의 어머니가 운영하는 미니마트 \'은하슈퍼\'로 등장한다.2회에서 볼 수 있듯이 지원은 2006년 여름 가게를 운영하던 중 백희성(이준기)을 처음 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5536, 31734, 17077, '이곳은 백희성(이준기)의 금속공예 공방 \'샛별이는 공방\'으로 등장하며 아내 차지원(문채원)과 딸 은하(정서연)와 함께 머물게 된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5537, 25955, 17077, '이 저택의 외관은 백희성(이준기/김지훈)의 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5538, 25913, 17077, '백희성(이준기)의 생일을 축하하는 어색한 저녁식사 자리로 1회에서 아내 차지원(문채원), 딸 은하(정서연), 부모님과 함께한 방이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5539, 31735, 17077, '12회에서는 도현수(이준기)와 형사 최영주(최영주)가 현수의 예감에 대해 비밀리에 이 식당으로 향하는 모습이 그려졌다. 그를 죽이기 위해 염상철(김기무)과 협정을 맺었다. 현수는 상철에게 그를 죽일 수 있는 절호의 기회를 주려는 계획에 따라 경찰이 그를 붙잡고 모든 배후의 범인을 알아낼 수 있도록 최형사에게 도움을 청하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5540, 31736, 17077, '현수(이준기)는 옛집에서 백희성(김지훈)과 대면하고, 자신이 아내 차지원(문채원)을 죽였다고 생각하고, 15회에서 그를 죽이려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5541, 31737, 18526, '11회에서 한밤중에 우호성(나철)이 무거운 가방을 끌고 이 폭포의 절벽에서 밀어내는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5542, 25787, 18526, '7회에서 남기태(김중희)는 살인을 저지르고 새벽 시간에 이 역 7번 출구계단을 오른다. 9화에서 송하영(김남길)은 서남 살인범의 발자취를 따라 밤길을 걸어 이 역 3번 출구로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5543, 31738, 18526, '6화에서 공중 촬영의 일부로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5544, 26319, 18526, '10회에서 연쇄 살인범에 대한 패널 토론이 이 화면에서 생중계됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5545, 25955, 18526, '이 곳은 5회에서 경제학 교수와 그의 아내가 살해당하는 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5546, 26273, 18526, '7회에서 송하영(김남길)은 새벽 미사를 위해 어머니를 모시고 이 교회로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5547, 31739, 18526, '8회에서 교도소에서 구용춘(한준우)을 심문한 송하영(김남길)과 국영수(진선규)는 술을 마시며 긴장을 푼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5548, 25809, 18526, '2회에서 송하영(김남길)은 이 감옥에 있는 용의자 양용철(고건)을 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5549, 31740, 18526, '1화에서 어린 송하영이 어머니와 함께 오리배를 타고 오다가 사고로 물에 빠진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5550, 25605, 18808, '5회에서 나일등(황인엽)은 어른이 되어 성공했지만 지루한 삶을 살고 있다고 상상한다. 스포츠카를 몰고 터널을 지나 노란 유채꽃밭으로 들어간 그는 이 꽃밭에서 리을(지창욱)이 윤아이(최성은)에게 마술을 가르치는 것을 멀리서 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5551, 24957, 18808, '이곳은 세원의 고등학교로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5552, 25340, 18808, '5회에서 나일등(황인엽)은 윤아이(최성은)를 따라 학교 입구 비탈을 내려간다. 그리고 그녀는 마술사가 살인자가 아니라는 자신의 믿음을 표현합니다. 차를 타고 학교 밖에 도착한 일등의 어머니는 그의 관심을 끌기 위해 경적을 울린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5553, 29247, 14879, '이곳에서 차영빈(서강준)과 안소희(안소희)가 밤에 수영장에서 키스를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5554, 31747, 14879, '1회 차영빈(서강준)이 절친들과 함께 간 곳이고 이호진(박정민)과 결별한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5555, 26173, 14879, '김은갑(조진웅 분)은 10회에서 극중 차영빈(서강준 분)의 역할을 결정하기 위해 가까이에서 미팅을 가졌고, 제이정(앰버)도 이 광장에서 합류해 앞으로의 계획을 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5556, 25607, 17821, '9회에서 오빛나(양혜지)와 남규현(김민귀)은 이 공원을 산책하지만 결국 화해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5557, 25951, 17821, '9회에서 양도혁(채종협)이 이 카페에서 기획사와 전속계약을 맺습니다. 동시에 도혁은 박재언(송강)과 윤설아(이열음)가 2층으로 걸어가는 것을 발견합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5558, 25830, 17821, '8회에서 박재언(송강)과 유나비(한소희)는 미술관을 나와 이곳에 왔다. 그는 박물관에서 그녀를 보고 첫눈에 반했다고 그녀에게 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5559, 31748, 17821, '2화에서 박재언(송강)은 이 건물 뒤편에 있는 진흙 구덩이에서 조소과의 처리를 위해 진흙을 통에 넣어 대학원생 안경준(정재광)을 돕습니다. 하지만 대학원생인 안경준(정재광)은 사무실에서 월급을 받고 아르바이트를 제안받고 나비(한소희)도 그곳에서 일하고 있을지 모른다고 말한다. 8회에서 도혁(채정협)은 아트홀 앞 등나무 아래에서 수다떨며 먹던 소풍 도시락으로 나비를 놀라게 한다. 그런 다음 그녀는 그를 캠퍼스의 일부로 안내합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5560, 26036, 17821, '3회에서 유나비(한소희)는 전 남자친구와 이 곳에서 데이트를 했던 것을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5561, 27507, 17821, '5회에서 오빛나(양혜지)는 이 길을 걷다가아이스크림 가게에서 다른 여자와 소개팅을 하고 있는 남규현(김민귀)을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5562, 25926, 17821, '5화에서 서지완(윤서아)과 윤솔(이호정)은 박재언(송강)과 유나비(한소희)가 있던 경찰서를 나와 이곳에서 시간을 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5563, 24939, 17821, '서지완(윤서아 분)은 8화에서 윤솔(이호정 분)이 집에 도착하기를 기다리며, MT 당일 밤에 있었던 일들을 모두 기억했다고 인정한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5564, 26293, 17821, '서지완(윤서아)과 윤솔(이호정)이 고등학교 친구들을 이 카페에서 만난다. 여기 있는 동안, 그들 중 한 명이 그녀가 결혼한다는 소식을 전하는데, 그녀는 그녀의 관계에 전환점이 왔다고 느끼기 때문이다. 이는 지완과 솔이 그들의 관계에 대해 이야기하고 그들의 문제를 극복하는 것으로 이어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5565, 25662, 17821, '1화에서 이 카페 입구에서 나비는 그녀에게서 영감을 받은 듯한 그의 위험한 작품에 대해 이곳에서 친구들과 상의합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5566, 29324, 17821, '유세훈(김무준)은 이 카페에서 짝사랑하는 윤솔(이호정)을 만날 예정이다. 그는 데이트라고 생각하지만, 그녀는 그가 단지 조각과 학교에 대해 이야기하고 싶어한다고 생각한다. 그녀의 가장 친한 친구인 서지완(윤서아)은 그녀가 항상 그곳에 가고 싶어했고, 그래서 소녀들이 세훈의 감정을 의식하지 못하기 때문에 어색한 세 명의 데이트가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5567, 26018, 17821, '4회에서 유나비(한수희)는 버스에서 소꿉친구 양도혁(채종협)과 마주친다. 비가 많이 내렸고, 도혁은 그녀와 우산을 나눠 쓰고 버스 정류장에서 집까지 걸어간다. 그들은 이 카페 밖에 서서 며칠 후에 제대로 만나기로 약속하고, 도혁은 빗속을 뚫고 버스 정류장으로 다시 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5568, 29471, 17821, '4회에서 유나비(한소희)는 이 카페의 야외 테라스에서 박재언(송강)과 진지한 대화를 나누는 모습을 상상한다. 그녀는 그와 데이트하는 것에 대해 그녀의 입장을 알렸습니다. 5회에서 재언은 이곳에서 정체불명의 여성을 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5569, 31749, 17821, '알고있지만 2회에서 유나비(한소희)가 이 클럽에서 박재언(송강)과 합류했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5570, 26399, 17821, '최종회 10회에서 유나비(한소희)를 이 카페에서 기다리는 동안, 양도혁(채종협)은 자신의 영상 채널 팬들에게 사인을 요청받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5571, 26139, 17821, '남규현(김민귀)이 살고 있는 옥탑방이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5572, 24907, 17821, '3회에서 영화가 끝난 뒤 유나비(한소희)와 김은한(이정하)이 나무길을 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5573, 31750, 17821, '서지완(윤서아 분)은 3회에서 이 장소에서 소개팅과 함께 영화를 보고, 절친 윤솔(이호정 분)도 즐겼을 것이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5574, 27056, 17821, '6회에서 서지완(윤서아)과 윤솔(이호정)이 이 서점 계단에 앉아 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5575, 24922, 17821, '9화에서 유나비(한소희)와 양도혁(채종협)은 이 길에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5576, 26434, 17821, '최종회 10회에서 유나비(한소희)는 카페에서 양도혁(채종협)을 발견했지만 박재언(송강)과 이 건물을 지나며 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5577, 31751, 17821, '이 곳은 유나비(한소희), 박재언(송강) 등 다른 미대생들이 다니는 \'홍서대학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5578, 25017, 17821, '3회에서 박재언(송강)은 윤설아(이열음)가 도착하기를 이곳에서 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5579, 31752, 17821, '조소과 MT 장소 촬영지였던 나주 한옥마을 복합문화공간');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5580, 31753, 17821, '유나비와 양도혁이 캠핑을 했던 해수욕장 캠핑장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5581, 31754, 17821, '유나비 이모의 공방으로 나왔던 여수 바다가 보이는 도예 공방');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5582, 31755, 17821, '6, 7회에서는 유나비(한소희)가 양도혁(채종협)의 선배 준비를 도와주는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5583, 31756, 17821, '유나비와 양도혁이 자전거를 타고 돌아다닌 여수 데이트 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5584, 26086, 16091, '대학답게 주인공들이 다닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5585, 27508, 16091, '에피소드 후반부에 그들은 공부하러 이곳에 오고, 수진은 하은이 친구들에게 사준 모든 물품과 대가로 부탁한 호의가 적힌 \'회계\' 책을 발견한다. 홍준기(김민성)는 하은이 단체 채팅방에서 나간 후 찾아와 그녀를 응원하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5586, 31764, 15924, '극중 유진우(현빈)가 이 호텔에 거주한다. 15화에서 진우가 치료받는 곳이고 같은 에피소드에서 차병준 교수(김의성)가 살해당하는 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5587, 31765, 15924, '9, 10회에서 엠마의 기타 워크샵을 여는 정희주(박신혜) 할머니 오영심(김용림)의 한국 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5588, 25827, 15924, '유진우(현빈)가 방문하는 스페인 그라나다의 동네골동품점으로 등장한다. 6회에서 밖에서 기다린다. AR게임 넥스트의무기 가게이기 때문에 가게에 침입하고 여기 있는 것이 진우를 가능하게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5589, 31766, 15924, '이 병원은 스페인에 위치한 병원으로 정기적으로 등장한다. 에피소드 5-6에서 유진우(현빈)는 여기에서 치료를 받고 발작을 일으키고, 발작을 일으키지 않고 스스로 릴리스하고 AR 게임 Next를 계속한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5590, 26037, 15924, '이 건물은 J 홀딩으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5591, 31767, 15924, '13~14회에서 정희주(박신혜)와 다친 유진우(현빈)의 짧은 만남이 나온다. 그리고 그녀는 그가 떠나야 할 때까지 \'천국의 열쇠\'에 대해 그에게 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5592, 26575, 15924, '14회에서 고유라(한보름)는 매니저 노영준(박진우)과 충돌하는 주거지 중 한 곳 이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5593, 25882, 15924, '16회에서 AR 게임 넥스트 길드전 이곳에서 펼쳐진다. 지나가던 사람들은 섀도우복싱을 하고 있는 한 무리의 사람들만 보이는 것 같아서 신기하게 그 과정을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5594, 26242, 15924, '12회에서 정희주(박신혜)와 유진우(현빈)가 키스하는 곳은 이랜드크루즈 선착장이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5595, 27336, 15924, '7화의 한국 AR Game Next 시험운행이 이 동상 바로 앞에서 진행된다. 한 쌍의 선수들이 각기 다른 전사들(박선호(이승준 분)과 그의 팀이 멀리서 감시하는)과 싸우는 동안 유진우(현빈 분)는 선수 지누로 모든 상대를 차를 몰고 지나쳐 총을 쏜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5596, 31768, 15924, '마지막 16회 엔딩 장면에서 정희주(박신혜)는 유진우(현빈)가 여기 있을 수 있습니다. 이 게이트 앞 횡단보도를 건너면 AR 게임 넥스트에 진입하고, 진우는 이 게이트 건너편에 있는 공원에서 넥스트에서 적들을 쏘고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5597, 27066, 15924, '3회에서 AR게임 속 이 공원의 그린하우스 앞에서 유진우(현빈)는 차형석(박훈)과 맞서싸우며 그를 이기는데 그의 실제 죽음은 다음날 아침에 발표된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5598, 25017, 15924, '13화에서 박선호(이승준)가 서울로 돌아와 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5599, 26125, 15924, '3, 4화에서 이 호텔의 인테리어는 차형석(박훈)과 아내 이수진(이시원)이 머물고 있는 스페인 그레나다의 호텔로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5600, 31769, 15924, '16회에서 AR 게임 넥스트(Next)가 이 교회에서 펼쳐진다. 두어 시간 후 이곳에 도착한 박선호(이승준)는 교회 곳곳에 모래더미의 형태로 파괴된 흔적만을 발견하고, 선에 합류한 정희주(박신혜)도 발견한다. 나중에 희주는 이 교회에 다시 와서 자신이 진우의 (가능한) 죽음의 일부일 수 있음을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5601, 31770, 6079, '억새가 아름다운 암살 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5602, 25345, 6079, '암살의 다양한 장면을 촬영한 영상 테마파크');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5603, 31771, 6079, '암살을 촬영한 광주의 이국적 기념관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5604, 31772, 6079, '암살 감독이 단골인 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5605, 24948, 6079, '암살을 촬영한 서울의 대표 야경지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5606, 31773, 6079, '영화 암살속 강인국과 미츠코의 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5607, 25639, 6079, '암살부터 도깨비를 촬영한 고궁');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5608, 25931, 6079, '기차역에서 서울의 대표 전시공간으로 거듭난 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5609, 31774, 14170, '1회에서 백야/백순동(박하나)은 백영준(심형탁)과 저녁 술 한잔하러 이 출구 앞에서 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5610, 28532, 14170, '18화에서 김도란(김유진/유이)이 그녀를 방문하는 장면에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5611, 24932, 14006, '12회에서 차정우(주상욱)는 나애라(김민정)의 투구 성공을 축하하기 위해 마감 시간까지 이곳에서 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5612, 26039, 14006, '국승현(서강준)과 동행하는 나애라(이민정)는 12화에서 호텔 로비(\'통일신라호텔\'로 등장)에서 이 호텔의 대표는 그녀에게 일자리를 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5613, 25802, 14006, '12~13회에서 나애라(이민정)가 \'통일신라호텔\'로 향하는 길을 걷고 있다. 갑자기 차정우(주상욱)가 나타나서 그녀가 회사를 옮기는 것을 막으려는데 훗날 \'벚꽃 키스\'로 불리기도 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5614, 27241, 14006, '6화에서 나애라(이민정)와 차정우(주상욱)가 백조의 외륜선을 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5615, 31775, 15390, '이 다리(연꽃호수교)는 2007년 강신우(이정신)가 한지수(이열음)를 만나기로 했던 \'반딧불이 다리\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5616, 25718, 15390, '10화에서 강신우(이정신)와 한지수(이율음)는 데이트를 위해 이 공원에서 웨스턴 돔 쇼핑몰로 향하는 쇼핑 거리로 모험을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5617, 27482, 15390, '6회에서 10시간 야근 후 지친 어른 한지수(이열음)가 일을 마치고 커피를 마시러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5618, 26672, 15390, '1회에서 강신우(이정신)가 베지박스 밖에서 한지수(이열음)와 커피를 마신 후 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5619, 31716, 15390, '1회에서 10년 만에 다시 만난 후, 강신우(씨엔블루 이정신)와 지수(이열음)가 채팅을 하러 왔다. 보고 싶다고 말하자 그녀는 시간이 얼마 없다며 만남을 줄인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5620, 24918, 15390, '강신우(씨엔블루 이정신)는 7회에서 고등학교 친구들과 함께 \'가람펍\'에 온다. 한지수(이열음)에게 모임을 알렸지만 답이 없었다. 그녀가 나타나 그는 기쁨을 나타냈다. 그러나 어린 자신(서지훈)이 그녀와 함께 나타나면서 공포가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5621, 25662, 15390, '강신우(씨엔블루 이정신)는 1회에서 준아를 만나러 왔다. 친구 김민석(김민석)은 카페에서 소개팅이 잡혔다는 말만 듣게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5622, 25640, 14449, '15화에서 도해강(김현주)과 최진언(지진희)이 여기에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5623, 25607, 14449, '이 공원의 메타세콰이어 골목길은 도해강(김현주)과 최지에게 특별한 장소다. 그들의 사랑이 거듭 확인되고 재확인된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5624, 31776, 14449, '해강(김현주)이 드라마에서 살던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5625, 26178, 14449, '16회에서 저녁에 술을 마시러 온 도해강(김현주)과 백석(이규한)이 술게임을 하고 진 백석은 옆에 앉은 여자에게 사랑한다고 말하고 엉덩이로 이름을 적어야 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5626, 25797, 14449, '1회에서 김현주가 쌍둥이 엄마 김규남(김청)을 이 다리에 두고 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5627, 25017, 13490, '3화에서 강재미(이보영)는 엄마 오정희(배정옥), 이모 오정심(윤현석)과 함께 괌 여행을 마치고 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5628, 31721, 15034, '이유미(송지은 분)는 대명리조트 솔비치 삼척에서 열리는 어머니의 결혼식에서 숨을 고르기 위해 1회에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5629, 31777, 15034, '1회에서 진욱은 아버지가 호텔 하나를 물려받아 일하라고 명령하고, 유미는 어머니의 두번째 결혼에 참석하기 위해 와서 차진욱(성훈)과 이유미(송지은)가 함께 머무르게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5630, 31778, 15034, '1회에서 이 버스 정류장에 있었다. 차진욱이 차를 몰고 갔을 때 그녀는 삼각 김밥을 먹으려 하고 있었다. 그는 멈춰 서서 그녀를 태워 주었다. 두 사람은 다시 말다툼을 했지만 유미는 버스가 멈춘다는 소식을 듣고 진욱의 차에 탔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5631, 26122, 15034, '여기는 차진욱(성)이 맡은 대복그룹 사옥으로 나온다. 2화에서 가족이 소유하고 이유미(송지은)가 일하는 장소로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5632, 25722, 15034, '5화에서 좌절한 이유미(송지은)는 술을 마시러 이곳에 왔고, 그곳에서 그녀의 정현태(김재영)를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5633, 26078, 17153, '1회에서 박진겸(주원)은 어머니가 돌아가신 날 이 쇼핑몰로 달려가 윤태이(김희선)를 발견한다. 멀리서 자신의 어머니라고 생각하고 그녀에게로 향한다. 트럭이 그에게 충돌하기 직전에 시간이 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5634, 25908, 17153, '1회에서 홍은수 엄마(오연아)는 한국으로 돌아가는 이곳 비행기 안에서 눈물을 글썽이며 자리를 바라보고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5635, 24900, 17153, '6화에서 시간 여행자가 도서관 책장에서 한 손님(과거의 자신으로 보이는)을 두드린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5636, 25345, 17153, '유명한 내과의사 장동식(장현성)은 1화에서 시간여행자(박인수)에게 자신의 집에서 살해당하는데 이걸 어린 딸이 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5637, 27515, 17153, '10화에서 김도연(이다인)과 그녀의 부모는 식사를 위해 여기에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5638, 26638, 17153, '이 대학은 드라마 속 \'한국대학교\'로 보인다. 윤태이(김희선)는 2010년 학부에서 공부한 뒤 2020년 강사로 활동한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5639, 31779, 17153, '박진겸(주원) )과 윤태이(김희선)는 5,6회에서 이곳에서 만나 술을 마시며 이야기를 나눈다. 태이는 이곳에서 진겸에게 자신의 어린 시절을 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5640, 26247, 17153, '2회에서 이 건물에 와서 주위를 맴도는 신비한 비행체를 발견하고 죽은 양민성(신)을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5641, 26002, 17153, '10화에서 박진겸(주원)과 윤태이(김희선)가 하루 외출을 위해 이 테마파크로 향한다. 진겸의 죽음을 앞두고 두 사람이 앞으로 보게 될 사진을 찍는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5642, 25335, 17153, '박진겸(주원)과 윤태이(김희선)는 이 다리를 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5643, 27227, 17153, '박진겸(주원)과 김동호(이재윤)는 석오원(최원영)이 은신처로 사용했는지 확인하기 위해 이 집에서 조사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5644, 26244, 17153, '이곳은 윤태이(김희선) 부모님이 운영하시는 중식당으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5645, 26188, 17153, '4회에서 박진겸(주원)이 참석한 가운데 (최원영)이 현장에서 \'나비효과\'에 대해 이야기하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5646, 25896, 17153, '10회에서 박진겸(주원)과 경찰은 감독 석오원의 행방에 대한 제보를 받고 이 버려진 테마파크로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5647, 31780, 17153, '1화에서 박진겸(주원)이 어머니 박선영(김희선)과 함께 살았던 어린 시절 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5648, 31781, 16956, '여기가 박진성 셰프(정일우)가 운영하는 심야 비스트로 \'브라더스\'가 있는 거리로 1화에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5649, 26030, 18742, '12회에서 김희우(이준기)와 동료 김석훈(최광일)이 이 식당에서 조태섭(이경영)을 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5650, 28109, 18742, '3회에서 김희우(이준기)는 이 카페에서 김한미(김재경)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5651, 26124, 18742, '3회에서 김희우(이준기)가 법원 경매에 참석하고 경매의 제왕 우용수(이순재)를 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5652, 25951, 18742, '12회에서 김희우(이준기)는 이 카페에서 김희아(김지은)를 만납니다. 그녀는 그에게 자신의 회사 기밀 정보인 "케이스"를 건냈습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5653, 25345, 18742, '1화에서 조태섭(이근영)이 사위 허재근(류진)을 청와대로 방문하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5654, 25835, 18742, '7회에서 김희우(이준기)가 장일현(김형묵)과 이 술집에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5655, 24935, 18742, '15회에서 김희우(이준기)가 이민수(정상훈)와 함께 술을 마신 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5656, 26085, 18742, '1화에서 서울중앙지검으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5657, 28436, 18742, '2회에서 김희우(이준기)가 전시 사무직으로 출근하기 위해 역 8번 출구에서 내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5658, 26247, 18742, '2회에 박진겸(주원)은 이 건물에 와서 주위를 맴도는 신비한 비행체를 발견하고 죽은 양민성(신영규)을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5659, 27121, 18742, '1회에서 이 건물의 외관은 찰청으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5660, 27530, 18742, '5회에서 김희우(이준기)와 김한미(김재경)가 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5661, 26499, 18742, '5회에서 입대 통보를 받은 김희우(이준기)가 밤에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5662, 25910, 18742, '3회에서 김한미(김재경)는 아버지와 결별한 뒤 이곳에서 술을 마시고 급하게 찾아온 김희우(이준기)에게 만취로 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5663, 26651, 18742, '이 건물은 화장품 회사 \'천하 사옥\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5664, 25846, 18742, '2회에서 김희우(이준기)가 다시 학업을 시작하는 한국대학교로 나온다. 같은 에피소드에서 신입생 파티가 열리는 \'다이크홀\'로 학술정보센터[중앙도서관 정보관]가 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5665, 29151, 17757, '15화에서 동경(박보영)이 웹소설 \'영왕자\'(귀공자)를 만난다. 박보영(탁선경)은 이 만화카페에서 차주익(이수혁)의 탑텐미디어컴퍼니와 재계약을 맺었다. 보영은 자신이 상사를 무서워한다고 인정하고 그의 "큰 눈과 창백한 피부" 때문에 그가 뱀파이어인지 묻는다. 동경은 주익이 계약을 하지 않으면 주익이 “피를 빨고 말겠다”고 말한다. 차례로 그녀는 그의 신분을 숨기고 그의 작곡이 그의 음악 경력을 위태롭게 하지 않을 것이라고 약속합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5666, 25948, 17757, '10화에서 멸망(서인국)을 여기로 데려와 그들에게 소개한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5667, 26198, 17757, '4회에서 그녀의 꿈에서 탁동경(박보영)과 멸망(서인국)은 이 도서관에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5668, 25951, 17757, '상단에 디지털 층이 추가된 이 카페의 건물은 차주익(이수혁) 소유이며, 탁동경(박보영)이 일하는 웹소설 출판사와 이현규(강태오)가 운영하는 1층 카페(실제로는 아이스타일)가 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5669, 31782, 17757, '이곳은 1회에서 손여신(정지소)이 치료를 받는 병원 옥상 테라스다. 병원에서 가끔 \'의사\'로 일하는 멸망(서인국)도 4화에서 보듯 옥상을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5670, 26527, 17757, '3화 회상 장면에서 어린 탁동경(이예서)이 부모님, 오빠와 함께 이 놀이동산을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5671, 31783, 17757, '6화에서 학교 동창회가 끝난 후 나지나(신도현)는 막 택시를 잡으려고 한다. 이현규(강태오)가 그녀를 집에 데려다 주겠다고 제안하고 그녀는 거절하고 그는 떠난다. 둘은 이렇게 헤어지고 첫 사랑이 끝난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5672, 31784, 17757, '10화에서 탁동경(박보영)은 밤에 멸망(서인국)의 생일을 축하하기 위해 멸망(서인국)을 이 공원으로 데려온다. 14화에서 두 사람은 이 공원의 다른 부분에 와서 다시 사랑을 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5673, 28489, 17757, '16회에서 나지나(신도현), 차주익(이수혁) 이 옥상 라운지에서 데이트하고 커플의 첫 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5674, 31785, 17757, '14회에서 멸망(서인국)은 탁동경(박보영)을 이 다리로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5675, 24937, 17757, '에피소드 7에서 이 장소에서 팀 만찬을 하고 초대된 작가가 있을 때 짜증이 나자 탁동경(박보영)은 그를 제자리에 앉히고 자리를 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5676, 25661, 17757, '6화에서 멸망(서인국)은 탁동경(박보영)을 이 거리로 끌고 나간다. 그리고 여기에서 그녀의 가능한 한 가지 소원은 그를 사랑하는 것이라고 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5677, 31786, 17757, '6회에서 멸망(서인국)이 탁동경(박보영)의 소개팅을 이 \'아시안 퓨전 펍\' 지점에서 충돌시킨다. 그리고 그 자리에 있던 다른 두 명을 잠들게 한 후 상황에서 그녀를 끌고 나왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5678, 26265, 17757, '멸망(서인국)은 탁동경(박보영)의 초대를 받아 그녀를 인간답게 관찰하고 이해하기로 한 후 5회에서 그는 동료들과 이 식당에서 점심 시간을 포함하여 일할 때 어디서나 그녀를 따라다닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5679, 26399, 17757, '6화에서 차주익과 나지나의 만남이 이루어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5680, 31787, 17757, '9화에서 이 장소의 야외 테라스에서 그가 신으로 어떻게 살았는지 이야기해 준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5681, 30589, 17757, '14회에서 나지나(신도현)가 이 공원으로 언덕을 오른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5682, 27530, 17757, '13회에서 나지나(신도현)와 이현규(강태오)가 이 주점에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5683, 26666, 17757, '13회에서 나지나(신도현)와 이현규(강태오)가 만날 때 그녀는 각 동창회에서 그를 만나기를 바라며 술집/레스토랑에서 열렸던 모임에 대해 생각합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5684, 26211, 17757, '14회에서 나지나(신도현)와 이현규(강태오)는 옛 고등학교였던 이 학교에 \'데이트\'를 위해 온 그들이 이곳에서 사랑에 빠졌던 때를 기억한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5685, 27212, 17757, '8화에서 탁동경(박보영)은 웹소설 작가 박영은/귀공자(남다름)를 이 거리의 남인사마당 야외무대에서 우연히 보게 되고, 보이밴드 하이틴 드림의 일원으로 한동안 헤어나지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5686, 31788, 17757, '6회에서 이 가게 앞에서 만난 멸망(서인국)과 소녀신(정지소). 그리고 탁동경(박보영)에 대한 그의 사랑이 잘 될 것인지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5687, 31789, 17757, '멸망(서인국)은 탁동경(박보영)을 이 카페에 데려온다. 13화에서 그녀에게 소녀신(정지소)을 만났는지 묻는다. 그녀는 \'사람\'이라는 이름으로 저장한 특정 번호로 최근에 전화를 거는 음성을 그에게 알려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5688, 31790, 17757, '탁동경(박보영 분)이 운명을 결정하는곳 바로 앞 횡단보도다. 1-2화에서 도착한 \'운명의 트럭\' 앞에서 멸망과 손을 잡는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5689, 31791, 17757, '6-7화에서 이 거리로 이어지는 계단을 오르면 탁동경(박보영)이 멸망(서인국)은 그녀와 사랑에 빠졌고, 그 전에 키스로 대답했다. 그리고 그는 갑자기 사라집니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5690, 31792, 17757, '탁동경(박보영)과 그녀의 남동생 탁선경(다원)은 이 집 옥상 아파트에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5691, 27222, 17757, '4회에서 탁동경(박보영)이 오빠가 있는 병원으로 택시를 타고 달려갈 때 이 거리를 지난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5692, 27130, 17757, '11회에서 그녀가 제주도로 가출한 지 며칠 후, 동경(박보영)의 가족과 친구는 멸망(서인국)의 연락을 받아 도착한다. 동경은 절친 지나(신도현)와 함께 오솔길을 걸으며 이 랜드마크 전망대에 들러 셀카를 찍는다. 그녀는 자신의 도피 시도에 대해 "도망친 줄 알았는데 도착하고 나니 내가 사랑하는 모든 것이 여기 있다는 걸 깨달았다"고 말했다. 지나는 그녀와 함께 도망가지 않고 항상 곁에서 화이팅을 약속한다. 지나는 현규와 다시 사귄다고 고백하지만, 동경은 직장에서 차주익이 마음에 들지 않아 묻는다. 지나는 사실이 아니라고 주장한다. 한편, 근처 절벽에서 멸망이 그들을 지켜보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5693, 31793, 17757, '11회에서 탁동경(박보영)과 멸망(서인국)은 이 식당에서 점심을 먹는다. 그리고 그는 그녀가 평소처럼 잔소리 하는 것에 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5694, 31794, 17757, '5화에서 멸망(서인국)은 동경(박보영)에게 어리둥절해 하며 그녀를 관찰하고 그녀를 알아내려 한다. 한동안 그녀를 따라다니던 그는 그녀가 슬픔의 무거운 짐을 지고 있다는 것을 깨닫고 그녀의 짐을 가볍게 하고 싶어한다. 그가 그녀의 눈을 가리고, 해안 산책로를 따라 자리잡은 제주도의 동경의 특별한 해변으로 그녀를 이끈다. 그녀는 그녀가 어렸을 때 매일 이곳에 와서 울고 싶었다고 말하고, 그녀의 어린 시절 이야기를 공유한다. 그들은 어색하게 키스를 하고는 아무일도 일어나지 않은 것처럼 집에 돌아온다. 이후 10화와 11화에서 동경이 서울을 떠나면서 가족과 친구들을 그리워하며 이곳에서 그녀를 발견하게 되고, 그녀는 마침내 헤어지고 자신의 소원이 사는 것임을 인정한다. 멸망은자신도 살고 싶고, 마지막까지 동경과 함께 하고 싶다고 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5695, 31795, 17757, '10회와 11회에서 탁동경(박보영)은 고향 제주도로 도망친다. 그녀는 이모 강수자(우희진)가 “언제나 살 집”을 위해 남겨준 이 별장에 머물고 있다. 그녀를 발견한 멸망(서인국)은 자신의 정신 건강을 걱정하며 가족과 가장 친한 친구에게 자신의 위치를 알리고 그녀를 지원하기 위해 도착한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5696, 26124, 6884, '김현수(김수현)의 재판이 열리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5697, 30790, 6884, '3, 4화에서 변호사 박미경(서재희)과 서수진(이설)이 일하는 \'범한 로펌\' 건물로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5698, 31796, 6884, '1회 김현수(김수현)와 홍국화(황세온)는 아이스크림 가판대에서 식사를 한 후 이 공원에서 한강 파라다이스를 지나쳐 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5699, 26136, 6884, '6화에서 신중한(차승원)은 이 건물 옥상에서 윤효종(나은샘)을 만나 홍국화(황세온)와의 상황을 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5700, 26118, 6884, '8회에서 김현수(김수현)가 이 건물 옥상에 서서 야경을 바라보는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5701, 25347, 6884, '1화에서 김현수(김수현)와 홍국화(황세온)는 아이스크림 노점에서 식사를 한 뒤 이 공원에서 한강 파라다이스를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5702, 27077, 6884, '3회에서 신중한(차승원)은 김현수(김수현)를 만나기 위해 마을버스를 타고 이 거리에 내려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5703, 31797, 6884, '2, 3화에서 4층에 신중한 변호사(차승원)의 로펌이 위치한 건물로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5704, 26524, 6884, '2회에서 재판을 받던 도중 교도소로 옮겨진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5705, 25718, 16195, '2회에서 고세연(박보영)과 차민(원오원 안효섭)은 이 공원 벤치에서 삼각김밥을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5706, 25908, 16195, '드라마 주인공인 차민(안효섭)의 엄마인 엄애란(윤유선)의 집으로 나온다. 2화에서 차민(안효섭) 어머니가 이곳에서 있는 장면을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5707, 24934, 16195, '2화에서 차민의 어머니 엄애란(윤유선)의 저택으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5708, 24957, 16195, '1화 회상씬에서 고세연(김사랑)이 갔던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5709, 31803, 16195, '1회에서 고세연(김사랑)이 죽기 전 근무하던 검찰청으로, 차민(원오원 안효섭)이 그녀를 찾아오는 곳으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5710, 26575, 16195, '1화에서 차민(안효섭)은 몸이 바뀌면 떠나야 하는 이곳의 한 아파트에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5711, 25347, 16195, '15회에서 고세연(박보영)과 차민(안효섭)이 데이트를 위해 이 공원 멀티플라자를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5712, 24927, 16195, '2회에서 고세연(박보영)과 차민(원오원)이 1호선 서울역 7번출구로 걸어가고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5713, 29258, 16195, '새롭게 환생한 고세연(김사랑)과 차민(원오원 안효섭)이 밥을 사러 가는 곳. 세연은 점원을 협박해 공짜로 주기 위해 일부러 유통기한이 지난 음식을 고른다. 시리즈 내내 듀오의 정기적인 만남의 장소가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5714, 25931, 16195, '고세연(박보영)과 차민(원오원의 안효섭)이 1호선 서울역 7번출구로 걸어나오는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5715, 31804, 16841, '2회에서 김솔아(신예은)는 길 건너편에 있는 민가로 이사를 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5716, 25659, 16841, '8회(4회)에서 솔아(신예은)가 공원에서 홍조(김명수)를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5717, 25661, 16841, '7회(전체 4회)에서 소개팅을 마친 김솔아(신예은)와 홍조(김명수)는 인간의 모습으로 오후를 함께 즐기기 위해 이 거리로 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5718, 26464, 16841, '김솔아(신예은)가 근무하는 \'날샘 디자인\' 사무실은 이 건물에 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5719, 24907, 16841, '4화(2회)에서 김솔아(신예은)의 사옥을 나와 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5720, 30832, 16841, '4화(전2화)에서 김솔아(신예은)의 사옥을 나와 이 육교를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5721, 25633, 16841, '7화에서 두 사람의 소개팅과 만남은 이 계단에서 이루어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5722, 25617, 16841, '1회에서 이곳은 재선이 홍조(김명수)를 고양이 변신으로 물려준 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5723, 31805, 15117, '어서와 한국은 처음이지 16회에서 찾은 춘천닭갈비');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5724, 31806, 15117, '어서와 한국은 처음이지 인도편에 나온 장어 전문점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5725, 31807, 15117, '어서와 한국은 처음이지 15회에 나온 글램핑장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5726, 31808, 15117, '어서와 한국은 처음이지 6화에 간 남한의 끝');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5727, 31809, 15117, '어서와 한국은 처음이지 독일편 경주 숙소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5728, 28574, 15117, '어서와 한국은 처음이지 7화에 나온 불국사');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5729, 26151, 15117, '어서와 한국은 처음이지 8화에 나온 연못');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5730, 27311, 15117, '어서와 한국은 처음이지 7화에서 간 천마총');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5731, 26639, 15117, '어서와 한국은 처음이지 11회에 나온 아쿠아리움');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5732, 31810, 15117, '어서와 한국은 처음이지 12회에서 간 러시아 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5733, 31811, 15117, '어서와 한국은 처음이지 1화에서 나온 경기장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5734, 27120, 15117, '어서와 한국은 처음이지 6화에서 간 역사탐방');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5735, 31812, 15117, '어서와 한국은 처음이지 4화에 나온 전망대');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5736, 25608, 15117, '어서와 한국은 처음이지 13회에 방문한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5737, 25710, 15117, '어서와 한국은 처음이지 17회에서 다녀간 박물관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5738, 31813, 15117, '어서와 한국은 처음이지 독일편 이태원 숙소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5739, 26243, 15117, '어서와 한국은 처음이지 14회에서 다녀간 한옥마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5740, 25945, 15117, '어서와 한국은 처음이지 14회에서 방문한 궁');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5741, 27862, 15117, '어서와 한국은 처음이지\'에 방영된 익선동 프랑스 가정식 요리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5742, 31814, 15117, '어서와 한국은 처음이지 14회에서 나온 떡볶이집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5743, 30577, 15117, '어서와 한국은 처음이지 2회에 나온 광장시장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5744, 25621, 15117, '어서와 한국은 처음이지 16회에서 간 궁중 요리점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5745, 26166, 16512, '이 위치의 본가는 은단오(김혜윤)의 집으로 등장합니다. 고산정원(글라스하우스)은 지난 10일 오남주(김영대)의 생일파티에 참석한 이도화(정건주)가 여주다(이나은)에게 자신의 마음을 고백하는 곳이다. 5화에서 그에 반해 코티지 가든은 스리고등학교 분식집을 배경으로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5746, 25604, 16512, '4화에서 이 곳은 백경(이재욱)가족의 별장으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5747, 25997, 16512, '22화(전체 11화)에서 기일을 맞아 백경(이재욱)이 이곳을 찾아간다. 은단오(김혜윤)가 잠시 후 합류한다. 잠시 자리에 앉자 그는 그녀에게 자신이 기댈 수 있는 사람은 그녀뿐이라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5748, 25827, 16512, '9화에 나오는 루프탑 카페는 이 단지에 위치해 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5749, 25848, 16512, '이 캠퍼스의 일부가 \'스리고등학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5750, 26539, 16512, '1화에서 은단오(김혜윤)가 등교할 때, 17화(9화)에서 하루(로운)가 기억을 되찾을 때 다시 골목길에서 단오와 재회한다. 7회 수학여행은 이 대학의 한학촌(한학촌)으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5751, 29261, 16512, '6~7회(전체 3~4회)에서 스리고등학교는 이곳에서 수학여행을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5752, 25953, 16512, '9~11회(5~6회 전체)에서 오남주(김영대)의 생일파티는 이곳에서 열린다. 여기서 은단오(김혜윤)가 만화 속 장면과 삶을 잠재적으로 바꾼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5753, 24957, 16512, '캠퍼스의 일부는 1, 2화에서 \'스리 고등학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5754, 25913, 16512, '16화에서는 은단오(김혜윤)와 백경(이재욱)이 가족들과 함께 저녁식사를 하는 모습이 그려졌다. 무대 밖에서 백경은 단오와 이야기를 나누며 단오와 함께한 모든 기억이 그녀에게 아무 의미가 없음을 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5755, 31815, 16512, '22회(전체 11회)에서 하루(로운)은 고려시대의 추억을 기억하고, 그는 백경(이재욱)이 은단오(김혜윤)를 이 공원으로 데려온 방법과 또 다른 시간에 백경의 배우자로 그녀를 어떻게 동행하는지 기억한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5756, 25865, 17701, '16회에서 한정현/이석규(지진희)와 최연수(김현주) 이 곳을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5757, 25649, 17701, '15회에서 하모니파티 후보 펩 랠리가 이 식당에서 벌어지는데, 경찰은 최연수(김현주)가 들어올 때까지 집회를 진압하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5758, 26051, 17701, '11회에서 최연수(김현주)와 고윤주(한고은)는 이 공원에서 만나기로 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5759, 25953, 17701, '1화에서 최연수(김현주)는 이 호텔 앞에서 강청모(이승준)와 마주쳤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5760, 26131, 17701, '10화에서 최연수(김현주)는 이 식당에서 곽문흠 부장검사(송영규)를 만나 조언을 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5761, 26541, 17701, '7회에서 강청모(이승준)가 최연수(김현주)에게 이야기를 청하고 이 공원 동쪽 끝으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5762, 26138, 17701, '4화에서 추동우(최대철)가 공수처에서 직무정지를 당하고 건물을 떠나는 장면과 박원종(주석태)이 이미 그의 작별을 기다리는 장면에서 국정원 본부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5763, 29154, 17701, '4회에서 임형락(허준호)은 송문배 편집장(차순배), 유상동(손종학) 의원과 함께 이 식당에서 저녁식사를 한다. 9회에서는 다시 형락과 상동, 그리고 이번에는 곽문흠(송영규 분) 검사장의 만남의 장소가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5764, 26478, 17701, '13회에서 한정현(지진희)가 반려견을 산책시키던 중 이 공원에서 납치된다. 14화에서 그의 자녀 한승구(유선호)와 한승미(이재인)는 이곳에서 그를 찾고 그들의 개만 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5765, 25335, 17701, '11화에서 각기 다른 당사자들이 모두 이 다리를 건너 \'미월드\'로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5766, 25910, 17701, '5회에서 한정현(연우진), 고윤주(박경리), 황정호(임지규)가 이 가게에서 술을 마십니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5767, 24948, 17701, '1회에서 영 한정현/이석규(연우진)가 오필재(권해효)와 함께 이 공원에서 술을 마시는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5768, 29181, 17701, '10회에서 어린 시절 최연수(한선화)는 회상 속 이 카페에서 절친한 친구 민상아(한보배)에게 어린 한정현/이석규(연우진)를 소개한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5769, 25615, 14835, '1화에서 주인정(강별)은 아버지 주면을 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5770, 25607, 19215, '2회에서 구아름(이다희)은 이 공원에서 전 약혼자 김인우(송종호)와의 데이트를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5771, 25835, 19215, '2회에서 박재훈(최시원)과 한지연(이주연)이 이 술집에서 또 다른 소개팅을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5772, 24935, 19215, '8회에서 박재훈(최시원)은 이 술집에서 첫 키스를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5773, 25791, 19215, '이 건물의 외관은 구여름(이다희)이 일하는 O.NA TV의 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5774, 26293, 19215, '6화에서 박재훈(최시원 분)과 한지연(이주연 분)은 이 카페에서 만나 카메라와 마이크 없이도, 버라이어티 쇼 \'사랑의 제왕-시즌2\' 촬영장 밖에서 이야기를 나눌 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5775, 25909, 19215, '4회에서 김인우(송종호)와 구여름(이다희)의 결혼식에 앞서 부모님과의 미팅을 이곳에서 가진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5776, 26156, 19215, '1회에서 박재훈(최시원)이 한강철교와 한강대교 사이 이 공원에서 한강을 따라 조깅을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5777, 26178, 16144, '1회에서 왕금동(서벽준)과 그의 아버지 왕재국(이한위)이 이곳에 조깅을 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5778, 24954, 16144, '1회에서 서남매의 아버지(김창완)가 한국에 머무는 동안 이곳에 머물렀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5779, 26501, 12688, '16회 마지막회에서 둘이 재회하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5780, 24998, 12688, '3화에서 유민우(송승헌)와 심혜원(손예진)이 두 번째로 등장한 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5781, 26732, 17426, '5회 새봄고 페스티벌 콘서트가 야외극장에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5782, 25718, 17426, '한준우(오의식)는 13화에서 길 건너 임희경(임세미)을 보고 이 공원 앞 상가 교차로에 선다. 그는 그녀가 길을 건너는 것을 막고, 그녀에게 전화를 걸어 그들이 휴식을 취해야 한다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5783, 31882, 17426, '13회에서 이수호(아스트로 차은우)가 임주경(문가영)을 찾아 공원길을 달리는 모습');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5784, 26721, 17426, '14회에서 데이트 중 이수호(차은우)와 임주경(문가영)이 서로를 그려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5785, 31883, 17426, '7화에서 고장난 엘리베이터를 탈출하고 급히 화장실로 달려간 임주경(문가영)은 이 건물에서 나와 눈이 내리기 시작했다는 것을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5786, 25964, 17426, '이수호(아스트로 차은우)와 임주경(문가영)은 14회에서 데이트를 하기 전 4번출구 버스터미널에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5787, 25600, 17426, '9회에서 차수호(아스트로 차은우)는 여기서 학교 친구를 만나지 않을 것이라고 믿고 공개적으로 데이트 할 수 있다고 생각하여 임주경(문가영)을 이 단지에 위치한 빕스 판교점으로 데려갔습니다. 10화에서 임주경(문가영)과 강수진(박유나)은 함께 쇼핑을 하기 위해 이 단지 앞에서 만났습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5788, 31884, 17426, '드라마에서에서 이수호(차은우)가 사는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5789, 31885, 17426, '9회에서 유도를 마친 임주경(문가영 분)과 이수호(아스트로 차은우 분)가 이 건물을 나서는 모습이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5790, 25908, 17426, '15화에서 이수호(차은우)가 미국에 있는 동안 머무는 주거지의 거실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5791, 25635, 17426, '5회에서 임희경(임세미)는 한준우(오의식)에게 이 곳에서 고백을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5792, 31886, 17426, '14회에서 이수호(아스트로 차은우)와 임주경(문가영)이 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5793, 31887, 17426, '12회에서 뜨개질을 하던 중 이수호(아스트로 차은우)는 한서준(황인엽)을 만난다. 무엇이 임주경(문가영)을 괴롭히는지 알아낸다. 배신감을 느낀 유태현(이일준)과 김초롱(박우제)은 각각 수호와 서준을 찾기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5794, 26034, 17426, '극중 등장인물인 이성용(신재휘)이 다니는 \'용파고등학교\'의 촬영지 이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5795, 31888, 17426, '마지막회 16회에서 임주경(문가영)과 한서준(황인엽)은 이 카페에서 의기투합한다. 진정한 미녀/여신강림(tvN, 2020-21)의 여신강림(여신강림)에서 그녀는 자신의 감정에 보답하지 못한 그에게 사과하고, 그녀를 혼란스럽게 만든 그는 차례로 사과하는 모습을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5796, 28487, 17426, '마지막 16회에서 이 미용실은 \'셀레나 뷰티\'로 임주경(문가영)이 인턴을 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5797, 25871, 17426, '9화에서 이수호(아스트로 차은우)와 강수진(박유나)은 부모님과 함께 이 식당에서 점심식사를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5798, 29444, 17426, '12회 학교에서 우연히 마주친 임주경(문가영) 수호(차은우), 임희경(임세미), 한준우(오의식)가 식사를 하며 수다를 떤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5799, 31889, 17426, '11회에서 회상으로 이수호(아스트로 차은우), 한서준(황인엽), 정세연(강찬희/SF9 찬희)이 가게 밖에서 물건을 둘러보는 모습이 나온다. 세연은 그들 모두를 위해 팔찌를 선택하고, 수호와 서준에게 항상 착용해야 한다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5800, 27277, 17426, 'tvN 2020-21 트루 뷰티/여신강림 16회 마지막회에서 임주경(문가영)이 이수호(아스트로 차은우)를 만난다. 근처에 앉아있는 여학생들이 부러워한다. 그녀는 대가로 그녀를 놀리는 수호 앞에서 과도하게 행동합니다. 나중에 그녀는 그를 메이크업 모델로 삼아 연습을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5801, 26293, 17426, '셀레나 뷰티 밖에서 만난 임주경(문가영)과 강수진(박유나)은 이곳에서 수진이 자신에게 사과하는 시간을 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5802, 25661, 17426, '10화에서 임주경(문가영)과 강수진(박유나)이 쇼핑하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5803, 25662, 17426, '4회에서 그녀의 소개팅을 만나러 가는 카페. 9회에서는 이수호(아스트로 차은우)와 강수진(박유나)이 부모님과의 점심식사를 마치고 이곳으로 오는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5804, 31890, 17426, '15회에서 새봄고 동문들이 이 식당에 모여 식사를 하고 있다. 임주경(문가영)이 술에 취해 기절하자 한서준(황인엽)이 나타나 그녀를 도왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5805, 24905, 17426, '12회에서 박세미(정혜원 분)가 친구들과 이 공원을 산책하던 중 임주경(문가영 분)에게 전화를 걸려다 실패한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5806, 26248, 17426, '14회에서 임주경(문가영)과 최수아(강민아)는 이 공원에서 포장마차에서 옥수수를 기다리며 각자 좋아하는 그룹을 향해 아첨한다. 그리고 그들은 주혜민(오유진)이 용파고 여자 불량배들에게 괴롭힘을 당하는 것을 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5807, 24957, 17426, '1회에서 임주경(문가영)이 전학하는 \'새봄고\'의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5808, 26002, 17426, '15회에서 한서준(황인엽)이 임주경(문가영)을 이곳에서 아이스스케이팅 데이트에 데려온다. 최종회 16회에서 주경과 이수호(차은우)가 가든 스테이지에서 서준의 데뷔 공연을 관람한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5809, 28098, 17426, '6회에서 임주경(문가영)은 한서준(황인엽)을 유혹하기 위해 고등학교 시절 괴롭힘을 당했다. 인엽)을 함정에 빠트렸다. 이후 이수호(아스트로 차은우)와 함께 등장한다. 잠시 후 강수진(박유나), 최수아(강민아), 유태훈(이일준)이 나타나 친구를 구하기 위해 방에서 난동을 일으키게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5810, 25608, 17426, '14회에서 이수호(차은우)와 한서준(황인엽)이 탑 밑으로 오는데, 수호는 그들 사이에서 이긴 내기의 일환으로 서준에게 노래를 불러달라고 요청한다. 15화에서 임주경(문가영)은 첫눈을 보기 위해 \'서울의 가장 높은 곳\'으로 향한다. 떨어뜨린 지갑을 찾으러 돌아오던 그녀는 미국에서 돌아온 이수호(차은우)와 마주친다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5811, 29411, 17426, '임주경(문가영)은 숙취에 빠진 누나와 함께 이 고급 찜질방(목욕탕)에 간다. 6화에서 매운 닭발과 삶은 계란을 먹겠다는 약속으로 뇌물을 받는다. 주경을 위해 불행하게도 서준(황인엽)과 또 다른 동급생이 도착한다. 화장을 하지 않은 자신을 알아볼까봐 걱정한 그녀는 무슨 수를 써서라도 그들을 피하려고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5812, 31891, 17426, '7회에서 임주경(문가영)은 학원으로 가는 길에 이 꽃집 밖에 서서 이수호의 머리핀에 감탄한다. (차은우) 전날 밤 그녀에게 선물로 주었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5813, 31892, 17426, '6회에서 임주경(문가영)은 고등학교 시절 괴롭힘을 당하고 도망친 뒤 이 계단 옆 골목길에 숨어 있던 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5814, 31893, 17426, '4회에서 임주경(문가영)은 영화를 보기 위해 대학 데이트를 하고 이 영화관에 온다. 이수호(아스트로 차은우)는 두 사람을 알아보기 위해 같은 영화관에 왔지만, 결국 다른 커플로 착각하게 된다. 건물 밖에서 수호는 이미 다른 사람을 만나고 있던 주경의 대학 시절이 거짓말을 했다는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5815, 25962, 17426, '6화에서 한서준(황인엽)은 임주경(문가영)의 전 고등학교 불량배들과 KFC대학로점 바로 앞에서 맞붙는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5816, 31894, 17426, '이곳은 임주경(문가영 분)과 그녀의 가족들이 머무는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5817, 27443, 17426, '9화에서 한서준(황인엽)은 이 거리에 오토바이를 세워둔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5818, 31895, 17426, '9회에서는 화보 촬영 후 임주경(문가영)이 한서준(황인엽)에게 식사를 대접하는 모습이 그려졌다. 그들이 떠날 때 서준은 주경을 집에 데려다 주겠다고 제안한다. 하지만 그녀는 이미 이수호(차은우)와 통화 중이어서 눈치채지 못했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5819, 26214, 17426, '1화에서 임주경(문가영)이 단골 만화가게를 향해 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5820, 29529, 17426, '1회 첫 장면에서 이수호(아스트로 차은우)가 특정 만화를 찾으러 왔다가 첫 페이지가 없어 짜증을 낸다. 이후 방송 말미에는 이 가게에서 임주경(문가영)과 수호가 만화를 놓고 다투다가 위에서 넘어졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5821, 24922, 17426, '10회에서 임주경(문가영)과 아버지 임재필(박호산)이 이수호(아스트로 차은우)의 아파트를 나와 이 길을 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5822, 25340, 17426, '1회에서 임주경(문가영)이 전학 온 학교이고 이수호(아스트로 차은우)가 있는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5823, 31896, 17426, '2회에서 임주경(문가영)은 그녀를 지나칠 때 이 거리에 위치한 (가짜) 버스 정류장에서 버스를 기다립니다. (그녀의 외모에 매료된) 버스에 있는 남자들은 운전사가 버스를 멈출 수 있도록 주의를 집중시켜 그녀가 여전히 들어갈 수 있도록 관리합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5824, 27076, 17426, '강수진(박유나)은 이곳에 산다. 11화에서 임주경(문가영)이 수진에게 이소후(차은우)와 열애 중임을 알리기 위해 이곳에 오는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5825, 27092, 17426, '2회에서 화장품 가게 사라제나가 있는 이 거리를 한서준(황인엽)이 오토바이를 타고 가다가 우연히 임주경(문가영)을 마주치게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5826, 25894, 17426, '임주경(문가영 분)은 이곳에서 케이블카를 타고 첫눈을 보기 위해 남산타워로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5827, 27321, 17426, '2회에서 한서준(황인엽)은 세운상업지원센터앞에서 자신을 쫓는 남자들과 마주친다. 그가 궁지에 몰리자마자 임주경(문가영)의 도움으로 경찰 알람이 울리고 남자들은 달아난다. 주경은 서준과 마주치자 헬멧을 쓴 채로 겁에 질려 도망간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5828, 28409, 17426, '낙담한 임주경은 1화에서 이 건물 옥상으로 올라갔다. 그녀를 구해주는 이수호(아스트로 차은우)의 모습이 포착됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5829, 25017, 17426, '13회 공항에서 이주헌(정준호)가 아들 이수호(아스트로 차은우)와 통화한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5830, 28099, 17426, '8,9화에서는 등장인물들의 모교인 새봄고등학교 후배들이 이 곳 캠핑장에서 현장학습을 하는 모습을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5831, 24932, 15803, '32회에서 다섯 친구 이수연(이제훈), 한여름(채수빈), 오대기(김경남), 나영주(이수경), 고은섭(SF9 로운)이 술 마시러 왔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5832, 30717, 15803, '어린 이수연(남다름)은 이 거리에서 자신과 함께 태그할 서인우(이동건)를 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5833, 31897, 15803, '이곳은 어린 이수연(남다름)이 \'별들의 나라\' 6회 회상씬에서 어린 서인우(이동건)가 앉았던 밴 뒤를 쫓다가 일어난 사고다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5834, 31898, 15803, '3번째 예고편에서 이수연(이제훈 분)이 이 식당 앞을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5835, 31899, 15803, '이곳의 외관은 한여름(채수빈)의 아버지 한재영(최원영)이 운영하던 예전 술집 \'여우각시별\'로 현재는 장씨의 식당, 집, 진료소로 변신했다. 이수연(이제훈)의 은밀한 곳이었다. 어린 시절 \'형\' 서인우(이동건)와 함께 이곳에 자주 왔던 청년(남다름)이 한여름(채수빈)의 모습을 본 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5836, 24922, 15803, '이 거리는 22회에서 수연(이제훈)의 집으로 돌아가는 길이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5837, 27598, 15803, '7회 플래시백에서 사고 후 휠체어에 쇠사슬로 묶인 어린 이수연(남다름)은 이 갤러리 옆 계단에서 몸을 던지고 싶어한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5838, 26466, 15803, '1회에서는 한여름(채수빈)은 출근길 비를 피해 이 식당 입구로 피신하고, 그곳에는 이미 이수연(이제훈)이 서 있다. 그녀는 그녀의 부적 목걸이(나침반)가 그의 팔에 닿을 때까지 그에게 빗방울을 쏟고 변명하며 "그는 그냥 거기에 서 있습니다". 그녀가 그것을 가져 가려고 할 때, 그가 우산을 열고 떠날 때까지 그에게 달라 붙습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5839, 27219, 15803, '이수연(이제훈)이 공항 동료들로부터 전화를 받고 한여름(채수빈)이 다치는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5840, 25017, 15803, '이수연(이제훈)과 한여름(채수빈)은 이곳 인천공항서비스팀에서 근무한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5841, 27447, 15803, '24회에서 이수연(이제훈), 한여름(채수빈), 오대기(김경남)와 나영주(이수경)가 이 곳에서 레일바이크를 탑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5842, 31900, 12922, '2회에서 고병희(고현정)가 오고 등대 앞에서 석양을 바라본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5843, 27604, 13493, '15화에 이연재(김선아)의 엄마 김순정(김혜옥)이 김사부(정동환)와의 데이트에 이곳에서 꽃을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5844, 26006, 13493, '15회에서 이연재(김선아), 어머니 김순정(김혜옥), 강지욱(이동욱)이 제주여행 가족사진을 찍었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5845, 25945, 12558, '드라마에서 이곳 대부분 장소가 왕과 왕비의 거처이자 후궁인 향원정과 추향교를 겸하고 있다.함화당, 비현각, 경회루 등이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5846, 25773, 4417, '진희(김새론)는 이곳 에서 미국인 양부모를 만나기 위해 공항을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5847, 25868, 14870, '김복주(이성경)와 정준형(남주혁)은  9회에서 해변으로 가는 길에 이 길을 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5848, 31923, 14870, '6회에서 정준형(남주혁)이 편의점 앞에 앉았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5849, 27609, 14870, '1회에서 이성경과 정준형(남주혁)이 이 단지 \'올림피아 수영경기장\'으로 등장에서 대결을 펼친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5850, 31924, 14870, '이 대학의 일부는 \'한얼체육대\'로 등장.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5851, 26051, 14870, '12회에서 정준형(남주혁)은 김복주(이성경)와 \'데이트\'를 위해 이곳을 방문할 계획을 세웠다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5852, 25657, 14870, '마지막 16화 육상에서의 마지막 키스신이 위치하며 부분적으로 \'한올체육대학교\'로 표시됩니다. 김복주(이성경)와 정준형(남주혁)이 자주 만나는 호수도 이곳에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5853, 31925, 14870, '15회부터 여기에서 훈련합니다. 그녀는 국가 대표팀에 합류한다. 16회에서는 정준형(남주혁)이 복주(이성경)를 태릉으로 돌려보내고 두 연인이 헤어지기 힘든 상황이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5854, 31926, 14870, '이 대학의 입구는 \'한얼체육대\'로 나온다. 1화에서 정준형(남주혁)과 김복주(이성경)의 첫 만남이 시작되는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5855, 26093, 14870, '10회에서 정준형(남주혁)은 이 공원에서 동생 정재이(김재윤)를 만나 훈훈한 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5856, 26744, 14870, '정준형(남주혁 분)은 3회에서 전 여자친구 송시호(경수진 분)와 이 다리 발치에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5857, 28490, 14870, '3화에서 정준형(남주혁)이 김복주(이성경)와 여전히 복주를 자신이라고 생각하는 동생 정재이(이재윤)와 함께 이곳에서 저녁식사를 하자고 제안을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5858, 24904, 14870, '10화에서 김복주(이성경)는 짝사랑하는 정재이(이재윤)에게 사과를 받고 싶어 이곳에서 만나자고 한다. 그것은 그녀에게 완전히 고통스러운 순간이며 그녀는 가능한 한 빨리 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5859, 26018, 14870, '11회에서 김복주(이성경)와 정준형(남주혁)이 그녀를 집으로 데려오려다 이 카페를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5860, 25722, 14870, '11회에서 김복주(이성경)와 정준형(남주혁)이 아르바이트에 대해 고백을 하고 이곳을 찾는다. 여기에서 그는 그녀가 실제로 그의 첫사랑이라고 고백하고 그녀가 그를 좋아하는지 확인하기 위해 한 달 동안 데이트를 제안합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5861, 29206, 14870, '드라마에 \'복치킨\'으로 등장하는 식당으로. 김복주 가족이 운영하는 가게다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5862, 31927, 14870, '1회에서 이 대학의 일부가 하늘체육대학으로 등장한다. 이곳은 정준형(남주혁)이 훈련하는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5863, 25668, 14870, '15회에서 김복주(이성경)가 미친 듯이 정준형(남주혁)을 찾다가 이곳으로 온다. 그녀는 그가 여기서 자살했을지도 모른다고 생각했습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5864, 25776, 14870, '김복주(이성경)는 2회에서 동료와 함께 이 식당에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5865, 31928, 14870, '3화의 역도요정 김복주(역도요정 김복주) 내 이 건물에 정재이(이재윤)가 운영하는 \'제이비만클리닉\'이 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5866, 25910, 14870, '9회에서 김복주(이성경)와 정준형(남주혁)이 술 한잔을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5867, 31929, 14870, '드라마에서 고아영(유다인)이 사는 곳이다. 13회에서는 4년 동안 짝사랑했던 정재이(이재윤)가 자신의 집에 찾아와 데이트 제안을 한다. 가슴 아프다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5868, 26158, 14870, '역도요정 김복주(역도요정 김복주) 6회에서 정재이(이재윤)가 콘서트에서 김복주(이성경)를 집으로 데려다주다가 이 박물관 앞에 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5869, 24953, 14870, '14회 정재이(이재윤)는 고아영(유다인)을 이 레스토랑으로 초대했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5870, 27601, 14870, '2회에서 복주(이성경)는 착한남자 정재이(이재윤)에게 반한다. 3화에서 두 사람은 여기서 다시 만나고, 복주는 그녀의 눈을 믿을 수 없다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5871, 29381, 14870, '한얼체육대학교로 등장한다. 자주 등장하는 장소 중 하나는 정자가 있는 인경연못이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5872, 25017, 14870, '인천국제공항은 2번의 중요한 장면에 등장한다. 15화에서 정준형(남주혁)이 어머니를 보낼 때, 16화에서 중국 대회에서 승리해 돌아온 김복주(이성경)를 데리러 올 때. 그녀는 그녀를 위해 거기에 있는 모든 기자들을 무시하고 준형에게 직접 달려갑니다. 인천공항에서 역대 최고로 환대합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5873, 29168, 14870, '정준형(남주혁) 심리학자의 사무실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5874, 31930, 14870, '3회에서 김복주(이성경) 대학이 등반 경주를 벌이던 곳. 복주는 의사재이(이재윤)의 프로그램에 등록하기 위해 가장 먼저 산에 오른 사람의 상금을 타기 위해 열성적으로 시작했고 심지어 친구들을 버리고 떠났다. 불행히도 정준형(남주혁)은 그녀를 발견하고 그녀를 따라가서 그녀를 놀리다가 정상에 오르던 도중 복주를 결국 경주에서 지게 만들었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5875, 31931, 18149, '이곳은 왕세자 이휘(박은빈)의 비밀 장소이자 6화에서 자은/이현(남윤수)이 합류하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5876, 25680, 18149, '어린 정지운(고우림)은 1화에서 정리한 대로 나무다리 위에서 어린 담이(최명빈)를 기다리지만, 안타깝게도 그녀는 오지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5877, 26080, 18149, '이곳은 왕궁으로 등장한다. 1화에서 어린 정지운(고우림)이 아버지 정석조(배수빈)의 손에 이끌려 이곳으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5878, 31932, 18149, '두 주인공이 재회 후 처음 만난 기억이 담긴 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5879, 31933, 18149, '3회에서 정지운(로운)과 이휘세자(박은빈)는 이 절벽에서 왕실 근위병들에게 궁지에 몰리다 탈출하기 위해 아래의 강으로 결국 뛰어내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5880, 25653, 18149, '이 세트는 조선시대 궁궐과 도시의 일부로, 2화에서 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5881, 31934, 18149, '1,2회에서 어린 정지운(고우림)이 어린 담이(고우림)를 찾는 모습에서 볼 수 있듯이, 이곳은 궁녀들의 숙소로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5882, 28440, 18149, '4회에서 이휘(박은빈) 세자에게 주어진 몇 가지 임무를 수행한 후, 왕세자의 강사 자리에 합당한 자임을 증명하기 위해 정지운(김석우/SF9 로운)은 이 정자 바로 앞에 흐르는 강물에 몸을 씻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5883, 26070, 18149, '2화에서 영정지운(고우림)은 이 곳 변두리에서 젊은 궁녀를 쫓던 아버지 정석조(배수빈)를 뒤쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5884, 31659, 18149, '궁궐 안의 일부로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5885, 25673, 18149, '4회에서 왕세자 이휘(박은빈)와 수행원들이 왕궁의 일부로 보이는 이들 정원의 다리를 건너는 과정에서 새 경호원 김가온(최병찬)이\'5보 뒤\' 규칙을 지키지 않자 \'그\'는 좌절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5886, 26073, 18149, '2회에서 어린 담이(최명빈)가 이휘세자의 신분으로 왕실 의식에 참석할 때 배경으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5887, 26015, 18149, '1화에서 영정(고우림)은 세탁한 옷을 되찾기 위해 왕궁 대나무길로 어린 담이(최명빈)를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5888, 26074, 18149, '이 위치의 일부는 왕궁의 일부이며, 1화에서 어린 정지운(고우림)이 기숙사 건물 서재 바로 앞에서 어린 담이(최명빈)를 쫓는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5889, 26076, 18149, '1화에서는 어린 왕세자 이휘(최명빈)가 자신과 꼭 닮은 젊은 궁녀 이휘/담이(최명빈)를 처음 만난다. 이후 5화에서는 담이가 황태자가 된 이휘(박은빈)와 어른 정지운(김석우)이 궁궐에서 가정교사로 일하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5890, 31716, 14103, '이훈동(허정민)이 운영하는 가게이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5891, 26152, 14103, '1회에서 주장미(한그루)가 이 호텔 방에서 이훈동(허정민)에게 프러포즈를 준비한다. 공기태(연우진)는 훈동에게 속아 같은 시간에 이 호텔의 레스토랑/로비에서 소개팅을 하러 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5892, 25796, 14103, '1-2회에서 주장미(한그루)가 계속되는 괴롭힘 혐의에 대해 자신을 변호해야 하는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5893, 26214, 17164, '1화에서 이나은(김소은)이 서둘러 아파트를 떠난 후 이 골목을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5894, 27602, 16583, '시원(양혜지)과 강파랑(손상연)이 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5895, 30597, 16583, '4회에서 정채소(이서빈)는 식당 밖에서 희수(박지원)와 마주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5896, 25945, 16583, '3화에서 등장인물들이 국립고궁박물관으로 당일 봉사활동을 하러 갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5897, 31640, 12890, '이 장소의 수영장은 유은호(손예진)의 직장으로 나타납니다. 이곳에서 김미연(오윤아)은 이동진(감우성)을 처음 만나 사랑에 빠진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5898, 26242, 12890, '이동진(감우성)과 정유경(문정희)은 한강 유람선에서 결혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5899, 26997, 12890, '유은호(손예진)와 이동진(감우성)의 결혼식을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5900, 25859, 14129, '연애의 발견에서 워크샵을 간 섬');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5901, 25641, 14129, '15회에서 남하진(성준)이 한여름(정유미)과 이 식당에서 저녁 식사를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5902, 25816, 14129, '최종회에서 강태하(에릭)와 남하진(성준)이 이곳에서 비즈니스 미팅을 한다. 멀리서 바라보는 하진의 모습에 태하(아직도 커플링을 차고 있는)는 한여름(정유미)이 생각난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5903, 26236, 14129, '연애의 발견에서 정유미, 문정혁이 간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5904, 25824, 14129, '이 곳은 강태하(에릭)의 인테리어 회사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5905, 31935, 14129, '연애의 발견에서 둘러본 예쁜 펜션');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5906, 31936, 14129, '연애의 발견에서 정유미, 문정혁이 건넌 다리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5907, 27419, 14129, '15회에서 도준호(윤현민)와 윤솔(김슬기)은 아침 조깅을 위해 이 공원에서 만난다. 늦게까지 꾸짖는 윤솔은 자신을 좋아하는 세 가지 이유를 묻고 응수한다. 이 심문은 그가 변덕스럽게 그녀에게 키스하는 것으로 끝납니다. 비록 동의하지 않았지만.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5908, 25654, 14129, '15회에서 남하진(성준)은 이 버스 정류장에서 안아림(윤진이)을 공항으로 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5909, 31937, 14129, '이곳은 한여름(정유미)의 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5910, 28618, 14129, '연애의 발견에서 정유미, 성준이 데이트한 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5911, 27507, 14129, '5화에서 강태하(에릭)와 점심을 먹은 후 한여름(정유미), 남하진(성준)은 약간의 다툼 끝에 이 거리에서 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5912, 31938, 14129, '술에 취한 전 남자친구 강태하(에릭)는 한여름(정유미)에게 새로운 사랑을 고백하고, 그녀는 나중에 친구들에게 남하진(성준)을 남자친구로 소개한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5913, 26018, 14129, '10회에서 화나고 답답한 강태하(에릭)는 절친 윤정목(이승준)과 함께 홍대야구 배틀케이지로 찾아온다. 마침내 정목이 태하에게 그만두라고 설득하자 두 사람은 바로 앞 거리에서 진솔한 대화를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5914, 31939, 14129, '10화에서 화난 강태하(에릭)가 친한 친구 윤정목(이승준)과 함께 대전 야구장 홍대에 찾아온다. 정목이 마침내 태하에게 그만하라고 설득했을 때, 둘은 그 곳 바로 앞 거리에서 진심 어린 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5915, 31940, 14129, '1회 회상회에서는 한여름(정유미)과 강태하(에릭)가 한 겨울에 이곳에 찾아온 모습이 그려졌다. 그녀는 그에게 선물로 나무 커플링 한 쌍을 줍니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5916, 24906, 14129, '1화에서 한여름(정유미)과 남하진(성준)이 소개팅 직후 처음 만나 키스하는 곳은 바로 평화의 공원이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5917, 28474, 14129, '5회에서 술에 취한 강태하(에릭)는 한여름(정유미)을 위해 이 거리로 이어지는 계단에서 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5918, 31941, 14129, '9화에서 한여름(정유미)과 강태하(신화 에릭)가 만난 후 점심을 먹기 위해 이 쇼핑몰에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5919, 25724, 14129, '8회에서 안아림(윤진이)이 자전거를 타고 이 다리를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5920, 27329, 14129, '1화에서 안아림(윤진이)은 이를 지나쳐 갑니다. 남하진(성준)의 진료소로 가는 길에 쇼핑을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5921, 25955, 14129, '남하진(성준)의 집인데 1회에서 한여름(정유미)이 어머니를 만나러 오는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5922, 24913, 14129, '연애의 발견에서 정유미, 문정혁이 걸은 길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5923, 31942, 14129, '연애의 발견에서 이승준,정수영이 간 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5924, 25738, 5306, '이동희(이민기)와 장영(김민희)이 이곳에서 화장을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5925, 31943, 17212, '1회에서 공유영(박지훈)이 이 게스트하우스로 이사한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5926, 26263, 17212, '공유영(박지훈)의 등교길로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5927, 24904, 17212, '9회에서 이경영(더보이즈 영훈)이 이 카페에서 아르바이트를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5928, 31944, 15808, '12회에서 한유경(한승연)은 이곳에서 차현오(신현수)에게 마지막 인사를 건넸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5929, 25827, 15808, '1회 회상장면에서 한유경(한승연)은 이 건물 앞 벤치에 앉아 찍은 사진을 들여다본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5930, 25635, 15808, '11회에서 문혜란(서은우)는 이곳에서 의사인 친구 김재욱(성창훈)을 만나 건강에 대해 정보를 얻게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5931, 24933, 15808, '차현오(신현수)는 10회에서 이곳에서 혼자서 한숨 돌린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5932, 31945, 15808, '2회에서 한유경(한승연)이 12일 오전 동작대교에서 일출을 찍기 위해 이 버스 정류장에서 내린다. 2010년 서울에서 4일간의 짧은 체류 셋째 날의 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5933, 26285, 15808, '문혜란(서은우)과 이 식당에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5934, 25335, 15808, '1회에서 이백만(장현성 분)은 한유경(한승연)과 함께 인천공항에서 북촌한옥마을까지 태워주는 조수석에 앉아 이 다리를 건너 게스트하우스로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5935, 25668, 15808, '3회에 고민하는 이백만(장현성)이 찾아오고 윤찬(황재원) 아빠로부터 전화를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5936, 25797, 15808, '한유경(한승연)은 3일 아침 일찍 이곳에서 일출을 촬영하기 위해 2회에서 이 다리로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5937, 25910, 15808, '1화에서 한유경(한승연, 전 카라)이 친구 강채와 함께 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5938, 31946, 15808, '이곳은 이백만(장현성)이 운영하는 게스트하우스 \'재회\'로 등장한다. 거의 다 여기에서 하룻밤 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5939, 31947, 15808, '1회에서 한유경(한승연)은 4일 간의 짧은 체류 첫날,  이 \'포토 스튜디오\' 앞에 멈춘다. 2010년 서울에서 그녀는 안으로 들어가기로 결심하고 가게 주인 이리(예수정)는 그녀에게 유경에게서 예리한 사진 작가의 눈을 본다고 말합니다. 결국 유경이 여기로 달려오는데 놀랍게도 차현오(신현수)가 문을 열어준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5940, 24948, 15808, '차현오(신현수)와 한유경(한승연)이 2010년 짧은 4일간의 서울 체류 첫날에서 이 공원에서 만나고 두번째로 이곳에서 만난다. 벤치에서 잠든 그녀는 그의 어깨에 머리를 기대고 잠에서 깨어나고 그는 그녀를 위해 춤을 추며 그가 댄서가 되고 싶어하는 이야기를 그녀에게 들려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5941, 25862, 15808, '차현오(신현수)는 2010년 짧은 4일간의 서울 체류 사흘째인 장면에서 \'제17회 서울현대무용단 공연\' 리허설을 하는 친구 박선주(한지은)를 찾아 아르코예술극장 밖에서 담소를 나누고 공유한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5942, 28445, 15808, '윤찬(김도완)은 \'절친\'과 7회에서 천다영(한다솔)은 이 호텔 앞에 위치한 (가짜) 버스 정류장으로 그녀를 공항까지 데려다준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5943, 25887, 15808, '1회에서 차현오(신현수)와 한유경(한승연)이 단독으로 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5944, 26650, 15808, '3,4회에서 게스트하우스로 돌아가는 길에 한유경(한승연), 차현오(신현수)가 2010년 서울에서 4일간의 짧은 체류의 마지막 날 비를 피하며 이 골목길 12-7번 집 앞에서 입맞춤을 나눈다. 바로 그날 밤 서울을 떠난다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5945, 31948, 15808, '차현오(신현수)는 2010년 서울에서의 4박 4일의 짧은 체류 첫날 1회에 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5946, 27596, 15808, '이 거리의 북쪽 끝에 있는 작은 광장은 통로이다. 한유경(한승연, 카라 출신)은 1회에서 북촌을 탐방할 때 이곳을 지나고, 2회에서는 민박을 찾을 때도 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5947, 26183, 15808, '3화에서 공기를 맑게하고 그들의 삼각 사랑 (그녀가 생각하는)을 끝내기 위해 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5948, 26071, 15808, '차현오(신현수)는 2010년 서울에서 4일간의 짧은 체류 중 셋째 날 아침 일찍 일출을 보러 가는 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5949, 29529, 15808, '이 가게는 \'D&N\'으로 등장하며 대부분의 캐릭터들이 이곳을 지나간다. 예를 들어 1회에서 이백만(장현성)이 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5950, 31949, 15808, '채널A 열두밤 1회에서 한유경(한승연, 카라)의 친구 강채원(이예은)이 이 아파트에 살고 유경이 그녀와 함께 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5951, 25890, 15808, '3회에서 한유경(한승연, 전 카라)과 차현오(신현수)가 이 집을 지나간다. 2010년 서울에서 현오의 자전거로 댄스 안무를 즉흥적으로 하기 위해 이곳에 들른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5952, 27511, 15808, '1회에서 이백만(장현성)이 한유경(구 카라 출신 한승연)을 이 역 2번 출구에서 내려주는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5953, 31950, 15808, '4회에서 차현오(신현수)는 박선주(한지은)에게 이 식당에서 한유경(한승연)에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5954, 25340, 15808, '2회에서 이 학교는 \'아람중학교\'로 등장하는데, 한유경(한승연, 카라 출신)과 친구 강채원(이예은)이 다니던 학교로, 유경이의 짧은 4일 중 서울 3일차에 방문하기로 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5955, 31951, 15808, '10회에서 \'운좋은 드라이클리닝\'으로 등장한다. 윤찬(김도완)은 \'절친\' 천다영(한다솔)과 함께 어머니에게 생일 선물로 받은 니트 스웨터를 이곳에서 드라이클리닝한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5956, 26663, 15808, '열두밤(열두밤) 1화에서 차현오(신현수)와 한유경(한승연, 카라 출신)의 세 번째 운명적인 만남이 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5957, 31952, 15808, '4회에서는 절친 강석(김영준)과 이 곳을 거닐며 서울에 머물기로 한 차현오(신현수)와 강석이 찾아온다. 강석은 여자들과도 1년이 지나면 서울에서 다시 만나기로 약속했기 때문에 현오의 상황을 잘 알고 있다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5958, 31953, 15808, '4회에서 차현오(신현수)는 명동에서 식사와 오후 시간을 보낸 후 이 역 6번 출구에서 친구 박선주(한지은)에게 작별 인사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5959, 25017, 15808, '1화에서 차현오(신현수)와 한유경(한승연, 전 카라)이 각자 이곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5960, 25018, 16315, '10회 \'천봉고\'의 11학년 여행이 벌어지는 곳이자 마음이 잠시 머무는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5961, 28385, 16315, '11화에서 (옹성우)는 유수빈(김향기)을 이 기념공원에 안장된 정후의 무덤으로 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5962, 27152, 16315, '이별 후 아침, 최준우(옹성우)와 유수빈(김향기)은 등교길에 같은 신호등에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5963, 26257, 16315, '1회에서 최준우(옹성우)는 이 곳에서 자전거를 탄다.이 다리와 2번 다리 사이의 강을 따라 학교로 이동합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5964, 25654, 16315, '3화 마지막 장면에서 최준우(옹성우)는 이 거리의 오솔길을 건너 유수빈(김향기)을 만난다. 앞으로 비에 젖지 말라고 하고 천둥소리가 들리고 그녀를 보호하기 위해 달려갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5965, 31954, 16315, '13, 16회에서 이 학교로 이어지는 긴 오솔길은 고등학교 입구로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5966, 30842, 16315, '드라마에서 최준우(옹성우)가 사는 집으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5967, 24941, 16315, '13회에서 결(강기영)이 제자 최준우(옹성우)를 데리고 이 식당에서 점심을 먹으러 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5968, 31955, 16315, '드라마 안에서 주인공들이 다니는 천봉고등학교\'로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5969, 26000, 16315, '최준우(옹성우) 자전거를 타고 2화에서 이 다리를 건너 아버지의 집으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5970, 31956, 16315, '1회에서 최준우(옹성우)가 아르바이트를 신청하기 위해 이 편의점에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5971, 31957, 16315, '1회에서 유수빈(김향기)은 친구들과 방과 후 카페에 찾아와 친구들과 수다를 떨고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5972, 25734, 16315, '1회에서 최준우(옹성우)가 탄 트럭이 이 다리를 건너는 모습이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5973, 31958, 16315, '여기는 최준우(옹성우)와 유수빈(김향기)는 12회에서 두 사람은 데이트를 위해 이 카페 옥상에 왔는데, 준우가 키스를 하려고 하자 수빈의 아버지가 전화를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5974, 31959, 16315, '마휘영(신승호)의 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5975, 31960, 16315, '12회에서 그는 그녀에게 이혼을 요구하지만 그녀는 이를 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5976, 25799, 16315, '1화에서 최준우(옹성우)가 이 건물 밖 도로에서 트럭에 올라타 그 건물을 지나친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5977, 25026, 16017, '김남길이 금새록에게 알려준 증인의 위치');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5978, 25948, 16017, '12회에서 구대영(김성균)은 그의 오랜 파트너 영천의 기일을 만나기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5979, 31961, 16017, '38회 초반, 결연한 김해일(김남길)이 이 고가도로를 걸어 이중권(김민재)과 맞붙는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5980, 26030, 16017, '14회에서 구대영(김성균)이 아버지 이(정동환)의 죽음에 연루된 사람들의 네트워크와 그 배경을 간략하게 소개할 때 이곳은 남석구(정인기)의 집으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5981, 25607, 16017, '40회에서 김해일(김남길)이 박경선(이하늬)과 함께 이 공원을 통해 자신을 비난하러 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5982, 25906, 16017, '8화에서 이 건물의 내부는 바티칸의 모습으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5983, 25612, 16017, '교도소 장면을 촬영한 수목원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5984, 27539, 16017, '김성균이 띠를 두루고 배회한 거리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5985, 31962, 16017, '이 거리는 극 중 파트너 형사 구대영(김성균)과 서승아(금새록)의 주 관할로 부분적으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5986, 31963, 16017, '김성균과 금새록이 함께 곱창을 먹은 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5987, 26093, 16017, '5화에서 김해일(김남길)은 국정원 시절 동료였던 성산대교 근처 공원에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5988, 26088, 16017, '이영준 신부(정동환)의 장례식을 마치고 돌아오는 길, 김해일(김남길)은 6화에서 교회 동료들에게 이 건물 앞에 내려달라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5989, 31964, 16017, '이곳은 드라마에서 김해일(김남길)과 박경선(이하늬)의 동네 편의점 \'TQ24 플러스\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5990, 26294, 16017, '11회에서 김성균과 서승아(금새록)가 이 건물 앞에서 그를 내려주는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5991, 26569, 16017, '김성균이 김남길에게 커피를 사준 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5992, 26265, 16017, '이하늬가 한주그룹 김건용을 만난 중식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5993, 25909, 16017, '13회에서 정동자(정영주)와 강석태(김형묵)가 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5994, 31965, 16017, '태국 청년 안창환이 근무하던 중식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5995, 25348, 16017, '14회에서 구대영(김성균)이 아버지 이(정동환)의 죽음에 연루된 사람들의 네트워크와 그 배경을 간략하게 소개할 때 박원무 의원(한기중)이 이 건물 앞에서 타격하는 모습이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5996, 24943, 16017, '이 나이트 클럽은 열혈 사제 25-27 화에서 나이트 클럽 \'Rising Moon\'으로 등장합니다. 시민들의 부패와 착취가 만연한 가상의 구담구 권력 피라미드의 중심이자 사제 김해일(김남길)을 중심으로 부적합한 팀이 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5997, 25776, 16017, '37회에서 이중권(김민재)이 부하들과 이 식당에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5998, 31966, 16017, '김남길의 단골 술집으로 나온 기사식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (5999, 31967, 16017, '지하에 5000억이 숨겨져 있던 대범무역');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6000, 26272, 16017, '이영준(정동환)의 목격자 중 한 명으로  9화에서 사건이 벌어지고 있는 곳에서 김해일(김남길)과 그의 새 동료 구대영(김성균)이 그녀와 이야기를 나누기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6001, 31968, 16017, '김남길과 김성균의 첫 공조 때 식사한 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6002, 26273, 16017, '열혈사제에서 구담성당으로 나온 성당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6003, 31969, 16017, '1회에 김남길이 기도를 드렸던 바닷가 성당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6004, 31970, 16017, '김남길이 버스를 타고 지나간 다리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6005, 31971, 16017, '김남길이 무당을 잡기 위해 뛰어다닌 바닷가');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6006, 26067, 3376, '8화에서 해명공주(오연서)를 보고 당황한 균우(주원)는 부끄러워 도망친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6007, 25945, 3376, '4회에서 궁궐 정원을 산책하는 모습. 혜명과 현우는 자기 소개를 하지만 곧 인사를 하고 자리에서 일어나 서로를 알아보다가 왕자가 서로 아는지 묻는 말에 직접 말다툼을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6008, 25723, 13535, '1회에서 김영광(안도규)의 아버지인 김인배(김인배)가 차를 몰고 집으로 가는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6009, 28422, 13535, '1화에서 젊은 윤재인(안은정)이 수녀들에게 입양되기 위해 이곳에 남겨진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6010, 31972, 16963, '은강병원 원장을 만났던 술집을 나온 뒤 취한 인동혁(태인호)은 아파트 로비(B동)에서 지영원(박예진)을 만나기 위해 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6011, 31973, 16963, '31화에서 이시준(신하균)과 한우주(전소민)가 외출을 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6012, 31974, 16963, '인동혁(태인호)은 2화에서 은강병원 원장과 술자리에 초대된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6013, 25796, 16963, '차동일(김동영) 4화(전편 2)에서 경찰 사칭 혐의로 재판을 받았지만 정신상태를 이유로 무죄를 선고받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6014, 27519, 16963, '지선과 친구인 한우주(정소민)는 이곳에 자주 와서 위로를 받고, 가끔 사람을 데려오기도 한다. 1화에서 연극 후배 두 명을 데리고 간식을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6015, 26179, 16963, '한우주(정소민)가 치료받는 \'지영원 정신과\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6016, 31975, 16963, '한우주(정소민)는 진료소에서 지영원(박예진)에게 폭언을 퍼부은 후 목적 없이 계속 걸어가다 결국 이 고가도로에 이르게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6017, 25335, 16963, '7화에서 이 다리 위의 차 앞에서 이시준(신하균)이 도움을 주기 위해 돌진한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6018, 26242, 16963, '9화(전체 5화) 회상회에서는 이시준(신하균)이 환자와 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6019, 27372, 16963, '6화에서 이시준(신하균)은 자신의 달리기 중 하나로 이 육교를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6020, 31976, 16963, '1화(풀 1화)에서 정신과 의사 이시준(신하균)이 근무하는 은강병원의 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6021, 24908, 16963, '1회에서 환자 차동일(김동영)이 \'경찰관\'으로서 이시준(신하균) 박사와 함께 이 거리를 순찰한다. 동일은 술에 취한 남자가 벽에 소변을 보는 것을 발견하고, 그를 막으려 한다. 술에 취한 남성은 결국 자신이 취하지 않았다고 주장하며 골목식당(19-6번)에 기댄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6022, 24910, 16963, '25화(전체 13화)에서 이시준(신하균) 징계 청문회에서 은강병원 원장과 함께있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6023, 24913, 16963, '10화에서 한우주(정소민)가 자신의 클리닉에서 지영원(박예진)을 향해 폭발한 뒤 이 거리를 정처 없이 걷는다. 그녀는 이곳에서 버스커들을 우연히 만나는데, 한국인은 그들이 공연하는 것을 볼 수 없을 정도로 괴로워한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6024, 24953, 16963, '31화(전체 16화)에서 인동혁(태인호)과 지영원(박예진)이 이곳에서 회식을 하고 미국의 John Hopkins에서 1년 동안 배치된 것에 대해 그녀에게 알려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6025, 26652, 13954, '10회에서 독고마테(장근석)는 귀지(김보라)와 이 식당에서 저녁 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6026, 25948, 13954, '독고마테(장근석)의 어머니가 이곳에 안장되어 있으며 벨아미 2화에서 영신이 그녀를 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6027, 25607, 13954, '6회에서 독고마테(장근석)가 이 공원에서 그녀에게 더 이상 연락하지 않기로 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6028, 27565, 13954, '예쁜남자 3회에서 독고마테(장근석)와 잭희(소유진)가 웨딩드레스를 찾는 모습');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6029, 25953, 13954, '독고마테(장근석)의 고객인 대표 잭희(소유진)는 이 곳에 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6030, 26095, 13954, '김보통(이지은)은 2회에서 이곳에 벼룩시장 가판대를 차리고 데이비드 최다비드(이장우)는 그녀에게 모자를 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6031, 26442, 13954, '1회에서 독고마테(장근석)는 이사 어머니와 함께 이 마을의 집에서 이사를 가기 전에 이곳에 머문다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6032, 26285, 13954, '9회에서 묘미(박지윤)가 이 식당에서 용서를 빌었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6033, 25335, 13954, '독고마떼(장근석)는 11화에서 이 다리를 건너 병원에 입원한 김보통(이지은)을 찾아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6034, 28805, 13954, '드라마 안에서 일렉요정으로 출연하는 일렉선녀(김예원) 이 장소에서 영업하는데, 이곳에 잭희(소유진) 대표가 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6035, 25896, 13954, '15회에서 김보통(아이유/이지은)은 독고마테(장근석)에게서 이 공원에서 만나자는 메시지를 받고 이 공원에서 만나기로 했습니다. 하지만 그녀는 납치되고 마침내 David Choi(이장우)가 그녀를 구합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6036, 26651, 13954, '이 공원은 11회에서 박기숙(독고영재)의 집으로 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6037, 24933, 14398, '강선우(조정석)가 11회에서 이 갤러리 카페에서 화보 촬영을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6038, 31977, 14398, '선우와 봉선의 수산시장 데이트 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6039, 26088, 14398, '1화 이후 이곳에서 PD로 일하는 오랜 친구 이소형(박정아)을 만나기 위해 강선우(조정석)가 오는 것을 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6040, 25791, 14398, '강선우(조정석)가 이 광장에서 코앞에 있는 CJ E&M 센터[CJ E&M 센터]에서 오랜 친구 이소형(박정아)을 만난 후.상암문화광장 오른쪽 YTN 사옥 앞에서 휴대폰으로 찍은 사진을 보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6041, 31846, 14398, '썬레스토랑의 실제 배경인 이탈리안 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6042, 25929, 14398, '13회 남산케이블카 남산타워에서 내려오다가 반한게 그녀가 아니라는 것. 그래서 세빛섬 앞에 앉아 수다를 떨고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6043, 25608, 14398, '12화에서 강선우(조정석)가 나봉선(박보영)을 깜짝 선물로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6044, 25930, 14398, '10화에서 강선우(조정석)가 전 연인에게 좋아하는 사람이 있다고 말한 직후에 이 역이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6045, 25894, 14398, '강선우(조정석)와 나봉선(박보영)도 케이블카를 타지만 오 나의 귀신님 12회 끝과 13회 시작에서 남산타워에서 하강한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6046, 31978, 14398, '썬 레스토랑 식구들이 숨을 돌렸던 단합장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6047, 31979, 14398, '단합대회 장면을 촬영한 바닷가 펜션');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6048, 26256, 14878, '1회에서 박진희(박진희)가 기도하러 왔다. 그녀는 가는 길에 유금비(허정은)를 보고 집에 데려다준다고 하지만 금비는 절까지 같이가자고 고집한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6049, 27536, 14878, '이 곳은 고강희(박진희)의 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6050, 25809, 14878, '모휘철(오지호)을 이곳에서 죄수로 처음 등장한다. 또한 유금비(허정은)가 아버지를 처음 만나는 곳이기도 하다. 아버지는 1화에서 이 감옥에서 출소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6051, 25607, 16965, '16회 최종회에서는 장하리(장나라)와 한이상(고준)의 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6052, 25643, 16965, '10회에서 장하리(장나라)와 한이상(고준)이 데이트할 때 이 동굴을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6053, 26078, 16965, '12회에서 이별을 앞둔 장하리(장나라)와 한이상(고준)은 이 쇼핑몰 잠시나마 힘들더라도 관계를 위해 모든 것을 바치기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6054, 28109, 16965, '3회에서 장하리(장나라)는 정자 기증자(여회현)를 만나기 위해 이 커피숍(본명 \'OPEN COFFEE\')에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6055, 25834, 16965, '13회에서 윤재영(박병은)이 하리 엄마(김혜옥)와 이곳에서 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6056, 25660, 16965, '장하리(장나라)와 한이상(고준)은 12회에서 이 거리를 산책한다. 이상은 휴식을 취하기 위해 짧은 여행을 가자고 제안하지만 둘은 서로를 오해하고 결국 하리는 인접한 터널로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6057, 26399, 16965, '2회에서 소개팅을 하게 되었는데 한이상도 같은 장소에 있는 것을 보고 놀란다. 하리가 소개팅과 대화를 나누는 동안 이상은 몇 테이블 떨어져서 어슬렁거린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6058, 26000, 16965, '9회에서 장하리(장나라)와 한이상(고준)이 다리 밑에서 우연히 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6059, 27154, 16965, '장하리(장나라)의 엄마 이옥란(김혜옥)이 서울숲 야외무대에서 야외 에어로빅 강습을 하고 있다. 10화에서는 한이상(고준)이, 12화에서는 이상과 윤재영(박병은)이 함께 수업을 듣게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6060, 26400, 16965, '6회에서 심야식당으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6061, 25335, 16965, '1회 초반에 라디오를 들으면서 차를 몰고 이 다리를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6062, 31980, 16965, '1회에서 장하리(장나라)가 친구 박연호(백승희)와 함께 빈티지 마이크를 사기 위해 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6063, 31981, 16965, '12회에서 장하리(장나라 분)가 말마 스모크하우스 바로 앞 이 거리에서 한이상(고준)과 헤어질 뻔했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6064, 26140, 16965, '3회에서 장하리(장나라)와 엄마가 이 쇼핑몰에서 쇼핑 데이트를 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6065, 26521, 14516, '14회에서 김영호(소지섭)가 이곳에 입원했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6066, 31982, 14516, '15회에서 김영호(소지섭)과 강주은(신민아)은 데이트 장소로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6067, 27179, 14516, '전시회 \'더 브리티시\' 뮤지엄컬렉션 휴먼이미지\'가 10회에 출연했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6068, 30771, 17645, '한비수(이민기)와 오주인(나나)은 이곳에서 시간을 보내며 둘은 1년 뒤 서로에게 편지를 쓰기도 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6069, 25980, 17645, '12회에서 한비수의 엄마 유해진(이휘황)은 절친 정화(김호중)와 함께 자동차 여행을 떠난다. 소나무 길을 따라 이 사찰까지 걸어가며 추억을 쌓을 수 있는 시간과 귀여운 손주를 위해 기도한다. 불행하게도 유해진은 쓰러지고 빗수(이민기)는 그녀의 곁을 지키기 위해 급히 병원으로 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6070, 26719, 17645, '톱 여배우 오주인(임진아/애프터스쿨 나나)이 출연한 화장품 회사 KT의 그녀는 가게 밖에 있는 차에서 내려 진열창으로 향하고 립밤을 손에 쥡니다. 오 마이 레이디로드/오! 마스터(오!님) (MBC, 2021)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6071, 28108, 17645, '1회에서 볼 수 있는 톱 여배우 오주인(임진아/애프터스쿨 나나)을 대표하는 소속사 \'피치 엔터테인먼트\' 사옥으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6072, 26079, 17645, '4화에서 한비수(이민기)와 오주인(임진아/애프터스쿨 나나)이 엄마 윤정화(김호중)와 함께 이 식물원 카페를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6073, 26644, 17645, '한비수(이민기)는 오주에게 문자를 보낸다. 그러나 그녀가 오늘 저녁에 예상하지 못한 것은 그녀가 도착했을 때 유진(강민혁)도 함께 있었고, 이에 비수는 두 사람에게 이제 진짜 데이트를 시작하자고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6074, 31888, 17645, '오주인(임진아/애프터스쿨 나나)은 이 카페에서 친구 배광자(이현정)와 러브라이브에 대한 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6075, 31983, 17645, '회의를 마친 주인은 한기를 풀기 위해 권투 라운지로 향하는데, 그곳에서 주인은 트레이너에게 화를 낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6076, 25871, 17645, '10회에서 오주인(임진아/애프터스쿨 나나)과 한비수(이민기)가 이 식당에서 데이트를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6077, 24902, 17645, '3회에서 대영은 어머니 강해진(이휘향)과 함께 이 식당에서 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6078, 26037, 17645, '6화에서 이 건물의 외관은 화장품 회사 KT로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6079, 26085, 17645, '2회에서 한비수(이민기)는 세인병원 로비에서 치명적인 교통사고를 당해 이전에 \'생명을 구한\' 수수께끼의 인물과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6080, 25792, 17645, '3화에서 방송국 MBS의 건물로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6081, 26136, 17645, '오주인(임진아)은 이 식당에서 한비수(이민기)를 저녁식사에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6082, 27119, 17645, '16회(마지막회)에서 한비수와 주인의 요원들은 자켓으로 비를 피하며 함께 빗속을 달릴 때 이 장소가 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6083, 25734, 17645, '한비수(이민기)와 오주인(나나)은 차를 타고 건너간다. 2회에서 요양원으로 가는 길로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6084, 25335, 17645, '파리에서 서울로 돌아온 정유진(강민혁)은 3회에서 이 다리를 건너다 오주인(임진아)에게 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6085, 25797, 17645, '9화에서 한비수(이민기)가 이 다리 위를 운전한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6086, 26232, 17645, '톱 여배우 오주인(임진아)이 출연하는 화장품 회사 KT의 광고에서, 그녀는 광고 상영에서 보듯, 이 다리를 건너는 동안 차창 밖을 애처롭게 바라보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6087, 24946, 17645, '4회에서는 서울로 돌아온 오주인(임진아)과 정유진(씨엔블루 강민혁)의 만남이 그려졌다. 5회에서 정유진은 이곳에서 비서에게 주인에 대해 브리핑을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6088, 28445, 17645, '한비수(이민기)가 지금까지 머물렀던 집으로 나오지만 그의 어머니가 팔았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6089, 27595, 17645, '이 거리는 1회에서 한비수(이민기)와 오주인(임진아)이 지나는 집으로 가는 길이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6090, 26404, 17645, '10화에서 오주인(임진아)이 아침 산책을 하는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6091, 26214, 17645, '10회에서 당황한 비수(이민기)는 식사를 위해 주인(임진아/나나)을 만나려 한 후 저스트레코드(삼청쿠크)로 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6092, 29474, 17645, '한비수(이민기)와 오주인(임진아/애프터스쿨 나나)은 이 카페를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6093, 24922, 17645, '4회에서 한비수(이민기)가 오주인(임진아/애프터스쿨 나나)의 집을 나와 컴퓨터를 팔에 끼고 이 거리를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6094, 31984, 17645, '한비수(이민기)가 자주 찾는 음반 가게 \'저스트 레코드\'가 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6095, 26663, 17645, '7화에서 이 횡단보도에 서 있을 때 한비수(이민기)는 길 건너편에서 이미 마주친 영혼(강승호)을 갑자기 알아차린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6096, 24913, 17645, '1회에서 한비수(이민기)는 어머니의 생일날 부모님 댁을 다시 방문한 뒤 깊은 생각에 잠긴 채 이 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6097, 25913, 17645, '한비수(이민기)는 1회에서 톱 여배우 오주인(임진아)과 이곳에서 만나 세 번째로 그녀를 거부한다. 10회에서는 둘 다 이곳으로 돌아와 1회 때의 나쁜 기억을 좋은 기억으로 바꾸고 비수는 주인에게 목걸이를 선물한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6098, 25909, 13556, '21회에서 장해준(김승수)이 이옥자(정재선)와 이곳에서 회식을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6099, 27336, 5912, '김현우(문채원 분)는 사랑의 일기예보(2015)에서 이 동상 앞에 기상캐스터를 촬영한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6100, 29508, 18985, '1회에서 네온콘텐츠서비스팀에 입사하기 위해 온마음(김세정)과 구준영(남윤수) 이 고가도로를 건넌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6101, 31985, 18985, '9회에서 네온웹툰의 만화가 오윤(손동운)이 이곳에서 카페에서 술을 마시며 다른 코스튬 유저들에게 인정받습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6102, 27151, 18985, '1화에서 드라마 남자주인공 구준영(남윤수)이 살고있는 주택가로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6103, 25726, 18985, '1회에서 온마음(김세정)이 전동스쿠터로 음식을 배달하며 봉트리살롱[봉트리]에 아르바이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6104, 26263, 18985, '1회에서 온마음(김세정)은 아르바이트를 마치고 이 공원에서 휴식을 취하고 네온으로부터 긍정적인 답변을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6105, 27530, 18985, '7화에서 만화가 백어진(김갑수)과 조수 임동희(백석광)는 이곳에서 진솔한 대화를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6106, 25335, 18985, '석지형(최다니엘)과 온마음(김세정)은 1회에서 웹툰 작가 백어진(김갑수)을 방문한 후 자신의 차를 타고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6107, 26156, 18985, '이 공원으로 이어지는 육교에서 마주친 온마음(김세정)과 보조만화가 임동희(백석광)는 이 공원의 습지[이촌한강 논습지] 근처에 앉아 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6108, 25797, 18985, '2회에서 온마음(김세정)과 구준영(남윤수)은 이 다리를 건너는 지하철을 타고 집으로 돌아가는데, 한강 너머로 노을을 보며 집에 갈 수 있는 직업을 늘 꿈꾸던 그녀가 얼마나 소원이 이루어졌는지를 들려준다. 그는 그저 그녀의 꿈이 얼마나 단순한지를 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6109, 25910, 18985, '7화에서 만화가 나강남(임철수)가 저녁에 술을 마시러 친구들과 만나는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6110, 24948, 18985, '주인공인 온마음(김세정)이 매일아침 이 공원에서 조깅 훈련을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6111, 26457, 14355, '3회에서 백마리(설현)가 먼저 라이트하우스에 와서 자신이 이미 정재민(여진구)에게 빠졌고, 그가 그리웠다는 것을 깨달았다. 그때 재민이도 나타나 그곳에서 마리를 발견했기 때문에 소원이 이루어졌다고 말했다. 그들은 마침내 서로에 대한 감정을 인정한 후 키스를 나눈다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6112, 26030, 3850, '5회에서 오로라(전소민)가 오빠 오금성(오창석)을 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6113, 31986, 3850, '사임당(서우림)이 이 저택에 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6114, 25620, 3850, '2회에서 오로라(전소민)가 쇼핑하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6115, 25913, 3850, '5화에서 박주리(신주아)가 황마마(손창민)와 함께 외식을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6116, 32004, 3450, '1회에서 영희수(김옥빈)와 권혁주(지현우)가 동네 버스정류장에서 버스를 타려는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6117, 26657, 3450, '권혁주(지현우)는 이 마을의 옥탑방에서 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6118, 26137, 3450, '뉴질랜드에서 영희수(김옥빈 분)를 주연으로 촬영한 렉스의 새 뮤직비디오는 2화에서 초연된다. 희수는 또한 그 축하 행사에 참여하고 그녀가 비디오에서 편집되었다는 것을 알아내야 한다. 친구들과 함께 영상 시사회를 보던 권혁주(지현우 분)가 달려와 위로한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6119, 32005, 3450, '6회 마지막회 잠실종합운동장 인테리어체육관 앞. 그는 그녀에게 경기장에 함께 가자고 제안하지만 결국 그녀는 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6120, 26019, 3450, '영희수(김옥빈)는 오버 더 레인보우 1회에서 렉스의 백댄서 오디션을 위해 프라이드 엔터테인먼트로부터 전화를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6121, 24948, 3450, '1회에서 이 공원에 있던 혁주(지현우)와 그의 크루들이 브레이크 댄스를 설명한 후 그에게 이동한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6122, 24933, 17760, '12회에서 김명수(성노진)와 함께 황희태(최원영)가 이 카페에서 장석철(윤진성)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6123, 26673, 17760, '5화에서 황희태(이도현)와 이수련(금새록)의 약혼식이 끝난 후, 김명희(고민시)는 정원으로 나가 담배를 피우고 있는 희태를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6124, 25345, 17760, '이 테마파크는 황희태(이도현)가 사는 서울 동네의 일부로 다른 여러 곳과 함께  보여진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6125, 31538, 17760, '1회에서 김명희(고민시)는 이 교회를 감독하는 사제(유순웅)를 찾아 대학 장학금 가능성에 대해 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6126, 29449, 17760, '1회에서는 이곳에서 이수련(금새록)의 체포 소식을 듣게 됩니다. 3회에서는 황희태(이도현)와 명희가 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6127, 32006, 17760, '4회에서 황희태(이도현)와 김명희(고민시)가 고아원에서 돌아온 후 이 집 근처 골목을 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6128, 25871, 17760, '2회에서 황희태(이도현)는 여전히 이수련(금새록)을 사칭한 김명희(고민시)와 이 식당에서 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6129, 29181, 17760, '1회에서 황희태(이도현)는 서울의 한 카페에서 전 여자친구 유진(권은빈)을 만난다. 그리고 그녀는 헤어진 그에게 결혼을 청하는 모습이 그려졌다. 8회에서 이수련(금새록)은 서울로 이사를 왔다가 홀로 이곳에 온다. 10회 회상 장면에서 희태와 그의 친구 김경수(권영찬)도 이곳에서 노는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6130, 24913, 17760, '8회에서 황희태(이도현)와 이수련(금새록)이 서울로 떠나기 전 김명희(고민시)가 자신에게 했던 말을 들려주는 산책길에 오른다. 이후 수련은 희태에게 "여기서 헤어지자"며 "덕수궁 돌담길을 함께 걸으면 부부는 꼭 헤어진다"고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6131, 32007, 17760, '1,2회에서 황희태(이도현)가 이 식당에서 소개팅을 기다리고 있다. 이수련(금새록)인 척 하는 김명희(고민시). 4회에서 희태는 이곳에서 이수련(금새록)을 만나 그달 말까지 연애를 계속하자고 애원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6132, 32008, 17760, '4회에서 황희태(이도현)가 김명희(고민시)와 함께 \'사랑고아원\'에서 아이들의 건강 검진을 하는 봉사활동을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6133, 26061, 17760, '드라마 배경의 주요 촬영지이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6134, 32009, 17760, '6회에서 황희태(이도현), 김명희(고민시)는 형제 황정태(최승훈), 김명수(조이현)와 함께 이 놀이동산에서 노는 장면, 그들은 회전목마를 타고 바이킹을 탄 모습(산희태)이 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6135, 25619, 17760, '약혼을 앞둔 황희태(이도현)와 이수련(금새록)은 이모(김현)의 손에 이끌려 이곳으로 예물을 보러온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6136, 27318, 13506, '오작교 형제들에서 황태범(류수영)과 차수영(최정윤)이 기자로 일하는 IBC 방송으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6137, 25668, 13506, '58회 마지막회에서 황태필(연우진)과 정윤숙(조미령)이 결별하고 2년 뒤에 다시 만나자고 서로 약속하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6138, 24945, 13506, '1화에서 황태희(주원)는 범인을 따라 이 거리로 들어와 철로 앞에서 멈춰야 하는 상황에서 범인을 체포한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6139, 25930, 13506, '1회에서 황태희(주원)는 3번 출구와 역을 잇는 육교로 범인을 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6140, 26098, 13506, '1회에서 황태희(주원)와 동료가 이 거리에서 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6141, 28424, 13506, '18회에서 이 회장의 그랜드볼룸에서 두 주인공이 결혼식을 올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6142, 26738, 13506, '18회에서 황태식(정웅인)이 집에 데려다주는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6143, 26139, 17634, '35회에서 이광식(전혜빈)이 그에게 아버지가 결혼을 승낙했다고 말하는 장면에서 보듯, 한예슬(김경남)은 이 건물 옥탑방에서 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6144, 25641, 13639, '이곳에서 용태무(이태성), 용태용(박유천), 박하(한지민)가 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6145, 26067, 13639, '조선시대 궁 배경의 촬영이 이 곳에서 이뤄졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6146, 26229, 13639, '이곳은 박하 (한지민)의 길입니다. 소년들이 따라잡는 동안 우리는 에피소드 7에서 그녀가 일을 하는 것을 봅니다. 15회에서 박하와 용태용(박유천)이 데이트로 다시 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6147, 32022, 13639, '15화에서 박하(한지민)와 용태용(박유천) 여기서 데이트 하고 불꽃놀이를 하고있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6148, 25854, 13639, '이각(박유천 분)은 4회에서 홈쇼핑 채널에서 신부 가운을 입은 홍세나(정유미 분)가 이 요트클럽에서 호화로운 결혼 제의를 광고하는 것을 보고 자신의 공주가 다른 사람과 결혼한다는 것을 추론한다. 그래서 세나와 맞서기 위해 달려오다가 세나를 지키기 위해 쳐들어오는 용태무(이태성 분)의 주먹에 맞아 요트에서 내린 용태용(박유천 분)처럼 이각은 건물 아래로 추락한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6149, 25608, 13639, '10회에서 용태용(박유천)과 홍세나(정유미)는 남산타워에서 야경을 감상하는 반면 세나는 조작을 계속한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6150, 24948, 13639, '12회 성곽에서 잠시 휴식을 취하는 모습');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6151, 26090, 13639, '13회에서 키스 후 여기로 온다. 그녀는 그에게 같은 기간 동안 그와 함께 살고 싶다고 말하고 그가 얼마나 오래 머물 계획이냐고 묻자 그는 조용히 대답한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6152, 26072, 13639, '2화에서 최우식(최우식), 우용술(정석원), 이각군이 21세기에 도착해 처음으로 궁을 향할 생각을 하게 되는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6153, 25894, 13639, '용태용(박유천)과 홍세나(정유미) 역시 10회에서 케이블카를 타고 남산타워에서 야경을 감상하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6154, 25673, 13639, '조선시대의 회상장면에서 왕세자비가 물에 빠져 (비소 중독으로) 죽은 장소에서 두드러진다. 마지막 20회에서 이각 왕세자가 박하(한지민 분)를 위한 편지를 묻었던 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6155, 25343, 13639, '13회에서 용태용(박유천)과 박하(한지민)가 이별 후 처음 이곳에서 재회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6156, 26501, 13090, '오승아(김하늘)가 그녀의 상을 거부하는 이곳에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6157, 32027, 3608, '유지태의 누나가 자살하는 장면이 촬영된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6158, 32028, 3608, '최민식과 유지태가 만나는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6159, 27737, 3608, '실제로 나온 군만두로 유명한 중국집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6160, 32029, 3608, '최민식이 만두 먹는 장면을 찍은 중국집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6161, 32030, 3608, '최민식이 동네 양아치들한테 상상훈련 테스트 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6162, 25809, 3608, '국내 유일한 익산에 있는 교도소 세트장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6163, 27120, 12660, '화에서 김인하(이병헌)가 이 감옥에 수감되어 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6164, 31931, 18274, '1회에서 어린 이산(이주원)이 휴식을 위해 찾아왔습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6165, 26080, 18274, '궁전으로 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6166, 26072, 18274, '7화에서 주함루를 등지고 부용지에서 하루 낚시를 하는 영조(이덕화)와 이산(2PM의 이준호)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6167, 32031, 18274, '<옷소매 붉은 끝동> 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6168, 25673, 18274, '궁궐의 일부로 등장. 3화에서 황태자 이산(이준호)과 정덕임(이세영)이 정연공주(김이온)에 의해 서로를 소개받는 장면에서 그녀가 학자를 짝사랑하고 있다는 것을 이해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6169, 31815, 18274, '5회에서 성덕임(이세영)이 이 숲을 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6170, 26076, 18274, '이 정원은 궁궐의 일부로 보여지며, 양궁장과 이산(이주원)의 사적 서재를 포괄한다. 6회에서 왕세자가 궁술을 연습할 때와 이후 정자에서 공부할 때 처음으로 등장한다. 그곳에서 성덕임(이세영)과 그녀의 궁녀들 밑에서 바베큐 파티를 하는 소리가 들리고, 덕임이 자신을 좋아한다는 말을 듣게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6171, 32038, 4924, '완득이가 엄마와 함께 있던 터미널');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6172, 32039, 4924, '완득이에서 완득이가 아빠를 찾아가는 장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6173, 32040, 4924, '완득이에서 나왔던 설렁탕 집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6174, 32041, 4924, '완득이 촬영지이자 완득이의 집이었던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6175, 32042, 4924, '완득이가 폭력배와 충돌이 있었던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6176, 30821, 14969, '6회에서 강봉구(성준)는 이곳에서 고객 미팅을, 심재복(고소영)은 이곳에서 면접을 보는 장면으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6177, 24933, 14969, '2화에서도 이곳이 만남의 장소가 된다. 하지만 이번에는 모든 것이 정사에 관한 것이다. 심재복(고소영)은 남편이 바람을 피운 여자 정나미(임세미)와 그녀의 가장 친한 친구 두 명을 만나 그녀를 지지한다. 그녀의 친구 중 한 명이 바람난 남자의 아내에게 공격을 받으면 모든 것이 엉망이 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6178, 24935, 14969, '1회에서 구정희(윤상현)는 여기에서 고객 만찬을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6179, 25787, 14047, '한복 대회가 끝나고 집으로 돌아가고 싶을 때, 장보리(오연서)는 13화에서 지하철을 탈 돈이 부족하다는 것을 깨닫는다.  6번 출구 앞에서 노점상에게 잔돈이 있는지 묻자 수봉(안내상)이 끼어들어 돈을 내놓는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6180, 26990, 14047, '12회에서 장보리(오연서)는 참가할 한복 경연대회의 조건을 논의하기 위해 이곳에 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6181, 25722, 14047, '34회에서는 이재화(김지훈)가 장보리(오연서)에게 이곳에서 프로포즈를 하는 장면이 방송됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6182, 26740, 14047, '이동후(한진희) 회장의 저택으로 나오는 장소.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6183, 32043, 15121, '24회에서는 왕원(임시완), 왕린(홍종현), 진달래가 만발한 아름다운 풍경 속에서 행복한 시간을 보내는 은산(임윤아)은 산에 대한 사랑이 커져가는 과정을 이야기하고, “고통으로 웃던 날도 잠시뿐이었다”고 가슴 아픈 결말을 맺는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6184, 26486, 15121, '드라마에서 이 테마파크는 고려의 궁으로 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6185, 26487, 15121, '40회에서 왕원(임시완)이 서 있는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6186, 26067, 15974, '6회에서는 하선(여진구)이 신 장관에게 아들 이검(최규)에게 지팡이를 짚으라고 명령하는 장면이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6187, 25680, 15974, '1회에서 하선(여진구)과 그의 극단은 \'나무다리\'를 뛰어넘는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6188, 26080, 15974, '왕세자 이헌/하선(여진구)의 방은 이곳에 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6189, 28437, 15974, '1회에서 광대 극단이 이 산을 넘습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6190, 25653, 15974, '6화에서는 병들고 복수심에 불타는 왕 하순(여진구)이 걷는 문이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6191, 26072, 15974, '왕이 된 남자의 궁전의 일부로 등장한다. 6회에서 주함루에서 여왕을 생각하며 반성하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6192, 26487, 15974, '2회에서 아버지를 이 나무 아래로 망명시켰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6193, 32044, 18905, '타산지석0\'으로 등장하는 식당으로, 공찬(황인엽)과 조폭 출신 셰프 구구갑(조달환), 소형칠(이규성)이 운영하는 가게다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6194, 25604, 18905, '7회에서 TK로펌 대표 최태국(허준호)가 사는 곳으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6195, 28426, 18905, '1회에서 오수재(서현진)가 이곳에서 소송을 변호하고 그녀가 계단을 내려가는 모습이 목격된다. 8화에서 수재는 피고인의 은행에 앉아 그녀의 사건은 그녀의 구금 연장 측면에서 여기에서 논의됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6196, 25917, 18905, '6회에서 오수재(서현진)와 임승연(김윤서)이 갤러리에서 만난다. 그녀와 남편을 상대로 한 소송에 대해 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6197, 24933, 18905, '1회의 회상장면에서 오수재(서현진)는 이 카페에서 소송 증인을 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6198, 26124, 18905, '1회에서 오수재(서현진)가 이곳에서 소송을 방어하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6199, 25848, 18905, '1회에서 오수재(서현진)는 TK 로펌에서 강등된 후 이 대학의 법대 교수로 임명된다. 이곳에서 공찬(황인엽)을 제자로 다시 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6200, 25835, 18905, '8회에서 최주완(지승현)이 두 친구와 술을 마시며 말다툼을 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6201, 32045, 18905, '홍석팔(이철민)이 운영하는 호스테스 바가 이 건물에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6202, 26152, 18905, '드라마에서 1회 \'여성변호사협회 총회\'가 이 호텔 셀레나 웨딩홀에서 열렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6203, 25720, 18905, '2화에서 오수재(서현진)는 플룻룸(토룸)에서 최택국 TK 로펌 회장(허준호)을 만나기로 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6204, 26037, 18905, '2화에서 한성범(이근영)이 운영하는 \'한수그룹\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6205, 26410, 18905, '7회에서 오수재(서현진)는 TK 로펌 대표 최태국(허준호)과 이 식당에서 만난다. 그녀에 대한 다음 행보를 알아보기 위해 물을 시험해 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6206, 25773, 18905, '5회 회상 장면에서 오수재(서현진)는 최주완(지승현)과의 아이를 기대하고 이 공항을 떠나 뉴욕으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6207, 26229, 18905, '한기택(전재홍)은 2022년 4회에서 술에 취해 아침에 딸들과 함께 공원을 찾았다. 그곳에서 오수재(서현진)는 그를 찾아 거래를 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6208, 25953, 18905, '2회에서 오수재(서현진)는 로비 카페에서 상사이자 TK 로펌 최태국(허준호) 회장을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6209, 26142, 18905, '10회에서 백진기(김창완) 서정대 로스쿨 학장이 이 식당에서 비즈니스 미팅을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6210, 32046, 18905, '옥상에 사는 공찬(황인엽)의 계모 지순옥(배해선)이 6회에서 그가 그녀의 집을 따라 아파트 문 밖에 음식을 두고 가는 모습을 보았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6211, 30842, 18905, '공찬(황인엽)의 계모 지순옥(배해선)은 6회에서 공찬(황인엽)의 집을 따라 아파트 문밖에 음식을 두고 나오는 모습을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6212, 26110, 18905, '2회에서는 이인수(조영진) 의원이 공식 일정의 첫 번째 정거장으로 찾아와 봉투를 받는다. 공동묘지를 떠날 때 배달된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6213, 25625, 18905, '4회에서 최윤상(배인혁)은 TK 로펌 비서 하일구(전진기)와 함께 이 식당에서 술을 마신다. \'안강원 사건\'에 아버지와 형이 가담하지 않았는지 확인한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6214, 26697, 18905, '이 타워의 외관은 드라마에서 최태국(허준호)이 운영하는 TK 로펌 건물로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6215, 25876, 18905, '오수재(서현진)와 공찬(황인엽)의 단골집이다. 4화에서 둘이 따로 왔는데 술에 취한 수재가 공찬에게 공찬이 자신을 좋아한다는 이야기를 엿듣게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6216, 26131, 18905, '8회에서 법무법인 TK 비서실장 하일구(전진기)는 이곳에서 홍석팔(이철민)을 만나 술에 취하게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6217, 32047, 18905, '6회에서 오수재(서현진)은 고된 일을 마치고 퇴근길에 이 거리의 신흥슈퍼 앞에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6218, 32048, 18905, '극중 드라마 주인공인 공찬(황인엽)의 양엄마인 지순옥(배해선)은 이 거리에 위치한 \'연성철강\'(가제) 에서 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6219, 25335, 18905, '5회에서 오수재(서현진)가 이 다리를 운전하는 모습이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6220, 25797, 18905, '7회에서 백진기(김창완) 법대 교수가 택시를 타고 이 다리를 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6221, 24910, 18905, '4회에서 최주완(지승현)은 술에 취한 한기택(전재홍)에게 내부자 정보를 얻기 위해 술집에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6222, 25855, 18905, '4회에서 TK 변호사 민영배(김선혁)가 이 호텔에서 오수재(서현진)를 만나는 로비로 등장한다. 그는 아내 정희영 교수(주지연)에게 직접 전화를 걸어 연인을 만난다고 생각하고 증거로 사진을 찍기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6223, 25809, 18905, '7회에서 오수재(서현진)가 여기에 수감된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6224, 25635, 15975, '1회에서 이진상(오지호)은 외로운 유부녀와 이곳에서 데이트를 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6225, 28424, 13385, '6화에서 이 장소의 그랜드 볼룸에서 축하 행사가 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6226, 25773, 4543, '강민호(솔경구)는 이곳에서 외국으로 딸을 데리러 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6227, 28535, 14423, '9화에서 김태현(주원)과 한여진(김태희)은 데이트를 위해 이 곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6228, 25976, 14423, '9화에서 김태현(주원)과 한여진(김태희)은 이곳에서 데이트를 하게 되고 여진은 키스를 위해 몸을 기울인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6229, 32049, 14423, '이곳은 투자사 레드라인 인베스트먼트 인 포레스트의 배경으로, 1~4회에서 강산혁(박해진 분)이 인수합병 이사로 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6230, 26428, 14423, '3회 회상장면에서 한여진(김태희)이 동생 한도준(조현재)과 금단의 사랑 이야기를 나누면서 조금씩 알게 된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6231, 25637, 14423, '드라마 내 \'한신병원\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6232, 32050, 17081, '김다솜과 송종호의 과거 회상 장면 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6233, 25822, 17081, '김민준이 과거 연인을 기리기 위해 방문한 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6234, 32051, 17081, '송지효와 손호준이 14년 전 기념일에 다툰 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6235, 32052, 17081, '송지효의 절친이 운영하는 단골 바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6236, 32053, 17081, '손호준이 송지효의 제안을 수락한 분수 장면 배경');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6237, 32054, 17081, '송지효와 손호준이 재회하는 장면 촬영 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6238, 26220, 17081, '송지효와 손호준이 다닌 대학교 캠퍼스 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6239, 25905, 14383, '1회에서 해원(신현빈)은 탈 것을 기다리며 이 버스 정류장에서 유리 반사를 통해 자신의 잘린 입술을 확인한다. 같은 장면에서 희주(고현정 분)의 단독 초대전 포스터가 이 버스정류장에 광고되고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6240, 25607, 17081, '이 공원에서 오대오(손호준)는 노애정(송지효)에게 사과를 하고 그들의 삶이 얼마나 달라졌을 수 있었는지 되풀이하며 오후에 메타세콰이어길에서 두 사람과 딸의 모습을 그린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6241, 24932, 17081, '10회에서 오대오(손호준)와 주아린(김다솜)이 이 식당에서 점심을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6242, 24900, 17081, '14회에서 혼수상태에서 깨어난 오대오(손호준)는 노애정(송지효)에게 사과하고 노애정(송지효)의 삶이 얼마나 달라졌을까를 되새기며 그녀가 유명 PD로 이 도서관에서 TV 인터뷰를 하는 모습을 상상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6243, 25871, 17081, '6화에서 오연우(구자성)의 엄마 주보혜(진희경)가 친구들과 이 식당에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6244, 26410, 17081, '7회에서 류진(송종호)과 소속사 \'송엔터테인먼트\'의 제니퍼 송(서정연)이 이 식당에서 식사를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6245, 32055, 17081, '노애정(송지효)의 딸 노하니(엄채영)가 다니는 학교로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6246, 25659, 17081, '2화에서 노애정(송지효)은 생각에 잠겨 이 공원을 가로질러 걷다가 빨간불 앞에서 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6247, 25662, 17081, '6회에서 노애정(송지효)와 오대오(손호준)는 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6248, 32056, 17081, '7회에서 오연우(구자성)는 노애정(송지효)에게 신발 한 켤레와 상의를 사준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6249, 26399, 17081, '3화에서 구파도(김민준)와 함께 이 카페에서 빚에 대해 이야기를 나누는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6250, 26000, 17081, '2화에서 화를 내며 도발한 오대오(손호준)가 다리 난간에 기대어 앉기 전까지 이 다리를 건너간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6251, 26631, 17081, '노애정(송지효)과 오대오(손호준)는 3-4회에서 이곳에서 각자의 업무 미팅을 하다가 우연히 마주친다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6252, 31598, 17081, '이 바는 오대오(손호준)와 류진(송종호)의 단골 만남의 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6253, 29174, 17081, '주아린(김다솜)은 우는 아이를 보아 그 아이에게 다가간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6254, 24946, 17081, '6회에서 노애정(송지효)과 오대오(손호준)가 그녀에게 연극을 하라고 설득하기 위해 여배우를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6255, 32057, 17081, '3회에서 노애정(송지효)이 이 코너 가게 앞 벤치에 앉아 생각에 잠긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6256, 26434, 17081, '11회에서 오대오(손호준)와 노애정(송지효)의 엄마 최향자(김미경)는 이 카페에서 대오가 손자의 아버지라는 사실을 알게 됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6257, 28533, 17081, '노애정(송지효)은 엄마 최향자(김미경), 딸 노하니(엄채영)와 함께 이 집에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6258, 25864, 17081, '12회에서 이 호텔에서 애준과 딸에 대한 악성 기사를 작성한 노애정(송지효)와 기자 나(이해운).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6259, 26152, 13748, '1화 이 호텔에서 서정훈(성준)은 혜윤(정소)에게 프러포즈한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6260, 31276, 13748, '3화에서는 윤진이(윤소희)와 4화에서는 오도연(이도연)과 함께 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6261, 26000, 13748, '하정훈(성준)은 절친인 기중(김영광)과 민호(김진수)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6262, 26000, 15515, '2회에서 길 잃은 송현철(김명민)이 이 다리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6263, 25868, 18802, '1회에서 라파엘(성훈)은 출근길에 차를 몰고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6264, 32058, 18802, '오우리(임수향)가 10대 시절 처녀 서약(1회 회상)을 하고 2년 사귄 남자친구 이강재(신동)와 함께 찾아온 성당이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6265, 25948, 18802, '13회에는 오우리(임수향)와 이강재(신동욱)와 강재가 등장한다. 그의 아버지에게 존경을 표하고 그의 생물학적 아버지가 아니며, 그가 그녀와 그녀의 아이를 받아들일 준비가 되어 있다는 것을 우리에게 알린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6266, 26737, 18802, '2회에서 오우리(임수향) 머리를 맑게 하기 위해 이 옥상으로 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6267, 26211, 18802, '10회 회상씬에서 오우리(임수향)는 어머니 외할머니가 깜짝 방문했을 때 이 고등학교에 다녔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6268, 25797, 18802, '1회에서 라파엘(성훈)은 출근길에 차를 몰고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6269, 24950, 18802, '10회에서 오우리(임수향)는 이 거리에서 택시를 잡으려다 결국 차가 멈춰서 그녀를 태워주고, 이강재(신동욱)는 운전석에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6270, 32059, 18736, '차승원이 인사하러 다닌 전통 시장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6271, 32060, 18736, '신민아와 노윤서가 만나 대화한 돌다리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6272, 32061, 18736, '한지민과 김우빈, 정은혜가 식사한 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6273, 27569, 18736, '극중 이병헌(동석)과 신민아(선아)의 바다여행지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6274, 28424, 13458, '17회에서 차성주(김병세)와 김진숙(나영희)은  이 장소 그랜드볼룸에서 결혼식을 올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6275, 29249, 14839, '7회에서는 기간 한정 데이트로 홍나리(수애)가 고난길(김영광)을 이 곳에 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6276, 25865, 14839, '16회 마지막 장면에서 홍나리(수애)와 고난길(김영광)이 손을 잡고 걸어간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6277, 32062, 14839, '이 카페는 홍나리(수애)가 중요한 정보를 얻는 동네 할머니 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6278, 27120, 14839, '5회 회상 장면에서는 고난길(김영광)이 이곳에 수감되어 있었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6279, 25948, 16373, '2회에서 허윤도(이장우)와 함께 돌아가신 어머니를 추모하기 위해 찾아오다 건물밖으로 나오다가 체포된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6280, 25865, 16373, '16회에서 이 공원에 안장된 동생 모완수(이규한)에게 마지막 조의를 표한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6281, 25908, 16373, '5화에서 모석희(임수향) 할아버지 모왕표(전국환)의 사무실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6282, 27243, 16373, '이 타워는 2화에서 MC 그룹의 본부로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6283, 29222, 16373, '이정인(한지민)은 14회에서 이 건물의 식당에서 권기석(김준한)의 아버지인 권영국(김창완)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6284, 31718, 16373, '4회에서 모석희(임수향)와 허윤도(이장우)는 이 광장을 따라 밤산책을 한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6285, 31943, 16373, '3회에서 허윤도(이장우 분)는 모석희(임수향 분)의 마약 사건에 대해 묻기 위해 이 게스트하우스를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6286, 26399, 16373, '1회에서 허윤도(이장우)를 만나 함께 일하자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6287, 26178, 16373, '이 공원에서 어린 허윤도(이장우)가 목숨을 끊고자 하지만 그의 (지금) 계부 허장수에게 구해진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6288, 27120, 16373, '16회에서 한제국(배종옥)은 이 감옥에서 시간을 보냈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6289, 25929, 16373, '허윤도(이장우)는 모석희( 임수향)은 6회에서 이 커플에게 좌절감을 토로할 수 있도록 예빛 사옥 바로 앞에 주차했다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6290, 24907, 16373, '이 계단에서 어린 모석희는 한제국(배종옥)에게 한제국(배종옥)에게 할아버지와 함께 한국에 머물게 해달라고 간청한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6291, 24944, 16373, 'MC그룹 하영서(문희경)가 1회에서 연인과 함께 밤에 찾아와 영상으로 협박 계획을 알게 되는 곳은 바로 이 장소 옆 요트다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6292, 25797, 16373, '2회 회상장면에서 한제국(배종옥) 탑 대표는 본 TOP의 첫 번째 미션을 회고적으로 마치고 이 다리를 운전해 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6293, 27536, 16373, '4회에서 \'우유마녀\'라는 이름으로 알려진 사채업자가 살고 있는 이곳에 모석희(임수향)가 허윤도와 함께 돈을 빌리러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6294, 25913, 16373, '5화에서 모왕표 회장(전국환 분)이 한제국(배종옥 분)에게 MC그룹에 합류해달라고 부탁한 방이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6295, 26524, 16373, '3화에서 모석희(임수향)는 여기에 갇혔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6296, 24939, 15861, '10회 마지막 회에서 문선아(정여원)와 우대우(김정윤)가 집 밖(이 다른 집 옆)에서 화해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6297, 28584, 15923, '1회에서 구해라(이민정)는 부산 근교 이탈리아 사업가에게 돈을 받고 그를 여기로 데려온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6298, 32076, 15923, '3~5회에서 구하라(이민정)의 부산 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6299, 26697, 15923, '태인준(주상욱)의 회사 \'골드슈즈\' 사옥으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6300, 25335, 15923, '5화에서 한 번도 아닌 시댁 식구들과 함께 생일 파티를 하고 난 뒤 좌절에 빠진 차씨 가족은 이 다리를 건너 집으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6301, 28424, 15923, '9회 \'황금화\' 이벤트가 열리는 행사장으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6302, 25913, 15923, '6화에서 엄마 한성숙(송옥석)과 곧 시댁 식구가 되는 이은숙(하민)이 피부 관리를 위해 이곳에 오게 되면서 우연히 만나게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6303, 28425, 14102, '11화에서 이건(장혁)과 강세라(왕지원)는 이 식당에서 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6304, 26991, 14102, '14회에서 김미영(장나라)이 첫 전시를 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6305, 28147, 14102, '이건(장혁)이 가나안 장로들을 자리에 앉히고 서약서에 서명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6306, 26167, 14102, '15회에서 아티스트 Ellie Kim(장나라)으로 알려진 김미영이 장인화학의 새 광고 캠페인 이미지 디자인을 의뢰받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6307, 25612, 14102, '이 정원은 1화에서 보듯이 속편의 배경이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6308, 25908, 14102, '이건(장혁)과 김미영(장나라)은 건의 사직 기자회견에서 화해한 후, 18화에서 이곳에서 하룻밤을 함께 보낸다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6309, 25958, 14102, '14회에서 \'언니\'를 만났을 때 도덕적 지지자로 다니엘 피트와 함께 김미영(장나라)이 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6310, 24932, 14102, '14회에서 김미영(장나라)과 다니엘 피트(최진혁)가 이곳에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6311, 32077, 14102, '3, 4회에서 \'여월섬\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6312, 32078, 14102, '15회에서는 이 가게 2층이 김엘리(강나라)의 서울 스튜디오 겸 집으로 소개된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6313, 26428, 14102, '9화 이 장소에서 자선경매가 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6314, 26094, 14102, '9화에서 이건(장혁)과 다니엘 피트(최진혁)가 술 경쟁을 하기 위해 이곳에 갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6315, 26285, 14102, '12회에서 김미영(장나라)과 이별했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6316, 25724, 14102, '문화 예술 기금 모금 행사에서 비참한 사건이 일어난 후 미영(장나라)은 8화에서 남편 건(장혁)의 도움으로 이 다리 아래 공원으로 그녀를 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6317, 26242, 14102, '7회에서 미영의 임신한 여동생 미자(이미도)와 처남 최씨(임형준)는 집을 구하러 상경하고 미영(장나라)과 건(장혁)에게 한강 유람선 더블데이트에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6318, 26245, 14102, '19회에서 이건(장혁)은 어린 시절 추억을 떠올리게 하는 오래된 소나무 사진을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6319, 27076, 14102, '재벌 이건(장혁) 회장과 할머니 왕씨(사장)이 이 맨션에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6320, 25714, 14102, '이 박물관 겸 카페는 디자이너 다니엘 피트(최진혁)의 카페 \'파도\'로 보이는데, 5화 이후 에피소드에서 이건(장혁)의 집 바로 아래 거리에서 오픈했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6321, 25856, 14102, '이건(장혁)과 김미영(장나라)은 다니엘 피트의 전시를 보기 위해 이곳에서 만날 계획이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6322, 26652, 14697, '1회에서 심보니(황정음)과 제수호(류준열)은 이곳에서 처음 마주친다. 그는 카지노에 놀러 왔고 그녀는 웨이트리스와 청소부로 아르바이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6323, 32079, 14697, '3화에서 제수호(류준열)가 자신의 프로젝트에 대해 생각하며 자전거를 타고 회사로 가는 배경에서 버스 정류장이 보입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6324, 26278, 14697, '12회에서 이 문 바로 맞은편, 수원천(수원천)의 모습이 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6325, 26067, 14697, '효명세자 (박보검)의 거처로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6326, 32080, 14697, '1회에서 심보니(황정음)의 집으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6327, 32081, 14697, '6회에서 제수호(류준열), 한설희(이청아), 최건욱(이수혁)이 만나는 곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6328, 27239, 14697, '3화에서 개리(최건욱)(이수혁)가 고민을 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6329, 24965, 18047, '4회 회상회에서는 한승욱/장알렉스(이상윤)가 버려진 신문을 통해 할아버지의 죽음을 알게 된 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6330, 25604, 18047, '여기는 어린 한승욱(영훈)이 살았던 곳으로 10회에서 강미가 살았던 순간을 회상한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6331, 25948, 18047, '여기에 안장된 연주의 할머니를 추모하기 위해 조연주(이하늬)와 안유준(이원근)이 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6332, 26030, 18047, '1회에서 조연주(이하늬)는 이 식당에서 회장을 만나 \'동등한 대우\'를 요청합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6333, 25816, 18047, '4-5회에서 조연주(이하늬)의 사고 목격자가 혼수상태로 입원하는 \'한주병원\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6334, 32082, 18047, '10회에서 한승욱/알렉스 장(이상윤)은 이 식당에서 식사를 하고 한주그룹 가사도우미 김경신(예수정)은 헤어진다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6335, 25906, 18047, '2~3회에서 유민그룹 가족의 장례가 치러지며, 강미나(이하늬)와 멤버들이 모인 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6336, 26080, 18047, '15회에서 이 공원의 궁궐 일대에서 한주그룹 회장 한영식(전국환)과 한성운(송원석)이 대화를 하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6337, 28386, 18047, '5회에서 왕필규(이규복)와 최대치(조달환)는 조연주(이하늬)의 아버지이자 이 감옥에 수감된 남문강의 두목(정인기)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6338, 25827, 18047, '16회에서 알렉스 창(이상윤)이 로스앤젤레스 전망대에서 별을 바라보며 관람객들을 안내한다. 조연주(이하늬)가 그를 만나러 오는 것은 바로 그곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6339, 25635, 18047, '이 카페는 강미나(이하늬)가 운영하는 혜별갤러리로 나온다. 4회에서 강미나(이하늬)와 한승욱/장알렉스(이상윤)가 만나는 장면에서 처음으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6340, 24934, 18047, '강미나(이하늬)가 사는 한주그룹 맨션으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6341, 32083, 18047, '7회에서 강미나/조연주(이하늬)가 심문을 위해 소환되는 \'서평지검\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6342, 26540, 18047, '유민그룹\' 사옥, 1화에서 강미나(이하늬) 가족의 사고 소식이 전해졌고, 4화에서는 조연주(강미나)가 본사를 방문하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6343, 26697, 18047, '14화에서 한성혜에게 아웃된 후 이 건물에서 비서 김은정(이화겸)이 탈출을 시도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6344, 27121, 18047, '이 건물의 외관은 박소이(박정화)가 나인오클락뉴스의 아나운서로 근무하는 SEC 방송국으로, 4화에서 처음 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6345, 26039, 18047, '유민그룹 사옥으로 등장. 3회에서 한주그룹 대표들이 주주총회에 참석하는 모습, 4회에서 조연주(강미나)가 총회에 참석하는 모습.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6346, 32084, 18047, '6회에서 한식당에서 오랜 인연을 이어온 박소이 아나운서(EXID 박정화)를 만났다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6347, 30848, 18047, '2-3회에서 유민그룹 가족의 장례가 치러지며, 강미나(이하늬/이하늬)와 멤버들이 모인 곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6348, 25913, 18047, '조연주(강미나)와 강미나(이하늬)가 5회에서 미나의 장인, 한주그룹 회장(전국환)을 만나 식사를 하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6349, 25781, 18047, '1회에서 강미나(이하늬)가 이 곳으로 경매 대금에 대해 갤러리 스태프와 논의하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6350, 25017, 18047, '1회에서 이 공항에 도착한 한승욱/장창(이상윤)은 터미널 대형 스크린으로 유민그룹 사고 소식을 접한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6351, 24915, 18047, '드라마에서 \'한주호텔\'로 나온다. 1화에서 강미나와 조연주(이하늬)가 2021년 해별갤러리 공모 경매에 참석하기 위해 도착하는 모습');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6352, 27000, 18047, '본관은 서울중앙지검 건물로 보인다. 1화에서 조연주(이하늬)와 안유준(이원근)이 함께 건물에 도착하는 모습이 그려졌다. 7회에는 중앙도서관[선문대학교 중앙도서관]도 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6353, 28105, 8391, '최종회 16회에서 지윤수(임수정)의 집으로 등장하며 최시안(신수연)이 자신의 논문을 발표하기 위해 방문하는 모습이 그려졌다. [스포일러] 백승유(이도현)도 머무는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6354, 25868, 17822, '8회에서 유자성(김지석)이 이 길 위를 드라이브 하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6355, 29247, 17822, '4회 후반부에 이 장소가 두드러지게 등장한다. 자성(김지석), 영원(정소민), 신겸(정건주)은  \'오두막집\' 중 한 곳을 촬영하고 기록하기 위해 하루를 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6356, 32085, 17822, 'JTBC 2021년 월간매거진 월간집 16회에서 유자성(김지석)의 여행 일정을 따라 나영원(정소민)이 그를 데려온다. 이 집의 강변 정원은 함께 있는 것만으로도 추억을 만드는 방법이라고 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6357, 25640, 17822, '술을 같이 마시고 집으로 돌아오는 길에 술에 취한 남상순(안창환)이 이 카페 앞에 앉는다. 6회에서 당황한 여의주(채정안)에게 자신을 좋아하는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6358, 25641, 17822, '2화에서 절친한 친구 유자성(김지석)과 신겸(정건주)이 이 식당에서 저녁을 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6359, 32086, 17822, '16회에서 나영원(정소민)과 유자성(김지석) 이 카페에서 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6360, 25948, 17822, '7회에서 이 컬럼바리움에 부모를 안치한 유자성(김지석)이 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6361, 27151, 17822, '1회에서 이곳에 8채의 집이 있는 유자성(김지석)이 나영원(정소민)과 함께 안과 검사를 받고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6362, 32087, 17822, '12화에서 유자성(김지석)과 최고 편집장(김원해)을 이 갤러리로 안내한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6363, 32088, 17822, '6회에서 나영원(정소민 분)은 이 카페에서 일 때문에 무언가를 줍고, 유자성(김지석 분)은 그녀를 여기까지 태워다 준다. 카페 주인은 영원에게 목재 작업장을 사용할 것을 제안하고, 자성은 깜짝 선물로 집 형태의 목재 열쇠고리를 만든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6364, 25908, 17822, '11회에서 드라마 안 잡지사의 월간집 편집팀이 방문하는 유명연예인 잉꼬부부의 아파트 인테리어 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6365, 25635, 17822, '15회에서 나영원(정수민)과 유자성(김지석)은 이 장소에서 점심을 먹습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6366, 24932, 17822, '15화에서 월간지 팀이 이 레스토랑에서 함께 점심을 먹고 남상순(안창환)은 자신이 여의주(채정안)와 사귀고 있다는 사실을 숨기려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6367, 26135, 17822, '12회에서 이 건물 앞에서 차를 세운다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6368, 24900, 17822, '월간 매거진 홈 12회에서 남상순(안창환)은 경제적인 문제로 인해 옥상으로 갈 수 있는 이 도서관의 계단에 앉아 해결책을 모색한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6369, 32089, 17822, '16회에서 유자성(김지석)이 나영원(정소민)과 당일여행으로 방문하는 곳 중 한 곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6370, 32090, 17822, '11회에서 매거진 월간집 편집부가 방문하는 연예계 커플 \'이하트 전하트\'의 아파트로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6371, 26644, 17822, '7회에서 유자성(김지석 분)은 부모님의 기일을 맞아 홀로 술을 마시러 이 술집을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6372, 31743, 17822, '3~4회에서 유자성(김지석)이 나영원(정소민)을 이 라운지에 초대해 술을 마시는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6373, 27515, 17822, '13회에서 신겸(정건주)이 나영원(정소민)을 이 식당에 초대했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6374, 25655, 17822, '14회에서 이 식당에서 회식을 하고 있는데 갑자기 나영원(정소민)이 끼어든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6375, 28097, 17822, '15화에서 유자성(김지석)은 온라인에서 이 식당에 대한 제안을 접하고 나영원(정소민)을 저녁 데이트에 초대하는 완벽한 계획으로 예약했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6376, 29514, 17822, '7화에서 이 호텔의 외관은 래미안 이촌 첼리투스 \'래미안첼리버스\'로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6377, 25726, 17822, '7회에서 나영원(정소민)은 누수로 아파트를 떠나야 하는 상황에서 하룻밤을 묵어야 할 곳을 찾아 이 길을 걸어간다. 그녀는 이웃 가게 바로 앞에 멈춰 서서 핸드백과 사랑에 빠지고 그것을 사기를 망설인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6378, 30817, 17822, '5회에서 설문조사를 위해 유자성(김지석), 나영원(정소민), 황비서 황(김우담)이 이 집을 방문한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6379, 32091, 17822, '1화에서 나영원(정소민)이 일을 시작하는 유자성(김지석)이 운영하는 라이프스타일 매거진 \'월간집\'의 사옥으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6380, 26247, 17822, '16회 마지막 회에서 이 건물(\'영원의 탑\'으로 등장)에서 그녀에게 콜라보를 해보라고 설득했지만 헛수고였다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6381, 32092, 17822, '1회 초반부터 유지성(김지석)이 이 다리를 달리고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6382, 27225, 17822, '14회에서 팀 오찬 후 함께 식사를 하고 그녀는 머뭇거리다가 수락하지만 그는 계획했던 대로 마음을 고백하지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6383, 25335, 17822, '나영원(정소민)과 유자성(김지석)은 8화에서 자신의 차를 타고 다른 장소 방문길에 올라 이 다리를 건넌다. 14화에서 다시 이 다리를 건너는 장면이 있으며 그들이 헤어진 직후이기 때문에 그들 사이의 분위기는 약간 차갑다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6384, 25347, 17822, '4화에서 신겸(정건주)은 나영원(정소민)을 자신의 \'세컨드 하우스\'로 초대했다. 세컨드 하우스는 사실 이 공원의 여의도 캠핑장에 친 텐트였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6385, 26139, 17822, '13회에서 그녀는 요즘 서울의 젊은이들이 사는 집에 대한 기사를 위해 이 옥상 집에 그를 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6386, 32093, 17822, '7회에서 유지성(김지석)이 이 아파트 (드라마 안에서는 \'엠타워\'로 나온다)의 아파트로 이사하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6387, 25910, 17822, '8회에서 아파트 청약에 당첨된 후, 남상순(안창환)은 그의 동료들을 초대해 이 식당에서 축하를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6388, 29230, 17822, '15회에서 신겸(정건주)과 육미라(이화겸)가 이 식당에서 저녁을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6389, 32094, 17822, '2회에서 유자성(김지석)이 장회장 집으로 가는 길에 팀과 함께 이 거리를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6390, 32095, 17822, '1화에서 유자성(김지석)이 나영원(정소민)에게 임대한 아파트를 소유한 아파트로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6391, 31960, 17822, '14회에서 이 카페에 신겸(정건주)과 나영원(정소민)이 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6392, 27331, 17822, '7화에서 이곳에서 \'월간 매거진 하우스 온기 나눔 바자회\'가 개최된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6393, 32096, 17822, '2회에서 남상순(안창환)은 여자친구(김소은)와 함께 이 카페에서 했던 데이트를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6394, 25781, 17822, '1회에서 볼 수 있듯이 유자성(김지석)의 아파트입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6395, 27222, 17822, '9화에서 회식 후, 월간 하우스팀이 이 플랫폼을 걸어 내려옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6396, 32049, 14776, '2화에서 미사 어패럴의 본사로 등장하는 건물로 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6397, 32097, 14776, '13, 15, 16회에서 김남일(곽시양), 최애라(김지원)의 이복동생이 운영하는 식당.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6398, 32098, 14776, '과거 \'월규 양복점\'으로 출연');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6399, 27318, 14776, '3화에서 최지연(차주영)(아나운서)이 강태양(현우)과 헤어지는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6400, 26659, 14776, '나연실(조윤희)은 이 건물의 옥상에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6401, 26660, 14776, '나연실(조윤희)은 이 건물의 옥상에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6402, 26139, 14776, '드라마 주인공인 나연실(조윤희)은 이 건물 옥탑방에 살고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6403, 26214, 14776, '1화에서 나연실(조윤희)은 결혼식 동안 어느 특별한 학창시절을 기억한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6404, 24922, 14776, '나연실(조윤희)은 이 거리에 위치한 덕성여중을 다녔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6405, 29258, 14776, '강태양(현우)은 첫 몇 회에서 세븐일레븐에서 아르바이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6406, 27076, 14776, '이동진(이동건)의 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6407, 29530, 14776, '이동진(이동건)의 전 시댁으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6408, 24932, 19123, '14회에서 정지호(고경표)와 전처 정지은(이주빈)이 이곳에 만나 서로의 행복을 바란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6409, 24934, 19123, '11화 정지호(고경표)와 최상은(박민영)이 찾아온 \'데이트 핫플레이스\'이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6410, 26135, 19123, '강해진(김재영)은 11화에서 스토커를 추적해 이곳 밖에서 붙잡는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6411, 31982, 19123, '1회에서 강해진(김재영)이 이 공원에서 드라마 촬영을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6412, 25871, 19123, '4화에서 정지호(고경표)와 최상은(박민영)은 지호의 상사 부부와 함께 이 식당에서 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6413, 26428, 19123, '1회에서 최상은(박민영)이 이곳에서 가짜 계약결혼을 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6414, 25953, 19123, '1화에서 최상은(박민영)은 주로 고객들과의 첫 만남을 이 호텔 로비 카페에서 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6415, 27090, 19123, '4회에서 정지호(고경표)는 최상은(박민영)에게 직장 동료들과 어울리라 상사에게 받은 피아노 콘서트 티켓을 선물한다. 그리고 둘은 따로 이 곳에 온다. 공연이 끝난 후 그들은 지호의 상사와 마주치게 되고, 두 사람의 관계에 대한 압박에 지호는 갑자기 두 사람이 결혼했다고 토로한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6416, 26293, 19123, '12회에서 정지호(고경표)는 전처이자 변호사인 정지은(이주빈)과 함께 이 카페에서 만나 부탁을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6417, 24905, 19123, '정지호(고경표 분)와 최상은(박민영 분)이 이 공원에 앉아 있는데, 그가 유 마담/유미호(진경 분)와의 관계에 대해 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6418, 26136, 19123, '14회에서 정지호(고경표 분)는 최상은(박민영 분)과 마담 유/유미호(진경 분)를 위해 이 식당에서 저녁 식사를 준비하는데, 이는 유 마담에게 사과할 기회를 주고 두 사람 모두 마음을 터놓고 대화를 나누는 것이다. 이는 지호가 미리 예약한 케이크와 촛불을 불어서 꺼주는 특별한 축하 이벤트로 왕관을 차지한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6419, 26399, 19123, '최상은(박민영)은 14회에서 밤에 이 카페에서 술 취한 정지호(고경표)와 합류해 전 남자친구와 막 만났다는 말에 질투를 느낀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6420, 27074, 19123, '강해진(김재영)의 형 강순진(오룡)은 강해진(김재영)의 사무실에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6421, 29381, 19123, '3화에서 이 대학교 본관은 정원 최상은(박민영)이 이혼 서류를 준비하는 가정법원으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6422, 25017, 19123, '3회에서 쇼핑몰 개발 프로젝트의 일원인 북미 유명 건축가 존 브라운(John D. Michaels)이 이 공항에 도착합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6423, 25948, 16371, '1회에서 보상 행진을 마친 후 지친 위대한(송승헌)은 결국 이 컬럼바리움에 도착해 아버지의 벽감 앞에 쓰러진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6424, 26088, 16371, '1회에서 정수현(이선빈)과 김혜진(박하나)이 일하는 \'CVN\' 방송사 건물로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6425, 25797, 16371, '1화에서 위대한(송승헌)이 돌아가신 아버지를 추모하기 위해 \'애도 행진\'의 일환으로 이 다리의 산책로를 건너 엎드린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6426, 26071, 16371, '1회에서 승헌이 돌아가신 아버지에게 효도하지 않은 아들로 이 동상 근처에서 공개 사과를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6427, 27361, 16371, '1화에서 위대한(송승헌)은 돌아가신 아버지를 추모하기 위해 \'애도 행진\'의 일환으로 이 거리의 횡단보도 건너편에 엎드려 절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6428, 25814, 15464, '13회에서 최수지(문가영)는 사랑에 빠진 박혜정(오하니)과 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6429, 26030, 15464, '11회에서 권시현(우도환)의 아버지, 권석우( 신성우)와 최수지(문가영)의 어머니 명미리(김서형)는 이곳에서 비즈니스 미팅을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6430, 32112, 15464, '10화에서 권시현(우도환)과 은태희(조이)는 이 옷가게에 있는 집을 오가며 서로 스쳐지나간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6431, 25738, 15464, '16화 은태희(조이)는 권시현(우도환)과 함께 건축 공부 숙제를 하러 온다. 그녀가 그림을 그리는 동안, 그는 백일몽을 꾼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6432, 32113, 15464, '13회에서는 술집에서 기절한 후 다음날 아침 여자친구 태희(레드벨벳 조이)가 카드 거절로 치료비를 낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6433, 28488, 15464, '13회에서 현(우도환)은 술에 취한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6434, 25952, 15464, '13화에서 이세주(김민재)와 최수지(문가영)은 권시현(우도환)이 이상하게 행동하고 있다는 유주환(김도완)의 전화를 받고 이 술집에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6435, 26617, 15464, '권시현(우도환) 아버지의 회사 \'JK그룹\'으로 이 회사가 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6436, 26137, 15464, '2-3화에서 2018 명정 재단 행사가 열리는 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6437, 32114, 15464, '1회에서 18회 졸업식이 거행되는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6438, 27335, 15464, '4회 이 정류장에 버스에서 권시현(우도환)이 은태희(레드벨벳 조이)를 성추행한 남자를 내쫓는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6439, 32115, 15464, '2회에서 권시현(우도환)은 \'아버지\'로부터 DNA 결과를 받은 후 밤새 거리를 걷다가 우연히 이 버스 정류장을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6440, 31893, 15464, '15회에서 권석우(신성우)와 설영원(전미선)은 데이트를 위해 이 영화관 앞에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6441, 26214, 15464, '15화에서 세주(김민재)와 수지(문가영)가 함께 골목길을 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6442, 28531, 15464, '15회에서 권석우(신성우)와 설영원(전미선)은 데이트를 위해 이 거리로 조금 걸어가는 모습을 보였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6443, 25956, 15464, '9회 권시현(우도환)은 대학에서 최수지(문가영)와 고경주(정하담)를 데리러 간다. 그리고 두 소녀를 여기로 데려옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6444, 25864, 15464, '이세주(김민재)가 사는 이씨의 저택으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6445, 26125, 15464, '1회에서 졸업식 날 최수지(문가영)는 중국인 입국 심사대를 거쳐 이탈리안 레스토랑 라 스칼라에서 남자친구를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6446, 32123, 14063, '8회에서 친구들과 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6447, 32124, 14063, '강유나(김옥빈)가 사는 거리로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6448, 24992, 16513, '16회 마지막회에서 유령(문근영)은 이 폭포에서 고지석(김선호)에게 프러포즈한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6449, 29509, 16513, '연쇄 살인범의 은신처로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6450, 32125, 16513, '1회부터 형사 고지석(김선호)과 유령(문근영)이 배정받은 \'왕수리역\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6451, 25335, 16513, '1회에서 차를 타고 이 다리를 건너는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6452, 25668, 16513, '13회에서 고지석(김선호)과 김우혁(기도훈)이 유령(문근영)을 따라 이 다리 발치까지 가는 모습이 그려졌다. 왜냐하면 현재 실종된 쌍둥이의 생일이기도 해서 자신의 생일을 축하하는 것에 유령이 속상했기 때문이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6453, 24945, 16513, '16회에서 유령(문근영)은 언니 유인(문근영)을 쫓아 이 거리를 질주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6454, 26319, 16513, '1회에서 지하철 연쇄 살인범의 네 번째 희생자가 이 건물 옥상 화면에 공개된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6455, 32126, 18037, '6회에서 유미(김고은)와 웅(안보현)은 주말 첫 데이로 해변에 가지만 숙박 시설에 대한 의견 불일치로 인해 취소됩니다. 떠나기 전에 그들은 이 식당에서 식사를 한다. 또한 유미는 웅이 과거에 배탈이 났던 개인적인 TMI를 큰 소리로 공유해 다른 식객들을 즐겁게 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6456, 29440, 18037, '6회에서는 유미(김고은)와 웅(안보현)이 이 호텔에 커플로 첫 주말 여행을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6457, 32058, 18037, '7,8회에서 김유미(김고은)는 이 성당에서 친구 결혼식에 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6458, 25607, 18037, '2회에서 고양꽃축제 기간 동안 이 공원의 장미정원 중앙에서 김유미(김고은)는 분수에서 동료 채우기(샤이니 최민호)를 만나기 위해 주선한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6459, 32127, 18037, '4화에서 구웅(안보현)이 서새이(박지현)와 기절한 루이(주종혁)가 가장 좋아하는 \'파전 장소\'에서 만나러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6460, 26171, 18037, '2회김유미(김고은)은 쇼핑몰 입구 분수대에서 직장동료 우기(최민호)의 친구 구웅(안보현)을 소개팅으로 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6461, 32128, 18037, '유미와 구웅이 겨울날 데이트를 했던 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6462, 32129, 18037, '김유미(김고은)가 사는 아파트 단지로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6463, 32130, 18037, '김유미(김고은)와 구웅(안보현)은 5회 영화 보기 전 더위를 식히기 위해 이 카페에 간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6464, 26672, 18037, '7회에서 김유미(김고은)가 가게 밖에서 전 남자친구인 지우기(이상이)와 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6465, 32131, 18037, '8회에서 김유미(김고은)는 조깅을 하던 중 유바비(박진)로 밝혀진 수수께끼의 남자를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6466, 26200, 18037, '2회에서 유미(김고은)는 점심을 먹으러 우기(최민호)를 이 식당에 데려가다가 자신에게 관심이 있다는 사실을 알게 된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6467, 24934, 18037, '3회에서 김유미(김고은)와 소개팅을 하던 중 구웅(안보현)은 근처 현수막을 보고 이 정원에서 열리는 꿀맘 개구리 축제에 가자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6468, 32132, 18037, '유미(김고은)는 4화에서 구웅(안보현)과의 두 번째 데이트에서 눈여겨보던 드레스를 사기 위해 이 부티크에 간다.');
