INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6546, 32173, 13933, '7회에서 성나정(고아라)이 쓰레기(정우)의 도움을 받아 마련한 소개팅 장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6547, 32174, 13933, '응답하라1994 나정과 쓰레기 친구들의 미팅장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6548, 32175, 13933, '1회에서 삼천포(김성균)는 서울 하숙집으로 가는 길에 이 역을 지나 2번 출구(드라마 속 1번 출구가 아님)로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6549, 32176, 13700, '광안고등학교(부산)로 등장한 고등학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6550, 29526, 13700, '13회 성시원(정은지)과 윤윤재(서인국)가 이곳에서 만나 재회한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6551, 32177, 13700, '이 저택은 아이돌 토니의 집으로 등장, 3회에서 성시원(정은지)이 쪼그려 앉는 모습을 보였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6552, 32178, 13700, '이곳에는 부모님 성동일(성동일), 이일화(이일화)와 함께 성시원(정은지)이 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6553, 24930, 15547, '20회에서 한재이(진기주)와 채도진/윤나무(장기용)의 데이트 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6554, 25605, 15547, '12회에서 영채도진(남다름)과 한재이(류한비)는 오래 떨어지지 않겠다고 이 자리에서 약속했다. 그녀는 또한 TV에서 그녀의 경력을 따라 가면서 항상 그녀를 먼저 찾을 수 있도록 그녀가 유명해질 것이라고 그에게 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6555, 26051, 15547, '20회에서 한재이(진기주)가 채도진(장기용)을 데려온 세 번째 데이트 장소다. 그가 높은 곳을 무서워해서 둘이서 타는 것은 회전목마뿐이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6556, 32180, 15547, '20화에서 채도진/윤나무(장기용)가 데이트를 위해 그녀를 데리러 오는 장면에 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6557, 26524, 15547, '6회에서 채도쟌(장기용)의 아버지, 연쇄살인범 윤희재(허준호)가 수감된 감옥이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6558, 25345, 16192, '1화에서 이 공원의 일부가 상하이로 등장하며, 그곳에서 정보원과 함께 김원봉(유지태)이 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6559, 28422, 16192, '1회에서 이 교회 꼭대기에서 김원봉(유지태)과 김남옥(조복래)이 만나 박혁(허지원)이 등장했다. 하지만 남옥은 전 친구를 죽이고 싶은 마음을 참지 못하고 원봉이 인계받아 총을 쏜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6560, 26654, 16192, '이영진(이요원) 부모의 저택이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6561, 32181, 17769, '7~8회에서 몇 번의 연애 시도가 빗나간 권력(이준영)는이 카페의 \'천국의 계단\'으로 이마하(정지소)를 데려오며 놀라움을 안긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6562, 26031, 17769, '1화에서 처음 본 것처럼 이미테이션에서 \'NGO 엔터테인먼트\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6563, 28403, 17769, '권력(이준영)은 6회에서 자신의 역할을 위해 이곳에서 훈련을 받고 있다. 떠오르는 보이그룹 스파클링의 리더 이유진(정윤호)이 극 중 단역으로 훈련에 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6564, 24934, 17769, '7회에서 데이트를 시도하던 중 권력(이준영/유키스 준)이 밤에 이마하(정지소)를 이 공원으로 데려다주었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6565, 26152, 17769, 'Annie (연시우)는 11회에서 매니저의 강요에 의해 박진만 대표(공정환)와 그의 동거회에 합류하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6566, 31785, 17769, '6화에서 이마하(정지소)는 이 다리에서 아침 조깅을 하다가 멈춰서 그녀가 권력(이준영)과 함께 있었던 순간을 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6567, 30842, 17769, '드라마 안에서 활동하는 걸그룹 티파티의 세 멤버의 초창기 숙소로 나오는 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6568, 25943, 17769, '6회에서 한국으로 돌아온 은조(SF9 강찬희)는 죽은 여자친구 애니(연시우)를 추모하기 위해 이 공원을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6569, 27119, 17769, '한국으로 돌아온 은조(SF9 강찬희)는 지나쳐 걸어간다. 최종화 12화에서 이 터널의 남단에 있는 작은 공원으로, 그가 이 공원에서 고인이 된 여자친구 애니(연시우)와 함께 버스커 그룹에 합류했던 순간을 생각하게 만드는 버스킹 연주를 보고있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6570, 32182, 17769, '11화에서 서울로 돌아오자 은조(강찬희/SF9 찬희)가 이 거리로 와서 신촌 플레이버스의 높이를 알아차린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6571, 26137, 17769, '5화 초반 이 호텔에 체크인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6572, 32183, 17769, '1화에서 은조(SF9 강찬희) 없이 처음 SHAX가 공연하는 2017 MML Festival이 이 장소에서 진행된다. 12회에서 그들이 마지막 공연을 하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6573, 27318, 17769, '이 방송국은 방송사 \'KBC\'으로 등장한다. 3회에서 라리마(티아라 박지연)는 이 장소에서 예능 \'아이돌 온에어\'에 출연한 뒤주차장에 있던 자신의 밴을 타고 방송을 회상한다. 4회에서 티파티가 \'뮤직 아레나\'에 출연한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6574, 25724, 17769, '1화에서 애니(연시우)는 이 다리에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6575, 25608, 17769, '12화 마지막 장면에서 권력(이준영) 그리고 이마하(정지소)가 공개 데이트 장소로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6576, 25621, 17769, '애니(연시우)는 11화에서 매니저의 강요에 의해 박진만 대표(공정환)와 동종업계 동료들의 만남을 회상하는데 그 장소가 이 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6577, 25781, 17769, '이유진(에이티즈 정윤호/윤호)은 절친을 초대하고 이마하(정지소)와 걸그룹 두 멤버(그룹 티파티 심현지(임나영)와 유리아(민서/김민서))를 짝사랑한다. 5화에서 이 식당에서 저녁식사를 하며 3년 만에 첫 월급을 받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6578, 32184, 15232, '14화에서 남세희(이민기)가 박준(박준)의 \'변하는 것은 없어\' 구매. 윤지호(정소민)는 조금 이따 찾아와 같은 책을 찾다가 마지막 책이 다 팔렸다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6579, 25640, 15232, '14회에서 남세희(이민지) 아버지가 윤지호(정소민)을 만나자고 한다. 작가라는 직업을 그만두지 말고 주부와 엄마가 되라는 말을 하며 자신과 세희 사이의 중재자 역할을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6580, 26030, 15232, '3회에서 윤지호(정소민)는 황 작가가 이 식당에 데려갔습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6581, 32185, 15232, '16화에서 호랑(김가은 분)과 원석(김민석 분)은 눈물의 재회와 호랑과 원석의 청혼 이후 함께 앞으로의 삶을 다시 계획하기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6582, 25600, 15232, '호랑(김가은)은 이 쇼핑복합몰의 레스토랑에서 일을 합니다. 4회에서 지호(정소민)는 계속 서울에 머물기로 결정하고 식당에 나타나 호랑을 놀라게 합니다. 그런 다음 지상의 벤치에서 대화를 나눕니다. 이 곳은 1, 2, 13, 14, 15화의 다른 장면에서도 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6583, 32079, 15232, '1회 버스에서 내리기 전 윤지호(정소민)가 남세희(이민기)에게 즉흥 키스를 했던 버스 정류장이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6584, 32186, 15232, '13회, 14회에서 이 교회 앞에서 남세희(이민기)는 곧 지호의 상사가 될 전 여자친구 고정민(이청아)을 12년 전 헤어진 후 처음으로 다시 만난다. 지호(정소민)가 모든 상황을 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6585, 32187, 15232, '15회 회상 장면에서 호랑(김가은)은 원석(김민석)에게 화를 낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6586, 32188, 15232, '양호랑(김가은)이 드림 소파를 판매하는 가구점이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6587, 25908, 15232, '1회에서 이곳에 있는 수희(윤소희)가 남편 오스카(윤두준)에게 이혼을 요구하는 장면이 나온다. 그가 그녀의 손목을 잡고 그녀 쪽으로 몸을 기울인다. 그러나 아내에게 키스하는 대신, 그는 책장에 있는 홍삼 한 봉지를 얻기 위해 손을 뻗는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6588, 26452, 15232, '10, 12회에서 이 곳은 우수지(이솜)와 마상구(박병은)는 처음 인연을 맺은 첫 만남의 장소로 이 호텔 304호에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6589, 32189, 15232, '1화에서 윤지호(정소민)와 남세희(이민기)와 결혼(2화 시작, 9화 회상). 9화 회상에서 알 수 있듯이, 연복남(김민구)은 결혼식에서 알바를 하며 웨딩 사진을 제안하기도 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6590, 25614, 15232, '2회 \'서울음대‘의 입구로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6591, 25657, 15232, '13회 플래시백 장면에서 윤지호(정소민)와 우수지(이솜)가 이 대학의 호수[붕어방]에서 \'열아홉 번째 방으로\'에 대해 이야기하는 것을 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6592, 32190, 15232, '16화에서 호랑(김가은)이 혼인신고를 하고 있다는 사실을 알게 된 원석(김민석)은 이 카페로 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6593, 26540, 15232, '16회에서 마상구(박병은)와 김과장은 건물 밖에서대화를 나눈다. 이후, 수지(이솜)는 그녀의 전 매니저인 김상구를 깜짝 놀라게 하기 위해 오토바이를 타고 상구를 태워준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6594, 28090, 15232, '16회 마지막 장면에서 역으로 등장했습니다. 윤지호(정소민), 남세희(김민기), 우수지(이솜), 마상구(박병은), 심원석(김민석), 양호랑(김가은)은 함께 버스를 타러 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6595, 25661, 15232, '12회에서 윤지호(정소민)와 남세희(이민기)는 데이트를 위해 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6596, 25662, 15232, '14회에서는 용석 PD와 남세희(이민기)가 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6597, 26263, 15232, '12회에서 윤지호(정소민)과 남세희(이민기)가 데이트 장소로 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6598, 25795, 15232, '심원석(김민석)이 윤보미에게 16회에서 아직 양호랑(김가은)에게 너무 애착이 있어 청혼을 받아들일 수 없다고 말하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6599, 24905, 15232, '우수지(이솜)가 그에게 관심을 보였을 때 설렘을 느끼며 마상구(박병은)가 걷는다. 8회에서는 이 공원에서 만나는 모든 사람에게 인사를 하고 지나가는 행인과 함께 춤을 추는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6600, 32191, 15232, '6회에서는 윤지호(정소민)와 남세희(김민기)의 결혼식에 참석한 후 우수지(이솜)와 양호랑(김가은)이 찾아왔다. 술을 마시며 이야기를 나누던 상구는 원석이 호랑을 위해 사준 소파를 꺼내며 호랑에게 화를 냈다고 한다. 그녀는 원석을 뒤에 두고 분노와 실망으로 식당을 뛰쳐나와 결국 결혼하고 싶다는 것을 알리고 그가 그녀에게 프러포즈하기를 원한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6601, 32056, 15232, '2화에서 윤지호(정소민)가 이 가게를 지나간다. 지호는 동료 계 감독(김욱)이 자신을 강요하자 충격을 받았다. 그녀는 길을 멈추고 가게 창에 비친 자신의 모습을 힐끗 쳐다보며 누구에게 전화를 걸어 이야기를 해야 할지 고민했습니다. 자하문터널에 들어가 한참을 걸어 내려가기 전입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6602, 25666, 15232, '이번 생은 처음이라에서 YOLO 카페로 알려졌던 곳이다. 연복남(김민구)과 그의 아버지가 운영하는 카페는 6회에서 지호(정소민)가 취직한 곳이었다. 8회에서 세희(이민기)가 복남의 값비싼 오토바이에 떨어지지 않으려고 백플립을 한 곳이기도 하다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6603, 26178, 15232, '윤지호(정소민)와 그녀의 좋은 친구 우수지(이솜)는 수지가 지호가 계약결혼을 했다는 사실을 알게 된 후 이곳에서 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6604, 27530, 15232, '1회에서 남세희(이민기)와 동료들이 퇴근 후 술 한 잔 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6605, 25668, 15232, '13회에서 이곳은 수지(이솜)와 마상구(박병은)가 서로의 오해를 풀기 위해 찾은 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6606, 24948, 15232, '7회에서 윤지호(정소민)의 직장 동료 연복남(김민구)은 퇴근 후 오토바이를 타고 윤지호를 데려온다. 그는 그녀에게 아름다운 경치를 보여주고 싶어하지만 그녀는 분명히 그에게 그녀가 그에게 감정이 없다고 말해요. 다음 날 저녁(8화), 그들은 이곳으로 돌아오고 본남이가 나쁜 의도를 가지고 있다고 생각하는 남세희(이민기)가 안전한 지호를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6607, 29181, 15232, '12화에서 세희(김민기)와 지호(정소민)가 경의선숲길공원 데이트를 마치고 찾아온 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6608, 27304, 15232, '9회에서 남세희(이민기)가 한 번은 윤지호(정소민)를 구하기 위해 이 거리에서 낙산공원으로 달려갈 때, 그리고 나서 낙산공원에서 아내와 손잡고 오는 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6609, 26465, 15232, '2회에서 계 감독(김욱)이 술에 취해 그녀를 강제하려 하자 낙심한 지호(정소민)가 임시숙소에서 탈출한 뒤 한밤중에 이 터널로 들어가 사용하는 계단과 홀로 걸어가는 계단.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6610, 26523, 15232, '16회에서 남세희(이민기)의 아파트를 나온 후 윤지호(정소민)가 묵는 게스트하우스로 등장한 이 집.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6611, 25799, 15232, '호랑(김가은)과 원석(김민석)의 옥상 집입니다. 둘이 헤어진 후 호랑은 집을 나가고 원석은 한동안 그 집에 혼자 있게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6612, 27001, 15232, '양호랑(김가은)이 심원석(김민석)과 살던 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6613, 32192, 15232, '11화에서 남세희(이민기)가 가족과 함께 김치를 담그는 동안 윤지호(정소민)는 그를 위해 약을 사러 마을로 들어간다. 정현종의 시집 \'섬\' 중 \'손님\'이라는 시를 읽으며 잠시 이 방파제에 멈춰 앉았다. 그녀를 찾던 세희는 여기에서 그녀를 발견하고 대화를 나누다가 판교 세븐벤처밸리 버스정류장에서 있었던 \'키스\'가 진짜가 아니라고 말한다. 12화에서 세희는 전화 한 통에 방해를 받을 때까지 그녀에게 진짜 키스가 무엇인지 보여줍니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6614, 25773, 17975, '1회에서 하송이(방민아)는 제주행 비행기로 다른 경연대회 우승자들과 함께 이 공항에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6615, 25866, 17975, '1화에서 다른 대회 우승자들과 함께 하송이(방민아)가 이 공항으로 날아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6616, 25910, 15552, '한상진(이성재)와 정수철(정웅인)은 이 식당에서 술을 마시고 진솔한 이야기를 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6617, 25611, 18908, '리얀 유치원으로 등장합니다. 1회에서 이라엘(서예지)이 등교 첫날 딸 보람(김시우)을 보내는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6618, 24900, 18908, '1화에서 여기서 열리는 북콘서트에서 서은평(이상엽)은 책을 펼친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6619, 25345, 18908, '1화에서 서은평(이상엽 분)이 대통령과 직접 통화를 하기 위해 청와대를 찾아오는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6620, 25953, 18908, '1회에서 리얀유치원 입학식 애프터파티가 여기서 진행됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6621, 26651, 18908, '1회에서 총리 한판로(전국환)의 집 외관으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6622, 25017, 18908, '1회에서 2009년으로 돌아가 서은평(이상엽)은 공항에서 미국행 비행기를 기다리던 어린 이라엘(김지안)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6623, 32127, 18899, '동그라미와 우영우가 만나는 레트로 감성 주점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6624, 26256, 18899, '13회에서 한바다 변호사팀이 대수도원장 피고인의 초청으로 이 절을 찾았다. 기독교 신자인 권민우(주종혁)는 혹독한 158배 기도를 건너뛰고 다른 팀원들은 의식을 따른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6625, 32193, 18899, '소덕동 팽나무가 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6626, 25796, 18899, '태수미(진경 분)가 이 건물 앞에서 기자회견을 열고 최종회에서 장관 후보직에서 물러나 엄마 노릇에 전념하겠다고 선언한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6627, 25909, 18899, '10회에서 권민우(주종혁)는 태수미가 동료 우영우(박은빈)와 관련이 있다는 직감을 확인하기 위해 태수미(진경)와 김선배를 이 식당에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6628, 27074, 18899, '2회에서 이준호(강태오)와 함께 웨딩드레스 사건을 조사하면서, 변호인 최수연(하윤경)은 여자화장실을 이용하기 위해 이 호텔에 들른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6629, 24944, 18899, '11화에서 수연(하윤경)은 맞선 상대인 종권(정욱진)과 이 식당에서 저녁 데이트를 한다. 전 여자친구(정가희)가 화장실에 다녀오겠다고 양해를 구한 뒤 나타나 수연에게 자신의 진짜 성격과 동기를 공개한다. 화가 난 수연은 가장 비싼 술을 들고 식당을 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6630, 24913, 18899, '제10화 저녁 데이트에서 우영우(박은빈)와 이준호(강태오)가 이 골목길을 걷다가 그에게 "덕수궁 돌담길을 함께 걷는 연인들이 헤어질 거라는 말 들어봤어 과거 서울가정법원과 대법원은 덕수궁 돌담길 북쪽 끝에 있었다. 이 말은 이혼을 하기 위해 이 길을 따라 내려가야 했던 데서 유래되었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6631, 32194, 18899, '4화에서 영우와 준호가 노을 보러간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6632, 32195, 18899, '4회 낙조마을 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6633, 32203, 13785, '4회에서 금엔리케(윤시윤)와 고독미(박신혜)가 해변 데이트를 하는 곳이다. 엔리케는 거대한 모래성을 쌓고 독미는 바다를 바라보기만 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6634, 25827, 13785, '2회에서 무학은 김달리(박규영)가 자신을 속였다는 확신을 점점 더 갖게 되면서 그녀를 찾아 다시 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6635, 32204, 13785, '금엔리케(윤시윤)가 묵고 있는 한태준(김정산)의 아파트');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6636, 32205, 13785, '고독미(박신혜)와 오진록(김지훈)이 사는 \'바다마을\'이라고 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6637, 25641, 16697, '6회에서 장근원(안보현)은 씨푸드 서정인(정유민)과 이 식당에서 소개팅을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6638, 26030, 16697, '14회에서 강민정(김혜은)과 조이서(김다미)는 이곳에서 투자자를 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6639, 25607, 16697, '오수아(권나라)는 방과 후 이 편의점에서 일하고 있으며, 박새로이(박서준)의 아버지 박성열(손현주)이 직장을 그만둔 후 1화에서 자신을 찾아가는 곳이다. 그는 그럼에도 불구하고 그녀에게 대학 학비를 계속 지원할 것이라는 것을 알려주고 오수아는 그에게 세 번 보답하겠다고 약속한다. 최종회 16회 에필로그에서는 조각 \'휴먼타임스\' 바로 뒤 호수에서 일몰을 보며 데이트를 하고 키스를 하는 박세로와 조이서(김다미)의 모습이 그려진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6640, 32206, 16697, '15회에서 병원에서 혼수상태에 빠진 박새로이(박서준)가 공원의 나무다리에서 일종의 백일몽으로 아버지 박성열(손현주)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6641, 32079, 16697, '5화에서 퇴근 후 조이서(김다미), 장근수(김동희), 최승권(류경수)이 이곳에서 버스를 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6642, 26278, 16697, '1회에서 아침에 박새로이(박서준)가 있는 동안 대학 수시 면접을 위해 달려가던 오수아(권나라)가 이 거리를 따라 조깅한다. 그녀는 버스에 타면서 집에서 아이디를 잊어버리고 달리는 수밖에 없었다는 것을 알아차렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6643, 32207, 16697, '박새로이 아버지 교통사고 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6644, 32208, 16697, '박새로이 아버지가 근무했던 장가 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6645, 26537, 16697, '조이서와 장근원이 만난 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6646, 25831, 16697, '드라마 주인공중 하나인 조이서(김다미)가 다녔던 학교로 드라마 안에서 \'용각고등학교\'로 설정되어있다. 3화 참조');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6647, 24902, 16697, '이 식당은 오수아(권나라)가 장가집을 그만두고 운영하는 식당 \'퀸즈팔레\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6648, 26085, 16697, '15회에 이 병원에서 깨어난 박새로이(박서준 분)는 조이서(김다미 분)를 구하기 위해 최승권(류경수 분)의 차에 오른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6649, 28442, 16697, '1화에서 이 장소의 외관은 장가펍 대표 장대희(유재명)의 자택으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6650, 25924, 16697, '이 대학은 박새로이(박서준)가 밖에서 기다리는 동안 오수아(권나라)가 법학부 건물에서 입학시험을 치르는 \'광진대학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6651, 25659, 16697, '장근원(안보현)은 아버지의 비서 김씨(홍서준)가 이태원 클래스10회에서 형을 선고받기 위해 자신을 법정으로 몰고 가는 것을 이해하고 정류장 신호등에서 황급히 차에서 내려 이 광장을 가로질러 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6652, 32209, 16697, '장가포차\'의 이태원점으로 등장한다. 6회에서는 오수아(권나라)가 길에서 박새로이(박서준)에게 다가간 후 그곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6653, 25662, 16697, '조이서(김다미)와 오수아(권나라) 이 카페의 겨울 정원에서 박새로이(박서준)에 대해 진지한 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6654, 32210, 16697, '퇴직한 아빠와 새로이가 함께 준비했던 포차');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6655, 26136, 16697, '9회에서 회장 장대희(유재명)가 조이서(김다미)를 이 식당에 저녁식사에 초대하고 장가에 들어가자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6656, 25666, 16697, '4회에서 박세로(박서준), 조이서(김다미), 오수아(권나라)가 이 카페에서 단밤 홍보에 대해 논의하지만, 사실 관계자 모두에게 불편하고 긴장된 상황이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6657, 29487, 16697, '6회에서 박새로이(박서준)가 조이서(김다미)와 통화를 하고 있는 동안, 호곡에서 오수아(권나라)가 그에게 접근한다. 그리고 \'최고의 선술집\'에 자신의 선술집이 소개될 거라고 그녀에게 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6658, 26131, 16697, '1회에서 박새로이(박서준)와 아버지 박성열(손현주)은 새로이가 학교를 그만두고 아버지가 직장을 그만둔 뒤 이 식당을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6659, 25806, 16697, '이서가 고민이 담긴 동전을 던진 휴식 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6660, 26096, 16697, '이 버스 정류장에서 박새로이(박서준)가 오수아(권나라)를 쫓는다. 6회에서 권나라는 조이서(김다미 분)에게 자신의 술집을 경찰에 신고한 사람이 수아가 아니라는 말을 들은 후 모습을 보였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6661, 26039, 16697, '7회에서는 박새로이(박서준)와 그의 오른팔 이호진(데이비드 이)이 강민정(김혜은)과 함께 캠프에 합류하기 위해 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6662, 25909, 16697, '6회에서 장근원(안보현)은 아버지 장대희(유재명)와 이 식당에서 저녁을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6663, 27372, 16697, '2회부터 사람들이 정기적으로 이 고가를 건넜다. 2회에서 오수아(권나라)는 이태원으로 향하는 길을 걷는다. 3화에서 조이서(김다미)와 장근수(김동희)도 스쿠터를 타고 등장합니다. 그리고 4화 초반에 박새로이(박서준)가 등장한다. 6회에서는 새로이와 이서가 다리에서 우연히 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6664, 26019, 16697, '3회에서 조이서(김다미)와 장근수(김동희)가 스쿠터를 타고 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6665, 32211, 16697, '9회에서 박새로이(박서준)의 두 번째 식당과 같은 거리에 위치하도록 만들어진 \'마루당 식당\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6666, 32212, 16697, '9회에서 박새로이(박서준)의 두 번째 식당과 같은 거리에 위치한 것으로 밝혀진 \'조조의 집\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6667, 26181, 16697, '2화에서 박새로이(박서준)가 집까지 걸어가자고 하자 그녀가 하룻밤 묵어 가자고 했지만 거절당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6668, 32213, 16697, '조이서가 다니던 독서실');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6669, 24907, 16697, '10화에서 이 계단에서 장근수(김동희)는 조이서(김다미)와 장가의 차기 대표가 되는 사이에서 고군분투한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6670, 25884, 16697, '8화에서 박새로이(박서준)가 \'단밤\'이라는 이름으로 새롭게 오픈한 식당으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6671, 32214, 16697, '이태원클라스의 메인 배경');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6672, 25994, 16697, '박새로이가 오수아를 우연히 만나는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6673, 32215, 16697, '4화에서 조이서(김다미)를 구하고 달려온 박새로이(박서준), 이서와 오수아(권나라)는 이 놀이터에서 숨을 고른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6674, 26251, 16697, '2회에서 박새로이(박서준)가 이 역에서 내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6675, 32216, 16697, '단밤포차\'의 실사판인 꿀밤포차');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6676, 32217, 16697, '16회에서 조이서(김다미)와 박새로이(박서준)가 데이트를 위해 이 건물을 지나고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6677, 25910, 16697, '12회에서 박새로이(박서준)와 오수아(권나라)가 이 식당을 찾아왔다. 한 잔의 술을 마시고 그녀는 그가 전날 저녁 그녀를 차버렸다고 말하지만 그는 그녀의 말을 이해하지 못했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6678, 32218, 16697, '스무 살 이서가 새로이에게 첫 입맞춤을 한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6679, 32219, 16697, '4회 말미, 박새로이(박서준)와 조이서(김다미)가 술을 마시러 이 술집을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6680, 26186, 16697, '7화에서 박새로이(박서준)와 오수아(권나라)가 우연히 마주치고 이곳을 산책한다. 새로이가 수아를 좋아하는 이유를 말하는 것은 바로 이 순간이다. 8회에서 새로이는 강민정(김혜은 분)을 만나 사업 이야기를 나누며 오수아를 보기 위해 세 번이나 다시 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6681, 31952, 16697, '이태원클라쓰 남산 성곽길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6682, 25913, 16697, '강민정(김혜은)이 장가그룹 장대희(유재명) 사장을 축출하기 위해 10회에서 이곳에서 이사회 지지자들과 간담회를 연다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6683, 26140, 16697, '11회에서 장근수(김동희)의 부탁으로 근처 술집에서 술자리 중 조이서(김다미)를 좋아한 적이 있다면, 박새로이 (박서준)은 이를 부인하자 이서가 자리를 털고 나간다. 새로이가 그녀를 따라가자 마음을 고백해선 안 된다는 말만 듣게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6684, 24915, 16697, '장대희(유재명) 사장이 이 호텔 이스트타워 더볼룸에서 기자회견을 열고 10회에서 아들 장근원(안보현)을 규탄하고 버리고 마침내 장가그룹의 지배력을 유지하기 위해 연단에 무릎을 꿇고 눈물을 흘리며 사과한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6685, 32220, 16697, '12회에서 조이서(김다미)가 마현이(이주영)가 트랜스젠더로 언론에 나왔다는 사실을 알게 된 곳은 바로 이 카페 테라스.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6686, 25866, 16697, '12회 엔딩 크레딧에서 조이서(김다미)가 공항으로 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6687, 26030, 15304, '7화에서 도한준(동하)의 부모, 명성희(김해숙)와 도진명(이덕화)이 저녁 식사를 위해 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6688, 26521, 15304, '드라마 중 3화에서 이곳은 \'서울 시민 병원\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6689, 28436, 15304, '1회에서 이정주(박은빈)는 출근길에 10번 출구에서 내린다. 그녀의 서류가방이 도난당하고(도둑이 신발을 던지는 것을 막으려는 시도 포함) 모든 것을 사의현(연우진)이 출근길에 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6690, 25796, 15304, '이정주(박은빈)와 사의현(연우진)이 재판장을 맡는다. 그리고 도한준(동하)의 직장이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6691, 32221, 15304, '7회에서 이정주(박은빈)는 도한준(동하)에게 점심을 먹으러 가자고 한다. 장소. 하지만 정주가 한 가지 사건에 대한 질문을 멈추지 않자 한준은 화를 내며 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6692, 25809, 15304, '4회에서 본 것처럼 이정주(박은빈)의 동생 최경호(지승현)는 이 곳에 잘못 수감되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6693, 26030, 16886, '8회에서 배규리(박주현)가 \'삼촌\' 류대열(임기홍)을 만나러 이 식당에 오지만 오지수(김동희)가 왔다는 사실을 알게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6694, 32146, 16886, '마지막 10화에서 조진우 선생님(박혁권)은 형사 이해경(김여진)에게 이 버스 정류장에서 내려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6695, 32230, 16886, '오지수(김동희)가 자주 가는 지하철역입니다. 4화에서 그가 전화로 거래를 할 때 배규리(박주현)가 어깨 너머로 쳐다보는 장면이 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6696, 25661, 16886, '2화에서 오지수(김동희)는 다른 전화기를 추적하면서 이 쇼핑 지역의 인파를 헤치고 메인을 향해 나아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6697, 27237, 16886, '1회에서 오지수(김동희)와 배규리(박주현)가 만나 이야기를 나눴다. 지수는 서민희(정다빈)가 \'삼촌\'에게 도움을 청하자 문제가 발생하고, 결국 뒷문으로 나가고 규리는 카페에 홀로 남겨진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6698, 32231, 16886, '오지수(김동희)가 묵는 아파트 단지입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6699, 32232, 16886, '2화에서 배규리(박주현)는 오지수(김동희)에게 연락한다. 이 엘리베이터 위에서 그의 또 다른 전화기, 그리고 바로 아래에서 자신의 밴을 몰고 있던 이왕철(최민수)에게 발견된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6700, 25797, 16886, '1회 말미 오지수(김동희)가 타는 기차가 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6701, 25633, 16886, '10회에서 오지수(김동희)는 이 계단에서 서민희(정다빈)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6702, 25609, 16886, '나성미(서예화)는 이왕철(최민수)을 만난다. 그녀는 왕철이 소녀들을 위해 독립적으로 일하도록 설득하는 방법으로 왕철에게 돈을 주려 하지만 후자는 거부한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6703, 25610, 16886, '5회에서 첫 손님을 만나러 가기를 망설이는 공원. 오지수(김동희)는 전화로 그를 설득하려 하고 배규리(박주현)는 옆에서 듣고 있다. 6화에서는 이왕철/삼촌(최민수)이 라이벌 갱단과 치명적인 싸움을 하는 모습이 그려지고, 지수와 서민희(정다빈)는 멀찍이서 이를 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6704, 26045, 16886, '3화에서 배규리(박주현)에게 휴대폰을 되찾은 뒤, 오지수(김동희)가 이왕철(최민수)과 접촉하는 동안 이왕철(최민수)은 이 위치 외곽에 주차되어 있었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6705, 29259, 16886, '마지막 10화에서 오지수(김동희)가 의식을 잃은 서민희(정다빈)를 이 동굴로 끌고 가는 꿈 시퀀스로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6706, 26152, 18043, '1회에서 좋은 친구 민정(손나은)에게 서기를 부탁하는 강재(류준열). 이 식당에서 의뢰인과 지인과의 만남에서 고객의 얼굴을 보호하기 위해 여자친구로 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6707, 26567, 18043, '16화에서 강재(류준열)와 그의 절친 탁(유수빈)는 카페에 온다. 강재는 탁을 당황케 하기 위해 엄청나게 복잡한 명령을 내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6708, 26267, 18043, '이 단지의 외관은 1화에서 부정(전도연)이 청소업체에 근무하는 호텔 \'왕궁\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6709, 26039, 18043, '정수(박병은)와 경은(김효진)은 7화에서 이 호텔에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6710, 32233, 18043, '1화에서 부정(전도연)이 아버지를 만나러 석우빌딩[석우빌딩]에 위치한 CU 용산역점을 지나고 있다. 이후, 신용산 지하도를 걷고 있는 강재(류준열)가 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6711, 31724, 18043, '1화에서 아버지와 함께 부여(전도연)는 버스를 타고 집으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6712, 32234, 18043, '16회에서 강재(류준열)는 故 정정우(나현우)의 집을 방문한 뒤 이 공간 앞에서 택시를 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6713, 32235, 18043, '16회에서 이강재(류준열)는 옛 친구 故 정정우(나현우)의 소지품을 가족들에게 넘기기 위해 이 집에 찾아온다. 그가 떠나자 정우의 어머니는 정우에게 아들이 어떻게 살았는지에 대해 자세히 말해달라고 요청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6714, 26627, 16330, '2회에서 강다미(민도희)와 친구들(려운, 진예주)은 학교를 빼먹고 이 대학을 방문하기 위해 서울로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6715, 32236, 16330, '4회에서는 강다미(민도희)와 그녀의 친구 윤성현(려운), 이하림(진예주)이 대학 입학 이력서를 쓰기 위해 카페에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6716, 25347, 16330, '2회에서 마포대교가 내려다보이는 이 공원의 멀티플라자에서 한강을 따라 모험을 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6717, 32253, 13656, '15회에서 김붕도(지현우)와 최희진(유인나)이 이 식당에 밥을 먹으러 오는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6718, 32254, 13656, '엘리베이터 키스로 유명해진 도서관이다. 4회에서 김붕도(지현우)와 최희진(유인나)이 자신의 시대를 읽기 위해 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6719, 26067, 13656, '최의진(유인나)이 김붕도(지현우)를 처음 만나는 장면을 비롯한 대부분의 장면을 촬영했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6720, 25737, 13656, '11회에서는 최희진(유인나)이 멍하니 페라가모를 지나치는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6721, 25773, 13656, '7회에서 김붕도(지현우), 최희진(유인나), 한동민(김진우)이 모두 제주에서 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6722, 26088, 13656, '7~9회처럼 최희진(유인나)이 드라마 촬영 중간에 기다리는 장면.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6723, 32255, 13656, '5회에서 최희진(유인나)은 매니저 조수경(가득희)과 함께 카페에 앉아 있다. 휴대폰으로 김붕도(지현우)에게 걸려온 전화. 수경은 처음에 전화를 받지 말라고 경고했지만 희진은 결국 전화를 받고 그가 제주도에 있다는 것을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6724, 32256, 13656, '1회에서 최희진(유인나)은 오디션에 한 시간 늦었다고 생각하며 이 건물로 달려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6725, 25887, 13656, '7회에서 김붕도(지현우)가 한옥 밖에서 검을 든 모습이 보인다. 그가 한때 태조가 소유했던 가문의 가보인 칼을 골동품 상인에게 팔았다는 것이 나중에 밝혀졌습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6726, 25945, 13656, '김붕도(지현우)는 광화문 중앙문을 나와 세종대로를 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6727, 26071, 13656, '1, 13회에서 봤듯이 김붕도(지현우)는 조선시대 시간여행을 하다 이곳에 몇 번이나 상륙한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6728, 25798, 13656, '앞으로 부상당한 김붕도(지현우 분)가 다시 등장하는13회에서 6번 출구와 7번 출구가 모두 배경으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6729, 24922, 13656, '이 거리는 3화에서 북촌 한옥마을에 등장한 김붕도(지현우)의 말에 대한 뉴스 보도에 잠깐 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6730, 27092, 13656, '9회에서 회상 속에서 한동민(김진우)은 이 거리에 위치한 당시 엠플라자(현 포에버 21/6ixty8ight) 앞에서 자신의 여자친구인 최희진(유인나)을 공개적으로 드러낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6731, 26457, 13656, '6회에서 희진(유인나)과 그녀의 친구이자 매니저인 조수경(가득희)은 제주도에서 김붕도(지현우)를 만난 후 이곳으로 왔다. 여기서 차를 마시고 수경은 붕도가 안전한 남자친구인지 확인하기 위해 붕도의 신분을 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6732, 25866, 13656, '6회에서 김붕도(지현우)가 공항 공중전화 부스에서 최희진(유인나)에게 전화를 걸고 있다. 신분증도 없고 비행기표를 살 돈도 없어 제주를 떠날 수 없다. 같은 날 희진은 자신과 매니저 조수경(가득희)의 표를 사고 제주에서 붕도를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6733, 25958, 15855, '8회에서 장순결(윤균상)은 다니엘 최(송재림)를 만나러간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6734, 24900, 15855, '11화에서 김혜원 앵커(나해령) 장순결(윤균상)이 초청된 사인회를 진행한다. 행사에 늦게 와서 그는 다른 사람을 좋아한다고 그녀에게명히 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6735, 27208, 15855, '1회에서 장선결(윤균상)과 앵커 김혜원(나해령)이 이곳에서 중매모임을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6736, 28442, 15855, '15화에서 최하인(송재림)은 이 장소에서 그의 점쟁이 고객 왕 보살(최유송)을 만납니다. 그리고 길오솔(김유정)에 대한 상반된 감정을 그녀와 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6737, 31925, 15855, '최종 16회에서 길오솔(김유정)이 그녀의 직업을 위해 약속을 잡고 숙소를 떠나는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6738, 26282, 15855, '이 대학은 \'명원대학교\'로 등장하며, 7회에서 그곳에서 최다니엘(송재림)이 그랜드피스에서 강연을 한다. 길오솔(김유정 분)과 그녀의 절친 민주연(민도희 분)이 참석한다. 장순결(윤균상)도 오솔에게 자신의 마음을 고백하기 위해 찾아와 다니엘이 그 앞에서 어떻게 행동하는지를 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6739, 25791, 15855, '이 건물은 \'JB 엔터테인먼트\'로 나온다. 1화에서 길오솔(김유정)이 방송을 위해 첫사랑 이도진(최웅)을 도와주고 순결(윤균상)은 업무상 미팅을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6740, 25795, 15855, '2회에서 면접 후 또 한 번 거절당한 후 좌절감에 이 공원 계단에 앉았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6741, 24941, 15855, '2회에서 길오솔(김유정)은 동생 길오돌(이도현)과 절친 전영식을 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6742, 32221, 15855, '1회에서 민주연(민도희)은 절친 길오솔(김유정)을 대학 동아리 모임에 끌고 간다. 오솔의 첫사랑 이도진(최웅)도 함께하고 그녀와 이야기할 때 그녀는 달 위에 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6743, 26002, 15855, '최종회 16회에서 길오솔(김유정)과 최건(송재림)은 아이스스케이트를 타러 이곳에 온다.그것이 최건의 작별 인사 방식이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6744, 25839, 15855, '12회에서 길오솔(김유정)의 전 청소팀이 일하러 왔다. 장순결(윤균상)은 자신의 작품을 살펴보러 오던 중 오솔이 TV 속 수중 세계를 보고 얼마나 놀랐는지 기억하고 그녀를 깜짝 초대하고 전 동료에게 거의 밝혀질 뻔한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6745, 27335, 15855, '8회에서 다니엘 최(송재림)와 길오솔(김유정)이 집으로 돌아가는 버스가 이 버스정류장을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6746, 25776, 15855, '길오솔(김유정)은 11화에서 옛 청소부 동료들과 만나 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6747, 32257, 15855, '길공태 그는 2화에서 술을 마시러 왔다가 9화에서 두 자녀와 함께 이곳으로 돌아옵니다. 마지막 16화에서 그와 장순결(윤균상)은 여기에서 하트 투 하트 이야기를 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6748, 25910, 15855, '마지막 16회에서 길오솔(김유정)이 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6749, 24951, 15855, '마지막 16회에서 열정을 가득 채운 술에 취해 마음이 상한 길오솔(김유정)과 장순결(윤균상)이 2년 간의 미국 생활을 마치고 이곳에 온다. 그리고 그가 그녀의 어깨에 기대고 잠든 사이 그녀는 그에게 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6750, 26215, 15855, '2화부터 장순결(윤균상)의 청소부 \'청소요정\' 본부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6751, 32258, 15855, '15화에서 본 저택으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6752, 24952, 15855, '마지막 16회에서 길오솔(김유정)과 장순결(윤균상)은 2년간의 미국 체류를 마치고 이곳에 와서 오랜만에 그녀를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6753, 25912, 15855, '9회에 길오솔(김유정), 장순결(윤균상)이 등장한다. 두 사람은 서로에 대한 사랑을 간접적으로 고백한다. "당신은 사랑의 꿈을 꾸나요" 그녀가 이 스카이가든에 있는 간판에서 이 질문을 읽고 그에게 묻고 그는 조금 늦게 대답한다 "나는 당신을 영원히 영원히 사랑합니다."');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6754, 25016, 15855, '6회에서 김오솔(김유정)이 청소 일을 하러 왔습니다. 장순결(윤균상)은 그 회사성추행블랙리스트 소식을 듣자마자 그녀를 구하기 위해 달려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6755, 25957, 15855, '5회에서 길오솔(김유정)은 청소팀과 함께 청소를 하러 왔다. 장순결(윤균상)과 그의 비서 권(유선)이 청소를 확인하러 왔을 때, 오솔은 자신이 서 있던 사다리에서 떨어져 순결의 팔에 직접 닿는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6756, 26682, 15855, '회사 회식 후 만취한 길오솔(김유정)을 차를 몰고 집까지 데려다 줄 때 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6757, 32259, 16377, '한국의 우유니 사막 / 예능 <일로 만난 사이> 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6758, 27080, 13121, '조선 시대 건축 양식을 보존하고 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6759, 25680, 13121, '극중에서 전염병이 퍼진 마을을 촬영한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6760, 26602, 13121, '사극 드라마 일지매의 주된 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6761, 26486, 13121, '고구려의 기운을 느낄 수 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6762, 25618, 13121, '이준기가 의적 일지매가 되는 장면을 촬영한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6763, 26487, 13121, '1화에서 어린 일지매/용이는 가난하고 교육받지 못한 아들에게 사과하는 아버지 쇠돌(이문식)과 함께 이곳에 온다. 같은 회에서 용이를 데려가고, 적대감을 느낀 아버지가 그를 배웅하기 위해 이 나무 뒤에 숨는 장면이기도 하다. 4회에서는 어른 일지매/용이가 이곳에 돌아와 기억을 되찾는다. 마지막 에피소드 86의 마지막 장면에서도 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6764, 32273, 13329, '자이언트에서 결혼식 장면을 찍은 성당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6765, 25605, 13329, 'SBS 드라마 자이언트 촬영장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6766, 32274, 13329, '드라마 자이언트의 배경이 된 남해 마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6767, 32275, 13329, '드라마 자이언트의 배경이 된 마을');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6768, 32276, 13329, '드라마 자이언트의 배경이 된 등대');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6769, 25345, 13329, '드라마 자이언트 촬영지로 알려진 테마파크');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6770, 27243, 13329, '1화에서 그의 몰락 이후 조필연(정보석)은 달리 결정하고 복수를 선택할 때까지 이 건물 옥상에서의 살았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6771, 25773, 13329, '마지막 60회에서 이미주(황정음)가 이곳을 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6772, 27120, 13329, '최종화 60화에서 조민우(주상욱)가 2년 만에 출소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6773, 26061, 13329, '드라마 자이언트의 배경이 된 촬영장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6774, 25988, 13329, '자이언트 속 추격신의 배경이 된 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6775, 25607, 14999, '11회에서 은호원(고아성)과 서우진(하석진)이 데이트를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6776, 25792, 14999, '5회에서 박상만(권해효)은 셰프 서현(김동욱)을 만나 동료들에 대해 불평한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6777, 25797, 14999, '1회에서 선호원(고아성)은 100번째 불합격 판정을 받았다. 선호원(고아성)은 이 다리 남단에 있는 바로 맞은편 선셋 카페를 지나며 걷는다. 그녀는 자살을 고민하다가 결국 강물에 빠진다. 그녀는 병원에서 깨어나 도기택(이동휘)과 장강호(호야)를 만난다. 세 사람은 (진짜) 생을 마감하고 여기로 오기로 결심한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6778, 25910, 14999, '14회에서는 박상만(권해효) 한정태(이윤상)도 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6779, 27069, 12729, '이 집은 오인혜(박지후)의 학교 친구 박효린(전채은)의 집으로 등장한다. 1화에서 오인주(김고은)가 여동생을 따라가다가 이 집 앞에 도착하는 장면을 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6780, 25737, 12729, '2화에서 화영(추자현)이 이 길을 건너는 모습이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6781, 25912, 12729, '2화에서는 오인주(김고은 분)가 맥주를 한 모금 마시며 화영(추자현 분)의 진미경 스카이그램 계정에 담긴 사진을 바라보는 모습이 그려진다. 이어 화영의 유서가 진미경의 사진 캡션임을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6782, 26700, 17447, '이 곳은 서연대학교로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6783, 25937, 17447, '4회에서 반아인(박수아)이 소꿉친구 박수하(김강민)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6784, 25612, 13374, '오하니(정소민)의 상상력이 발동하고 그녀는 백승조(김현중) 사이의 바로크 전투 장면을 꿈꾼다. 5회에서 두 사람은 편의점 쓰레기통에 누가 더 잘 던지는가를 놓고 이 정원에서 봉준구(이태성)와 경쟁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6785, 27442, 13374, '11회에서 오하니(정소민)와 기태(최성준)는 데이트 중에 여기에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6786, 32277, 13374, '15회에서 백승조(김현중)와 오해니(정소민)가 제주도로 신혼여행을 갔을 때 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6787, 26160, 13374, '15회에서 백승조(김현중)와 오하니(정소민)가 신혼여행 제주도로 이 리조트에 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6788, 25859, 14171, '1회에서 박차돌(이장우)과 백장미(한선화)가 데이트 장소로 나선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6789, 25335, 14171, '14명이 여의도 한강공원에서 자전거 여행을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6790, 28424, 14171, '18~19회에서 고재동(최필립)이 제단에서 도망친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6791, 26067, 13832, '장옥정(김태희)가 머물던 거처로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6792, 25673, 13832, '6회에서 세자(유아인)가 장기를 가지고 이 정원의 봉래섬 장관. 또한 3~4화에서는 이 정원의 완월정에서 간택 장면이 나온다. 20화 마지막회에서 이각세자가 박하(한지민)에게 편지를 묻힌 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6793, 25945, 12645, '드라마 안에서 주인공인 장희빈(김혜수)가 생활하는 곳으로, 드라마 대부분의 장면이 이곳에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6794, 26066, 15349, '16회의 마지막 장면에서 남치원(최다니엘)과 좌윤이(백진희)가 산책을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6795, 26122, 15349, '이곳 로비 내부는 드라마 안에서 \'YB그룹\' 사옥으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6796, 30593, 15349, '이곳은 YB Ad 사옥을 배경으로, 주요 출연진 대부분이 근무하고 있다. 건물 로비에 경례(정혜인)의 카페가 있는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6797, 25997, 15349, '2회에서 남치원(최다니엘)이 돌아가신 삼촌에게 조의를 표하기 위해 찾아왔다. 그리고 삼촌의 친구이기도 한 도 부사장(김창완)과 마주친다. 3, 4회에서 서도우(이상윤)가 그녀를 찾아온 곳이다. 12회에서는 김혜원(장희진)이 그녀를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6798, 30504, 15349, '왕정애(강혜정)의 집으로 보이는 이곳. 2화에서 좌윤이(백진희), 보나(차주영), 경례(정혜인)가 방문하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6799, 32278, 15349, '1회에서 도 부사장(김창완)의 집 구하라고 잔소리를 들은 남치원(최다니엘)은 횡단보도에서 내려달라고 부탁하고 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6800, 28483, 15349, '1회에서 좌윤이(백진희)가 택시를 부를 때 배경으로 등장하는 건물');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6801, 32279, 15349, '왕정애(강혜정)와 아들 건우(정준원)가 살았던 곳이다. 15회에서 황보율(이원근)이 휴가를 마치고 찾아온다. 그가 그녀의 집에서 장면을 만들고 있다는 사실에 불만을 품은 정애는 일어나서 자리를 떴고, 율은 그녀를 뒤따랐다. 밖에서 이야기를 나누는 동안 황보 일가의 간부는 몰래 그들의 대화를 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6802, 26093, 15349, '1회에서는 좌윤이(백진희)가 전여친과 데이트를 하는 모습이 그려졌다. 이 공원에서 남자친구 이경준(성준)은 상사로부터 전화를 받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6803, 25661, 15349, '1회에서 좌윤이(백진희)가 전 남자친구(성준)와 데이트를 하는 모습이 잠깐 등장하기도 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6804, 26666, 15349, '6회에서 좌윤이(백진희)가 열쇠를 건네주겠다고 약속한 뒤 함께했다. 처음에는 팀에 상황이 조금 어색했지만 팀에 그가 있어 모두 행복했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6805, 25797, 15349, '1화에서 이 다리 위에서 젊은 YB 감독 황보율(오토바이를 타고 출근하는 원근). 8회에서 남치원(최다니엘)은 이 다리의 한쪽 끝에 앉아 좌윤이(백진희)가 자신의 신뢰를 깨뜨렸을 수도 있다는 사실을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6806, 25712, 15349, '1회 오프닝 장면에서 좌윤이(백진희)는 전 남자친구이자 미술 큐레이터 이경준(성준)을 만났다. 프랑스 화가 조르주 루오의 1930년 작품인 The Juggler [Jongleur]에서 영감을 받은 작품을 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6807, 32280, 15349, '9회에서 좌윤이(백진희)가 설날에 남치원(최다니엘)을 찾으러온 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6808, 25633, 15349, '8회에서 남치원(최다니엘)은 이 계단 꼭대기에서 좌윤이(백진희)에게 키스하기 위해 손을 뻗는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6809, 32165, 15349, '8회에서 좌윤이(백진희)는 돌아가신 삼촌을 존경하게 된 남치원(최다니엘)을 찾기 위해 이 곳을 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6810, 32281, 15349, '이 집에는 좌윤이(백진희)가 살고 있다. 3화에서 남치원의 상사가 옥상으로 이사를 온다. 알고 보니 이곳은 사고로 가족이 사망한 후 삼촌과 함께 살았던 어린 시절 집이었다. 치원은 어린 시절 집에 불이 났을 때 삼촌의 죽음을 목격한 과거의 트라우마와 맞서고 극복하기 위해 이사를 가기로 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6811, 32282, 15349, '9회 회상 장면에서 왕정애(강혜정)가 이 사진관을 지나고 있다. 이날 새벽 별거 중인 남편 준표(전석호)는 그녀에게 다른 가족을 위해 떠나겠다고 말했고, 아들 건우의 생명보험에 가입할 계획을 밝혔다. 박건우(정준원) 대학교육. 사진관에서 그녀는 창가에 전시된 세 사람의 가족 사진을 보기 위해 그 앞에 멈춰 서서 감정이 북받쳐 오르기 시작하자 그의 사진에 화를 내며 소리친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6812, 28486, 16960, '5화에서 또 다른 저녁 식사 약속을 하는 레스토랑. 김해경(송승헌)은 우도희(서지혜)를 데려온다. 저녁 식사 후 그는 펭귄을 선물로 주었지만 갑자기 떠나야 한다. 그녀에게 곧 돌아올 것이라고 말합니다. 기다리는 동안 그녀는 점점 더 취하여 그가 도착하기 직전에 그녀가 거의 기절할 뻔하고 그녀의 과거 사랑에 대해 한탄하기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6813, 25963, 16960, '김해경(송승헌)의 \'편안한 정신과의원\'. 4회에서 우도희(서지혜)가 자신이 제작하는 쇼를 위해 그를 스카우트하기 위해 그를 만나려고 하는 것을 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6814, 26698, 16960, '1회에서 김해경(송승헌)이 오토바이를 타고 이 쇼핑몰에서 환자를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6815, 27302, 16960, '7회에서 \'O & Cafe\'로도 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6816, 26537, 16960, '3회에서 김해경(송승헌)은 \'문강\'으로 등장하는 이 식당에서 고객 미팅을 한다. 재결합을 위해 가명으로 예약한 것이 전 여자친구인 진노을(손나은)이라는 사실을 그는 거의 알지 못한다. 동시에, 우도희(서지혜)는 해경을 찾아 이곳에 도착하지만 그를 추적하지 않고 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6817, 26475, 16960, '2회에서 우도희(서지혜)가 근무하는 미디어 회사 \'투앤박스\'로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6818, 30541, 16960, '우도희(서지혜)는 김해경(송승헌)을 밥그릇 파는 노점상 \'호랑이 주먹밥\'으로 데려간다. 3회에서 팔공차 노리낙진점, 옥상에서 식사를 합니다. 잠시 후, 정재혁(이지훈)도 이 포장마차에서 밥 한그릇을 사들고 옥상으로 올라가 먹기도 한다. 4회에서 도희와 해경은 서로를 찾기 위해 다시 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6819, 25866, 16960, '1회(전체 1회)에서 우도희(서지혜)는 남자친구 영동(김정현)의 생일을 맞아 서프라이즈를 하고 싶을 때 이 공항에 도착합니다. 1회에서 도희는 김해경(송승헌)을 남자친구 카페에서 길을 건너 이 공항으로 데려다준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6820, 26649, 16960, '2회(전체 1회)에서 우도희(서지혜)는 그녀의 남자친구 영동(김정현 카메오)이 바람을 피웠고 그 사실을 알게 된 후에 그녀는 그녀를 이 해변으로 데려갔습니다. 자신도 모르게 그녀의 뒤를 이어 자신의 삶을 마감할 계획을 세운 김해경(송승헌)이 따라온다. 도희가 바다와 아주 가까운 용암 바위 위에 서서 균형을 잡기 힘들어하자, 해경은 그녀를 구하기 위해 휩쓸려 들어온다. 그녀는 몸을 숙이고 바다에 빠지고 결국 가벼운 뇌진탕을 겪습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6821, 32283, 16960, '1회(전체 1회) 우도희(서지혜)는 시작 부분에서 클래식 영화 \'티파니에서 아침을 먹으며\' 가장 좋아했던 장면에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6822, 25796, 16319, '동급생을 폭행하고 성폭행한 국세청 아들을 변호하기 위해 그가 자신의 범죄에서 무죄를 선고받도록 하는 것입니다. 2화(1화)에서는 또 다른 재판을 위해 돌아온 그가 검사로 밝혀진 서연아(나나)와 마주친다. 태경과 함께 건물까지 갔던 송우용(손현주)도 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6823, 26524, 16319, '2회(전체 1회)에서 이태경(최진혁)이 출소를 지시받은 조폭을 평가하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6824, 25673, 4104, '문정왕후가 의붓아들 인종을 독살했다는 소문을 허구로 각색한 시리즈중 마지막 부분에 사용되고 있다.자신의 아들(미래의 명종)이 통치할 수 있는 것을 싫어합니다. 마지막 장면에서 왕실 조사관은 문정왕후(이덕희)에게 왜 이 정원의 오작교에서 그를 살려줬는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6825, 32085, 14169, '1회에서 차앵란(전인화)은 아들 마도진(도상우)을 찾아 이 집에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6826, 28424, 14169, '17회에서 마도진(도상우)과 보경(홍아름)이 진달래 결혼식장에서 결혼식을 올린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6827, 25955, 14169, '신화주식회사 마가의 저택으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6828, 25781, 14169, '이 호텔은 \'신화호텔\'로 등장하며 \'신화그룹\'의 본사이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6829, 25866, 14169, '30회에서 모두가 제주도로 여행을 하고 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6830, 25938, 6630, '서울 전학생 강찬(이지훈), 서재우(김진우), 그리고 탑독 조태웅(서지훈)가 다니는 부산 명성고등학교 외관입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6831, 26274, 6630, '이 고등학교의 교실과 실내 체육관은 부산의 명성의 일부로 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6832, 26486, 4510, '이 공원은 홍길동이 조를 탈출하여 세운 율도국을 대표한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6833, 32436, 15745, '10회에서 이솜(E-som) 이영재와 온준영이 두 번째로 이별하는 장면이 나온다. 방금 일어난 일을 부인한 준영은 영재를 카페로 데려오고 싶어 화제를 돌리거나 상황을 진정시키려 한다. 그는 거리에 있는 여러 카페로 달려가지만 그곳에 도달하면 각 카페의 불을 끕니다. 그 순간 영재는 그들의 시즌이 끝났다는 것을 알았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6834, 27189, 15745, '이영재 (이솜)과 그녀의 오빠 수재(양동근)는 10대/20대에 살고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6835, 26228, 15745, '1회에서 온준영(서강준)과 이영재(이솜)가 이 곳에서 2012년 12월 31일에 다시 만난다. 준영은 잠복 중이고 영재는 친구 백주란(이윤지)과 파티를 하기 위해 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6836, 32437, 15745, '6회에서 암벽등반을 마친 백주란(이윤지 분)과 강철남(이하율 분)은 이곳 \'펫카페\'로 식사를 하러 온다. 주란은 운동이나 애완동물에 대해 특별히 관심이 있는 것은 아니지만 철남이가 그 이야기를 꺼낼 때면 그들에게 관심이 있는 척한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6837, 29429, 15745, '4화에서 온준영(서강준)이 이 건물 옆 주차장 앞 인도에 위치한 포창마차에서 동료들과 합류한다. 그는 처참한 소개팅으로 슬픔을 달래던 정 형사의 부러움을 사며 이영재(이솜)에게 애정 어린 전화를 건다. 이 장면에서는 코엑스 웨스트와 세븐럭 카지노가 배경으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6838, 32438, 15745, '4회에서 이영재(이솜)가 홍 디자이너(최병모)의 쇼케이스 헤어 디자인에 대해 논의하기 위해 이곳을 찾았다. 그곳에서 그녀는 김소희(신도현)가 이벤트의 메인 모델임을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6839, 30789, 15745, '패밀리 하우스의 파사드로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6840, 25926, 15745, '15회에서는 민세은(김윤혜)이 온준영(서강준)을 만나 이 곳에서 만난다. 여기에서 그는 사실 전날 밤 집에 있는 것이 아니라 영재와 함께 있었다고 자신의 행방에 대해 고백을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6841, 32439, 15745, '13회에서 공개된 현재 온리원(박규영 분)의 부동산 사무실 외관이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6842, 25661, 15745, '7회에서 결혼식장에서 우연히 마주친 이영재(이솜)와 최호철(민우혁)은 이곳을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6843, 32440, 15745, '식당에서 집으로 돌아오는 길에 온준영(서강준)은 다시 한 번 이영재(이솜)가 인스턴트 식품을 사는 모습이 포착한다. 이 편의점에서 그는 그녀가 그 자리를 떠날 때 거기에 있었고 잘 먹지 않는 그녀를 부드럽게 꾸짖습니다. 그녀가 맛이 좋지 않은 매운 낙지 볶음이 있다고 말하자 그는 그녀에게 만드는 법을 가르쳐 주겠다고 말합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6844, 32441, 15745, '10회에서 강력범죄3팀은 이 가게를 몰래 찾아오는 \'스네이크헤드\'라는 수배범을 잡기 위해 이 가게를 측설합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6845, 25335, 15745, '온준영(서강준)과 이영재(이솜)는 4회에서 고등학교 친구들을 만난 식당에서 그녀를 태우고 이 다리를 건넌다. 길을 가던 중 둘은 처참한 소개팅을 함께 나갔던 정 형사와 주란(이윤지 분)으로부터 각각 전화를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6846, 25347, 15745, '4회에서 온준영(서강준)과 이영재(이솜)는 이곳 강가 잔디밭에 누워 소풍 데이트를 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6847, 24907, 15745, '이 계단 꼭대기에는 이영재(이솜)의 오빠 수재(양동근)의 커피차 \'아모르파티\'가 자리 잡고 있다. 온준영(서강준 분)은 3화에서 자신을 찾으러 왔고, 7년 전 영재가 자신과 헤어지던 날 밤 일어난 일에 대한 진실을 알게 되는 곳이 바로 여기다. 4화에서 준영은 다시 이곳으로 돌아와 수재에게 언니의 남자친구로서 직업, 부모님 등에 대한 자세한 질문을 받는다. 준영은 포르투갈 배낭여행을 떠나기 전 이곳에서 수재를 마지막으로 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6848, 32442, 15745, '16회 최종회에서 공개된 민세은(김윤혜 분)의 아파트 외관입니다. 온준영(서강준)은 여기서 그녀를 기다리며 그녀와 함께 부모를 만날 수 없다고 말하고 그녀는 그와 헤어지기로 결정한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6849, 30596, 15745, '이영재(이솜)의 아파트이다. 12회에서 온준영(서강준)의 식당에서 식사를 마친 후, 최호철(민우혁)은 그녀의 뒤에서 아파트까지 걸었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6850, 25797, 15745, '6회에서 헤어쇼가 끝나고 쇼 후 축하연을 하러 가는 온준영(서강준)과 이영재(이솜)가 이 다리를 건넌다. 영재는 기분이 좋고 준영(서강준)은 최호철(민우혁)과의 일을 목격하고도 체면을 유지하려고 한다. 에피소드 후반부에 다리를 다시 건널 때, 그들은 티격태격하고 영재는 그에게 길가에 차를 세워달라고 부탁한다. 그녀가 걸어가고, 그는 그녀를 따라나오지만, 스스로 걷지 못하게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6851, 25776, 15745, '온준영(서강준)은 5회에서 팀원들과 식사를 하러 왔는데, 결국 하루 종일 긴장했던 이유를 털어놓는다. 이들은 각자 퇴근 시간 이후 미용실에서 남자 손님의 머리를 감는 영재(이솜)에 대해 분석하고 합리화할 때 \'천사/악마\' 역할을 시도한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6852, 25910, 15745, '14회에서 이수재(양동근)는 이곳에 온준영(서강준)을 초대해 함께 식사를 한다. 그는 준영이 그의 시나리오의 영감을 준 사람 중 하나임에도 불구하고 그동안 준영에게 술을 사주지 못해 미안하다고 말한다. 이후, 준영이 식사를 결제하고 수재대신 운전을 해주면서 이러한 역사가 되풀이된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6853, 32443, 15745, '8회에서 강력범죄단이 내세우는 \'스카라\' 당구장이 위치한 건물로 민세은 경관(김윤혜) 잠입. 측설 중 준영은 오랜만에 수배범 \'뱀머리\'를 잡을 수 있을지가 불안해 영재(이솜)에게 전화를 걸어 그녀의 목소리를 듣게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6854, 31746, 15745, '6회에서 백주란(이윤지)은 강철남(이하율)과 암벽등반 데이트를 하러 이 곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6855, 32444, 15745, '9회에서 주랑 드 보떼와 HC 성형외과 팀이 자원봉사 목적지로 가는 페리에 탑승하기 전에 이곳에서 만난다. 온준영(서강준)는 이영재(이솜),백주란(이윤지), 이수재(양동근), 최호철(민우혁)을 따라 이 여정에 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6856, 25017, 15745, '11화에서 온준영(서강준)이 포르투갈에서 5년의 시간차를 두고 이곳으로 출국하고 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6857, 26686, 12806, '1화에서 전두환(이덕화)과 허화평(이진우)은 아침에 이 거리를 따라 함께 조깅을 하고, 잠시 후 허삼수(차광수)와 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6858, 32445, 12806, '32층에 있는 Onyx Room. 중국집 도림의 한 부분');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6859, 25931, 12806, '14회에서 시위가 이 역 앞에서 벌어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6860, 25907, 17369, '최종화 10회에 제일 마지막에 나오는 장면이 이곳에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6861, 32449, 17369, '이 카페는 탁기현(공민정)이 운영하는 이색 카페 \'내추럴 테이블\'로 알려져 있습니다. 서지성(송하윤)과 문예슬(에이핑크 김보미)이 자주 찾는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6862, 25792, 17369, '서지성(순하윤)은 드라마에서 약혼자 방준한(이시훈)을 만나기 위해 이 건물 로비로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6863, 25882, 17369, '1회에서 서지성(송하윤)은 이 곳에서 한 남자가 친구에게 사귀는 여자를 욕하는 소리를 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6864, 32450, 13345, '명장 팔봉(장항선)이 운영하는 곳이 여기에 있습니다. 이곳은 김탁구(윤시윤)와 구마준(주원)이 베이킹 견습생으로 훈련하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6865, 25936, 13913, '제왕의 딸, 수백향의 주요 촬영지이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6866, 26484, 19051, '1회에서 유세풍(김민재)과 서은우(김향기)가 이 절벽에서 처음으로 마주친다.그리고 그녀는 그의 삶을 계속할 가치가 있다고 그를 설득합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6867, 31785, 15124, '16화 회상에서 한무영(남궁민)이 다리를 뛰어넘어 자살하려는 윤선우(이주승)를 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6868, 26409, 15124, '16회 파노라마 장면에서 본 문으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6869, 25908, 17211, '6화에서 김무영(최진혁)의 상상장면에서 자신의 미래의 모습을 상상하는 재벌집으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6870, 24937, 17211, '최종화 12화에서 본 할로윈 파티가 이곳에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6871, 27318, 17211, '공순지(박주현)가 시사 PD로 일하는 HBS 방송국이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6872, 25882, 17211, '1화에서 공순지(박주현)는 PD(배유람)와 함께 삼각김밥에 라면을 곁들였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6873, 25809, 17211, '9화에서 이광식(박상면)은 이곳에 수감된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6874, 32452, 3838, '영화 \'종려나무 숲\' 촬영지로 알려진 수선화 스팟');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6875, 25989, 16369, '시즌2에서 김소현이 기도훈을 만난 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6876, 32453, 16369, '시즌2에서 김소현이 다니는 대학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6877, 25019, 16369, '김조조(김소현)는 1회에서 절친 두 명과 함께 원마운트 스노우파크[원마운트파크]에서 스케이트를 탄다. 4회에서 조조는 황순오(송강)와 스케이트 데이트를 하러 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6878, 27610, 16369, '1회에서 육지(김시은)와 황순오(송강)의 사진 촬영 장소가 있습니다. 그녀를 데리러; 모두가 그녀의 남자 친구를 질투하게 만듭니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6879, 32454, 16369, '시즌2에서 김소현과 정가람이 벚꽃 데이트하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6880, 31785, 16369, '7회에서 이혜영(정가람)은 첫 눈이 내리는 날 자전거를 타고 이 다리로 김조조(김소현)를 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6881, 25953, 16369, '5회에서 황순오(송강)는 이 호텔의 바에 술을 마시러 오고 이혜영(정가람)에게 전화를 걸었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6882, 30717, 16369, '5, 6회에서 김조조(김소현)는 밤의 거리를 달리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6883, 32056, 16369, '1회에서 김조(김소현)는 아르바이트를 마치고 저녁에 이혜영(정가람)이 조용히 이 가게를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6884, 32455, 16369, '1회에서 아르바이트를 마치고 돌아오는 길에 이 가게를  김조조(김소현)가 지나간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6885, 27160, 16369, '김소현과 정가람이 4년만에 재회하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6886, 29396, 16369, '시즌1에서 헤어진 김소현과 송강이 재회한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6887, 26002, 16369, '4회에서 원마운트에서 스케이트 데이트를 한 후, 김조조(김소현)과 황순오(송강)는 밤에 이 놀이공원 호수를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6888, 26311, 16369, '3회에서 황선오(송강)와 김조조(김소현)가 곰말다리를 건너 사랑을 알리는 앱을 사용해 서로의 사랑을 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6889, 27554, 16369, '김소현과 송강이 밤에 산책했던 호수');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6890, 25335, 16369, '6회에서 황순오(송강)가 김조조(유령을 잡아라)를 만나러 가는 길에 이 다리를 운전해서 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6891, 26232, 16369, '김소현이 정가람의 좋알람을 울려주는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6892, 32456, 16369, '1회에서 이 편의점은 김조조의 이모가 운영하는 곳이며 여기서 조조(김소현)가 아르바이트를 하는 곳이다. 그녀는 이 가게 꼭대기 옥상에 이모와 함께 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6893, 25945, 16369, '7회에서 김조조(김소현)는 한 순간을 떠올린다. 이혜영(정가람)이 이 궁의 동벽 밖 벤치에 앉아 있는 동안 이혜영(정가람)이 자신의 마음을 고백했을 때를 떠올린다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6894, 29475, 16369, '6회에서 김조조(김소현)가 이혜영(정가람)에게 사랑의 알람을 이 박물관 앞에서 울린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6895, 24922, 16369, '8회에서 황선오(송강)는 이별한 김조조(김소현)를 위해 덕성여중 정문 앞 이 거리에서 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6896, 25340, 16369, '1회에서 김조조(김소현), 이혜영(정가람), 황순오(송강)와 그녀의 친구들은 이 학교에 다닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6897, 26186, 16369, '3회에서 이혜영(정가람)과 황순오(송강)는 저녁에 산책을 하며 성곽에 잠시 앉아 어린 시절 함께 보낸 시간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6898, 32457, 16369, '시즌 2에서 송강과 정가람이 이야하는 산책길');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6899, 25856, 16369, '8회에서 좋아하면 울리는 2.0의 새로운 기능이 공개며 이곳에 있는 사람들이 어플을 확인하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6900, 27220, 16369, '1회에서 이혜영(정가람)과 황순오(송강)는 자전거를 타고 이 거리를 지나 학교에 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6901, 27223, 16369, '정가람과 송강이 등교길에 지나간 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6902, 26220, 16369, '김소현의 벚꽃이 만개한 산책하는 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6903, 32458, 16369, '김소현과 송강의 제주도 수학여행 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6904, 32459, 16369, '마지막회에 김소현과 정가람이 재회하는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6905, 32460, 16369, '시즌1에서 주인공들의 제주도 숙소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6906, 32461, 16369, '고민시가 반 단체사진을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6907, 26311, 13899, '공효진의 첫 사랑 감정을 자극한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6908, 32463, 3508, '2회에서 이루다(백진희)는 직장 생활에서 최악의 날 중 하나가 계속되는 가운데 이 교차로에서 생각을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6909, 25678, 3508, '18회에서 이루다(백진희)와 강준호(공명)가 데이트를 하러 가는 놀이동산입니다. 백진상(강지환)은 타임루프를 이용해 두 사람을 막으려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6910, 32464, 3508, '백진상(강지환)이 팀장 평가에 불합격한 후 점장으로 일하는 MW 치킨 가리봉 프랜차이즈로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6911, 26540, 3508, '이루다(백진희), 백진상(강지환) 등 직원들이 일하는 MW 푸드 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6912, 25960, 3508, '2회에서 이 건물을 지나며 직장생활 최악의 날이 계속 반복되고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6913, 30713, 3508, '1,2회에서는 팀장 백진상(강지환)이 회식자리에서 기절하자 이루다(백진희)와 강준호(공명)가 그를 데리고 거리로 나온다. 루다의 바지에 토한 후, 그는 술에 취해 비틀거리며 큰길로 나가 가까이 다가온다. 이후 루다 앞에서 트럭에 치어 쓰러진다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6914, 25792, 9161, '이 곳은 드라마에서 한지원(유이)과 탁수진(김한나)가 일하는 곳으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6915, 26249, 13517, '2회에서 DN그룹은 이 곳 홀에서 기념일 축하연을 열고, 차지헌(지성 분)이 공황발작으로 방을 떠나야 할 때까지 자신을 새로운 최고재무책임자로 소개한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6916, 25608, 13517, '17회에서 은재(최지우)와 형우(윤상현)는 케이블카를 타고 남산타워에서 만나기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6917, 25894, 13517, '17회에서 은재(최지우)와 형우(윤상현)는 각자 케이블카를 타고 남산타워에서 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6918, 27605, 13517, '10회에서 연형우(윤상현), 이은재(최지우) 사랑을 다시 시작하기 위해 여기로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6919, 26729, 18525, '이 식당은 청산(윤찬영)의 부모님이 운영하는 \'청산 치킨\'으로 등장합니다. 1화에서 청산은 친구 온조(박지후), 경산(함성민), 이삭(김주아)과 함께 자신의 엄마의 새로운 닭튀김 레시피를 시험해 보기 위해 들렀습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6920, 32529, 18525, '최남라(최이현)와 이나연(이유미)이 사는 아파트 단지입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6921, 32530, 18525, '좀비 바이러스 발생의 그라운드 제로인 \'효산고등학교\'로 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6922, 26066, 18285, '13회에서 하영은(송혜교)이 윤재국(장기용)과 함께 이 정원에서 화보 촬영을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6923, 24929, 18285, '13회에서 하영은(송혜교)은 이 식당에서 패션 매거진 \'누벨바그\' 기자와 만나 인터뷰를 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6924, 25816, 18285, '15회에서 윤재국(장기용)이 이곳(인천공항으로한)을 떠나고, 하영은(송혜교)이 사랑에 대한 믿음의 말로 작별 인사를 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6925, 28584, 18285, '1회에서 황치숙(최희서 분)은 남자친구인 모델 지미(김도건 분)를 부르려다 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6926, 26131, 18285, '9화에서 윤재국(장기용)이 하영은(송혜교)의 아버지 하택수(최홍일)와 이곳에서 술자리를 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6927, 25608, 18285, '10회에서 석도훈(김주훈)은 황치숙(최희서)을 이곳으로 걸어오게 하고 난간에 자물쇠를 채우고 열쇠를 건네며 사랑을 고백한다. 나중에, 그들은 여기로 돌아오고 그녀는 그의 감정을 받아들인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6928, 26737, 18285, '9회에서 윤재국(장기용)과 하영은(송혜교)은 야경을 즐기면서도 앞으로의 길을 함께 걸으며 서로를 위로하기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6929, 32531, 18285, '9회에서 윤재국(장기용)이 이 꽃집을 지나갈 때 밖에 진열된홍장미가 눈길을 끕니다. 꽃집 주인은 그에게 다가가 이렇게 말합니다. "만약 당신의 여자친구에게 이것을 준다면, 이걸 사셔야 합니다. 핑크 장미는 \'행복한 사랑\'을 의미합니다." 그러자 그는 즉시 “이거 살게요.”라고 대답합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6930, 26583, 18285, '5회에서 윤재국(장기용)은 이 식당에서 황치숙(최희서)을 거절합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6931, 31543, 18285, '이곳은 10화에서 윤재국(장기용)이 여는 \'우리사진관\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6932, 27067, 18285, '이 건물은 하영은(송혜교)이 팀장으로 일하는 패션 회사 \'더 원\'으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6933, 27480, 12602, '1-2화에서 윤호재(권상우)는 순백의 거짓말로 최규인(소지섭)을 이곳으로 오게 하고, 호정은 학비를 마련하기 위해 이곳에서 일하는 여학생과 사랑에 빠진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6934, 25607, 12602, '12회에서 윤호정(채림)과 최규인(소지섭)이 첫 데이트를 하러 왔다. 일산호수공원 주제광장에서 자전거를 타고 소풍을 한다. 16회에서는 수지(이의정)와 윤호재(권상우)가 이곳에서 결혼한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6935, 26639, 12602, '11화에서 윤호정과 건우가 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6936, 26002, 12602, '1회 윤호정(채림)은 건우에게 사랑에 빠진다. 5회 이곳에서 아르바이트를 한다. 10회 채림은 건우와 함께 아이스링크로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6937, 32532, 12602, '윤호중( 채림), 강수지(이의정), 한재영(성시경)은 기차가 이 역에 정차하자 지하철에 앉아 있고, 재영은 그곳에서 찰나의 아는 여자가 지나가는 것을 본다. 1화에서 그는 모두에게 기차에서 내리라고 안내한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6938, 25931, 12602, '9회에서 군복무를 마치고 휴가를 마치고 집에 돌아올 때의 역으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6939, 32544, 18276, '생일날 아이들이 보는 앞에서 천사의 선고를 받았던 죄인 박정자가 새진리회에게 30억을 받고 지옥행 장면을 시연했던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6940, 25659, 18276, '카페를 초토화시킨 후 도망치는 죄인과 지옥의 사자들이 추격전을 펼치는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6941, 32545, 18276, '덜덜 떨고 있는 남자를 잡으로 유리를 깨부수고 들어온 지옥행 사자들');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6942, 32546, 18276, '새진리회 지옥행 시연 장면으로 나온 강당공간');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6943, 32547, 18276, '새진리회를 이끄는 유아인이 사람들에게 천사와 지옥에 대해 설명하는 첫 등장씬');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6944, 32548, 18276, '3화에서 유아인 독백장면이었던 폐건물');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6945, 24935, 19209, '6회에서 고중도(이시운)와 백은지(주보영)가 이 술집에 자신을 소개하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6946, 32549, 19209, '5회에서 진정(도경수/디오)과 그의 팀은 이 클럽애서 마약 조직원을 추적한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6947, 25913, 19209, '5화에서 나오는 강산 법무법인 김창완 대표(서현규) 사무실이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6948, 25621, 19209, '1회에서 서울중앙지검 형사부가 팀 회식을 하는 \'청심관\'이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6949, 25607, 12478, '3화에서 이자영 (최지우)과 박승재 (손지창)가 자전거 데이트를 하러 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6950, 24957, 12478, '이자영(최지우), 정현우(류시원), 이신희(박선영)는 이 대학에 다닌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6951, 26002, 12478, '이자영(최지우)과 정현우(류시원)는 5화에서 아이스 스케이팅을 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6952, 26016, 16020, '7회에서 여여름(손성윤)은 사무실 밖에서 실신한 권정록(이동욱)의 도움으로 병원으로 이송된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6953, 32550, 16020, '이 아파트 단지의 대문은 오윤서(유인나)가 살고 있는 \'하이츠맨션\'의 입구로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6954, 25607, 16020, '권정록(이동욱)은 7회에서 이 공원 메타세쿼이아 골목길에서 오윤서(유인나)에게 손편지를 건네며 심경을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6955, 32551, 16020, '8회에서 법무법인 올웨이즈 팀이 워크숍을 위해 찾아왔다. 권정록(이동욱)과 오윤서(유인나)는 잠을 이루지 못하고 우연히 숙소 밖에서 만나 첫키스를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6956, 26320, 16020, '9회에서 오진심(유인나)의 영상을 찾을 때, 권정록(이동욱)이 그녀가 시상식에 참석하기 위해 이 건물의 계단을 걸어 올라가는 것을 보고 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6957, 25827, 16020, '5회에서 권정록(이동욱)과 오윤서(유인나)는 구치소에서 서울로 돌아오는 길에 차를 타고 이곳 입구를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6958, 24932, 16020, '5회에서 여기에 권정록(이동욱)과 유여름(손성윤)이 오고 그녀는 방금 맡은 사건에 대해 조언을 구합니다. 이 점심 데이트를 위해 정록은 오윤서(유인나)를 두고 떠났습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6959, 24901, 16020, '14회에 권정록(이동욱)이 출연한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6960, 31974, 16020, '14회 5년 전 회상 속에서 권정록(이동욱)은 연준규(오정세)의 이 바에서 조사 결과를 조작하던 화학 회사의 당시 의뢰인에 대한 내부고발자로 인해 소독제가 독성이 있다고 판단했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6961, 32552, 16020, '13~14회에서 권정록(이동욱)이 이곳에서 버스를 타고 출근하기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6962, 26410, 16020, '권정록(이동욱)은 2회에서 이곳에서 장광설 고객을 만나 오윤서(유인나)의 환영 파티에 늦는다. 3화에서 그는 자신의 선배이기도 한 상대 재판 변호사를 만나기 위해 이곳으로 돌아와 식사를 한다. 이번에는 선배가 윤서의 광팬이라 치장한 윤서를 데려온다. 윤서는 화장실 밖으로 나오면서 선배 변호사와 마주치게 되고, 선배 변호사는 자신에게 신분을 밝히라고 꼬드겨보려 하지만, 때맞춰 정록에게 제지당한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6963, 27602, 16020, '범죄 현장으로 꼽히는 살인 용의자 임윤희의 집 정면이다. 6회에서는 권정록(이동욱 분)이 증거를 보기 위해 이곳에 오는데, 오윤서(유인나 분)가 동행한다. 집에 있는 동안 정록은 침입자에게 목을 얻어맞아 윤서를 놀라게 한다. 약간 멍한 상태임에도 불구하고, 그는 침입자를 쫓아 내려오기 전에 그녀에게 경찰을 부르라고 말한다. 이후 매니저 이두엽(박지환 분)은 이 집 바로 맞은편 186-7호(실제 10호) 밖에서 노인 주민과 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6964, 31628, 16020, '6화에서 권정록(이동욱)은 침입자 사건 이후 이 마트 밖에서 흔들리는 오연서(유인나)와 재회하고, 정록이가 어떻게 상처받았는지를 보고 눈물을 흘린다. 그녀와 이야기를 나눈 후, 그는 그녀를 택시에 태워 집으로 보낸다. 이후 매니저 이두엽(박지환)은 살인 사건 당일 밤 범행 현장에 있었던 것으로 추정되는 지적장애인 정육점 주인 박수명에 대해 알아보기 위해 이곳의 한 노인과 대화를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6965, 32553, 16020, '7회에서는 권정록(이동욱)이 오윤서(유인나)를 심야 데이트에 데려가는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6966, 25953, 16020, '마지막회 16회에서 권정록(이동욱)과 오윤서(유인나)가 연 엔터테인먼트의 10주년 기념행사에 함께 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6967, 25657, 16020, '오윤서(유인나)와 권정록(이동욱)은 4회에 이곳에서 만나 법률 과외를 받는다. 그녀는 그것이 다소 낭만적이라고 상상했지만, 그것은 결국 다소 지루한 공부 시간이 되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6968, 29222, 16020, '9회에서 오윤서(유인나)가 신선한 공기를 마시러 경의선 광장에 찾아와 공원에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6969, 32554, 16020, '임윤희 사건 승소 이후 정록은 회식 대신 술 한 잔을 선택한 이유를 묻자 “그냥 한 잔 하고 싶었다”고 답했다. 14화에서 정록이를 술에 초대하고 그들이 헤어졌음에도 불구하고 그는 그녀의 제안을 받아들인다. 그가 도착한 직후, 그는 그녀에게 밖으로 나가서 이야기를 하자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6970, 26744, 16020, '이 다리는 1화 오프닝 장면에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6971, 31718, 16020, '권정록(이동욱)과 오윤서(유인나)는 9회에서 정록의 술 취한 수다를 선보인 후 신선한 공기를 마시러 이곳에 왔다. 그녀는 그에게 그의 새로운 면을 보는 것이 신선하다고 말했고, 그는 차례로 그녀가 아직 그녀가 원하는 것을 공유할 수 없어도 괜찮다고 그녀에게 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6972, 32555, 16020, '9회 초반에서 권정록(이동욱)은 오윤서(유인나)와 함께 새로운 의뢰인을 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6973, 25664, 16020, '2회에서 최윤혁(심형탁)이 샌드위치 가게에서 난감한 사건을 목격한 후 단문희(박경혜)의 뒤를 따라 그녀를 놀리는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6974, 27119, 16020, '4회에서는 밤에 집으로 돌아가는 길에 비서 양은지(장소연)가 그녀의 집으로 가는 지름길인 이 터널을 지나가기로 결심합니다. 그러는 동안 그녀는 갑자기 누군가가 자신을 따라오고 있음을 감지하고 속도를 높입니다. 그녀는 그 사람이 그녀를 따라잡았을 때 놀랐지만 알고 보니 그녀의 동료 이두섭(박지환)이었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6975, 25796, 16020, '권정록(이동욱 분)이 하이라이트 릴에서 법정을 나서는 모습이 그려진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6976, 25734, 16020, '1화 오프닝 장면과 2화에 등장한다. 16화에서는 오윤서(유인나)가 타고있는 승합차가 이 다리를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6977, 28427, 16020, '1회 오프닝 장면에 등장하는 다리이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6978, 30714, 16020, '4회에서 올웨이즈 로펌 팀은 회식 후 이곳에서 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6979, 32556, 16020, '15회에서 로펌 팀(인턴 제외)이 점심을 함께 먹으러 왔다. 오윤서(유인나)의 키스신에 예민해진 권정록(이동욱)은 연준규(오정세)의 말에 화를 내며 자리를 뜬다. 이후 준규는 테이블에 있는 모든 사람이 싱글인 이유를 언급하고 회사에 오피스 커플이 있어야 하는 방식에 대해 이야기한다. 그때 변호사 최윤혁(심형탁)과 단문희(박경혜)가 공개연애를 인정한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6980, 32557, 16020, '최종회 16회에서 오윤서(유인나)의 드라마 \'사랑은 아파요\'의 출연진과 제작진이 이 바베큐 식당에서 랩 파티를 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6981, 25668, 16020, '3회에서 매니저 공혁준(오의식)이 여기서 오윤서(유인나)를 차에 태운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6982, 31473, 16020, '권정록(이동욱)이 찾아온다. 14회에서 김세원(이상우)에게 “만나고 싶은 사람”이 있다며 전화를 받은 후, 도착하자마자 세원과 함께 상사인 연준규(오정세)를 발견한다. 두 사람은 서로의 의견 차이로 어색하지만 세원은 서로의 차이점을 해결하자고 주장하며 결국 두 사람을 혼자 남겨둔다. 준규는 모든 상황에 대한 자신의 심정을 이야기하기 시작했고, 정록에게 5년 전처럼 자신의 로펌을 조작 혐의로 폭로했을 때처럼 자신이 무너지는 것을 보고 싶지 않았다고 말한다. 하지만 준규는 “이거 같이 가자. 아무리 힘들어도 함께 이겨냅시다."라고 한다. 그리고 결국 두 사람은 화해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6983, 30597, 16020, '12회에서 김세원(이상우)과 여름(손성윤)이 대학시절 밥을 먹던 곳, 그리고 다시 먹으러 오는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6984, 32558, 16020, '5회에서 권정록(이동욱)이 목격자와 만나는 곳이 바로 이 장소 3층이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6985, 27305, 16020, '권정록(이동욱)과 오윤서(유인나)는 이 카페에 온다. 10회에서 데이트를 하기 위해 스톤헨지 실루엣 컬렉션의 목걸이와 귀걸이를 선물한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6986, 25954, 16020, '5회 늦은 퇴근 후, 권정록(이동욱)과 오윤서(유인나)는 저녁을 함께 먹는다. 식당으로 가는 길에 이 길을 걸으며 정록은 유서에게 자신을 좋아하느냐고 묻는다. 14화에서는 정록과 윤서가 감자탕집에서 만난 후 길 반대편에서 대화를 나눈다. 헤어진 이유를 알고 있다고 말하며 다시 시작하자고 한다. 자신의 입장을 고수했지만, 정록은 그녀에 대한 감정을 더 이상 참을 수 없었다. 마침내 윤서에게 사랑을 고백하고 두 사람은 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6987, 27145, 16020, '2회에서 권정록(이동욱)과 오윤서(유인나)가 저녁을 먹고 이 카페 밖 골목으로 걸어간다. 그는 여기서 그녀에게 공식적으로 자신의 비서가 될 기회를 주겠다고 말하고, 기뻐하는 윤서는 그에게 달려들기 시작한다. 이 장면은 쇼의 오프닝 크레딧에 잠깐 등장하기도 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6988, 24922, 16020, '5화에서 권정록(이동욱)과 오윤서(유인나)가 이 곳에서 대화를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6989, 29381, 16020, '3회에서 검찰청으로 등장하는데, 오윤서(유인나)가 과거 사건을 복사하기 위해 찾아가는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6990, 25017, 16020, '9회에서 오윤서(유인나)의 적대자 이강준이 한국으로 귀국하기 위해 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6991, 26045, 16020, '11회에서 권정록(이동욱)은 오윤서(유인나)를 이곳으로 데려와 무슨일이 있어도 그녀를 지켜주겠다고 약속하며 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6992, 27000, 16020, '오윤서(유인나 분)와 권정록(이동욱 분)이 3회에서 재판을 성공적으로 마치고 이곳을 산책한다. 정록은 윤서의 칭찬에 가슴을 설레게 하고, 목도리가 바람에 날리면 목에 감는다. 정록이 또 다른 사건을 변호하러 온 5화에서 둘 다 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6993, 25641, 14781, '질투의 화신에서 공효진, 고경표가 간 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6994, 32559, 14781, '질투의 화신에서 조정석이 간 유아용품점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6995, 32560, 14781, '질투의 화신에서 공효진이 일기예보 찍은 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6996, 32561, 14781, '이곳은 코스모스 꽃 축제가 열리는 곳으로, 13회에서 표나리(공효진)가 일기예보를 촬영하라는 지시를 받은 곳이다. 결국 아나운서 테스트 캐스팅에 참여하지 못함에도 불구하고, 이화신(조정석)은 나리가 제시간에 SBC 방송국에 도착할 수 있도록 헬리콥터로 그녀를 태운다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6997, 27418, 14781, '2회에서 고정원(고경표)은 차비서(박성훈)에게 표나리(공효진)에게 잘못된 일기예보에 관한 항의 전화를 걸어달라고 부탁한다. 비서가 이유를 묻자 정원은 “그냥.”이라고 대답한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6998, 26084, 14781, '24회에서 여기서 표나리(공효진)와 이화신(조정석)이 결혼했다. 두 사람은 2층 토파즈 웨딩홀 \'토파즈홀\'을 선택했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (6999, 26493, 14781, '질투의 화신에서 공효진, 조정석이 간 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7000, 29311, 14781, '고정원(고경표)이 디렉터로 있는 L그룹의 명품 브랜드/숍 헤라의 위치이다. 9화에서 이 건물 바로 앞에서 표나리(공효진)와 정원의 첫 키스 장면을 이화신(조정석)이 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7001, 26152, 14781, '이 호텔은 L 그룹의 막내 상속인 고정원(고경표)이 아침 수영을 하러 가는 L그룹의 일부이다. 그의 반려견과 그의 서명을 요구하는 호텔 경영진이 뒤따랐다. 이후 방송에서 호텔 엘리베이터도 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7002, 32562, 14781, 'SBC 빌딩에서 일하는 모든 사람들이 퇴근 후 앉아서 술을 마시는 포장마차, 이 공원의 북동쪽에 SBS 프리즘타워가 마주보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7003, 26697, 14781, '질투의 화신에서 공효진, 조정석이 일한 방송국');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7004, 25726, 14781, '20화에서 절친 고정원(고경표)과 이화신(조정석)이 심하게 말다툼을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7005, 32563, 14781, '7회 마지막에서 표나리(공효진), 이화신(조정석), 고정원(고경표)이 식사를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7006, 26624, 14781, '태양대학교 병원으로 나온다. 이화신(조정석)이 치료를 받고 화신의 형이 죽는 곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7007, 25017, 14781, '6회에서 정원(고경표)은 어머니와 화신(조정석)을 마중하러 입국장으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7008, 32564, 14781, '질투의 화신에서 조정석, 고경표가 간 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7009, 26045, 14781, '질투의 화신에서 조정석, 고경표가 간 테마파크');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7010, 32565, 14781, '11회에서 표나리(공효진)가 새들에게 먹이를 주는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7011, 32566, 14781, '질투의 화신에서 조정석, 고경표가 간 갯벌');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7012, 28426, 18919, '최종회 16회에서 정현태(이훈)가 진세드의 무죄 판결을 받는 법정으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7013, 26085, 18919, '은성캐피탈의 외관으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7014, 24937, 18919, '대장경(이호정)은 이곳에서 선민준(지도훈)과 결혼을 강요하는 어머니를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7015, 24907, 18919, '선동식(최정우 분)에게 자신의 계략에 넘어가지 않겠다고 말하고, 오리올에서 이슬비(서주현/소녀시대 서현 분)와 선민준(기도훈 분)을 만난 공수광(나인우 분)은 이 계단에서 생각에 잠긴 카페를 내려다본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7016, 25884, 18919, '9회에서 선동식(최정우)은 밤에 모두를 이 식당 옥상으로 초대하고 공수광(나인우)을 이슬비(서주현/소녀시대 서현)의 힘으로 이익을 얻기 위해 같이 일하자고 설득한다. 수광은 옛 절친의 음모를 직감하고 자리를 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7017, 25635, 15461, '14회에서 송우진(류수영)은 이 식당을 빌려 언니 차도희로 변장한 차선희(이다해)에게 결혼을 청합니다. 선희는 혼란스러워하고 이 프로포즈를 거절합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7018, 28424, 15461, '20화에서 송우진(류수영)과 차도희(이다해)의 결혼은 이곳에서 일어난다. 도희가 검거되는 결말');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7019, 25799, 15461, '쌍둥이 엄마 이문숙(양금석)이 사는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7020, 26246, 15461, '이 건물은 1화에서 드라마에서 \'동해항공\' 본사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7021, 31721, 13202, '22회에서 이 회사로 출장을 갔을 때. 이 등대에서 현수교로 이어지는 작은 길로 돌아오는 길에 우환은 은성에게 마음을 고백하고 그녀는 도망가기 전에 키스를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7022, 25640, 13202, '16회에서 선우환(이승기)이 기다리는 가짜 버스 정류장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7023, 25718, 13202, '고은성(한효주)은 계모가 돈 때문에 자신을 배신한 사실을 알고 19화에서 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7024, 25607, 13202, '15회에서 아버지와 충돌한 준세를 응원하기 위해 오후에 이 공원의 잉어다리[호수공원 잉어다리]에 고은성(한효주)와 준세(배수빈)가 찾아온다. 저녁에 그들은 여기로 돌아오고, 준세는 은성에게 \'오빠\'가 아니라 그녀의 남자가 되고 싶다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7025, 26619, 13202, '장석자(반효정)는 가족과 함께 이곳에서 고은에게 질문 하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7026, 32567, 13202, '4회에서 김현우(문채원)의 일기예보 중 하나가 이 백화점 앞이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7027, 26744, 13202, '찬란한 유산 9회에서 유승미(문채원)가 어머니와 싸운 후 선우환(이승기)은 위로한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7028, 24957, 13202, '4화에서 유승미(문채원)가 공부하고, 선우환(이승기)은 그녀를 오토바이에 태운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7029, 27304, 13202, '2회에서 고은성(한효주)은 오빠 고은우(연준석)와 함께 이 벽에서 뛰어내리며 두 사람의 삶을 끝내고 싶어한다. 그녀는 12화에서 그 동안 실종된 오빠를 생각하며 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7030, 27213, 13202, '선우환(이승기)과 고은성(한효주)의 만남, 은성은 이미 미국으로 출국하기로 결정했고 오늘이 이별이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7031, 27201, 13202, '3,4회에서 고은성(한효주)이 사는 집으로 등장한다. 이곳에서 그녀는 의식불명 상태에 빠진 선우환(이승기)의 할머니 장석자(반효정)를 껴안는다. 일부 에피소드에서 은성은 우환의 저택으로 이사하지만 20화에서 그녀가 쫓겨났을 때 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7032, 26544, 13202, '고은성(한효주)는 3, 4회에서 동생을 찾는 시간을 갖기 위해 길거리 음식을 팔기로 했다. 그곳에서 그녀는 선우환(이승기)의 할머니 장석자(반효정)를 처음 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7033, 26642, 12710, '13회에서 한태화(신현준)이 이 역을 떠나고 아픈 한정서(최지우)와 차송주(권상우)가 그를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7034, 25773, 12710, '천국의 계단 2회에서 차송주(백성현)와 어린 한영이 이 공항에서 미국으로 출국한다. 정서(박신혜)는 그에게 작별인사를 하고 그녀에게 사랑을 고백하기 위해 시간에 맞춰 도착하고 그는 그녀에게 목걸이를 선물한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7035, 26002, 12710, '드라마에서 이 놀이동산은 차송주(권상우)와 한정서(최지우)의 만남의 장소로 등장한다. 1화에서는 어린 송주(백성현)와 정서(박신혜)가 미국으로 떠나기 전에 이곳에서 만나고, 12화에서는 두 사람이 어른이 되어 이곳에 온다. 한유리(김태희)와 약혼한 후 그는 그녀에게 자신의 가장 친한 친구, 즉 연인이 되어 달라고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7036, 26455, 12710, '이 호텔은 천국의 계단에서 차송주 (권상우)의 가족 사업으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7037, 26019, 12710, '이별 후 차송주(권상우)와 한정서(최지우)는 다시 서로를 향해 달려가다가 이 역의 엘리베이터 밑에서 서로를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7038, 32532, 12710, '천국의 계단 12회에서 차송주(권상우)는 한정서(최지우)에게 친구로 지내자고 한 후 그녀를 역에 데려다 주겠다고 제안하고, 그녀는 거절하지만 그는 그녀를 뒤쫓고 역에 도착한 후 그녀의 얼굴에서 비를 닦고 손수건으로 그녀를 둡니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7039, 27597, 12710, '이 저택에 어른 차송주(권상우)가 산다. 12화에서 한태화(신현준)가 한유리(김태희)와의 약혼에 대해 그를 대면하기 위해 찾아온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7040, 25931, 12710, '한태화(신현준)와 한정서(최지우)는 천국의 계단 13-14회에서 이 역에 도착한다. 차송주(권상우)는 이미 기찻길에서 그녀를 기다리며 그녀에게 사랑을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7041, 27565, 14172, '19회에서 윤차영(홍아름)은 진현웅(인교진)이 함께 웨딩드레스 피팅을 하러 왔다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7042, 32568, 19126, '극중 의뢰인(건물 벽화를 그려준 청년)의 아버지, 사라진 화가가 평소 좋아하는 곳이라 그림으로 그렸다는 연못');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7043, 26320, 19126, '1화에서 천지훈(남궁민 분)과 백마리(김지은 분)가 자신들이 대리하는 재판에 들어가기 전 건물 밖에서 인사하는 모습이 포착되는 법정으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7044, 25827, 19126, '11회에서 천지훈(남궁민 분)이 월남할 때 실제로 이곳에서 강의를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7045, 29455, 19126, '2회에서 백마리(김지은)는 그녀의 어머니와 이곳에 쇼핑하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7046, 28097, 19126, '이날 방송에서는 천지훈(남궁민)이 이주영(이청아)에게 이 식당에서 프러포즈를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7047, 32569, 19126, '천지훈 변호사의 사무실');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7048, 32570, 19126, '천지훈 변호사가 꼬마 의뢰인에게 천원짜리 아이스크림으로 수임료를 대신하는 가게');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7049, 25659, 19126, '11화에서 천지훈(남궁민)은  백마리(김지은)가 이 공원을 가로질러 팬 빌딩을 향해 안내한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7050, 30590, 19126, '천지훈(남궁민)은 앞서 이 대학 상상관에서 검사 나예진(공민정)과 토론을 벌인다. 그 뒤 이주영(이청아)이 나타나 본인을 지지하는 것을 듣고 밤에 이 건물 옥상 테라스에서 아버지를 조사할지 고민한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7051, 25335, 19126, '1화에서 천지훈 (남궁민)은 이 다리를 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7052, 26232, 19126, '천지훈(남궁민)은 1화에서 이 다리에서 목숨을 끊으려는 의뢰인을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7053, 24945, 19126, '1화에서 천지훈(남궁민)은 제일세탁소로 가는 길에 이 거리의 건널목에 들른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7054, 24927, 19126, '소매치기로 오해받아 구속된 소매치기 전과가 있는 의뢰인을 돕기 위해 갔던 사건현장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7055, 32571, 19126, '천지훈 변호사팀에서 사무장(박진우)의 본근무지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7056, 24948, 19126, '1회에서 무장(박진우)은 이 공원에서 깊은 생각에 잠긴 듯한 천지훈(남군 민)을 발견한다. 무장은 지훈에게 그들이 어디론가 가야 할 필요가 있으니 내려오라고 말하며 새로운 고객을 얻었음을 암시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7057, 32572, 19126, '천변, 백시보, 사무장이 술잔을 기울이던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7058, 28424, 13536, '18화 이서연(수애)의 생일파티가 이곳에서 펼쳐진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7059, 26080, 17429, '철인왕후 주요 궁전으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7060, 25653, 17429, '철인왕후의 궁궐 일부로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7061, 32573, 17429, '김소용(신혜선)과 철종(김정현)은 이곳에서 차를 마시다 스핀킥으로 침입자로부터 자신을 보호하는 모습에 심장이 조금 뛴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7062, 26995, 17429, '20화에서 식물인간 상태에서 깨어난 장봉환(최진혁)은 병원을 탈출하고 결국 이 역 1번 출구를 나서게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7063, 25781, 17429, '1회에서 이 스위트룸에서 장봉환(최진혁)은 인터뷰를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7064, 25603, 17429, '철인왕후에서 장봉환이 도착했을 때의 첫 번째 미션은 호수에 물을 채우는 것이었고, 그것이 현재로 돌아가는 방법의 관문이라고 생각한다. 5~6회에서 그는 호수로 뛰어들고 돌아오려 했지만 이원범(김정현)에게 구해진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7065, 25865, 3992, '23회에서 성찬혁(최수종)과 이승연(이효경)이 데이트를 위해 이 공원을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7066, 26709, 16146, '마지막회에서 한송이(정채연)와 도현(정진영)은 함께 강릉으로 견학을 가기로 한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7067, 24928, 16146, '9회에서 여자친구 류세현(홍지윤)과 함께 여행을 떠난다. 수영장에서 도현과 송이의 첫키스는 태오에게만 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7068, 32574, 16146, '12화(시즌 2, 4)에서 한송이(정채연)와 윤태오(지수)는 서울에서 이 버스 터미널(\'전주 시외버스터미널\')에 도착한다. 돌아오는 길에 서도현(정진영)은 송이와 함께 서울로 돌아가기 위해 버스터미널에 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7069, 32575, 16146, '첫사랑은 처음이라서 2화에서 서도현(정진영)이 아르바이트를 하고 있는 편의점이다. 5회에서 한송이(정채연)는 윤태오(지수)와 치열한 농구 대결을 펼친 후 떡볶이를 들고 그를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7070, 25718, 16146, '10회에서 최훈(강태오)과 오가린(최리)은  가린의 가족 스태프들을 아슬아슬하게 탈출시킨 후 이 육교를 가로질러 달려간다. 훈은 숨을 돌리면서 가린에게 미안한 마음으로 돈이 솔깃해 자신의 행방을 알려준 사람이 바로 자신이었다고 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7071, 25824, 16146, '6화에서 이 건물은 배우 최훈(강태오)이 계약을 위해 이곳을 찾아오면서 배우가 아닌 대체 배우로 영입한 탤런트 컴퍼니 \'액트뮤지컬컴퍼니\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7072, 25635, 16146, '3회에서 최훈(강태오)은 드라마 엑스트라로 이 카페 겸 레스토랑에서 공연을 하고 오가린(최리)을 데려와 그녀도 약간의 돈을 벌게 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7073, 32576, 16146, '6회에서 윤태오(지수)는 류세현(홍지윤)과 함께 책을 사기 위해 온다. 여기에서 세현은 아버지 태오에게 출판된 사진집을 보여주며 지혜의 진주 중 하나를 공유한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7074, 32577, 16146, '5화에서 도현(정진영)은 이 건물 앞 이 언니네 푸드트럭 가판대에서 한송이(정채연)를 위해 츄러스를 샀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7075, 25726, 16146, '4, 5, 7회에서 한송이(정채연)가 집으로 가는 길에 이 두 가게를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7076, 24939, 16146, '한송이(정채연)는 4회에서 윤태오(지수)에게 같이 등교하는 것을 거부 당한 후 집에서 이 거리로 걸어나온다. 태오는 그의 스쿠터를 타고 그녀 곁을 빠르게 지나갔고, 그녀는 그가 믿을 수 없다는 듯이 떠나는 것을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7077, 32578, 16146, '윤태오(지수)가  3화에서 류세현(홍지윤)을 위해 서도현(정진영)과 함께 과일 타르트를 타고 온다. 기다리는 동안 그들은 한송이(정채연 분)에 대해 이야기하고, 태오는 한송이가 연애를 시작하면 더 이상 그녀를 돌볼 수 없기 때문에 누군가가 그녀와 빨리 사귀기를 바란다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7078, 25661, 16146, '7화에서 데이트의 일환으로 즐거운 그림 그리기 활동을 마친 한송이(정채연)와 서도현(정진영)이 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7079, 32579, 16146, '9회에서 서도현(정진영)이 한송이(정채연)를 이 음반가게에 데려간다. 그리고 그녀가 레코드를 들을 때 그가 그녀에게 키스한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7080, 27119, 16146, '13회에서 윤태오(지수)와 서도현(정진영)은 이 터널에서 서로의 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7081, 26248, 16146, '6화에서 최훈(강태오)과 오가린(최리)이 이 공원을 걷는다. 9회에서는 서도현(정진영)과 윤태오(지수)가 이 공원 계단으로 나와 술을 마시며 담소를 나눈다. 태오는 송이와 \'20년이 넘는 우정\'을 바탕으로 도현과 송이에 대해 더 많은 이야기를 나누자고 제안하지만, 도현은 "연애는 사람을 알아가는 것이 전부"라며 정중하게 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7082, 32580, 16146, '윤태오(지수)가 선배에게 괴롭힘을 당하는 한송이(정채연)를 구한 후, 둘 다 9회에서 술을 마시러 카페로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7083, 30590, 16146, '이 대학의 일부는 윤태오(지수), 도현(정진영), 한송이(정채연)가 다니는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7084, 32581, 16146, '첫사랑은 처음이라서의 4화에서 윤태오(지수)는 한송이(정채연)를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7085, 25668, 16146, '2화에서 힘들고 외로운 한송이(정채연)가 이 다리를 건너고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7086, 32582, 16146, '첫사랑은 처음이라서의 여러 장면에 사용되었다. 시즌 1 3화에서 윤태오(지수)와 한송이(정채연)는 송이가 힘든 시기를 겪고 있다는 사실을 알게 된 태오가 자신에게 말하지 않고 그에게 도움을 청한다. 시즌 1 6화에서 태오와 송이는 학교 운동장 미끄럼틀에 앉아 아이스크림을 먹으며 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7087, 32583, 16146, '한송이(정채연)는 15회에서 어머니와 시간을 보내고 제주도에서 돌아온 윤태오(지수)에게 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7088, 25015, 16146, '1회에서 서도현(정진영)과 윤태오(지수)가 이 식당 \'판타스틱 루프탑\' 테라스에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7089, 25797, 16146, '6화에서 윤태오(지수)와 류세현(홍지윤)은 데이트를 결심한 뒤 자신의 스쿠터를 타고 이 다리를 건너 집에 데려다준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7090, 24945, 16146, '2화에서 윤태오(지수)는 집으로 가는 길에 지나가는 기차 때문에 이 거리의 기찻길 앞에서 멈춰야 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7091, 25910, 16146, '15회에서 서도현(정진영)이 아버지(박수영)와 함께 이 곳에 와서 술을 마시며 진솔한 이야기를 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7092, 29230, 16146, '이곳은 한송이(정채연)가 사는 식당이다. 한송이(정채연)는 아르바이트를 하고(1,2,5화 참조) 윤태오(지수)는 2화에서 그녀를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7093, 26577, 16146, '윤태오(지수)와 한송이(정채연)는 1회에서 태오의 스쿠터를 타고 등교길에 이 거리의 보성여자중고입구 버스정류장을 지나간다. 15화에서 다시 잠깐 등장하는데 태오와 송이가 만나는 계단 바로 옆이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7094, 26403, 16146, '찻집은 한송이(정채연)의 집(아버지가 지은 집)으로 등장하며, 1화에서 쫓겨난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7095, 26516, 16146, '11화에서 한송이(정채연)가 이 꽃집에 온다. 주인에게 최근에 어머니를 본 적이 있는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7096, 32584, 16146, '7화 프로젝트 모임에서 한송이(정채연)는 동료 학생들과 함께 이곳에 온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7097, 32585, 16146, '6회에서 긴 스쿠터를 탄 후 윤태오(지수)와 서도현(진영)이 이곳에 들러 담소를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7098, 25340, 16146, '1화의 회상 장면에서 볼 수 있듯이 윤태오(지수)와 한송이(정채연)는 둘 다 이 학교를 다녔다. 3회에서 태오는 류세현(홍지윤)이 집까지 데려다 주는 게 싫다고 말해서 이곳에 내려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7099, 27092, 16146, '첫사랑은 처음이라서 9회에서 데이트를 하다가 H&M 맞은편 푸드트럭 앞에 멈춰서 계란빵을 샀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7100, 25856, 16146, '16화 윤태오(지수)와 한송이(정채연)가 마지막 심야 쇼핑을 마치고 이곳을 산책한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7101, 24953, 16146, '9화에서 서도현(정진영)과 한송이(정채연)가 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7102, 25913, 16146, '2화에서 오가린(최리)의 어머니가 묵는 호텔로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7103, 25017, 16146, '1회에서 오가린(최리)이 이 곳에 도착하고 윤태오(지수)가 있는 곳으로 어머니가 보낸 경호원 2명을 피한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7104, 32586, 16146, '한송이(정채연), 서도현(정진영), 윤태오(지수)는 첫사랑은 처음이라서 12회(시즌 2, 4)에서 전주에서 이 버스 터미널에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7105, 32587, 16146, '12회에서 한송이(정채연)가 어머니와 함께 있는 모습을 본 후 어머니와 이야기하기 위해 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7106, 25676, 16146, '전주에 도착한 윤태오(지수)와 한송이(정채연)는 12화에서 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7107, 32567, 13779, '16회에서 한세경(문근영)은 이 백화점을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7108, 32588, 13779, '한세경(문근영)은 최종회 16회에서 이 지하철역 7번 출구를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7109, 26697, 13779, '이 건물은 청담동 앨리스에서 차승조가 운영하는 명품 브랜드 아르테미스의 사옥으로 나타난다. 마지막 16회에서 한세경(문근영)이 면접을 보러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7110, 29411, 13779, '청담동 앨리스 8회에서 한세경(문근영)이 이 온천장에 숨는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7111, 32589, 13779, '최종화 16회에서 서윤주(소이현)와 토미홍(김지석)은 이 쇼핑몰 입구에서 우연히 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7112, 32590, 3317, '청춘3부에서 채희(황수정)와 현우(장동건)는 오후에 함께 이 음반가게를 지나고, 이 음반가게(지금은 존재하지 않음) 앞 횡단보도까지 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7113, 32591, 3317, '1화에서 미국에서 돌아온 채희(황수정)는 이 갤러리에서 작업한다. 이곳에서 현우(장동건)가 그녀와 오랜 세월 떨어져 지내다가 그녀를 처음 발견한다. 저녁에 갤러리에서 만나기로 약속했지만 현우는 약속시간에 늦게 도착한다. 2화에서 그는 약혼자와 함께 있는 채희를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7114, 26501, 3317, '청춘에서 그는 그녀가 일어나서 집으로 돌아가야 한다고 그녀에게 다가간다. 그녀가 머물 것을 주장하고 그에게 그녀를 내버려 두라고 말한다. 그는 집으로 돌아간다. 하지만 돌이켜보면 두 남자에게 원영이 괴롭힘을 당하는 모습을 보고 자신이 남자친구라는 사실을 알린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7115, 26652, 17203, '2회에서 사혜준(박보검)은 패션 디자이너 찰리 정(이승준)이 그를 이 클럽에 데려와 어떻게 그에게 고백했는지를 기억합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7116, 30830, 17203, '안정하(박소담)와 사혜준(박보검)은 저녁 데이트에 이 공원에 온다. 6회에서 이 공원의 피아노 앞에서 첫 입맞춤을 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7117, 27454, 17203, '이 장소의 정원은 12회에서 사혜준(박보검 분)이 허브빌리지에서 드라마 촬영을 할 때 등장한 곳이기도 하다. 메이크업 스타일리스트 여자친구 정하(박소담)는 조연으로 있는 절친 혜효(변우석)와 함께 현장에 도착하고, 장면 사이에 혜준이 어떻게 하면 피할 수 있을지 의논한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7118, 25827, 17203, '청춘기록 2회에 사혜준(박보검)이 참석하는 밀라노 패션쇼가 실제로 이 장소에서 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7119, 32592, 17203, '8회에서 안정하(박소담)와 사혜준(박보검)이 시승을 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7120, 25653, 17203, '10회에서 사혜준(박보검)이 이 세트에서 드라마를 촬영한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7121, 25720, 17203, '청춘기록 3화에서 사혜준(박보검)과 안정하(박소담)가 이 장소의 별마당 도서관[별마당 도서관]에서 만난다. 혜준은 7회와 8회에서 역할 공부를 위해 다시 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7122, 26038, 17203, '사혜준(박보검)이 12회에서 자신의 새 영화 \'첫 인간\' 홍보를 위해 이 호텔에서 인터뷰를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7123, 25726, 17203, '5회에서 사혜준(박보검)과 안정하(박소담)가 밤에 집을 나설 때 이 가게를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7124, 25874, 17203, '14회에서 안정하(박소담)와 사혜준(박보검)은 이 \'가게\'를 지나다가 \'커플문화\'를 이야기하다 그 앞에 멈춘다. 그녀는 무슨 수를 써서라도 다툼을 피하고 함께 발전하면 건강한 부부가 될 수 있다고 생각한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7125, 25661, 17203, '2회에서 안정하(박소담)는 저녁 이 거리에서 자신의 영상 블로그를 위해 무료 메이크업 조언을 해주었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7126, 26136, 17203, '배우 박도하(김건우)와 그의 매니저 이태수(이창훈)는 PD와 시나리오 작가(김로사)와 함께 이 식당에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7127, 32593, 17203, '4회에서 사혜준(박보검)과 안정하(박소담)가 오후 함께 이 건물에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7128, 27119, 17203, '4회에서 사혜준(박보검)이 술에 취해 집으로 돌아갈 때 안정하(박소담)가 이 터널의 남쪽 입구에서 허탈해 운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7129, 28474, 17203, '이 작은 거리는 안정하(박소담)가 사는 곳,  편의점 옆 건물이다. 1회에서 그녀는 직장 친구와 함께 길을 걷고 있는 모습, 자신의 영상 채널에 메이크업 팁을 전하며 셀카를 찍는 모습이 그려졌다. 5, 6회에서 사혜준(박보검)은 집 앞까지 걸어가는데도 불구하고 편의점 밖 비를 피할 수 없는 처지에 놓이게 된다. 혜준은 6회에서 정하에게 자신을 좋아한다고 인정한 후 근처 터널을 지나 집으로 걸어가는데, 뒤에서 정하가 기다리고 있는지 확인하기 위해 뒤돌아보지 않겠다고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7130, 26137, 17203, '사혜준(박보검)은 16화에서 이 호텔 컨벤션센터에서 사회복지사 명예홍보대사로 위촉되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7131, 32594, 17203, '6회에서 사혜준(박보검)과 그의 할아버지는 시니어 모델을 위한 훈련을 이 공원에서 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7132, 31787, 17203, '5화에서 사혜준(박보검), 안정하(박소담), 원해효(변우석)는 혜준의 매니저 이민재(신동미)와 함께 짜장면을 먹기로 한다. 혜준은 자신이 주차를 잘했다고 자부하며 조수석에 앉은 정해와 말다툼을 하고 민재는 그들을 진정시키려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7133, 25882, 17203, '12화에서 김진우(권수현)과 원해나(조유정)는 공원의 이 부분에서 만나 연애와 결혼의 장단점, 현실과 환상의 세계에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7134, 25335, 17203, '청춘기록 3회에서 안정하(박소담)의 버스가 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7135, 27372, 17203, '혜준이 드라마 게이트웨이에 캐스팅되었다는 소식을 접한 직후, 8화에서 절친한 친구인 원해효(변우석)와 사혜준(박보검)은 이 육교를 지나 중간에 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7136, 32595, 17203, '사혜준(박보검)이 출퇴근길에 자주 지나치는 동네 편의점으로 등장한다. 1,2,3회에서 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7137, 26737, 17203, '최종회 16회에서 사혜준(박보검 분)이 안정하(박소담 분)를 이 빈 주차장으로 태워다 주며, 헤어진 이유에 대한 답이 필요하고, 또 다시 재결합을 시도한다. 정하는 그의 경력에서 이미 많은 것을 성취한 반면, 그녀는 여전히 그녀의 사업이 성공하기 위해 노력하고 있었기 때문에 그들의 시기가 잘못되었다고 생각했다. 혜준은 그녀에게 사랑을 고백하며 애원하지만, 정하는 그들이 함께 했던 아름다운 시간들을 떠올릴 뿐, 그를 외면한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7138, 32596, 17203, '14회에서는 사혜준(박보검)이 부자 친구 원해효(변우석)에게 자신의 삶에 대해 이야기하는 모습이 그려졌다. 16화에서 혜준은 다시 옥상에 올라가 그 아래 펼쳐진 한남동을 바라보며 자신이 꿈을 이뤘음을 깨닫고 마지막으로 해야 할 일인 입대를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7139, 32597, 17203, '1회에서 사혜준(박보검)은 친구 김진우(권수현)를 속여 저녁 조깅을 하게 된다. 이 동네에 사는 이 건물을 지나갈 때, 원해효(변우석)와 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7140, 32598, 17203, '사혜준(박보검)이 사는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7141, 27075, 17203, '1화에서 저녁 조깅을 마친 사혜준(박보검), 김진우(권수현), 원해효(변우석)는 이 공원에서 시간을 보낸다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7142, 32599, 17203, '2회에서 저녁 외식 후 이 거리를 사혜준(박보검)이 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7143, 26577, 17203, '1회에서 사혜준(박보검)이 탄 버스는 소월로 20-20으로 바뀔 때까지 이 거리를 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7144, 32600, 17203, '혜준(박보검)이 아르바이트를 하는 곳으로 1화, 6화에서 처음으로 등장했습니다. 혜준이가 워낙 유명해서 여자 손님이 잘생긴 알바생을 보러 많이 오게 됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7145, 25856, 17203, '1~2회에서 사혜준(박보검)과 김진우(권수현)가 모델로 나서는 \'옴므정 패션쇼\'가 이곳에서 펼쳐진다. 안정하(박소담)가 짝사랑하는 혜준을 메이크업 아티스트로 처음 만나는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7146, 25913, 17203, '원씨 가족은 원해나(조유정)가 5화에서 법학부에 합격한 것을 축하하기 위해 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7147, 31828, 17203, '마지막 16회에서 사혜준(박보검)이 \'사랑은 비와 함께 온다\'라는 로맨틱 드라마를 촬영하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7148, 25956, 17203, '3회에서 사혜준(박보검)과 안정하(박소담)가 이 장소를 지나간다. 같은 우산을 쓰고 서로 시시덕거린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7149, 30523, 17203, '3회에서 김진우(권수현)와 원해나(조유정)는 저녁 데이트에 이 식당에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7150, 25017, 17203, '2회에서 사혜준(박보검)과 그의 새 매니저 이민재(신동미)가 밀라노 여행을 마치고 귀국해 공항에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7151, 25673, 17203, '사혜준(박보검)은 9회에서 이 세트장에서 드라마 촬영을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7152, 28474, 14741, '정기적으로 (2화부터) 보여진다. 경제적으로 넉넉한 학생 이나(화영)가 차를 타고 편안하게 이동하는 모습, 집으로 걸어갈 때나 아르바이트를 할 때면 항상 이곳에서 동거인이자 학생인 진명(한예리)을 지나친다. 하지만, 이나는 그녀의 친구를 태워주기 위해 차를 멈추지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7153, 25871, 14741, '청춘시대1에서 한예리가 일한 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7154, 32601, 14741, '청춘시대1에서 박혜수, 신현수가 간 술집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7155, 26399, 14741, '청춘시대1에서 한승연, 지일주가 간 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7156, 24957, 14741, '청춘시대1에서 한예리가 다닌 대학교');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7157, 29247, 16194, '이 휴양지에서 베로니카 박(김재경)이 기다리고 있다. 20화(전체 10화)에서 대우(구자성)에게 좋은 저녁을 함께 보내려 하지만 그는 약속을 취소하기 위해 전화를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7158, 26148, 16194, '정갈희(진기주 분)가 24회에서 \'베로니카 박\' 도민익(김영광 분)과 헤어지기를 기다리는 곳은 이 놀이공원 회전목마 앞이다. 그러나 민익이 도착했을 때는 이미 모든 것을 정리하고 속아서 가슴이 찢어지는 이곳을 떠난 뒤였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7159, 25906, 16194, '18회(전체 9회). 또다시 도민익(김영광)과 정갈희(진기주)가 가까워지고 심장이 두근거리며 다시 얼굴을 보게 만든다. 자신의 침략자를 알아차리고 뒤를 쫓는 그는 이 회장 밖에서 베로니카 박(김재경)의 제지와 경례를 받았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7160, 26080, 16194, '27화(전체 14화)에서 베로니카 박(김재경)은 휴식을 취하러 이곳에 오고, 기대주(구자성)는 그녀를 찾으러 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7161, 32622, 16194, '21화(전체 11화)에서 기대주(구자성)가 캡핑 낚시 여행을 오고 베로니카 박(김재경)이 그의 마음을 얻기 위해 서프라이즈로 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7162, 25870, 16194, '도민익(김영광)은 자신의 비서인 정갈희(진기주)의 요구를 그녀가 자신이 아닌 줄로 착각해 오해한다. 1화(전체 에피소드 1)에서 이 시설에서 회사의 운동회 행사에 함께 참여하기를 주장하지만 갈희가 진정으로 원했던 것은 결국 정규직 계약이었다. 대신 사건 직후 해고됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7163, 25613, 16194, '도민익(김영광)과 정갈희(진기주)는 15화에서 이 식당으로 점심을 먹으러 오지만 여전히 고군분투하는 동료들과 생각에 잠겨 저녁을 그렇게 많이 즐길 수 없다.민익은 그녀에게 그들과 함께 하라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7164, 24902, 16194, '이 식당의 외관은 베로니카 박(김재경)이 있는 식당이며, 15화(전체 8화)에서 고려(구자)와 저녁 식사를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7165, 26410, 16194, '1회에서 기대주(구자성)는 회사에 도민익(김영광)의 삼촌 심해용(김민상)의 초대를 받아 이곳에서 저녁식사를 한다. 해영은 물고기 머리를 대주의 접시에 담을 때 머리를 한 번 맛보면 다시는 꼬리를 먹지 않는다고 설명한다. 13화(7화)에서 해용과 대우 다시 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7166, 25660, 16194, '도민익(김영광)과 정갈희(진기주)는 14화(전체 7화)에서 갑자기 내린 비로 길 끝에 주둔해 있는 트랙터 근처로 피난처가 될 때 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7167, 25877, 16194, '8화에서 이 식당은 베로니카 박(김재경)과 기대주(구자성)가 저녁을 먹는 식당으로 등장하며, 베로니카가 이보다 더 나아가지 않을 것이라고 말한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7168, 32221, 16194, '정갈희(진기주)가 해고된 후 이 식당에서 혼자 외식을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7169, 32623, 16194, '도민익(김영광)은 세븐일레븐에 오면 바로 이 버스 정류장을 바라본다 17화에서 정갈희(진기주)와 함께한 시간을 회상한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7170, 32624, 16194, '도민익(김영광)과 정갈희(진기주)는 \'해피24\' 편의점으로 등장해 이 편의점에 온다 15화(전체 8화)에서 12시간 동안의 열애 중 민익이에게 커피를 만들어주는 것이 소원이다. 17회(전체 9회)에서는 가슴 아픈 민익이 이 데이트를 회상하며 돌아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7171, 26542, 16194, '이 건물 외관은 도민익(김영광)이 운영하는 티앤티모바일의 본사로, 정갈희(진기주)가 비서로 일하고 있다. 이 건물의 콘서트 홀 층에 있는 옥상 테라스는 민익과 그의 절친한 친구인 기대주(구자성)의 정기적인 만남의 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7172, 25347, 16194, '13회(전체 7화)에서 도민익(김영광)이 오후에 혈압을 올리기 위해 이 공원을 질주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7173, 25854, 16194, '도민익(김영광)이 그의 절친인 기대주(구자성)와 어렸을 때부터 만났던 비밀 은신처이다. 3회에서 볼 수 있듯이 이 클럽에 머물러 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7174, 25015, 16194, '16화(전체 8화)에서 이 식당 2층 옥상에서 도민익(김영광)와 정갈희(진기주)가 키스한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7175, 24948, 16194, '11회에서 기대주(구자성)는 베로니카 박(김재경)에게 이 공원에서 만나자고 요청하며 다가오는 티앤티모바일 주주총회를 위해 대리서명을 요구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7176, 26214, 16194, '4회(전체 2회)에서 해고된 후 정갈희(진기주)는 하루를 쉬며 인생을 즐기고 서울을 거닐었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7177, 26226, 16194, '정갈희(진기주)와 동생 정중희(서동원)는 동생 정남희(김지민)의 연극 무대를 축하하기 위해 이 가게에서 꽃을 사기로 한다. 28회(전체 14화)에서 질투하는 도민익(김영광)이 그들을 멀리서 관찰한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7178, 32625, 16194, 'T&T 비서들이 함께 이야기를 나누며 시간을 보내는 점심식사 장소이다. 5화와 9화에서 그들은 여기서 만난다. 13화에서 도민익(김영광)과 정갈희(진기주)는 우연히 이곳에서 만나고 나중에 그녀는 자전거를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7179, 27228, 16194, '15화(전체 8화)에서 베로니카로 변장한 도민익(김영광)과의 저녁 데이트 후, 정갈희(진기주)는 민익에게 불륜을 하고 싶냐고 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7180, 26745, 16194, '도민익(김영광)은 6회에서 이 공원에서 차에서 내리고 킹덤 호텔에서 소개팅과 함께 저녁 약속 장소로 달려가기 위해 운전기사를 남겨두고 간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7181, 26624, 16194, '이 병원의 외관은 도민익(김영광)이 의사 삼촌 구석찬(김병춘)에게 치료를 받는 상모병원으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7182, 25799, 16194, '17회(전체 9화)에서 정갈희(진기주)가 집으로 가는 길에 이 집으로 올라가는 계단을 올라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7183, 26125, 16194, '10-12회에서 티앤티모바일 주주총회 도민익(김영광) 직위를 이 방에서 투표한다. 베로니카 박(김재경)이 참석했다. 민익은 정갈희(진기주)를 투표소로 끌어들이고 함께 투표를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7184, 24925, 16575, '2회에서 이강(윤계상)과 문차영(하지원)은 어른이 되어 처음으로 이곳에서 다시 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7185, 30786, 16575, '16회에서 문차영(하지원)이 이 교회에 기도하러 온다. 이강(윤계상)은 문간에서 그녀를 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7186, 25827, 16575, '2회에서 문차영(하지원)의 쌍둥이 형 문씨가 만난 그리스 경찰서로 등장한 이 건물이다. 태현(민진웅)이 만나기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7187, 26539, 16575, '이씨 가족이 운영하는 \'거성대병원\'으로 등장한다. 첫 회에서 이강(윤계상 분)과 이준(장승조 분)이 일하고 문차영(하지원 분)이 치료받는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7188, 24946, 16575, '3회에서 이강(윤계상)은 \'스파이&파티룸\' 바에서 절친 권민성(유테)과 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7189, 26544, 16575, '1회에서는 2013년을 맞이하는 종소리 리얼 라이브 영상이 공개됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7190, 32626, 16575, '12회에서 문차영(하지원)이 이강(윤계상)을 기다리며 벤치에서 잠드는 \'해변공원\'이다. 그가 그녀를 찾으러 왔을 때, 그녀는 여전히 자고 있다. 그는 그녀를 재킷으로 덮고 그녀가 깨어날 때까지 4시간 동안 어깨에 기대고 계속 자게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7191, 32565, 16575, '이 해변에서 문차영(하지원)은 14회에서 이강(윤계상)에게 그녀의 첫사랑에 대한 \'긴 이야기\'를 들려준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7192, 25607, 13577, '진진심(왕지혜)과 한 태양(지창욱)이 최종화 24화에서 이 공원에서 데이트를 하러 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7193, 26320, 13973, '15화에서 남다정(임윤아)이 이 건물에서 만나기로 한 권율(이범수)에게 약속을 한다. 하지만 실제로 그는 그곳에서 그를 기다리는 아내 박나영(정애연)을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7194, 25721, 13973, '4회에서 강다미(민도희)와 그녀의 친구 윤성현(려운), 이하림(진예주)은 대학 입학 이력서를 쓰기 위해 이 카페를 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7195, 24913, 13973, '15화의 남다정(윤아)과 권율(이범수)은 이 거리를 걷는다. 데이트에서 그녀는 그에게 헤어지면 어떻게 되냐고 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7196, 32627, 15164, '16회에서는 현수(윤정일)가 어머니(이항나)에게 강수(고경표)의 집에 있는 이유를 놓고 대립하는 장면이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7197, 32628, 15164, '16회에서 회사의 생계를 위협하는 큰 어려움에 직면한 후 위안을 찾기 위해 최강수(고경표)가 고향으로 돌아온다. 이단아(채수빈)는 서울로 돌아가라고 설득하기 위해 찾아오고, 그의 어머니(이항나)도 그의 상황을 듣고 몰래 그를 방문하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7198, 32629, 15164, '4회에서 강수(고경표)는 마트 밖에서 공지(김기두)와 그의 부하들을 만난다. 공지는 강수를 한양 설렁탕에서 만찬에 초대하여 그를 환영한다. 그러나 강수는 그에게 한양이 임시로 문을 닫았다고 알린다. 이어 주인의 손자인 현수(윤정일)의 사고 소식을 전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7199, 32630, 15164, '4회에서 오진규(김선호)의 차고 배경이었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7200, 27150, 15164, '12회에서 \'길동역\'(5호선 실제 역)으로 등장했는데, 그곳에서 최강수(고경표), 이단아( 채수빈)을 비롯한 최강 배달부 일행은 자신들의 새로운 배달 사업을 홍보하기 위해 전단지를 전달했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7201, 32631, 15164, '이 식당은 최강수(고경표)의 주적 역으로 \'정패밀리 설렁탕\'의 한 지점으로 등장한다. 이지윤(고원희)의 어머니가 운영하는 체인으로, 오진규(김선호)가 6회부터 지점장으로 일하다가 11회에서 그만둔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7202, 32632, 15164, '5회에서는 최강수(고경표)와 배달부들이 현수(윤정일)의 히트작을 알아보기 위해 거리를 행진하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7203, 32633, 15164, '12회에서 최강 배달꾼 팀이 민찬(허지원)의 고향인 매향리에서 MT를 하던 중 이곳에 들렀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7204, 32634, 15164, '12회에서는 배달부 일행이 모두 매향리까지 MT를 타고 매향리로 향하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7205, 25834, 15164, '3화에서 오진규(김선호)와 이지윤(고원희)이 이곳에서 회식을 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7206, 26617, 15164, '오진규(김선호) 아버지의 회사 본사이다. 진규는 2화에서 사무실에서 아버지에게 심한 구타를 당하고, 회사 건물 밖으로 내동댕이쳐진다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7207, 32635, 15164, '동네 배달부 단장 백공기(김기두)의 본거지인 중국집 \'성국\'이다. 1회 말과 2회 초반에 최강수(고경표)와 대결을 펼치는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7208, 32636, 15164, '이 건물은 이단아(채수빈)가 머물렀던 곳이다. 룸메이트 최연지(남지현)와 함께 옥상에서 살았다. 12, 13회에서는 이지윤(고원희)이 마지못해 오진규(김선호)를 끌고 3층으로 이동하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7209, 32637, 15164, '이곳은 16회에서 성재(김경남)가 고객층을 확대한 후 배달을 위해 꽃을 줍던 꽃집이었다. 배달을 좀 더 친절한 얼굴로 받아 달라는 플로리스트의 요청에 그는 이곳에서 묘한 미소를 지었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7210, 32638, 15164, '최강수(고경표)와 이단아(채수빈)가 일하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7211, 32639, 15164, '8회에서 강수(고경표)와 단아(채수빈)가 영화 데이트를 마치고 이 거리에 수직인 보도를 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7212, 31785, 15164, '2회 엔딩에서 이 다리에서 뛰어내려 자살하고 아빠의 \'마지막 명령\'을 따르고 싶다. 아직도 자신을 질책하는 동안, 단아(채수빈)가 지나가면서 그를 설득하기 시작하지만, 그는 미끄러져 결국 둘 다 한강에 빠진다. 그리고 우리는 단아가 한때 진규와 정확히 같은 장소에 있었다는 것을 플래시백으로 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7213, 26086, 15164, '최종회 16회 단아(채수빈)는 에필로그에서 이곳 대학에 진학한다. 그녀는 학생회 건물에서 나오고 강수(고경표)는 그곳에서 그녀를 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7214, 26027, 15164, '경찰로부터 도주하는 동안 오진규(김선호)는 운전을 한다 5회에서는 이단아(채수빈)와 함께했다. 5화 후반부에서 단아가 이 다리 밑에서 테이크아웃 전단을 나눠주는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7215, 25929, 15164, '1화에서 오진규(김선호)가 반포대교를 운전할 때, 배경으로 건물이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7216, 26470, 15164, '11회에서 정패밀리를 그만뒀다. 정혜란 회장(김혜리)의 계략에 빠져 괴로워한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7217, 32640, 15164, '오진규(김선호)는 광진대교에서 뛰어내려 자살을 시도하고, 이단아(채수빈)는 그를 구한 후 잠실대교 옆 편의점으로 오게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7218, 26992, 15164, '최강수(고경표)가 4, 5회에서 배달 일을 하며 만난 모든 친구들을 만나 이곳에서 자동차 경주의 범인을 찾도록 도와달라고 부탁했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7219, 32641, 15164, '7회에서 최강수(고경표)가 술에 취한 이단아(채수빈)를 집으로 데려다준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7220, 32642, 15164, '최강수(고경표)의 엄마 가게이다. 최강수는 11회에서 좋은 친구(나중에 이복형제임을 알게 됨)를 방문하기 위해 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7221, 25809, 15164, '7회에서 최강수(고경표)는 이곳에 수감되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7222, 25877, 13819, '2화에서 이혜신(손태영)은 미국에서 돌아온 직후 이곳에서  친구들과 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7223, 26679, 13819, '신준호(조정석)는 3회에서 신이정(배거린 분)에게 이 카페에서 특정 기획사와 계약하라고 경고한다. 그리고 그녀는 그를 믿지 않고 그가 여전히 그녀에게 감정을 가지고 있다고 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7224, 25937, 13819, '21화에서 신준호(조정석)가 이순신(이지은)는 이 가게 앞에서 그녀를 잡아채고, 23화에서 서진욱(정우)과 이혜신(손태영)이 밤에 이곳을 지나다가 마침내 그녀에게 자신을 소개한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7225, 27092, 14148, '1회에서 차기영(박시연 분)이 선보인 CKTV \'뉴스\'에 출연해 젊은 행인들이 결혼에 대한 인터뷰를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7226, 26636, 13463, '16화에서 이 버스 정류장에서 기다리는 사람들이 모두 독고진(차승원)의 영상을 보고 공유한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7227, 26990, 13463, '7,9,12화에서 볼 수 있다. 9화에서 제니(이희진)가 이승기(이승기 카메오)를 발견하고 두 사람이 같은 스카프를 두르는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7228, 25607, 13463, '독고진(차승원)은 구애정(공효진)을 붙잡으려 하고 키스로 심장 박동수를 테스트한다. 그러나 애정이 윤필주(윤계상)와 함께 무대에 오르게 하고 그와 함께 새로운 커플로 축하받는 것은 아무 소용이 없다. 바로 직전(10화) 강세리(유인나)는 필주에게 사랑을 고백해 그를 막으려 했지만 소용이 없었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7229, 26152, 13463, '이 호텔의 외관은 독고진(차승원)과 구애정(공효진)이 함께한 장소에서 나타난다. 16화에서 결혼이 이루어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7230, 26282, 13463, '10회 독고진(차승원)이 영화 \'파이터\'로 노미네이트 된 시상식이 이 장소에서 진행된다. 여기에 구애정(공효진)의 전 매니저 상(정만식)이 아역상을 주지 못하도록 개입하고, 고진은 매니저가 이를 알게 되자 물리적으로 갚는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7231, 32643, 13463, '15회에서 자신의 진료소에 약초를 사러 갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7232, 32644, 13463, '애정(공효진)이 사는 집이다. 10, 11, 13회에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7233, 25722, 13463, '7회에서 구애정(공효진)과 윤필주(윤계상)과 진심어린 이야기를 이곳에서 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7234, 26002, 13463, '1회에서는 구애정(공효진)이 이 공원에서 촬영하는 에피소드로 그녀의 마지막 도전은 롤러코스터를 타고 머물기 위해 라면 한 그릇을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7235, 27554, 13463, '5화에서 독고진(차승원)은 이 공원의 \'경관명소\'(우수경관명소)에서 벚꽃 아래에서 구애정(공효진)에게 사랑을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7236, 26285, 13463, '문 대통령(최화정)은 독고진(차승원)과 구애정(공효진)을 초대한다. 7회에서 이 식당으로 찾아와 갈등을 해소하지만 그녀가 식사를 하기로 되어 있는 개인방을 나오자 독고진과 구애정은 자신의 음반 계약을 놓고 다시 논쟁을 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7237, 26155, 13463, '15화에 MBS 방송사로 출연한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7238, 25335, 13463, '독고진(차승원)은 1회에서 밴에 실려 이 다리를 건너다 매니저로부터 주연배우 김준성(김남길)과 함께 밴 스크린에서 보는 드라마보다 자신의 영화 \'피겨\'가 더 좋은 시청률을 보였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7239, 32645, 13463, '윤필주(윤계상)의 진료소로 등장한다. 1,2,9,13화에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7240, 26501, 13463, '최고의 사랑 10회에서 독고진(차승원)이 그의 영화 \'피터\'로 노미네이트되는 시상식이다. 이 장소에서 진행된 \'대한민국 영화대상\' 실화 영상과 함께 소개된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7241, 26245, 13463, '16회에서 두 사람은 이곳에서 훈훈한 대화를 나눴고 그는 곧 유학을 떠난다는 사실을 그녀에게 알린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7242, 32234, 13463, '최고의 사랑에서 제니(이희진)가 운영하는 곳이다. 예를 들어 8화에서 강세리(유인나)가 그녀를 방문하는 장면에서 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7243, 32646, 13463, '13화에서 윤필주(윤계상)와 전 걸그룹 국보녀(내셔널 트레져걸스)의 한미나(배슬기)를 만나러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7244, 26624, 13463, '독고진(차승원)은 이 병원에서 치료를 받는다. 2화에서 그는 \'교통사고\'로 치료를 받기 위해 이곳에 남았지만, 이는 사실상 영화 제의를 철회할 수 있는 기회이다. 3화에서는 심장에 이상이 생긴 것 같아 이 병원에서 심전도 검사를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7245, 25773, 16211, '1화에서 고민채(최희진)는 1년간 해외로 출국한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7246, 26263, 16211, '1화에서 최웅(정건주)이 고민채(최희진)와 이 거리에서 이별한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7247, 25802, 15800, '16회에서 강휘루(배두나)와 조석무(차태현)가 이 길을 걷고 다시 감정을 느낀다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7248, 26139, 15800, '강휘루(배두나)의 여동생 강마루(김혜준)는 친구와 함께 이 옥탑방에서 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7249, 25797, 15800, '1회에서 조석무(차태현)는 직장 의뢰인을 찾아 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7250, 25787, 15093, '11화에서 최우승(이세영)은 본 방송국 3번출구에서 아르바이트를 위해 동물 복장을 한 전단지를 배포한다. 이후, 그녀는 유현재(윤시윤)에게 자신을 도와달라고 부탁하고, 그의 빚을 일부 갚는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7251, 31972, 15093, '8회에서 유현재(윤시윤)가 화장실 갈 때 그의 노래 중 하나가 재생되고 이지훈(김민재)을 여기에서 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7252, 25795, 15093, '3화에서 유현재(윤시윤)가 시간여행을 했을 뿐이라고 정말 이해하는 대목이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7253, 25877, 15093, '25화에서 유현재(윤시윤)는 최우승(이세영)과 그녀의 전 절친을 이 식당으로 데려간다. 현재는 전 여자친구가 초대할 거라고 믿게 만들지만, 그와 우석은 사막을 마친 후 계산서를 그녀에게 맡긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7254, 25335, 15093, '이광재(차태현)는 7회에서 아버지 이순태(이덕화)와 소속 연예기획사의 두 스타와 함께 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7255, 25668, 15093, '16회 말미 1990년대 회상 장면에 등장하는 장소입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7256, 25347, 15093, '4화에서 2017년에 도착한 유현재(윤시윤)는 이 공원에서 자신의 차에서 잠을 잔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7257, 26250, 15093, '4회에서 유현재(윤시윤 분)가 2017년으로 가는 시간여행을 받아들이면서 잠시 자리에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7258, 26017, 13284, '추노 7회에서 송태하(오지호)는 아픈 김혜원/운년(이다해)을 이곳으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7259, 27086, 13284, '4화에서 김혜원(이다해)은 죽은 자를 기리기 위해 이 절에 온다. 그녀가 사랑하는 이대길(장혁). 송태하(오지호)는 이튿날 아침 이곳에서 길을 건너 그녀를 납치하려는 두 남자로부터 그녀를 구하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7260, 25673, 13284, '롱꼬도/용과다(청장군) 잉글다이/용골대(윤동환)를 궁술 게임을 하기 위해 만난다. 13화에서 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7261, 26698, 15032, '13화에서 유설옥 (최강희)이 새 우산을 들고 이 카페를 지나간다. 한편 남편 김호철(윤희석 분)은 젊은 여자와 함께 이 카페에 앉아 있지만, 설옥은 우산에 만족해하며 이들을 눈치채지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7262, 32112, 15032, '하완승 형사(권상우)와 유설옥(최강희)은 에피소드에서 프러포즈가 실패한 후 레스토랑 퀴송을 지나기 직전 이 가게를 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7263, 32647, 15032, '1화에서 프러포즈가 실패한 뒤 이 카페를 지나기 직전 형사 하완승(권상우)과 유설옥(최강희)이 보니스팍스 백현동점을 지나게 된다. 그 동안 둘 다 손을 잡고 싶지만 이루어지지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7264, 31900, 15032, '7회에서 하완승(권상우)과 유설옥(최강희)이 연쇄살인마의 희생양이 된 시댁을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7265, 32648, 15032, '7회에서는 연쇄 살인범의 희생양이 된 시댁을 찾는 하완승(권상우)과 유설옥(최강희)의 모습이 그려졌다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7266, 32649, 15032, '16회에서 하완승(권상우)이 운동을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7267, 32650, 15032, '김경미(김현숙)의 식당으로 등장한다. 그녀의 가장 친한 친구인 유설옥(최강희)은 이곳에서 아르바이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7268, 32651, 15032, '13회에 시니어 센터로 등장한다. 여성 중 한 명이 독살당하면서 7부대에 대한 새로운 사건이 시작되고, 14화에서 유설옥(최강희)에 의해 결국 해결된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7269, 32083, 15032, '이곳은 드라마에서 \'서동경찰서\'로 등장한다. 등장인물들이 주로 나타나는 메인 촬영지중 하나이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7270, 32114, 15032, '유설옥(최강희)은 1화에서 그를 추적하기 위해 \'퀸즈 웨딩 하우스\'로 보이는 이 웨딩홀에서 사기꾼과 가짜 결혼한다. 이는 훗날 \'결혼 사기 사건\'으로 알려지며 설옥이 명예경찰이 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7271, 24922, 15032, '14회에서 유설옥(최강희)과 하완승(권상우)이 덕성여중 한복판에서 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7272, 32652, 15032, '8회에서 유괴범이 유설옥(최강희)의 시어머니를 데려온 곳이다. 그녀는 낭만적인 데이트 중이라고 생각한다. 이를 알게 된 설옥은 유괴범에게 기절당하고 결국 자신의 뒤를 쫓던 하완승(권상우)에게 구출된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7273, 25017, 15032, '6회, 7회에서 하완승(권상우)과 유설옥(최강희)은 연쇄살인범의 희생양이 된 시댁을 찾아 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7274, 32655, 17083, '8회에서 구역질을 하는 구세라(임진아)가 지역 대표 만찬을 마친 후 약국을 발견한 것은 이 지하철역(\'정의역\'으로 표시) 3번 출구이다. 그리고 약간의 안도를 위해 거기로 가기로 결정한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7275, 32656, 17083, '4회에서 혼자 배불리 먹었던 구세라(나나)는 지하철에서 내리던 중 이 약국(이하 \'립봉약국\')을 보고 나서기로 한다. 그곳에서 그녀는 만성위염 처방을 받으러 온 서공명(박성훈)과 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7276, 32657, 17083, '21화(전체 11화)에서 구세라가 윤희수를 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7277, 32658, 17083, '19화(전체 10화)에서 구세라(임진아/애프터스쿨 나나)와 서공명(박성훈)은 “사량서원”에 온다. 사랑동 장로(최종렬)를 만나기 위해 세라에게 사랑동 이름 변경에 반대하는 편지를 여러 차례 보냈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7278, 29154, 17083, '10회에서 조맹덕(안내상)과 원소정(배해순)이 \'스마트 원 시티\' 프로젝트에 대해 논의하기 위해 이곳에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7279, 29459, 17083, '14회에서 구세라(임진아/애프터스쿨 나나)와 서공명(박성훈)의 아버지가 이곳에서 만나 자녀 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7280, 24950, 17083, '5회 회상장면에서 어린 구세라(김하연 분)가 가출한 서공명(서윤혁 분)을 찾아 빗길을 걷는다. 그들의 아버지인 구영태(안길강)와 조맹덕(안내상)도 아이들을 찾아 이곳으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7281, 32659, 17083, '11회에서 구세라(임진아/애프터스쿨 나나)와 서공명(박성훈)은 각각 503번지 동네 대표 집을 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7282, 32660, 17083, '등장인물인 구세라(나나)가 사는 아파트로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7283, 26297, 17083, '3회에서 구세라(임진아/애프터스쿨 나나)의 엄마 김삼숙(장혜진)이 딸의 당선을 위해 이곳 계단에서 유세를 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7284, 25609, 17083, '11화에서 구의회 건물 밖에서 만난 권우영(김미수)과 김민재(한준우)는 마원신용협동조합 가입을 위해 이 카페로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7285, 32661, 17083, '1화, 2화에서 구세라(애프터스쿨 임진아/나나)가 밤에 지역구 대표후보로 직장인들 앞에서 자유발언을 하는 장면, 4화에서 일하는 사무실 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7286, 32662, 17083, '4화에서 구세라(나나)는 어린이 운전사들이 길을 건너는 것을 바라보며 서공명(박성훈)에게 엉뚱한 말을 한다. 여기서 그녀는 마원구가 적자로, 직원이나 시의원에게 급여를 지급하지 못할 수도 있음을 알게 된다. 에피소드의 에필로그 후반부에서 공명은 세라에게 의회에서의 결정이 얼마나 큰 일인지 설명하려고 하며, 공명은 취임 후 자신의 유일한 목표는 많은 돈을 버는 것이었음을 상기시킨다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7287, 32663, 17083, '2회에서 영구세라(김하연)와 서공명(서윤혁)이 모앤팝 가게 밖에서 쌍둥이 아이스 캔디를 나눠 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7288, 32664, 17083, '구세라(임진아는 서공명을 만난다. 28회에서 박정래 할머니(강애심)를 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7289, 31543, 17083, '이 건물은 구세라(임진아/애프터스쿨 나나)의 아지트로 가장 먼저 등장하는 만화가게, 레코드가게이고, 구세라의 절친 중 한 명인 장한비(신도현)가 운영하는 바이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7290, 32665, 17083, '3회 에필로그에서 취한 구세라(나나)가 이 버스 정류장에 앉아 있는 모습으로 서공명, 그녀를 찾아온 (박성훈)이 술 취한 그녀의 어깨에 기대어 잠이 든다. 7화에서 공명은 다시 이 버스 정류장에, 이번에는 세라에게 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7291, 30800, 17083, '2화에서 구세라(임진아/애프터스쿨 나나)가 절친한 친구 장한비(신도현)의 지원을 받아 이 카페 앞에서 캠페인을 펼치고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7292, 32666, 17083, '2화(전체 1화)에서 구세라(애프터스쿨 임진아/나나)는 이 문구점 바로 앞 아케이드 게임에서 김자룡(최고)을 무자비하게 이긴다. 져주지 않는 어른을 소년이 한탄하자 세라는 아이들도 사회의 어려움을 맛봐야 한다고 반박한다. 9화(전체 5화)에서 세라가 학대를 당할 가능성이 있는 아동을 보호하기 위한 비밀 이웃 요원 육성에 관한 조례 법안에 대해 이야기하는 장면이 다시 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7293, 32667, 17083, '출사표 속 가상의 마원지구 중심가이다. 3회에서 구세라(애프터스쿨 임진아/나나)는 자신이 당선된 사실을 알기 직전 이 거리 주민들의 우편함에 핑계를 대는 모습이 그려졌다. 10회에서 세라와 그녀의 현 비서인 서공명(박성훈)은 \'스마트원\'에 링크된 청원에 대해 재차 확인하기 위해 이 거리에 사는 주민들을 확인하기 위해 여기에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7294, 26595, 17083, '구세라(임진아)가 걸어갈 때 출사표에서 자주 등장하는 가상의 거리이다. 22회(11회)에서는 세라가 냉전 이후 서공명(박성훈)과 화해하는 모습이 그려졌다. 2회에서 권우영(김미수)이 초등학교 밖 건설공사 반대 선거운동단과 만나기 위해 이 거리를 질주하는 유모차를 밀치는 모습도 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7295, 32668, 17083, '6회에서 구세라(임진아)가 서공명(박성훈)이 그녀를 지나갈 때 이 고가 도로에서 이상한 위치에서 운동을 하고 있다. 마지막 16회에서는 슬로건을 재활용한 선거 포스터에서 영감을 받아 권우영(김미수)이 직접 출마를 결심하고 이 고가도로를 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7296, 31544, 17083, '6회에서 동네 어린이 공부방을 찾은 윤희수(유다인)는 서공명(박성훈)에게 정치에 입문하는 이유를 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7297, 32669, 17083, '구세라(임진아)는 6화(전체 3화)에서 이 버스 정류장에서 마지막 순간을 보내고 있다. 투표소장으로 그녀를 따라다녔던 서공명(박성훈)은 그녀에게 선거운동 후 규칙을 알려주며 정리를 한다. 악수를 한 후 그녀는 압도된 기분으로 연결 육교로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7298, 32670, 17083, '5화에서 구세라(임진아)는 손은실(박미현)과의 만남 후 깊은 생각에 잠긴 채 이 고가도로를 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7299, 32671, 17083, '5회(전체 3회)에서 김삼숙(장혜인), 구세라(임진아)의 엄마가 함께 캠페인 길에 합류하고 장한비(신도현)와 함께 이 무지개 다리를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7300, 25781, 17083, '8회에서 구세라(임진아/애프터스쿨 나나)가 교대근무를 맡아 이 호텔 앞에서 의뢰인을 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7301, 31739, 17083, '구세라(임진아/)와 서공명(박성훈)은 출사표에 있는 이 식당의 단골 손님이다. 4, 6, 7, 9화에 나온다. 10화에서 세라와 공명은 식사를 하며 사랑동 이름 바꾸기 캠페인에 대해 논의하기 위해 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7302, 32672, 17083, '1회에서 취한 구세라(나나)가 이 거리 모퉁이에서 남자친구 김민재(한준우)와 전화통화를 하고 있다. 그곳에서 그녀는 길 건너편 가로등 기둥에 쓰러진 남자를 발견하고 신고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7303, 27222, 17083, '4회에서 구세라(애프터스쿨 임진아/나나)는 새 동료들의 환영회를 위해 일어선 뒤 이 정류장에서 버스를 기다리다가 지나가던 서공명(박성훈)을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7304, 26694, 17083, '15화에서 구세라(나나)는 투표에서 살아남은 후 집으로 가는 길에 보행자 보호소를 지나간다. 그녀는 피곤한 발을 위해 슬리퍼 한 켤레를 신고 출구에서 기다리고 있는 아버지(안길강)를 발견한다. 3년 전 국회의원 후보 손은실(박미현)을 처음 만난 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7305, 26445, 17083, '1회에서 구세라(애프터스쿨 임진아/나나)가 남자친구 김민재(한준우)에게 마원구청 서비스센터 비서로 취직한 일에 대해 폭언하는 장면이 나온다. 3화에서 세라와 서공명(박성훈)은 세라가 해고당하는 모습을 목격한 여성을 만나 사과를 하기 위해 이 카페에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7306, 28405, 19211, '8회에서 진선호(김현진)는 부모님과 함께 이곳을 여행했던 기억을 떠올리고 아버지가 다른 남자아이와 어머니에게 데려가는 모습을 보게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7307, 25738, 19211, '5회에서 도해이(한지현)와 박정우(배인혁)가 기차를 타고 MT를 타고 연희 치어리더팀에 합류한다. 이 역을 지나는 선로를 따라 그들의 숙소로 걸어갈 때, 그는 그녀의 별 별자리를 보여주고, 진선호(김현진 분)가 그들을 데리러 오면 그녀의 상처에 연고를 발라준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7308, 26198, 19211, '11회에서 박정우(배인혁)는 도해이(한지현)에게 이 도서관에서 시간을 보내자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7309, 28474, 19211, '2화에서 도해이(한지현)와 남자친구가 헤어지고, 6~7화에서 도해이(한지현)를 찾아 헤매던 걱정스러운 박정우(배인혁)가 마침내 그녀를 발견하고 둘 다 해이를 찾았던 진선호(김현진)와 합류하는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7310, 24957, 19211, '이곳은 \'연희 대학교\' 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7311, 24944, 19211, '6화에서 도해이(한지현)가 이곳에서 전 남자친구만나 그를 돕는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7312, 29247, 15654, '5회에서 한강호(윤시윤) 고등학교 친구의 생일 풀파티가 이곳에서 펼쳐진다. 그리고 주은(권나라)이 화해할 수 있도록 초대하기도 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7313, 26030, 15654, '13회에서 점심식사를 위해 이 식당의 외관이 등장한다. 이호성 재판에 대해 논의하기 위해 한강호(윤시윤)가 한영그룹 대표를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7314, 25605, 15654, '3-4회에서 납치된 한수호(윤시윤)는 이곳에서 인질로 잡혀간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7315, 25796, 15654, '대부분의 사람들은 여기에서 일한다. 3회부터는 한강호가 동생 한수호(윤시윤)의 자리를 대신하고, 연습생 판사 송소은(이유영)이 그의 조수가 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7316, 24910, 15654, '1회에서 송소은(이유영)이 선배 홍정수(허성태)와 함께 이곳에 찾아와 그에게 성추행을 당하는 사이, 한수호(윤시윤)는 오대양(김명곤)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7317, 26524, 15654, '1-2회에서 한강호(윤시윤)가 죄수로서 드나드는 감옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7318, 32679, 15654, '4화 회상씬에서 송소은(이유영)은 가해자가 판결을 받은 후 언니 송지연(곽선영)과 함께 이곳에 왔고, 언니는 그녀에게 가장 끔찍한 것은 심판이 끝난 후 그가 웃는 모습을 보는 것이 가장 끔찍했다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7319, 27202, 3811, '영화 속에서 나온 푸른 초원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7320, 32680, 3811, '박찬욱감독 및 영화인들의 단골 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7321, 30555, 3811, '친절한 금자씨에서 나온 가족나들이 하기 좋은 유적지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7322, 26639, 3811, '영화 속에서 금자씨가 고등학교시절 갔던 아쿠아리움');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7323, 32681, 3811, '수요미식회에도 나온 박찬욱 단골 라멘집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7324, 32682, 3811, '박찬욱감독이 단골인 일본 가마메시 전문 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7325, 32683, 3811, '친절한 금자씨와 무한도전등 각종 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7326, 25797, 17278, '1회에서 황폐해진 강현채(남규리)가 다리 위에 서 있다. 그리고 김서진(신성록)이 그녀에게 전화를 걸자 그녀는 사과하며 이대로 계속할 수 없다고 말한다. 이후 소진은 아내를 찾아 경찰들과 함께 다리 위에서 무너진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7327, 24946, 17278, '1회에서 김서진(신성록)이 딸 김다빈(심혜연)을 데려가는 모습으로 펼쳐진다. 결국 사라진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7328, 25621, 17278, '김서진(신성록)은 서도균(안보현)과 함께 유서일(신구)을 만난다. 6회에서 주정건설에서 태중시를 인수할 때가 됐다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7329, 24933, 14810, '13화에서 박혜주(전혜빈)는 갤러리의 카페에서 조예령(윤지민)을 만나 주주총회 파일의 몇 가지 변경 사항을 제안합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7330, 25796, 14810, '차금주(최지우)가 정기적으로 보조원으로 일하고 있는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7331, 26138, 14810, '박혜주(전혜빈)가 변호사 배지를 벗고 이동수(장현성) 대표가 자신을 위해 시궁창에 던져주는 것은 13회에서 차를 타고 두 IFC를 지날 때다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7332, 26992, 14810, '13회에서 차금주(최지우)는 유태호를 만나는데 갑자기 강 프로(박병은)가 그들에게 다가온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7333, 32684, 16331, '캠핑 클럽 특별 이벤트로 팬미팅한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7334, 32685, 16331, '핑클 멤버들이 끼를 대방출했던 롤러스케이트장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7335, 32686, 16331, '교련복 입고 와서 피자를 먹었던 카페 앤 펍');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7336, 32687, 16331, '울진에 위치한 캠핑 클럽의 3번째 정박지인 캠핑장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7337, 32688, 16331, '진안에 위치한 캠핑 클럽의 1번째 정박지인 캠핑장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7338, 32689, 18174, '박석(박호산)이 운영하는 \'2세대 다방\'으로 등장. 2화에서 강고비(옹성우)가 바리스타 일을 시작하는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7339, 32690, 18174, '3회에서 이후 \'투커피\'의 고객이 된 성민(이규형)은 할리스커피와 에스마켓 의정부점을 지나 이 거리를 지나며 설명한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7340, 32691, 18174, '4화에서 김주희(서영희)는 이 카페에서 전남편을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7341, 28634, 18174, '강고비(옹성우)가 이 카페에서 일을 시작했을 때, 박석(박호산)은 김주희(서영희)를 점심 데이트에 초대한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7342, 27066, 18174, '6화에서 기순호(SF9 다원)가 연못휴게소를 지나 이 공원을 질주한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7343, 32692, 13001, '1화에서 최한결(공유)이 미국에서 돌아온 후 저녁식사를 하며 사촌 최한성(이선균)을 만나는 곳이며 고은찬(윤은혜)이 아르바이트를 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7344, 25335, 13001, '2회에서 컨버터블을 타고 이 다리를 건너는 고은찬(윤은혜)과 최한결(공유)이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7345, 32693, 13001, '1회에서 고은찬(윤은혜)이 배달을 하다가 스쿠터를 타고 이 가게를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7346, 27226, 13001, '커피프린스에서 최한성(이선균)이 사는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7347, 28634, 13001, '7화에서 고은찬(윤은혜)은 최한결(공유)에게 점심을 먹자고 한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7348, 24922, 13001, '3화에서 고은찬(윤은혜)과 최한결(공유)은 덕성여중 입구를 지나 이 거리를 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7349, 26185, 13001, '7회에서 최한결(공유)은 이 서점에서 고은찬(윤은혜)에게 커피에 관한 책을 사준다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7350, 26738, 13001, '드라마 서브 주인공인 한유주(채정안)가 살고 있는 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7351, 32694, 13001, '최한결(공유)은 이 단지의 일부인 서울만화박물관 옥상에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7352, 32695, 17437, '고유한(허현준 분)은 최연우(유준 분)를 이 장소에서 열리는 미디어 아트 전시회에 데려와서 전시된 화려한 색상들을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7353, 25673, 12785, '1회에서 성춘향(한채영 분)과 이몽룡(재희 분)이 이곳에서 처음 만나는 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7354, 29238, 18668, '2회에서 이신아(Krystal Jung)는 두 명의 경찰에게 이 역으로 향하려 하고 멈춰 서서 따라오라고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7355, 27069, 18668, '1화 말미에 나오는 고탑교육 노고진(김재욱) 대표의 별장입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7356, 32699, 18668, '4회에서 도박 중독자가 된 강민(이시언)은 다음 계획을 세우기 위해 이 식당을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7357, 32700, 18668, '11회에서 백수영(유인영)과 오세기(하준)가 이 술집에서 만나 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7358, 25835, 18668, '11화에서 백수영(유인영)과 오세기(하준)가 이 술집에서 만나 술을 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7359, 32701, 18668, '카페 \'Eunee\'로 등장. 비서 이신아(크리스탈 정)가 상사 GOTOP 대표 노고진(김재욱)을 위해 신선한 커피를 마시기 위해 아침 일과에 들르는 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7360, 29173, 18668, '7회 이신아(크리스탈)는 노고진(김재욱)의 집에서 밤늦게까지 일한 뒤 콜라를 사겠다는 핑계로 이 거리 볼보 전시장 앞에서 택시를 부른다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7361, 30534, 18668, '9화에서 이신아(정수정)와 함께 FestIVA 레스토랑에서 외식을 할 때, 노고진(김재욱)은 백수영(유인영)과 함께 이 거리에서 데이트를 했던 기억을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7362, 25655, 18668, '9회에서 백수영(유인영)이 노고진(김재욱)과 이신아(크리스탈 정)를 이 식당에 초대해 저녁식사를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7363, 26327, 18668, '12회에서 노고진(김재욱)이 술에 취한 백수영(유인영)을 발견한 술집 중 하나로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7364, 25662, 18668, '9회에서 백수영(유인영)과 이신아(정수정)의 만남');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7365, 25944, 18668, '김래완(김선호)이 한은성(이유영)의 당시 남자친구 현성을 처음 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7366, 32702, 18668, '노고진(김재욱)이 운영하는 GOTOP Education의 사옥이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7367, 27225, 18668, '4화에서 GOTOP교육이 입시설명회를 여는 컨벤션의 일부로 등장하며, 이신아(크리스탈)와 백수영(유인영)이 마주치고 앉아 잠시 이야기를 나누는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7368, 32703, 18668, '이신아(정수정)는 절친 추옥희(박한솔)와 함께 이 아파트에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7369, 26156, 18668, '10회에서 박양태(임원희)와 고탑교육 제보자 마은정(백주희)이 이 공원 논습지에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7370, 25633, 18668, '9회에서 저글러스와 집에서 혼자 이 계단에서 키스하는 장면을 보고 노고진(김재욱)은 자신이 이신에게 키스하는 상상을 하기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7371, 26408, 18668, '10회에서 노고진(김재욱)은 저녁식사를 위해 이신아(정수정)를 이 식당에 초대한 후 그녀를 해고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7372, 25017, 18668, '3화에서 오세기(하준/송하준)가 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7373, 24915, 18668, 'KPEDA 10주년 기념행사는 이 호텔에서 열리며, 8화에서 노고진(김재욱 분)이 이신아(크리스탈)와 함께 참석한다. 놀랍게도 백수영(유인영)은 자신의 교육 회사인 백에듀를 설립한 협회의 새로운 회원으로 소개된다. 고진은 이 소식을 듣고 곧장 떠나가 수영과 신아를 따라나오자 그는 이 호텔 웨스트 타워 그랜드 로비 계단에 차를 세우고 신아에게 3초간 부탁하고 변명하며 몸을 숙여 키스를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7374, 25846, 18668, '노고진(김재욱) GOTOP교육 대표가 1회에서 이 대학 강석규 교육원에서 초청 강연을 하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7375, 32704, 10758, '16회에서 \'무경험운전사\' 강인규(고경표)와 고지인(전소민)이 운전면허를 취득한 후 이 도로를 따라 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7376, 32705, 10758, '7회. 16화에서 인규는 시련 1년 후, 이번에는 좀 더 평화로운 상태로 그들을 방문하기 위해 이곳으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7377, 32706, 10758, '3회에서 손연희 박사(양진성)는 강인규(고경표)를 산부인과로 데려가 조산아의 상태를 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7378, 32707, 10758, '10회에서 김철호(정은표)가 건물 밖 교차로에서 대기하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7379, 30713, 10758, '10회에서 장팀장이 이끄는 경찰감시단이 김철호(정은표)를 따라 이 거리로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7380, 26132, 10758, '장기이식센터장 고정훈(조재현), 이식코디네이터 고지인(전소민), 이식전문가 이주혁(김지한-전) 등 주요 출연진 대부분이 이곳에서 일한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7381, 25796, 10758, '16회에서 강인규(고경표) )은 공판을 마치고 법정을 나서며, 첫 번째 범행에 대해서는 단기 정직 처분만 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7382, 32708, 10758, '8회에서 강인규(고경표)는 도주한 김형범(허성태)의 뒤를 쫓아온 만식(정도원)을 피하기 위해 이 가게에 숨는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7383, 31544, 10758, '8, 9회에서 강인규(고경표)는 김형범(허성태)이 숨어 있는 곳을 찾기 위해 만식(정도원)을 따라 이 거리를 찾는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7384, 25017, 10758, '16회에서 이주혁(김이한)은 손연희(양진성)를 터미널에서 하차시켰다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7385, 32709, 10758, '14회에서 강인규(고경표)가 가로등 뒤에서 김형범(허성태)의 부하 중 한 명이 일하는 식당의 염탐하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7386, 26524, 10758, '외과의사 강인규(고경표)가 입사 지원 후 이 교도소에서 근무한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7387, 26030, 15127, '8회에서 백산(김영철)과 강기형(손현주)이 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7388, 32710, 15127, '5회에서 김준현(이준기)은 생화학 테러 용의자를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7389, 31982, 15127, '이 장소는 아동 유괴가 일어난 곳이다. 8화에서 어린 소녀 유나는 어머니의 코 바로 아래에서 떨어져 나갔고, 이는 NCI가 사건에 연루되게 합니다. 9회에서는 김현준(이준기)과 하선우(문채원)가 유나의 납치 사건을 추적하기 위해 주변을 순회하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7390, 32711, 15127, '19화에서 모방범 킬러 강치환(기수)은 1화(컨벤시아)에서 일어난 납치 사건을 재현하기 위해 이 교차로에서 여성 구매자를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7391, 26088, 15127, '1화 초반에 폭탄이 터지는 것은 이 건물에서다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7392, 28088, 15127, '14회에서는 유민영(이선빈)과 이한(고윤)이 팀을 위해 아이스 아메리카노 6잔을 주문하기 위해 카페를 찾는 모습이 그려졌다. 19회(18시 29분)에서 정학당한 하선우(문채원)가 사건을 조사하던 중 김현준(이준기)과 합류해 모방 고양이 살인 사건에 대해 이야기를 나눴다. (이 장면에서는 이 지점의 외면만 촬영되었습니다)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7393, 27120, 15127, '16화에서 백서연(김보라)은 아버지를 찾아가고, 이수겸(준)은 두부로 아버지를 맞이한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7394, 32712, 15127, '1,20회에 김현준(이준기)의 모집으로 등장하는 곱창고 지점이 이 건물에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7395, 24927, 15127, '16회에서는 유민영(이선빈), 나나(유선), 민영의 조카딸, 강팀장 아들이 방금 여행에서 돌아왔음을 보여주기 위해 서울역 외관이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7396, 32713, 15127, '1회에서 경찰 김준현(이준기)이 이 거리에서 납치 피해자의 발자취를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7397, 25809, 15127, '12회에서 사형 선고를 받은 죄수들이 수감된 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7398, 26458, 15127, '5회에서 생화학 테러 공격에 가담한 용의자와 공범을 쫓는 팀이 바로 여기다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7399, 32714, 15127, '3, 4화에서 강기형(손현주)의 아들 한별(김강훈)이 쇼핑몰에서 사신과 함께 끝날 때 팔을 벌리고 있습니다. 19회에서는 강기형(손현주)이 돌아가신 아내 혜원(오연수)과 식사를 하는 장면에 이 쇼핑몰 안에 위치한 카페 떡볶이가 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7400, 26282, 3517, '지혜와 상민이 비를 피해 도착한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7401, 32715, 3517, '상민이 지혜에게 선물을 고르라고 한 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7402, 29371, 3517, '준하와 주희의 첫 만남이 이루어진 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7403, 25718, 15426, '은경수(오지호)는 2018년 10회에서 백지민(박시연)과 통화하던 중 이 공원을 지나간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7404, 24930, 15426, '33화에서 손무한(감우성)과 안순진(김선아)은 장미정원 어린이 동물원에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7405, 25827, 15426, '14회에서 이수(옹성우)는 미국을 통한 포토여행 중 밤에 정우연(신예은)에게 전화를 거는 미국의 어느 곳으로 나오는 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7406, 25796, 15426, '4회 회상 장면에서 손무한(감우성)이 안순진(김선아)과 은경수(오지호)의 대화를 목격한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7407, 31652, 15426, '1, 3, 9화에 카페로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7408, 24948, 15426, '1화에서 손무한(감우성)은 창의적인 사고를 위해 그의 개와 함께 여기에옵니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7409, 27596, 15426, '12회에서 손무한(감우성)과 안순진(김선아)은 데이트와 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7410, 25810, 14244, '16회에서 차도현(지성)과 오리진(황정음)이 데이트하러 온다. 도현은 여기서 계약을 해지하고 망연자실한 리진을 남겨둔다. 오리온(박서준)은 그녀를 태우고 해변가에 있는 레스토랑 다집횟을에서 그녀의 슬픔을 듣는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7411, 28405, 14244, '16회에서 차도현(지성)과 석호필 박사(고창석)가 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7412, 30822, 14244, '오씨 가족의 집으로 등장하는 식당이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7413, 25678, 14244, '오리진(황정음)과 차도현(지성)의 첫키스가 있었던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7414, 26173, 14244, '11회에서 차도현(지성)과 오리진(황정음)이 이 광장에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7415, 25637, 14244, '드라마 안에서 정신건강의학과 레지던트 인 오리진(황정음)이 일하는 \'강한병원\'으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7416, 27069, 18684, '12회에서 볼 수 있듯이 현욱(김재철)과 함신애(한수연) 부부는 여기 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7417, 26719, 18684, '10회에서 기모란(이혜영)과 최정현(윤현수)이 함께 쇼핑을 하는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7418, 32717, 18684, '12회에서 서준범(정의재)은 밤에 이 술집에서 짝사랑하는 동료 우현(김하늘)에게 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7419, 25816, 18684, '이 건물은 우현(김하늘)의 직장인 UNI건물로 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7420, 26419, 18684, '1화에서 함신애(한수연)가 유니홈쇼핑 기장 모란(이혜영)과의 만남을 요청하는 사설 미술관으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7421, 28442, 18684, '2화에서 UNI 홈쇼핑 전무 기모란(이혜영)은 우현(김하늘)과 UNI 홈쇼핑 이현욱(김재철) 사장의 만남을 주선한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7422, 25662, 18684, '1회에서 우현(김하늘)이 라이벌 홈쇼핑 회사 대표인 유혜림(이혜은)을 만나 입사 가능성에 대해 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7423, 26407, 18684, '10회에서 기모란(이혜영)이 이곳에 약속을 잡았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7424, 25680, 15972, '대표적인 전통문화 테마파크이자 킹덤의 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7425, 26080, 15972, '킹덤의 촬영지이자 한복 입고 사진 찍기 좋은 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7426, 25908, 15972, '백민갑(김권) 안태규(백철민)이 술을 마시는 모습에서 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7427, 26017, 15972, '독특하고 예쁜 포천 여행 명소이자 킹덤의 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7428, 28437, 15972, '이창(주지훈)과 무영(김상호)이 이 산을 넘습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7429, 26602, 15972, '킹덤 시즌1과 시즌2 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7430, 32718, 15972, '킹덤의 촬영지며 내연산에 있는 12개의 자연폭포');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7431, 25945, 15972, '처음에는 고종의 후궁 이씨의 은신처로, 다음은 건청궁(건청궁, 고종, 명성황후의 은신처)으로 사용되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7432, 26072, 15972, '이창(주지훈) 세자가 킹덤(킹덤/킹덤)의 이 정자에 서 있는 모습. 섭정의 제의를 마친 후 정자에서 조학주(류승룡)와 그의 딸 섭정(김혜준)이 사적인 대화를 나눈다. 이 정자의 일부인 연못은 왕국에서 좀비 감염 인원의 궁전의 처리 구역이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7433, 32719, 15972, '왕비의 사택으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7434, 32720, 15972, '조선시대 왕비의 침전이자 킹덤의 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7435, 28145, 15972, '이순신 장군이 이끌던 수군 본영이자 킹덤의 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7436, 25988, 15972, '킹덤의 촬영지이자 자연학습장으로 유명한 갈대밭');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7437, 32721, 8134, '8, 9회에서 윤종우(임시완)는 다음날 고향으로 돌아가 에덴 고시원에는 돌아오지 않기로 결심했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7438, 32722, 8134, '이 교회는 \'에덴 고시원\'으로 등장한다. 종우(임시완)의 지옥 같은 악몽은 낯선 사람들에게 둘러싸인 이 고시원에 정착하기로 결심하면서 시작된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7439, 25940, 8134, '1화에서 종우(임시완)는 서울에서의 첫 저녁식사를 이곳에서 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7440, 32723, 8134, '4화에서 지은(김지은)은 이 커피숍에서 고객 미팅을 마치고 SMS로 종우에게 자신이 근처에 있음을 알립니다. 5화에서 재호(차래형)는 이곳에서 지은을 만나며 종우(임시완)의 행동의 변화에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7441, 24908, 8134, '5회에서 종우(임시완)의 사무실이 두 번째 술자리를 찾아 이 길을 걷는다. 종우는 어머니를 생각나게 하는 아줌마에게서 꽃다발을 사기 위해 걸음을 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7442, 25345, 17569, '5회에서 서정인(이주영)이 서기태(김영철) 사장을 만나는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7443, 26142, 17569, '5화에서 용의자를 따라 이 식당에 갔고 그가 무고한 민간인임이 밝혀졌습니다. 하지만 정인은 옆방에서 김영주(문정희)를 언뜻 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7444, 26697, 17569, '5회에서 이진우(이서진)가 바로 앞 사거리에서 신호등에 멈춘다. 이 건물의 초록불을 기다리며 뉴스를 읽는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7445, 26114, 15758, '2회에서 영준(지수)은 이 술집에서 아르바이트를 하며 김득환(유재명)을 초대하고, 득환은 영준을 도와주려는 노력을 거부하기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7446, 32724, 3663, '탄광에 숨어 있는 인민군을 포로로 잡는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7447, 27202, 3663, '퇴각명령을 받고 후퇴를 준비하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7448, 32725, 3663, '낙동강 방어선 전투와 두밀령 고지 전투 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7449, 25345, 3663, '영화 태극기 휘날리며 전투 세트장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7450, 32726, 3663, '벚꽃이 아름다운 태극기 휘날리며 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7451, 31921, 3663, '장동건의 훈장 씬, 피난열차 장면 등을 찍은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7452, 27127, 3663, '시체가 산을 이루고 우물에서 폭탄 터진 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7453, 29371, 3663, '제사를 지내고 피난 떠나는 장면의 배경지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7454, 28422, 3663, '태극기 휘날리며에서 원빈이 일을 도왔던 성당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7455, 32428, 14600, '6, 7화에서 가상의 마을 \'우르크\'에서 지진이 발생하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7456, 32737, 14600, '우르크 장면들을 다수 촬영한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7457, 32738, 14600, '상상의 나라 우르크의 일부를 촬영한 곳입니다. 6회에서 서대영(진구)이 신병을 훈련시키고 유시진(송중기)이 아주 멋진 모습으로 그를 찾아오는 한국의 군사 훈련장 촬영 장소이기도 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7458, 32739, 14600, '1, 13화에서 유시진(송중기 분)과 서대영(진구 분)의 데이트 장소이며 유씨의 휴대폰이 도난당하는곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7459, 32740, 14600, '16회에서 유시진(송중기)과 강모연(송혜교)이 낚시여행을 떠나는 도중에 들리는 카페이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7460, 25612, 14600, '6회에서 유시진(송중기)은 낚시를 간다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7461, 32741, 14600, '강모연의 집으로 나왔던 가구점');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7462, 32742, 14600, '강모연(송혜교)의 아파트로 등장한다.');
