INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7484, 32751, 6531, '목진우(김민상)가 어릴 적 화양에서 살았던 집. 15화에서는 신재이(이유영)를 납치해 그녀를 심문하고 만년필이 있는 곳을 찾기 위해 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7485, 32006, 6531, '1980년대 장면에 등장한 박광호(최진혁)와 신연숙(이시아)의 신혼집 외관이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7486, 32752, 6531, '11회에서 중범죄 1부대 팀이 저녁 식사를 위해 여기에 모여 저녁을 먹는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7487, 25657, 6531, '드라마에서 \'화양대학교\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7488, 26744, 6531, '14회에 김선재(윤현민)가 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7489, 31803, 6531, '8화에서 신재이(이유영)는 이 도서관의 마이크로 필름실로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7490, 32753, 6531, '8회에서 박광호(최진혁)와 김선재(윤현민)는 이 거리에서 중대 1부대원들을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7491, 26242, 6531, '1회에서 80년대 중반, 크루즈가 개항을 앞두고 박광호(최진혁)가 아내 연숙(이시아)을 데리고 오겠다고 약속한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7492, 25955, 6531, '4회에서 광호(최진혁)와 김선재(윤현민)는 침입을 조사하기 위해 이 저택에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7493, 25637, 6531, '화양대학교 병원. 4회에서는 어린 윤수정(최명빈)이 찬장에 숨은 채 발견돼 구치소에 갇히는 장면이 그려졌다. 11화에서 신재이(이유영)는 살인 용의자에게 습격을 받아 이곳에 입소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7494, 32754, 6531, '80년대 형사 박광호(최진혁)의 시간여행 시련을 현재까지 일으킨 터널이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7495, 25809, 6531, '모방범 킬러 정호영(허성태)이 갇힌 곳. 법의학자 목진우(김민상)의 방문을 받고 스스로 목숨을 끊었다. 12회에서 박광호(최진혁)와 김선재(윤현민)는 수사석이 끝난 후 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7496, 32755, 6531, '5회에서는 박광호(최진혁)와 김선재(윤현민)가 살인사건을 조사하기 위해 이 휴게소에 왔다가 폭우로 인해 이곳을 드나드는 통로가 모두 막히면서 주인과 용의자 일행과 함께 갇히게 된다. 신재이(이유영 분)도 놀랍게도 비에 흠뻑 젖은 채로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7497, 25737, 5216, '3화에서 차뷰티에서 해고된 한수연(김보라)은 이 거리를 걷는다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7498, 27465, 5216, '차정혁(주상욱)이 운영하는 \'차 프리미엄 메이크업 & 부티크\' 또는 \'차뷰티\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7499, 25719, 5216, '이 건물은 패션 매거진 더블엑스의 한국 지사로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7500, 32756, 5216, '7회에서 전직 모델 백지윤(한다감)과 전 매니저 김효진(이채은)이 이 식당에서 저녁 식사를 하는 모습');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7501, 24937, 5216, '7회에서 이 장소에서 민강호(송재희)를 만나 사업 제안을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7502, 26027, 5216, '7회에서 차정혁(주상욱)은 백지윤(한다감)과 주차장에서 헤어진 순간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7503, 25659, 5216, '4화에서 강도진(이태환)은 이 광장에서 자신이 더블엑스 표지에 등장하게 될 것이라는 사실을 알게 됩니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7504, 26656, 5216, '이 집은 한수연(김보라)과 정영아(연우, 전 모모랜드)의 여동생 집으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7505, 27304, 5216, '2편에서 한수연(김보라)은 11년 전 고은성이 오빠와 함께 왔던 이 담벼락 같은 자리에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7506, 25804, 5216, '3회에서 이 맨션은 강도진(이태환)이 사는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7507, 25781, 5216, '4화에서 차정혁(주상욱)은 당시 여자친구였던 모델 백지윤(한다감)이 이 호텔에서 다른 사람과 결혼할 것이라는 사실을 알게 된 때를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7508, 26282, 15873, '1화에서 유백(김지석)이 참석하는 드라마 시상식 무대로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7509, 25865, 15306, '20회에서 차동탁(조정석)이 감옥에서 막 살해당한 절친 이두식(이재원)에게 작별인사를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7510, 26521, 15306, '8회에서 이 병원에 도착한 동탁은 수창이 아버지의 이름을 맑게 해주겠다고 약속한 꼬마라는 사실을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7511, 24933, 15306, '1회에서 차동탁(조정석)은 사기꾼을 추적합니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7512, 25662, 15306, '25회에서 차동탁(조정석)은 귀신같이 따라붙는 공유창(김선호)과 함께 잠재적 정보원을 만납니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7513, 26294, 15306, '6화에서는 오토바이 갱단을 쫓는 차동탁(조정석)과 그의 경찰 친구들, 그리고 공수창(김선호)의 사기꾼 친구들이 차를 몰고 갱단을 에워싼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7514, 25347, 15306, '6회에서 차동탁(조정석)이 귀신 공수창(김선호)과 함께 이곳에 찾아와 스트립쇼를 하다가 자신이 미쳤다고 생각한 동탁은 드디어 수창을 믿게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7515, 26232, 15306, '1회에서 체포된 사기꾼 공수창(김선호)이 다리 위에서 오토바이 갱단에게 에워싸인다. 2화에서 동탁은 의식을 잃은 수창을 어깨에 메고 다리를 뛰어내려 현장을 탈출한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7516, 25910, 15306, '2회에서 차동탁(조정석) 팀 새 팀원의 환영식을 위해 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7517, 32758, 15306, '4회에서 이 가게에서 술을 사고 난 뒤, 공주창(김선호)  차동탁(조정석)의 몸은 바로 앞 공원에서 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7518, 26650, 15306, '11회에서 공수창(김선호) 차동탁(조정석)의 몸은 사기꾼 박씨와 전화협상을 하며 이 길을 걷는다. 앞서 10화에서는 같은 거리에 있는 신발가게 Spr 앞에서 사기꾼이라고 자책하는 모습을 봤다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7519, 31981, 15306, '3회에서 차동탁(조정석)의 몸은 걸스데이 혜리 송지안과 함께 소웬카페를 지나 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7520, 26098, 15306, '1화에서 차동탁(조정석)이 점쟁이를 만나 그의 미래를 듣기 위해 따라가는 곳은 바로 이 집 앞에서다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7521, 32759, 15306, '차동탁(조정석)과 송지안(혜리)이 여기에 와서 이야기를 나눕니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7522, 26186, 15306, '2회에서는 차동탁(조정석)이 송지안(혜리) 기자에게 특정 사건에 대해 보도하지 말아달라고 부탁하는 모습이 그려졌다. 20화에서는 두 사람이 데이트를 위해 이곳으로 돌아와서 키스를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7523, 25799, 15306, '14화에서 이 집은 가정폭력 범죄 현장의 일부로 사용된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7524, 27483, 18531, '1회에서 황동주(임시완)가 장례식에 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7525, 26085, 18531, '극 중 \'국세청\'의 일부로 등장합니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7526, 32763, 18531, '1회에서 황동주(임시완)는 이 카페에서 황철민(박호산)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7527, 32764, 18531, '1화에서 서혜영(고아성)과 황동주(임시완)가 팀장으로 일하기 시작하는 국세청 5과 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7528, 32765, 17087, '윤시윤A에게 윤시윤B가 원래 자리로 가라고 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7529, 25906, 17087, '9회에서 서도원(윤시윤) 아버지의 장례가 이곳에서 치러진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7530, 32766, 17087, '이곳은 서도원(윤시윤)이 \'8210\' 기차를 타고 세계를 오가는 대체 우주의 무경역이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7531, 26085, 17087, '3회에서 이 병원(이하 \'무경병원\')으로 달려가 병원을 찾아 도착한다.');
