INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7533, 32767, 17087, '9회에서 서도원(윤시윤)은 석민준(최승윤)의 행방을 추적한 뒤 이 건물에 찾아와 \'인사이드\'로 직진한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7534, 32768, 17087, '1회에서 한서경(경수진)의 전 계모 조영란(윤복인)이 운영하는 미용실로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7535, 32769, 17087, '서도원(윤시윤)과 한서경(경수진)이 형사로 일을 하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7536, 32770, 17087, '트레인에서 무경역으로 등장하는 낡은 역');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7537, 25719, 14089, '이곳은 소속사 샤인스타의 사옥으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7538, 32771, 14089, '13회에서 장준현(지현우)과 박수인(이세영)의 약혼식이 있었던 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7539, 32813, 12742, '대통령 관저인 청와대로 등장했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7540, 25720, 12742, '한기주(박신양)의 파리지앵 타운하우스 인테리어로 쓰였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7541, 26176, 12742, 'CGV 시절에는 한기주(박신양)의 전처 백승경(김서형)의 직장으로 출연했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7542, 26997, 12742, '드라마 안에서 수영장 장면, 한기주(박신양)와 강태영(김정은)의 로비 키스와 술집에서 김태영을 위해 노래하는 태영이 나오는 장면, 그리고 그들의 약혼 파티와 대학 하키 파티는장면이 이곳에서 촬영되었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7543, 26992, 12742, '6화에서 상심한 유수혁(이동건)이 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7544, 26232, 12742, '6화에서 상심한 유수혁 (이동건)은 오토바이를 타고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7545, 25887, 12742, '3회에서 서울로 돌아온 강태영(김정은)은 이 거리에 있는 아버지 집에 직접 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7546, 31596, 12742, '3화에서 한기주(박신양)는 강태영(김정은)에게 이 건물 앞에서 만나자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7547, 26159, 12742, '18화에서 태영(박신양)과 기주(김정은)는 약혼하기 전에 분수에 동전을 던지고 고통스럽게 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7548, 25017, 12742, '3화에서 한기주(박신양)와 강태영(김정은)은 모두 이 공항을 통해 대한민국에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7549, 26522, 4679, '17회에서 장도한(김영광)이 오광호(김상호)를 찾아간 뒤 이 카페를 지나가고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7550, 25831, 4679, '조수지(이시영)의 딸을 죽인 학생다니는 학교. 4화에서는 수지가 복수를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7551, 24935, 4679, '1화에서 장도한(김영광)이 술을 마시며 침입하는 술꾼으로부터 한 여성을 구하는 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7552, 32814, 4679, '17화에서 장도한(김영광)과 조수지(이시영) 출연장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7553, 31779, 4679, '10화에서 팀이 택시를 타고 여기를 찾아오는 살인마를 쫓고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7554, 32815, 4679, '조수지(이시영), 공경수(키), 서보미(김슬기)의 은신처의 낙서 옥상으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7555, 25348, 4679, '24-28화에서 검사의 윤승로(최무성) 청문회를 하는 장소로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7556, 32258, 4679, '검사 윤승로(최무성)의 집으로 등장하는 저택으로 니온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7557, 26273, 4679, '1회에서 키(공경수)가 촛불을 켜고 임무를 완수했다는 메시지를 남기고 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7558, 25016, 4679, '서보미(김슬기)가 이곳에 살고, 13회에서 보미를 지키려다 경찰에 체포되는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7559, 26170, 4679, '7회에서 김은중( 김태훈)은 경찰 이순애(김선영)에게 중요한 사건 정보가 있어서 전화를 걸고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7560, 25864, 4679, '4회에서 윤승로(최무성) 출연장면');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7561, 32816, 14807, '8회에서 이소혜(김현주)는 형의 빚을 갚고 싶어 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7562, 24933, 14807, '10화에서 이소혜(김현주)와 홍상화(윤지원)가 만나 드라마 대본에 대해 이야기하고, 소혜가 이상화에게 암에 걸렸다고 이야기하는 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7563, 29455, 14807, '3회에서 류해성(주상욱)이 이소혜(김현주)를 저녁식사에 초대했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7564, 26147, 12812, '13회에서 김동영(주진모)은 이 목장으로 장빈(천정명)을 초대해 같이 점심 피크닉을 즐겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7565, 26285, 5859, '7회에서 이가영(신세경)과 점심식사를 하러 찾아온 정재혁(이제훈)은 그녀에게 새 회사에 들어가자고 제안한다. 9화에서는 재혁이 다시 돌아왔지만 이번에는 결혼할 의향이 있냐고 묻는 안나와 거래를 하듯이 재혁이 승낙하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7566, 24925, 16264, '8화(전체 4화)에서 E-Stream 워크샵이 열리는곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7567, 26066, 16264, '1회에서 민재희(하재석)는 신비한 향수를 몸에 바르고 아름답고 평화로운 정원에 있는 자신을 본다. 5화에서 그녀는 남편이 다른 여자는 절대 보지 않겠다고 말했을 때를 회상한다. 7회에서 서이도(신성록)는 정원에서 재희와 춤을 추는 꿈을 꾼다. 17회(6회)에서는 재희와 이도가 현실로 이 정원에 찾아와 첫키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7568, 32085, 16264, '15-16회(전체 8회)에서 서이도(신성록)가 민예린(고원희)을 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7569, 26030, 16264, '9화(전체 5화)에서는 민예린(고원희)이 윤민석(김민규)에게 이곳에서 잔소리를 하기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7570, 32817, 16264, '10화(전체 5화)에서 서이도(신성록)가 민예린(고원희)을 이곳에 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7571, 32818, 16264, '1회에서 볼 수 있듯이 민재희(하재석)가 사는 맞은편에 사는 서이도(신성록)의 패션 하우스 겸 매장 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7572, 27515, 16264, '18회(전체 9화)에서 민예린(고원희)을 만나기 위해 서이도(신성록)가 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7573, 26327, 16264, '8화에서 극 중 2019 뉴웨이브 패션위크가 끝난 후 뒷풀이 장소인 나이트클럽 \'오블발렌\'의 촬영장소다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7574, 26540, 16264, '11화에서 이 건물 주변에서 전화기를 사기 위해 기다리는 고객들의 행렬이 펼쳐진다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7575, 25659, 16264, '1화에서 박준용(김기두)과 전화통화를 하며 자신의 향수를 김태가 훔쳤을 가능성이 높다고 말했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7576, 32819, 16264, '18회(9회)에서 남편의 이혼소송(한상진 특별출연) 이후, 한지나(차예련)는 이 콤플렉스의 계단을 내려가다 서이도(신성록)와 마주친다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7577, 30542, 16264, '9회에서 민예린(고원희)의 신체 대사와 건강이 60대 여성 수준임을 알게 된 서이도(신성록)는 운동을 위해 민예린(고원희)을 여기로 끌고 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7578, 25929, 16264, '12회(전체 6회)에서 동대문에서 가짜 데이트를 한 후 민예린(고원희)과 윤민석(김민구)이 이곳에 왔다. 신기하게 사진을 찍는 행인들의 모습이 담겼다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7579, 26002, 16264, '8화(4회)에서 민예린(고원희)과 진경(진경)은 놀이동산을 통째로 예약한 후 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7580, 26039, 16264, '2화(전체 1화)에서 꿈을 꾼 서이도(신성록)가 처음으로 눈을 떴다. 19회(전체 10화)에서 이도는 다시 이 호텔에 묵고 모델 손미유(AOA 혜정)를 찾아와 사과한다. 22/23회(전체 11/12)에서는 이 호텔의 연회장에서 수성전자의 행사가 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7581, 26285, 16264, '18화(본편 9화)에서 서이도(신성록)는 손미와 함께 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7582, 27318, 16264, '11화(전체 6화)에서 윤민석(김민구)과 민예린(고원희)의 열애 소식이 공개된 후 민석의 팬들은 소속사 로비에 몰려들어 그를 안심시키려 애쓴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7583, 25335, 16264, '16회에서 윤민석(김민규)과 민예린(고원희)이 차 안에서 대화를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7584, 25348, 16264, '7화에서 박준용(김기두)이 민예린(고원희)의 진실을 알게 된 장소이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7585, 25856, 16264, '7화에서 모든 모델이 테스트에 참가해야 하는 2019 뉴웨이브 패션위크가 여기에서 열린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7586, 24953, 16264, '3화에서 이 식당에서 서이도(신성록)와 민예린(고원희)은 식사를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7587, 25896, 16264, '4,8화에서 민예린(고원희)은 이 놀이동산에서 어린 진경(진경)과 함께 너무 무거워서 회전 목마에서 떨어진 민재희로서 자신을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7588, 25957, 16264, '9회에서 윤민석(김민구)과 서이도(신성록)의 소속사 모델들이 출연하는 광고 촬영이 여기서 부상을 당했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7589, 27535, 16264, '3회(김민규) 팬미팅이 이곳에서 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7590, 26682, 16264, '2회에서 박준용(김기두)은 이 거리를 따라 서이도(신성록)를 몰고 집으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7591, 25781, 16264, '12화(전체 6화)에서 서도(신성록)가 묵는 호텔방이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7592, 26141, 14635, '1회에서 정차식(지수)은 친구들과 함께 이 터널에 설치된 업라이트 피아노에 멈춘다. 2화에서 윤유슬(김소현)을 데려와 피아노를 부탁한다. 3화에서 두 사람은 피아노 2인용 콩쿠르를 위해 연습을 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7593, 25947, 17273, '15회에서 하윤철(윤종훈)과 하은별(최예빈)의 저녁식사장소. 6회에서는 오윤희(유진), 주단태(엄기준), 로건 리(박은석)가 공동 협약을 축하하기 위해 이 식당에서 식사를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7594, 25819, 17273, '6회에서 천서진(김소연)은 청아예술제 기간 동안 있었던 일에 대해 이곳에서 하은별(최예빈)과 대면한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7595, 28386, 17273, '1회에서 강마리(신은경)의 남편 유동필(박호산)이 이곳에서 풀려나고 조비서(김동규)에게 픽업된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7596, 32821, 17273, '15회에서 오윤희(유진)가 이 식당에서 천서진(김소연)을 만나자고 한다. 21화에서는 주단태가 같은 방에서 한 의원을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7597, 24935, 17273, '14회에서 오윤희(유진)는 이 술집에서 로건리(박은석)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7598, 25614, 17273, '윤지호(정소민)는 이 대학 동문이다. 4화의 플래시백과 5화의 사진에서 그녀를 대학 정문에서 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7599, 32819, 17273, '11회에서 하윤철(윤종훈)과 천서진(김소연)의 이혼이 확정된 곳은 여기다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7600, 32822, 17273, '강마리(신은경)의 사우나 \'마리탕\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7601, 26700, 17273, '헤라팰리스 아이들이 다니는 학교. 2화에서는 배로나(김현수)가 헤라팰리스 아이들과 함께 실기고사를 치르는 모습이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7602, 27074, 17273, '10화에서 주단태(엄기준)와 이규진(봉태규)은 자신의 호텔에서 한국계 미국인 투자자 로건 리를 만나려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7603, 25854, 17273, '18회에서 오윤희(유진)는 이곳에서 심수련(이지아)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7604, 32823, 17273, '최종회 21회에서 주단태(엄기준)가 로건리(박은석)에게 복수하기 위해 찾아온다. 로건리는 심수련(이지아)이 헤라팰리스에서 습격당했다는 뉴스를 보고 공항을 나오다가 단태의 부하들에게 붙잡혔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7605, 24910, 17273, '주단태(엄기준)는 이곳에서 오윤희(유진)를 만나 키스를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7606, 32824, 17273, '10회에서는 하윤철(윤종훈)이 이 식당에서 술에 취한 오윤희(유진)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7607, 25621, 17273, '하윤철(윤종훈 분)이 11화에서 장인(정성모 분)을 이곳에서 만나기로 했다. 그러나 윤철이 아버지에게 아무것도 밝히지 못하도록 아내 천서진 (김소연)도 나타난다. 그리고 나서 그들은 노금정에서 식사를한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7608, 25017, 17273, '20회에서 로건리(박은석)는 미국행 비행기를 탄 심수련(이지아)을 위해 공항에 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7609, 26125, 17273, '이 건물의 일부가 아파트 전용 단지인 \'헤라팰리스\'를 구성하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7610, 32830, 17014, '편의점샛별이에서 지창욱이 혼자 술을 마신 고기집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7611, 24935, 17014, '편의점샛별이에서 한선화가 와인을 마신 바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7612, 26085, 17014, '최대현(지창욱)은 6화에서 이 병원에 오는 모습이 그려진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7613, 25953, 17014, '7화에서 황금비(서예화)의 집으로 등장하고 친구들은 정샛별(김유정)에게 잠옷 파티에 오지 말라고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7614, 24918, 17014, '편의점샛별이에서 지창욱과 김유정이 맥주를 마신 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7615, 32831, 17014, '편의점샛별이에서 김유정이 배달알바를 하는 분식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7616, 32832, 17014, '편의점샛별이에서 지창욱이 동기를 만난 곱창집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7617, 32581, 17014, '1회에서 최대현(지창욱)은 여자친구와 데이트를 하던 중 이 버스 정류장에서 버스를 기다립니다. 하수구에 있는 새끼 고양이를 보면 안전하게 키우기 위해 온갖 짓을 다하고, 그 와중에 옷도 더러워진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7618, 26039, 17014, '최대현(지창욱) 1화에서 여자친구와 이 호텔에서 만남을 가졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7619, 32833, 17014, '12회에서 유연주(한선화)는 조승준(도상우)과 이 식당에서 저녁을 먹던 중 전화를 받고 서둘러 떠난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7620, 25335, 17014, '5회에서 유연주(한선화)와 최대현(지창욱)의 드라이브, 그녀를 집에서 데려온 후 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7621, 25668, 17014, '6화에서 최대현(지창욱)은 늦은 밤 여의도 다리 끝에 자신의 밴을 주차하고 유연주(한선화)와의 관계에 대해 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7622, 32834, 17014, '편의점샛별이에서 김유정과 지창욱이 이별한 부대찌개집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7623, 32835, 17014, '9, 10회 뒷골목 유연주(한선화)가 이 곳에서 최대현(지창욱)을 만나 이별을 고한다. 그렇게 한 뒤 그녀는 조승준(도상우)의 차에 태워진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7624, 32836, 17014, '편의점샛별이에서 지창욱과 한선화가 뮤지컬을 본 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7625, 32837, 17014, '최대현(지창욱)과 부모님이 사는 집, 8화에서 유연주(한선화)가 깜짝 방문하는 장면을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7626, 32838, 17014, '12회에서 최용필(이병준)이 동네 편의점 밖에서 아내 공분희(김선영)를 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7627, 26120, 17014, '한달식(엄문석)과 황금비(서예화)는 11회에서 이 거리가 교차하는 사거리에서 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7628, 25640, 16320, '손지은(박하선)과 최수아(예지원)는 16회에서 이 카페에 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7629, 27354, 16320, '이 박물관의 외관은 갤러리 화이트블럭의 외관처럼 보인다. 3회에서 이영재(최병모)의 오랜 친구 김빛나(유서진)가 운영하는 갤러리로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7630, 24933, 16320, '이곳의 내부는 이영재(최병모)의 오랜 친구 김빛나(유서진)가 사업 이야기를 하기 위해 방문하는 갤러리로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7631, 25613, 16320, '최수아(예지원)은 1회에서 이 식당에서 가족 외식을 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7632, 25980, 16698, '1회(전체 2회)에서 정영재(조보아)는 자신이 \'파견\'된 병원으로 태워다 주다가 이 사찰로 이어지는 길까지 끌려가 건물을 찾아 전나무길을 걷고 있는 자신을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7633, 32049, 16698, '포레스트의 배경으로 1-4회에서 강산혁(박해진 분)이 인수합병 이사로 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7634, 32839, 16698, '송혜원(김태리)의 시골 어린 시절 집으로 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7635, 24935, 16698, '27회(전체 14회)에서 박형수(이시훈)가 이 곳에서 태성그룹 조광필(김영필)을 만나러 왔고, 정영재(조보아)를 경찰에서 석방하도록 요구했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7636, 24939, 16698, '3화에서 경우연(신예은)과 이수(워너원 옹성우)는 브런치 카페를 지나 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7637, 26659, 16698, '강산혁(박해진)의 할머니는 이 옥상에 살았고 그가 이 곳과 그 기억으로 돌아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7638, 26080, 14554, '이 공원은 장단비(김슬기)가 시간여행을 하는 이도(윤두준)의 궁궐이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7639, 25868, 14873, '20회 마지막회에서 허준재(이민호)와 심청(지안나 준)이 이 구불구불한 길을 행복하게 운전한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7640, 26559, 14873, '20회에서 이 해변 카페바는 허준재 (이민호)와 심청 (전지현)이 행복하게 사는 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7641, 27480, 14873, '허준재(이민호)는 눈이 가장 많이 내리는 곳을 찾아 심청(지안나 준)을 데려간다. 그는 그녀에게 스키를 타는 법을 가르치고, 물론 그녀는 그가 그녀에게 브레이크 방법에 대한 설명을 마치기도 전에 슬로프를 질주한다. 그는 그녀를 붙잡고 둘 다 눈 속에서 굴러간다. 말을 멈춘 준재는 마침내 그녀에게 단어를 발음해 달라고 부탁한다. "나는 당신을 사랑합니다."라고 말하며 그가 그녀에게 이 말을 되풀이하라고 말할 때 거의 고백처럼 들립니다. 하지만 사실은 목소리를 기억해 그녀의 목소리인지 확인하고 싶어한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7642, 31914, 14873, '청과 준재가 스키장 데이트 했던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7643, 27344, 14873, '11회에서 이 찜질방이 청해스파 사우나로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7644, 28425, 14873, '2회에서 스페인의 바닷가 교회로 등장한 곳이다. 이곳에서 허준재(이민호)와 심청(지안나 준)은 동료 사기꾼 \'토마스 선교사\'(안재홍)에게 쫓기며 피난처와 지원을 받는다. 하룻밤을 지내고 토마스와 준재는 가짜 목회자로 그동안 모은 돈을 나눠먹고 다음날 아침 출발한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7645, 24933, 14873, '이 곳은 이 3인조의 새로운 사기의 전부인 9화에서 브로커가 일부 부유한 아줌마들과 투자 조언을 나누는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7646, 29187, 14873, '조선시대의 청과 담령이 재회한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7647, 32840, 14873, '1회에서 심청(지안나 준)과 허준재(이민호)가 방문하는 스페인 쇼핑몰로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7648, 32841, 14873, '조선시대의 청과 준재가 묵은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7649, 26602, 14873, '쫓기는 청과 청을 돕는 담령의 모습이 담긴 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7650, 24935, 14873, '1화에서 허준재(이민호)는 술집에서 마술로 여자를 홀린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7651, 29514, 14873, '허준재(이민호)가 3회에서 눈물을 흘리며 엘레베이터에서 내리면서 자신을 기다리고 있는 팀원들에게 소송을 제기해달라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7652, 32842, 14873, '14회에서 심청(지안나 준)과 허준재(이민호)가 데이트를 하는 곳이다. 또한 준재는 정에게 계속해서 행복하게 살겠다고 약속하고 그가 갑자기 죽으면 새로운 파트너를 찾을 것을 부탁하기 때문에 둘의 데이트가 돌연 끝나는 곳이기도 하다. 그녀는 평생 단 한 사람만을 사랑할 수 있기 때문에 인어로서 할 수 없는 일이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7653, 25796, 14873, '1회에서 사기꾼 3인조 허준재(이민호), 조남두(이희준), 태오(신원호)가 엘리베이터 기사로 위장해 \'고객\' 부부를 맞이하는 사무실도 이곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7654, 26249, 14873, '3회에서 심청(지안나 준)은 허준재(이민호)를 만나러 수족관에서 수영을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7655, 25335, 14873, '7화에서 배고픈 심청 (지아나 준)이 이곳에서 물고기를 찾습니다. 그러는 동안, 그녀는 동료 인어 유정훈(조정석)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7656, 25668, 14873, '4회에서 심청(전지현)은 허준재(이민호)를 따라 여기까지 왔다. 그는 사기꾼으로부터 그녀를 구하고, 이후 함께 불꽃 축제를 관람하고, 망설이다가 청을 집으로 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7657, 25608, 14873, '첫 눈 오는 날, 청과 준재가 만나기로 한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7658, 26577, 14873, '5회에서는 첫눈이 내리는 날 허준재(이민호)가 남산도서관 동쪽 길을 남산타워 방향으로 운전하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7659, 32843, 14873, '8회에서 허준재(이민호)가 심청(지안나 준)과 함께 자신(담령)을 만나기 위해 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7660, 32180, 14873, '허준재(이민호)는 이곳에서 부모가 이혼할 때까지 어린 시절을 보냈다. 아버지 허일중(최정우)과 계모 강서희(황신혜), 이복형 허치현(이지훈)이 사는 곳. 그의 아버지가 살해된 곳이기도하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7661, 27607, 14873, '차시아 (신혜선)의 작업장으로 등장한다. 9화와 10화에서 그녀는 허준재(이민호)를 데리고 와서 그들의 가장 최근 발견물인 담령의 초상화를 보여준다. 저장실에 들어서자 준재는 영정에게 다가가 담령이 자신과 닮았다는 것을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7662, 32844, 14873, '허준재(이민호)가 사는 집의 또 다른 부분이자 그와 모두가 사는 곳이다. 옥상, 발코니 및 거실 장면의 일부는 여기에서 촬영되었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7663, 25637, 14873, '11회에서 허준재(이민호)의 아버지, 허일중(최정우)의 장례식이 CKU 병원 VVIP홀에서 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7664, 26480, 14873, '15, 16회에서 하준재(이민호)가 어머니를 다시 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7665, 27535, 14873, '1회에서 이곳은 사기꾼 허준재(이민호)가 사기를 치고 두 범죄 파트너인 조남두(이희준)와 태오(신원호)와 합류한 곳이기도 하다. . 남두는 준재를 해리포터 사기꾼의 더 나은 버전이라고 부른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7666, 26412, 14873, '타워는 1화의 배경으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7667, 32652, 14873, '3회에서 그녀는 바다가 갈라지는 것과 같은 "모세의 기적"을 보기 위해 이곳에 온 한 무리의 아줌마들로부터 환영받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7668, 24998, 14873, '4화에서 심청(지아나 준)은 담령(이민호)을 만나기 위해 이곳에 갔다가 양왕의 검객들에게 둘러싸인다. 그녀는 결국 5화에서 담령에 의해 구출된다. 나중에 그는 그녀에게 "추억"을 상징하는 차나무 꽃을 선물한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7669, 32845, 14873, '현대의 청과 준재가 재회하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7670, 26457, 14873, '1회에서 스페인행 비행기에서 만난 승무원과 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7671, 25764, 14873, '현대의 청과 준재가 조폭들에게 쫓기는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7672, 27569, 14873, '1회에서 담령이 등장하는 배경');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7673, 26039, 15094, '20회에서 김효주(이희진)가 펜트하우스에 묵는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7674, 25890, 5540, '1회에서 다니엘(B1A4 정진영)이 서울을 답사하던 중 이 저택을 지나가다가 풍선을 잃어버린 소녀에게 선물을 건넸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7675, 26745, 5540, '1화에서 여진(미나)이 이 공원에서 열린 도서전에 회사를 대표한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7676, 25816, 14279, '30회에서 수(정유진)와 인정의 누나가 떠나고 한인상(이준)과 서봄(고아성)이 작별인사를 하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7677, 25973, 14484, '6화에서 박리환(이돈욱)과 김행아(정려원) 이 해변에 와서 둘이 데이트를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7678, 25976, 14484, '김행아(정려원)의 아버지를 기리는 나무가 있는 곳이다. 그녀는 6화에서 박리환(이동욱)과 함께 방문하는데, 둘 다 열애 중임을 자랑스럽게 뽐낸다. 그날 일찍이 행아의 어머니는 이미 이곳을 방문한 적이 있었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7679, 27480, 14484, '6회에서 클럽하우스는 홍이슬(박희본)의 어머니(박준금) 저택으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7680, 32846, 14484, '3화와 5화에서 김행아(정려원), 강석준(이종혁), 오세영(김정난)이 일하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7681, 26615, 14484, '8화에서 강석준(이종혁)은 채널코리아의 고정현을 만난다. 만남 직전 김행아(정려원)에게 괜찮은지 묻는 전화를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7682, 24935, 14484, '2회에서 절친 강석준(이종혁)과 조동일(박원상)이 이 술집에서 한 잔 하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7683, 26136, 14484, '7회에서 이 식당에서 저녁을 먹고 술을 마시고 바람을 피운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7684, 25335, 14484, '박리환(이동욱)과 김행아(정려원)이 4회에 나오는 다리이다. 6회에서는 오세영(김정난)과 예준수(안우연)가 스쿠터를 타고 이 다리를 건너는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7685, 25608, 14484, '6회에서 박리환(이동욱)과 김행아(정려원)는 데이트를 하며 이 탑의 발치까지 걸어가는데, 이환은 함께 있으면 얼마나 좋은지 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7686, 25910, 14484, '7회에서 이 식당에서 권지훈(이승준)과 노동화(고보결)의 개연성이 없는 듀오가 이 식당에서 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7687, 26214, 14484, '3화에서 박리환(이동욱)과 홍이슬(박희본)이 오후를 함께 보내며 이 골목길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7688, 32847, 14484, '3화에서 박리환(이동욱)은 홍이슬(박희본)에게 이 마트에서 아이스크림을 사자고 제안한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7689, 32848, 14484, '이 장소는 드라마 1, 2, 4화에서 박리환(이동욱)이 운영하는 \'훈과환 한의원\'으로 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7690, 25633, 14484, '6회에서는 박리환(이동욱)과 김행아(정려원)가 등장한다. 데이트를 하면서 계단 바로 앞 노점에서 오뎅을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7691, 32849, 14484, '6화에서는 박리환(이동욱)과 김행아(정려원)가 데이트를 하며 이 거리를 산책하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7692, 25715, 14484, '희본(이동욱)은 5회에서 그녀를 이 호텔로 직접 데려다준 박리환(이동욱)에게 급급한다. 그녀의 할아버지가 호흡기 마스크를 쓰고 침대에 누워 있는 이 건물의 방으로 그녀의 어머니(박준금)는 몇 분 뒤 그녀를 뒤쫓아 이 호텔에 도착했는데, 딸을 누가 내려주었는지 궁금해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7693, 25938, 14484, '이 학교는 박리환(이동욱)과 김행아(정려원)의 고등학교로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7694, 25637, 14484, '이 병원에서 일하고 있는 김행아(정려원). 3화에서 치료를 받기 위해 여기에 옵니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7695, 26152, 12837, '이 호텔은 드라마에서 지영우(김민준)의 아버지가 운영하는 호텔입니다. 12회에서는 강혜주(윤세아)가 이 호텔에 머물고, 최상현(김주혁)이 그녀를 데려오고, 윤재희(전도연)가 이곳에서 만남을 갖는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7696, 25668, 12837, '17화에서 최상현(김주혁)과 윤재희(전도연)는 지영우(김민준)와의 결혼을 파기한 직후 이 다리 밑으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7697, 24951, 12837, '윤재희(전도연)와 최상현(김주혁)은 산책을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7698, 32850, 12837, '7회에서는 윤재희(전도연)가 최상현(김주혁)에게 이 공원에서 만나자고 제안했다. 그녀는 처음에는 (야근을 해야 한다는 핑계를 대며) 거절하지만 결국 포기하고 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7699, 24913, 12837, '최상현(김주혁)은 18회에서 윤재희(전도연)를 위해 프러포즈를 준비했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7700, 27088, 12837, '16회에서는 윤재희(전도연)가 경찰에서 물러난 후, 최상현(김주혁)이 바닷가 낚시터에서 가장 좋아하는 장소에서 윤재희를 발견하고 함께 시간을 보내기 위해 이 사찰을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7701, 25678, 14354, '7, 8회에서 라준모(차태현)와 백승찬(김수현)이 1박 2일 촬영을 위해 일했던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7702, 31982, 14354, '이곳은 5화에서 백승찬(김수현)에게 받은 도전을 모두 마치고 천막에서 잠을 자는 신디(아이유)의 캠핑장이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7703, 32851, 14354, '3회에서 라준모(차태현)는 수지의 \'1박 2일\' 캐스팅에 대해 박진영과 이야기를 나누기 위해 찾아왔다. 하지만 그는 영상 통화를 통해서만 그와 대화할 수 있었습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7704, 27318, 14354, '프로듀서에 나오는 대부분의 사람들은 여기에서 작업하고 백승찬(김수현)과 탁예진(공효진) 사이의 많은 장면이 오프닝처럼 이 곳의 주차장에서 촬영되었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7705, 25856, 14354, '10화에서는 장미원 중간 구간 바로 앞에서 후배 PD 백승찬(김수현)이 인터뷰를 하는 장면이 공개됐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7706, 25827, 14571, '프로듀스 시즌 1의 101명의 아이돌 연습생들이 이곳에 머물며 훈련하는 캠퍼스로 이곳에서 숙식과 연습을 하며 촬영을 진행하였다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7707, 30593, 15749, '5회에서 하리(송승헌) 일행은 검찰을 이끌고 그의 사무실을 압수수색한 은형건설 박현종 사장을 체포한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7708, 24932, 15749, '2회에서 장검사(김원해)는 처음 받은 저울 오브 다이크 USB 드라이브가 천 회장을 감옥에 가두는 데 어떻게 도움이 되었는지 회상합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7709, 26285, 15749, '3화에서 볼 수 있듯이 나원학(김형묵)은 모든 비리 혐의가 풀린 후 식사를 한다. 바람을 피우고 있는 아내와 전화통화를 하는 곳도 이곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7710, 26511, 15749, '8회에서 동료 검사들과 점심을 먹기 위해 주선한 악당 변호사 진용준(차은표)에게 갱단이 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7711, 26125, 15749, '1화에서 돈세탁범이 28층 방에 몰래 숨겨둔 돈세탁 사건의 원성호텔로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7712, 32085, 14944, '이 곳은 8, 17화에서 배경으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7713, 25796, 14944, '18회에서 서은혜(소녀시대 권유리)가 여기에 재심을 신청한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7714, 28424, 14944, '18화에서 전 감방 동료들과 함께 저녁식사를 하기 위해 박정우(지성)와 서은혜(권유리)의 만남. 이 장소의 휘닉스룸에서 어린 석준표(안단태)(남궁민)는 이 장소의 에메랄드 홀에서 길을 잃거나 납치된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7715, 25019, 14197, '7, 8회에서 두 형제가 처음으로 다시 만나는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7716, 25615, 14197, '9회에서 형 기재명(윤균상)이 사고 후 이곳에 입원하지만 경미한 부상으로 빨리 퇴원한다. 최인하(박신혜)가 퇴원하자 그를 인터뷰하려 하고 하명이 인터뷰를 방해한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7717, 26697, 14197, '드라마에서 \'MSC 빌딩\' 으로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7718, 31779, 14197, '기하명(이종석)과 최인하(박신혜)가 단골이다. 8화에서 둘이 여기서 우동을 먹는 모습, 15화에서 두 사람의 두 번째 입맞춤은 식당 앞에서 벌어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7719, 25668, 14197, '4화에서 기하명(이종석)과 최인하(박신혜)는 옥상에서 이 다리를 바라보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7720, 25797, 14197, '최달포(이종석)는 선셋카페 앞에서 택시를 멈춘다. 이 다리 남단에 있는 구름카페 바로 맞은편에서 최인하(박신혜)의 엄마와 이성을 이야기하고 싶다. 결국 그는 늑대가 되어 그녀에게 돌아오겠다고 맹세한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7721, 25620, 14197, '8회에서 기하명(이종석)과 최인하(박신혜)의 키스가 있었던 곳.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7722, 26308, 15736, '7회에서 왕이육(정은우)이 장다야(윤진이)의 엄마의 마음에 불만을 품고 위로하고 사랑을 확인하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7723, 24932, 15736, '오은영(차화연)은이 식당에서 정기적으로 그녀의 친구들을 만납니다. 예를 들어 에피소드 1에서 그녀는 여기에서 그녀의 친구들을 만나기로 되어 있었지만(여기에서 그녀에게 전화를 걸었던) 그녀는 뒤늦은 남편의 기일을 준비하느라 그것을 완전히 잊어 버렸습니다. 5회에서는 모두 이곳에서 다시 만나기로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7724, 32860, 15736, '4화에서 김도란(유이)은 김씨 가족과 함께 이곳에서 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7725, 28541, 15736, '16화에서 김미란(나혜미)과 어머니 소양자(임예진)는 고리대금업자들을 피해 역을 통해 달아나 막판에 기차를 잡는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7726, 30542, 15736, '김도란(김유진/전 애프터스쿨의 유이)은 5화에서 엄마(생모가 아님)에게 쫓겨나 이 공원에 온다. 이곳은 여동생 김미란(나혜미 분)으로부터 아버지 김동철(이두일 분)이 사고를 당해 병원에 입원했다는 전화를 받는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7727, 32861, 15736, '이 건물은 왕대륙(이장우)과 김도란(김유진/유이, 전 애프터스쿨 멤버)이 일하는 \'봄앤푸드\' 회사로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7728, 25335, 15736, '왕대륙(이장우)과 김도란(김유진) 28회에서 출근, 불고기 시식할 때 이 다리 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7729, 25797, 15736, '부모가 친부모가 아님을 알고 \'어머니\'에게 쫓겨난 김도란(유이)는 5회에서 이 다리로 와서 아빠가 옆에 있고 그녀와 이야기하는 모습을 상상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7730, 28532, 15736, '박금병(정재선)은 이 저택에 산다. 18화에서 김도란(김유진/유이)이 그녀를 방문하는 장면에서 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7731, 26273, 15736, '강수일(최수정)은 \'하나뿐인 내사랑\'에서 출소 후 이 교회에서 빵 굽는 일을 하고 있다. 많은 장면이 담겨져있다. 28화에서 나홍주(진경)는 돌아가신 남편의 기일을 맞아 이 교회에 찾아와 남편이 보고 싶어 눈물을 흘리며 기도하는 모습이 그려졌다. 그녀는 돌아오는 길에 한쪽 방향으로 달리는 아이들을 봅니다. 그녀는 호기심에 그들을 따라 제빵실로 향하다가 강수일을 보고 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7732, 26524, 15736, '1회에서 일(최수종)은 이곳에 수감되었고 김동철(이두일)은 그를 정기적으로 방문하고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7733, 25989, 14249, '에피소드 3에서 구씨 가족 별장으로 나타납니다. 장하나(한지민)가 구서진(현빈)을 찾으러 갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7734, 25607, 14249, '하이드 지킬앤아이 12회에서 구서진(현빈)과 장하나(한지민)가 이 공원의 메타세콰이어 골목길을 산책하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7735, 25612, 14249, '마지막 20회에서 이 정원에서 장하나(한지민)에게 자신의 마음을 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7736, 31982, 14249, '3회에서 구서진(현빈)의 집 정원으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7737, 26002, 14249, '드라마에서 구서진(현빈)이 운영하는 놀이공원 \'원더랜드\'로 등장하며, 여기서 장하나(한지민)는 서커스의 매니저로 활동한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7738, 29174, 14249, '극 중 \'Wonderland Office Building\'으로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7739, 25859, 16771, '14회에서 차유리(김태희)와 고현정(신동미)이 이 섬에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7740, 29500, 16771, '유리와 현정이 탔던 짚라인');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7741, 25607, 16771, '유리가 귀신들에게 쫓기던 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7742, 32888, 16771, '16회에서 차유리(김태희)가 미동댁(윤사봉)에게 이별을 고한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7743, 32889, 16771, '유리 어머니가 다니는 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7744, 26040, 16771, '드라마 안에서 주인공인 고현정(신동미)이 운영하는 술집으로 등장한다. 드라마의 메인 촬영 장소로 여러 회차에서 해당 장소를 확인할 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7745, 24980, 16771, '강화와 유리가 바다 데이트를 즐겼던 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7746, 26045, 16771, '과거에 강화와 유리가 놀러갔던 놀이공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7747, 25718, 9125, '1회에서 이 공원의 \'화이트 스캔들\' 소송 결과에 대한 여론조사를 읽고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7748, 30831, 9125, '1회에서 윤희재(주지훈)는 이 카페 옥상에서 브라이틀링 시계를 들고 정금자(김혜수)에게 마음을 고백한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7749, 32891, 9125, '무자비한 변호사 정금자(김혜수)가 건물 밖 교통섬에 서서 쳐다보며 살 날을 꿈꾸는 모습이 여러 번 등장한다. 그녀의 권위 있는 법률 사무소를 엽니다. 마지막 에피소드에서 그녀는 이 장소(원래 건물)에서 자신이 고아로 버려졌다가 입양되었다가 반복해서 돌아왔다는 사실을 공유합니다. 그녀는 현재 건물을 소유함으로써 과거의 악마를 정복할 것입니다. 가격에 대한 문의를 많이 하고 있지만 드라마가 진행되는 동안 건물을 사지 못한다. 마지막 장면에서 변호사 윤희재(주지훈)와의 성공적인 파트너십은 미래에 일어날 수 있음을 암시한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7750, 26644, 9125, '윤희재(주지훈)는 1회에서 이 술집에서 가지혁(전석호)과 술 한 잔한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7751, 28128, 9125, '2회에서 이 식당은 엘리트 변호사 윤희재(주지훈)와 상속녀 리바운드 여자친구 최연지(정)가 만나는 일식집 \'사이\'로 등장한다. 맛있는 사시미로 이별. 둘 다 모르는 사이 희재의 전 여자친구인 변호사 정금자(김혜수)는 옆 테이블 커튼 뒤에 앉아 그의 재벌 고객을 밀렵하기 위한 그녀의 다음 행보를 모의한다. 계속되는 충돌의 결과는 희재가 싱글이 되어 다시 화를 내고, 아무도 값비싼 점심을 끝내지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7752, 25871, 9125, '11화에서 정금자(김혜수)와 케빈 정(김재철)이 식사를 하며 그노시스에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7753, 25796, 9125, '윤희재(주지훈)가 일하는 곳. 같은 회 말미 희재는 자신의 모습을 보고 깜짝 놀란다. 이 법정에서 양육권 소송에서 정금자(김혜수)와 반대 입장을 하고 풀려났지만 금자에게 해명을 묻지 않은 채 법원 주차장에서 답변으로 그와 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7754, 32892, 9125, '10회에서 윤희재(주지훈)는 맛있는 옥수수 피자 점심과 한국의 상속세 설명회를 위해 직장 팀을 이 피자 알볼로 지점으로 데려갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7755, 25335, 9125, '1회 \'하얀 스캔들\' 소송 승소 후 가혁(전석호)이 윤희재(주지훈)를 몰아 승리를 축하하기 위해 드렁큰 타이거의 "Double Up"을 들으면서 이 다리를 건너 로펌으로 돌아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7756, 25910, 9125, '8회에서 한 잔 하러 정은영(김혜수)과 윤희재(주지훈)는 이 식당에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7757, 25913, 9125, '윤희재(주지훈)가 근무하는 송앤김 법률사무소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7758, 26198, 18046, '1회에서 이 도서관은  \'HSC 국제학교\' 의 일부로 등장한다. 송여울(조여정)과 아들 안이찬(장선)이 학교 입학 인터뷰 전에 방문하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7759, 25835, 18046, '10회에서 남지선(김지수)은 이 술집에서 \'HSC국제학교재단\' 금융이사 알렉스 코머(김성태)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7760, 26085, 18046, '이 건물은 1회에서 \'HSC International School\'의 일부로 등장한다. 송여울(조여정) 학교에서 아들 안이찬(장선율)의 입학 면접을 기다리고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7761, 24946, 18046, '13회에서 카지노로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7762, 26273, 18046, '10회에서 황나윤(박세진)이 이 교회로 추억여행을 하며 생각한다. 이 곳에서 안지용(김남희)이 그녀에게 프러포즈를 했던 순간으로 거슬러 올라간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7763, 25781, 18046, '14화에서 \'Panow\' bar로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7764, 24935, 16569, '주서연의 (오연서) 오빠 주원석(차인하)이 운영하는 게이바이다. 1화에서 서연은 정태(강태오 카메오)와 헤어진 후 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7765, 26410, 16569, '이혁(구원)은 아버지를 초대한다. 8화에서 잠시 한국에 들른 이 식당에 가다가 혼잣말을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7766, 26086, 16569, '이 대학 공학부 앞 \'한국대\'로 등장, 하자있는 인간들(2019-20) 5화(전체 3화)에서 주원석(차인하)이 최호돌(장유상)의 지갑을 되찾길 기다리는 모습. 원석은 또한 호돌에게 자신이 연애할 생각이 없다는 사실을 알린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7767, 29430, 16569, '이강우(안재현)가 주서연(오연서)을 위해 이 장소의 마켓홀에서 깜짝 고백을 준비한다. 5회에서 밤에 만나자고 한다. 그녀가 홀에 들어서자 조명이 켜지고 강우가 나타나며 서연이 이게 뭐냐고 묻자 서연은 먼저 그녀의 육상팀을 후원하기로 결정했다고 대답한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7768, 29514, 16569, '2화에서 주원재(민욱혁)는 이 호텔 입구 앞 클럽에서 하룻밤 외출 후 차가 오기를 기다린다. 이강희(황우슬혜)가 도착했을 때, 그녀는 그를 지갑 소년으로 착각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7769, 27236, 16569, '14회에서 주원석(차인하)이 최호돌(장유상)을 이 카페로 데려가 자신의 상처를 이야기했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7770, 27603, 16569, '이 집의 외관은 세 형제와 함께 사는 주서연(오연서)의 집으로 나타난다. 13,14회에서는 이강우(안재현)와 이민혁(구원)이 티앤제인 앞에 서 있는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7771, 25726, 16569, '14회에서 주서연(오연서)가 이 가게들을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7772, 26293, 16569, '10화 전체에서 이 카페에 오씨(윤해영) 그녀의 물음에 그는 사촌 이강우(안재현)가 주서연(오연서)을 사랑이 하고 있다고 확인한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7773, 26263, 16569, '2회에서 소속사 메이트는 갑자기 눈앞에서 배달부로 일하는 동급생 이주희(주해은)를 보게 된다. 이 공원에 있는 이발소. 친구에게 작별 인사를 하고 여동생 주서연(오연서)이 자신을 찾고 있음을 알린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7774, 26000, 16569, '주서연(오연서)은 7화에서 이강우(안재현)가 따라오며 아침 조깅으로 이 다리를 건너는 모습을 보여준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7775, 26002, 16569, '10회 풀타임에서 주서연(오연서)과 이강우(안재현)가 첫 리얼 데이트로 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7776, 27554, 16569, '3화에서 이 공원의 원형 극장으로 이동한다. 동시에 주서연(오연서)은 이곳에 도착해 제자 주희와 말다툼을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7777, 25882, 16569, '1화에서 백장미(신도현)가 여의도 공원 이 부분에 가까이 앉아 운동을 하고 있다. 돈 가득한 가방을 들고 돈이 많아도 소용없다고 생각한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7778, 25335, 16569, '3회동창회에서 그를 태운 우(안재현)가 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7779, 31676, 16569, '주서연(오연서 분)과 이강우(안재현 분)가 10회에서 첫 번째 데이트에 이 사진관에서 커플 사진을 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7780, 32893, 16569, '드라마 안에서 이 집은 이강우(안재현)의 집으로 자주 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7781, 26215, 16569, '이 장소는 박현수(허정민)가 운영하는 탤런트 기획사 \'하이세븐 엔터테인먼트\'( HS엔터테인먼트)에서  형 주서준(김재용)이 훈련하고 있다. 그녀는 1화에서 제자의 행방을 찾던 그에게 부탁을 하기 위해 그를 만나러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7782, 25340, 16569, '김미경(김슬기)과 주서연(오연서)은 이 학교에서 교사로, 이민혁(구원)은 학교의 의사로 일한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7783, 24953, 16569, '1화에서 이강우(안재현)와 이민혁(구원)이 가족 만찬을 마치고 이 식당을 찾아왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7784, 25912, 16569, '10회 본편에서 그들의 첫 실제 데이트 장소로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7785, 25913, 16569, '13회에서 공개적으로 그녀에 대한 마음을 고백하지만 그녀에게 진지하게 받아들이지 않는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7786, 25781, 16569, '5화(전체 3화)에서 박현수(허정민) 소속사 모델 중 한 명이 이 방에 묵는다. 현수는 모델이 광고 촬영 약속을 잊어버려서 그를 찾으러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7787, 26412, 16569, '1화에서 파노라믹65는 \'판타지아\'로 등장한다. 이 곳은 정태(강태오)가 주서연(오연서)에게 만나자고 하는 프러포즈 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7788, 25017, 16569, '1화에서 이강우(안재현)가 한국의 이 공항에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7789, 32894, 14252, '차홍도(최강희)가 사는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7790, 26711, 15089, '하트시그널2 정재호 송다은의 데이트코스이자 강릉의 인기 해변');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7791, 32895, 15089, '임현주와 김현우가 함께 탄 설악산 케이블카');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7792, 32896, 15089, '하트시그널2 순대국을 좋아하는 현우를 위해 영주가 찾은 맛집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7793, 26421, 15089, '하트시그널2 촬영지이자 분위기 좋은 데이트 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7794, 28112, 15089, '하트시그널3 촬영지이자 산책하기 좋은 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7795, 32897, 15089, '하트시그널3 촬영지 동탄호수공원 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7796, 25748, 15089, '오영주와 이규빈이 서로 책을 선물한 책방');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7797, 27173, 15089, '오영주와 이규빈이 산책한 동백섬');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7798, 31365, 15089, '하트시그널2 도균이 현주에게 마카롱을 선물해주려고 찾아간 프랑스 빵집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7799, 32898, 15089, '하트시그널3 이가흔&천인우 이탈리안 레스토랑');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7800, 32899, 15089, '김현우와 임현주가 크리스마스에 방문한 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7801, 32900, 15089, '하트시그널3 이가흔&천인우의 도자기공방');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7802, 31845, 15089, '김장미,김도균이 데이트한 원데이 클래스');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7803, 32901, 15089, '김도균, 송다은이 크리스마스에 방문한 한우식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7804, 32902, 15089, '하트시그널3 이가흔&천인우의 성수데이트');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7805, 32903, 15089, '김현우가 사온 이태원 마카롱');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7806, 32904, 15089, '오영주와 김현우의 마지막 데이트 코스 일식집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7807, 24948, 15089, '장천과 서지혜가 산책했던 낙산공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7808, 27862, 15089, '하트시그널3에 방영된 익선동 프랑스 가정식 요리');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7809, 32905, 15089, '하트시그널2 종로 인사동 막걸리집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7810, 32906, 15089, '하트시그널3 이탈리안 스몰디쉬바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7811, 32907, 15089, '하트시그널3 박지현이 좋아하는 빵집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7812, 32908, 15089, '오영주, 이규빈이 데이트했던 재즈 바');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7813, 30543, 15089, '하트시그널2 스페셜 촬영지인 평창동 카페');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7814, 32909, 15089, '하트시그널3 박지현 임한결 서촌 데이트 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7815, 29279, 15089, '오영주와 이규빈이 방문한 돈카츠 식당');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7816, 32910, 15089, '하트시그널3 데이트 장소인 풍경이 아름다운 절');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7817, 29734, 15089, '강화도 칼국수 맛집이자 하트시그널3 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7818, 31665, 15089, '김장미,김도균이 탄 여수 해상 케이블카');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7819, 28648, 15089, '하트시그널2 장미와 도균이 소원 빌었던 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7820, 32911, 13778, '이 학교는 \'승리고등학교\' 로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7821, 26066, 15122, '현태운(김정현)의 어머니가 묻힌 곳이 바로 여기다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7822, 32912, 15122, '이 드라마에서 주요 고등학교로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7823, 32913, 15122, '15회에서 홍남주(설인아)는 학교를 그만두고 이곳에서 아르바이트를 한다. 송대휘(장동윤)가 찾아와 커피를 주문한다. 그는 그녀가 교대 근무를 마칠 때까지 그녀를 기다리며 그녀가 보고 싶으니까 학교로 돌아오라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7824, 25661, 15122, '13회에서 라은호(김세정)와 현태운(김정현)이 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7825, 25712, 15122, '라은호(김세정)와 송대휘(장동윤)는 지난 10일 한 벤치(프로덕션 소품)에서 이야기를 나눴다. 에피소드 4에서 이 박물관 앞에서 대화를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7826, 27000, 15122, '이 대학은 한국 대학으로 나타납니다. 라은호(김세정)는 자신의 짝사랑 종근(강민규 카메오)이 이곳에서 공부하기 때문에 이 대학에 입학하기를 원한다. 1화에서는 현태운(김정현)이 \'데이트\'를 위해 이곳에 하차하고, 3화에서는 합격 시험을 위해 하차하는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7827, 25887, 18273, '진지원(조이현 분)과 김요한(공기준 분)은 16회에서 이 거리를 방문해 이 집 앞에서 셀카를 찍는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7828, 26214, 18273, '16회에서 진지원(조이현)과 김요한(공기준)의 데이트 현장이 골목길을 걷고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7829, 24922, 18273, '마지막 16회에서 진지원(조이현)과 김요한(공기준)이 데이트를 위해 이 거리에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7830, 25937, 18273, '1회에서 답답한 진지원(조이현)이 밤에 이 미니마트에서 인형뽑기를 하러 온다. 그녀가 다시 이기지 못해 기계를 차기 시작할 때, 그녀의 새 학교 친구 정영주(추영우)는 기계를 채우기 위해 오토바이를 들고 들른다. 그가 떠나자 지원은 기계 위에 앉아 있는 테디를 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7831, 27066, 18273, '1화에서 처음으로 등장하는 \'눌지과학기술고등학교\'를 오가는 통로다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7832, 27220, 18273, '14회에서 정영주(추영우)를 구해준 김요한(공기준)과 진지원(조이현)은 이 공원에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7833, 26445, 18273, '6화에서 옛 동료 이강훈(전석호)과 정철주(서재우)가 이 카페에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7834, 25617, 18273, '이 강변 공원은 진지원(조이현)의 집을 오가는 길로 등장합니다. 4화에서 김요한(공기준)의 집으로 가는 길에 공원을 달리는 모습이 처음으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7835, 26278, 16834, '송다희(이초희)와 윤재석(이상이)은 64화(전체 32화)에서 이 성벽을 따라 계단을 따라 앉아 있다. 헤어진 후 서로에게 사과하고 화해했습니다. 그들이 첫 키스를 나누는 것도 여기에서 하였다. 그리고 자신도 모르는 사이에 누나 송나희(이민정)와 오빠 윤규진(이상엽)에게 자신도 모르게 목격된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7836, 32914, 16834, '전날 밤 자신의 집에서 어머니와 형 윤규진(이상엽) 앞에서 만취한 것에 대해 사과하는 곳으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7837, 27183, 16834, '송나희(이민정)와 윤규진(이상엽)은 동강병원과 태화강 사이 공원 동쪽에서 오후 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7838, 26323, 18421, '6회에서 재벌 남친 구지표(한규원)는 성미도(레드벨벳 조이)를 데이트 장소로 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7839, 29424, 18421, '8회에서 음악을 들으며 혼자 여기에서 전화를 받고 앉아 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7840, 32915, 18421, '11화에서 경찰관 오진규(장인섭)가 성미도(레드벨벳 조이)의 차를 이 곳으로 추적한다. 이혼 전문 변호사 지윤서(이영진)와의 만남에서 강세연(강예원)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7841, 26085, 18421, '표인숙(안은진)은 멍하니 의사의 진찰을 받고 나와 이 건물의 출구를 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7842, 25773, 18421, '5화에서 강세연(강예원)은 이 공항에서 회상장면으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7843, 32916, 18421, '바닷가 은신처를 떠난 표인숙(안은진)과 민우천(김경남)은 버스를 타고 서울로 돌아오며 이 버스터미널에 도착한다. 터미널을 나오자 인숙은 몸이 안 좋아지기 시작했고, 우천에게 이제 그만 도망쳐야 할 때라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7844, 25953, 18421, '3화에서 강세연(강예원)은 어머니를 모시고 이 호텔(이하 몽호텔)의 스위트룸으로 들어갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7845, 32917, 18421, '민우천(김경남)이 향하는 1화에서 보듯 \'나래청소\'는 사설탐정회사의 표지인 \'나래청소\'가 있는 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7846, 25657, 18421, '7,9회 회상 장면에서 볼 수 있듯이 강세연(강예원)과 지윤서(이영진)가 학창시절 만났던 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7847, 25661, 18421, '14회에서는 표인숙(안은진), 성미도(레드벨벳 조이), 강세연(강예원)이 새 옷을 입고 쇼핑을 하고 거리를 활보하는 모습이 그려졌다. 6회에서 두 사람 모두 현금이 없다는 사실을 알게 된 표인숙(안은진)과 민우천(김경남)은 이 골목 계단에 앉아 어떻게 할지 고민한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7848, 25662, 18421, '표인숙(안은진), 성미도(레드벨벳 조이), 강세연(강예원)은 14회에서 쇼핑을 마치고 애프터눈 티를 마신다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7849, 30755, 18421, '6회에서 민우천(김경남)은 표인숙(안은진)을 이 떡볶이 가게에 데려간다. 하지만 먹고 살 돈이 없다는 사실을 알게 된 두 사람은 당황한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7850, 32918, 18421, '11회에서 표인숙(안은진)과 민우천(김경남)은 이 역(\'고산역\'으로 나타남)의 10번 출구로 나오며, 그들과 교류를 원하는 다양한 사람들에게 즉시 다가가는데, 그런 다음 그들은 12번 출구를 통과하여 차에 치인 소년을 간신히 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7851, 26000, 18421, '10회에서 구지표(한규원)가 다리를 따라 조깅을 하던 중 고하용을 던진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7852, 32919, 18421, '한사람만(한사람만) 10회에서 민우천(김경남)은 병원에서 탈출한 후 표인숙(안은진)을 이 골목길로 데려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7853, 25776, 18421, '15회에서 민우천(김경남)과 함께 인숙 어머니의 추도식을 마치고 이 식당에서 식사를 하는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7854, 32549, 18421, '2회에서 \'알리바이\'의 일환으로 성미도(레드벨벳 조이)는 표인숙(안은진)과 강세연(강예원)을 이 \'클럽\' 파티에 데려갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7855, 27361, 18421, '병원을 탈출한 표인숙(안은진)과 민우천은 교차로에서 성미도(레드벨벳 조이)와 강세연(강예원)에게 데리러 간다. 11화에서 청계광장을 배경으로 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7856, 28122, 18421, '8회에서는 성미도(조이)와 구지표(한규원)의 약혼식이 펼쳐진다. 파티를 마친 두 사람은 각자의 방으로 돌아가 술에 취한 미도가 지표에게 비밀을 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7857, 32920, 18421, '1회에서 가게 손님과 충돌이 생긴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (7858, 32768, 18421, '1회에서 불량차량이 이 미용실을 지나 길을 질주한다.');
