INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8070, 29499, 14573, '16회에서 김건학(김태훈)은 이곳에서 아내를 위해 화분을 산다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8071, 25637, 14573, '송수혁(정경호)의 친구 구해준(권율)과 우연수(황선희)가 이곳에서 일하며 장소는 \' 사랑병원\'. 그 중 1층, 11층 병동, 수술실, 대기실, 1층 베란다가 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8072, 33071, 14743, '2회에서 노을(배수지)은 겨울에 이 거리에서 자신의 삶에 대한 다큐멘터리를 촬영하도록 신준영(김우빈)을 설득하려 하고, 그녀는 결국 쓰러진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8073, 25604, 14743, '드라마에 나오는 신준영(김우빈)의 집으로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8074, 26521, 14743, '10회에서 신준영(김우빈)이 교통사고를 당한 노을(배수지)을 데려오는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8075, 27319, 14743, '6화에서 신준영(김우빈)이 여기에 숨어 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8076, 32183, 14743, '5회 신준영(김우빈)이 이곳에서 콘서트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8077, 25724, 14743, '17회에서 신준영(김우빈)이 윤정은(임주은)을 만나 자신을 멀리하라고 말하는 장면이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8078, 33072, 14743, '드라마 안에서 신준영(김우빈) 엄마가 운영하는 식당이 이 건물에 위치하는 것으로 설정되었다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8079, 28422, 14743, '이 교회는 신준영(김우빈)이 1회에서 촬영한 영화의 배경이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8080, 26067, 13608, '정조의 원대한 꿈과 효심이 느껴지는 화성행궁');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8081, 25945, 13608, '해를 품은 달의 첫 촬영지인 경복궁');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8082, 25653, 16019, '해치의 장면들이 이곳에서 촬영되었는데, 트레일러에서 확인된 것은 궁궐부분의 광화문 입구와 사헌부 입구, 또는 법무부[司憲府]이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8083, 25603, 16019, '2회에서 이금(정일우)과 이권/연령군주(노영학)가 정자에서 심쿵 토크를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8084, 26124, 9587, '6회부터 나오는 이 법정은 \'어느날\'에서 김현수(김수현)의 재판이 열리는 곳이다. 최종화 12화의 간단한 법정 장면이 여기서 진행됩니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8085, 26030, 17435, '7회에서 데일리코리아 신문 박 대표(김재철)가 이곳에서 정의원(최승일)과 고의원(신현종)을 만나 점심을 먹습니다. 그들은 언론과 법조계를 성공적으로 조작하여 고의원을 채용 청탁 혐의에서 무죄 판결을 내리고 정의원 아들을 음주 운전 혐의에서 벗어났다고 자축합니다. 온갖 더러운 일을 하던 에디터 윤(이지훈)도 이들에 가세합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8086, 25796, 17435, '3회에서 한국일보 디지털뉴스데스크 정세준(김원해) 부편집장이 무죄 판결을 받은 뒤 이 법원 앞 계단에서 사기 혐의를 추가하며 MP 고(신현종)를 부추기려 한다. 그 의원은 뇌물을 받고 기사를 조작하는 기자에 대한 자신의 주장으로 반박한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8087, 24951, 14842, '1화에서 서울광장에서 처참한 공연을 한 뒤 헤어진 다음 날 유탄(김명수)은 여자친구 문다인(윤소희)과 거리를 나섰고 이 다리를 건넜다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8088, 26745, 14842, '1화에서 음울한 공연을 펼치는 공간으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8089, 25913, 14842, '1화에서 강예슬(이태임)은 KG 엔터테인먼트 사옥의 이 회의실에서 유탄(김명수)을 만난다. 자신에게 제안된 전속계약을 살펴보던 중, 1억여 원의 수표를 받고 깜짝 놀란다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8090, 32082, 18740, '신해준(신동미)은 약혼자에게 남동생과 시누이를 소개한다. 30회에서 이윤재(오민석)가 이곳 3층에 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8091, 28109, 18740, '1화에서 이현재(윤시윤) 변호사가 의뢰인 현미래(배다빈)를 이 카페에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8092, 25678, 18740, '35화에서 이현재(윤시윤)와 현미래(배다빈)가 데이트를 위해 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8093, 26038, 18740, '그녀의 장모 윤정자(반효정)의 응원을 받는 수정');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8094, 33090, 18740, '진수정(박지영)은 5회에서 아버지의 작품에 대한 인터뷰를 위해 기자와 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8095, 26137, 18740, '1화에서 결혼하지 않은 세 아들의 엄마 한경애(김혜옥)가 이 호텔의 결혼식에 참석한다. 그리고 그녀는 이 호텔의 \'테라스 라운지\'에서 지인들 앞에서 아들들을 변호하는 자신을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8096, 29446, 18740, '현미래(배다빈)는 남편 박준형(이현진)을 혼인 무효 소송으로 \'1인칭\'으로 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8097, 32594, 18740, '3화에서 이현재(윤시윤), 이윤재(오민석), 이수재(서범준) 3형제가 밤에 공원에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8098, 26153, 18740, '36회에서는 이현재(윤시윤)와 현미래(배다빈)의 각 가족이 결혼식을 앞두고 이 단지에서 만나 소개를 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8099, 26666, 18740, '14회에서 이현재(윤시윤), 이윤재(오민석), 이수재(서범준) 3형제가 밤에 술을 마시러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8100, 26679, 18740, '8화에서 현미래(배다빈)는 이 카페에서 첫 손님을 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8101, 25608, 18740, '16회에서 이현재(윤시윤)와 현미래(배다빈)가 심야 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8102, 26737, 18740, '16회에서 이윤재(오민석)는 심해준(신동미)을 저녁 데이트에 데리고 나와 이 주차장에 주차한다. 식당에서 몇 번의 불상사가 있은 후, 그들은 결국 이곳에서도 키스를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8103, 26992, 18740, '3회에서 다빈과 그녀의 (여전한) 남편 박준형(이현진)이 밤에 이곳을 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8104, 25797, 18740, '25회에서 고민하는 이수재(서범준)가 밤에 이 다리를 걷고 그가 SOS를 볼 때 위기의 전화 그는 망설이지 않고 전화를 건다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8105, 33091, 18740, '11, 13, 14화에서 현미래(배다빈)는 부모님, 오빠, 할머니와 함께 이 저택에 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8106, 26741, 18740, '현재는 아름다워 (현재는 아름다워) 11회에서 이수재(서범준)와 나유나(최예빈)가 밤에 공원을 지나고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8107, 24913, 18740, '4회에서 좋은 친구 이수재(서범준)와 나유나(최예빈)가 밤길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8108, 33092, 9223, '드라마에서 강민재(김재원)가 의사로 일하는 곳입니다. 그리고 유민(정다빈)의 동생 한강표(허정민)가 치료받는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8109, 32157, 9223, '11회에서 강승재(윤계상)는 이 공연장에서 한유민(정다빈)과 그의 동생 강민재(김재원)에게 공연 티켓을 준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8110, 33093, 9223, '모든 중요한 회의가 여기에서 열립니다. 예를 들어 2,3회에서 강민재가 한유민에게 계약 제안을 하고, 그녀가 아버지와 저녁식사를 하러 오는 장면이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8111, 26744, 9223, '10화에서 강승재(윤계상)와 최수지(김민희)가 다리 밑에서 한유민(정다빈)을 왜 그렇게 사랑하는지 묻는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8112, 25335, 9223, '강민재가 여기에 와서 마침내 그의 오랜 짝사랑을 부르기 위해 용기를 낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8113, 25668, 9223, '11화에서는 이 곳에서 모두가 케잌을 먹고 폭죽을 쏘며 유민의 생일을 축하합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8114, 25340, 9223, '강승재(윤계상)는 이 고등학교에 다닌다. 4회에는 한유민(정다빈)이 합류한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8115, 32829, 9223, '강민재는 호텔 밖 계단에서 한유민을 발견한다. 강승재의 과거 발언으로 인해 그녀가 상처를 입었다는 사실을 알게 된 그는 그녀 옆에 앉아 그녀를 위로한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8116, 33094, 16317, '호텔델루나 내부에 있는 해수욕장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8117, 25607, 16317, '구찬성(여진구)은 이곳에서 결혼 주문과 함께 빨간 주머니를 찾고, 장만월(이지은)은 그를 붙잡기 위해 모든 것을 다하다가 마침내 그의 친구 산체스(조현철)에게 발견된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8118, 24930, 16317, '1회에서 찬성의 아버지(오지호 특별출연)가 건물을 탐방하던 호텔 정원의 일부로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8119, 33095, 16317, '2화에서 빵과 마카롱을 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8120, 31916, 16317, '호텔 델루나의 삼도천 다리 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8121, 33096, 16317, '눈 없는 귀신이 기억하는 사람이 일하는 빵집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8122, 27347, 16317, '2화에서 깊은 원한을 품은 귀신이 구찬성(여진구)을 습격하자 그는 호텔을 뛰쳐나와 이 캠퍼스의 호수로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8123, 33097, 16317, '눈 없는 귀신이 기억하는 사람을 찾기 위해 간 빵집');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8124, 26080, 16317, '이 공원의 일부는 15회에서 조선시대 \'만월당 여관\'이나 \'만월 여관\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8125, 25612, 16317, '왕회장이 손녀인 설리의 꿈에서 만난 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8126, 25908, 16317, '11화에서 호텔 델루나의 404호실로 등장하는 장소이다. 이곳을 나와 도착하는 인간 부부의 모습을 볼 수있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8127, 24900, 16317, '16회에서 구찬성(여진구)는 김유나(강미나)를 이 도서관에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8128, 27387, 16317, '아이유가 박유나와 처음 만난 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8129, 33098, 16317, '아이유, 여진구가 신을 돌려놓기 위해 간 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8130, 25345, 16317, '피오가 죽어서 아이유를 만날 당시의 델루나');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8131, 26670, 16317, '여기는 장만월(아이유/이지은)이 연결된 문트리가 있는 호텔로 나온다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8132, 25653, 16317, '과거의 만월당으로 간 여진구 촬영장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8133, 27137, 16317, '5회에서 김유나(강미나)가 지현중(블락비 피오/표지훈)에게 샌드위치를 먹으러 가자고 합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8134, 24937, 16317, '호텔 델루나 10~12회와 14회에서는 장만월(이지은)이 이곳에서 원한에 가득 찬 유령 설지원(데이비드)에게 손을 내미는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8135, 33099, 16317, '1화에서 지하철에서 장만월(이지은)을 만난 후 놀란 구찬성(여진구)이 지하철역에 내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8136, 33100, 16317, '1회에서 구찬성(여진구)은 해맞이 선물인 달맞이꽃을 손에 들고 이곳에서 호텔 델루나 방향으로 지하철을 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8137, 32643, 16317, '5회에서 구찬성(여진구 분)이 약국을 찾기 위해 이 시장에 머문다. 정문에서 출발해 2번 게이트로 향하고 마침내 인사한의원으로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8138, 33101, 16317, '이 집은 8회에서 이미라(박유나)의 친구 집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8139, 33102, 16317, '2화에서 회를 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8140, 26541, 16317, '오지호와 아역 김강훈이 마고신과 만난 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8141, 26658, 16317, '호텔델루나 내부에 있는 도서관');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8142, 26542, 16317, '2회에서 장만월(이지은)과 구찬성(여진구 분)은 신발이 잿더미에 갇히면서 \'잃어버린\' 후 신발을 사러 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8143, 32892, 16317, '산체스(조현철)의 가게로 피자집 촬영 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8144, 25724, 16317, '아이유가 받은 금괴를 확인한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8145, 25335, 16317, '2회에서 구찬성(여진구)은 장만월(아이유/이지은)을 차에 태우고 이 다리를 건넌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8146, 33103, 16317, '1회에서 장만월(아이유/이지은)과 구찬성(여진구)이 만두를 먹고 이 거리를 걷는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8147, 26156, 16317, '1화에서 1989년 구현모(오지호)가 아들 구찬성(김강훈)과 함께 반포대교 북단 구간 이 공원 맨 끝으로 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8148, 33104, 16317, '호텔 델루나 11회 회상에서는 이 호텔이 1981년의 구 호텔 델루나로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8149, 33105, 16317, '과거 호텔 델루나의 촬영 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8150, 33106, 16317, '이 한옥은 5, 8, 12회에서 볼 수 있듯이 호텔 델루나에서 산체스의 집으로 등장한다. 여기에 구찬성(여진구)도 살고, 8화에서는 이미라(박유나)도 이사를 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8151, 25954, 16317, '구찬성(여진구)은 1회에서 택시를 기다리는 한 여인을 보게 된다. 그는 그녀에게 다가가 자신이 먼저 온 거라고 말하지만, 그녀의 대답은 "그를 볼 수 없다"라는 것이었고, 그녀는 눈이 없는 유령으로 밝혀진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8152, 26404, 16317, '1회에서 찬성(여진구)은 자신을 쫓는 여자 귀신에게 쫓겨났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8153, 27009, 16317, '1화에서 구찬성(여진구)은 여자 귀신을 피해 카페 앞을 질주하고, 장만월(아이유/이지은)은 평행벽에서 균형을 잡던 중 그의 뒤를 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8154, 33107, 16317, '1회에서 구찬성(여진구)은 여자 귀신을 탈출하기 위해 이 거리를 질주하고 버려진 물건들을 덮는 모습을 그린다. 장만월(아이유/이지은)은 건물의 벽 너머로 숨어있는 찬성을 내려다보더니 조약돌을 던지고 다시 뛰게 하여 그를 놀라게 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8155, 24922, 16317, '1회에서 구찬성(여진구)은 지하철에서 장만월(아이유/이지은) 화분을 들고 이 거리를 걷다가 만난 일을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8156, 27124, 16317, '1회에서 구찬성(여진구)이 이 벽화를 배경으로 길을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8157, 33108, 16317, '아이유, 여진구가 만두먹으러 간 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8158, 33109, 16317, '1회에서 구찬성(여진구)을 여자 귀신으로부터 구한 장만월(아이유/이지은)이 이 식당에서 밥을 먹자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8159, 28134, 16317, '1회에서 구찬성(여진구)이 눈이 없는 여자 귀신에게 쫓기면서 미친 듯이 이 거리를 달린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8160, 32258, 16317, '김유나(강미나)가 사는 집으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8161, 32250, 16317, '눈 없는 귀신이 사고를 당한 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8162, 33110, 16317, '2화에서 팥죽과 찹쌀떡을 먹은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8163, 26146, 16317, '10화에서 장만월(아이유/이지은)과 구찬성(여진구)이 비즈니스를 위해 캠퍼스를 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8164, 26684, 16317, '마지막회에서 아이유, 여진구가 벤치에 앉은 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8165, 26125, 16317, '1화에서 구찬성(여진구)이 근무하는 호텔 \'로열 퍼시픽\'이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8166, 32271, 16317, '장만월(이지은)과 그녀의 직원들이 수세기 동안 운영해 온 죽은 영혼들을 위한 호텔 \'호텔 델루나\'의 외관이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8167, 26487, 16317, '장만월(이지은)은 5회에서 연우(이태선)와 함께 이 나무 밑에 서서 자신이 가장 좋아하는 나무로 집을 지어주겠다고 약속했던 때를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8168, 33111, 16317, '삼도천 터널 촬영지');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8169, 31719, 14042, '호텔킹 26회에서 차재완(이동욱)과 아모네(이다해) 비 맞으며 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8170, 27369, 14042, '32화에서 차재완(이동욱)과 아모네(이다해)가 이 방파제에 온다. 이곳은 그가 그녀에게 그가 그녀와 함께 할 수 없다고 말하는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8171, 33112, 14042, '호텔킹 31회에서 어머니 백미녀(김해숙)와 씨엘호텔에 취직한 뒤 이곳으로 이사온다. 호텔로 돌아갈 때까지 이곳에 머물다가 카페를 이어받은 \'형\' 로만 리(진태현)');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8172, 24925, 14042, '이 호텔은 차재완(이동욱)이 운영하는 시엘호텔로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8173, 25643, 14811, '8회에서 진정숙(하식진)과 박하나(박하선)가 와인 시음을 하는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8174, 33113, 14811, '혼술남녀에서 김기범(김기범)과 김동영(김동영)의 학교 식당으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8175, 30541, 14811, '이 거리에는 진정석(하석진)과 박하나(박하선)가 가르치는 술취한 솔로의 예비학교도 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8176, 25959, 14811, '1화에서 진공명(공명)과 김기범(키)이 이곳에서 만납니다. 술취한 솔로 공명이 기범을 만나러 왔을 때 친구를 기다리다 형 수업 광고 포스터에 덫을 놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8177, 33114, 14811, '1회에서 진공명(공명)과 김기범(SHINee 키)이 아케이드 게임을 마치고 카페에 들르는 모습이 그려졌다. 여자친구(하연수)와 친구 김동영(김동영)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8178, 27179, 14811, '15회 갤러리 나윤춘의 \'이미 추억\' 그림 앞에서 진정석(하식진)은 박하나(박하선)와 헤어진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8179, 26249, 14811, '16회에서 이별 만찬을 위해 공명은 이곳에 박하나(박하선)를 데려갑니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8180, 25668, 14811, '9회에서 여기는 박하나(박하선)가 자신을 버린 진정석(하석진)에게 짝사랑하고 있다는 사실을 깨닫고 가는 곳이다. 황진이(황우슬혜)가 그녀를 데리러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8181, 26242, 14811, '15회에서 이 곳에서 이별여행을 한다. 기범(샤이니 키)은  63빌딩을 가까이서 보고 싶다는 채연(다이아 정채연)의 소원을 들어준다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8182, 26232, 14811, '여기에서 박하나(박하선)가 술에 취해 좌절감을 풀기 위해 가는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8183, 33115, 14811, '8회에서 진정숙(하석진)이 혼자 먹고 마시는 곳입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8184, 25930, 14811, '4회에서 박하나(박하선)와 동료 황진이(황우슬혜)가 향하는 곳은 바로 이곳이다. 하나는 역 카페에 두고 온 서류가방을 찾으라는 상사의 미션 때문에 결국 기차를 몇 초 차이로 놓쳐 결국 기차를 타지 못한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8185, 26069, 17963, '1화에서 훔쳐간 도복을 입고 서로를 알아간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8186, 31659, 17963, '하람/일월성(안효섭)은 이 마을의 한 집에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8187, 25673, 17963, '홍천기 1화에서 하람(최승훈)이 인신공양으로 강제 참여하는 비의식이 이 장소에서 일어난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8188, 33125, 14906, '드라마 화랑 촬영지인 세트장이 있는 리조트');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8189, 27083, 14906, '드라마에서 화랑들이 항상 모이던 정원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8190, 25680, 14906, '최민호와 동생이 얘기하는 장면 촬영 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8191, 33126, 14906, '화랑에서 나온 저작거리 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8192, 33127, 14906, '화랑들이 있는 넓은 초원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8193, 33128, 14906, '화랑이 존재한 신라 느낌의 세트장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8194, 33129, 14906, '박서준과 고아라가 함께 등장한 계곡');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8195, 33130, 14906, '드라마에서 보인 마애솔숲 기암 절벽');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8196, 26487, 14906, '드라마 화랑에서 나타난 사랑나무');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8197, 27089, 14906, '화랑들이 생활하는 곳으로 나온 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8198, 25607, 15346, '11화에서 마왕(차승원)이 올림픽 성화를 들고 악마에 홀린 인간에게 칼에 찔리는 사건이 발생한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8199, 33148, 15346, '이 정원은 화유기에서 손오공(이승기) 집의 정원으로 나타난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8200, 25678, 15346, '화유기 2회에서는 크리스마스 이브에 손오공(이승기)과 진선미(오연서)가 \'데이트\'를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8201, 25824, 15346, '진선미(오연서)의 부동산 회사가 있는 건물이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8202, 25908, 15346, '드라마 안에서 유력한 대권 후보자인 강대성(송종호)의 사무실로 등장한다. 19화에서 이곳에서 대화를 나누는 강대성(송종호)과 좀비소녀(이세영)의 모습을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8203, 24900, 15346, '9회에서는 이 도서관에서 사악한 악마가 움직이며 동화책을 통해 아이들을 유혹하여 집을 떠나 그녀에게 다가옵니다. 그녀는 10화에서 진선미(오연서)와 손오공(이승기)에게 그녀를 구한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8204, 24956, 15346, '화유기 5회에서 손오공(이승기)과 함께 영화를 보다가 진선미(오연서)가 영화 속으로 빨려 들어가는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8205, 24935, 15346, '6회에서 손오공(이승기)과 프로스트 장군(성혁)은 오늘 밤까지 삼양/진선미(오연서)를 어떻게 제거할지 계획을 세우기 위해 여기에서 만난다. 오공은 결국 그럴 수 없는 모든 변명을 늘어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8206, 26228, 15346, '화유기 4화에서 영진선미(오연서)가 다니는 클럽으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8207, 25719, 15346, 'PK(이홍기)는 이곳에서 행사를 하고 밖에 나가면 손오공(이승기)의 차라는 것을 깨닫고 주차하는 방법에 대해 논평을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8208, 32149, 15346, '화유기 9회 초반, 진선미(오연서)는 신부 드레스를 입고 제단을 향해 걸어가는 자신을 발견합니다. 신랑의 손오공(이승기)은 이미 그녀를 기다리고 있고 우휘(차승원)는 피아노를 치고 있다. 그녀는 오공이 정말 그녀의 소울메이트인지, 운명의 남편인지 스스로에게 묻고, 오공이 악마를 제거할 수 있도록 그에게 다가가 갑자기 옆으로 밀려나게 됩니다. 선미는 답을 가지고 있지만 여전히 오공이 이 결혼식장을 떠날 때 애타게 제단을 바라보며 그녀를 진심으로 사랑했을 때의 꿈을 꾸고 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8209, 26088, 15346, '이 건물은 2화에 잠깐 등장하여 마치 슈퍼스타 쇼가 열리는 듯한 느낌을 줍니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8210, 27238, 15346, '이 공원의 T1 유리관은 화유기에서 지상천국의 본부로 등장하며 드라마에 여러 번 등장합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8211, 32221, 15346, '화유기 1화에 손오공(이승기)이 진선미(오연서)를 만난 후 주인에게 자신이 25년을 기다렸다는 것을 이해할 수 없다는 불평을 토로한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8212, 25929, 15346, '화유기 5회에서 진선미(오연서)와 손오공(이승기)은 악귀와 악인을 쫓아낸 후 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8213, 25608, 15346, '6화에서여자들의 머리를 자르는 악마가 있다; 진선미(오연서)는 열쇠 자물쇠를 걸고 나중에 그것을 풀려고 한다; 손오공(이승기)은 여기 선미와 합류해서 그녀를 긴장시키고 나중에 여기로 돌아오게 만든다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8214, 33149, 15346, '2회에서 귀신들린 꼭두각시가 가게에 앉아 있었다. 여기에서 귀신은 젊은 여자를 소유하고 나중에 손오공(이승기)은 두 번째 꼭두각시 귀신의 행방을 찾기 위해 이 가게를 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8215, 27359, 15346, '3화에서는 진선미(오연서) 이모와 삼촌이 이 집에 살고 있고 할머니의 기념일에 돈과 할머니가 좋아하는 팝콘을 추모상으로 가져오려고 환영받지는 않았지만 그녀가 여기에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8216, 32180, 15346, '1회에서 집주인 아이의 몸에서 귀신을 쫓아내는 사제가 나오는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8217, 26409, 15346, '진선미(오 연서)는 화유기 3회에서 어린 소년 귀신에게 팝콘을 주기 위해 빨간 신호등 앞에 서서 자신의 부동산 전단지를 굴린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8218, 26624, 15346, '화유기 11화에서 우휘(차승원)가 치료를 받는 장면에 나오는 병원이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8219, 33150, 15346, '화유기 1화에서 그는 그녀가 자신의 이름을 부르면 자신을 어떻게 불러야하는지 기억을 지워서 그녀를 영원히 보호한다는 약속을 즉시 취소한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8220, 26170, 15346, '손오공(이승기)이 아이스크림을 먹으러 정기적으로여기에 오고, 여기가 PK(이홍기) 팬들이 그의 행아웃인 만큼 그를 충실히 기다리는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8221, 25781, 15346, '16화 Panoramic65에서 PK(이홍기)가 미팅 후 데리러 오기를 기다리는 동안 손오공(이승기)은 여사제(이세영)를 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8222, 26412, 15346, '16회에서 이 건물 65층 Panoramic65에서 손오공(이승기)과 여사제(이세영)를 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8223, 29281, 15346, '화유기에서 잡화점의 손자(정제원)가 이 건물 앞에 색다른 매력의 노점상을 세운다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8224, 26165, 18898, '4회에서 낙수(고윤정)와 장일(이재욱)은 끝으로 이곳에서 자신의 옛 마을인 사리마을로 출발한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8225, 26486, 18898, '2화에서 낙수(고윤정)의 시신이 공개적으로 불태워지는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8226, 26487, 18898, '낙수(고윤정)는 5회에서 아침 일찍 이 나무의 발치에 와서 달을 보기 위해 나무 꼭대기로 올라가 소원을 들어주겠다고 약속하는 장욱(이재욱)과 함께 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8227, 32085, 15197, '도경은 서지안(신혜선)에게 빚을 갚기 위해 번역을 부탁한다. 그는 그녀에게 지불을 잊어버려 그녀는 집에 돌아갈 수 없었고 그녀가 비에 흠뻑 젖자 선우혁 (이태환)이 그녀를 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8228, 25641, 15197, '50회에서는 최서현(이다인)과 아버지 최재성(전노민)이 이 곳에서 진심어린 이야기를 나눈다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8229, 31716, 15197, '8화에서 \'게지박스\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8230, 32914, 15197, '15회에서 이 카페에서 서지안(신혜선)은 친구 강명신 그녀를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8231, 24932, 15197, '52회에서 서지안(신혜선)과 최도경(박시후)은 지안이 핀란드 유학을 마치고 한국에 잠시 머무는 동안 이곳에서 우연히 소개팅으로 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8232, 32814, 15197, '5회에서 최재성(전노민)이 여기서 만년필을 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8233, 31542, 15197, '이 지하철역 3번 출구는 황금빛 내 인생 3회에서 서지안(신혜선)이 아버지와 헤어지는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8234, 26744, 15197, '6회에서 서지안(신혜선)의 아버지인 절망의 서태수(천호진)가 이 다리 기슭에 앉아 소주를 마시고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8235, 28407, 15197, '36회에서 서지안(신혜선)은 최씨 일가를 떠난 뒤 이 집 앞에서 우연히 노명희(나영희)를 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8236, 25875, 15197, '25회에서 서지안(신혜선)의 아버지가 딸을 찾기 위해 안절부절 못하다가 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8237, 25661, 15197, '39회에서 선우혁(이태환)과 서지수(서은수)가 첫 데이트를 하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8238, 25662, 15197, '45회에서 서지수(서은수)와 선우혁(이) 태환) 이 카페에서 지수 아빠의 데이트 허락을 받는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8239, 26095, 15197, '서지호(신현수 분)가 45회에서 이곳에 옷가게를 내놨고, 최서현(이다인 분)이 찾아온다. 그녀는 그가 그녀에게 자신에게 반했느냐고 물었을 때 어떻게 반응해야 할지 모른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8240, 31652, 15197, '서지안(신혜선)과 최도경(박시후)은 해상그룹 회장으로 임명된 후 이곳에서 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8241, 25668, 15197, '30화에서 서지호(신현수)는 최서현(이다인)을 이곳으로 초대해 치킨과 맥주를 함께 먹었다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8242, 27442, 15197, '11회에서 서지태(이태성)와 이수아(박주희)가 헤어진 뒤 서로를 그리워하며 이 공원에 데이트를 하러 왔을 때를 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8243, 29204, 15197, '황금빛 내 인생 1회에서 서지수(서은수)는 짝사랑하는 선우혁(이태환)을 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8244, 26214, 15197, '4회에서 선우혁(이태환)은 왕자만화방(구 파스텔 드 나타)을 지나 자전거를 타고 이 골목길로 들어선다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8245, 24922, 15197, '1회에서 서지수(서은수)는 이 거리에서 선우혁(이태환)을 쫓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8246, 27124, 15197, '1화에서 선우혁(이태환)은 서지수(서은수)를 쫓을 때 자전거를 타고 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8247, 29530, 15197, '최씨 가문의 저택, 해성그룹 부회장과 그의 아내 노명희(나영희)는 아이들 최도경(박시후), 최서현(이다인), 그리고 남편과 함께 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8248, 26409, 15197, '32회에서 서지안(신혜선)과 최도경(박시후)은 함께 남대문시장으로 가는 길에 이곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8249, 28642, 15197, '서지안(신혜선 분)과 최도경(박시후 분)은 이 박물관에서 열리는 임시 전시회 \'새로운 여성의 도착\' 전시회을 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8250, 26148, 12994, '김명수(김희철)는 5화에서 자신의 짝사랑 상대인 강세미(안미나)를 핑계로 이 공원으로 데려오지만 그녀는 금세 그의 의도를 알아차리고 외면한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8251, 25865, 12994, '4회에서 강우남(강신일)은 중매결혼 전날 강준우(송창의)와 진주응옌(이영아)을 이곳으로 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8252, 25800, 12994, '강준우(송창의)가 LP로 일하는 C&P 사옥으로 건물이 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8253, 25953, 12994, '5회에서 옥지영(최여진)과 김영민(송종호)이 이 집에서 약혼을 자축한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8254, 26282, 12994, '1회에서 강준우(송창의)는 옥지영(최여진)과 함께 했던 학창시절의 행복한 순간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8255, 26744, 12994, '4화 이 다리에서 강세미(안미나)에 대한 사랑에 대해 양옥경(견미리)을 설득하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8256, 27566, 12994, '옥경이 아들의 장점을 뽐내기 시작하자 한숙은 한통의 전화를 받고 갑자기 자리를 뜬다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8257, 24998, 12994, '64화 마지막 장면에서 행복한 가족 강준우(송창의) ), 진주 응웬(이영아)과 그들의 아이가 이 차밭을 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8258, 26551, 15852, '이곳은 나왕식(최진혁)의 부상자를 이곳에서 돌보고, 그를 소생시키기 위해 간호하고 나중에 그를 황궁 근위병이 되도록 훈련시키는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8259, 30835, 15852, '37회에서 강태후(신은경)가 사회봉사 시간을 청소해야 하는 역사박물관. 오써니(장나라)의 아버지와 여동생은 그녀가 제대로 하는 것을 지켜본다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8260, 26521, 15852, '22회에서 나왕식(최진혁)과 오써니(장나라)는 황제 이혁(신성록)과 민유라(이엘리야)에게 머리에 총알이 박힌 사실을 숨기기 위해 이 병원에서 온갖 짓을 다한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8261, 26256, 15852, '17회에서 나왕식(최진혁)은 변선생님(김명)을 만나러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8262, 33098, 15852, '이 정원의 사계관은 황후의 품격에서 강태후(신은경)가 단독으로 사용하던 로열 유리집으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8263, 26153, 15852, '황제 이혁(신성록)이 할머니와 조태후(박원석)에게 민유라(이엘리야)를 소개하려 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8264, 26002, 15852, '49회에서 세 사람은 \'평범한 가족 데이트\'를 위해 이 놀이동산에 왔고, 아리는 \'공포의 무덤\'에 가자고 조른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8265, 26311, 15852, '4회에서 이혁(신성록)과 오써니(장나라)가 가짜 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8266, 26039, 15852, '40회에서 이 호텔의 한 방에서 강희 딸의 즉위를 계획했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8267, 25668, 15852, '36회에서 황제 이혁(신성록)이 동생 이윤(오승윤)과 만나 전처의 죽음에 대해 알게 된 사실을 털어놓는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8268, 25347, 15852, '39화에서 오쓰니(장나라) 왕후가 변선생(김명수)을 이 공원에서 만났다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8269, 29174, 15852, '9회에서는 황제 이혁(신성록)이 민유라(이엘리야)를 데리고 황실 재물을 과시하는 모습이 그려졌다. 22회에서는 황제 이혁(신성록)과 오수니(장나라)가 다시 찾아온 사랑을 보여주기 위해 찾아오는데, 수니의 습격을 받자 나왕식(최진혁)이 지켜내는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8270, 25608, 15852, '32회에서 나왕식(최진혁)은 어머니의 죽음의 원인을 알게 된 오써니(장나라)를 데려왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8271, 26232, 15852, '12화에서 나왕식(최진혁)에게 이혁(신성록)의 행방을 알리게 하고 그녀를 다리로 데려온다. 여기에서 두 사람은 왕이 이 다리 아래를 지나는 배에서 연인 민유라(이엘리야)와 키스하는 것을 지켜본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8272, 26071, 15852, '나왕식(최진혁)이 가까운 공중전화 부스에서 민유라(이엘리야)에게 전화를 걸고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8273, 25936, 15852, '대한제국의 본거지인 이화궁의 외관으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8274, 25603, 15852, '2회에서 보상을 요구하러 온 오써니(장나라 분)를 맞이하는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8275, 33157, 15852, '나왕식(최진혁)의 외할머니집으로 등장하는 맛집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8276, 33158, 15355, '회사를 그만두기 좋은 날 1회에서 구직자 연지(고원희)가 회사원 남자친구 민우(고경)를 만납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8277, 26551, 15553, '10화에서는 강훈남(남궁민)과 유정음(황정음)이 소울을 찾아와 오드리에게 소울에게 진심으로 관심이 있는지 알아보자고 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8278, 25737, 15553, '6회에서 정음(황정음)은 드레스를 교환하려는 회사 상사와 함께 이곳에 오다가 우연히 오두리(정영주)와 훈남(남궁민)을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8279, 24941, 15553, '8화에서 정음(황정음)과 그녀의 절친 양코치(오윤아)가 막걸리를 곁들인 파 팬케이크를 먹는 식당 \'명불허\'로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8280, 25910, 15553, '정음(황정음)이 사는 \'내장식당\'으로 등장한다. 1회에서 친구 최준수(최태준)와 술을 마시며 만난 정음(훈남정음)의 굴욕적인 첫 만남에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8281, 25017, 15553, '1회에서 정음(황정음)과 훈남(남궁민)은 이곳에서 처음으로 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8282, 26487, 15553, '아버지가 장난감에 집착하지 말라고 했을 때, 훈남(남궁민)은 어린 시절을 회상하며 이 나무 아래에서 아버지를 기다렸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8283, 24932, 15350, '11회에서 수호(김래원)는 하라(신세경)가 이 식당에 도착하기를 기다린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8284, 25790, 15350, '6화에서 샤론(서지혜)은 강물에 빠져 죽으려 하고 장백희(장미희)는 그녀가 이 절벽 아래에 의식을 잃은 채 누워 있는 것을 발견한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8285, 27105, 15350, '여기는 김영미(신소율)가 운영하는 가게로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8286, 25791, 15350, '6화에서 정해라(신세경)의 몸에 빙의한 샤론(서지혜)은 이 건물 바로 앞 포장마차에 앉아 문수호(김래원)를 부른다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8287, 24941, 15350, '3회에서 정해라(신세경)와 문수호(김래원)가 밥 먹으러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8288, 26136, 15350, '19회에서 정해라(신세경)의 전 절친 김영미(신소율)가 그녀에게 이 식당에 저녁을 먹으러 오라고 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8289, 25722, 15350, '7화에서 정해라(신세경)는 추운 겨울날 숙모 이숙희(황정민)와 함께 이곳에서 운동을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8290, 26678, 15350, '16회에서 직장 동료들과 함께 이곳에서 저녁식사를 했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8291, 26499, 15350, '1회에서 전 남자친구가 사기꾼이지 검사가 아니라는 사실을 폭로했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8292, 26997, 15350, '문수호(김래원)와 정해라(신세경)는 그랜드 하얏트 야외 아이스링크장에서 스케이트를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8293, 33160, 15350, '흑기사 5화에서 샤론이 다쳐서 도움이 필요하다고 전화로 문수호(김래원)에게 전화로 알리라고 아줌마에게 부탁한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8294, 25910, 15350, '15회에서 박곤 (박성훈)은 재개발 사업을 위해 적극적으로 시민들의 마음을 사로잡기 위해 노력하고 있는데 그가 하는 곳 중 하나.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8295, 29204, 15350, '문수호(김래원)는 이 거리를 걸으며 비서와 동네 프로젝트에 대해 이야기합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8296, 29181, 15350, '1975년, 5화에서 박철민은 샤론(서지혜)에게 샤론(서지혜)이 25살이 될 때까지 이 카페에서 프러포즈를 해주기를 기다려줄 수 있느냐고 고백한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8297, 27309, 15350, '3회에서 익선동 한옥마을 골목을 배회하면서 젠트리피케이션에 맞서는 동네 재생사업을 구상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8298, 26523, 15350, '15화에서 \'금성동 재개발 청문회\'가 열리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8299, 33161, 15350, '11회에서 장백희(장미희)는 이 거리를 걷다가 샤론(서지혜)이 박철을 만들었다는 것을 깨닫는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8300, 25946, 15350, '김영미(신소율)가 운영하는 가게입니다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8301, 31898, 15350, '문수호(김래원)와 정해라(신세경)는 익선 골목을 배회하던 중 자신의 서울동네 프로젝트(젠트리피케이션에 맞서는 동네 재생 사업)에 대해 설명하고 이 식당을 지나갔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8302, 28146, 15350, '6회, 김영미(신소율)은 정해라(신세경)를 이곳에서 저녁식사에 초대하지만 그 자리에 함께 있던 박철민(김병옥)은 하라에게 쇼핑몰 운영을 도와달라고 부탁할 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8303, 33162, 15350, '문수호(김래원)의 회사 \'블랙 스미스\'로 나오는 곳이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8304, 24959, 15350, '1회에서 정해라(신세경)가 그녀의 \'꿈\'에서 드레스를 사기 위해 오래된 양복점을 찾고 있다. 양복점에 갈 때마다이 성당이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8305, 32095, 15350, '이 건물의 외관은 정해라(신세경)의 직장으로 나온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8306, 26273, 15350, '2회에 장백희(장미희)가 이곳에 와서 자매에게 자신이 250세이고 이제야 늙어가며 중죄를 저질렀다고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8307, 26188, 15350, '이곳에서 장백희(장미희)는 블랙나이트 도서관에서 자신의 책 강연을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8308, 25017, 15350, '1회에서 여행사에 근무하는 정해라(신세경)는 공항을 찾은 관광객들에게 가져가야 할 것, 가져가지 말아야 할 것을 안내하고, 승객들의 짐에 스티커를 붙인 후 “즐겁게 다녀오세요!”라고 말했다. 그녀는 밖에 나가서 일에 너무 지쳐서 하이힐을 벗고 휴식을 취한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8309, 33163, 15350, '흑기사 \'샤론 재단사\' 입구');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8310, 25948, 14219, '17회에서 서정후(지창욱) 아버지의 유골을 안고 있는 힐러(힐러) 채영신(지창욱)과 함께 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8311, 32814, 14219, '힐러 12화에서 서정후(지창욱)의 어머니가 아들의 상견례 전에 겨울 재킷을 여기에서 구입합니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8312, 33164, 14219, '서정후(지창욱)와 채영신(박민영)이 만나는 곳은 박물관 옥상이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8313, 33165, 14219, '11회에서 채영신/오지안(박민영)이 힐러(지창욱)와의 영화 데이트를 기다리는 곳이 바로 이 쇼핑몰이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8314, 26499, 14219, '11회에서 채영신(박민영)이 서정후(지창욱)와 거짓 데이트를 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8315, 25335, 14219, '7회에서 강대영(태미)이 차를 몰고 이 다리를 건너는 동안 이영국(박상원)은 여기에서 멈춘다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8316, 26992, 14219, '서정후(지창욱)와 강대영(태미)의 만남의 장소이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8317, 33166, 14219, '이 카페는 힐러에서 \'커피베이\'로 등장합니다. 채영신(박민영)이 사는 곳이기도 하다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8318, 24962, 14219, '대표이사 김문식(박상원)의 저택으로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8319, 33167, 14219, '15회에서 그녀의 팀에 합류할 새로운 기자를 만나기 위해 이곳으로 파견된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8320, 33168, 14219, '배상수(박상욱)의 보안회사 사옥으로 등장');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8321, 26654, 14219, '힐러에서 박정대 장로(최종원)의 집이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8322, 33169, 14219, '이 복합 단지의 일부 외부는 힐러의 버려진 건물에 그가 세운 힐러의 다락방으로 나타납니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8323, 33170, 14968, '12회에서 도봉순(박보영)과 안민혁(박형식)이 이곳으로 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8324, 33171, 14968, '12회에서 도봉순(박보영)과 안민혁(박형식)은 \'워크숍\'을 위해 이곳을 찾습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8325, 26148, 14968, '도봉순(박보영)은 안민혁(박형식)의 기분을 바꾸고 싶어 도봉순을 이곳으로 끌고 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8326, 33172, 14968, '2회에서 박형식(안민혁)이 개인 경호원으로 출근 첫날 도봉순(박보영)을 이곳으로 끌고 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8327, 26699, 14968, '안민현(박형식)의 게임회사로 도봉순(박보영)이 그의 보디가드로 일하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8328, 33173, 14968, '민혁과 봉순의 분위기 있는 데이트 장소');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8329, 28412, 14968, '민혁의 집에 있는 가구를 만나 볼 수 있는 곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8330, 33174, 14968, '16회에서 도봉순(박보영)의 온가족 사위 안민혁(박형식)이 이곳으로 회식하러 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8331, 33175, 14968, '마지막회 민혁과 봉순네 가족이 바베큐 파티를 연곳');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8332, 29525, 14968, '3회에서 어색한 가족 외식 장소가 열렸다. 실제로 이 스튜디오는 안민혁(박형식) 집의 거실로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8333, 33176, 14968, '여기에 공비서(전석호)와 아인소프트 직원 오동병(김원해)이 동시에 아이스크림을 먹고 있는 모습도 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8334, 33177, 14968, '2회에서 안민혁(박형식)이 개인 경호원으로 출근하는 첫날 도봉순(박보영)을 이곳으로 끌고 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8335, 27507, 14968, '11회에서 도봉순(박보영)과 안민혁(박형식)이 데이트를 위해 이 거리를 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8336, 26095, 14968, '11회에서 데이트 중인 도봉순(박보영)과 안민혁(박형식)이 이 공원 외벽을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8337, 24906, 14968, '8회에서 안민혁(박형식)은 "사랑했던 여자 만나러 가자"라고 말하며 도봉순(박보영)을 평화의 정원 나무 아래 묻힌 어머니의 무덤에 데리고 간다. 그 곳에 도착하고, 그들은 하늘공원 메타세콰이어길을 따라 걷는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8338, 26178, 14968, '8화에서 도봉순(박보영)과 함께 지구력 훈련을 위해 조깅하는 모습을 볼 수 있다. 두 사람은 13화에서 다시 이곳으로 돌아오는데, 연애를 시작한 후 이곳에서 벌어지는 첫 충돌이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8339, 26296, 14968, '도봉순(박보영)과 안민혁(박형식)은 이곳에서 결혼해 행복한 연애를 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8340, 25882, 14968, '민혁이 전동휠을 타다가 습격 받은 공원');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8341, 25802, 14968, '마지막회 16화에서 도봉순(박보영)과 안민혁(박형식)이 데이트를 하러 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8342, 33178, 14968, '도봉순의 아버지가 여기에서 일하고 그녀의 어머니가 그녀의 아주머니 친구들과 항상 어울리는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8343, 26211, 14968, '도봉순(박보영)과 인국두(지수)가 다녔던 학교이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8344, 25910, 14968, '15회에서 도봉순(박보영)과 안민혁(박형식)이 함께 점심을 먹습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8345, 24948, 14968, '7화에서 도봉순(박보영)은 낙산공원 놀이터에서 어머니, 할머니와 함께 시간을 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8346, 26097, 14968, '이 집은 도봉순(박보영)이 가족들과 함께 살고 있는 집이다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8347, 26587, 14968, '도봉순네 집 및 도봉순 동네의 배경');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8348, 25349, 14968, '13화에서 납치범 김장현(장미관)이 도봉순(박보영)을 해칠 계획을 세운 것은 공원 입구 중 한 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8349, 27331, 14968, '드라마 남자 주인공인 안민혁(박형식)의 저택으로 등장한다. 7, 8회에서 볼 수 있습니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8350, 33179, 14968, '6회에서 안민혁(박형식)은 도봉순(박보영)을 데려와 그녀의 힘을 시험해 본다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8351, 33180, 14968, '6회에서 도봉순(박보영)과 안민혁(박형식)이 함께 일몰을 보기 위해 이곳에 왔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8352, 32679, 14968, '3화, 도봉순(박보영) 어머니와 함께 사원에서 돌아올 때 이 다리를 건넌다. 6화에서는 엄마가 아주머니들과 함께 산에서 등산을 할 때 이 다리를 건너는 모습을 볼 수 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8353, 33181, 14968, '도봉순이 정기를 받은 도봉산');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8354, 33182, 14968, '4회에서 도봉순(박보영)과 어머니가 이 절에 오는 모습을 회상했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8355, 26141, 16382, '방영백(인피니트 이호원/호야)과 송하진(이나은)은 시간이 늦어진 것을 깨닫고 또한 2번 트랙에 약속이 있음을 깨닫고 이 터널에서 함께 오후를 마무리한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8356, 26576, 16382, '방영백(이호원)과 송하진(이나은)은 이곳에서 오후에 함께 놀 때 이 도서관에서 책을 빌린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8357, 32232, 16382, '송하진(이나은)은 2번 트랙에서 오후 함께 이 계단 위의 롱보드 위에서 방영백(이호원/인피니트 호야)에게 스케이트 타는 법을 보여주고 있다. 이후 방송에서 그는 학교에서 돌아와 절친인 서기하(한현민)와 함께 계단을 내려간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8358, 24907, 16382, '방영백(이호원)이 새 집이 내려다보이는 풍경을 보기 위해 이 계단의 꼭대기로 걸어간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8359, 33183, 16382, '1화에서 방영백(이호원)은 서울에 도착하면 이 버스 정류장에서 내린다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8360, 28508, 17961, '5회에서 안준호(정해인)는 동료 군인이자 현직 탈영병 조석봉(조현철)을 찾기 위해 이 버스 터미널로 향한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8361, 29261, 17961, '3화에서 부산 동료들을 만나기 위해 이 호텔에 온 안준호(정해인)와 한호열(구교환).');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8362, 28584, 17961, '3화에서 부산에 도착한 안준호(정해인)와 한호열(구교환)은 이 다리를 건너는 부산시티투어 버스를 탄다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8363, 26657, 17961, '6회에서 볼 수 있듯이 이 집에는 한국전쟁 참전용사 이영천(전무송)이 살고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8364, 24946, 17961, '안준호(정해인)는 임무를 수행하던 중 불편한 마음에 박성우(고경표 카메오)를 따라 친구들과 술자리를 갖기 위해 이곳으로 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8365, 31751, 17961, '4회에서 안준호(정해인)와 한호열(구교환)은 탈영병 허치도의 동급생과 대화를 나누기 위해 이 대학 캠퍼스(\'청파대학교\'로 등장)에 찾아온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8366, 30780, 15863, '10회에서 이수임(이태란)이 한서진(염정아)과 만나 책 사건에 대한 이야기를 나눴다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8367, 26030, 15863, '10회에서 강준상(정준호)은 자녀들의 교육을 이야기하기 위해 스카이캐슬 이웃 차민혁(김병철)을 이 곳에서 만납니다.이것은 사실 민혁과 가까워지기 위한 구실일 뿐입니다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8368, 25906, 15863, '이 장소는 13회에서 이웃 강준상(정준호)과 차민혁(김병철) 준상의 상사 최인호(송민형)와 함께 정치를 하기 위해 모였다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8369, 28385, 15863, '2회에서 이명주(김정난)가 잠들어 있는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8370, 26615, 15863, '여기는 한서진(염정아)과 김주영(김서형) 선생님의 만남의 장소이다. 이들은 9, 11회에서 강예서(김혜윤)의 성공을 계획하기 위해 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8371, 25997, 15863, '10회에서 이수임(이태란)이 자신의 제자 중 한 명이 이 묘지에 안장된 것을 추모하기 위해 이곳을 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8372, 27151, 15863, '드라마의 메인 촬영장소인 \'SKY City\' 의 주택단지로 나오는데 드라마의 등장인물들이 이곳에 살고 있기 때문에 다양한 장면에서 이 곳을 확인 할 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8373, 25831, 15863, '극 중 아이들이 다니는 엘리트 고등학교인 \'신하고등학교\'로 설정되어 나오는 배경지로, 9화에서 이 곳을 다니는 아이들의 모습을 볼 수 있다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8374, 26607, 15863, '이 병원은 \'주남대병원(주남대병원)\'으로 등장한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8375, 24935, 15863, '2회에서 강준상(정준호)은 아내를 잃은 박수창(유성주)과 함께 술을 마시러 왔다. 그리고 준상은 수창이 아들 박영재(송건희)를 아내 자살의 범인으로 말해 놀랐다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8376, 33445, 15863, '2회에서 엄마 한서진(염정아)과 진진희(오나라)가 아이들을 기다리며 커피를 마시러 가는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8377, 26617, 15863, '3회에서 한서진(염정아)과 이웃 이명주(김정난)가 이곳에서 만난다. 10회에서 김혜나(김보라)는 한서진(염정아)에게 딸 강예빈(이지원)의 동거 가정교사를 부탁하지만 서진은 이를 직접 거절한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8378, 33446, 15863, '18회에서 한서진(염정아)이 김주영(김서형)을 상대로 고소장을 제출했다. 그녀가 이 건물을 떠날 때 남편 강준상(정준호)은 놀라움과 지원으로 다가온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8379, 26410, 15863, '한서진(염정아)와 김주영 선생님(김서형)은 9-10회에서 점심을 먹으러 이곳에서 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8380, 28442, 15863, '한서진(염정아)의 시어머니 윤부인(정애리)은 이 건물에 산다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8381, 25909, 15863, '19회에서 박영재(송건희)는 부모님과 함께 이 식당에서 보낸 행복한 시간을 회상한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8382, 26211, 15863, '4회 회상에서 한서진(염정아)과 이수임(이태란)이 함께 이 학교를 다녔다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8383, 25910, 15863, '강준상(정준호)은 절친 박수창과 술을 마시러 이곳에 온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8384, 27227, 15863, '여기는 김주영(김서형)의 장애 딸 케이(조미녀)가 거주하는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8385, 25955, 15863, '김혜나(김보라)는 맨션에 사는 가족을 위해 아르바이트를 하고 아들의 성과평가를 받으며 7회, 이곳에 와서 연봉협상을 한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8386, 24953, 15863, '6회에서 한서진(염정아)과 선생님 김주영(김서형)이 이곳에서 만나 점심을 먹는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8387, 33447, 15863, '김주영(김서형)의 사무실이 있는 \'D센터\'로 등장한다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8388, 25913, 15863, '한서진(염정아)은 여기서 김주영(김서형)의 비서 조선생(이현진)과 만나 김혜나가 남편의 딸이라는 사실을 알게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8389, 25781, 15863, '김주영(김서형)의 아파트이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8390, 25017, 15863, '11화에서 노승혜(윤세아)와 차민혁(김병철)의 딸 차세리(박유나)가 미국(미국 하버드대)에서 이곳에 도착한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8391, 26125, 15863, '1화에서 고객들의 고등학생들을 위해 최고의 튜터들과 매치메이킹을 하는 한남은행의 VIP 이벤트가 펼쳐지는 곳이다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8392, 26456, 15863, '드라마 안 SKY 캐슬 동네의 입구로 나오는 장소이다. 한서진(염정아) 뒷좌석에 딸을 태우고 이 곳을 지나간다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8393, 25948, 16518, '박성준(이상윤)은 28화(14화)에서 하유리(표예진)와 함께 돌아가신 어머니의 틈새시장을 방문한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8394, 24992, 16518, '최종회 16회에서 나정선(장나라)은 머리를 맑게 하기 위해 여행길에 이 폭포를 찾는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8395, 24933, 16518, '박성준(이상윤)이 3화에서 티폰의 직원 리아킴(김소이 분 특별 카메오)을 요청으로 이곳에서 만난다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8396, 25830, 16518, '28화(전체 14화)박성준(이상윤), 나정선(장나라), 하유리(표예진), 마상우(신재하)는 자신의 사무실에서 부유한 가족을 위한 중매인과의 만남에 참석한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8397, 28092, 16518, '8회에서 주얼리 쇼케이스 및 경매가 진행된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8398, 26995, 16518, '3화(전체 2화)에서 이 역 6번 출구는 온유리(표예진)가 상점들을 지나는 모습이 보인다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8399, 24935, 16518, '29회(전체 15회)에서 박성준(이상윤)은 하재웅 부사장(박성근)을 비롯한 성운백화점 주주들과의 만남을 위해 이곳을 찾았다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8400, 33463, 16518, '4화(본편 2화)에서 박성준(이상윤)은 회식 후 이곳에서 아내 나정선(장나라)에게 먼저 자기 없이 떠나라고 말한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8401, 29455, 16518, '3회(전체2회)에서 이현아(이청아)는 온유리(표예진)가 갈 때 옷을 빌릴 수 있도록 이 부티크에 온유리(표예진)를 데려온다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8402, 25031, 16518, '4화에서 하 부사장(박성근)은 숭운백화점 직원들을 위해 이 식당에서 회식을 진행했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8403, 33464, 16518, '6화(전체 3화)에서 박성준(이상윤)과 나정선(장나라)이 이곳에서 만난다. 성준의 불륜 사실을 알게 된 정선이의 감정에 대해 이야기한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8404, 25773, 16518, '1화에서 마상우(신재하)에게 중요한 서류를 넘기기 위해 공항에 도착했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8405, 25953, 16518, '4화(전체 2화)에서 나정선(장나라)은 남편 박성준(이상윤)을 따라 이 호텔로 향했다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8406, 26000, 16518, '2화(1화)에서 송미나(곽선영)는 이 다리의 갑판에 잠겨 서 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8407, 25734, 16518, '1화에서 나정선(장나라)이 쏟아지는 비 속에서 오토바이를 타고 이 다리를 건너, 공항에 제때 도착해 박성준(이상윤)과 마상우(신재하)에게 중요한 서류를 건네는 모습이 그려진다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8408, 33465, 16518, '9회에서 2002년으로 회상한다. 대학생 이현아(이청아)가 인문대 밖에서 자신의 사진동아리 박성준(이상윤)이 농구하는 모습을 보고 있다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8409, 26039, 16518, '이선영 한정그룹 회장(유선 특별 카메오)이 이 스위트룸을 개인실로 사용해 현재 진행 중인 티폰 주얼리 경매를 관람하게 된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8410, 25335, 16518, '6화(전체 3화)에서 준(이상윤)과 나정선(장나라)이 차를 타고 이 다리를 건너는 모습이 그려졌다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8411, 26662, 16518, '16화에서 현상을 위해 보내온 사진을 가지러 이 사진관에 온다. 스튜디오 밖으로 나온 그는 사진을 보며 나정선(정나라 분)과 나눈 추억의 홍수가 자신을 덮치며 울기 시작한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8412, 25910, 16518, '진호는 온유리(표예진)를 만나고 있다는 의혹에 대해 성준과 대면한다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8413, 24927, 16518, '티폰 행사장에서 성운 백화점 부사장의 아내가 온유리(표예진)를 학대했다는 뉴스가 18화에 이 방송국 옆의 큰 화면에서 방송된다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8414, 33466, 16518, '11회(전체 6회)에서 산책을 하다가 쓰러진 티폰 회장 김다니엘이 휴식을 위해 나정선(장나라) 일행에 의해 이곳으로 모이게 된다');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8415, 24951, 16518, '나정선(장나라)은 6화에서 다리와 다리 사이의 벤치에 앉는다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8416, 28547, 16518, '13화(전체 7화)에서 박성준(이상윤)은 이 술집에서 하 부회장의 부을 만난다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8417, 25017, 16518, '13회(7화)에서 박성준(이상윤)이 티폰 김다니엘 회장을 이 곳에서 보낸다.');
INSERT INTO king.place_content (id, place_id, content_id, description) VALUES (8418, 24915, 16518, '2회에서 이현아(이청아)는 이 호텔의 방을 예약하러 옵니다.');
